INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '11', '11007', '', '', 'Definizioni stradali: l’intersezione (incrocio) a livelli sfalsati', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '11', '11007', '97', '1', 'L''intersezione (incrocio) a livelli sfalsati comprende strade situate ad altezze diverse', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '11', '11007', '98', '2', 'L''intersezione (incrocio) a livelli sfalsati facilita la circolazione, perché esclude l''incrocio diretto fra  veicoli', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '11', '11007', '99', '3', 'L''intersezione (incrocio) a livelli sfalsati comprende rampe, sovrappassi o sottopassi', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '11', '11007', '100', '4', 'L''intersezione (incrocio) a livelli sfalsati è munita di infrastrutture (rampe, sovrappassi, sottopassi) per il collegamento fra strade poste a livelli diversi', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '11', '11007', '101', '5', 'L''intersezione a livelli sfalsati è un incrocio a raso fra una strada e i binari del treno', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '11', '11007', '102', '6', 'Nell''intersezione (incrocio) a livelli sfalsati non valgono i limiti massimi generali di velocità', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '11', '11007', '103', '7', 'L''intersezione (incrocio) a livelli sfalsati è, di norma, regolata da semafori', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '12', '11010', '', '', 'Definizioni stradali: il passaggio a livello ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '12', '11010', '104', '1', 'In un passaggio a livello si possono trovare le barriere', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '12', '11010', '105', '2', 'In un passaggio a livello si possono trovare le semibarriere', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '12', '11010', '106', '3', 'In un passaggio a livello si possono trovare le luci rosse', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '12', '11010', '107', '4', 'In vicinanza di un passaggio a livello si trovano i pannelli distanziometrici', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '12', '11010', '108', '5', 'In un passaggio a livello si può trovare la croce di S. Andrea', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '12', '11010', '109', '6', 'In un passaggio a livello si può trovare un dispositivo di segnalazione acustica', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '12', '11010', '110', '7', 'Il passaggio a livello è un cavalcavia ferroviario o tranviario', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '12', '11010', '111', '8', 'In un passaggio a livello si possono trovare le luci gialle lampeggianti', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '12', '11010', '112', '9', 'Il passaggio a livello è caratterizzato dalla presenza di un attraversamento autostradale', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '12', '11010', '113', '10', 'In un passaggio a livello si possono trovare le corsie di accelerazione', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '12', '11010', '114', '11', 'Il passaggio a livello è un passo carrabile', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '12', '11010', '115', '12', 'Il passaggio a livello è caratterizzato dalla presenza di un sottopassaggio    ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '13', '11019', '', '', 'Definizioni stradali: la banchina', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '13', '11019', '116', '1', 'La banchina non consente, di norma, il transito ai veicoli', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '13', '11019', '117', '2', 'La banchina è parte della strada, posta al di fuori della carreggiata', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '13', '11019', '118', '3', 'La banchina è parte della strada, situata oltre la linea bianca continua di margine della carreggiata', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '13', '11019', '119', '4', 'La banchina è riservata ai veicoli lenti, nelle strade in salita', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '13', '11019', '120', '5', 'Sulle banchine la sosta dei veicoli è consentita per un massimo di 5 ore', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '13', '11019', '121', '6', 'La banchina divide la carreggiata in due corsie', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '13', '11019', '122', '7', 'La banchina è parte della carreggiata, destinata alla sosta dei veicoli', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '14', '11020', '', '', 'Definizioni stradali: Il marciapiede', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '14', '11020', '123', '1', 'Il marciapiede è una parte della strada, rialzata o delimitata in altro modo, riservata ai pedoni', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '14', '11020', '124', '2', 'Il marciapiede è una parte della strada riservata, di norma, ai pedoni', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '14', '11020', '125', '3', 'Il marciapiede consente la sosta dei veicoli solo in presenza di apposite strisce di parcheggio', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '14', '11020', '126', '4', 'Il marciapiede è una parte della strada, al di fuori della carreggiata, destinata alla circolazione dei pedoni', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '14', '11020', '127', '5', 'Il marciapiede è una parte della carreggiata', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '14', '11020', '128', '6', 'Il marciapiede è una parte della strada riservata anche alle biciclette', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '14', '11020', '129', '7', 'Il marciapiede è riservato ai pedoni e ai veicoli in sosta, qualora non sia rialzato', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '14', '11020', '130', '8', 'Il marciapiede è una parte della strada dove, di norma, è consentita la sosta dei ciclomotori', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '15', '11004', '', '', 'Definizioni stradali: l’attraversamento pedonale ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '15', '11004', '131', '1', 'L''attraversamento pedonale viene segnalato con strisce bianche parallele', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '15', '11004', '132', '2', 'L''attraversamento pedonale è una parte della carreggiata sulla quale è vietata la fermata dei veicoli', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '15', '11004', '133', '3', 'L''attraversamento pedonale è una parte della carreggiata, in cui i veicoli devono dare la precedenza ai pedoni, che attraversano sulle apposite strisce', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '15', '11004', '134', '4', 'L''attraversamento pedonale è una parte della carreggiata destinata al transito dei pedoni che  attraversano', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '15', '11004', '135', '5', 'L''attraversamento pedonale è una parte della carreggiata sulla quale è vietata la sosta dei veicoli', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '15', '11004', '136', '6', 'L''attraversamento pedonale è una parte della strada, separata dalla carreggiata mediante una striscia gialla', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '15', '11004', '137', '7', 'L''attraversamento pedonale è una parte della carreggiata vietata al transito dei veicoli', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '15', '11004', '138', '8', 'L''attraversamento pedonale non fa parte della carreggiata', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '15', '11004', '139', '9', 'L''attraversamento pedonale è posto al di fuori della carreggiata ed è destinato al transito dei pedoni', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '15', '11004', '140', '10', 'L''attraversamento pedonale è una parte della carreggiata in cui è consentita la fermata ma non la sosta dei veicoli', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '16', '11022', '', '', 'Definizioni stradali: il passo carrabile', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '16', '11022', '141', '1', 'Il passo carrabile consente ai veicoli di accedere alle proprietà private laterali alla strada (garage, cortili, ecc.)', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '16', '11022', '142', '2', 'Il passo carrabile permette ai veicoli che circolano su una strada pubblica di accedere ad un''area laterale adibita alla sosta', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '16', '11022', '143', '3', 'Il passo carrabile deve essere corredato da apposito segnale, che riporti l''ente e il numero di autorizzazione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '16', '11022', '144', '4', 'Il passo carrabile è una zona per l''accesso dei veicoli alle proprietà laterali alla strada', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '16', '11022', '145', '5', 'In corrispondenza del passo carrabile è consentita la fermata', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '16', '11022', '146', '6', 'Il passo carrabile è un parcheggio scambiatore', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '16', '11022', '147', '7', 'Il passo carrabile è una strada riservata ai carri trainati da animali', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '16', '11022', '148', '8', 'Il passo carrabile è una strada riservata agli autocarri', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '16', '11022', '149', '9', 'Il passo carrabile è un salvagente circolare per i pedoni', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '16', '11022', '150', '10', 'Il passo carrabile è un segnale che impone la circolazione rotatoria su una piazza', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '16', '11022', '151', '11', 'In corrispondenza del passo carrabile è consentita la sosta', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '17', '11006', '', '', 'Definizioni stradali: l’isola di traffico', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '17', '11006', '152', '1', 'L''isola di traffico serve a facilitare l''incanalamento dei veicoli verso varie direzioni', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '17', '11006', '153', '2', 'L''isola di traffico è delimitata da strisce bianche continue', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '17', '11006', '154', '3', 'L''isola di traffico può essere a raso o in rilievo (rialzata)', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '17', '11006', '155', '4', 'l''isola di traffico viene evidenziata mediante zebrature di colore bianco', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '17', '11006', '156', '5', 'L''isola di traffico è una parte della strada sulla quale è vietato il transito dei motocicli', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '17', '11006', '157', '6', 'L''isola di traffico a raso è costituita da zebrature di colore bianco', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '17', '11006', '158', '7', 'L''isola di traffico, se rialzata, è preceduta da zebrature di colore bianco', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '17', '11006', '159', '8', 'L''isola di traffico è una parte della strada sulla quale è vietato il transito dei veicoli', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '17', '11006', '160', '9', 'L''isola di traffico serve a separare la carreggiata dalla banchina', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '17', '11006', '161', '10', 'L''isola di traffico serve per il transito dei pedoni', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '17', '11006', '162', '11', 'L''isola di traffico è una piazzola di sosta per i veicoli', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '17', '11006', '163', '12', 'L''isola di traffico può essere percorsa solo dai taxi e dagli autobus urbani', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '17', '11006', '164', '13', 'L''isola di traffico è la parte della città con maggiore traffico', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '17', '11006', '165', '14', 'L''isola di traffico corrisponde, in genere, al centro storico della città', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '17', '11006', '166', '15', 'L''isola di traffico è una piazzola per effettuare l''inversione di marcia', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '17', '11006', '167', '16', 'L''isola di traffico è posta al di fuori della strada', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '17', '11006', '168', '17', 'L''isola di traffico serve per la fermata dei veicoli', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '17', '11006', '169', '18', 'L''isola di traffico facilita la salita e la discesa di passeggeri da tram, filobus e autobus', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '17', '11006', '170', '19', 'L''isola di traffico è una zona destinata al parcheggio dei veicoli', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '17', '11006', '171', '20', 'L''isola di traffico è un''area pedonale urbana', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '17', '11006', '172', '21', 'L''isola di traffico è una parte della strada sulla quale è consentito il transito dei ciclomotori', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '18', '11021', '', '', 'Definizioni stradali: il salvagente', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '18', '11021', '173', '1', 'Il salvagente, posto in corrispondenza di attraversamenti pedonali, serve anche al riparo o alla sosta dei pedoni che attraversano la strada', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '18', '11021', '174', '2', 'Il salvagente facilita la salita e la discesa dei passeggeri da tram, filobus o autobus', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '18', '11021', '175', '3', 'Il salvagente può essere segnalato da colonnine luminose a luce gialla fissa', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '18', '11021', '176', '4', 'Il salvagente serve a dividere i sensi di marcia in una strada a doppio senso di circolazione', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '18', '11021', '177', '5', 'Il salvagente serve a incanalare i veicoli in varie direzioni', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '18', '11021', '178', '6', 'Il salvagente è una persona preposta al primo soccorso', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '18', '11021', '179', '7', 'Il salvagente è un itinerario ciclopedonale', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '19', '11011', '', '', 'Definizioni stradali: l’area pedonale ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '19', '11011', '180', '1', 'Nelle aree pedonali possono transitare i veicoli ad emissione zero (elettrici), se espressamente consentito', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '19', '11011', '181', '2', 'Nelle aree pedonali possono transitare i pedoni', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '19', '11011', '182', '3', 'Nelle aree pedonali possono transitare le biciclette', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '19', '11011', '183', '4', 'Nelle aree pedonali possono transitare i veicoli al servizio di persone diversamente abili', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '19', '11011', '184', '5', 'Nelle aree pedonali possono transitare i veicoli della polizia, con sirena e luce lampeggiante blu in funzione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '19', '11011', '185', '6', 'Nelle aree pedonali possono transitare gli autoveicoli dei vigili del fuoco, con luce lampeggiante blu e sirena in funzione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '19', '11011', '186', '7', 'Nelle aree pedonali possono transitare tutti i motocicli, purché diano la precedenza ai pedoni', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '19', '11011', '187', '8', 'Nelle aree pedonali possono transitare i trattori stradali, con velocità non superiore a 30 km/h', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '19', '11011', '188', '9', 'Nelle aree pedonali i ciclomotori possono transitare solo di giorno', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '19', '11011', '189', '10', 'Nelle aree pedonali possono transitare tutti gli autoveicoli, ma con prudenza', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '19', '11011', '190', '11', 'Nelle aree pedonali possono transitare i quadricicli a motore, purché non superino la velocità di 50 km/h', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '20', '11009', '', '', 'Definizioni stradali: la zona a traffico limitato', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '20', '11009', '191', '1', 'Una zona a traffico limitato è un''area in cui può essere limitata la circolazione dei veicoli solo in alcune ore', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '20', '11009', '192', '2', 'Una zona a traffico limitato è un''area nella quale può essere limitata la circolazione solo di particolari categorie di veicoli', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '20', '11009', '193', '3', 'In una zona a traffico limitato possono circolare solo i veicoli autorizzati', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '20', '11009', '194', '4', 'Una zona a traffico limitato è un''area nella quale può essere limitata la circolazione solo a particolari categorie di utenti', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '20', '11009', '195', '5', 'Una zona a traffico limitato è delimitata da appositi segnali stradali di inizio e di fine', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '20', '11009', '196', '6', 'In una zona a traffico limitato non possono entrare più di un certo numero di veicoli', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '20', '11009', '197', '7', 'Una zona a traffico limitato è un''area nella quale è consentito solo il transito di ciclomotori e biciclette', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '20', '11009', '198', '8', 'Una zona a traffico limitato è un''area sempre vietata al transito dei veicoli a motore', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '20', '11009', '199', '9', 'Una zona a traffico limitato è un''area vietata al transito dei pedoni durante le ore notturne', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '21', '11013', '', '', 'Definizioni stradali: l’autostrada ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '21', '11013', '200', '1', 'L''autostrada non ha incroci a raso che l''attraversano', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '21', '11013', '201', '2', 'L''autostrada è dotata di speciali corsie per l''ingresso e l''uscita dei veicoli', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '21', '11013', '202', '3', 'L''autostrada è a carreggiate indipendenti o separate da spartitraffico', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '21', '11013', '203', '4', 'I motocicli con cilindrata di almeno 150 cm3 possono circolare in autostrada', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '21', '11013', '204', '5', 'Le macchine agricole non possono circolare in autostrada', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '21', '11013', '205', '6', 'Non tutti i tipi di veicoli possono circolare in autostrada', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '21', '11013', '206', '7', 'L''uscita dei veicoli dalle autostrade avviene tramite corsie di decelerazione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '21', '11013', '207', '8', 'L''autostrada deve essere lateralmente recintata', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '21', '11013', '208', '9', 'Nelle autostrade possono circolare solo le autovetture', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '21', '11013', '209', '10', 'Nelle autostrade possono circolare tutti i tipi di motocicli', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '21', '11013', '210', '11', 'L''autostrada può comprendere piste ciclabili', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '21', '11013', '211', '12', 'Nelle autostrade possono circolare i ciclomotori e i motocicli con cilindrata fino a 125 cm3', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '21', '11013', '212', '13', 'Nelle autostrade possono circolare solo gli autoveicoli', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '21', '11013', '213', '14', 'Nelle autostrade possono circolare tutti i motocicli e i ciclomotori', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '22', '11005', '', '', 'Definizioni stradali: la strada extraurbana principale', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '22', '11005', '214', '1', 'Le strade extraurbane principali sono a carreggiate separate', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '22', '11005', '215', '2', 'Le strade extraurbane principali sono dotate di apposite aree di sosta', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '22', '11005', '216', '3', 'La strada extraurbana principale non ha incroci a raso che l''attraversano', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '22', '11005', '217', '4', 'Le strade extraurbane principali sono segnalate da cartelli stradali di inizio e fine', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '22', '11005', '218', '5', 'Nelle strade extraurbane principali possono circolare solo alcune categorie di veicoli a motore', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '22', '11005', '219', '6', 'Nelle strade extraurbane principali si possono trovare intersezioni (incroci) a livelli sfalsati', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '22', '11005', '220', '7', 'Le strade extraurbane principali sono provviste di corsie di accelerazione per l''ingresso', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '22', '11005', '221', '8', 'Sulle strade extraurbane principali è consentito circolare alla velocità massima di 130 km/h', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '22', '11005', '222', '9', 'Le strade extraurbane principali sono contraddistinte da segnaletica verde', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '22', '11005', '223', '10', 'Le strade extraurbane principali possono essere ad un''unica carreggiata', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '22', '11005', '224', '11', 'Sulle strade extraurbane principali è consentito circolare alla stessa velocità massima prevista per le autostrade', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '22', '11005', '225', '12', 'Sulle strade extraurbane principali i quadricicli leggeri possono transitare', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '22', '11005', '226', '13', 'Le strade extraurbane principali hanno una sola corsia per ogni senso di marcia', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '22', '11005', '227', '14', 'Sulle strade extraurbane principali i ciclomotori possono transitare', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '22', '11005', '228', '15', 'Nelle strade extraurbane principali non vi sono curve né dossi', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '22', '11005', '229', '16', 'Le strade extraurbane principali devono essere dotate di pannelli a messaggio variabile', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '23', '11023', '', '', 'Classificazione dei veicoli: i ciclomotori ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '23', '11023', '230', '1', 'I veicoli a due ruote, con motore termico fino a 50 cm3 di cilindrata e velocità massima di 45 km/h sono ciclomotori', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '23', '11023', '231', '2', 'I ciclomotori possono avere due o tre ruote', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '23', '11023', '232', '3', 'Non tutti i veicoli a motore a due ruote vengono classificati ciclomotori', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '23', '11023', '233', '4', 'I ciclomotori a due ruote non possono avere un motore termico di cilindrata superiore a 50 cm3', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '23', '11023', '234', '5', 'I ciclomotori sono soggetti a revisione dopo quattro anni dalla prima immatricolazione, successivamente ogni due anni', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '23', '11023', '235', '6', 'I ciclomotori non hanno più di due ruote', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '23', '11023', '236', '7', 'Le biciclette con pedalata assistita, dotate di motore elettrico ausiliario di potenza massima fino a 0,25 kW, sono definite ciclomotori', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '23', '11023', '237', '8', 'Si definiscono ciclomotori tutti i veicoli a due ruote di cilindrata superiore a 50 cm3', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '23', '11023', '238', '9', 'I ciclomotori, per essere definiti tali, devono essere muniti di cambio manuale', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '23', '11023', '239', '10', 'I ciclomotori possono circolare alla velocità di 60 km/h fuori dai centri urbani', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '23', '11023', '240', '11', 'I ciclomotori non sono soggetti a revisione periodica', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '24', '11025', '', '', 'Classificazione dei veicoli: i quadricicli a motore leggeri', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '24', '11025', '241', '1', 'I quadricicli leggeri alimentati a benzina hanno cilindrata del motore inferiore o pari a 50 cm3', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '24', '11025', '242', '2', 'I quadricicli sono veicoli a motore a quattro ruote', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '24', '11025', '243', '3', 'I quadricicli leggeri, ai fini della circolazione, sono assimilati ai ciclomotori', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '24', '11025', '244', '4', 'I quadricicli diversi da quelli leggeri possono essere guidati senza patente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '24', '11025', '245', '5', 'È consentito sostituire il dispositivo di scarico dei quadricicli leggeri per aumentare la velocità massima, purché non si aumenti la cilindrata del motore', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '24', '11025', '246', '6', 'È consentito aumentare la cilindrata dei quadricicli leggeri purché non si aumenti la velocità', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '24', '11025', '247', '7', 'È consentito sostituire il motore del quadriciclo leggero per aumentare la velocità massima oltre i 45 km/h', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '25', '11030', '', '', 'Classificazione dei veicoli: i motocicli', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '25', '11030', '248', '1', 'I motocicli sono veicoli a due ruote', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '25', '11030', '249', '2', 'I motocicli hanno cilindrata superiore a 50 cm3', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '25', '11030', '250', '3', 'I ciclomotori non rientrano nella categoria dei motocicli', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '25', '11030', '251', '4', 'I motocicli possono essere dotati di cambio automatico', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '25', '11030', '252', '5', 'I motocicli sono soggetti a revisione dopo quattro anni dalla prima immatricolazione, successivamente ogni due anni', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '25', '11030', '253', '6', 'Tutti i veicoli a motore con tre ruote sono dei motocicli', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '25', '11030', '254', '7', 'I motocicli sono veicoli a motore con più di due ruote', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '25', '11030', '255', '8', 'Il motociclo è un qualunque veicolo a due ruote', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '25', '11030', '256', '9', 'Si definiscono motocicli solo i veicoli a due ruote di cilindrata fino a 125 cm3', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '25', '11030', '257', '10', 'I motocicli hanno motore di cilindrata fino a 50 cm3 e sviluppano una velocità fino a 45 km/h', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '25', '11030', '258', '11', 'Ai fini della circolazione i quadricicli leggeri sono assimilati ai motocicli', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '26', '11024', '', '', ' Classificazione dei veicoli: i tricicli a motore ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '26', '11024', '259', '1', 'I tricicli a motore sono veicoli a motore a tre ruote simmetriche', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '26', '11024', '260', '2', 'I tricicli a motore sono veicoli con motore di cilindrata superiore a 50 cm3', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '26', '11024', '261', '3', 'I tricicli a motore sono veicoli a motore aventi una velocità massima per costruzione superiore a 45 km/h', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '26', '11024', '262', '4', 'I tricicli a motore sono soggetti a revisione dopo quattro anni dalla prima immatricolazione, successivamente ogni due anni', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '26', '11024', '263', '5', 'I tricicli a motore sono veicoli a motore che possono essere condotti senza patente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '26', '11024', '264', '6', 'I tricicli a motore sono costituiti da un motociclo e un sidecar', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '26', '11024', '265', '7', 'I tricicli a motore sono veicoli con motore di cilindrata inferiore a 50 cm3 e aventi velocità massima per costruzione fino a 45 km/h', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '26', '11024', '266', '8', 'I tricicli a motore non sono soggetti a revisione periodica', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '27', '11025', '', '', 'Classificazione dei veicoli: i quadricicli a motore', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '27', '11025', '267', '4', 'La potenza dei quadricicli non leggeri è sempre inferiore o uguale a 15 kW', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '27', '11025', '268', '5', 'I quadricicli sono soggetti a revisione periodica', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '27', '11025', '269', '8', 'I quadricicli non leggeri, ai fini della circolazione, sono equiparati ai ciclomotori', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '27', '11025', '270', '9', 'Per condurre i quadricicli non leggeri è sufficiente essere in possesso del certificato di idoneità alla guida dei ciclomotori', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '27', '11025', '271', '10', 'La guida dei quadricicli non leggeri è consentita già al compimento del quattordicesimo anno di età', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '27', '11025', '272', '11', 'I quadricicli non leggeri a manubrio e con carrozzeria aperta possono essere condotti senza casco', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '27', '11025', '273', '12', 'I quadricicli non sono soggetti a revisione periodica', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '28', '11031', '', '', 'Classificazione dei veicoli: gli autoveicoli', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '28', '11031', '274', '1', 'Gli autoveicoli sono veicoli a motore con almeno quattro ruote, esclusi i quadricicli', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '28', '11031', '275', '2', 'L''autoveicolo è un veicolo a motore con non meno di quattro ruote, eccetto i quadricicli', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '28', '11031', '276', '3', 'L''autobus viene classificato autoveicolo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '28', '11031', '277', '4', 'L''autocarro è un autoveicolo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '28', '11031', '278', '5', 'I quadricicli non sono autoveicoli', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '28', '11031', '279', '6', 'Gli autoveicoli sono tutti i veicoli a motore con quattro o più ruote, compresi i quadricicli', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '28', '11031', '280', '7', 'Gli autoveicoli sono tutti i veicoli con almeno due ruote', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '28', '11031', '281', '8', 'Gli autoveicoli sono tutti i veicoli con almeno quattro ruote, compresi quelli senza motore', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '28', '11031', '282', '9', 'Il triciclo a motore è un autoveicolo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '28', '11031', '283', '10', 'L''autoveicolo è un qualunque veicolo a motore', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '28', '11031', '284', '11', 'L''autoveicolo è un qualunque veicolo adibito al trasporto di merce', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '29', '11032', '', '', 'Classificazione dei veicoli: le autovetture ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '29', '11032', '285', '1', 'L''autovettura è un veicolo a motore con quattro ruote, destinato al trasporto di massimo nove persone, compreso il conducente', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '29', '11032', '286', '2', 'L''autovettura fa parte degli autoveicoli', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '29', '11032', '287', '3', 'L''autovettura non può avere più di nove posti, compreso il conducente', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '29', '11032', '288', '4', 'L''autobus non è un''autovettura', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '29', '11032', '289', '5', 'Le autovetture possono avere motore elettrico', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '29', '11032', '290', '6', 'Le autovetture sono soggette a revisione periodica', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '29', '11032', '291', '7', 'Si definiscono autovetture tutti i veicoli a motore che hanno la trazione posteriore', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '29', '11032', '292', '8', 'Le autovetture non possono trainare carrelli-appendice o rimorchi', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '29', '11032', '293', '9', 'Vengono definite autovetture tutti i veicoli con motore anteriore', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '29', '11032', '294', '10', 'L''autovettura è un veicolo con dieci posti in totale, compreso il conducente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '29', '11032', '295', '11', 'Si definisce autovettura il veicolo blindato e appositamente attrezzato per il trasporto di valori', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '29', '11032', '296', '12', 'L''autocarro è un''autovettura', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '29', '11032', '297', '13', 'L''autovettura può avere, al massimo, cinque posti', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '29', '11032', '298', '14', 'Le autovetture sono soggette a revisione ogni cinque anni', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '30', '11028', '', '', 'Classificazione dei veicoli: gli autocaravan (camper)', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '30', '11028', '299', '1', 'Le autocaravan (camper) sono veicoli aventi una speciale carrozzeria per essere adibiti al trasporto ed all''alloggio di persone', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '30', '11028', '300', '2', 'Le autocaravan (camper) sono veicoli aventi una speciale carrozzeria per essere adibiti al trasporto ed all''alloggio al massimo del numero di persone indicato nella carta di circolazione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '30', '11028', '301', '3', 'Le autocaravan (camper) sono veicoli attrezzati permanentemente per essere adibiti al trasporto ed all''alloggio di persone', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '30', '11028', '302', '4', 'Le autocaravan sono soggette a revisione periodica', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '30', '11028', '303', '5', 'Le autocaravan (camper) sono rimorchi', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '30', '11028', '304', '6', 'Le autocaravan (camper) sono veicoli destinati ai lavori agricoli', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '30', '11028', '305', '7', 'Le autocaravan (camper) sono veicoli destinati alla costruzione e alla manutenzione di opere stradali', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '30', '11028', '306', '8', 'Sulle autocaravan (camper) è possibile viaggiare in piedi, solo per brevi tratti', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '30', '11028', '307', '9', 'Le autocaravan sono soggette a revisione ogni cinque anni', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '31', '11033', '', '', 'Classificazione dei veicoli: i rimorchi e i semirimorchi', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '31', '11033', '308', '1', 'I rimorchi o i semirimorchi sono veicoli privi di motore, destinati ad essere trainati', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '31', '11033', '309', '2', 'L''autosnodato non è classificato rimorchio', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '31', '11033', '310', '3', 'L''autocaravan non è un rimorchio', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '31', '11033', '311', '4', 'Il caravan non è un rimorchio', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '31', '11033', '312', '5', 'I semirimorchi sono rimorchi privi di impianto frenante', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '31', '11033', '313', '6', 'I rimorchi sono muniti di silenziatore', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '32', '11027', '', '', ' Classificazione dei veicoli: i caravan (roulottes)', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '32', '11027', '314', '1', 'I caravan (roulottes) sono rimorchi attrezzati per essere adibiti ad alloggio', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '32', '11027', '315', '2', 'I caravan (roulottes) sono utilizzabili come alloggio solo con veicolo fermo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '32', '11027', '316', '3', 'I caravan (roulottes) sono muniti di targa', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '32', '11027', '317', '4', 'È possibile alloggiare nei caravan (roulottes) durante la circolazione', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '32', '11027', '318', '5', 'I caravan (roulottes) sono rimorchi destinati ai lavori agricoli', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '32', '11027', '319', '6', 'I caravan (roulottes) sono rimorchi destinati esclusivamente al trasporto di attrezzature turistiche e sportive', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '33', '27036', '', '', 'Rischio di ribaltamento', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '33', '27036', '320', '1', 'Nei caravan, per ridure il rischio di ribaltamenti iin curva, occore sistemare il carico degli oggetti pesanti in modo da mantenere il baricentro il più basso possibile', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '33', '27036', '321', '2', 'Nei caravan, per ridure il rischio di ribaltamento in curva, occore sistemare il carico in modo il più possibile equilibrato tra lato destro e sinistra', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '34', '27038', '', '', 'Rimorchi T.A.T.S.', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '34', '27038', '322', '1', 'I rimorchi T.A.T.S. sono destinati al transporto di attrezature che non devono costituire oggetto di commercio, ma essere impiegate solo per il tempo libero', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '35', '11033', '', '', 'Classificazione dei veicoli: i rimorchi leggeri  ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '35', '11033', '323', '1', 'I rimorchi leggeri sono quelli di massa massima a pieno carico fino a 750 kg', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '35', '11033', '324', '2', 'I rimorchi leggeri sono quelli di massa massima a pieno carico fino a 500 kg', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '36', '11026', '', '', 'Classificazione dei veicoli: le macchine operatrici e agricole ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '36', '11026', '325', '1', 'Le macchine agricole possono circolare su strada per il proprio trasferimento se immatricolate', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '36', '11026', '326', '2', 'Le macchine agricole possono essere a ruote o a cingoli', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '36', '11026', '327', '3', 'Le macchine agricole a ruote o a cingoli possono circolare su strada per il trasporto di prodotti agricoli per conto delle aziende agricole e forestali', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '36', '11026', '328', '4', 'Le macchine agricole possono essere semoventi o trainate', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '36', '11026', '329', '5', 'Le macchine agricole operatrici possono essere dotate di speciali attrezzature per l''esecuzione di operazioni agricole', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '36', '11026', '330', '6', 'Le macchine operatrici possono essere semoventi o trainate', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '36', '11026', '331', '7', 'Le macchine operatrici possono essere veicoli sgombraneve', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '36', '11026', '332', '8', 'Le macchine operatrici possono circolare ad una velocità superiore a 50 km/h', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '36', '11026', '333', '9', 'Le macchine operatrici a cingoli possono circolare ad una velocità superiore a 45 km/h', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '36', '11026', '334', '10', 'Le macchine operatrici semoventi possono trasportare fino a quattro passeggeri', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '36', '11026', '335', '11', 'Per condurre macchine operatrici è necessario essere titolari della patente di guida della categoria A', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '36', '11026', '336', '12', 'Le macchine agricole possono trasportare fino a quattro passeggeri', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '36', '11026', '337', '13', 'Sulle macchine agricole e sui loro rimorchi è consentito il trasporto di persone in piedi', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '36', '11026', '338', '14', 'Con la patente di categoria A si possono guidare tutte le macchine agricole', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '36', '11026', '339', '15', 'Con la patente di categoria B si possono guidare le macchine operatrici eccezionali', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '37', '11037', '', '', 'Doveri del conducente: divieto di spostare la segnaletica stradale', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '37', '11037', '340', '1', 'È vietato rimuovere la segnaletica stradale', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '37', '11037', '341', '2', 'È vietato spostare o imbrattare la segnaletica stradale', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '37', '11037', '342', '3', 'Spostare la segnaletica stradale può comportare pericolo grave per gli altri utenti della strada', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '37', '11037', '343', '4', 'È consentito spostare la segnaletica stradale, se non si tratta di segnali di pericolo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '37', '11037', '344', '5', 'È consentito spostare la segnaletica stradale, dopo aver avvisato gli organi di polizia', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '37', '11037', '345', '6', 'È consentito spostare la segnaletica stradale, se si tratta delle barriere di delimitazione o dei coni stradali', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '38', '11039', '', '', 'Doveri del conducente: divieto di lanciare oggetti dal veicolo e di sporcare la carreggiata', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '38', '11039', '346', '1', 'Gettare mozziconi di sigaretta accesi dai finestrini è molto pericoloso soprattutto se sopraggiungono veicoli a due ruote', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '38', '11039', '347', '2', 'Gettare mozziconi accesi dai finestrini in prossimità di boschi o aree verdi potrebbe causare incendi', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '38', '11039', '348', '3', 'È vietato lanciare dal finestrino mozziconi di sigaretta accesi', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '38', '11039', '349', '4', 'È vietato spargere fango o detriti sulle strade con le ruote sporche dei veicoli', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '38', '11039', '350', '5', 'Il conducente che si accorge che il suo veicolo perde olio deve compiere tutte le azioni necessarie per evitare pericoli per altri utenti', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '38', '11039', '351', '6', 'Il conducente che si accorga che il suo veicolo ha forti perdite di olio o carburante, deve immediatamente avvisare l''ente proprietario della strada o gli organi di polizia', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '38', '11039', '352', '7', 'Alla guida di un veicolo il conducente deve procedere a bassa velocità sopra le pozzanghere', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '38', '11039', '353', '8', 'Se, uscendo da un sentiero fangoso, si sporca notevolmente la strada, si può incorrere in una sanzione amministrativa', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '38', '11039', '354', '9', 'Per evitare di sporcare la carreggiata, bisogna pulire le ruote del veicolo, prima di uscire da un sentiero fangoso', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '38', '11039', '355', '10', 'Uscendo da un sentiero fangoso o da un cantiere, bisogna assicurarsi che durante la marcia non cada terriccio che possa sporcare la strada', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '38', '11039', '356', '11', 'È consentito lanciare la carta dal finestrino', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '38', '11039', '357', '12', 'Il conducente può lanciare oggetti dal finestrino sulla banchina', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '38', '11039', '358', '13', 'Il conducente può superare i veicoli in coda accelerando bruscamente e cambiando ripetutamente corsia', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '38', '11039', '359', '14', 'È consentito spostare i segnali stradali verticali temporanei per parcheggiare il veicolo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '38', '11039', '360', '15', 'Su una strada ricoperta da brecciolino il conducente può circolare a velocità sostenuta', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '39', '34015', '', '', 'Comportamenti vietati sulle strade ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '39', '34015', '361', '1', 'È vietato danneggiare le opere e gli impianti stradali', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '39', '34015', '362', '2', 'È vietato gettare oggetti e rifiuti dai veicoli in movimento', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '39', '34015', '363', '3', 'È vietato sporcare la carreggiata depositando rifiuti', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '39', '34015', '364', '4', 'È vietato immettersi sulle strade con le ruote del veicolo sporche di fango', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '39', '34015', '365', '5', 'Su tutte le strade è vietato esercitarsi alla guida', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '39', '34015', '366', '6', 'Su tutte le strade sono vietati gli avvisatori acustici (clacson, trombe)', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '39', '34015', '367', '7', 'Su tutte le strade è vietato il transito dei veicoli che superano 10 m di lunghezza', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '39', '34015', '368', '8', 'È vietato parcheggiare nelle zone residenziali', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '40', '11038', '', '', 'Doveri del conducente: divieto di gareggiare in velocità', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '40', '11038', '369', '1', 'Sulle strade è vietato gareggiare in velocità', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '40', '11038', '370', '2', 'Il conducente che partecipa a gare di velocità non autorizzate su aree pubbliche è punito con la reclusione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '40', '11038', '371', '3', 'È punito con la reclusione il conducente che gareggia in velocità su strade pubbliche', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '40', '11038', '372', '4', 'È punito con la reclusione chi scommette su veicoli che partecipano a gare di velocità non autorizzate su strade pubbliche', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '40', '11038', '373', '5', 'Le competizioni sportive con veicoli su aree pubbliche sono consentite senza autorizzazione', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '40', '11038', '374', '6', 'Sulle strade pubbliche sono consentite le gare di velocità, anche se non autorizzate, dalle ore 23.00 alle ore 6.00', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '40', '11038', '375', '7', 'Sono consentite le gare di velocità, anche se non autorizzate, purché partecipino solo due autovetture', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '40', '11038', '376', '8', 'Sulle strade pubbliche sono consentite le gare di velocità, anche se non autorizzate, tra motocicli e non tra autoveicoli', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '41', '11040', '', '', 'Comportamenti da tenere verso gli altri utenti della strada', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '41', '11040', '377', '1', 'Il conducente di un veicolo, oltre a rispettare le norme specifiche della circolazione, deve tenere un rapporto corretto e tollerante verso gli altri utenti della strada', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '41', '11040', '378', '2', 'Quando si avvicina ad un incrocio, il conducente deve sempre usare la massima prudenza, anche quando gli altri conducenti devono dargli la precedenza', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '41', '11040', '379', '3', 'Il conducente di un veicolo deve tutelare la propria e l''altrui sicurezza', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '41', '11040', '380', '4', 'Il conducente di un veicolo deve rispettare gli agenti del traffico e collaborare con essi', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '41', '11040', '381', '5', 'Il conducente di un veicolo deve cercare di prevedere il comportamento degli altri utenti', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '41', '11040', '382', '6', 'Il conducente di un veicolo deve essere paziente e tollerante anche in caso di rallentamento della circolazione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '41', '11040', '383', '7', 'Il conducente di un veicolo non deve fare sempre affidamento sul comportamento degli altri, che può non essere corretto', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '41', '11040', '384', '8', 'Il conducente di un veicolo non può pretendere di passare per primo quando sorge il dubbio che non gli venga concessa la precedenza', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '41', '11040', '385', '9', 'Il diritto di precedenza non autorizza il conducente ad attraversare l''incrocio senza usare prudenza', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '41', '11040', '386', '10', 'Il conducente di un veicolo di grandi dimensioni ha, di norma, la precedenza sul conducente di un veicolo più piccolo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '41', '11040', '387', '11', 'Il conducente di un veicolo può approfittare della potenza del proprio veicolo per non dare la precedenza a chi spetta', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '41', '11040', '388', '12', 'Il conducente di un veicolo pesante può tenere un comportamento che è causa di insofferenza negli altri', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '41', '11040', '389', '13', 'Il conducente di un veicolo deve rispettare gli agenti del traffico solo se sono in divisa', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '41', '11040', '390', '14', 'Il conducente di un veicolo può fare a meno di usare la massima prudenza quando si avvicina ad un incrocio in cui ha il diritto di precedenza', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '41', '11040', '391', '15', 'Il conducente deve dare segni di impazienza, se il veicolo che sta davanti procede troppo lentamente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '42', '11036', '', '', 'Doveri del conducente: entrando in un centro abitato', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '42', '11036', '392', '1', 'Entrando in un centro abitato bisogna tenere conto che può cambiare spesso la precedenza agli incroci', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '42', '11036', '393', '2', 'Entrando in un centro abitato, bisogna tenere conto che il flusso della circolazione viene spesso intralciato o interrotto da veicoli che sono fermi, che partono o che cambiano direzione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '42', '11036', '394', '3', 'Entrati in un centro abitato, bisogna tenere conto che gli altri utenti della strada (quali pedoni e ciclisti) si mescolano maggiormente con il traffico motorizzato', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '42', '11036', '395', '4', 'In un centro abitato bisogna essere più attenti, prudenti e tolleranti, a causa del maggior numero di utenti che interagiscono nell''ambiente urbano', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '42', '11036', '396', '5', 'Entrando in un centro abitato, bisogna continuare a mantenere la concentrazione alla guida anche se potrebbero aumentare i motivi per distrarsi', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '42', '11036', '397', '6', 'Entrando in un centro abitato, bisogna considerare che è possibile sostare in corrispondenza di incroci non regolati', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '42', '11036', '398', '7', 'In un centro abitato è sempre consentita la marcia per file parallele', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '42', '11036', '399', '8', 'In un centro abitato, bisogna considerare che i pedoni e i diversamente abili devono sempre dare la precedenza ai veicoli a motore', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '43', '11034', '', '', 'Doveri del conducente: parti della strada da lasciare libere', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '43', '11034', '400', '1', 'Bisogna lasciare liberi gli incroci in caso di traffico intenso', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '43', '11034', '401', '2', 'Bisogna lasciare liberi i passaggi a livello e i binari del tram anche in caso di traffico intenso', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '43', '11034', '402', '3', 'Bisogna lasciare liberi gli attraversamenti pedonali e quelli ciclabili in caso di formazione di code di veicoli', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '43', '11034', '403', '4', 'Bisogna lasciare libere le immissioni stradali (incroci, confluenze, ingressi dei parcheggi, ecc.) anche in caso di traffico intenso', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '43', '11034', '404', '5', 'Bisogna lasciare liberi solo gli incroci regolati da semaforo in caso di formazione di code di veicoli', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '43', '11034', '405', '6', 'Bisogna lasciare liberi i cavalcavia in caso di traffico intenso', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '43', '11034', '406', '7', 'Bisogna lasciare libere le gallerie in caso di formazione di code di veicoli', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '43', '11034', '407', '8', 'Bisogna lasciare liberi gli ingressi delle abitazioni private in caso di formazione di code di veicoli', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '43', '11034', '408', '9', 'Bisogna lasciare liberi i sottovia in caso di traffico intenso', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '44', '11035', '', '', 'Doveri del conducente: procedendo in fila con altri veicoli', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '44', '11035', '409', '1', 'Quando si viaggia in fila con altri veicoli, non bisogna limitarsi a osservare solamente il veicolo che precede, ma occorre porre attenzione anche in lontananza, per controllare la situazione del traffico', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '44', '11035', '410', '2', 'Per agevolare il traffico, è opportuno lasciare liberi gli sbocchi delle strade laterali quando si viaggia dietro ad una fila di veicoli', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '44', '11035', '411', '3', 'Quando si viaggia in fila con altri veicoli, bisogna mantenere ugualmente la distanza di sicurezza', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '44', '11035', '412', '4', 'Per una maggiore sicurezza, quando si viaggia in fila con altri veicoli, è opportuno non avvicinarsi troppo al veicolo che precede', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '44', '11035', '413', '5', 'Per una maggiore sicurezza, quando nei centri urbani si viaggia in fila con altri veicoli, è opportuno tenere sempre accese le luci di emergenza', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '44', '11035', '414', '6', 'Quando si viaggia in fila con altri veicoli è consentito distrarsi se si è a bassa velocità', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '44', '11035', '415', '7', 'Quando si viaggia dietro in fila con altri veicoli, è opportuno spostarsi il più possibile a sinistra, per creare una corsia libera a destra', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '44', '11035', '416', '8', 'Quando si viaggia in fila con altri veicoli, è opportuno spostarsi al centro della carreggiata, per controllare la situazione del traffico', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '44', '11035', '417', '9', 'Anche in condizioni di scarsa visibilità quando si viaggia dietro ad un veicolo che solleva polvere, conviene sorpassarlo subito', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '45', '11041', '', '', 'Comportamenti da tenere verso pedoni, ciclisti e motociclisti', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '45', '11041', '418', '1', 'Un incidente, anche a bassa velocità, potrebbe creare gravi problemi fisici a pedoni, ciclisti e motociclisti, se coinvolti', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '45', '11041', '419', '2', 'Bisogna controllare frequentemente nello specchietto retrovisore se sopraggiunge un ciclista o un motociclista', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '45', '11041', '420', '3', 'Il conducente, dopo aver posteggiato il veicolo al lato della carreggiata, prima di aprire lo sportello deve controllare che non sopraggiungano ciclisti o motociclisti', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '45', '11041', '421', '4', 'I montanti interni degli autoveicoli condizionano la visibilità', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '45', '11041', '422', '5', 'I montanti interni degli autoveicoli riducono la possibilità di vedere un ciclista o un motociclista', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '45', '11041', '423', '6', 'Un incidente a bassa velocità non crea problemi di incolumità ai conducenti coinvolti', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '45', '11041', '424', '7', 'Il conducente che scende da un veicolo in sosta non deve controllare se sopraggiungono ciclisti', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '45', '11041', '425', '8', 'Nello specchietto retrovisore il conducente deve controllare solo se sopraggiungono autocarri o autobus', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '45', '11041', '426', '9', 'Il conducente di un motociclo può fare lo slalom tra i veicoli fermi al semaforo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '45', '11041', '427', '10', 'Il conducente del motociclo può parcheggiare sul marciapiede anche se non vi sono appositi spazi di posteggio', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '45', '11041', '428', '11', 'Il conducente del motociclo può circolare sul marciapiede se il traffico sulla carreggiata è bloccato', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '46', '11042', '', '', 'Comportamenti da tenere verso gli utenti deboli della strada', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '46', '11042', '429', '1', 'Bisogna usare maggiore prudenza nei confronti delle persone diversamente abili', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '46', '11042', '430', '2', 'Bisogna usare maggiore prudenza nei confronti delle persone anziane', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '46', '11042', '431', '3', 'Bisogna usare maggiore prudenza nei confronti dei bambini che attraversano la strada', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '46', '11042', '432', '4', 'Bisogna usare maggiore prudenza nei confronti dei conducenti che dimostrano una guida incerta', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '46', '11042', '433', '5', 'Bisogna usare maggiore prudenza nei confronti dei conducenti di veicoli adattati per persone diversamente abili', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '46', '11042', '434', '6', 'Bisogna usare maggiore prudenza nei confronti delle persone in bicicletta e di quelle con scarsa capacità di movimento', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '46', '11042', '435', '7', 'Bisogna usare particolare prudenza nei confronti dei conducenti che hanno conseguito la patente di guida in uno Stato estero', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '46', '11042', '436', '8', 'Bisogna usare maggiore prudenza nei confronti dei conducenti di autoveicoli elettrici', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '46', '11042', '437', '9', 'Bisogna usare maggiore prudenza nei confronti dei conducenti di taxi', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '46', '11042', '438', '10', 'Bisogna usare maggiore prudenza nei confronti dei conducenti di tricicli a motore', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '46', '11042', '439', '11', 'Bisogna usare maggiore prudenza nei confronti dei pedoni muniti di bastone bianco, ma non di quelli con bastone bianco-rosso', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '47', '11043', '', '', 'Comportamenti da tenere verso le persone anziane', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '47', '11043', '440', '1', 'Quando le persone anziane attraversano la carreggiata bisogna tenere presente che potrebbero non fare adeguata attenzione ai veicoli', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '47', '11043', '441', '2', 'Quando le persone anziane attraversano la carreggiata bisogna tenere presente che potrebbero fermarsi improvvisamente al centro della strada', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '47', '11043', '442', '3', 'Quando le persone anziane attraversano la carreggiata bisogna tenere presente che potrebbero non guardare né a destra, né a sinistra', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '47', '11043', '443', '4', 'Quando le persone anziane attraversano la carreggiata bisogna tenere presente che, data la loro età, non reagiscono prontamente alle situazioni di pericolo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '47', '11043', '444', '5', 'Quando le persone anziane attraversano la carreggiata non bisogna avvicinarsi troppo con il veicolo, per evitare che si sentano minacciati e reagiscano con movimenti pericolosi', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '47', '11043', '445', '6', 'Le persone anziane che attraversano la carreggiata sono esposte a maggiori pericoli, perché le loro capacità visive e uditive non sono più eccellenti', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '47', '11043', '446', '7', 'Le persone anziane che attraversano la carreggiata sono esposte a maggiori pericoli, perché potrebbero valutare male la velocità dei veicoli che sopraggiungono', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '47', '11043', '447', '8', 'Quando le persone anziane attraversano la carreggiata bisogna tenere presente che hanno i riflessi più rapidi e quindi attraversano con più accortezza', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '47', '11043', '448', '9', 'Quando le persone anziane attraversano la carreggiata bisogna tenere presente che, di solito, attraversano rapidamente e con prudenza', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '47', '11043', '449', '10', 'Quando le persone anziane attraversano la carreggiata bisogna tenere presente che, avendo più esperienza e un campo visivo maggiore, intuiscono meglio le situazioni di pericolo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '47', '11043', '450', '11', 'Quando le persone anziane attraversano la carreggiata bisogna lampeggiare e suonare il clacson per farle attraversare rapidamente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '47', '11043', '451', '12', 'Quando le persone anziane attraversano la carreggiata bisogna dare la precedenza solo a coloro che attraversano da sinistra a destra', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '48', '11044', '', '', 'Comportamenti da tenere verso persone munite di bastone bianco (non vedenti) o bianco-rosso (sordociechi)', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '48', '11044', '452', '1', 'Il conducente deve fermarsi e dare la precedenza al pedone non vedente con il bastone bianco che si accinge ad attraversare la strada fuori dalle strisce pedonali', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '48', '11044', '453', '2', 'Il conducente deve fermarsi e facilitare l''attraversamento al pedone non vedente con il bastone bianco che attraversa la strada fuori dalle strisce pedonali', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '48', '11044', '454', '3', 'Il conducente deve dare la precedenza al pedone non vedente con il bastone bianco che attraversa la strada fuori dalle strisce pedonali, anche se non è accompagnato da un cane guida', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '48', '11044', '455', '4', 'Il conducente deve fermarsi per concedere la precedenza al pedone sordocieco che, con il bastone bianco-rosso, attraversa la strada fuori dalle strisce pedonali', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '48', '11044', '456', '5', 'Il conducente deve dare la precedenza al pedone con il bastone bianco o bianco-rosso che attraversa la strada, anche se è fuori dalle strisce di un attraversamento pedonale', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '48', '11044', '457', '6', 'Il conducente deve fermarsi quando un pedone non vedente con il bastone bianco si accinge ad attraversare la strada, evitando di frenare bruscamente per non spaventarlo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '48', '11044', '458', '7', 'Il conducente deve accelerare per sgombrare rapidamente la carreggiata quando un pedone non vedente con il bastone bianco attraversa la strada senza cane guida', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '48', '11044', '459', '8', 'Il conducente deve passare per primo, in quanto ha la precedenza, quando un pedone non vedente con il bastone bianco attraversa la strada fuori dalle strisce pedonali', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '48', '11044', '460', '9', 'Il conducente, quando un pedone non vedente con il bastone bianco attraversa la strada fuori dalle strisce pedonali, deve dargli la precedenza solo se l''attraversamento pedonale dista oltre 100 metri', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '48', '11044', '461', '10', 'Il conducente deve dare la precedenza a un pedone non vedente che attraversa la strada fuori dalle strisce pedonali, solo se munito di bastone bianco-rosso', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '48', '11044', '462', '11', 'Il conducente deve prevedere che un pedone non vedente con il bastone bianco che attraversa la strada fuori dalle strisce pedonali camminerà rapidamente e senza incertezza', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '48', '11044', '463', '12', 'Se un pedone con bastone bianco attraversa la strada fuori dalle strisce pedonali, bisogna suonare il clacson per sollecitarlo ad attraversare rapidamente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '49', '11046', '', '', 'Comportamenti a salvaguardia dei bambini', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '49', '11046', '464', '1', 'Quando vi sono bambini fermi al semaforo bisogna usare prudenza, in quanto potrebbero attraversare improvvisamente la strada appena la luce del semaforo veicolare passa dal verde al giallo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '49', '11046', '465', '2', 'Quando vi sono bambini fermi al semaforo bisogna usare prudenza, in quanto potrebbero attraversare velocemente la strada anche con il rosso, ritenendo che duri troppo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '49', '11046', '466', '3', 'Quando vi sono bambini fermi al semaforo bisogna usare prudenza, perché può accadere che si spingano a vicenda, finendo sulla carreggiata', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '49', '11046', '467', '4', 'Quando vi sono bambini fermi al semaforo bisogna usare prudenza, perché potrebbero scendere dal marciapiede, invadendo la carreggiata, quando ancora per loro il semaforo è disposto al rosso', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '49', '11046', '468', '5', 'Quando vi sono bambini fermi al semaforo, bisogna tenere presente che possono attraversare la strada solo se accompagnati da un adulto', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '49', '11046', '469', '6', 'I bambini, al semaforo, possono attraversare la strada solo dopo aver ricevuto un gesto di consenso da parte degli automobilisti', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '49', '11046', '470', '7', 'Quando vi sono bambini fermi al semaforo, si può essere certi che attraversino la strada solo quando il semaforo pedonale abbia la luce verde accesa', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '49', '11046', '471', '8', 'Quando vi sono bambini fermi al semaforo, si può essere certi che non attraverseranno la strada se il semaforo è a luce gialla lampeggiante', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '50', '11047', '', '', 'Comportamenti da tenere in vicinanza delle piazzole di fermata di autobus per scolari', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '50', '11047', '472', '1', 'Alla guida di un veicolo, in vicinanza di una piazzola di fermata di autobus che trasporta scolari, occorre fare attenzione ai bambini che potrebbero aver iniziato ad attraversare la carreggiata nascosti dall''autobus', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '50', '11047', '473', '2', 'Alla guida di un veicolo, in vicinanza di una piazzola per la fermata di autobus per scolari, occorre fare attenzione ai bambini, che potrebbero attraversare di corsa la strada per salire sullo scuolabus', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '50', '11047', '474', '3', 'Alla guida di un veicolo, in vicinanza di una piazzola di fermata di autobus per scolari, occorre prepararsi a frenare, nel caso in cui dei bambini attraversino improvvisamente la strada', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '50', '11047', '475', '4', 'Alla guida di un veicolo, in vicinanza di una piazzola di fermata di autobus per scolari, occorre essere prudenti e tenere conto dei comportamenti imprevedibili dei bambini', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '50', '11047', '476', '5', 'In vicinanza di una piazzola di fermata di autobus per scolari, può accadere che i bambini scendano dall''autobus senza fare attenzione ai veicoli che sopraggiungono', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '50', '11047', '477', '6', 'Alla guida di un veicolo, in vicinanza di una piazzola di fermata di autobus per scolari, bisogna rallentare particolarmente se vi è lo scuolabus', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '50', '11047', '478', '7', 'Alla guida di un veicolo, in vicinanza di una piazzola di fermata di autobus per scolari, non si deve rallentare, per evitare di ostacolare la circolazione degli altri veicoli', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '50', '11047', '479', '8', 'Alla guida di un veicolo, in vicinanza di una piazzola di fermata di autobus per scolari, occorre passare per primi, avendo sempre la precedenza sui bambini che attraversano la strada', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '50', '11047', '480', '9', 'Alla guida di un veicolo, in vicinanza di una piazzola di fermata di autobus per scolari, occorre suonare per invitare i bambini che attraversano sulle strisce a dare la precedenza', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '50', '11047', '481', '10', 'Alla guida di un veicolo, in vicinanza di una piazzola di fermata di autobus per scolari, si deve rallentare solo se vi sono bambini che attraversano', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '50', '11047', '482', '11', 'Alla guida di un veicolo, in vicinanza di una piazzola di fermata di autobus per scolari, occorre fare attenzione allo scuolabus, che dopo la fermata farà sicuramente retromarcia', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '50', '11047', '483', '12', 'Alla guida di un veicolo, in vicinanza di una piazzola di fermata di autobus per scolari, non si può proseguire nella marcia se le portiere dello scuolabus sono ancora aperte', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '50', '11047', '484', '13', 'Alla guida di un veicolo, in vicinanza di una piazzola di fermata di autobus per scolari, non è necessario rallentare se lo scuolabus è fermo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '51', '11049', '', '', 'Comportamenti da tenere quando pedoni o ciclisti occupano la carreggiata', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '51', '11049', '485', '1', 'Quando la carreggiata è occupata da ciclisti o pedoni, per sorpassarli è prudente mantenere un''adeguata distanza di sicurezza laterale', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '51', '11049', '486', '2', 'Quando la carreggiata è occupata da ciclisti o pedoni, non è prudente sorpassarli se non si può rispettare una distanza laterale sufficiente', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '51', '11049', '487', '3', 'Quando la carreggiata extraurbana è occupata da ciclisti o pedoni, è consigliabile suonare il clacson prima di sorpassarli', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '51', '11049', '488', '4', 'Il conducente che intende sorpassare ciclisti o pedoni che occupano la carreggiata deve usare molta prudenza', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '51', '11049', '489', '5', 'Poiché la marcia dei ciclisti a volte avviene con leggeri sbandamenti, il conducente di un veicolo deve tener conto di tale particolarità, in caso di sorpasso', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '51', '11049', '490', '6', 'Il conducente che intende sorpassare ciclisti o pedoni che occupano la carreggiata deve mantenere una distanza laterale di almeno 2 metri', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '51', '11049', '491', '7', 'È vietato sorpassare ciclisti o pedoni che occupano la carreggiata', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '51', '11049', '492', '8', 'Il conducente, di notte, non deve dare la precedenza ai pedoni che attraversano sulle strisce pedonali', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '51', '11049', '493', '9', 'Quando la carreggiata è occupata da ciclisti o pedoni, si devono sorpassare senza superare la velocità di 30 km/h', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '52', '11050', '', '', 'Comportamenti da tenere verso donne in gravidanza o che spingono una carrozzina per bambini', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '52', '11050', '494', '1', 'Il conducente deve consentire ad una donna incinta di attraversare la strada senza metterle fretta', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '52', '11050', '495', '2', 'Bisogna usare maggiore prudenza nei confronti delle donne in stato di gravidanza che attraversano la strada', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '52', '11050', '496', '3', 'Bisogna usare maggiore prudenza nei confronti dei pedoni che spingono una carrozzina per bambini', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '52', '11050', '497', '4', 'Quando si parcheggia un veicolo è vietato ostruire eventuali scivoli del marciapiede per non impedire il passaggio di pedoni che spingono una carrozzina per bambini', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '52', '11050', '498', '5', 'Quando una donna in stato di gravidanza attraversa la carreggiata non bisogna avvicinarsi troppo con il veicolo, per evitare di spaventarla', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '52', '11050', '499', '6', 'Quando una donna in stato di gravidanza attraversa la carreggiata bisogna tenere presente che ha il passo più rapido e quindi attraversa velocemente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '52', '11050', '500', '7', 'Quando un pedone che spinge una carrozzina per bambini attraversa la carreggiata bisogna tenere presente che, di solito, attraversa rapidamente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '52', '11050', '501', '8', 'È consentito posteggiare un veicolo davanti agli scivoli del marciapiede solo dalle ore 22.00 alle ore 8.00', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '52', '11050', '502', '9', 'Quando una donna in stato di gravidanza attraversa la carreggiata bisogna darle la precedenza solo se attraversa da sinistra a destra', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '52', '11050', '503', '10', 'Quando un pedone che spinge la carrozzina per bambini attraversa la carreggiata bisogna dargli la precedenza solo se attraversa da sinistra a destra', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '52', '11050', '504', '11', 'È consentito lasciare il veicolo in sosta sul marciapiede senza lasciare spazio ai pedoni che spingono una carrozzina per bambini', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '53', '11046', '', '', 'Comportamenti a salvaguardia dei bambini', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '53', '11046', '505', '5', 'Prima di impegnare un incrocio, dopo che è scattata la luce verde del semaforo, il conducente deve attendere che i pedoni abbiano finito di attraversare la strada', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '54', '11045', '', '', 'Comportamenti da tenere nelle vicinanze di un attraversamento pedonale', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '54', '11045', '506', '1', 'In vicinanza di un attraversamento pedonale, il conducente deve considerare che il pedone, nell''attraversare la strada, può diventare insicuro e ritornare improvvisamente indietro', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '54', '11045', '507', '2', 'In vicinanza di un attraversamento pedonale, il conducente deve considerare che le persone anziane solitamente attraversano lentamente e con difficoltà', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '54', '11045', '508', '3', 'In vicinanza di un attraversamento pedonale, il conducente deve considerare che le reazioni dei bambini che attraversano sono imprevedibili e, quindi, possono creare pericolo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '54', '11045', '509', '4', 'In vicinanza di un attraversamento pedonale, il conducente deve considerare che i bambini potrebbero avanzare sulle strisce, per poi tornare indietro improvvisamente, senza motivo apparente', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '54', '11045', '510', '5', 'In vicinanza di un attraversamento pedonale, il conducente deve considerare che il pedone potrebbe fermarsi improvvisamente sulle strisce dell''attraversamento pedonale', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '54', '11045', '511', '6', 'In vicinanza di un attraversamento pedonale, il conducente deve considerare che i pedoni, in special modo i bambini, possono sbucare all''improvviso tra i veicoli parcheggiati', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '54', '11045', '512', '7', 'In vicinanza di un attraversamento pedonale, il conducente deve considerare che i veicoli parcheggiati in prossimità delle strisce impediscono la visuale dei pedoni', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '54', '11045', '513', '8', 'In vicinanza di un attraversamento pedonale, il conducente deve aspettarsi dai bambini una reazione rapida e un comportamento corretto', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '54', '11045', '514', '9', 'In vicinanza di un attraversamento pedonale, i pedoni devono fare un cenno con la mano prima di iniziare l''attraversamento', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '54', '11045', '515', '10', 'In vicinanza di un attraversamento pedonale, il conducente deve considerare che un pedone munito di bastone bianco ha più esperienza su come attraversare la carreggiata', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '54', '11045', '516', '11', 'In vicinanza di un attraversamento pedonale, il conducente deve arrestarsi se il pedone che attraversa è munito di bastone bianco-rosso; deve solo rallentare se, invece, è munito di bastone bianco', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '54', '11045', '517', '12', 'In vicinanza di un attraversamento pedonale, il conducente deve accelerare quel tanto che basta per passare davanti al pedone che attraversa', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '54', '11045', '518', '13', 'In vicinanza di un attraversamento pedonale, il conducente deve considerare che se il pedone si è fermato in mezzo alle strisce dell''attraversamento significa che ha rinunciato al suo diritto di passare', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '54', '11045', '519', '14', 'In vicinanza di un attraversamento pedonale, il conducente deve considerare che i pedoni devono attraversare la strada affiancati, in modo da impiegare meno tempo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '55', '11048', '', '', 'Comportamenti da tenere in vicinanza di un posteggio riservato ai taxi', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '55', '11048', '520', '1', 'Il conducente, in vicinanza di un posteggio riservato ai taxi, deve rallentare ed eventualmente fermarsi se vi sono pedoni che attraversano la strada, senza guardare', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '55', '11048', '521', '2', 'Il conducente, in vicinanza di un posteggio riservato ai taxi, deve fare attenzione ai pedoni che salgono o scendono dal taxi', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '55', '11048', '522', '3', 'Il conducente di un veicolo diverso dai taxi non può sostare nei posteggi riservati ai taxi', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '55', '11048', '523', '4', 'Il conducente, in vicinanza di un posteggio riservato ai taxi, deve fare attenzione ai pedoni, che possono attraversare improvvisamente la strada senza le dovute precauzioni', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '55', '11048', '524', '5', 'Il conducente, in vicinanza di un posteggio riservato ai taxi, deve dare la precedenza ai taxi che s''immettono sulla strada', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '55', '11048', '525', '6', 'Il conducente, in vicinanza di un posteggio riservato ai taxi, deve dare la precedenza ai taxi in partenza, qualora s''immettano dal lato sinistro della carreggiata', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '55', '11048', '526', '7', 'Il conducente di un veicolo diverso dai taxi può sostare nei posteggi riservati ai taxi dalle ore 20.00 alle ore 8.00', 'F');
