INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '374', '17012', '', '', 'Semaforo per i veicoli', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '374', '17012', '3429', '1', 'Il segnale luminoso in figura è un semaforo per i veicoli ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '374', '17012', '3430', '2', 'Il semaforo in figura è posto, di norma, in un incrocio', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '374', '17012', '3431', '3', 'Il semaforo in figura serve, di norma, a regolare il passaggio dei veicoli in un incrocio', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '374', '17012', '3432', '4', 'Il semaforo in figura, con luce gialla fissa accesa, consente l''attraversamento dell''incrocio, se lo si è già impegnato ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '374', '17012', '3433', '5', 'Il semaforo in figura non vale per i veicoli in servizio di linea per trasporto di persone', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '374', '17012', '3434', '6', 'Il segnale luminoso in figura è un semaforo che non vale per le biciclette ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '374', '17012', '3435', '7', 'Il semaforo in figura si trova in tutti i caselli autostradali ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '374', '17012', '3436', '8', 'Il semaforo in figura si trova in vicinanza dei binari ferroviari ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '374', '17012', '3437', '9', 'Il semaforo in figura, con luce gialla lampeggiante accesa, non consente il transito dei veicoli ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '375', '17001', '', '', ' Luce rossa del semaforo', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '375', '17001', '3438', '1', 'In presenza della luce semaforica rossa i veicoli devono arrestarsi ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '375', '17001', '3439', '2', 'Nei semafori sistemati in verticale la luce rossa si trova in alto, in quelli posti in orizzontale si trova a sinistra ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '375', '17001', '3440', '3', 'Nel semaforo la luce rossa può essere di dimensioni più grandi delle altre', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '375', '17001', '3441', '4', 'Quando è accesa la luce rossa del semaforo bisogna arrestarsi prima della striscia trasversale d''arresto', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '375', '17001', '3442', '5', 'Durante il periodo di accensione della luce rossa i veicoli non devono superare la striscia di arresto', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '375', '17001', '3443', '6', 'La luce rossa accesa del semaforo consente di svoltare a destra con prudenza, dando precedenza ai pedoni che attraversano la strada ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '375', '17001', '3444', '7', 'La luce rossa accesa del semaforo consente di ripartire lentamente quando appare il giallo per gli altri veicoli ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '375', '17001', '3445', '8', 'La luce rossa accesa del semaforo consente di impegnare l''incrocio con molta prudenza a condizione che non ci siano altri veicoli o pedoni ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '375', '17001', '3446', '9', 'La luce rossa accesa del semaforo consente l''attraversamento dell''incrocio, purché sia libero ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '375', '17001', '3447', '10', 'La luce rossa accesa del semaforo obbliga ad affrettarsi per liberare velocemente l''incrocio ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '376', '17002', '', '', 'Luce verde del semaforo', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '376', '17002', '3448', '1', 'Quando è accesa la luce verde del semaforo in figura è possibile svoltare a sinistra, dando la precedenza ai veicoli che arrivano di fronte ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '376', '17002', '3449', '2', 'Quando è accesa la luce verde del semaforo in figura si può svoltare a destra ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '376', '17002', '3450', '3', 'Quando è accesa la luce verde del semaforo in figura si può proseguire diritto ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '376', '17002', '3451', '4', 'Quando è accesa la luce verde del semaforo in figura si può impegnare l''incrocio, soltanto avendo la certezza di poterlo sgomberare prima dell''accensione della luce rossa', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '376', '17002', '3452', '5', 'Quando è accesa la luce verde del semaforo in figura si può attraversare l''incrocio, usando prudenza ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '376', '17002', '3453', '6', 'Avvicinandosi ad un semaforo, che già da molto tempo ha la luce verde accesa, occorre proseguire con prudenza, pronti eventualmente a fermarsi se, quando si accende la luce gialla, non si è ancora                                                                                             impegnato l''incrocio', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '376', '17002', '3454', '7', 'Avvicinandosi ad un semaforo, che già da molto tempo ha la luce verde accesa, occorre accelerare, senza che vi sia necessità di guardare la luce del semaforo ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '376', '17002', '3455', '8', 'Quando è accesa la luce verde del semaforo in figura si può svoltare a sinistra, ma solo quando si accende anche la luce gialla', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '376', '17002', '3456', '9', 'Quando è accesa la luce verde del semaforo in figura, il conducente che svolta a sinistra ha la precedenza sui veicoli che provengono di fronte ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '376', '17002', '3457', '10', 'Quando è accesa la luce verde del semaforo in figura si può svoltare solo se la carreggiata è divisa in corsie', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '376', '17002', '3458', '11', 'Quando è accesa la luce verde del semaforo in figura si deve proseguire diritto ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '376', '17002', '3459', '12', 'Quando è accesa la luce verde del semaforo in figura si può proseguire diritto, dando però la precedenza ai pedoni ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '377', '17004', '', '', 'Luce gialla fissa del semaforo', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '377', '17004', '3460', '1', 'La luce gialla fissa si accende appena si spegne il verde ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '377', '17004', '3461', '2', 'La luce gialla fissa si spegne prima che si accenda il rosso', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '377', '17004', '3462', '3', 'La luce gialla fissa obbliga chi ha già impegnato l''incrocio a liberarlo al più presto ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '377', '17004', '3463', '4', 'La luce gialla fissa obbliga a fermarsi prima del punto di arresto, purché lo si possa fare senza creare pericolo ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '377', '17004', '3464', '5', 'La luce gialla fissa si accende quando il verde e il rosso sono spenti ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '377', '17004', '3465', '6', 'La luce gialla fissa consente in ogni caso di proseguire impegnando l''incrocio ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '377', '17004', '3466', '7', 'La luce gialla fissa obbliga il conducente a tornare indietro se ha già superato la striscia trasversale di arresto ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '377', '17004', '3467', '8', 'La luce gialla fissa ci obbliga ad arrestarci in ogni caso, senza impegnare l''incrocio ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '377', '17004', '3468', '9', 'La luce gialla fissa si accende quando è ancora accesa quella rossa ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '377', '17004', '3469', '10', 'La luce gialla fissa si spegne insieme con il verde', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '377', '17004', '3470', '11', 'La luce gialla fissa si accende insieme con il rosso', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '378', '17005', '', '', 'Luce gialla lampeggiante del semaforo', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '378', '17005', '3471', '1', 'È possibile trovare la luce gialla lampeggiante quando le altre sono spente ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '378', '17005', '3472', '2', 'Nel semaforo la luce gialla lampeggiante impone l''arresto sempre prima dell''incrocio ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '378', '17005', '3473', '3', 'La luce gialla lampeggiante insieme a quella rossa in figura indica pericolo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '379', '17008', '', '', 'Casi in cui è consentito il passaggio in presenza del semaforo per veicoli', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '379', '17008', '3474', '1', 'Incontrando il semaforo in figura è consentito il passaggio quando è accesa la luce verde ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '379', '17008', '3475', '2', 'Incontrando il semaforo in figura è consentito il passaggio quando si accende la luce gialla fissa, solo se non ci si può arrestare in condizioni di sicurezza prima dell''incrocio ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '379', '17008', '3476', '3', 'Incontrando il semaforo in figura è consentito il passaggio quando è accesa la luce gialla lampeggiante, usando però la massima prudenza e moderando la velocità ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '379', '17008', '3477', '4', 'Incontrando il semaforo in figura è consentito il passaggio quando esso è spento, usando però la massima prudenza ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '379', '17008', '3478', '5', 'Incontrando il segnale luminoso in figura è consentito il passaggio in caso di semaforo guasto, usando però la massima prudenza ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '379', '17008', '3479', '6', 'Incontrando il semaforo in figura è consentito il passaggio poco prima che si accenda la luce verde, purché sia già accesa quella rossa per gli altri veicoli ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '379', '17008', '3480', '7', 'Incontrando il semaforo in figura è consentito il passaggio quando si accende la luce rossa, usando la massima prudenza ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '379', '17008', '3481', '8', 'Incontrando il semaforo in figura è consentito il passaggio quando, pur essendo accesa la luce rossa, non vengono veicoli dalle altre direzioni ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '380', '17003/17013', '', '', 'Semafori di corsia', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '380', '17003/17013', '3482', '1', 'I segnali luminosi in figura sono semafori di corsia ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '380', '17003-17013', '3483', '2', 'Il semaforo in figura riguarda i veicoli che devono proseguire nella direzione della freccia ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '380', '17003-17013', '3484', '3', 'Nei semafori in figura le frecce verdi accese indicano le direzioni verso le quali si può proseguire ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '380', '17003/17013', '3485', '4', 'Il semaforo in figura consente di proseguire nella sola direzione della freccia verde ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '380', '17003/17013', '3486', '5', 'Nei semafori in figura le frecce rosse accese indicano che non si può proseguire nelle direzioni indicate ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '380', '17003/17013', '3487', '6', 'I semafori di corsia in figura, con freccia rossa accesa impongono l''arresto dei veicoli diretti nel senso  della freccia ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '380', '17003/17013', '3488', '7', 'Nei semafori in figura le frecce gialle fisse accese indicano di liberare  l''incrocio, qualora sia stato già impegnato, o di arrestarsi in condizioni di sicurezza ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '380', '17003/17013', '3489', '8', 'I semafori in figura, con freccia gialla fissa accesa, impongono di liberare immediatamente l''incrocio, se lo si è già impegnato ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '380', '17003/17013', '3490', '9', 'Nel semaforo in figura le frecce gialle lampeggianti accese indicano di attraversare l''incrocio con particolare prudenza ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '380', '17003/17013', '3491', '10', 'I semafori in figura, se spenti consentono di passare con particolare prudenza ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '380', '17003/17013', '3492', '11', 'I segnali luminosi in figura valgono anche per le biciclette ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '380', '17003/17013', '3493', '12', 'Nel semaforo in figura le frecce verdi accese, rivolte verso l''alto, indicano che si può proseguire in tutte le direzioni ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '380', '17003/17013', '3494', '13', 'Nel semaforo in figura le frecce rosse accese indicano di proseguire con prudenza', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '380', '17003/17013', '3495', '14', 'I segnali luminosi in figura sono semafori per i veicoli in servizio di linea per trasporto di persone ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '380', '17003/17013', '3496', '15', 'I segnali luminosi in figura sono semafori per i veicoli su rotaie (tram, treni) ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '380', '17003/17013', '3497', '16', 'I segnali luminosi in figura sono posti all''inizio di una galleria ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '380', '17003/17013', '3498', '17', 'I semafori in figura vengono utilizzati negli accessi ai ponti mobili ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '380', '17003/17013', '3499', '18', 'I semafori in figura regolano l''ingresso sul pontile d''imbarco di navi traghetto ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '380', '17003/17013', '3500', '19', 'I semafori in figura regolano l''attraversamento di un passaggio a livello ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '380', '17003/17013', '3501', '20', 'I segnali luminosi in figura sono semafori per corsie reversibili ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '381', '17007/17014', '', '', 'Semaforo per veicoli di trasporto pubblico (1)', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '381', '17007/17014', '3502', '1', 'Il segnale luminoso in figura indica un semaforo per i veicoli di trasporto pubblico ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '381', '17007/17014', '3503', '2', 'Il semaforo in figura vale per i veicoli in servizio di linea per trasporto di persone ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '381', '17007/17014', '3504', '3', 'Il semaforo in figura può avere la luce bianca orizzontale accesa ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '381', '17007/17014', '3505', '4', 'Il semaforo in figura impone l''arresto ai veicoli in servizio di linea per trasporto di persone quando è accesa la barra bianca in alto ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '381', '17007/17014', '3506', '5', 'Il semaforo in figura può avere la luce bianca verticale accesa ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '381', '17007/17014', '3507', '6', 'Il semaforo in figura consente ai veicoli di trasporto pubblico di persone di proseguire quando in basso è accesa la barra bianca ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '381', '17007/17014', '3508', '7', 'Il semaforo in figura è valido solo per i veicoli che marciano su rotaie (tram, treni) ', 'F');
