INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '390', '17018', '3615', '7', 'Il semaforo in figura non consente di superare la velocità indicata ', 'F');
