INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '390', '17018', '3617', '9', 'Il semaforo in figura indica di procedere con prudenza ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '390', '17018', '3618', '10', 'Il semaforo in figura, anche se spento, indica la velocità obbligatoria ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '390', '17018', '3619', '11', 'Il semaforo di onda verde in figura viene posto anche sulle autostrade ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '390', '17018', '3620', '12', 'Il semaforo di onda verde in figura riporta i limiti massimi di velocità delle autovetture, motocicli e autocarri ', 'F');
