INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '390', '17018', '3622', '14', 'Il semaforo di onda verde in figura è un semaforo per lavori in corso ', 'F');
