INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '396', '17024', '', '', 'Significato del suono prolungato del fischietto da parte dell’agente preposto al traffico', '');
