INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '396', '17024', '3669', '4', 'Il suono prolungato del fischietto da parte dell''agente preposto al traffico indica che occorre arrestarsi, se non si è ancora occupato l''incrocio ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '396', '17024', '3670', '5', 'Il suono prolungato del fischietto da parte dell''agente preposto al traffico indica che occorre tornare indietro ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '396', '17024', '3671', '6', 'Il suono prolungato del fischietto da parte dell''agente preposto al traffico serve per arrestare o deviare solo i veicoli che arrivano dalla sua destra ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '396', '17024', '3672', '7', 'Il suono prolungato del fischietto da parte dell''agente preposto al traffico significa che i veicoli fermi devono mettersi in marcia ', 'F');
