INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '400', '16003', '3700', '6', 'La doppia striscia continua in figura si può trovare ai bordi della strada, per segnalare i margini ', 'F');
