INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '432', '16028', '3916', '8', 'La segnaletica rappresentata in figura segnala la vicinanza di un incrocio regolato da Polizia locale ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '432', '16028', '3917', '9', 'La segnaletica rappresentata in figura delimita un parcheggio di autobus in servizio pubblico di linea', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '433', '16017', '', '', 'Iscrizioni regolamentari sulla pavimentazione ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '433', '16017', '3918', '1', 'È regolamentare trovare sulla pavimentazione la scritta P L, con croce di S. Andrea, per segnalare un passaggio a livello ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '433', '16017', '3919', '2', 'È regolamentare, nelle zone destinate alla fermata degli autobus in servizio pubblico di linea, trovare sulla pavimentazione la scritta BUS di colore giallo ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '433', '16017', '3920', '3', 'È regolamentare trovare sulla pavimentazione la scritta indicante il nome di una località, a completamento della freccia direzionale ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '433', '16017', '3921', '4', 'È regolamentare trovare sulla pavimentazione la scritta TAXI di colore giallo, per indicare zone riservate alla sosta dei taxi in servizio ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '433', '16017', '3922', '5', 'È regolamentare trovare sulla pavimentazione la scritta STOP, a completamento del relativo segnale verticale ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '433', '16017', '3923', '6', 'È regolamentare trovare sulla pavimentazione la scritta STOP, per completare il segnale verticale di DARE PRECEDENZA', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '433', '16017', '3924', '7', 'È regolamentare trovare sulla pavimentazione la scritta indicante una prossima stazione di servizio ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '433', '16017', '3925', '8', 'È regolamentare trovare sulla pavimentazione una scritta pubblicitaria che indica un distributore di carburante ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '433', '16017', '3926', '9', 'È regolamentare trovare sulla pavimentazione una scritta di incoraggiamento per i corridori ciclisti', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '433', '16017', '3927', '10', 'È regolamentare trovare sulla pavimentazione la scritta TAXI, di colore bianco, nei parcheggi per taxi ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '433', '16017', '3928', '11', 'È regolamentare trovare sulla pavimentazione la scritta GIOCHI, in vicinanza di giardini pubblici ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '434', '16017', '', '', 'Iscrizioni regolamentari sulla pavimentazione ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '434', '16017', '3929', '1', 'Quando si incontrano le strisce di figura dipinte sulla pavimentazione occorre moderare la velocità ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '435', '16026', '', '', 'Rallentatore di velocitàe', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '435', '16026', '3930', '1', 'Il dispositivo in figura è un dosso artificiale ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '435', '16026', '3931', '2', 'L''elemento in figura è un rallentatore di velocità installato sulla strada ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '435', '16026', '3932', '3', 'L''elemento in figura rappresenta un rallentatore di velocità ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '435', '16026', '3933', '4', 'L''elemento in figura è posto su un tratto di strada su cui è vietata la sosta  ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '435', '16026', '3934', '5', 'L''elemento in figura individua un attraversamento ciclabil ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '435', '16026', '3935', '6', 'L''elemento in figura individua un attraversamento pedonale ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '436', '16026', '', '', 'Strisce di delimitazione gialle che individuano un’area di parcheggio destinata a persone invalide', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '436', '16026', '3936', '1', 'Le strisce di delimitazione gialle in figura individuano un''area di parcheggio riservata a persone invalide ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '436', '16026', '3937', '2', 'Le strisce gialle diagonali in figura devono essere lasciate libere per consentire l''entrata e l''uscita dal veicolo delle persone invalide ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '436', '16026', '3938', '3', 'Le strisce gialle in figura delimitano un''area di parcheggio riservata ai taxi ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '436', '16026', '3939', '4', 'Le strisce gialle in figura delimitano un''area riservata al carico e allo scarico di merci  ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '437', '16026', '', '', 'La velocità consigliata', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '437', '16026', '3940', '1', 'L''iscrizione in figura indica la velocità consigliata', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '437', '16026', '3941', '2', 'In presenza dell''iscrizione in figura non è consentito il transito di veicoli con massa a pieno carico superiore a 50 quintali ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '438', '23002', '', '', 'Comportamenti vietati per non costituire pericolo o intralcio per la circolazione ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '438', '23002', '3942', '1', 'È vietato, in quanto costituisce pericolo o intralcio per la circolazione, tenersi sul margine sinistro della carreggiata per svoltare in una strada a destra', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '438', '23002', '3943', '2', 'È vietato, in quanto costituisce pericolo o intralcio per la circolazione, sostare sui binari tramviari', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '438', '23002', '3944', '3', 'È vietato, in quanto costituisce pericolo o intralcio per la circolazione, effettuare un cambiamento di direzione senza segnalare la manovra con sufficiente anticipo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '438', '23002', '3945', '4', 'È vietato, in quanto costituisce pericolo o intralcio per la circolazione, gareggiare in velocità', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '438', '23002', '3946', '5', 'È vietato, in quanto costituisce pericolo o intralcio per la circolazione, circolare a velocità troppo bassa senza giustificato motivo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '438', '23002', '3947', '6', 'È vietato, in quanto costituisce pericolo o intralcio per la circolazione, immettersi nel flusso della circolazione provenendo da strade laterali', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '438', '23002', '3948', '7', 'È vietato, in quanto costituisce pericolo o intralcio per la circolazione, regolare la velocità in relazione alle caratteristiche e condizioni delle strade', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '438', '23002', '3949', '8', 'È vietato, in quanto costituisce pericolo o intralcio per la circolazione, fermare momentaneamente un veicolo lungo il margine destro di una carreggiata a senso unico', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '439', '23011', '', '', 'Comportamenti necessari per non costituire pericolo o intralcio per la circolazione ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '439', '23011', '3950', '1', 'Per non costituire pericolo o intralcio per la circolazione, è necessario guidare in modo adeguato alle caratteristiche del veicolo, della strada e del traffico', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '439', '23011', '3951', '2', 'Per non costituire pericolo o intralcio per la circolazione, è necessario osservare non solo le norme sulla circolazione, ma anche le cautele dettate dal buonsenso', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '439', '23011', '3952', '3', 'Per non costituire pericolo o intralcio per la circolazione, è necessario unire all''applicazione delle fondamentali norme sulla circolazione un forte senso civico', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '439', '23011', '3953', '4', 'Per non costituire pericolo o intralcio per la circolazione, è necessario limitarsi ad osservare i limiti massimi di velocità', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '439', '23011', '3954', '5', 'Per non costituire pericolo o intralcio per la circolazione, è necessario procedere sempre a velocità costante', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '439', '23011', '3955', '6', 'Per non costituire pericolo o intralcio per la circolazione, è necessario dare, comunque, la precedenza a tutti i veicoli negli incroci', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '439', '23011', '3956', '7', 'Per non costituire pericolo o intralcio per la circolazione, è necessario limitarsi ad osservare i limiti minimi di velocità', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '440', '21017', '', '', 'Pericoli: discesa con il cambio in folle ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '440', '21017', '3957', '1', 'Se si percorre una lunga discesa con il cambio in folle e a motore spento, diventa difficoltoso muovere lo sterzo se il veicolo è dotato di servosterzo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '440', '21017', '3958', '2', 'Nei veicoli dotati di servofreno, se si percorre una lunga discesa con il cambio in folle e a motore spento, aumenta pericolosamente lo spazio di frenatura', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '440', '21017', '3959', '3', 'Nei veicoli dotati di servofreno, se si percorre una lunga discesa con il cambio in folle e a motore spento, diventa sempre più difficoltoso arrestare il veicolo ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '440', '21017', '3960', '4', 'Nei veicoli dotati di servofreno, se si percorre una lunga discesa con il cambio in folle e a motore spento, aumenta il rischio di provocare incidenti', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '440', '21017', '3961', '5', 'Se si percorre una lunga discesa con il cambio in folle e a motore spento, diventa difficoltoso frenare se il veicolo è dotato di servofreno', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '440', '21017', '3962', '6', 'Nei veicoli dotati di servosterzo, se si percorre una lunga discesa con il cambio in folle e a motore spento, diventa difficoltoso girare rapidamente lo sterzo per schivare un ostacolo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '440', '21017', '3963', '7', 'Se si percorre una lunga discesa con il cambio in folle e a motore spento, si ha un sovraccarico di corrente elettrica', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '440', '21017', '3964', '8', 'Se si percorre una lunga discesa con il cambio in folle e a motore spento, si può avere un''anomalia al differenziale', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '440', '21017', '3965', '9', 'Per una maggiore sicurezza, è conveniente percorrere le lunghe discese con il cambio in folle e a motore spento, risparmiando così anche carburante', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '440', '21017', '3966', '10', 'Se si percorre una lunga discesa con il cambio in folle e a motore spento, conviene estrarre la chiave di accensione del veicolo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '440', '21017', '3967', '11', 'Nei veicoli dotati di servofreno a depressione, se si percorre una lunga discesa con il cambio in folle e a motore spento, si ottiene la stessa forza frenante che si ha con il motore acceso', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '441', '21019', '', '', 'Pericoli: pallone che rotola sulla carreggiata ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '441', '21019', '3968', '1', 'In un centro abitato, se si vede un pallone rotolare sulla carreggiata, bisogna diminuire subito la velocità', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '441', '21019', '3969', '2', 'In un centro abitato, se si vede un pallone rotolare sulla carreggiata, bisogna rallentare per potersi fermare, se necessario', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '441', '21019', '3970', '3', 'In un centro abitato, se si vede un pallone rotolare sulla carreggiata, bisogna prevedere anche il possibile attraversamento della strada da parte di un bambino', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '441', '21019', '3971', '4', 'In un centro abitato, se si vede un pallone rotolare sulla carreggiata, bisogna rallentare progressivamente, per evitare di essere tamponati dal veicolo che segue', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '441', '21019', '3972', '5', 'In un centro abitato, se si vede un pallone rotolare sulla carreggiata, bisogna sterzare bruscamente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '441', '21019', '3973', '6', 'In una strada a doppio senso di circolazione, se si vede un pallone rotolare sulla carreggiata, bisogna scansarlo, anche invadendo la corsia opposta', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '441', '21019', '3974', '7', 'In un centro abitato, se si vede un pallone rotolare sulla carreggiata, non occorre rallentare, per evitare di essere tamponati dal veicolo che segue', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '441', '21019', '3975', '8', 'In un centro abitato, se si vede un pallone rotolare sulla carreggiata, bisogna azionare immediatamente la segnalazione luminosa di pericolo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '442', '21020', '', '', 'Pericoli: passare molto vicino ad un’auto parcheggiata ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '442', '21020', '3976', '1', 'Passare molto vicino ad un''auto parcheggiata, a bordo della quale si trovano ancora persone, può essere pericoloso, per la possibile apertura imprudente delle portiere', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '442', '21020', '3977', '2', 'Può essere pericoloso circolare troppo vicino alle auto parcheggiate, perché potrebbero impedire la vista di un disabile in carrozzella o di un pedone che spinge un passeggino per bambini, che sono in procinto di attraversare', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '442', '21020', '3978', '3', 'Passare molto vicino ad un''auto parcheggiata, a bordo della quale si trovano ancora persone, non rappresenta un grave pericolo se avviene a bassa velocità', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '442', '21020', '3979', '4', 'Passare molto vicino ad un''auto parcheggiata, a bordo della quale si trovano ancora persone, è pericoloso solo se una delle portiere risulta già aperta', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '442', '21020', '3980', '5', 'Passare molto vicino ad un''auto parcheggiata, a bordo della quale si trovano ancora persone, è pericoloso solo se il conducente ed alcuni passeggeri sono già scesi', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '442', '21020', '3981', '6', 'Passare molto vicino ad un''auto parcheggiata, a bordo della quale si trovano ancora persone, può essere pericoloso, solo se si viaggia ad alta velocità', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '443', '21008', '', '', 'Regolazione della velocità ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '443', '21008', '3982', '1', 'La velocità deve essere moderata nei tratti di strada a visuale non libera', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '443', '21008', '3983', '2', 'Si deve tenere una velocità che non costituisca pericolo per la sicurezza della circolazione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '443', '21008', '3984', '3', 'Si deve tenere una velocità che consenta di intervenire con sicurezza in caso di imprevisti', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '443', '21008', '3985', '4', 'Si deve tenere una velocità adeguata alle proprie condizioni psicofisiche', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '443', '21008', '3986', '5', 'È obbligo del conducente regolare la velocità in relazione al carico del veicolo ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '443', '21008', '3987', '6', 'Si deve tenere una velocità regolata in funzione del tempo che abbiamo a disposizione', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '443', '21008', '3988', '7', 'La velocità deve essere moderata fuori dai centri abitati', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '444', '21007', '', '', 'Regolazione della velocità ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '444', '21007', '3989', '1', 'La velocità dei veicoli deve essere regolata in modo da non causare intralcio alla circolazione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '444', '21007', '3990', '2', 'E''obbligatorio ridurre la velocita e all''occoranza fermarsi negli incroci, per far passare i veicoli cui spetti la precedenza', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '444', '21007', '3991', '3', 'Si è tenuti a regolare la velocità in funzione del tipo di alimentazione del motore', 'F');
