INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '449', '21003', '4063', '5', 'È obbligatorio ridurre la velocità e all''occorrenza fermarsi quando gli animali che si trovano sulla strada danno segni di spavento', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '449', '21003', '4064', '6', 'È obbligatorio ridurre la velocità e all''occorrenza fermarsi quando si rimane abbagliati', 'V');
