INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '449', '21003', '4069', '11', 'È obbligatorio ridurre la velocità e all''occorrenza fermarsi in corrispondenza di qualunque segnale stradale', 'F');
