INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '450', '21016', '', '', 'Regolazione della velocità agli incroci', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '450', '21016', '4071', '1', 'Se si attraversa ad alta velocità un incrocio avendo diritto di precedenza, in caso di incidente, non può escludersi un concorso di colpa', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '450', '21016', '4072', '2', 'Se si attraversa un incrocio a forte velocità avendo il diritto di precedenza, in caso di incidente si è completamente privi di responsabilità', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '450', '21016', '4073', '3', 'Se si attraversa ad alta velocità un incrocio in cui si ha precedenza, in caso di incidente la responsabilità sarà solo del conducente del veicolo proveniente dalla strada senza diritto di precedenza', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '450', '21016', '4074', '4', 'Se si attraversa un incrocio con diritto di precedenza, non si ha alcun obbligo di rallentare ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '450', '21016', '4075', '5', 'Se si attraversa ad alta velocità un incrocio con precedenza, in caso di incidente la responsabilità sarà del conducente del veicolo che ha impegnato per primo l''incrocio', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '451', '21005', '', '', 'Incidenza della velocità sui consumi di carburante e sulla tenuta di strada ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '451', '21005', '4076', '1', 'Viaggiare ad alta velocità incide sul consumo di carburante e sull''inquinamento ambientale', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '451', '21005', '4077', '2', 'Brusche variazioni di velocità comportano maggior consumo di carburante', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '451', '21005', '4078', '3', 'La velocità in curva incide sulla tenuta di strada del veicolo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '451', '21005', '4079', '4', 'L''eccessiva velocità in curva può determinare il ribaltamento del veicolo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '451', '21005', '4080', '5', 'I tratti di strada in curva debbono essere percorsi con il pedale della frizione abbassato ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '452', '21015', '', '', 'Regolazione della velocità in caso di lavori in corso o in presenza del triangolo di veicolo fermo ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '452', '21015', '4081', '1', 'Si deve, in particolare, regolare la velocità in presenza di lavori in corso', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '452', '21015', '4082', '2', 'Si deve, in particolare, regolare la velocità se si incontra sulla strada il segnale di veicolo fermo (triangolo mobile di pericolo)', 'V');
