INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '482', '22015', '4339', '2', 'La distanza di sicurezza deve essere di almeno 100 metri, in caso di forte pioggia', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '482', '22015', '4340', '3', 'La distanza di sicurezza può essere diminuita nelle gallerie', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '482', '22015', '4341', '4', 'Quando si circola in autostrada, è possibile diminuire la distanza di sicurezza, in quanto non ci si aspetta una frenata improvvisa dal veicolo che precede', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '482', '22015', '4342', '5', 'Se il veicolo che precede è un autocarro, si deve diminuire la distanza di sicurezza, in quanto i veicoli pesanti hanno uno spazio di frenatura molto più lungo rispetto agli altri veicoli', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '483', '22001', '', '', 'La distanza di sicurezza ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '483', '22001', '4343', '1', 'Il valore della distanza di sicurezza da mantenere da una macchina sgombraneve in azione è di 20 metri', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '483', '22001', '4344', '2', 'Il valore della distanza di sicurezza da mantenere da una macchina spanditrice di sale in azione è di 20 metri ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '483', '22001', '4345', '3', 'Su strada extraurbana a una corsia per senso di marcia, la distanza fra veicoli adibiti al trasporto di merci di massa complessiva superiore a 3,5 tonnellate, cui sia stato imposto il divieto di sorpasso, deve essere di almeno 100 metri ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '483', '22001', '4346', '4', 'Il valore della distanza di sicurezza da mantenere da una macchina sgombraneve in azione è di 10 metri ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '483', '22001', '4347', '5', 'Il valore della distanza di sicurezza da mantenere da una macchina spanditrice di sale in azione è di 15 metri ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '483', '22001', '4348', '6', 'La distanza fra autotreni, in presenza di segnali di divieto di sorpasso fra di loro, deve essere pari ad almeno 10 metri ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '484', '23005', '', '', 'Comportamenti necessari per un corretto uso della strada ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '484', '23005', '4349', '1', 'L''uso corretto della strada comporta che i veicoli procedano ad una velocità adeguata   alle condizioni della strada e del traffico', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '484', '23005', '4350', '2', 'L''uso corretto della strada comporta che siano osservate le norme di comportamento  e quelle dettate dalla comune prudenza', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '484', '23005', '4351', '3', 'L''uso corretto della strada comporta che ci si affretti a sgombrare l''incrocio, quando  compaia sul semaforo la luce gialla fissa e non si è in grado di arrestare il veicolo in sicurezza prima della striscia di arresto', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '484', '23005', '4352', '4', 'L''uso corretto della strada comporta che si dia la precedenza ai pedoni che attraversano sugli attraversamenti pedonali', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '484', '23005', '4353', '5', 'L''uso corretto della strada comporta che si usino proiettori anabbaglianti nei centri abitati nelle ore notturne', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '484', '23005', '4354', '6', 'L''uso corretto della strada comporta che si guardino gli specchi retrovisori prima di azionare l''indicatore di direzione per cambiare corsia', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '484', '23005', '4355', '7', 'L''uso corretto della strada comporta che i veicoli impegnino sempre la carreggiata il più rapidamente   possibile', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '484', '23005', '4356', '8', 'L''uso corretto della strada comporta che ci si arresti sempre ai passaggi pedonali ripartendo solo se non sopraggiungono pedoni', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '484', '23005', '4357', '9', 'L''uso corretto della strada comporta che si usino in ogni caso i proiettori a luce abbagliante', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '484', '23005', '4358', '10', 'L''uso corretto della strada comporta che si proceda a velocità elevata per non creare intralcio', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '485', '23005', '', '', 'Comportamenti necessari per un corretto uso della strada ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '485', '23005', '4359', '1', 'L''uso corretto della strada comporta che ci si arresti in ogni caso in presenza del segnale in figura ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '486', '23012', '', '', 'Comportamenti da tenere in caso di traffico intenso ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '486', '23012', '4360', '1', 'In caso di traffico intenso il conducente deve regolare la propria velocità uniformandola il più possibile a quella della corrente di traffico che scorre lungo la corsia occupata', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '486', '23012', '4361', '2', 'In caso di traffico intenso il conducente deve mantenere sempre la distanza di sicurezza dal veicolo che lo precede', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '486', '23012', '4362', '3', 'In caso di traffico intenso il conducente deve evitare di suonare il clacson per invitare i veicoli che lo precedono ad accelerare l''andatura', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '486', '23012', '4363', '4', 'In caso di traffico intenso il conducente deve cambiare ripetutamente corsia di marcia in modo da impegnare sempre quella più libera', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '486', '23012', '4364', '5', 'In caso di traffico intenso il conducente deve occupare le corsie riservate alla marcia dei veicoli in servizio pubblico, se libere', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '486', '23012', '4365', '6', 'In caso di traffico intenso il conducente deve fare uso del lampeggio simultaneo di tutti gli indicatori di  direzione', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '487', '23003', '', '', 'Comportamenti da tenere alla guida dei veicoli, anche se ad elevate prestazioni ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '487', '23003', '4366', '1', 'Chi guida un veicolo, anche se ad elevate prestazioni, non deve, comunque, superare i limiti di velocità previsti dalla legge', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '487', '23003', '4367', '2', 'Chi guida un veicolo, anche se ad elevate prestazioni, deve dare, di norma, la precedenza nei crocevia a tutti i veicoli, che provengono da destra', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '487', '23003', '4368', '3', 'Chi guida un veicolo, anche se ad elevate prestazioni, deve evitare di costituire pericolo per gli altri utenti della strada', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '487', '23003', '4369', '4', 'Chi guida un veicolo, anche se ad elevate prestazioni, deve guardare nello specchietto retrovisore prima di segnalare l''intenzione di cambiare corsia', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '487', '23003', '4370', '5', 'Chi guida un veicolo, anche se ad elevate prestazioni, deve tenersi il più vicino possibile al margine destro della carreggiata, quando effettua una svolta a destra', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '487', '23003', '4371', '6', 'Chi guida un veicolo deve guidare sempre al centro della strada, però senza superare la striscia di corsia', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '487', '23003', '4372', '7', 'Chi guida un veicolo deve tenere in funzione gli abbaglianti di giorno anche in città', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '487', '23003', '4373', '8', 'Chi guida un veicolo può sorpassare in ogni condizione di traffico, purché non ci sia il segnale di divieto', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '487', '23003', '4374', '9', 'Chi guida un veicolo deve usare il clacson nei centri abitati, ogni qualvolta ci si avvicina ad un attraversamento ciclabile', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '487', '23003', '4375', '10', 'Chi guida un veicolo può montare trombe bitonali per essere sentito più agevolmente, usandole però solo fuori dei centri abitati', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '488', '23010', '', '', 'Comportamenti necessari quando di notte si incrocia un veicolo con fari a luce abbagliante accesi ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '488', '23010', '4376', '1', 'Allorché di notte, su strada extraurbana, incrociamo un veicolo con fari a luce abbagliante accesi, occorre procedere con prudenza, usando le luci anabbaglianti', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '488', '23010', '4377', '2', 'Allorché di notte, su strada extraurbana, incrociamo un veicolo con fari a luce abbagliante accesi, occorre distogliere lo sguardo dal fascio luminoso per non essere abbagliati', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '488', '23010', '4378', '3', 'Allorché di notte, su strada extraurbana, incrociamo un veicolo con fari a luce abbagliante accesi, occorre rallentare adeguatamente ed eventualmente fermarsi', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '488', '23010', '4379', '4', 'Allorché di notte, su strada extraurbana, incrociamo un veicolo con fari a luce abbagliante accesi, occorre tener conto della ridotta visibilità nel nostro comportamento di guida', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '488', '23010', '4380', '5', 'Allorché di notte, su strada extraurbana, incrociamo un veicolo con fari a luce abbagliante accesi, occorre evitare brusche manovre e cambi di corsia', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '488', '23010', '4381', '6', 'Allorché di notte, su strada extraurbana, incrociamo un veicolo con fari a luce abbagliante accesi, occorre disporre il retrovisore interno in posizione antiabbagliante', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '488', '23010', '4382', '7', 'Allorché di notte, su strada extraurbana, incrociamo un veicolo con fari a luce abbagliante accesi, occorre procedere a nostra volta con i fari abbaglianti, per veder meglio la strada', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '488', '23010', '4383', '8', 'Allorché di notte, su strada extraurbana, incrociamo un veicolo con fari a luce abbagliante accesi, occorre fermarsi immediatamente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '488', '23010', '4384', '9', 'Allorché di notte, su strada extraurbana, incrociamo un veicolo con fari a luce abbagliante accesi, occorre azionare le trombe bitonali per richiamare l''attenzione del veicolo incrociante', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '488', '23010', '4385', '10', 'Allorché di notte, su strada extraurbana, incrociamo un veicolo con fari a luce abbagliante accesi, occorre cercare di ripararsi gli occhi con le mani', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '489', '23017', '', '', 'Uso delle corsie in una strada a doppio senso di marcia con due corsie ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '489', '23017', '4386', '1', 'I veicoli devono, di norma, circolare sulla parte destra della carreggiata ed in vicinanza del margine destro', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '490', '23017', '', '', 'Uso delle corsie in una strada a doppio senso di marcia con due corsie ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '490', '23017', '4387', '2', 'In una carreggiata a doppio senso di marcia con due corsie, come in figura, i veicoli devono circolare nella corsia di destra, impegnando solo quella di sinistra per il sorpasso ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '490', '23017', '4388', '3', 'In una strada a doppio senso di marcia con due corsie, come in figura, i veicoli possono circolare per file parallele', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '491', '23014', '', '', 'Uso delle corsie in una strada a doppio senso di marcia con due corsie ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '491', '23014', '4389', '1', 'Su una strada a due corsie a doppio senso di marcia, in figura, si deve impegnare quella di sinistra solo per il sorpasso [FIGURA 502]', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '491', '23014', '4390', '2', 'Nelle strade composte da una carreggiata a due corsie a doppio senso di marcia, in figura, si può marciare per file parallele', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '492', '23018', '', '', 'Posizione dei veicoli sulla carreggiata ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '492', '23018', '4391', '1', 'I veicoli senza motore devono circolare il più possibile vicino al margine destro della carreggiata', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '492', '23018', '4392', '2', 'Chi conduce gli animali li deve tenere il più possibile vicino al margine destro della carreggiata', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '492', '23018', '4393', '3', 'Su strada a senso unico i veicoli che percorrono una curva a sinistra devono tenersi in prossimità del margine sinistro della carreggiata', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '492', '23018', '4394', '4', 'I veicoli che vogliono effettuare una svolta a sinistra devono tenersi il più possibile vicino al margine destro della carreggiata', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '492', '23018', '4395', '5', 'I filobus devono, di norma, circolare il più possibile vicino al margine sinistro della carreggiata', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '492', '23018', '4396', '6', 'In caso di traffico intenso, i motocicli possono circolare sulle corsie di emergenza', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '492', '23018', '4397', '7', 'Su strade a 2 corsie per senso di marcia, in caso di intenso traffico i ciclomotori possono circolare sulla corsia di sinistra', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '492', '23018', '4398', '8', 'Su strade a 2 corsie per senso di marcia, in caso di intenso traffico i ciclomotori a due ruote possono circolare sulla corsia di sinistra', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '493', '23018', '', '', 'Posizione dei veicoli sulla carreggiata ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '493', '23018', '4399', '1', 'su strade a doppio senso di marcia con due corsie , come in figura, incrociando altri veicoli, occorre spostarsi il piu vicino possibile al margine destro della carreggiata ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '493', '23018', '4400', '2', 'In una carreggiata a doppio senso di marcia con due corsie, come in figura, i veicoli devono circolare nella corsia di destra, impegnando quella di sinistra solo per il sorpasso ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '494', '23018', '', '', 'Posizione dei veicoli sulla carreggiata ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '494', '23018', '4401', '1', 'Su strada a doppio senso di marcia con due corsie, come in figura, i veicoli che percorrono il tratto in salita di un dosso devono tenersi il più possibile vicino al margine destro della carreggiata ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '495', '23018', '', '', 'Posizione dei veicoli sulla carreggiata ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '495', '23018', '4402', '1', 'I veicoli che percorrono una curva, su strada a due corsie per ogni senso di marcia, come in figura, possono marciare oltrepassando la striscia discontinua se la strada è libera', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '496', '23018', '', '', 'Posizione dei veicoli sulla carreggiata ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '496', '23018', '4403', '1', 'In una carreggiata come in figura in caso di intenso traffico, ciclomotori e motocicli possono circolare sulla pista ciclabile se non superano la velocità di 20 km/h', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '497', '23014', '', '', 'Uso delle corsie in una strada a doppio senso di marcia con tre corsie ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '497', '23014', '4404', '1', 'Su una strada a tre corsie a doppio senso di marcia, in figura, si deve impegnare quella centrale solo per il sorpasso', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '497', '23014', '4405', '2', 'Su strada a doppio senso di marcia con tre corsie, come in figura, i veicoli possono usare la corsia centrale per la marcia normale', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '497', '23014', '4406', '3', 'Quando una strada è a doppio senso di marcia con tre corsie, come in figura, i veicoli possono circolare per file parallele', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '498', '23017', '', '', 'Uso delle corsie in una strada a tre corsie per senso di marcia ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '498', '23017', '4407', '1', 'Su carreggiate a tre corsie per senso di marcia, come in figura, i veicoli a motore devono, di norma, percorrere la corsia più libera a destra', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '499', '23017', '', '', ' Uso delle corsie in una strada a tre corsie per senso di marcia ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '499', '23017', '4408', '1', 'Viaggiando su semicarreggiata a tre corsie, come in figura, per svoltare a sinistra si deve utilizzare la corsia centrale', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '500', '23017', '', '', 'Uso delle corsie in una strada divisa in due carreggiate separate ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '500', '23017', '4409', '1', 'In una strada divisa in due carreggiate separate come in figura, i veicoli devono, di norma, circolare sulla carreggiata di destra rispetto alla propria direzione di marcia', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '500', '23017', '4410', '2', 'Su strada a due carreggiate separate, come in figura, i veicoli che percorrono un dosso devono tenersi vicino al margine sinistro della carreggiata', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '501', '23017', '', '', 'Uso delle corsie in una strada divisa in due carreggiate separate ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '501', '23017', '4411', '1', 'Su una strada a due carreggiate separate, in figura, si deve percorrere la carreggiata di destra per la marcia normale e quella di sinistra per il sorpasso', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '502', '23017', '', '', 'Uso delle corsie in una strada divisa in tre carreggiate separate ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '502', '23017', '4412', '1', 'Su una strada divisa in tre carreggiate separate come in figura, i veicoli devono, di norma, circolare sulla carreggiata centrale o su quella di destra rispetto alla propria direzione di marcia', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '503', '23017', '', '', 'Uso delle corsie in una strada divisa in tre carreggiate separate ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '503', '23017', '4413', '1', 'Nelle strade a quattro carreggiate separate, quelle centrali sono riservate al sorpasso', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '504', '23017', '', '', 'Posizione dei veicoli sulla carreggiata ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '504', '23017', '4414', '1', 'I veicoli a motore possono circolare per file parallele in vicinanza di incroci regolati da vigili o da semafori', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '504', '23017', '4415', '2', 'Per svoltare a sinistra i veicoli debbono tenersi sul margine destro della carreggiata', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '504', '23017', '4416', '3', 'È possibile circolare al centro della carreggiata quando la strada è libera', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '504', '23017', '4417', '4', 'In caso di intenso traffico è consentito circolare per file parallele, utilizzando le corsie riservate agli autobus e ai veicoli in servizio pubblico', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '505', '23022', '', '', 'Marcia per file parallele ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '505', '23022', '4418', '1', 'Durante la marcia per file parallele è consentito cambiare corsia per effettuare manovre di svolta', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '505', '23022', '4419', '2', 'La marcia per file parallele è ammessa quando viene autorizzata dagli agenti del traffico', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '505', '23022', '4420', '3', 'È consentita la marcia per file parallele su strade a due corsie per senso di marcia ad un incrocio regolato da semaforo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '505', '23022', '4421', '4', 'Di norma, è consentito viaggiare per file parallele lungo il tratto di strada che porta ad un incrocio regolato da agenti del traffico', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '505', '23022', '4422', '5', 'Percorrendo una carreggiata a senso unico la svolta a sinistra si effettua immettendosi in una corsia qualsiasi, se si viaggia per file parallele  ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '505', '23022', '4423', '6', 'È consentito viaggiare per file parallele lungo il tratto di strada che porta ad un incrocio non regolato da semaforo o da agenti del traffico', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '505', '23022', '4424', '7', 'Nei centri urbani è consentito marciare per file parallele in qualunque condizione di traffico', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '505', '23022', '4425', '8', 'Durante la marcia per file parallele, percorrendo la corsia di sinistra, bisogna mantenere in funzione l''indicatore di direzione sinistro', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '505', '23022', '4426', '9', 'La marcia per file parallele non è ammessa se la strada è a una sola carreggiata', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '505', '23022', '4427', '10', 'Durante la marcia per file parallele i motocicli possono procedere passando tra i veicoli affiancati', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '506', '23022', '', '', 'Marcia per file parallele ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '506', '23022', '4428', '1', 'Su strade con almeno due corsie per senso di marcia, come in figura, qualora il traffico sia intenso, si può viaggiare per file parallele ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '506', '23022', '4429', '2', 'È sempre consentito viaggiare per file parallele se la carreggiata a doppio senso ha almeno due corsie per ogni senso di marcia, come quella in figura ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '507', '23022', '', '', 'Marcia per file parallele ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '507', '23022', '4430', '1', 'In condizioni di traffico intenso su carreggiata a senso unico a tre corsie, come in figura, è consentito viaggiare per file parallele ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '508', '23022', '', '', 'Marcia per file parallele ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '508', '23022', '4431', '1', 'È obbligatorio viaggiare per file parallele sulle carreggiate a tre corsie per senso di marcia, come quella in figura ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '509', '23022', '', '', 'Marcia per file parallele ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '509', '23022', '4432', '1', 'È consentito viaggiare per file parallele su strada con due corsie e doppio senso di marcia, come quella in figura ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '510', '23020', '', '', 'Cambio di direzione ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '510', '23020', '4433', '1', 'Quando si vuole cambiare direzione si deve azionare in anticipo l''indicatore di direzione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '510', '23020', '4434', '2', 'Quando si vuole cambiare direzione è obbligatorio fare uso delle segnalazioni acustiche (clacson o trombe)', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '511', '23020', '', '', 'Cambio di direzione ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '511', '23020', '4435', '1', 'Il veicolo in figura, può svoltare a destra ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '512', '23033', '', '', 'Obblighi per chi intende immettersi nel flusso della circolazione ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '512', '23033', '4436', '1', 'Il conducente che intende immettersi nel flusso della circolazione deve assicurarsi di poter effettuare la manovra senza creare pericolo o intralcio agli altri utenti della strada', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '512', '23033', '4437', '2', 'Il conducente che intende immettersi nel flusso della circolazione deve tenere conto della posizione, distanza, direzione degli altri utenti della strada', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '512', '23033', '4438', '3', 'Il conducente che intende immettersi nel flusso della circolazione deve azionare l''indicatore di direzione per segnalare con sufficiente anticipo l''intenzione di effettuare tale manovra', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '512', '23033', '4439', '4', 'Il conducente che intende immettersi nel flusso della circolazione deve dare la precedenza ai veicoli in transito', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '512', '23033', '4440', '5', 'Il conducente che intende immettersi nel flusso della circolazione può evitare di azionare l''indicatore di direzione se non sopraggiungono altri veicoli', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '512', '23033', '4441', '6', 'Il conducente che intende immettersi nel flusso della circolazione deve dare la precedenza ai veicoli in transito solo in presenza di apposito segnale', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '512', '23033', '4442', '7', 'Il conducente che si immette nel flusso della circolazione deve dare la precedenza ai veicoli in transito ad eccezione delle biciclette', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '513', '23034', '', '', 'Immissione nel flusso della circolazione ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '513', '23034', '4443', '1', 'Il conducente di un motociclo che parte dal margine della carreggiata e si immette nel flusso della circolazione deve indossare ed allacciare il casco', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '513', '23034', '4444', '2', 'Il conducente di un autoveicolo che parte dal margine della carreggiata e si immette nel flusso della circolazione deve allacciare la cintura di sicurezza salvo che non sia esentato', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '513', '23034', '4445', '3', 'Il conducente di un motociclo che parte dal margine della carreggiata e si immette nel flusso della circolazione deve accendere il proiettore anabbagliante', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '513', '23034', '4446', '4', 'Il conducente che parte dal margine della carreggiata e si immette nel flusso della circolazione deve accertarsi che la strada sia libera, guardando anche negli specchietti retrovisori', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '513', '23034', '4447', '5', 'Il conducente che parte dal margine della carreggiata e si immette nel flusso della circolazione può allacciare la cintura di sicurezza dopo avere concluso tale manovra', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '513', '23034', '4448', '6', 'Il motociclista che parte dal margine della carreggiata e si immette nel flusso della circolazione può indossare il casco dopo avere concluso tale manovra', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '514', '23001', '', '', 'Immissione nel flusso della circolazione in retromarcia ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '514', '23001', '4449', '1', 'Il veicolo S, muovendosi in retromarcia per immettersi nella circolazione, deve dare la precedenza ai veicoli provenienti sia da destra che da sinistra', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '514', '23001', '4450', '2', 'Il veicolo S, muovendosi in retromarcia per immettersi nella circolazione, deve azionare gli indicatori di direzione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '514', '23001', '4451', '3', 'Il veicolo S, muovendosi in retromarcia per immettersi nella circolazione, deve fare attenzione agli eventuali pedoni in transito dietro di esso e dar loro la precedenza', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '514', '23001', '4452', '4', 'Il veicolo S, muovendosi in retromarcia per immettersi nella circolazione, deve dare la precedenza a tutti i veicoli in transito', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '514', '23001', '4453', '5', 'Il veicolo S, muovendosi in retromarcia per immettersi nella circolazione, deve assicurarsi che la strada sia libera e fare la manovra con particolare prudenza', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '514', '23001', '4454', '6', 'Il veicolo S, muovendosi in retromarcia per immettersi nella circolazione, deve azionare la segnalazione luminosa di pericolo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '514', '23001', '4455', '7', 'Il veicolo S, muovendosi in retromarcia per immettersi nella circolazione, deve accendere la luce posteriore per nebbia', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '514', '23001', '4456', '8', 'Il veicolo S, muovendosi in retromarcia per immettersi nella circolazione, deve dare la precedenza solo ai veicoli che transitano nel suo senso di marcia', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '514', '23001', '4457', '9', 'Il veicolo S, muovendosi in retromarcia per immettersi nella circolazione, deve manovrare velocemente per liberare il posteggio', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '514', '23001', '4458', '10', 'Il veicolo S, muovendosi in retromarcia per immettersi nella circolazione, deve suonare ripetutamente il clacson durante la manovre', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '515', '23035', '', '', 'Immissione nel flusso della circolazione in retromarcia ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '515', '23035', '4459', '1', 'Il conducente del veicolo S in figura, muovendosi in retromarcia per immettersi nella circolazione, deve assicurarsi che la strada sia libera ed eseguire la manovra con prudenza', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '515', '23035', '4460', '2', 'Il conducente del veicolo E in figura, muovendosi in retromarcia per immettersi nella circolazione, deve azionare gli indicatori di direzione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '515', '23035', '4461', '3', 'Il conducente del veicolo S in figura, muovendosi in retromarcia per immettersi nella circolazione, deve dare la precedenza a tutti i veicoli provenienti sia da destra sia da sinistra', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '515', '23035', '4462', '4', 'Il conducente del veicolo S in figura, muovendosi in retromarcia per immettersi nella circolazione, deve fare attenzione agli eventuali pedoni in transito dietro di esso', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '515', '23035', '4463', '5', 'Il conducente del veicolo S in figura, muovendosi in retromarcia per immettersi nella circolazione, deve azionare la segnalazione luminosa di pericolo (quattro frecce lampeggianti simultaneamente)', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '515', '23035', '4464', '6', 'Il conducente del veicolo S in figura, muovendosi in retromarcia per immettersi nella circolazione, deve suonare il clacson durante l''intera manovra', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '515', '23035', '4465', '7', 'Il conducente del veicolo S in figura, muovendosi in retromarcia per immettersi nella circolazione, deve dare la precedenza ai veicoli che transitano nel suo senso di marcia, ad eccezione delle biciclette', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '516', '23037', '', '', ' Mancato rispetto delle norme per immettersi nel flusso della circolazione e per fermarsi ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '516', '23037', '4466', '1', 'Il mancato rispetto delle norme prescritte per immettersi nel flusso della circolazione comporta, tra l''altro, la perdita di punti dalla patente', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '516', '23037', '4467', '2', 'Il mancato rispetto delle norme prescritte per immettersi nel flusso della circolazione non comporta la sottrazione di punti dalla patente, ma sanzione pecuniaria', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '516', '23037', '4468', '3', 'In caso di fermata sul margine destro della carreggiata senza segnalare con sufficiente anticipo l''intenzione di effettuare tale manovra, è disposta la sottrazione di 4 punti dalla patente del conducente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '517', '23036', '', '', 'Obblighi per chi intende fermarsi sul margine destro della carreggiata ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '517', '23036', '4469', '1', 'Il conducente che intende fermarsi sul margine destro della carreggiata deve effettuare la manovra senza creare pericolo o intralcio agli altri utenti della strada', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '517', '23036', '4470', '2', 'Il conducente che intende fermarsi sul margine destro della carreggiata deve tenere conto della posizione, distanza, direzione degli altri utenti della strada', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '517', '23036', '4471', '3', 'Il conducente che intende fermarsi sul margine destro della carreggiata deve evitare di creare pericolo ai conducenti di veicoli a due ruote sopraggiungenti', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '517', '23036', '4472', '4', 'Il conducente che intende fermarsi sul margine destro della carreggiata deve segnalare con sufficiente anticipo la sua intenzione di effettuare tale manovra', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '517', '23036', '4473', '5', 'Il conducente che intende fermarsi sul margine destro della carreggiata deve evitare brusche frenate, per garantire la sicurezza degli altri utenti della strada', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '517', '23036', '4474', '6', 'Il conducente che intende fermarsi sul margine destro della carreggiata deve eseguire la manovra lampeggiando ripetutamente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '517', '23036', '4475', '7', 'Il conducente che intende fermarsi sul margine destro della carreggiata deve segnalare con sufficiente anticipo azionando la segnalazione luminosa di pericolo (quattro frecce lampeggianti simultaneamente)', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '517', '23036', '4476', '8', 'Il conducente che intende fermarsi sul margine destro della carreggiata può evitare di segnalare la sua intenzione di effettuare tale manovra se il traffico è scarso', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '518', '23015', '', '', 'Obblighi per chi intende cambiare corsia ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '518', '23015', '4477', '1', 'Chi intende cambiare corsia deve controllare che la corsia che vuole occupare sia libera davanti per un tratto sufficiente', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '518', '23015', '4478', '2', 'Chi intende cambiare corsia deve controllare che la corsia che vuole occupare sia libera dietro per un tratto sufficiente', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '518', '23015', '4479', '3', 'Chi intende cambiare corsia, per effettuare un sorpasso, deve controllare che il veicolo che sta davanti non abbia segnalato l''inizio della stessa manovra', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '518', '23015', '4480', '4', 'Chi intende cambiare corsia deve segnalare la manovra in anticipo, tramite l''indicatore di direzione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '518', '23015', '4481', '5', 'Chi intende cambiare corsia non deve creare intralcio o pericolo per chi percorre la corsia che vuole impegnare', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '518', '23015', '4482', '6', 'Chi intende cambiare corsia non deve azionare l''indicatore di direzione se la strada è libera', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '518', '23015', '4483', '7', 'Chi intende cambiare corsia ha l''obbligo di segnalare la manovra con l''uso dell''avvisatore acustico (clacson o trombe)', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '518', '23015', '4484', '8', 'Prima di cambiare corsia è necessario accertare che le corsie siano separate da una striscia continua', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '519', '23019', '', '', 'Obblighi per chi intende cambiare corsia ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '519', '23019', '4485', '1', 'Quando si vuole cambiare corsia non si deve creare intralcio o pericolo a chi si trova sulla corsia da impegnare', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '519', '23019', '4486', '2', 'Quando si vuole cambiare corsia si deve segnalare in anticipo la manovra da compiere', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '519', '23019', '4487', '3', 'Quando si vuole cambiare corsia bisogna controllare che la corsia da impegnare sia libera davanti per un tratto sufficiente', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '519', '23019', '4488', '4', 'Quando si vuole cambiare corsia si deve controllare che la striscia che divide le corsie sia tratteggiata', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '519', '23019', '4489', '5', 'Quando si vuole cambiare corsia si deve segnalare la manovra, facendo uso dell''indicatore di direzione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '519', '23019', '4490', '6', 'Quando si vuole cambiare corsia bisogna controllare che sulla corsia che si vuole occupare non stiano arrivando altri veicoli', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '519', '23019', '4491', '7', 'Nelle strade urbane è consentito, quando si vuole svoltare, circolare a cavallo delle strisce di corsia', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '519', '23019', '4492', '8', 'Quando si vuole cambiare corsia l''uso dell''indicatore di direzione è facoltativo se ci sono le strisce discontinue (tratteggiate)', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '519', '23019', '4493', '9', 'Quando si vuole cambiare corsia, per immettersi nella corsia di destra, non occorre alcuna particolare attenzione', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '519', '23019', '4494', '10', 'Quando si vuole cambiare corsia per un sorpasso è obbligatorio ridurre la velocità', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '520', '23019', '', '', 'Obblighi per chi intende cambiare corsia ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '520', '23019', '4495', '1', 'In vicinanza degli incroci, come in figura si può sempre cambiare corsia fino alla striscia trasversale di arresto', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '521', '23013', '', '', 'Svolta a destra ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '521', '23013', '4496', '1', 'Per effettuare la svolta a destra occorre tenersi il più vicino possibile al margine destro della carreggiata', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '521', '23013', '4497', '2', 'Per effettuare la svolta a destra bisogna segnalare in anticipo la manovra da compiere, azionando l''indicatore di direzione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '521', '23013', '4498', '3', 'La svolta a destra si effettua immettendosi nella corsia che consente di eseguire tale manovra', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '521', '23013', '4499', '4', 'La svolta a destra si effettua moderando la velocità man mano che ci si avvicina al punto di svolta', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '521', '23013', '4500', '5', 'La svolta a destra si effettua senza creare pericolo o intralcio agli altri', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '521', '23013', '4501', '6', 'Per svoltare a destra bisogna tenersi il più vicino possibile al margine sinistro della carreggiata', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '521', '23013', '4502', '7', 'Per svoltare a destra bisogna tenersi sulla corsia di sinistra', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '521', '23013', '4503', '8', 'La svolta a destra si effettua senza mai dare la precedenza ai pedoni, se il semaforo ha la luce verde accesa', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '521', '23013', '4504', '9', 'La svolta a destra si effettua girando attorno alla colonnina luminosa eventualmente posta al centro dell''incrocio', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '521', '23013', '4505', '10', 'Quando si vuole svoltare a destra è necessario portarsi in vicinanza del centro dell''incrocio', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '522', '23027', '', '', 'Svolta a destra di un autotreno ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '522', '23027', '4506', '1', 'Se un autotreno intende svoltare a destra in una strada stretta, i conducenti  degli altri veicoli debbono tener presente che potrebbe diminuire notevolmente la velocità', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '522', '23027', '4507', '2', 'Se un autotreno intende svoltare a destra in una strada stretta, i conducenti degli altri veicoli debbono tener presente che nella manovra si sposti a sinistra (si allarghi)', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '522', '23027', '4508', '3', 'Se un autotreno intende svoltare a destra in una strada stretta, non bisogna stargli troppo vicino, per non essere d''intralcio, in caso sia necessaria la manovra di retromarcia', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '522', '23027', '4509', '4', 'Se un autotreno intende svoltare a destra in un incrocio, occorre rinunciare a sorpassarlo, perché potrebbe impedire la vista di segnali o di veicoli provenienti lateralmente', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '522', '23027', '4510', '5', 'Se un autotreno intende svoltare a destra in una strada stretta, bisogna sorpassarlo subito a sinistra', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '522', '23027', '4511', '6', 'Se un autotreno intende svoltare a destra in una strada stretta, i conducenti dei veicoli che seguono devono sorpassarlo appena aziona l''indicatore di direzione', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '522', '23027', '4512', '7', 'Se un autotreno intende svoltare a destra in una strada stretta, i conducenti dei veicoli che seguono debbono azionare la segnalazione luminosa di pericolo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '523', '23016', '', '', 'Svolta a sinistra ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '523', '23016', '4513', '1', 'In una carreggiata a doppio senso di circolazione per svoltare a sinistra bisogna avvicinarsi al centro della carreggiata', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '523', '23016', '4514', '2', 'In una carreggiata a doppio senso di circolazione per svoltare a sinistra bisogna dare la precedenza ai veicoli provenienti da destra, salvo diversa segnalazione. ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '523', '23016', '4515', '3', 'In una carreggiata a doppio senso di circolazione per svoltare a sinistra occorre spostarsi il più possibile sul margine sinistro della carreggiata', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '523', '23016', '4516', '4', 'In una carreggiata a doppio senso di circolazione per svoltare a sinistra bisogna effettuare la manovra velocemente, per evitare i veicoli provenienti da destra e da sinistra', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '523', '23016', '4517', '5', 'In una carreggiata a doppio senso di circolazione per svoltare a sinistra bisogna dare la precedenza ai veicoli che arrivano da sinistra, salvo diversa segnalazione', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '523', '23016', '4518', '6', 'In una carreggiata a doppio senso di circolazione per svoltare a sinistra occorre suonare il clacson, per attraversare con maggior sicurezza l''incrocio', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '524', '23016', '', '', 'Svolta a sinistra ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '524', '23016', '4519', '1', 'In una carreggiata a doppio senso di circolazione per svoltare a sinistra occorre, di norma, occupare l''area sinistra dell''incrocio, come i veicoli in figura, salvo diversa segnalazione ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '525', '23016', '', '', 'Svolta a sinistra ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '525', '23016', '4520', '1', 'In una carreggiata a doppio senso di circolazione per svoltare a sinistra bisogna girare intorno al centro dell''incrocio come il veicolo H in figura, se c''è il segnale di rotatoria ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '526', '23016', '', '', 'Svolta a sinistra ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '526', '23016', '4521', '1', 'In una carreggiata a doppio senso di circolazione per svoltare a sinistra bisogna effettuare la svolta occupando sempre il lato destro dell''incrocio, come i veicoli in figura ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '527', '23020', '', '', 'Traiettoria da seguire rispetto al centro dell’incrocio nella svolta a sinistra ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '527', '23020', '4522', '1', 'Per svoltare a sinistra bisogna, di norma, lasciare il centro dell''incrocio alla nostra destra, come i veicoli in figura', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '528', '23020', '', '', 'Traiettoria da seguire rispetto al centro dell’incrocio nella svolta a sinistra ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '528', '23020', '4523', '1', 'Per svoltare a sinistra bisogna girare intorno al centro dell''incrocio, come i veicoli in figura ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '529', '23021', '', '', 'Svolta a sinistra ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '529', '23021', '4524', '1', 'Su una strada a doppio senso di marcia per svoltare a sinistra non si deve prendere contromano la strada in cui si svolta', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '529', '23021', '4525', '2', 'Su una strada a doppio senso di marcia per svoltare a sinistra occorre inserirsi nelle corsie che consentono di proseguire in tale direzione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '529', '23021', '4526', '3', 'Su una strada a doppio senso di marcia per svoltare a sinistra è necessario spostarsi a sinistra della linea continua di mezzeria', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '529', '23021', '4527', '4', 'Su una strada a doppio senso di marcia per svoltare a sinistra ci si deve tenere lungo il margine destro della carreggiata', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '529', '23021', '4528', '5', 'Su una strada a doppio senso di marcia per svoltare a sinistra è necessario arrestarsi prima di completare la manovra, anche se non sopraggiungono veicoli', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '530', '23021', '', '', 'Svolta a sinistra ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '530', '23021', '4529', '1', 'Su una stada a doppio senso di marcia nello svoltare a sinistra si deve, di norma, lasciare il centro dell''incrocio alla nostra destra, come i veicoli in figura ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '531', '23020', '', '', 'Svolta a sinistra da una strada a senso unico ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '531', '23020', '4530', '1', 'Su strada a senso unico per svoltare a sinistra bisogna tenersi sul margine sinistro', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '532', '23022', '', '', 'Svolta a sinistra da una strada a senso unico', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '532', '23022', '4531', '1', 'Percorrendo una carreggiata a senso unico la svolta a sinistra si effettua segnalando in anticipo la manovra da compiere', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '533', '23020', '', '', 'Inversione di marcia ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '533', '23020', '4532', '1', 'Durante l''effettuazione dell''inversione di marcia l''indicatore di direzione deve rimanere in funzione per l''intera durata della manovra', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '534', '23028', '', '', 'Obblighi per chi intende effettuare l’inversione di marcia ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '534', '23028', '4533', '1', 'Il conducente che intende effettuare l''inversione di marcia su una strada a doppio senso deve dare la precedenza ai veicoli che sopraggiungono', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '534', '23028', '4534', '2', 'Il conducente che intende effettuare l''inversione di marcia su una strada a doppio senso deve rinunciare a compiere la manovra, qualora vi sia traffico intenso e continuo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '534', '23028', '4535', '3', 'Il conducente che intende effettuare l''inversione di marcia su una strada a doppio senso deve rinunciare ad eseguire la manovra, se vi è scarsa visibilità', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '534', '23028', '4536', '4', 'Il conducente che intende effettuare l''inversione di marcia su una strada a doppio senso deve azionare l''indicatore di direzione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '534', '23028', '4537', '5', 'Il conducente che intende effettuare l''inversione di marcia su una strada a doppio senso non deve creare pericolo o intralcio agli altri utenti della strada', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '534', '23028', '4538', '6', 'Il conducente che intende effettuare l''inversione di marcia su una strada a doppio senso deve azionare la segnalazione luminosa di pericolo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '534', '23028', '4539', '7', 'Il conducente che intende effettuare l''inversione di marcia su una strada a doppio senso può compiere la manovra anche in curva, se dà precedenza a tutti i veicoli', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '534', '23028', '4540', '8', 'Il conducente che può effettuare l''inversione di marcia su una strada a doppio senso deve accertarsi se la linea di mezzeria è continua', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '534', '23028', '4541', '9', 'Il conducente che intende effettuare l''inversione di marcia su una strada a doppio senso può compiere la manovra anche con scarsa visibilità, se la linea di mezzeria è tratteggiata', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '534', '23028', '4542', '10', 'Il conducente che intende effettuare l''inversione di marcia su una strada a doppio senso può effettuarla solo sulle strade larghe, che permettono di eseguirla con una sola manovra', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '535', '23029', '', '', 'Inversione di marcia ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '535', '23029', '4543', '1', 'Nelle strade a doppio senso di circolazione non è consentito effettuare l''inversione del senso di marcia in vicinanza o in corrispondenza di curve', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '535', '23029', '4544', '2', 'L''inversione del senso di marcia non è consentita in vicinanza o in corrispondenza degli incroci', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '535', '23029', '4545', '3', 'Nelle strade a doppio senso non è consentito effettuare l''inversione del senso di marcia in tutti i casi di scarsa visibilità', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '535', '23029', '4546', '4', 'Nelle strade a doppio senso di circolazione non è consentito effettuare l''inversione del senso di marcia in vicinanza o in corrispondenza di dossi', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '535', '23029', '4547', '5', 'Quando manca la segnaletica orizzontale non è consentito effettuare manovre di inversione del senso di marcia', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '535', '23029', '4548', '6', 'Qualora la linea di margine della carreggiata sia continua, non è consentito effettuare l''inversione di marcia', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '535', '23029', '4549', '7', 'Fuori dai centri abitati non è consentito effettuare l''inversione di marcia', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '535', '23029', '4550', '8', 'Sulle autostrade a tre corsie per ogni senso di marcia è consentito l''inversione del senso di marcia', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '536', '23008', '', '', 'Comportamenti da tenere quando ci si imbatte in un corteo ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '536', '23008', '4551', '1', 'Quando, in un centro abitato, il conducente di un veicolo si imbatte in un corteo, deve fermarsi sulla destra e attendere che la carreggiata si liberi', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '536', '23008', '4552', '2', 'Quando, in un centro abitato, il conducente di un veicolo si imbatte in un corteo, deve evitare di suonare il clacson', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '536', '23008', '4553', '3', 'È opportuno che il conducente di un veicolo, quando in un centro abitato si imbatte in un corteo, imbocchi, se possibile, una strada laterale, purché la manovra possa essere fatta in maniera corretta', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '536', '23008', '4554', '4', 'Quando, in un centro abitato, il conducente di un veicolo si imbatte in un corteo, deve accertarsi, prima di passare, che esso sia transitato completamente', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '536', '23008', '4555', '5', 'È opportuno che il conducente di un veicolo, quando in un centro abitato si imbatte in un corteo, eviti di retrocedere se ciò ostacola il flusso della circolazione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '536', '23008', '4556', '6', 'È opportuno che il conducente di un veicolo, quando in un centro abitato si imbatte in un corteo, non faccia inversione di marcia, qualora ciò costituisca intralcio alla circolazione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '536', '23008', '4557', '7', 'Quando, in un centro abitato, il conducente di un veicolo si imbatte in un corteo, deve suonare discretamente onde avvertire il corteo di far cessare al più presto l''ingombro', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '536', '23008', '4558', '8', 'Quando, in un centro abitato, il conducente di un veicolo si imbatte in un corteo, deve cercare di farsi strada interrompendo il corteo, ma con estrema prudenza', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '536', '23008', '4559', '9', 'È opportuno che il conducente di un veicolo, quando in un centro abitato si imbatte in un corteo, accenda le luci di profondità per vedere meglio ed evitare incidenti', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '536', '23008', '4560', '10', 'È opportuno che il conducente di un veicolo, quando in un centro abitato si imbatte in un corteo, se proprio ha molta fretta, passi rapidamente usando il clacson in maniera continua', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '536', '23008', '4561', '11', 'Quando, in un centro abitato, il conducente di un veicolo si imbatte in un corteo, deve regolarsi in base alla sua densità, oltrepassandolo o interrompendolo qualora sia possibile', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '536', '23008', '4562', '12', 'Quando, in un centro abitato, il conducente di un veicolo si imbatte in un corteo, deve retrocedere rapidamente fino al primo incrocio', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '537', '23024', '', '', 'Comportamenti da tenere avvicinandosi ad un incrocio ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '537', '23024', '4563', '1', 'Giungendo in vicinanza di un incrocio bisogna predisporsi ad osservare le norme di precedenza', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '537', '23024', '4564', '2', 'Giungendo in vicinanza di un incrocio ci si deve spostare per tempo sulla corsia destinata alla direzione che si intende prendere', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '537', '23024', '4565', '3', 'Giungendo in vicinanza di un incrocio si deve segnalare per tempo l''intenzione di svoltare', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '537', '23024', '4566', '4', 'Giungendo alla guida di un veicolo a due ruote in vicinanza di un incrocio, bisogna incolonnarsi dietro gli altri veicoli in attesa', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '537', '23024', '4567', '5', 'Giungendo ad un incrocio è consentito impegnarlo solo se si ha la possibilità di proseguire e liberarlo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '537', '23024', '4568', '6', 'Giungendo in vicinanza di un incrocio si deve usare la massima prudenza per evitare incidenti', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '537', '23024', '4569', '7', 'Giungendo in vicinanza di un incrocio bisogna passare sempre prima dei veicoli che arrivano da destra', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '537', '23024', '4570', '8', 'Giungendo in vicinanza di un incrocio bisogna sorpassare velocemente a sinistra se si vuole svoltare a destra', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '537', '23024', '4571', '9', 'Giungendo in vicinanza di un incrocio nei centri abitati occorre suonare il clacson', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '538', '23025', '', '', 'Comportamenti vietati in vicinanza o in corrispondenza degli incroci ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '538', '23025', '4572', '1', 'In vicinanza o in corrispondenza degli incroci non è consentito procedere a zig zag, anche se le corsie direzionali sono segnate da strisce discontinue (tratteggiate)', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '538', '23025', '4573', '2', 'In vicinanza o in corrispondenza degli incroci non è consentito cambiare improvvisamente la direzione di marcia', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '538', '23025', '4574', '3', 'In vicinanza o in corrispondenza degli incroci non è consentito effettuare l''inversione di marcia', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '538', '23025', '4575', '4', 'In corrispondenza di incroci extraurbani non è consentito effettuare la sosta', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '538', '23025', '4576', '5', 'In corrispondenza di incroci extraurbani non è consentito effettuare la fermata', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '538', '23025', '4577', '6', 'In vicinanza o in corrispondenza degli incroci non è consentito sorpassare una bicicletta, se si deve occupare la corsia opposta di marcia', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '538', '23025', '4578', '7', 'In vicinanza o in corrispondenza degli incroci non è consentito usare i fari anabbaglianti ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '538', '23025', '4579', '8', 'In vicinanza o in corrispondenza degli incroci extraurbani non è consentito superare la velocità di 60 km/h ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '538', '23025', '4580', '9', 'In corrispondenza degli incroci urbani è consentito non azionare l''indicatore di direzione per svoltare a destra ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '538', '23025', '4581', '10', 'In vicinanza o in corrispondenza degli incroci regolati da semaforo non è consentito il sorpasso ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '538', '23025', '4582', '11', 'In vicinanza o in corrispondenza degli incroci non si deve rallentare se il semaforo è a luce gialla lampeggiante ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '538', '23025', '4583', '12', 'In vicinanza o in corrispondenza degli incroci extraurbani non è consentito azionare i dispositivi di segnalazione acustica (clacson o trombe) ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '539', '23026', '', '', 'Comportamenti da tenere quando ad un incrocio giungono contemporaneamente due veicoli ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '539', '23026', '4584', '1', 'Se ad un incrocio giungono contemporaneamente da strade diverse due veicoli, l''obbligo di dare la precedenza spetta, di norma, al conducente del veicolo che arriva da sinistra', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '539', '23026', '4585', '2', 'Se ad un incrocio giungono contemporaneamente da strade diverse due veicoli, entrambi hanno l''obbligo di moderare la velocità, per evitare incidenti', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '539', '23026', '4586', '3', 'Giungendo ad un incrocio, bisogna essere prudenti e tolleranti nei confronti di chi, pur non avendo la precedenza, passa ugualmente per primo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '539', '23026', '4587', '4', 'Se ad un incrocio giungono contemporaneamente da strade diverse due veicoli, l''obbligo di dare la precedenza spetta al conducente del veicolo che percorre la strada più stretta', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '540', '23006', '', '', 'Comportamento agli incroci se non si è in grado di capire subito chi ha la precedenza ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '540', '23006', '4588', '1', 'Quando, giunti in prossimità di un incrocio, ci accorgiamo di aver sbagliato la corsia di preselezione dobbiamo seguire la direzione consentita dall''eventuale segnaletica orizzontale', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '540', '23006', '4589', '2', 'Quando, giunti in prossimità di un incrocio, ci accorgiamo di aver sbagliato la corsia di preselezione non dobbiamo effettuare cambiamenti di corsia, anche per non intralciare i veicoli che seguono', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '540', '23006', '4590', '3', 'Quando, giunti in prossimità di un incrocio, ci accorgiamo di aver sbagliato la corsia di preselezione non ci dobbiamo arrestare bruscamente, per evitare tamponamenti e confusione nella circolazione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '540', '23006', '4591', '4', 'Quando, giunti in prossimità di un incrocio, ci accorgiamo di aver sbagliato la corsia di preselezione non ci dobbiamo fermare a chiedere informazioni, per non ostacolare il flusso della circolazione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '540', '23006', '4592', '5', 'Quando, giunti in prossimità di un incrocio, ci accorgiamo di aver sbagliato la corsia di preselezione dobbiamo procedere nel senso voluto dalla segnaletica, dando le dovute precedenze', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '540', '23006', '4593', '6', 'Giungendo ad un incrocio, se non si è in grado di capire subito chi ha la precedenza, bisogna procedere con prudenza e accortezza', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '540', '23006', '4594', '7', 'Quando, giunti in prossimità di un incrocio, ci accorgiamo di aver sbagliato la corsia di preselezione dobbiamo effettuare la manovra di svolta che intendevamo fare, dando però precedenza a tutti i veicoli', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '540', '23006', '4595', '8', 'Quando, giunti in prossimità di un incrocio, ci accorgiamo di aver sbagliato la corsia di preselezione dobbiamo chiedere consiglio agli agenti del traffico', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '540', '23006', '4596', '9', 'Quando, giunti in prossimità di un incrocio, ci accorgiamo di aver sbagliato la corsia di preselezione dobbiamo azionare l''accensione simultanea di tutti gli indicatori di direzione, quale segnale di pericolo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '540', '23006', '4597', '10', 'Quando, giunti in prossimità di un incrocio, ci accorgiamo di aver sbagliato la corsia di preselezione dobbiamo accostarci sulla destra e fermarci finché non sia passata la colonna dei veicoli che seguono', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '540', '23006', '4598', '11', 'Quando, giunti in prossimità di un incrocio, ci accorgiamo di aver sbagliato la corsia di preselezione cerchiamo di retrocedere, con prudenza, fino al punto in cui le strisce continue di corsia', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '540', '23006', '4599', '12', 'Quando, giunti in prossimità di un incrocio, ci accorgiamo di aver sbagliato la corsia di preselezione dobbiamo arrivare al semaforo e attendere che la luce ci consenta di effettuare il cambio di corsia', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '540', '23006', '4600', '13', 'Giungendo ad un incrocio, se non si è in grado di capire subito chi ha la precedenza, si può proseguire senza rallentare, se si sceglie di andare diritto', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '541', '23009', '', '', 'Comportamento ad un incrocio al semaforo verde, il veicolo che precede tarda a riprendere la marcia ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '541', '23009', '4601', '1', 'Quando ad un incrocio urbano, al segnale di via libera, il veicolo che precede tarda a riprendere la marcia, è opportuno attendere pazientemente la ripresa della marcia', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '541', '23009', '4602', '2', 'Quando ad un incrocio urbano, al segnale di via libera, il veicolo che precede tarda a riprendere la marcia, è opportuno evitare di suonare il clacson con insistenza e di provocare una situazione di intolleranza negli altri automobilisti', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '541', '23009', '4603', '3', 'Quando ad un incrocio urbano, al segnale di via libera, il veicolo che precede tarda a riprendere la marcia, è opportuno, se c''è bisogno ed è possibile farlo senza pericolo, prestare aiuto all''automobilista rimasto fermo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '541', '23009', '4604', '4', 'Nei centri abitati è vietato l''uso del segnalatore acustico per sollecitare il conducente del veicolo che precede a riprendere la marcia quando il semaforo diventa verde', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '541', '23009', '4605', '5', 'Quando ad un incrocio urbano, al segnale di via libera, il veicolo che precede tarda a riprendere la marcia, non si deve superarlo con manovre pericolose', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '541', '23009', '4606', '6', 'Quando ad un incrocio urbano, al segnale di via libera, il veicolo che precede tarda a riprendere la marcia, è opportuno suonare insistentemente per sollecitare il conducente a rimettersi in moto', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '541', '23009', '4607', '7', 'Quando ad un incrocio urbano, al segnale di via libera, il veicolo che precede tarda a riprendere la marcia, è opportuno richiamare con il clacson l''attenzione degli automobilisti che seguono', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '541', '23009', '4608', '8', 'Quando ad un incrocio urbano, al segnale di via libera, il veicolo che precede tarda a riprendere la marcia, è opportuno tirare il freno a mano e scendere per cercare un agente del traffico che prenda i dovuti provvedimenti', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '541', '23009', '4609', '9', 'Quando ad un incrocio urbano, al segnale di via libera, il veicolo che precede tarda a riprendere la marcia, è opportuno cercare di sgombrare al più presto la carreggiata, anche invertendo il senso di marcia', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '542', '23030', '', '', 'Comportamento a un incrocio regolato con circolazione rotatoria ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '542', '23030', '4610', '1', 'Negli incroci regolati con circolazione rotatoria, in assenza di specifico segnale, vale la regola generale di dare la precedenza a destra', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '542', '23030', '4611', '2', 'Negli incroci regolati con rotatoria, la circolazione può essere disciplinata in modo che i veicoli in entrata diano la precedenza a quelli già circolanti nell''anello', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '542', '23030', '4612', '3', 'In un incrocio regolato con circolazione rotatoria, l''obbligo di dare la precedenza può essere imposto ai veicoli che stanno per immettersi', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '542', '23030', '4613', '4', 'Giungendo ad un incrocio in cui la circolazione è regolata con rotatoria, bisogna dare la precedenza ai veicoli provenienti da sinistra, solo se vi è il segnale DARE PRECEDENZA', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '542', '23030', '4614', '5', 'Giungendo ad un incrocio, regolato con circolazione rotatoria, prima di immettersi è opportuno moderare la velocità ed usare la massima prudenza, controllando nel contempo il comportamento degli altri utenti', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '542', '23030', '4615', '6', 'Giungendo ad un incrocio in cui la circolazione è regolata con rotatoria, è obbligatorio dare la precedenza ai veicoli provenienti da sinistra', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '542', '23030', '4616', '7', 'Per immettersi in un incrocio, regolato con circolazione rotatoria, bisogna dare sempre la precedenza ai veicoli già circolanti all''interno della rotatoria', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '542', '23030', '4617', '8', 'In un incrocio regolato con circolazione rotatoria, i veicoli circolanti nell''anello possono effettuare manovre di retromarcia', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '542', '23030', '4618', '9', 'Quando un incrocio è regolato con rotatoria, i veicoli circolanti all''interno dell''anello possono sorpassare sia a destra che a sinistra', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '543', '23023', '', '', 'Norme sulla precedenza ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '543', '23023', '4619', '1', 'È obbligatorio dare la precedenza a destra e a sinistra quando, da fermi, ci si immette nella circolazione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '543', '23023', '4620', '2', 'È obbligatorio dare la precedenza a tutti i veicoli circolanti sulle strade quando si effettua l''inversione del senso di marcia', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '543', '23023', '4621', '3', 'È obbligatorio dare la precedenza a tutti i veicoli quando si effettua la retromarcia', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '543', '23023', '4622', '4', 'Agli incroci è obbligatorio dare la precedenza ai veicoli circolanti su rotaie (tram e treni), salvo diversa  segnalazione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '543', '23023', '4623', '6', 'È obbligatorio dare la precedenza a destra e a sinistra quando si esce da un passo carrabile', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '543', '23023', '4624', '9', 'È obbligatorio dare la precedenza a destra e a sinistra in presenza del segnale ROTATORIA', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '543', '23023', '4625', '10', 'È obbligatorio dare la precedenza a tutti i veicoli circolanti negli incroci regolati da semaforo a luce gialla lampeggiante', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '543', '23023', '4626', '11', 'È obbligatorio dare la precedenza a tutti i veicoli circolanti quando si svolta a sinistra', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '544', '23023', '', '', 'Norme sulla precedenza ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '544', '23023', '4627', '1', 'È obbligatorio dare la precedenza a destra e a sinistra in presenza del segnale in figura ', 'V');
