INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '601', '25002', '5116', '3', 'Il conducente che intende effettuare il sorpasso ha l''obbligo di azionare il segnale acustico', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '601', '25002', '5117', '4', 'Il conducente che intende iniziare una manovra di sorpasso non ha l''obbligo di azionare l''indicatore di direzione', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '602', '25001', '', '', ' Obblighi del conducente che intende effettuare il sorpasso ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '602', '25001', '5118', '1', 'Il conducente che intende sorpassare deve accertarsi che la visibilità sia tale da consentire la manovra', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '602', '25001', '5119', '2', 'Il conducente che intende sorpassare deve accertarsi che la manovra possa compiersi senza costituire intralcio', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '602', '25001', '5120', '3', 'Il conducente che intende sorpassare deve accertarsi che il conducente che lo precede nella stessa corsia non abbia segnalato di voler compiere analoga manovra', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '602', '25001', '5121', '4', 'Il conducente che intende sorpassare deve accertarsi che il conducente che lo segue sulla stessa carreggiata non abbia iniziato il sorpasso', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '602', '25001', '5122', '5', 'Il conducente che intende sorpassare deve accertarsi che la strada sia libera per consentire la completa esecuzione del sorpasso', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '602', '25001', '5123', '6', 'Il conducente che intende sorpassare deve valutare lo spazio necessario per la manovra anche in relazione alla differenza di velocità tra il proprio veicolo e quello da sorpassare', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '602', '25001', '5124', '7', 'Il conducente che intende sorpassare deve tener conto di eventuali veicoli che provengono dalla direzione  opposta', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '602', '25001', '5125', '8', 'Il conducente che intende sorpassare deve valutare lo spazio necessario in relazione alla lunghezza del proprio veicolo e di quello da sorpassare', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '602', '25001', '5126', '9', 'Il conducente che intende sorpassare deve accertarsi che la larghezza della carreggiata consenta la manovra in condizioni di sicurezza', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '602', '25001', '5127', '10', 'Il conducente che intende sorpassare deve accertarsi che nessun segnale verticale vieti la manovra', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '602', '25001', '5128', '11', 'Il conducente che intende sorpassare deve accertarsi semplicemente che nessun veicolo provenga dalla direzione opposta', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '602', '25001', '5129', '12', 'Il conducente che intende sorpassare ha come unico obbligo quello di azionare l''indicatore di direzione (frecce)', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '602', '25001', '5130', '13', 'Il conducente che intende sorpassare deve unicamente accertarsi che non vi sia nessun segnale di divieto di sorpasso', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '602', '25001', '5131', '14', 'Il conducente che intende sorpassare deve assicurarsi che la potenza del motore del proprio veicolo sia maggiore di quella del veicolo da sorpassare', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '603', '25002', '', '', 'La manovra del sorpasso ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '603', '25002', '5132', '1', 'Il sorpasso si effettua tenendo una adeguata distanza laterale dal veicolo che si sorpassa', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '603', '25002', '5133', '2', 'Dopo aver effettuato una manovra di sorpasso il conducente del veicolo deve riportarsi a destra appena possibile senza creare pericolo o intralcio', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '603', '25002', '5134', '3', 'Dopo il sorpasso il veicolo può immediatamente spostarsi a destra se taglia la strada a quello sorpassato', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '604', '25005', '', '', 'La manovra del sorpasso ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '604', '25005', '5135', '1', 'Quando si inizia il sorpasso è consentito segnalare la manovra ai veicoli che precedono lampeggiando con i proiettori abbaglianti', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '604', '25005', '5136', '2', 'Nell''eseguire la manovra di sorpasso occorre regolare la velocità in relazione a quella del veicolo da sorpassare', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '604', '25005', '5137', '3', 'Per eseguire la manovra di sorpasso nei centri urbani si possono momentaneamente superare i limiti di velocità', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '604', '25005', '5138', '4', 'Nell''eseguire la manovra di sorpasso bisogna mantenere strettamente la destra', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '604', '25005', '5139', '5', 'Nell''eseguire la manovra di sorpasso non bisogna accelerare', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '604', '25005', '5140', '6', 'Dopo il sorpasso bisogna rientrare immediatamente a destra, rallentando', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '605', '25007', '', '', 'Cautele da adottare nella manovra del sorpasso ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '605', '25007', '5141', '1', 'Nella fase di sorpasso, il conducente è tenuto a evitare di stringere o di tagliare la strada al veicolo sorpassato', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '605', '25007', '5142', '2', 'Nella fase di rientro dal sorpasso il conducente deve segnalare acusticamente la manovra al veicolo sorpassato', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '605', '25007', '5143', '3', 'Nella fase di sorpasso, il conducente è tenuto a mantenersi il più a lungo possibile nella corsia di sorpasso', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '605', '25007', '5144', '4', 'Nella fase di sorpasso, il conducente è tenuto a segnalare continuamente con l''avvisatore acustico l''effettuazione della manovra', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '605', '25007', '5145', '5', 'Nella fase di sorpasso, il conducente è tenuto a mantenere una velocità almeno doppia di quella del veicolo sorpassato', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '605', '25007', '5146', '6', 'Nella fase di rientro dal sorpasso in autostrada, il conducente deve distanziarsi di almeno 100 m dal veicolo sorpassato', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '606', '25003', '', '', 'Sorpasso di più veicoli senza rientrare   EXTRA', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '606', '25003', '5147', '1', 'Dopo aver eseguito una prima manovra di sorpasso, in strade ad almeno due corsie per senso di marcia, è possibile eseguirne un''altra senza rientrare a destra, se non si crea intralcio', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '606', '25003', '5148', '2', 'È vietato effettuare un secondo sorpasso senza prima essersi riportati a destra ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '607', '25002', '', '', 'Cautele da adottare quando si viene sorpassati ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '607', '25002', '5149', '1', 'Il conducente del veicolo che viene sorpassato deve agevolare la manovra di sorpasso e non accelerare', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '607', '25002', '5150', '2', 'Il conducente del veicolo che viene sorpassato, nelle strade ad una corsia per senso di marcia, deve tenersi il più vicino possibile al margine destro', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '607', '25002', '5151', '3', 'Il conducente del veicolo sorpassato può gareggiare in velocità con il conducente del veicolo che lo sta sorpassando', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '608', '25004', '', '', 'Spazio necessario per la manovra di sorpasso ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '608', '25004', '5152', '1', 'Lo spazio necessario al sorpasso aumenta in relazione alla maggiore lunghezza del veicolo da sorpassare', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '608', '25004', '5153', '2', 'Lo spazio necessario alla manovra di sorpasso aumenta in relazione alla maggiore lunghezza del veicolo che sorpassa', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '608', '25004', '5154', '3', 'Lo spazio necessario al sorpasso è maggiore se vi è poca differenza di velocità tra i veicoli', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '608', '25004', '5155', '4', 'La distanza di sicurezza va rispettata anche quando si inizia la manovra di sorpasso', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '608', '25004', '5156', '5', 'La pericolosità del sorpasso nasce dalla possibilità di urtare altri veicoli che, davanti o dietro, intendano compiere la stessa manovra', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '608', '25004', '5157', '6', 'È pericoloso effettuare il rientro dopo il sorpasso se la manovra costringe i veicoli sorpassati a frenare bruscamente', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '608', '25004', '5158', '7', 'Prima di rientrare nella corsia di destra dopo il sorpasso, occorre controllare la posizione del veicolo sorpassato attraverso gli specchietti retrovisori ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '608', '25004', '5159', '8', 'Lo spazio necessario al sorpasso aumenta se il veicolo da sorpassare accelera', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '608', '25004', '5160', '9', 'Lo spazio necessario per il sorpasso aumenta se il veicolo da sorpassare rallenta', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '608', '25004', '5161', '10', 'Lo spazio necessario per il sorpasso è indipendente dalla lunghezza dei veicoli', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '609', '25008', '', '', 'Obblighi del conducente del veicolo che viene sorpassato ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '609', '25008', '5162', '1', 'Il conducente del veicolo da sorpassare ha l''obbligo di non gareggiare in velocità con il veicolo sorpassante', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '609', '25008', '5163', '2', 'Il conducente del veicolo da sorpassare ha l''obbligo di non accelerare la propria andatura', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '609', '25008', '5164', '3', 'Il conducente del veicolo da sorpassare ha l''obbligo di fermarsi appena possibile per far defluire il traffico se guida un veicolo molto lento', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '609', '25008', '5165', '4', 'Il conducente del veicolo da sorpassare ha l''obbligo di tenersi il più possibile vicino al margine destro su strade ad unica corsia per senso di marcia', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '609', '25008', '5166', '5', 'Il conducente del veicolo da sorpassare ha l''obbligo di portarsi al centro della carreggiata per evitare sorpassi in scia', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '609', '25008', '5167', '6', 'Il conducente del veicolo da sorpassare ha l''obbligo di azionare la segnalazione luminosa di pericolo (lampeggio simultaneo di tutte le frecce) per segnalare che la strada è sgombra', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '609', '25008', '5168', '7', 'Il conducente del veicolo da sorpassare ha l''obbligo di ridurre sempre la propria velocità', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '609', '25008', '5169', '8', 'Il conducente del veicolo da sorpassare ha l''obbligo di inserire le marce più basse per facilitare il sorpasso', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '610', '25007', '', '', 'Pericolosità del sorpasso', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '610', '25007', '5170', '1', 'La pericolosità del sorpasso è determinata dal rischio di urtare altri veicoli che manifestano l''intenzione di compiere la stessa manovra', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '610', '25007', '5171', '2', 'La pericolosità del sorpasso è determinata dal rischio di collisione, in fase di avvicinamento, con il veicolo da sorpassare', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '610', '25007', '5172', '3', 'La pericolosità del sorpasso è determinata dal rischio di collisione, in fase di rientro in corsia, con il veicolo sorpassato', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '610', '25007', '5173', '4', 'La pericolosità del sorpasso è determinata dal rischio di urto frontale con un veicolo che provenga dal senso contrario', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '610', '25007', '5174', '5', 'La pericolosità del sorpasso è determinata dalla ridotta larghezza della carreggiata', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '610', '25007', '5175', '6', 'La pericolosità del sorpasso è determinata dalla presenza, sulla strada, di delineatori normali di margine', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '611', '25009', '', '', 'Sopragggiunge un veicoli del senso opposto ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '611', '25009', '5176', '1', 'Se si viene sorpassati mentre sopraggiunge un veicoli del senso opposto, è consigliabile ridurre la velocità', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '611', '25009', '5177', '2', 'Se si viene sorpassati mentre sopraggiunge un veicoli del senso opposto, occore spostarsi il piu vicino possibile al margine destro della carreggiata', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '611', '25009', '5178', '3', 'Se si viene sorpassati ed avviene un incidente, si può avere una parte di responsabilità, se non si è fatto il possibile per facilitare la manovra di soprasso ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '611', '25009', '5179', '4', 'Se si viene sorpassati mentre sopraggiunge un veicolo dal senso opposto, occorre tenere accesi i proiettori abbaglianti, come segnale di pericolo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '611', '25009', '5180', '5', 'Se si viene sorpassati mentre sopraggiunge un veicolo dal senso opposto, si ha l''obbligo di mantenere la stessa velocità', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '611', '25009', '5181', '6', 'Se si viene sorpassati mentre sopraggiunge un veicolo dal senso opposto, occorre azionare l''indicatore di direzione destro, come segnale di pericolo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '612', '25009', '', '', 'Sorpasso dei veicoli della Polizia ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '612', '25009', '5182', '1', 'È consentito sorpassare un''auto della Polizia, se non è in servizio di emergenza e non vi sono altri tipi di impedimenti', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '612', '25009', '5183', '2', 'È vietato sorpassare un''auto della Polizia, non in servizio di emergenza, per non ostacolarne la marcia', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '612', '25009', '5184', '3', 'Sulle autostrade a tre corsie per senso di marcia è consentito sorpassare un''auto della polizia, anche se in servizio di emergenza', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '613', '25006', '', '', 'Casi in cui è vietato il sorpasso ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '613', '25006', '5185', '1', 'È vietato sorpassare accodandosi ad un''ambulanza in servizio di emergenza', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '613', '25006', '5186', '2', 'Non si può sorpassare in corrispondenza dei dossi su carreggiata a senso unico a due corsie', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '613', '25006', '5187', '3', 'Non si può sorpassare un veicolo che abbia segnalato l''intenzione di accostarsi al margine destro della carreggiata', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '613', '25006', '5188', '4', 'Non si può sorpassare in corrispondenza dell''uscita di un pubblico parcheggio', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '613', '25006', '5189', '5', 'È vietato il sorpasso se alla sinistra del veicolo si trova una linea bianca discontinua affiancata da una continua', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '614', '25010', '', '', 'Sorpasso in autostrada ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '614', '25010', '5190', '1', 'Durante un sorpasso in autostrada, la scarsa attenzione posta ai veicoli che seguono può essere causa di incidente', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '614', '25010', '5191', '2', 'Durante un sorpasso in autostrada, l''insufficiente distanza di sicurezza può essere causa di incidente', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '614', '25010', '5192', '3', 'Durante un sorpasso in autostrada, può essere causa di incidente l''improvviso spostamento sulla corsia di sorpasso di uno dei veicoli che precedono', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '614', '25010', '5193', '4', 'Durante un sorpasso in autostrada, mantenersi troppo a lungo sulla corsia di sorpasso può essere motivo di intralcio', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '614', '25010', '5194', '5', 'Il sorpasso in autostrada può essere motivo di incidente, se viene effettuato accodandosi ad un veicolo che sta sorpassando', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '614', '25010', '5195', '6', 'Il sorpasso in autostrada può essere motivo di incidente, se il forte vento laterale compromette la stabilità dei veicoli', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '614', '25010', '5196', '7', 'Il sorpasso in autostrada può essere motivo di incidente, se viene effettuato in vicinanza di una piazzola di sosta', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '614', '25010', '5197', '8', 'Il sorpasso in autostrada può essere motivo di incidente, se si viaggia con le luci anabbaglianti sempre accese', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '614', '25010', '5198', '9', 'In autostrada si può sorpassare indifferentemente a destra e a sinistra ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '615', '25003', '', '', 'Sorpasso a destra', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '615', '25003', '5199', '1', 'La manovra di sorpasso deve essere eseguita a destra se il conducente del veicolo da sorpassare ha segnalato l''intenzione di voler svoltare a sinistra', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '615', '25003', '5200', '2', 'La manovra di sorpasso deve essere eseguita a destra se, su strada a senso unico, il conducente del veicolo da sorpassare ha segnalato l''intenzione di voler accostare a sinistra', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '615', '25003', '5201', '3', 'La manovra di sorpasso del tram può effettuarsi su ambo i lati se la strada è a senso unico e la larghezza della carreggiata lo consente', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '615', '25003', '5202', '4', 'Se il tram o il filobus sono fermi la manovra di sorpasso può effettuarsi a destra se esiste il salvagente', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '615', '25003', '5203', '5', 'Se il tram o il filobus sono fermi la manovra di sorpasso non può effettuarsi a destra se manca il salvagente', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '615', '25003', '5204', '6', 'Se il tram o il filobus sono fermi per la salita e la discesa dei passeggeri, la manovra di sorpasso può sempre effettuarsi a destra', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '615', '25003', '5205', '7', 'Non si può sorpassare sulla destra il tram che marcia al centro della carreggiata', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '615', '25003', '5206', '8', 'Il sorpasso a destra è consentito quando il filobus è fermo per la salita e la discesa dei passeggeri, anche se non esiste salvagente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '615', '25003', '5207', '9', 'Il sorpasso a destra è consentito quando si percorre una strada extraurbana a senso unico', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '615', '25003', '5208', '10', 'È sempre vietato effettuare la manovra di sorpasso a destra', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '615', '25003', '5209', '11', 'La manovra di sorpasso può, di norma, effettuarsi a destra sulle autostrade', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '615', '25003', '5210', '12', 'La manovra di sorpasso può effettuarsi a sinistra soltanto se il veicolo da sorpassare sta svoltando a destra', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '615', '25003', '5211', '13', 'Il sorpasso tra motocicli si può effettuare sia a destra che a sinistra ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '615', '25003', '5212', '14', 'È consentito il sorpasso a destra dei motocicli se vi è spazio sufficiente ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '616', '25004', '', '', 'Casi in cui è vietato il sorpasso - 1', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '616', '25004', '5213', '1', 'È vietato il sorpasso se si deve valicare la striscia longitudinale bianca continua semplice o doppia', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '616', '25004', '5214', '2', 'Il sorpasso è vietato in caso di scarsa visibilità', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '616', '25004', '5215', '3', 'È obbligatorio accertarsi prima di effettuare il sorpasso che non vi siano segnali che lo vietino', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '616', '25004', '5216', '4', 'È vietato sorpassare sulle corsie di accelerazione o di decelerazione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '616', '25004', '5217', '5', 'È consentito il sorpasso in prossimità o in corrispondenza di passaggi a livello senza barriere se la circolazione è regolata da semafori', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '616', '25004', '5218', '6', 'È vietato il sorpasso nelle strade a senso unico', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '616', '25004', '5219', '7', 'Il sorpasso è vietato se si è costretti a superare la linea longitudinale tratteggiata', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '616', '25004', '5220', '8', 'È vietato il sorpasso quando il veicolo che precede ha azionato l''indicatore di direzione destro ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '616', '25004', '5221', '9', 'È vietato il sorpasso se nevica ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '616', '25004', '5222', '10', 'Il sorpasso è vietato quando sulla carreggiata è tracciata la linea tratteggiata di mezzeria', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '616', '25004', '5223', '11', 'Il sorpasso è vietato quando sulle strade di montagna si è preceduti da un autobus di linea', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '616', '25004', '5224', '12', 'Per effettuare il sorpasso bisogna considerare che un veicolo trainante un caravan non può mai essere superato in una discesa a forte pendenza', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '617', '25004', '', '', 'Casi in cui è vietato il sorpasso - 3', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '617', '25004', '5225', '1', 'Il sorpasso è vietato in prossimità delle curve su strade a due corsie a doppio senso di marcia', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '617', '25004', '5226', '2', 'Il sorpasso è vietato in prossimità dei dossi su strade a due corsie a doppio senso di marcia', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '617', '25004', '5227', '3', 'Il sorpasso è consentito su dossi se la strada è a due carreggiate separate e a due corsie per senso di marcia.', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '617', '25004', '5228', '4', 'Il sorpasso è vietato in curva anche se la strada è a due carreggiate separate', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '617', '25004', '5229', '5', 'Il sorpasso è vietato in curva anche se la strada è ad almeno due corsie per senso di marcia', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '617', '25004', '5230', '6', 'Il sorpasso è vietato sulle curve e sui dossi delle strade a senso unico', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '618', '25004', '', '', 'Casi in cui è vietato il sorpasso - 4', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '618', '25004', '5231', '1', 'È vietato il sorpasso di un veicolo che abbia rallentato in corrispondenza di un passaggio pedonale per consentire l''attraversamento dei pedoni', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '618', '25004', '5232', '2', 'È vietato il sorpasso di un veicolo che si sia arrestato in corrispondenza di un passaggio pedonale per consentire l''attraversamento di pedoni', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '618', '25004', '5233', '3', 'È vietato il sorpasso se il veicolo da superare si è fermato per dare precedenza ai pedoni, anche fuori dagli attraversamenti pedonali', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '618', '25004', '5234', '4', 'È vietato il sorpasso in corrispondenza dei passi carrabili ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '618', '25004', '5235', '5', 'È vietato il sorpasso in prossimità degli ospedali ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '618', '25004', '5236', '6', 'È vietato il sorpasso in prossimità delle stazioni di servizio ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '618', '25004', '5237', '7', 'Il sorpasso è vietato di notte, nei centri urbani con pubblica illuminazione insufficiente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '619', '25004', '', '', 'Casi in cui è vietato il sorpasso - 5', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '619', '25004', '5238', '1', 'È vietato il sorpasso di un veicolo che ne stia superando un altro se ciò impone di spostarsi sul lato riservato all''altro senso di marcia', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '619', '25004', '5239', '2', 'È vietato il sorpasso di veicoli in lento movimento ai passaggi a livello quando, a tal fine è necessario spostarsi sul lato riservato all''altro senso di marcia', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '619', '25004', '5240', '3', 'È vietato il sorpasso di veicoli fermi ai passaggi a livello, se bisogna impegnare la semicarreggiata opposta', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '619', '25004', '5241', '4', 'È vietato il sorpasso di veicoli fermi ai semafori quando, a tal fine è necessario spostarsi sul lato riservato all''altro senso di marcia', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '619', '25004', '5242', '5', 'È vietato il sorpasso di veicoli in lento movimento a causa di congestione della circolazione quando, a tal fine è necessario spostarsi sul lato riservato all''altro senso di marcia', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '619', '25004', '5243', '6', 'È vietato il sorpasso di un autobus fermo per la salita e la discesa dei passeggeri', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '619', '25004', '5244', '7', 'È vietato il sorpasso di veicoli della polizia', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '619', '25004', '5245', '8', 'È vietato il sorpasso degli autobus in servizio pubblico di linea ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '620', '25004', '', '', 'Casi in cui è consentito il sorpasso ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '620', '25004', '5246', '1', 'È consentito il sorpasso in prossimità o in corrispondenza delle intersezioni se regolate dai semafori', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '620', '25004', '5247', '2', 'È consentito il sorpasso a destra nelle intersezioni regolate da semaforo, se il veicolo da sorpassare ha indicato di voler svoltare a sinistra ed ha iniziato la manovra', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '620', '25004', '5248', '3', 'È consentito il sorpasso nelle intersezioni se la strada è a precedenza purché a due carreggiate separate e le corsie siano delimitate dall''apposita segnaletica orizzontale', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '620', '25004', '5249', '4', 'È consentito il sorpasso in corrispondenza delle intersezioni se si supera un veicolo a due ruote non a motore e non si invade il lato riservato al senso opposto', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '620', '25004', '5250', '5', 'È consentito il sorpasso nelle intersezioni regolate da agenti del traffico', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '620', '25004', '5251', '6', 'È consentito il sorpasso sul tratto discendente di un dosso in una strada a due corsie a doppio senso di marcia', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '620', '25004', '5252', '7', 'Il sorpasso è consentito in curva se la strada è due carreggiate separate con almeno due corsie per senso di marcia.', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '620', '25004', '5253', '8', 'È consentito il sorpasso sulle intersezioni purché non provenga nessuno dalla destra', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '620', '25004', '5254', '9', 'È consentito il sorpasso di veicoli in lento movimento ai passaggi a livello senza barriere se il treno è distante', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '620', '25004', '5255', '10', 'Il sorpasso è consentito in caso di scarsa visibilità purché si lampeggi durante la manovra', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '620', '25004', '5256', '11', 'È consentito il sorpasso di veicoli fermi ai passaggi a livello senza barriere se il treno è distante', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '620', '25004', '5257', '12', 'È consentito il sorpasso sulle corsie d''accelerazione purché la manovra venga effettuata con attenzione', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '620', '25004', '5258', '13', 'In caso di intenso traffico è consentito ai motociclisti utilizzare per il sorpasso le corsie riservate', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '620', '25004', '5259', '14', 'È sempre consentito superare un veicolo a due ruote', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '621', '25006', '', '', 'Casi in cui è ammesso il sorpasso in prossimità dei dossi ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '621', '25006', '5260', '1', 'Il sorpasso in prossimità di un dosso è consentito sulle strade a senso unico', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '621', '25006', '5261', '2', 'Il sorpasso in prossimità di un dosso è consentito sulle carreggiate a senso unico', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '621', '25006', '5262', '3', 'Il sorpasso in prossimità di un dosso è consentito sulle strade a due corsie per senso di marcia', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '621', '25006', '5263', '4', 'Il sorpasso in prossimità di un dosso è consentito sulle strade a due carreggiate separate', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '621', '25006', '5264', '5', 'Su carreggiate a due corsie a doppio senso di marcia, in prossimità di un dosso, è consentito sorpassare veicoli molto  lenti', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '621', '25006', '5265', '6', 'Su carreggiate a due corsie e a doppio senso in prossimità di un dosso, è consentito sorpassare veicoli a trazione animale', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '621', '25006', '5266', '7', 'Su carreggiate a due corsie a doppio senso di marcia in prossimità di un dosso, è consentito sorpassare nella scia del veicolo che precede', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('15', '621', '25006', '5267', '8', 'In prossimità di un dosso è consentito invadere l''opposta semicarreggiata, per sorpassare autobus di linea fermi per la salita e discesa dei passeggeri', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '622', '26003', '', '', 'Operazioni da compiere prima di iniziare a guidare un veicolo ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '622', '26003', '5268', '1', 'Prima di iniziare a guidare un veicolo a motore bisogna essere certi che la categoria di patente posseduta ne consenta la guida', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '622', '26003', '5269', '2', 'Prima di iniziare a guidare un veicolo, bisogna essere certi del perfetto stato di efficienza del mezzo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '622', '26003', '5270', '3', 'Prima di iniziare a guidare un veicolo bisogna accertarsi che gli oggetti trasportati siano sistemati in modo da evitarne la caduta o dispersione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '622', '26003', '5271', '4', 'Prima di iniziare a guidare un veicolo, bisogna controllare che non sia compromessa la visibilità posteriore e laterale, per la presenza di passeggeri o per il carico mal posizionato', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '622', '26003', '5272', '5', 'Prima di iniziare a guidare un autoveicolo, bisogna individuare i comandi (leve e pulsanti) necessari per la guida e comprenderne bene il funzionamento', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '622', '26003', '5273', '6', 'Prima di iniziare a guidare un autoveicolo è opportuno regolare il sedile e il poggiatesta, secondo la propria statura', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '622', '26003', '5274', '7', 'Prima di iniziare a guidare un autoveicolo è opportuno regolare gli specchi retrovisori interni ed esterni', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '622', '26003', '5275', '8', 'Prima di iniziare a guidare un motociclo è opportuno regolare gli specchi secondo la propria statura', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '622', '26003', '5276', '9', 'Prima di iniziare a guidare un''autovettura, bisogna controllare che i passeggeri abbiano regolarmente allacciato le cinture di sicurezza', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '622', '26003', '5277', '10', 'Quando si guida un veicolo a motore, bisogna avere con sé tutti i documenti necessari per la guida (carta di circolazione, certificato di assicurazione, patente ecc.), e in corso di validità', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '622', '26003', '5278', '11', 'Prima di iniziare a guidare un veicolo a motore bisogna regolare il contachilometri parziale', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '622', '26003', '5279', '12', 'Prima di iniziare a guidare un veicolo a motore bisogna disinserire l''airbag del lato conducente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '622', '26003', '5280', '13', 'Prima di iniziare a guidare un autoveicolo, bisogna verificare che le cinture di sicurezza siano dotate di pretensionatore', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '622', '26003', '5281', '14', 'Prima di iniziare a guidare un veicolo, bisogna verificarne lo stato di efficienza solo se si deve intraprendere un lungo viaggio', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '623', '26004', '', '', 'Fermata e arresto', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '623', '26004', '5282', '1', 'L''arresto è l''interruzione della marcia del veicolo dovuta ad esigenze della circolazione, come ad esempio ad un semaforo rosso', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '623', '26004', '5283', '2', 'La fermata di un veicolo è la temporanea sospensione della marcia per esigenze di brevissima durata', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '623', '26004', '5284', '3', 'La fermata di un veicolo è la temporanea sospensione della marcia per consentire la salita o la discesa delle persone', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '623', '26004', '5285', '4', 'La fermata di un veicolo è la temporanea sospensione della marcia per esigenze di brevissima durata, come ad esempio per chiedere informazioni agli agenti del traffico', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '623', '26004', '5286', '5', 'L''arresto è la temporanea sospensione della marcia per esigenze del conducente o dei passeggeri di un veicolo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '623', '26004', '5287', '6', 'La fermata è l''interruzione della marcia del veicolo dovuta ad esigenze della circolazione', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '623', '26004', '5288', '7', 'La fermata per esigenze del conducente può avere la durata massima di dieci minuti', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '623', '26004', '5289', '8', 'La fermata di un veicolo è vietata se i passeggeri che scendono sono più di uno', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '624', '26005', '', '', 'Obblighi del conducente in caso di fermata', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '624', '26005', '5290', '1', 'La fermata non deve arrecare intralcio alla circolazione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '624', '26005', '5291', '2', 'Durante la fermata il conducente non deve impedire con il proprio autoveicolo il normale flusso del traffico', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '624', '26005', '5292', '3', 'Durante la fermata il conducente deve comportarsi in modo da non costituire pericolo  agli altri utenti della strada', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '624', '26005', '5293', '4', 'Durante la fermata il conducente deve essere sempre presente e pronto a riprendere la marcia', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '624', '26005', '5294', '5', 'Durante la fermata il conducente deve sempre adottare le opportune cautele atte ad evitare incidenti', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '624', '26005', '5295', '6', 'Non è consentito fermarsi per chiedere informazioni agli agenti del traffico, quando ciò possa causare intralcio o rallentamento alla circolazione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '624', '26005', '5296', '7', 'Durante la fermata il conducente può allontanarsi per brevissimo tempo prima di riprendere la marcia', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '624', '26005', '5297', '8', 'La fermata di un veicolo è sempre consentita per chiedere informazioni agli agenti del traffico', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '624', '26005', '5298', '9', 'Durante la fermata il conducente, se si allontana, deve lasciare il veicolo con il motore acceso', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '625', '26010', '', '', 'Sosta ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '625', '26010', '5299', '1', 'La sosta è la sospensione della marcia del veicolo protratta nel tempo, con possibilità del conducente di allontanarsi', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '625', '26010', '5300', '2', 'Durante la sosta il conducente può allontanarsi dal veicolo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '625', '26010', '5301', '3', 'Durante la sosta il conducente deve adottare le opportune cautele atte ad evitare incidenti', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '625', '26010', '5302', '4', 'Durante la sosta il conducente deve impedire l''uso del veicolo senza il suo consenso', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '625', '26010', '5303', '5', 'Durante la sosta il conducente non può allontanarsi dal veicolo per più di tre ore', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '625', '26010', '5304', '6', 'Durante la sosta nelle ore notturne il conducente deve lasciare in ogni caso accese le luci di posizione', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '626', '26022', '', '', 'Sosta di emergenza ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '626', '26022', '5305', '1', 'La sosta di emergenza è l''interruzione della marcia nel caso in cui il veicolo è inutilizzabile per avaria', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '626', '26022', '5306', '2', 'La sosta di emergenza è l''interruzione della marcia nel caso in cui il veicolo deve arrestarsi per malessere fisico del conducente o di un passeggero', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '626', '26022', '5307', '3', 'La sosta di emergenza è l''interruzione della marcia dovuta ad esigenze della circolazione', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '626', '26022', '5308', '4', 'La sosta di emergenza è la temporanea sospensione della marcia per consentire la discesa di persona invalida', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '626', '26022', '5309', '5', 'La sosta di emergenza è la temporanea sospensione della marcia per consentire ad una persona invalida di salire sul veicolo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '627', '26006', '', '', 'Posizionamento del veicolo in caso di fermata ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '627', '26006', '5310', '1', 'In caso di fermata, ove non esista marciapiede rialzato, il conducente deve lasciare uno spazio non inferiore ad un metro per il transito dei pedoni', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '627', '26006', '5311', '2', 'Fuori dei centri abitati, il conducente, in caso di fermata, deve collocare, se possibile, il veicolo fuori della carreggiata', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '627', '26006', '5312', '3', 'Fuori dei centri abitati, il conducente, in caso di fermata, deve collocare il veicolo fuori della carreggiata, ma non sulle piste ciclabili', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '627', '26006', '5313', '4', 'Fuori dei centri abitati, se non è possibile collocare il veicolo fuori della carreggiata, il conducente deve effettuare la fermata il più vicino possibile al margine destro della carreggiata', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '627', '26006', '5314', '5', 'Fuori dei centri abitati, il conducente, in caso di fermata ha l''obbligo di collocare il veicolo fuori della carreggiata, eventualmente su una pista ciclabile', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '627', '26006', '5315', '6', 'In caso di fermata su strada urbana in cui non esiste il marciapiede rialzato, il conducente deve collocare il veicolo il più vicino possibile al margine destro della carreggiata', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '628', '26012', '', '', 'Posizionamento del veicolo in caso di sosta ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '628', '26012', '5316', '1', 'Salvo diversa segnalazione il conducente, in caso di sosta nei centri abitati, deve collocare il veicolo il più vicino possibile al margine destro della carreggiata, parallelamente ad esso e secondo il senso di marcia, purché esista il marciapiede rialzato ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '628', '26012', '5317', '2', 'In caso di sosta ove non esista marciapiede rialzato, il conducente deve lasciare uno spazio non inferiore ad un metro per il transito dei pedoni ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '628', '26012', '5318', '3', 'Fuori dei centri abitati, il conducente che deve sostare ha l''obbligo di collocare il veicolo, ove possibile, fuori della carreggiata ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '628', '26012', '5319', '4', 'Fuori dei centri abitati, il conducente che deve sostare ha l''obbligo di collocare il veicolo fuori della carreggiata, ma non sulle piste ciclabili', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '628', '26012', '5320', '5', 'Fuori dei centri abitati il conducente deve collocare il veicolo in sosta fuori della carreggiata, ma non sulle banchine, salvo diversa segnalazione ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '628', '26012', '5321', '6', 'Fuori dei centri abitati, in caso di impossibilità a collocare il veicolo fuori della carreggiata, il conducente deve sostare il più vicino possibile al margine destro della carreggiata ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '628', '26012', '5322', '7', 'In caso di sosta in un centro abitato, il conducente deve collocare il veicolo il più vicino possibile al margine destro della carreggiata, anche dove non esiste il marciapiede rialzato ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '628', '26012', '5323', '8', 'Fuori dei centri abitati, il conducente in caso di sosta deve collocare il veicolo  fuori della carreggiata, eventualmente anche sulle piste ciclabili ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '628', '26012', '5324', '9', 'Fuori dei centri abitati, in caso di impossibilità di collocare il veicolo fuori della carreggiata, il conducente non deve effettuare la sosta ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '629', '26013', '', '', ' Posizionamento del veicolo in caso di sosta ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '629', '26013', '5325', '1', 'Nelle strade urbane a senso unico di marcia, la sosta è consentita anche lungo il margine sinistro della carreggiata, purché rimanga uno spazio sufficiente al transito almeno di una fila di veicoli', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '629', '26013', '5326', '2', 'Nelle strade urbane a senso unico di marcia, la sosta è consentita anche lungo il margine sinistro della carreggiata, purché rimanga uno spazio non inferiore a tre metri di larghezza per il transito dei veicoli', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '629', '26013', '5327', '3', 'Nelle zone predisposte per la sosta, il conducente deve collocare il veicolo nel modo prescritto dalla segnaletica', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '629', '26013', '5328', '4', 'Nelle zone nelle quali gli spazi di sosta sono delimitati da segnaletica orizzontale, il conducente deve sempre sistemare il proprio veicolo entro uno degli appositi spazi (stalli), senza invadere quelli contigui', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '629', '26013', '5329', '5', 'Nelle strade urbane a senso unico di marcia, la sosta è sempre consentita anche lungo il margine sinistro della carreggiata', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '629', '26013', '5330', '6', 'È vietato sostare con autoveicoli o motocicli nei centri abitati, quando non esistano le apposite strisce', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '629', '26013', '5331', '7', 'Nelle zone nelle quali gli spazi di sosta sono delimitati da segnaletica orizzontale, il conducente può sistemare il proprio veicolo anche fuori dall''apposito spazio (stallo)  ad esso destinato, a condizione che la sosta sia di brevissima    ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '629', '26013', '5332', '8', 'In una zona in cui la sosta è regolata con parchimetro, non occorre utilizzarlo se la sosta è di breve durata', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '630', '26011', '', '', 'Obbligo di bloccare il veicolo in caso di sosta', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '630', '26011', '5333', '1', 'Durante la sosta è opportuno che il conducente azioni il freno di stazionamento ed  inserisca il rapporto più basso del cambio di velocità', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '630', '26011', '5334', '2', 'Durante la sosta il conducente deve adottare accorgimenti atti a garantire l''immobilità del veicolo, indipendentemente dal grado di pendenza della strada', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '630', '26011', '5335', '3', 'Durante la sosta nelle strade a forte pendenza, è opportuno che il conducente azioni il freno di stazionamento, inserisca il rapporto più basso del cambio di velocità e lasci il veicolo con le ruote sterzate verso il marciapiede', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '630', '26011', '5336', '4', 'Durante la sosta il conducente deve lasciare il veicolo con il motore spento', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '630', '26011', '5337', '5', 'Durante la sosta il conducente deve posizionare il cambio di velocità dell''autovettura in folle', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '630', '26011', '5338', '6', 'Durante la sosta il conducente che lascia il veicolo su strada in salita, deve avere cura di inserire la quarta marcia', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '631', '26001', '', '', 'Parcheggio in zona regolamentata con parchimetro o disco orario ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '631', '26001', '5339', '1', 'Se si parcheggia in una zona regolamentata con parchimetro, occorre esporre l''apposito tagliando, in modo che sia ben visibile', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '631', '26001', '5340', '2', 'Per parcheggiare in una zona regolamentata mediante disco orario, bisogna esporre in modo ben visibile l''orario di arrivo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '631', '26001', '5341', '3', 'In una zona in cui la sosta è regolamentata con parchimetro, non occorre utilizzarlo se ci si ferma soltanto per far salire o scendere un passeggero', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '631', '26001', '5342', '4', 'Nelle aree di parcheggio a tempo limitato, i veicoli al servizio di persone diversamente abili non sono obbligati a rispettare il limite di tempo stabilito per la sosta', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '631', '26001', '5343', '5', 'Per parcheggiare in una zona regolamentata mediante disco orario, bisogna indicare sul disco l''orario di fine della sosta, oppure trascriverlo su un foglio di carta', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '631', '26001', '5344', '6', 'In una zona in cui la sosta è regolamentata mediante disco orario, prima che scada il tempo di validità della sosta è consentito aggiornare l''orario, senza che il veicolo venga spostato', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '631', '26001', '5345', '7', 'In una zona in cui la sosta è regolamentata con parchimetro, non occorre utilizzare il parchimetro se si effettua una breve sosta', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '631', '26001', '5346', '8', 'Se si parcheggia in una zona regolamentata mediante disco orario, bisogna trascrivere su un foglio di carta l''ora di arrivo, senza indicare i minuti', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '632', '26002', '', '', 'Cautele da adottare in caso di sosta nell’aprire le portiere e nello scendere dal veicolo ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '632', '26002', '5347', '1', 'Il conducente di un autoveicolo in sosta sul lato destro di una strada urbana, prima di aprire la portiera, deve fare particolare attenzione ai veicoli che sopraggiungono', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '632', '26002', '5348', '2', 'Il conducente di un autoveicolo in sosta su una strada stretta, prima di scendere deve controllare che non sopraggiungano veicoli', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '632', '26002', '5349', '3', 'È opportuno che il conducente di un autoveicolo in sosta sul lato sinistro in una strada urbana a senso unico, ricordi al passeggero di prestare particolare attenzione ai veicoli che sopraggiungono, prima di aprire la portiera', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '632', '26002', '5350', '4', 'È opportuno che il conducente di un autoveicolo, in sosta sul lato destro di una strada urbana, ricordi al passeggero che apre la portiera di destra di fare attenzione ai pedoni in transito', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '632', '26002', '5351', '5', 'Il conducente di un autoveicolo in sosta su una strada stretta, prima di scendere deve preoccuparsi dei veicoli che sopraggiungono da dietro, ma non di quelli di fronte', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '632', '26002', '5352', '6', 'Il conducente di un autoveicolo può lasciare il motore acceso se sosta per poco tempo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '633', '26023', '', '', 'Cautele da adottare in caso di sosta nell’aprire le portiere e nello scendere dal veicolo ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '633', '26023', '5353', '1', 'Quando si aprono le porte di un veicolo, bisogna assicurarsi che ciò non costituisca pericolo o intralcio per gli altri utenti della strada', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '633', '26023', '5354', '2', 'Prima di aprire lo sportello di un veicolo dal lato rivolto verso il centro della strada bisogna assicurarsi che non sopraggiungano altri veicoli', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '633', '26023', '5355', '3', 'Prima di aprire lo sportello di un veicolo dal lato rivolto verso il marciapiede bisogna assicurarsi che non sopraggiungano pedoni', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '633', '26023', '5356', '4', 'Quando si scende da un veicolo bisogna assicurarsi che ciò non costituisca pericolo o intralcio per gli altri utenti della strada', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '633', '26023', '5357', '5', 'Allorché si accinge a scendere dall''autoveicolo, il conducente deve assicurarsi, anche mediante lo specchietto retrovisore, che non sopraggiungano altri veicoli', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '633', '26023', '5358', '6', 'Prima di scendere dal veicolo in sosta, il conducente deve assicurarsi di avere spento il motore', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '633', '26023', '5359', '7', 'L''apertura di uno sportello del veicolo dal lato del marciapiede comporta l''obbligo di attivare l''indicatore di direzione da quel lato', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '633', '26023', '5360', '8', 'L''apertura degli sportelli del veicolo dal lato rivolto verso il centro della strada è vietato', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '633', '26023', '5361', '9', 'Il conducente può lasciare il veicolo in sosta con il motore acceso per consentire il funzionamento dell''aria condizionata', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '633', '26023', '5362', '10', 'Prima di scendere dal veicolo in sosta, il conducente deve azionare il freno a mano del veicolo, su tutte le strade in discesa ma non in quelle in salita', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '634', '26007', '', '', 'Divieto di fermata', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '634', '26007', '5363', '1', 'La fermata è vietata in corrispondenza o in prossimità dei passaggi a livello', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '634', '26007', '5364', '2', 'La fermata è vietata nelle gallerie, salvo diversa segnalazione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '634', '26007', '5365', '3', 'La fermata è vietata sui binari di linee ferroviarie o tranviarie o così vicino ad essi da intralciare la marcia dei veicoli su rotaia', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '634', '26007', '5366', '4', 'La fermata è vietata nei sottovia, sotto i sovrapassaggi, sotto i fornici e i portici, salvo diversa segnalazione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '634', '26007', '5367', '5', 'La fermata è vietata sui dossi', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '634', '26007', '5368', '6', 'Fuori dai centri abitati e sulle strade urbane di scorrimento la fermata è vietata in prossimità dei dossi', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '634', '26007', '5369', '7', 'La fermata è vietata nelle curve e, fuori dei centri abitati e sulle strade urbane di scorrimento, anche in loro prossimità', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '634', '26007', '5370', '8', 'La fermata è vietata sul margine destro della carreggiata', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '634', '26007', '5371', '9', 'La fermata è vietata lungo il margine sinistro di una carreggiata a senso unico di circolazione', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '634', '26007', '5372', '10', 'La fermata di breve durata è consentita anche in curva', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '634', '26007', '5373', '11', 'La fermata è vietata allo sbocco dei passi carrabili', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '634', '26007', '5374', '12', 'La fermata è vietata sulle aree destinate al mercato o al carico e allo scarico delle merci', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '635', '26008', '', '', 'Divieto di fermata ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '635', '26008', '5375', '1', 'La fermata è vietata in prossimità e in corrispondenza di segnali stradali verticali in modo da occultarne la vista', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '635', '26008', '5376', '2', 'La fermata è vietata in prossimità e in corrispondenza di segnali semaforici in modo da occultarne la vista', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '635', '26008', '5377', '3', 'La fermata è vietata in corrispondenza dei segnali orizzontali di preselezione e lungo le corsie di canalizzazione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '635', '26008', '5378', '4', 'Fuori dei centri abitati, la fermata è vietata in corrispondenza e in prossimità delle aree di intersezione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '635', '26008', '5379', '5', 'Nei centri abitati, la fermata e la sosta sono vietate in corrispondenza delle aree di intersezione e in prossimità di esse a meno di 5 metri, salvo diversa segnalazione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '635', '26008', '5380', '6', 'La fermata è vietata in prossimità dei segnali stradali, anche se il veicolo non ne occulta la vista', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '635', '26008', '5381', '7', 'La fermata è vietata lungo le autostrade, anche in caso di emergenza', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '635', '26008', '5382', '8', 'La fermata è consentita sulle corsie di accelerazione, purché brevissima', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '635', '26008', '5383', '9', 'Nei centri abitati la fermata è vietata in prossimità delle aree di intersezione, a più di 5 metri', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '635', '26008', '5384', '10', 'La fermata è vietata limitatamente alle ore di esercizio, in corrispondenza dei distributori di carburante', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '636', '26015', '', '', 'Divieto di sosta e di fermata', '');
