INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '636', '26015', '5391', '7', 'La fermata è vietata dopo il segnale DIVIETO DI SOSTA', 'F');
