INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '637', '26009', '5395', '4', 'La fermata è consentita allo sbocco dei passi carrabili', 'V');
