INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '637', '26009', '5398', '7', 'La fermata per esigenze di brevissima durata è vietata sulle piste ciclabili ma non agli sbocchi di esse', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '637', '26009', '5399', '8', 'La fermata è vietata allo sbocco dei passi carrabili', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '637', '26009', '5400', '9', 'La fermata è vietata in ogni caso nelle ore notturne', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '637', '26009', '5401', '10', 'La sosta ma non la fermata è vietata sugli attraversamenti pedonali', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '637', '26009', '5402', '11', 'La sosta, ed anche la fermata, sono vietate negli spazi destinati a servizi di emergenza o di igiene pubblica indicati dalla apposita segnaletica', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '637', '26009', '5403', '12', 'La sosta così come la fermata sono vietate davanti ai cassonetti dei rifiuti urbani', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '637', '26009', '5404', '13', 'La fermata è sempre vietata in seconda fila', 'F');
