INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '641', '26018', '5430', '2', 'La sosta è vietata sulle aree destinate al mercato e ai veicoli per il carico e lo scarico di cose, nelle ore stabilite ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '641', '26018', '5431', '3', 'La sosta è vietata sulle banchine, salvo diversa segnalazione ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '641', '26018', '5432', '4', 'La sosta è vietata negli spazi riservati alla fermata o alla sosta dei veicoli per persone invalide ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '641', '26018', '5433', '5', 'La sosta è vietata in corrispondenza degli scivoli o dei raccordi tra i marciapiedi e la carreggiata utilizzati dai veicoli per persone invalide ', 'V');
