INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '641', '26018', '5436', '8', 'La sosta è consentita negli spazi riservati alla fermata o alla sosta dei veicoli per persone invalide purché di breve durata ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '642', '26019', '', '', 'Divieto di sosta ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '642', '26019', '5437', '1', 'La sosta è vietata nelle corsie o carreggiate riservate ai mezzi pubblici', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '642', '26019', '5438', '2', 'La sosta è vietata nelle aree pedonali urbane', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '642', '26019', '5439', '3', 'La sosta è vietata nelle zone a traffico limitato per i veicoli non autorizzati', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '642', '26019', '5440', '4', 'La sosta è vietata negli spazi destinati a servizi di emergenza o di igiene pubblica indicati dalla apposita segnaletica', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '642', '26019', '5441', '5', 'La sosta è vietata davanti ai cassonetti dei rifiuti urbani', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '642', '26019', '5442', '6', 'Brevi soste sono consentite nelle aree pedonali urbane', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '643', '26020', '', '', 'Divieto di sosta ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '643', '26020', '5443', '1', 'La sosta è vietata, limitatamente alle ore di esercizio, in corrispondenza dei distributori di carburante, e in loro prossimità sino a 5 metri prima e dopo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '643', '26020', '5444', '2', 'Nei centri abitati il conducente non deve lasciare in sosta un rimorchio staccato dalla motrice, salvo diversa segnalazione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '643', '26020', '5445', '3', 'Nel caso in cui la sosta è espressamente vietata da una norma del codice stradale, l''osservanza di tale divieto non è condizionata dalla presenza di cartelli segnaletici', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '643', '26020', '5446', '4', 'La sosta è vietata in corrispondenza dei distributori di carburante, sino a 5 metri prima e dopo, anche quando i distributori sono chiusi', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '643', '26020', '5447', '5', 'Nel caso in cui la sosta è espressamente vietata da una norma del codice della strada, l''osservanza di tale divieto è comunque condizionata dalla presenza di cartelli segnaletici', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '643', '26020', '5448', '6', 'La sosta negli spazi riservati allo stazionamento e alla fermata di autobus, filobus e veicoli circolanti su rotaia, è consentita se si utilizza il disco orario', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '643', '26020', '5449', '7', 'La sosta a meno di 15 metri dal segnale di fermata autobus, qualora gli spazi di stazionamento non siano delimitati, è consentita azionando la segnalazione luminosa di pericolo  (quattro frecce lampeggianti simultaneamente)', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '643', '26020', '5450', '8', 'La sosta nelle corsie o carreggiate riservate ai mezzi pubblici, è consentita, nei centri abitati, dalle ore 22.00 alle ore 8.00', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '644', '26021', '', '', 'Conseguenze dell’infrazione delle norme sul divieto di sosta ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '644', '26021', '5451', '1', 'La sosta negli spazi riservati allo stazionamento e alla fermata di autobus, filobus e veicoli circolanti su rotaia comporta, tra l''altro, la sottrazione di punti dalla patente', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '644', '26021', '5452', '2', 'La sosta, a meno di 15 metri dal segnale di fermata degli autobus, qualora gli spazi di fermata non siano delimitati, comporta tra l''altro, la sottrazione di punti dalla patente', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '644', '26021', '5453', '3', 'La sosta negli spazi riservati alla fermata e alla sosta dei veicoli per persone invalide, comporta, tra l''altro, la sottrazione di punti dalla patente', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '644', '26021', '5454', '4', 'La sosta in corrispondenza degli scivoli o dei raccordi tra i marciapiedi e la  carreggiata utilizzati dai veicoli per persone invalide comporta, tra l''altro, la sottrazione di punti dalla patente', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '644', '26021', '5455', '5', 'La sosta nelle corsie o carreggiate riservate ai mezzi pubblici comporta, tra l''altro, la sottrazione di punti dalla patente', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '644', '26021', '5456', '6', 'La sosta negli spazi riservati alla fermata e alla sosta dei veicoli per persone invalide comporta una sanzione pecuniaria, ma non la sottrazione di punti dalla patente del conducente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('16', '644', '26021', '5457', '7', 'La sosta in corrispondenza degli scivoli o dei raccordi tra i marciapiedi e la carreggiata utilizzati dai veicoli per persone invalide, comporta la sospensione della patente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '645', '27002', '', '', 'Comportamenti in caso di ingombro della carreggiata per avaria del veicolo o caduta del carico ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '645', '27002', '5458', '1', 'In caso di ingombro della carreggiata per avaria del veicolo, il conducente deve sollecitamente rendere libero il transito per il traffico sopraggiungente', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '645', '27002', '5459', '2', 'In caso di ingombro della carreggiata per avaria del veicolo, il conducente deve spingere il veicolo stesso fuori della carreggiata o, se ciò non è possibile, collocarlo sul margine destro della carreggiata', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '645', '27002', '5460', '3', 'In caso di ingombro della carreggiata per caduta del carico o per qualsiasi altra causa, il conducente deve rimuovere l''ingombro, per quanto possibile', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '645', '27002', '5461', '4', 'In caso di ingombro della carreggiata per avaria del veicolo, il conducente non è tenuto a spostarlo, in attesa dei soccorsi, se il traffico è scarso', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '645', '27002', '5462', '5', 'Nel caso di ingombro della carreggiata per caduta accidentale del carico il  conducente deve evitare di spostarlo per consentire la ricostruzione corretta dell''accaduto', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '646', '27003', '', '', 'Comportamenti in caso di ingombro della carreggiata per avaria del veicolo e caduta del carico ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '646', '27003', '5463', '1', 'Su strada extraurbana, nel caso di incidente che provochi l''ingombro della carreggiata per la presenza di veicoli danneggiati che non è possibile rimuovere, il conducente o i passeggeri devono presegnalare la zona mediante il segnale triangolare mobile di pericolo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '646', '27003', '5464', '2', 'Nel caso di ingombro della carreggiata per avaria del veicolo che non è possibile spostare, il conducente deve informare l''organo di polizia, senza dover opportunamente presegnalare la zona', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '646', '27003', '5465', '3', 'Nel caso di ingombro della carreggiata per caduta del carico non removibile, il conducente può evitare di informare un organo di polizia se presegnala la zona con efficaci segnali manuali', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '647', '27001', '', '', 'Comportamenti da tenere in caso di ingombro della carreggiata per caduta accidentale del carico ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '647', '27001', '5466', '1', 'Nel caso di ingombro della carreggiata per caduta accidentale del carico il conducente deve provvedere a rimuovere il carico, se possibile', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '647', '27001', '5467', '2', 'Su strada extraurbana, nel caso di ingombro della carreggiata per caduta accidentale del carico, il conducente deve presegnalare l''ostacolo mediante il segnale di veicolo fermo (triangolo)', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '647', '27001', '5468', '3', 'Nel caso di ingombro della carreggiata per caduta accidentale del carico il conducente deve rendere sollecitamente libero, per quanto possibile, il transito', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '647', '27001', '5469', '4', 'Nel caso di ingombro della carreggiata per caduta accidentale del carico il conducente deve presegnalare l''ostacolo mediante il segnale di STOP', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '647', '27001', '5470', '5', 'Nel caso di ingombro della carreggiata per caduta accidentale del carico il conducente deve restare a bordo del veicolo in attesa dei soccorsi', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '648', '27004', '', '', 'Comportamenti da tenere in caso di caduta sulla carreggiata di materie viscide, infiammabili o pericolose ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '648', '27004', '5471', '1', 'Chiunque non abbia potuto evitare la caduta o lo spargimento di materie viscide, infiammabili o comunque pericolose, deve, tra l''altro, presegnalare la zona con il segnale mobile di pericolo anche in mezzo alla carreggiata', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '648', '27004', '5472', '2', 'Su strada extraurbana, chiunque non abbia potuto evitare la caduta o lo spargimento di materie viscide, infiammabili o comunque pericolose deve, tra l''altro, presegnalare la zona con il segnale mobile di pericolo posto anche in mezzo alla carreggiata', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '648', '27004', '5473', '3', 'Chiunque non abbia potuto evitare la caduta o lo spargimento di materie pericolose, deve, tra l''altro, eseguire segnali manuali per impedire il transito dei veicoli dalla parte dove non è stato posto il segnale mobile di pericolo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '648', '27004', '5474', '4', 'Chiunque non abbia potuto evitare la caduta o lo spargimento di materie viscide, infiammabili o pericolose, deve, tra l''altro, rimuoverle o spargere sul terreno, se possibile, sabbia, terra, segatura o altro materiale idoneo a ripristinare l''aderenza', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '648', '27004', '5475', '5', 'Chiunque non abbia potuto evitare la caduta o lo spargimento di materie pericolose deve, tra l''altro, eseguire segnali manuali atti a segnalare il pericolo, solo dalla parte dove è stato posto il segnale triangolare mobile di pericolo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '648', '27004', '5476', '6', 'Il conducente di un autoveicolo che non abbia potuto evitare lo spargimento di materie pericolose, può evitare di presegnalare la zona con il segnale mobile di pericolo se esegue segnali sonori per allontanare gli altri veicoli', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '648', '27004', '5477', '7', 'Chiunque non abbia potuto evitare la caduta di materie viscide, deve deviare il traffico in attesa che l''ente proprietario della strada intervenga per ripristinare le condizioni ottimali', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '649', '27005', '', '', 'Segnale mobile triangolare di pericolo (Parte 1)', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '649', '27005', '5478', '1', 'Fuori dei centri abitati, di notte, quando mancano o sono insufficienti le luci posteriori di posizione o di emergenza, è obbligatorio presegnalare il veicolo, fermo sulla carreggiata, con il segnale triangolare mobile di pericolo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '649', '27005', '5479', '2', 'Fuori dei centri abitati, di notte, è obbligatorio presegnalare con il segnale triangolare mobile di pericolo ogni carico caduto accidentalmente dal veicolo sulla carreggiata', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '649', '27005', '5480', '3', 'Fuori dei centri abitati, è obbligatorio anche di giorno presegnalare un veicolo fermo sulla carreggiata mediante il segnale triangolare di pericolo quando non può essere visto nettamente a 100 metri di distanza', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '649', '27005', '5481', '4', 'Fuori dei centri abitati è obbligatorio, anche di giorno, presegnalare un carico accidentalmente caduto sulla carreggiata mediante il segnale triangolare mobile di pericolo, quando l''ingombro non può essere visto nettamente a 100 metri di distanza', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '649', '27005', '5482', '5', 'Il segnale mobile triangolare di pericolo fa parte dell''equipaggiamento obbligatorio degli autoveicoli', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '649', '27005', '5483', '6', 'Il segnale mobile triangolare di pericolo deve essere posto dietro al veicolo o all''ostacolo da segnalare, ad almeno 50 metri', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '649', '27005', '5484', '7', 'Nel caso di intersezione a distanza inferiore a 50 metri, il segnale mobile triangolare di pericolo deve essere posto dietro al veicolo nella posizione più idonea ad essere avvistato', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '649', '27005', '5485', '8', 'Il segnale mobile triangolare di pericolo deve essere posto sulla carreggiata in modo che sia visibile ad una distanza di almeno 100 metri dai veicoli sopraggiungenti', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '649', '27005', '5486', '9', 'Fuori dei centri abitati, di notte, quando mancano o sono insufficienti le luci posteriori di posizione o di emergenza, è consigliabile presegnalare il veicolo fermo sulla carreggiata, con il segnale triangolare mobile                                                                                                                                     di pericolo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '649', '27005', '5487', '10', 'Il triangolo mobile di pericolo deve essere obbligatoriamente usato per segnalare il veicolo fermo per avaria di notte, se l''ostacolo non è visibile ad almeno 150 metri di distanza', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '649', '27005', '5488', '11', 'Fuori dei centri abitati, è facoltativo presegnalare, anche di giorno, un carico accidentalmente caduto sulla carreggiata mediante il segnale di pericolo, quando non può essere visto nettamente a 100 metri di distanza', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '649', '27005', '5489', '12', 'Di notte, quando mancano o sono insufficienti le luci posteriori di posizione o di emergenza, è obbligatorio presegnalare il veicolo fermo con il segnale triangolare mobile di pericolo anche nei centri abitati', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '649', '27005', '5490', '13', 'Di notte, quando mancano o sono insufficienti le luci posteriori di posizione, è obbligatorio presegnalare velocipedi, ciclomotori e motocicli fermi con il segnale triangolare mobile di pericolo anche  nei centri abitati', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '649', '27005', '5491', '14', 'Il segnale mobile triangolare di pericolo deve essere posto sul carico accidentalmente caduto sulla carreggiata', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '649', '27005', '5492', '15', 'Il segnale mobile triangolare di pericolo deve essere posto sulla carreggiata in modo che sia visibile ad una distanza di almeno 50 metri dai veicoli sopraggiungenti', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '650', '27006', '', '', 'Posizionamento del segnale mobile triangolare di pericolo ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '650', '27006', '5493', '1', 'Il segnale mobile triangolare di pericolo deve essere posto sulla corsia occupata dal veicolo fermo o dal carico caduto', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '650', '27006', '5494', '2', 'Il segnale mobile triangolare di pericolo deve essere posto sulla carreggiata ad almeno un metro dal bordo esterno di essa', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '650', '27006', '5495', '3', 'Nel caso di carreggiata a più corsie, il segnale mobile triangolare di pericolo deve essere posto sulla corsia immediatamente a sinistra del veicolo fermo o del carico caduto', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '650', '27006', '5496', '4', 'Il segnale mobile triangolare di pericolo deve essere posto sulla carreggiata ad almeno un metro dalla striscia di separazione dei sensi di marcia', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '651', '27007', '', '', 'Giubbotto o bretelle retroriflettenti ad alta visibilità ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '651', '27007', '5497', '1', 'Durante le operazioni di presegnalazione con il segnale mobile di pericolo il conducente deve rendersi visibile indossando il giubbotto o le bretelle retroriflettenti ad alta visibilità', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '651', '27007', '5498', '2', 'Il giubbotto o le bretelle retroriflettenti ad alta visibilità da usare durante le operazioni di presegnalazione con il segnale mobile di pericolo devono essere di tipo approvato', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '651', '27007', '5499', '3', 'Durante le operazioni di presegnalazione con il segnale mobile di pericolo il conducente deve indossare il giubbotto ad alta visibilità anche se si trova sulle corsie di emergenza o sulle piazzole di sosta', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '651', '27007', '5500', '4', 'Per effettuare le operazioni di presegnalazione con il segnale mobile di pericolo è vietato al conducente scendere dal veicolo e circolare sulla strada senza indossare il giubbotto ad alta visibilità', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '651', '27007', '5501', '5', 'Durante le operazioni di presegnalazione con il segnale mobile di pericolo è facoltativo l''uso del giubbotto o delle bretelle retroriflettenti ad alta visibilità', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '651', '27007', '5502', '6', 'Durante le operazioni di presegnalazione con il segnale mobile di pericolo il conducente non deve indossare il giubbotto ad alta visibilità se si trova sulle corsie di emergenza o sulle piazzole di sosta', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '651', '27007', '5503', '7', 'Il conducente deve indossare il giubbotto ad alta visibilità di notte, anche nei centri abitati, qualora debba fermarsi sulla carreggiata perché il suo veicolo ha un guasto', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '651', '27007', '5504', '8', 'Il giubbotto o le bretelle retroriflettenti ad alta visibilità possono sostituire, in ogni caso, il triangolo mobile di pericolo ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '651', '27007', '5505', '9', 'Il giubbotto o le bretelle retroriflettenti ad alta visibilità possono essere solo di colore giallo ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '651', '27007', '5506', '10', 'Il giubbotto retroriflettente ad alta visibilità protegge il conducente anche da rischi chimici, calore, fuoco ed elettricità ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '651', '27007', '5507', '11', 'All''interno del veicolo bisogna tenere giubbotti o bretelle retroriflettenti ad alta visibilità, in numero pari a quello dei posti disponibili ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '651', '27007', '5508', '12', 'L''obbligo di indossare il giubbotto ad alta visibilità si ha quando si sosta fuori dai centri abitati, mentre le bretelle retroriflettenti vanno indossate nelle aree urbane ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '651', '27007', '5509', '13', 'Il giubbotto retroriflettente ad alta visibilità serve per chiedere aiuto agli automobilisti in transito ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '651', '27007', '5510', '14', 'Il giubbotto retroriflettente ad alta visibilità è utile solo quando si deve sostituire una ruota forata ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '652', '27008', '', '', 'Veicoli ammessi a circolare sulle autostrade e sulle strade extraurbane principali ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '652', '27008', '5511', '1', 'Sulle autostrade e strade extraurbane principali è vietata la circolazione di velocipedi, ciclomotori, motocicli di cilindrata inferiore a 150 cm3', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '652', '27008', '5512', '2', 'Sulle autostrade e strade extraurbane principali è vietata la circolazione di autovetture che non sono in grado di sviluppare per costruzione la velocità in piano di almeno 80 km/h', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '652', '27008', '5513', '3', 'Sulle autostrade e strade extraurbane principali è vietata la circolazione di macchine agricole', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '652', '27008', '5514', '4', 'Sulle autostrade e strade extraurbane principali è vietata la circolazione di veicoli con carico disordinato e non solidamente assicurato o sporgente oltre i limiti consentiti', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '652', '27008', '5515', '5', 'Sulle autostrade e strade extraurbane principali è vietata la circolazione di veicoli a tenuta non stagna e con carico scoperto, se trasportano materiali che possono disperdersi', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '652', '27008', '5516', '6', 'Sulle autostrade e strade extraurbane principali è vietata la circolazione di veicoli le cui condizioni di uso, equipaggiamento e gommatura possono costituire pericolo per la circolazione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '652', '27008', '5517', '7', 'Sulle autostrade e strade extraurbane principali è vietata la circolazione di autobus per trasporto di studenti', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '652', '27008', '5518', '8', 'Sulle autostrade e strade extraurbane principali è consigliabile evitare la circolazione di veicoli a tenuta non stagna e con carico scoperto, se trasportano materiali che possono disperdersi', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '652', '27008', '5519', '9', 'Sulle autostrade e strade extraurbane principali è vietata la circolazione di veicoli che trasportano animali vivi', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '652', '27008', '5520', '10', 'Sono ammessi a circolare su autostrade e strade extraurbane principali i ciclomotori che sviluppano una velocità di almeno 50 km/h', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '652', '27008', '5521', '11', 'Sono ammesse a circolare su autostrade e strade extraurbane principali le macchine agricole, non trainanti un rimorchio', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '652', '27008', '5522', '12', 'Sono ammessi a circolare su autostrade e strade extraurbane principali i veicoli non muniti di pneumatici, se assicurati', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '653', '27009', '', '', 'Circolazione di pedoni e animali su autostrade, strade extraurbane principali e loro pertinenze ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '653', '27009', '5523', '1', 'Sulle autostrade e strade extraurbane principali è vietata la circolazione di pedoni e animali, ad eccezione delle aree di servizio e delle aree di sosta', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '653', '27009', '5524', '2', 'Nelle aree di servizio e nelle aree di sosta delle autostrade e delle strade extraurbane principali gli animali possono circolare solo se debitamente custoditi', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '653', '27009', '5525', '3', 'Lungo le corsie di emergenza delle autostrade i pedoni possono transitare solo per raggiungere i punti per le richieste di soccorso', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '653', '27009', '5526', '4', 'In autostrada, il traino di veicoli in avaria su carreggiate, rampe, svincoli, aree di servizio o di parcheggio è consentito solo ai veicoli di soccorso autorizzati', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '653', '27009', '5527', '5', 'Sulle autostrade e strade extraurbane principali è vietata la circolazione di pedoni e animali, anche nelle aree di servizio e nelle aree di sosta', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '653', '27009', '5528', '6', 'Nelle aree di servizio e nelle aree di sosta delle autostrade e delle strade extraurbane principali gli animali non possono circolare neppure se debitamente custoditi', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '654', '27010', '', '', 'Autostop, attività commerciali, sosta e campeggio su autostrade e strade extraurbane principali ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '654', '27010', '5529', '1', 'Sulle carreggiate, sulle rampe, sugli svincoli, sulle aree di servizio o di parcheggio e in ogni altra pertinenza autostradale è vietato richiedere o concedere passaggi', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '654', '27010', '5530', '2', 'Nelle aree di servizio o di parcheggio delle autostrade e in ogni altra pertinenza autostradale è possibile svolgere attività commerciali solo se autorizzate dall''ente proprietario della strada', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '654', '27010', '5531', '3', 'Sulle carreggiate, sulle rampe, sugli svincoli, sulle aree di servizio o di parcheggio e in ogni altra pertinenza autostradale è consentito campeggiare, solo nelle aree a ciò destinate e per il periodo stabilito dall''ente proprietario della strada', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '654', '27010', '5532', '4', 'Nelle aree di servizio delle autostrade, il conducente non deve lasciare in sosta il veicolo per più di 24 ore', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '654', '27010', '5533', '5', 'Nelle autostrade è consentito concedere passaggi a persone invalide', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '654', '27010', '5534', '6', 'Sulle aree di servizio delle autostrade o di parcheggio è consentito chiedere e concedere passaggi', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '654', '27010', '5535', '7', 'Sulle aree di servizio o di parcheggio delle autostrade e delle strade extraurbane principali è consentito campeggiare', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '654', '27010', '5536', '8', 'Nelle aree di servizio e di parcheggio delle autostrade la durata massima per la sosta di un veicolo è di tre ore', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '655', '27011', '', '', 'Uso dei proiettori anabbaglianti su autostrade e strade extraurbane principali', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '655', '27011', '5537', '1', 'Durante la marcia sulle autostrade e strade extraurbane principali è obbligatorio l''uso dei proiettori anabbaglianti anche di giorno', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '655', '27011', '5538', '2', 'Durante la marcia sulle autostrade e strade extraurbane principali l''uso dei proiettori anabbaglianti è facoltativo in condizioni di perfetta visibilità', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '656', '27012', '', '', 'Norme di circolazione su autostrade e strade extraurbane principali ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '656', '27012', '5539', '1', 'Sulle carreggiate, sulle rampe e sugli svincoli di autostrade e strade extraurbane principali è vietato percorrere la carreggiata o parte di essa nel senso di marcia opposto a quello consentito', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '656', '27012', '5540', '2', 'Sulle carreggiate, sulle rampe e sugli svincoli di autostrade e strade extraurbane principali è vietato effettuare la retromarcia', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '656', '27012', '5541', '3', 'È consentito circolare sulle corsie per la sosta di emergenza delle autostrade e strade extraurbane principali per arrestarsi per avaria del veicolo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '656', '27012', '5542', '4', 'Sulle corsie per le soste di emergenza delle autostrade è vietato effettuare la retromarcia', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '656', '27012', '5543', '5', 'Sulle carreggiate, sulle rampe e sugli svincoli di autostrade e strade extraurbane principali il conducente può invertire il senso di marcia adottando tutti gli accorgimenti necessari', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '656', '27012', '5544', '6', 'Sulle carreggiate, sulle rampe e sugli svincoli di autostrade e strade extraurbane principali il conducente può invertire il senso di marcia in casi di particolare emergenza', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '656', '27012', '5545', '7', 'Sulle carreggiate, sulle rampe e sugli svincoli di autostrade e strade extraurbane principali il conducente può procedere in senso contrario al normale flusso del traffico, per brevi tratti', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '656', '27012', '5546', '8', 'Sulle autostrade e strade extraurbane principali il conducente può utilizzare la corsia di emergenza se il traffico è intenso', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '657', '27013', '', '', 'Comportamenti da tenere su autostrade e strade extraurbane principali ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '657', '27013', '5547', '1', 'Sulle autostrade e sulle strade extraurbane principali il conducente che percorre la corsia di accelerazione deve dare la precedenza ai veicoli in circolazione sulle corsie di marcia', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '657', '27013', '5548', '2', 'Sulle autostrade e sulle strade extraurbane principali, per uscire dalla carreggiata il conducente deve impegnare per tempo la corsia di destra, e quindi immettersi nella corsia di decelerazione dal suo inizio', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '657', '27013', '5549', '3', 'Sulle autostrade e sulle strade extraurbane principali il conducente deve segnalare tempestivamente il cambiamento di corsia servendosi degli indicatori di direzione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '657', '27013', '5550', '4', 'In caso di ingorgo su strade extraurbane principali o autostrade, se la corsia di emergenza manca o non è sufficiente alla circolazione dei veicoli di polizia o di soccorso, il conducente del veicolo che occupa la prima corsia di destra deve disporsi il più vicino possibile alla striscia di sinistra', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '657', '27013', '5551', '5', 'In caso di ingorgo il conducente può transitare sulla corsia per la sosta di emergenza solo per uscire dall''autostrada, a partire dal cartello di preavviso di uscita posto a 500 metri dallo svincolo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '657', '27013', '5552', '6', 'Sulle autostrade e sulle strade extraurbane principali, per uscire dalla carreggiata il conducente può immettersi nella corsia di decelerazione anche nel tratto successivo al suo inizio', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '657', '27013', '5553', '7', 'Sulle autostrade e sulle strade extraurbane principali se il traffico non è intenso il conducente non ha l''obbligo di servirsi degli indicatori di direzione per segnalare il cambiamento di corsia', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '657', '27013', '5554', '8', 'Sulle autostrade e sulle strade extraurbane principali è consentita la marcia per file parallele anche in assenza di traffico', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '658', '27014', '', '', 'Sosta di emergenza sulle autostrade ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '658', '27014', '5555', '1', 'Sulle carreggiate, sulle rampe e sugli svincoli delle autostrade è consentito sostare solo in situazioni di emergenza dovute a malessere del conducente o dei passeggeri oppure ad inefficienza del veicolo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '658', '27014', '5556', '2', 'Sulle carreggiate, sulle rampe e sugli svincoli delle autostrade è consentito, nel caso di malessere di un passeggero, fermarsi sulla corsia per la sosta di emergenza', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '658', '27014', '5557', '3', 'In caso di malessere di un passeggero durante la circolazione sulla carreggiata autostradale, è consentito fermarsi sulla corsia per la sosta di emergenza', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '658', '27014', '5558', '4', 'Sulle carreggiate, sulle rampe e sugli svincoli delle autostrade la sosta di emergenza non deve superare il tempo strettamente necessario e non deve protrarsi comunque oltre tre ore', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '658', '27014', '5559', '5', 'Sulle carreggiate, sulle rampe e sugli svincoli delle autostrade è vietato sostare o fermarsi, in caso di malessere dei passeggeri', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '658', '27014', '5560', '6', 'Sulle carreggiate, sulle rampe e sugli svincoli delle autostrade la sosta di emergenza non deve superare il tempo strettamente necessario e può protrarsi fino a 24 ore', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '659', '27015', '', '', 'Comportamenti da tenere in presenza di un automobilista fermo per avaria in autostrada ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '659', '27015', '5561', '1', 'In autostrada è vietato dare un passaggio ad un automobilista fermo per avaria del veicolo sulla corsia di emergenza', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '659', '27015', '5562', '2', 'Qualora il conducente veda un automobilista fermo con veicolo in avaria in autostrada, lo deve accompagnare fino alla stazione di servizio più vicina', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '659', '27015', '5563', '3', 'Qualora il conducente veda un automobilista fermo per avaria del veicolo in autostrada, si deve fermare a prestare soccorso', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '659', '27015', '5564', '4', 'Qualora il conducente veda un automobilista fermo per avaria del veicolo in autostrada, se lo ha superato può retrocedere  con prudenza per soccorrerlo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '659', '27015', '5565', '5', 'Qualora il conducente veda un automobilista fermo in autostrada per avaria del veicolo può trainare prudentemente il veicolo, fino al primo casello di uscita', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '660', '23014', '', '', 'Norme di circolazione su autostrade e strade extraurbane principali ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '660', '23014', '5566', '1', 'Sulle strade extraurbane principale la sosta e consentita solo nelle apposite aree', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '661', '27016', '', '', 'Trasporto di persone e animali sui veicoli ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '661', '27016', '5567', '1', 'Tutti i passeggeri dei veicoli a motore devono prendere posto in modo da non limitare la libertà del conducente e da non impedirgli la visibilità', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '661', '27016', '5568', '2', 'Il numero massimo di posti sulle autovetture è indicato sulla carta di circolazione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '661', '27016', '5569', '3', 'Sui veicoli è consentito il trasporto di un animale domestico, comunque in condizione da non costituire impedimento o pericolo per la guida', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '661', '27016', '5570', '4', 'Sui veicoli è consentito il trasporto di animali domestici anche in numero superiore ad uno, purché custoditi in gabbia o nel vano posteriore, appositamente diviso da rete', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '661', '27016', '5571', '5', 'Il numero delle persone trasportabili sulle autovetture indicato sulla carta di circolazione non comprende il conducente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '661', '27016', '5572', '6', 'Il numero delle persone trasportabili sulle autovetture può raggiungere il massimo di dieci', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '661', '27016', '5573', '7', 'Sui veicoli è consentito il trasporto di cani anche in numero superiore ad uno, purché adeguatamente trattenuti al guinzaglio', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '662', '27034', '', '', 'Numero di persone trasportabili sui veicoli ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '662', '27034', '5574', '1', 'Il numero di persone trasportabili sui veicoli è indicato sulla carta di circolazione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '662', '27034', '5575', '2', 'Il numero dei posti di un veicolo è indicato sulla carta di circolazione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '662', '27034', '5576', '3', 'Il numero massimo di posti delle autovetture è pari a nove, compreso quello del conducente', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '662', '27034', '5577', '4', 'Il numero di persone trasportabili sulle autovetture può essere al massimo di nove, compreso il conducente', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '662', '27034', '5578', '5', 'Il numero di persone trasportabili sui motocicli è sempre di due, oltre il conducente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '662', '27034', '5579', '6', 'Il numero di persone trasportabili sulle autovetture è al massimo di quattro', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '662', '27034', '5580', '7', 'Il numero di persone trasportabili sugli autocarri è sempre di cinque, compreso il conducente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '662', '27034', '5581', '8', 'Il numero di persone trasportabili su un veicolo, riportato sulla carta di circolazione, non comprende il conducente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '663', '35017', '', '', 'Il dispositivo di sicurezza ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '663', '35017', '5582', '1', 'Quando si transportano bambini è consigliabile inserire il dispositivo di sicurezza che impedisce l''apertura delle portiere dall''interno del veicoli.', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '664', '11039/23004/27017', '', '', 'Trasporto di persone e oggetti sui motocicli ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '664', '27017', '5583', '1', 'Sui motocicli è vietato il trasporto di minori di anni 5', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '664', '23004', '5584', '2', 'Sui motocicli è consentito il transporto di animali se custoditi in apposite gabbia', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '664', '27017', '5585', '3', 'Sui motocicli è vietato trasportare oggetti che non siano solidamente assicurati', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '664', '27017', '5586', '4', 'Sui motocicli è vietato trasportare oggetti che impediscano o limitino la visibilità al conducente', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '664', '23004', '5587', '5', 'Il carico deve essere sistemato sui motocicli in modo da non sporgere lateralmente di oltre 50 cm', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '664', '11039', '5588', '6', 'Il conducente di veicoli a due route puo circolare portando con sè un animale domestico, se custodito in apposite gabbia, purchè non sia sporgente di oltre 50 centimetri.', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '664', '27017', '5589', '7', 'Sui motocicli è possibile trasportare un passeggero solo se quest''ultimo è maggiorenne', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '664', '23004', '5590', '8', 'Sui motocicli è consentito trasportare sacchetti con la spesa anche se non saldamente assicurati', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '664', '23004', '5591', '9', 'Sui motocicli in nessun caso è consentito trasportare un carico', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '664', '27017', '5592', '10', 'Sui motocicli è vietato trasportare oggetti che sporgano longitudinalmente rispetto alla sagoma del veicolo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '667', '27018', '', '', 'Sistemazione del carico sui veicoli ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '667', '27018', '5593', '1', 'Il carico dei veicoli deve essere sistemato in modo da evitarne la caduta o la dispersione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '667', '27018', '5594', '2', 'Il carico dei veicoli deve essere sistemato in modo da non diminuire la visibilità al conducente né impedirgli la libertà dei movimenti nella guida', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '667', '27018', '5595', '3', 'Il carico dei veicoli deve essere sistemato in modo da non compromettere la stabilità del veicolo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '667', '27018', '5596', '4', 'Il carico dei veicoli deve essere sistemato in modo da non mascherare dispositivi di illuminazione e di segnalazione visiva né le targhe di riconoscimento e i segnali fatti col braccio', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '667', '27018', '5597', '5', 'Il carico del veicolo deve essere sistemato in modo da evitarne la caduta o la dispersione solo se il conducente deve condurre il veicolo su strada extraurbana', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '667', '27018', '5598', '6', 'Il carico dei veicoli deve essere sistemato in modo da non mascherare dispositivi di illuminazione e di segnalazione visiva soltanto durante la circolazione notturna', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '668', '35017', '', '', 'Sistemazione del carico sui veicoli ()', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '668', '35017', '5599', '1', 'E'' sconsigliabile sistemare oggetti sotto il sedile del conducente per evitare che, scivolando durante la marcia, vadano ad intralciare i movimenti dell''autista.', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '669', '23004', '', '', 'Carico dei veicoli ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '669', '23004', '5600', '1', 'Il carico deve essere sistemato sugli autoveicoli in modo da non diminuire la visibilità del conducente', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '669', '23004', '5601', '2', 'Il carico deve essere sistemato sugli autoveicoli in modo da non impedire la libertà di movimenti nella guida', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '669', '23004', '5602', '3', 'Il carico deve essere sistemato sugli autoveicoli in modo da evitarne la caduta', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '669', '23004', '5603', '4', 'Il carico deve essere sistemato sugli autoveicoli in modo da non sporgere dalla parte posteriore per più dei tre decimi della lunghezza del veicolo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '669', '23004', '5604', '5', 'Il carico sugli autoveicoli non deve superare la portata indicata nella carta di circolazione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '669', '23004', '5605', '6', 'Il carico deve essere sistemato sugli autoveicoli in modo da evitare la sporgenza laterale dello stesso per più di tre decimi della lunghezza del veicolo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '669', '23004', '5606', '7', 'Il carico deve essere sistemato sugli autoveicoli in modo da non superare di oltre il 10% l''altezza massima prevista dalla carta di circolazione, ma entro 4 metri', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '669', '23004', '5607', '8', 'Il carico deve essere sistemato sugli autoveicoli in modo da non superare di oltre il 10% la portata utile indicata sul documento di circolazione', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '669', '23004', '5608', '9', 'Il carico deve essere sistemato sugli autoveicoli in modo da sporgere dalla parte anteriore del veicolo di più dei tre decimi della sua lunghezza', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '669', '23004', '5609', '10', 'Il carico deve essere segnalato, di giorno, con un panno rosso se sporge dalla parte posteriore', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '669', '23004', '5610', '11', 'La sporgenza posteriore del carico deve essere segnalata con un pannello retroriflettente a strisce gialle e rosse', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '670', '27019', '', '', 'Limiti di sporgenza laterale del carico sui veicoli ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '670', '27019', '5611', '1', 'Sugli autoveicoli possono essere trasportate cose che sporgono lateralmente fuori della loro sagoma non più di 30 centimetri dalle luci di posizione anteriori e posteriori (comunque entro i limiti massimi di sagoma)', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '670', '27019', '5612', '2', 'Il carico può sporgere lateralmente, se costituito da cose indivisibili, fino ai 3/10 della larghezza del veicolo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '671', '27019', '', '', 'Trasporto di cose che strisciano sul terreno', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '670', '27019', '5613', '1', 'È vietato trasportare o trainare cose che strisciano sul terreno, anche se in parte sostenute da ruote', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '670', '27019', '5614', '2', 'È consentito trasportare o trainare cose che strisciano sul terreno se in parte sostenute da ruote', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '672', '27019', '', '', 'Limiti di sporgenza del carico sui veicoli ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '672', '27019', '5615', '1', 'Il carico non deve superare il limite di sagoma stabilito per ogni tipo di veicolo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '672', '27019', '5616', '2', 'Sugli autoveicoli, il carico non può sporgere longitudinalmente dalla parte anteriore', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '672', '27019', '5617', '3', 'Il carico può sporgere longitudinalmente dalla parte posteriore, se costituito da cose indivisibili, fino ai 3/10 della lunghezza dell''autoveicolo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '672', '27019', '5618', '4', 'Se il carico sporge oltre la sagoma del veicolo, il conducente deve adottare tutte le cautele idonee ad evitare pericolo agli altri utenti della strada', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '673', '27020', '', '', 'Segnalazione delle sporgenze del carico ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '673', '27020', '5619', '1', 'La sporgenza longitudinale del carico, fino ai 3/10 della lunghezza del veicolo, deve essere segnalata mediante uno o due pannelli quadrangolari in figura 302 ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '673', '27035', '5620', '2', 'Il pannello in figura deve essere applicato in ogni caso in cui il carico sporge posteriormente, anche se di poco', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '673', '27020', '5621', '3', 'Il pannello in figura segnala carichi indivisibili che sporgono dalla parte posteriore del veicolo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '673', '27020', '5622', '4', 'Se il carico sporge longitudinalmente per l''intera larghezza della parte posteriore del veicolo, i pannelli in figura devono essere due, posti trasversalmente alle due estremità del carico', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '674', '27020', '', '', 'Segnalazione delle sporgenze del carico ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '674', '27020', '5623', '1', 'Il pannello in figura segnala carichi indivisibili che sporgono dalla parte posteriore destra', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '675', '27035', '', '', 'Segnalazione delle sporgenze del carico ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '675', '27035', '5624', '1', 'Il pannello raffigurato deve essere messo all''estremità del carico che sporge dietro al veicolo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '675', '27035', '5625', '2', 'Il pannello raffigurato segnala dei carichi che sporgono dalla parte posteriore del veicolo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '675', '27035', '5626', '3', 'Il pannello raffigurato deve essere retroriflettente', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '675', '27035', '5627', '4', 'Il pannello raffigurato va usato nel caso in cui il carico sporga dietro al veicolo, anche se di poco', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '675', '27035', '5628', '5', 'Il pannello raffigurato può essere messo sui veicoli guasti, quando vengono trainati e non funzionano le luci di emergenza', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '675', '27035', '5629', '6', 'Il pannello raffigurato deve essere sistemato all''estremità del carico sporgente dal veicolo, in modo che sia sempre ben visibile', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '675', '27035', '5630', '7', 'Il pannello raffigurato invita i conducenti che stanno dietro al veicolo su cui è applicato, a circolare con particolare prudenza', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '675', '27035', '5631', '8', 'Il pannello raffigurato segnala il trasporto di carichi sporgonti ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '675', '27035', '5632', '9', 'Il pannello raffigurato si usa solo se il carico sporge di oltre tre decimi della lunghezza del veicolo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '675', '27035', '5633', '10', 'Il pannello raffigurato va messo anche di lato, quando il carico sporge oltre la larghezza del veicolo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '675', '27035', '5634', '11', 'Il pannello raffigurato identifica gli autocarri specializzati per il trasporto di materiali da costruzione', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '675', '27035', '5635', '12', 'Il pannello raffigurato va messo sui veicoli che trasportano la spazzatura', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '675', '27035', '5636', '13', 'Il pannello raffigurato va messo sui veicoli, solo se il carico sporge di almeno 50 centimetri', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '675', '27035', '5637', '14', 'Il pannello raffigurato va messo dietro a tutti gli autocarri', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '675', '27035', '5638', '15', 'Il pannello raffigurato può essere sostituito da una bandiera di colore rosso, delle dimensioni di 50x50 centimetri', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '676', '27021/11029', '', '', 'Pannello 303', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '676', '27021', '5639', '1', 'Il pannello arancione retroriflettente in figura segnala veicoli che stanno trasportando merci pericolose ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '676', '11029', '5640', '2', 'Il pannello in figura segnala il trasporto di merci pericolose ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '676', '27021', '5641', '2', 'Il pannello arancione retroriflettente riprodotto in figura è collocato posteriormente per segnalare carichi sporgenti pericolosi', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '677', '27021/11029', '', '', 'Pannello 304', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '677', '27021', '5642', '1', 'Il pannello in figura facilita l''individuazione dalla parte posteriore di autocarri con massa a pieno carico superiore a 3,5 tonnellate ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '677', '11029', '5643', '2', 'Il conducente che vede i pannelli in figura sta seguendo un autocarro con massa a pieno carico superiore a 3,5 tonnellate ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '677', '11029', '5644', '3', 'Durante la circolazione, gli autocarri con massa massima a pieno carico  superiore a 3,5 tonnellate devono essere segnalati posteriormente con  pannelli retro-riflettenti in figura', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '677', '27021', '5645', '4', 'I pannelli in figura sono posti sulla carreggiata per segnalare un ostacolo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '677', '11029', '5646', '5', 'Il pannello in figura è installato posteriormente ai rimorchi o semirimorchi con massa a pieno carico superiore a 3,5 tonnellate  ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '678', '27021/11029', '', '', 'Pannello 305', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '678', '27021', '5647', '1', 'Il pannello giallo in figura posto nella parte posteriore del rimorchio individua un autotreno o un autoarticolato ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '678', '11029', '5648', '2', 'Il conducente che vede i pannelli in figura sta seguendo un rimorchio o semirimorchio per trasporto di cose con massa a pieno carico superiore a 3,5 tonnellate ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '678', '11029', '5649', '3', 'Durante la circolazione, i rimorchi e i semirimorchi per trasporto di cose con massa massima a pieno carico superiore a 3,5 tonnellate devono essere segnalati posteriormente con pannelli retroriflettenti ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '678', '27021', '5650', '4', 'Il pannello in figura deve essere applicato posteriormente agli autobus di massa totale superiore a 3,5 tonnellate', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '678', '11029', '5651', '5', 'Durante la circolazione, gli autocarri di massa complessiva a pieno carico inferiore o uguale a 3,5 tonnellate devono essere segnalati posteriormente con pannelli retro riflettenti gialli e rossi', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '679', '11029', '', '', 'Strisce retro riflettenti sui veicoli ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '679', '11029', '5652', '1', 'Durante la circolazione, gli autocarri ed i rimorchi con massa massima a pieno carico superiore a 3,5 t devono essere segnalati con strisce posteriori e laterali retro riflettenti', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '679', '11029', '5653', '2', 'Le strisce retro riflettenti applicate lateralmente ad alcuni veicoli possono essere di colore bianco o giallo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '679', '11029', '5654', '3', 'Le strisce retro riflettenti applicate dietro ad alcuni veicoli possono essere di colore rosso o giallo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '679', '11029', '5655', '4', 'Le strisce retro riflettenti laterali devono essere apposte sugli autocarri di massa complessiva a pieno carico inferiore a 3,5 tonnellate', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '679', '11029', '5656', '5', 'I caravan (roulottes) devono essere muniti posteriormente di strisce retro riflettenti di colore rosso o giallo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '679', '11029', '5657', '6', 'Gli autobus devono essere muniti, nella parte posteriore di strisce retro riflettenti di colore rosso o bianco', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '684', '27022', '', '', 'Traino di un veicolo ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '684', '27022', '5658', '1', 'Nessun veicolo può trainare o essere trainato da più di un veicolo (salvo il caso di trasporti eccezionali)', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '684', '27022', '5659', '2', 'Un autoveicolo può trainare un veicolo che non sia un rimorchio se questo non può più circolare per avaria o per mancanza di organi essenziali', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '684', '27022', '5660', '3', 'Il traino per situazione di emergenza di un veicolo da parte di un altro deve avvenire attraverso un solido collegamento, come fune, catena, cavo, barra rigida, purché idoneamente segnalati', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '684', '27022', '5661', '4', 'Il traino per incombente situazione di emergenza di un veicolo in avaria deve avvenire attraverso un solido collegamento, purché idoneamente segnalato per rendere i veicoli chiaramente avvistabili dagli altri utenti della strada', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '684', '27022', '5662', '5', 'Qualora, in situazione di emergenza, il conducente traini con il suo veicolo un altro veicolo senza rispettare le prescrizioni relative alla solidità del collegamento è punito con la sottrazione di 2 punti dalla patente', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '684', '27022', '5663', '6', 'In autostrada, un autoveicolo può trainare un veicolo che non sia un rimorchio, se questo non può più circolare per qualsiasi grave motivo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '684', '27022', '5664', '7', 'Il traino per incombente situazione di emergenza di un veicolo in avaria deve avvenire attraverso un solido collegamento, anche se non è possibile segnalarlo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '684', '27022', '5665', '8', 'Durante le operazioni di traino di un veicolo in avaria, il veicolo trainato deve mantenere accese le luci posteriori in mancanza di altra idonea segnalazione', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '684', '27022', '5666', '9', 'Qualora, in situazione di emergenza, il conducente traini con il suo veicolo un altro veicolo senza rispettare le prescrizioni relative alla solidità del collegamento è punito con la sottrazione di 4 punti dalla patente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '685', '27017', '', '', 'Divieto di traino per i motocicli ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '685', '27017', '5667', '1', 'È vietato ai conducenti di motocicli trainare e farsi trainare da altri veicoli', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '685', '27017', '5668', '2', 'È consentito ai motocicli farsi trainare, ma solo da autovetture', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '686', '27033', '', '', 'Traino di un veicolo guasto ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '686', '27033', '5669', '1', 'Quando un veicolo guasto, munito di servosterzo, viene trainato, può risultare faticoso manovrare lo sterzo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '686', '27033', '5670', '2', 'Per guidare un''autovettura che traina un veicolo guasto, con conducente a bordo, occorre la patente di guida della categoria E', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '686', '27033', '5671', '3', 'Il conducente a bordo di un veicolo guasto, che viene trainato, può essere privo di patente di guida', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '686', '27033', '5672', '4', 'Se un veicolo guasto viene trainato con le quattro ruote al suolo, occorre inserire la quarta marcia del cambio di velocità', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '686', '27033', '5673', '5', 'Se si traina un veicolo guasto, munito di servosterzo, lo sterzo risulterà sempre bloccato', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '686', '27033', '5674', '6', 'Quando si traina un veicolo guasto, occorre che la distanza fra i due veicoli sia di almeno 3 metri', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '687', '27033', '', '', 'Traino di un rimorchio ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '687', '27033', '5675', '1', 'I rimorchi trainati da autovetture sono muniti, in genere, di freno ad inerzia', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '688', '27037', '', '', 'Traino di un rimorchio ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '688', '27037', '5676', '1', 'La frenatura del rimorchio dotato di freno a inerzia non avviene simultaneamente a quella della motrice, in quanto l''azionamento si ha solo dopo che il rimorchio ha esercitato una spinta sulla motrice', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '688', '27037', '5677', '2', 'In un rimorchio dotato di freno a inerzia l''effetto della frenata avviene quando il rimorchio si avvicina alla motrice', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '688', '27037', '5678', '3', 'Nel rimorchio è presente un dispositivo di sicurezza che, in caso di sganciamento accidentale, aziona automaticamente il freno di stazionamento', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '688', '27037', '5679', '4', 'Quando si lascia un rimorchio in sosta occorre azionare la leva del freno di stazionamento e, in caso di strada in pendenza, è opportuno applicare dei cunei di arresto alle ruote', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '688', '27037', '5680', '5', 'Nei rimorchi con freno a inerzia, dopo un prolungato periodo di inattività, i freni potrebbero aver perso efficienza a causa della ruggine', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '688', '27037', '5681', '6', 'Alla guida di un veicolo con rimorchio, durante una frenata in marcia rettilinea, il carico trasportato tenderà a spostarsi all''indietro', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '688', '27037', '5682', '7', 'Alla guida di un veicolo con rimorchio, durante una brusca accelerazione in marcia rettilinea, il carico trasportato tenderà a spostarsi in avanti', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '688', '27037', '5683', '8', 'Nelle autovetture dotate di A.B.S. non è possibile agganciare un rimorchio privo di impianto frenante', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '688', '27037', '5684', '9', 'Nei rimorchi dotati di freno a inerzia la frenatura del rimorchio avviene simultaneamente a quella della motrice', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '688', '27037', '5685', '10', 'Nei rimorchi dotati di freno a inerzia il rimorchio frena circa un secondo prima della motrice', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '689', '27033 / 27039 ', '', '', 'Svolta con rimorchio agganciato', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '689', '27033 / 27039', '5686', '1', 'Alla guida di un veicolo con rimorchio, nello svoltare a destra, bisogna fare particolare attenzione a non salire con la ruota posteriore sul marciapiede', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '689', '27033 / 27039', '5687', '2', 'Alla guida di un veicolo con rimorchio, nello svoltare a destra, bisogna fare particolare attenzione a non investire, con la ruota posteriore destra, eventuali pedoni che si trovino vicino al marciapiede', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '689', '27033 / 27039', '5688', '3', 'Quando si traina un rimorchio, bisogna considerare che nelle curve la fascia di ingombro è maggiore', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '689', '27033 / 27039', '5689', '4', 'Prima di effettuare qualsiasi manovra con il rimorchio agganciato è bene accertarsi che non vi siano cose e/o persone nel raggio di manovra del complesso', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '689', '27033 / 27039', '5690', '5', 'Nelle curve il rimorchio tende a stringereperché percorre una traiettoria circolare con raggio inferiore alla  motrice', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '689', '27033 / 27039', '5691', '6', 'Nell’affrontare una curva a velocità moderata il rimorchio compie sempre lo stesso percorso fatto dalla motrice', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '689', '27033 / 27039', '5692', '7', 'Quando si traina un rimorchio, bisogna considerare che nelle curve strette la fascia di ingombro è minore', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '690', '27033 / 27040 ', '', '', 'Controlli da fare prima di trainare un rimorchio ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '690', '27033 / 27040', '5693', '1', 'Prima di trainare un rimorchio, è opportuno accertarsi che gli impianti di illuminazione e di segnalazione funzionino regolarmente', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '690', '27033 / 27040', '5694', '2', 'Prima di iniziare a trainare un rimorchio, bisogna controllare dagli specchi retrovisori che la visibilità sia adeguata', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '690', '27033 / 27040', '5695', '3', 'Per poter trainare un rimorchio non occorre effettuare il collegamento elettrico fra motrice e rimorchio', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '690', '27033 / 27040', '5696', '4', 'Per trainare un rimorchio leggero non occorre effettuare il collegamento elettrico', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '690', '27033 / 27040', '5697', '5', 'Per poter trainare un rimorchio leggero non occorre effettuare il collegamento elettrico fra motrice e rimorchio ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '691', '27033 / 27039/27040 ', '', '', 'Carico sul gancio', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '691', '27033 / 27039/27040', '5698', '1', 'Trainando un rimorchio occorre verificare che il carico gravante sul gancio non alteri l’assetto del veicolo e l’inclinazione del fascio luminoso dei fari', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '691', '27033 / 27039/27040', '5699', '2', 'Quando si traina un rimorchio occorre verificare che il carico verticale gravante sul gancio non ecceda il limite indicato dal costruttore ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '691', '27033 / 27039/27040', '5700', '3', 'Il carico verticale massimo che può gravare sul gancio della motrice è indicato in  un’apposita targhetta applicata sul gancio di traino ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '691', '27033 / 27039/27040', '5701', '4', 'Per garantire una maggiore sicurezza durante la guida di un veicolo con rimorchio, è consigliabile concentrare il carico massimo in corrispondenza del gancio di traino', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '692', '27033/27040 ', '', '', 'Organi di traino di un autoveicolo e di rimorchio', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '692', '27033/27040 ', '5702', '1', 'Le parti meccaniche in movimento degli organi di traino di un autoveicolo e di un rimorchio devono essere mantenute pulite e adeguatamente lubrificate ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '692', '27033/27040 ', '5703', '2', 'Gli organi di traino di un veicolo non devono presentare giochi, deformazioni o incrinature ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '692', '27033/27040 ', '5704', '3', 'Gli organi di traino di un veicolo devono consentire sufficienti oscillazioni in senso verticale tra motrice e rimorchio, in modo da poter compensare le diverse inclinazioni dei veicoli dovute ai dislivelli stradali ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '692', '27033/27040 ', '5705', '4', 'Gli organi di traino di un veicolo non devono essere periodicamente lubrificati', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '692', '27033/27040 ', '5706', '5', 'Gli organi di traino di un veicolo possono non essere omologati, purché vengano trainati rimorchi di massa inferiore a 750 chilogrammi', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '692', '27033/27040 ', '5707', '6', 'Gli organi di traino di un veicolo non necessitano di lubrificazione, ma devono essere periodicamente sgrassati con appositi detergenti o con benzina ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '692', '27033/27040 ', '5708', '7', 'Gli organi di traino di un veicolo possono non essere di tipo omologato, se destinati solo al traino di rimorchi leggeri ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '692', '27033/27040 ', '5709', '8', 'In caso di rottura degli organi di traino di un veicolo, gli stessi possono essere riparati tramite saldatura presso officina specializzata ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '693', '27033', '', '', 'Traino di un rimorchio ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '693', '27033', '5710', '1', 'Trainare un rimorchio su strade con forti salite fa aumentare la temperatura del liquido di raffreddamento del motore', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '693', '27033', '5711', '2', 'Se si traina un rimorchio aumenta lo spazio di frenatura', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '693', '27033', '5712', '3', 'Trainando un rimorchio leggero, bisogna considerare che è maggiore la forza ribaltante esercitata dal vento', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '693', '27033', '5713', '4', 'Per una maggiore sicurezza, è consigliabile che nel rimorchio il carico venga sistemato in modo che gli oggetti pesanti si trovino il più vicino possibile al suo asse', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '693', '27033', '5714', '5', 'Quando i rimorchi sono staccati dalla motrice, devono avere una copertura assicurativa che vale anche per il rischio statico (da fermo) ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '693', '27033', '5715', '6', 'I rimorchi trainati da autovetture sono muniti, in genere, di freno ad inerzia', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '693', '27033', '5716', '7', 'I rimorchi devono avere targa propria', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '693', '27033', '5717', '8', 'Trainando un rimorchio nelle forti discese, occorre tener conto della forza di spinta esercitata dal rimorchio sulla motrice', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '693', '27033', '5718', '9', 'Quando si traina un rimorchio, specie se carico, si ha una sensibile riduzione della capacità di accelerazione (ripresa) del veicolo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '693', '27033', '5719', '10', 'I movimenti del liquido che si verificano all’interno di un serbatoio vincolato ad un rimorchio durante i cambiamenti di direzione, in frenata e in accelerazione, sono le maggiori cause di instabilità  dei complessi di veicoli', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '693', '27033', '5720', '11', 'Trainando un rimorchio nelle forti discese, non occorre tener conto della forza di spinta esercitata dal rimorchio sulla motrice', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '693', '27033', '5721', '12', 'Durante la marcia, il rimorchio deve avere il ruotino pivottante di manovra abbassato (poggiato al suolo)', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '693', '27033', '5722', '13', 'Nei veicoli dotati di sensori di parcheggio non è possibile agganciare un rimorchio', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '693', '27033', '5723', '14', 'Trainare un rimorchio non comporta un aumento dei consumi di carburante se si marcia a velocità moderata', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '694', '27041', '', '', 'Traino di un rimorchio ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '694', '27041', '5724', '1', 'Il conducente di un veicolo con rimorchio deve sistemare la merce trasportata in modo che, durante la marcia, non si verifichino spostamenti, rovesciamenti, cadute o sfregamenti', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '694', '27041', '5725', '2', 'In caso di prolungata inattività del rimorchio è consigliabile, se non è possibile sollevarlo da terra, aumentare la pressione di gonfiaggio degli pneumatici e cambiare periodicamente la loro superficie di appoggio, in modo da evitare la loro ovalizzazione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '694', '27041', '5726', '3', 'Nel caso in cui il rimorchio rimanga inattivo per lungo tempo è consigliabile, se possibile, mantenere gli pneumatici sollevati da terra, applicando appositi sostegni sotto al telaio', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '694', '27041', '5727', '4', 'Quando si traina un rimorchio, la pressione di gonfiaggio degli pneumatici del veicolo trainante deve essere la stessa degli pneumatici del rimorchi', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '695', '27033', '', '', 'Traino di un rimorchio ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '695', '27033', '5728', '1', 'Quando i rimorchi sono staccati dalla motrice, devono avere una copertura assicurativa che vale anche per il rischio statico (da fermo)', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '695', '27033', '5729', '2', 'I rimorchi devono avere targa propria', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '696', '27038', '', '', 'Traino di un rimorchio ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '696', '27038', '5730', '1', 'Nei centri abitati il conducente non deve lasciare in sosta un rimorchio staccato dalla motrice, salvo diversa segnalazione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '696', '27038', '5731', '2', 'Durante la circolazione, i rimorchi devono essere assicurati con la stessa polizza della motrice', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '696', '27038', '5732', '3', 'Gli specchietti esterni montati sulla motrice non devono sporgere oltre 20 centimetri dalla sagoma del veicolo (motrice o rimorchio), con riferimento al punto di maggiore larghezza ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '696', '27038', '5733', '4', 'Il rapporto di traino, entro i limiti di quello tecnicamente ammesso per il veicolo, non deve superare il valore di 1, nel caso di rimorchi muniti di freno di servizio e di massa fino a 3,5 tonnellate', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '696', '27038', '5734', '5', 'Il massimo rapporto di traino per un rimorchio sprovvisto di qualsiasi dispositivo di frenatura è di 0,5', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '696', '27038', '5735', '6', 'Quando il gancio di traino non viene utilizzato, perché non si sta trainando un rimorchio, la sua parte sporgente dalla sagoma posteriore del veicolo deve essere sfilata, smontata o ripiegata ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '696', '27038', '5736', '7', 'I conducenti di autoveicoli trainanti un rimorchio, quando la massa complessiva a pieno carico del complesso veicolare è superiore a 3,5 tonnellate, sono considerati in stato di ebbrezza qualora sia stato accertato un valore corrispondente ad un tasso alcolemico superiore a zero grammi per litro ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '696', '27038', '5737', '8', 'Nelle autostrade con carreggiate a tre o più corsie, ai conducenti di complessi di veicoli di lunghezza totale superiore a 7 metri, è vietato impegnare altre corsie all''infuori delle due più vicine al bordo destro della carreggiata ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '696', '27038', '5738', '9', 'Durante la circolazione, i rimorchi con massa massima a pieno carico fino a 3,5 tonnellate, devono essere segnalati con strisce posteriori e laterali retroriflettenti', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '696', '27038', '5739', '10', 'È possibile trainare rimorchi non immatricolati, se di massa inferiore ai 750 chilogrammi', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '696', '27038', '5740', '11', 'Il rimorchio si considera parte integrante del veicolo se ha una massa fino a 3,5 tonnellate', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '696', '27038', '5741', '12', 'La larghezza del rimorchio non deve superare quella del veicolo trainante', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '696', '27038', '5742', '13', 'Il rimorchio è parte integrante del veicolo a cui è stato abbinato in fase di collaudo e non può essere agganciato a un altro veicolo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '696', '27038', '5743', '14', 'Il titolare di patente di guida della categoria BE che, successivamente, consegue la patente della categoria C, può guidare anche veicoli della categoria CE', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '696', '27038', '5744', '15', 'Per i complessi di veicoli con rimorchi fino a 3,5 tonnellate di massa, non vige l''obbligo di apporre nella  parte posteriore i contrassegni indicanti i limiti massimi di velocità specifici loro imposti', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '696', '27038', '5745', '16', 'Se nella carta di circolazione manca il valore della massa rimorchiabile, ciò significa che il veicolo è atto al traino di rimorchi di qualsiasi massa', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '696', '27038', '5746', '17', 'I rimorchi devono essere sottoposti a revisione periodica insieme al veicolo trainante', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '696', '27038', '5747', '18', 'Se si trainano caravan e rimorchi T.A.T.S. non è obbligatorio che il veicolo trainante sia munito di  specchietto retrovisore destro ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '696', '27038', '5748', '19', 'Gli specchietti esterni montati sulla motrice possono sporgere fino a 50 centimetri dalla sagoma del veicolo di maggiore ingombro, qualora siano posizionati ad un''altezza da terra non superiore a 1 metro ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '696', '27038', '5749', '20', 'Nel rispetto della larghezza massima stabilita per i rimorchi T.A.T.S., le imbarcazioni trasportate possono sporgere fino a 50 centimetri per ogni lato rispetto ai bordi esterni delle luci di posizione posteriori ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '696', '27038', '5750', '21', 'Quando si traina un rimorchio per trasporto di animali vivi occorre tenere in funzione la segnalazione luminosa di pericolo per segnalare agli altri conducenti di aumentare la distanza di sicurezza ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '696', '27038', '5751', '22', 'Quando si traina un rimorchio occorre staccare la targa della motrice e applicarla nella parte posteriore del rimorchio ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '696', '27038', '5752', '23', 'Sui rimorchi, il carico trasportato può sporgere dalla parte anteriore fino ai 3/10 della lunghezza del complesso dei veicoli ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '696', '27038', '5753', '24', 'Sui rimorchi leggeri, il carico trasportato può sporgere posteriormente senza obbligo di specifiche segnalazioni ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('17', '696', '27038', '5754', '25', 'I rimorchi per il trasporto di cavalli (van) non possono circolare in autostrada ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '697', '28002', '', '', 'Uso delle luci ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '697', '28002', '5755', '1', 'Durante la marcia nei centri abitati, è obbligatorio tenere accese le luci anabbaglianti da mezz''ora dopo il tramonto del sole a mezz''ora prima del suo sorgere', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '697', '28002', '5756', '2', 'Durante la marcia, in caso di scarsa visibilità per condizioni atmosferiche (neve, pioggia, nebbia), è obbligatorio tenere accese le luci di posizione e quelle anabbaglianti', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '697', '28002', '5757', '3', 'Durante la marcia, in caso di nebbia con visibilità inferiore a 50 metri, bisogna usare la luce posteriore per nebbia, se il veicolo ne è dotato', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '697', '28002', '5758', '4', 'Di notte, nei centri abitati, il veicolo in sosta al margine della carreggiata, può essere segnalato con le luci di sosta', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '697', '28002', '5759', '5', 'Durante la marcia, è obbligatorio tenere accese le luci abbaglianti, a partire dal tramonto del sole fino al suo sorgere', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '697', '28002', '5760', '6', 'Di notte, in caso di sosta fuori dalla carreggiata, è obbligatorio tenere accese le luci anabbaglianti', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '697', '28002', '5761', '7', 'Se si lascia il veicolo in un parcheggio autorizzato, è obbligatorio tenere accese per tutto il tempo le luci di sosta', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '698', '28003', '', '', 'Uso delle luci di giorno ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '698', '28003', '5762', '1', 'È obbligatorio accendere i proiettori anabbaglianti, anche di giorno, quando si transita in galleria', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '698', '28003', '5763', '2', 'È obbligatorio accendere le luci del veicolo in caso di scarsa visibilità per le condizioni del tempo (nebbia, neve, pioggia)', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '698', '28003', '5764', '3', 'È obbligatorio accendere i proiettori anabbaglianti nelle gallerie stradali, solo se scarsamente illuminate', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '698', '28003', '5765', '4', 'Percorrendo una strada extraurbana durante una giornata assolata è sufficiente accendere le luci di posizione', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '698', '28003', '5766', '5', 'Percorrendo una strada extraurbana, è obbligatorio tenere accesi i proiettori abbaglianti durante le ore diurne', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '699', '28004', '', '', 'Uso delle luci ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '699', '28004', '5767', '1', 'Durante la marcia fuori dai centri abitati, è obbligatorio fare uso delle luci anabbaglianti, sia di giorno che di notte', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '699', '28004', '5768', '2', 'Durante la marcia nelle ore notturne, l''uso delle luci anabbaglianti è obbligatorio anche se l''illuminazione pubblica è sufficiente', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '699', '28004', '5769', '3', 'Durante la marcia, in caso di pioggia intensa, oltre alle luci anabbaglianti bisogna tenere accesa la luce posteriore per nebbia, se il veicolo ne è dotato', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '699', '28004', '5770', '4', 'Durante la marcia, in caso di fitta nevicata, oltre alle luci anabbaglianti bisogna tenere accesa la luce posteriore per nebbia, se il veicolo ne è dotato', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '699', '28004', '5771', '5', 'Proiettori anabbaglianti male orientati possono abbagliare gli altri utenti della strada', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '699', '28004', '5772', '6', 'Durante la marcia, l''uso delle luci anabbaglianti non è consentito nelle gallerie illuminate', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '699', '28004', '5773', '7', 'Durante la marcia, l''uso delle luci di posizione è sempre consentito in alternativa a quelle anabbaglianti', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '699', '28004', '5774', '8', 'L''uso delle luci anabbaglianti è obbligatorio quando si sorpassa un''auto della polizia', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '700', '31012', '', '', 'Uso delle luci ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '700', '31012', '5775', '1', 'Durante la circolazione su strada, per rendersi maggiormente visibili, i motocicli devono tenere accessi i proiettori anabbaglianti anche di giorno.', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '701', '28001', '', '', 'Uso delle luci ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '701', '28001', '5776', '1', 'Nelle autostrade e strade extraurbane, è obbligatorio accendere i fari anabbaglianti anche di giorno o, in alternativa, le luci di marcia diurna', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '701', '28001', '5777', '2', 'L''errata impostazione dell''orientamento dei proiettori può ridurre la visibilità del conducente o abbagliare gli altri utenti', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '701', '28001', '5778', '3', 'Proiettori anabbaglianti male orientati possono abbagliare gli altri utenti della strada', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '701', '28001', '5779', '4', 'Pur avendo acceso le luci anabbaglianti, si corre il rischio di abbagliare gli altri se le lampade sono montate in maniera errata o non sono omologate', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '701', '28001', '5780', '5', 'Su un veicolo a motore non è consentito utilizzare lampade non omologate', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '701', '28001', '5781', '6', 'Per le luci di posizione è possibile utilizzare lampade di qualsiasi colore', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '701', '28001', '5782', '7', 'L''uso delle luci di posizione è obbligatorio di notte durante la sosta fuori della carreggiata', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '701', '28001', '5783', '8', 'Percorrendo una strada extraurbana durante una giornata assolata è sufficiente accendere le luci di posizione', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '701', '28001', '5784', '9', 'Percorrendo una strada extraurbana, è obbligatorio tenere accesi i proiettori abbaglianti durante le ore diurne', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '702', '28015', '', '', 'Uso dei proiettori di profondità (abbaglianti) ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '702', '28015', '5785', '1', 'L''uso dei proiettori a luce abbagliante è vietato, di norma, durante la marcia nei centri abitati', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '702', '28015', '5786', '2', 'L''uso dei proiettori a luce abbagliante è necessario, fuori dei centri abitati, quando l''illuminazione pubblica è insufficiente e non si incrociano altri veicoli', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '702', '28015', '5787', '3', 'L''uso dei proiettori a luce abbagliante è necessario, fuori dei centri abitati, quando l''illuminazione pubblica manca e non si incrociano altri veicoli', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '702', '28015', '5788', '4', 'L''uso dei proiettori a luce abbagliante va sostituito con quello dei proiettori a luce anabbagliante quando si incrociano altri veicoli', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '702', '28015', '5789', '5', 'L''uso dei proiettori a luce abbagliante è di norma consentito in città, in caso di nebbia fitta', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '702', '28015', '5790', '6', 'L''uso dei proiettori a luce abbagliante è necessario nelle gallerie urbane a senso unico', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '702', '28015', '5791', '7', 'L''uso dei proiettori a luce abbagliante è sempre ammesso quando si trasportano feriti o ammalati gravi', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '702', '28015', '5792', '8', 'L''uso dei proiettori a luce abbagliante è necessario, di giorno, se si circola su un''autostrada viaggiando controsole', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '703', '28016', '', '', ' - Uso intermittente dei proiettori di profondità (abbaglianti)', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '703', '28016', '5793', '1', 'È consentito l''uso intermittente dei proiettori di profondità (a luce abbagliante), sia di giorno che di notte, per dare avvertimenti utili al fine di evitare incidenti', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '703', '28016', '5794', '2', 'È consentito l''uso intermittente dei proiettori di profondità (a luce abbagliante), anche all''interno dei centri abitati, per dare avvertimenti utili al fine di evitare incidenti', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '703', '28016', '5795', '3', 'È consentito l''uso intermittente dei proiettori di profondità (a luce abbagliante) per segnalare al veicolo che precede, sia di giorno che di notte, l''intenzione di sorpassare', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '703', '28016', '5796', '4', 'È consentito l''uso intermittente dei proiettori di profondità (a luce abbagliante) per segnalare al veicolo che precede, anche all''interno dei centri abitati, l''intenzione di sorpassare', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '703', '28016', '5797', '5', 'È consentito l''uso intermittente dei proiettori di profondità (a luce abbagliante), soltanto sulle strade extraurbane', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '703', '28016', '5798', '6', 'È consentito l''uso intermittente dei proiettori di profondità (a luce abbagliante), anche all''interno dei centri urbani, ma unicamente di giorno', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '703', '28016', '5799', '7', 'È consentito l''uso intermittente dei proiettori di profondità (a luce abbagliante), solo quando si trasportano feriti o ammalati gravi', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '703', '28016', '5800', '8', 'È consentito l''uso intermittente dei proiettori di profondità (a luce abbagliante), purché l''illuminazione esterna manchi o sia insufficiente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '703', '28016', '5801', '9', 'È consentito l''uso intermittente dei proiettori di profondità (a luce abbagliante), esclusivamente nei casi di pericolo immediato', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '704', '28017', '', '', 'Obbligo di spegnere i proiettori di profondità (abbaglianti) ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '704', '28017', '5802', '1', 'Fuori dei centri abitati, i conducenti devono spegnere i proiettori di profondità procedendo con quelli anabbaglianti, quando vi sia il pericolo di abbagliare gli altri utenti della strada', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '704', '28017', '5803', '2', 'Fuori dei centri abitati, i conducenti devono spegnere i proiettori di profondità procedendo con quelli anabbaglianti, qualora vi sia il pericolo di abbagliare i pedoni', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '704', '28017', '5804', '3', 'Fuori dei centri abitati, i conducenti devono spegnere i proiettori di profondità procedendo con quelli anabbaglianti, se vi è pericolo di abbagliare i conducenti di veicoli circolanti su altre strade', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '704', '28017', '5805', '4', 'Fuori dei centri abitati, i conducenti devono spegnere i proiettori di profondità procedendo con quelli anabbaglianti, quando stanno per incrociare altri veicoli', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '704', '28017', '5806', '5', 'Fuori dei centri abitati, i conducenti devono spegnere i proiettori di profondità procedendo con quelli anabbaglianti, quando seguono un veicolo a breve distanza', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '704', '28017', '5807', '6', 'Fuori dei centri abitati, i conducenti devono spegnere i proiettori di profondità procedendo con quelli anabbaglianti, dopo un loro uso prolungato, per evitare di scaricare la batteria', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '704', '28017', '5808', '7', 'Fuori dei centri abitati, i conducenti devono spegnere i proiettori di profondità procedendo con quelli anabbaglianti, solo quando il veicolo che li precede a breve distanza è un''autovettura', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '705', '28013', '', '', 'Uso degli indicatori di direzione ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '705', '28013', '5809', '1', 'Si devono usare gli indicatori di direzione prima di ogni cambio di corsia, quando si viaggia su strada suddivisa in corsie', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '705', '28013', '5810', '2', 'Si devono usare gli indicatori di direzione se, marciando su strada a senso unico, ci si sposta sulla corsia di sinistra provenendo da quella di destra', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '705', '28013', '5811', '3', 'Si devono usare gli indicatori di direzione ogni volta che si deve effettuare una manovra di svolta', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '705', '28013', '5812', '4', 'Si devono usare gli indicatori di direzione se si intende uscire dalla carreggiata per effettuare una fermata', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '705', '28013', '5813', '5', 'Si devono usare gli indicatori di direzione quando, partendo dal margine della carreggiata, ci si vuole immettere nella circolazione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '705', '28013', '5814', '6', 'Si devono usare gli indicatori di direzione per segnalare l''intenzione di spostarsi nella corsia di decelerazione di uno svincolo autostradale', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '705', '28013', '5815', '7', 'Si devono usare gli indicatori di direzione per segnalare l''intenzione di entrare sulla carreggiata autostradale, provenendo da una corsia di accelerazione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '705', '28013', '5816', '8', 'Non si devono usare gli indicatori di direzione quando si cambia corsia mentre si circola su strada a senso unico', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '705', '28013', '5817', '9', 'Non è mai necessario usare gli indicatori di direzione quando si circola su strada a doppio senso ma con carreggiate separate da spartitraffico', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '705', '28013', '5818', '10', 'Non si devono usare gli indicatori di direzione se, per effettuare una fermata fuori della carreggiata, ci si sposta sul margine sinistro', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '705', '28013', '5819', '11', 'Si devono usare gli indicatori di direzione destri per segnalare che si sta per frenare bruscamente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '705', '28013', '5820', '12', 'Non si devono usare gli indicatori di direzione se, per entrare in un passo carrabile, occorre svoltare a destra', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '705', '28013', '5821', '13', 'Si devono usare gli indicatori di direzione quando si effettua una svolta a sinistra, ma non quando si svolta a destra', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '705', '28013', '5822', '14', 'Si devono usare gli indicatori di direzione sinistri quando si deve percorrere un tornante verso sinistra', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '706', '28014', '', '', ' - Uso degli indicatori di direzione', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '706', '28014', '5823', '1', 'L''uso degli indicatori di direzione è necessario per segnalare che si vuole effettuare un cambio di corsia', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '706', '28014', '5824', '2', 'L''uso degli indicatori di direzione è necessario per segnalare l''intenzione di effettuare una manovra di sorpasso', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '706', '28014', '5825', '3', 'L''uso degli indicatori di direzione è necessario per segnalare l''intenzione di parcheggiare sul margine sinistro di una strada a senso unico', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '706', '28014', '5826', '4', 'L''uso degli indicatori di direzione è necessario ogni qualvolta si deve fare una manovra di svolta', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '706', '28014', '5827', '5', 'L''uso degli indicatori di direzione è necessario quando si effettuano manovre di retromarcia', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '706', '28014', '5828', '6', 'L''uso degli indicatori di direzione è necessario nella marcia per file parallele, anche se non si effettuano cambiamenti di corsia', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '706', '28014', '5829', '7', 'L''uso degli indicatori di direzione è necessario per almeno 500 metri dopo aver effettuato un sorpasso', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '706', '28014', '5830', '8', 'L''uso degli indicatori di direzione è necessario quando si inizia una manovra di sorpasso, ma non quando la si termina rientrando nella corsia di marcia normale', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '706', '28014', '5831', '9', 'L''uso degli indicatori di direzione non è necessario se si deve parcheggiare il veicolo spostandosi verso il margine destro della carreggiata', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '706', '28014', '5832', '10', 'L''uso degli indicatori di direzione è necessario quando ci si arresta ad un semaforo rosso', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '706', '28014', '5833', '11', 'L''uso degli indicatori di direzione destri è necessario quando ci si arresta per cause di interruzione della circolazione', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '707', '28007', '', '', 'Luce della targa ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '707', '28007', '5834', '1', 'La targa posteriore di un autoveicolo è illuminata per consentire una facile lettura dei caratteri che la compongono', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '707', '28007', '5835', '2', 'La targa posteriore di un autoveicolo deve essere illuminata con una luce bianca', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '707', '28007', '5836', '3', 'La luce della targa di un autoveicolo deve rimanere accesa se si sosta fuori dalla strade', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '707', '28007', '5837', '4', 'La luce della targa di un autoveicolo si deve accendere solo di notte', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '708', '28005', '', '', 'Catadiottri ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '708', '28005', '5838', '1', 'I catadiottri sono dispositivi che riflettono la luce', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '708', '28005', '5839', '2', 'I catadiottri installati nella parte posteriore dei veicoli sono di colore rosso', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '708', '28005', '5840', '3', 'I catadiottri installati nella parte posteriore di rimorchi e carrelli-appendice sono di colore rosso e di forma triangolare', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '708', '28005', '5841', '4', 'I catadiottri vengono applicati anche sui rimorchi e sui carrelli-appendice', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '708', '28005', '5842', '5', 'I catadiottri installati sulla parte davanti di rimorchi e carrelli-appendice sono di colore bianco', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '708', '28005', '5843', '6', 'I catadiottri vanno accesi mezz''ora dopo il tramonto del sole', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '708', '28005', '5844', '7', 'I catadiottri vanno accesi insieme alle luci di posizione', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '708', '28005', '5845', '8', 'I catadiottri sostituiscono le luci di posizione durante la circolazione nelle strade urbane', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '709', '28006', '', '', 'Funzione dei catadiottri ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '709', '28006', '5846', '1', 'I catadiottri hanno la funzione di indicare la presenza e l''ingombro dei veicoli su cui sono applicati', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '709', '28006', '5847', '2', 'I catadiottri di colore bianco hanno la funzione di evidenziare la parte davanti dei rimorchi e semirimorchi', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '709', '28006', '5848', '3', 'I catadiottri, se illuminati, hanno la funzione di rendere più visibili, specialmente di notte, i veicoli e i rimorchi in sosta sulla strada', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '709', '28006', '5849', '4', 'I catadiottri rendono più visibile un veicolo guasto, nel caso in cui non funzionino le luci di posizione posteriori', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '709', '28006', '5850', '5', 'I catadiottri con gli indicatori di direzione, segnalano la svolta di un veicolo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '709', '28006', '5851', '6', 'I catadiottri di colore bianco sono installati negli autoveicoli che trasportano materiale infiammabile', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '709', '28006', '5852', '7', 'I catadiottri possono sostituire gli indicatori di direzione', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '710', '28008', '', '', 'Spie di colore rosso ', '');
