INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '710', '28008', '5854', '2', 'La spia della temperatura dell''acqua di raffreddamento è di colore rosso', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '710', '28008', '5855', '3', 'La spia della pressione dell''olio è di colore rosso', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '710', '28008', '5856', '4', 'La spia della cintura di sicurezza è di colore rosso', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '710', '28008', '5857', '5', 'La spia del freno a mano è di colore rosso', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '710', '28008', '5858', '6', 'La spia di accensione degli indicatori di direzione è di colore rosso', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '710', '28008', '5859', '7', 'La spia di accensione delle luci anabbaglianti è di colore rosso', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '710', '28008', '5860', '8', 'La spia del dispositivo per sbrinare o disappannare il parabrezza è di colore rosso', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '710', '28008', '5861', '9', 'La spia delle luci fendinebbia è di colore rosso', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '710', '28008', '5862', '10', 'La spia di accensione delle luci abbaglianti è di colore rosso', 'F');
