INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '711', '28010', '5864', '2', 'Il simbolo raffigurato è posto su una spia a luce verde', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '711', '28010', '5865', '3', 'Il simbolo raffigurato è posto su una spia a luce blu', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '711', '28010', '5866', '4', 'Il simbolo raffigurato segnala l''accensione della luce posteriore per nebbia', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '711', '28010', '5867', '5', 'Il simbolo raffigurato è posto sulla spia delle luci abbaglianti accese', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '711', '28010', '5868', '6', 'Il simbolo raffigurato segnala l''accensione delle luci di posizione', 'F');
