INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '712', '28009', '5870', '1', 'Il simbolo raffigurato è posto sul comando di accensione dei proiettori di profondità', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '712', '28009', '5871', '2', 'Il simbolo raffigurato è posto su una spia a luce blu', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '712', '28009', '5872', '3', 'Il simbolo raffigurato è posto su una spia a luce verde', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '712', '28009', '5873', '4', 'Il simbolo raffigurato è posto sul comando di accensione dei proiettori anabbaglianti', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '712', '28009', '5874', '5', 'Il simbolo raffigurato segnala l''accensione della luce posteriore per nebbia', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '712', '28009', '5875', '6', 'Il simbolo raffigurato ricorda al conducente che deve spegnere le luci di posizione', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '713', '28011', '', '', 'Simbolo degli indicatori di direzione ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '713', '28011', '5876', '1', 'Il simbolo raffigurato indica il comando degli indicatori di direzione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '713', '28011', '5877', '2', 'Il simbolo raffigurato si trova sulla spia a luce verde lampeggiante dell''indicatore di direzione azionato', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '713', '28011', '5878', '3', 'Il simbolo raffigurato è posto su una spia a luce verde', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '713', '28011', '5879', '4', 'Il simbolo raffigurato è posto su una spia di colore rosso', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '713', '28011', '5880', '5', 'Il simbolo raffigurato si trova su una spia che si attiva quando sono accese le luci di posizione', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '713', '28011', '5881', '6', 'Il simbolo raffigurato indica il comando per accendere le luci di posizione', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '714', '28012', '', '', 'Simbolo della segnalazione luminosa di pericolo ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '714', '28012', '5882', '1', 'Il simbolo raffigurato indica il comando per azionare la segnalazione luminosa di pericolo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '714', '28012', '5883', '2', 'Il simbolo raffigurato è posto sul comando che provoca l''accensione contemporanea di tutti gli indicatori di direzione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '714', '28012', '5884', '3', 'Il simbolo raffigurato indica un dispositivo da usare in casi di emergenza', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '714', '28012', '5885', '4', 'Il simbolo raffigurato è posto su una spia di colore rosso', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '714', '28012', '5886', '5', 'Il simbolo raffigurato indica il comando che aziona il segnale mobile di pericolo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '714', '28012', '5887', '6', 'Il simbolo raffigurato è posto sulla spia di freno a mano inserito', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '714', '28012', '5888', '7', 'Il simbolo raffigurato indica un dispositivo che si accende automaticamente ad ogni incidente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '715', '28022', '', '', 'Simbolo delle cinture di sicurezza ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '715', '28022', '5889', '1', 'Il simbolo raffigurato è posto su una spia di colore rosso che, se accesa durante la marcia, indica che il conducente o un passeggero non hanno indossato le cinture di sicurezza', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '715', '28022', '5890', '2', 'La spia rossa contraddistinta dal simbolo in figura, se accesa durante la marcia, è in genere abbinata ad un segnale acustico in funzione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '715', '28022', '5891', '3', 'Il simbolo raffigurato è posto su una spia di colore rosso', 'V');
