INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '719', '28021', '5926', '4', 'Una spia di colore rosso contrassegnata dal simbolo di figura, se accesa durante la marcia, comporta di provvedere rapidamente a una manutenzione o riparazione dell''impianto frenante', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '719', '28021', '5927', '5', 'Il simbolo raffigurato è posto su una spia di colore rosso', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '719', '28021', '5928', '6', 'Una spia di colore rosso contrassegnata dal simbolo di figura, se accesa durante la marcia, segnala che la centralina elettronica è fuori uso', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '719', '28021', '5929', '7', 'Una spia di colore rosso contrassegnata dal simbolo di figura, se accesa durante la marcia, indica che occorre effettuare il tagliando periodico previsto dalla casa costruttrice entro 500 chilometri', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('18', '719', '28021', '5930', '8', 'Una spia di colore rosso contrassegnata dal simbolo di figura, se accesa durante la marcia, indica che occorre effettuare la revisione periodica di legge entro un mese', 'F');
