INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '720', '29006', '', '', 'Uso delle cinture di sicurezza ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '720', '29006', '5934', '1', 'Le cinture di sicurezza hanno lo scopo di limitare i danni al conducente e ai passeggeri in caso di incidente stradale', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '720', '29006', '5935', '2', 'Le cinture di sicurezza devono essere indossate sia dal conducente che dai passeggeri delle autovetture che ne siano dotate', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '720', '29006', '5936', '3', 'Le cinture di sicurezza richiedono un periodico controllo del loro stato di usura e di efficienza', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '720', '29006', '5937', '4', 'Le cinture di sicurezza devono essere di tipo omologato e devono essere mantenute sempre efficienti', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '720', '29006', '5938', '5', 'Le cinture di sicurezza hanno lo scopo di mantenere il corpo attaccato al sedile in caso di urto violento', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '720', '29006', '5939', '6', 'Le cinture di sicurezza hanno lo scopo di evitare che il conducente o i passeggeri vadano a urtare parti interne del veicolo in caso di urto violento', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '720', '29006', '5940', '7', 'Le cinture di sicurezza hanno lo scopo di evitare che il conducente o i passeggeri siano proiettati all''esterno della autovettura in caso di urto violento', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '720', '29006', '5941', '8', 'Le cinture di sicurezza devono essere utilizzate in qualsiasi situazione di marcia', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '720', '29006', '5942', '9', 'Le cinture di sicurezza devono essere utilizzate anche dai passeggeri dei sedili posteriori delle autovetture', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '720', '29006', '5943', '10', 'Le cinture di sicurezza devono essere di tipo omologato e mantenute sempre efficienti', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '720', '29006', '5944', '11', 'Non è obbligatorio utilizzare le cinture di sicurezza su una autovettura quando si circola ad una velocità inferiore ai 30 km/h', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '720', '29006', '5945', '12', 'Non è obbligatorio, ma è consigliato, indossare le cinture di sicurezza se si occupano i posti posteriori di una autovettura', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '720', '29006', '5946', '13', 'Non è obbligatorio l''uso delle cinture di sicurezza su una autovettura da parte di persone anziane (età superiore ai 70 anni)', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '720', '29006', '5947', '14', 'Non è obbligatorio l''uso delle cinture di sicurezza sui veicoli muniti di air-bag', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '720', '29006', '5948', '15', 'La spia che indica il mancato agganciamento delle cinture può essere disabilitata dal conducente con apposito comando posto sul cruscotto', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '720', '29006', '5949', '16', 'Le persone obese possono utilizzare appositi strumenti che evitano che la cintura di sicurezza stia aderente al corpo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '720', '29006', '5950', '17', 'L''arrotolatore delle cinture di sicurezza può essere disattivato o modificato dal conducente, se crea fastidio al passeggero', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '720', '29006', '5951', '18', 'Le cinture di sicurezza sono di utilità trascurabile nella circolazione urbano', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '720', '29006', '5952', '19', 'Non è obbligatorio utilizzare le cinture di sicurezza su una autovettura quando si effettuano tragitti di lunghezza inferiore a un chilometro all''interno dei centri abitati', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '720', '29006', '5953', '20', 'L''uso delle cinture di sicurezza consente il trasporto di bambini sui sedili anteriori in braccio a un adulto, senza pericoli', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '721', '29001', '', '', 'Uso delle cinture di sicurezza ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '721', '29001', '5954', '1', 'Sono previste sanzioni per chi, pur facendo uso della cintura di sicurezza, ne alteri o ostacoli il normale funzionamento', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '721', '29001', '5955', '2', 'Se non si allaccia la cintura di sicurezza, in caso d''improvvisa frenata si può essere proiettati contro il parabrezza', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '721', '29001', '5956', '3', 'Se il veicolo è dotato di airbag frontali e laterali, si può fare a meno di allacciare la cintura di sicurezza', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '721', '29001', '5957', '4', 'Le cinture di sicurezza vanno agganciate solo se si circola a velocità uguale o superiore a 30 km/h', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '722', '29004', '', '', 'Categorie di persone non obbligate ad agganciare le cinture di sicurezza ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '722', '29004', '5958', '1', 'Le donne in stato di gravidanza con particolari condizioni di rischio certificate non sono obbligate ad agganciare la cintura di sicurezza', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '722', '29004', '5959', '2', 'Le persone che soffrono di particolari malattie certificate non sono tenute ad agganciare la cintura di sicurezza', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '722', '29004', '5960', '3', 'Gli istruttori di guida nello svolgimento della loro professione non sono obbligati ad agganciare la cintura di sicurezza', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '722', '29004', '5961', '4', 'Il postino, durante l''attività lavorativa, non ha l''obbligo di agganciare la cintura di sicurezza', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '722', '29004', '5962', '5', 'Le persone con oltre 65 anni di età non sono obbligate ad agganciare la cintura di sicurezza', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '722', '29004', '5963', '6', 'Chi guida un''autovettura munito di autorizzazione ad esercitarsi alla guida (foglio rosa), non è obbligato ad utilizzare la cintura di sicurezza', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '723', '29003', '', '', 'Funzione del poggiatesta ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '723', '29003', '5964', '1', 'Il poggiatesta è utile per prevenire il colpo di frusta', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '723', '29003', '5965', '2', 'Il poggiatesta è utile se viene regolato correttamente, sia in altezza che in inclinazione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '723', '29003', '5966', '3', 'Il poggiatesta ha una funzione non di sicurezza, ma estetica', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '723', '29003', '5967', '4', 'Il poggiatesta è particolarmente utile per rilassare la muscolatura durante la guida', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '724', '29007', '', '', 'Sistemi di ritenuta per bambini ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '724', '29007', '5968', '1', 'I bambini di altezza inferiore a 1,50 metri, quando sono trasportati su una autovettura, devono essere trattenuti da sistemi di ritenuta omologati, adeguati alla loro statura e peso', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '724', '29007', '5969', '2', 'I bambini di età inferiore a 3 anni non possono viaggiare su autovetture sprovviste degli idonei sistemi di ritenuta', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '724', '29007', '5970', '3', 'I sistemi di ritenuta per bambini sono classificati in gruppi, in funzione del peso del bambino che possono accogliere', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '724', '29007', '5971', '4', 'I sistemi di ritenuta per bambini devono essere di tipo omologato e mantenuti sempre efficienti', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '724', '29007', '5972', '5', 'Se un''autovettura è munita di airbag, è possibile trasportare bambini sul sedile anteriore assicurati ad idoneo seggiolino solo se il veicolo è dotato di comando per disattivare l''airbag stesso', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '724', '29007', '5973', '6', 'I sistemi di ritenuta per bambini sono di un unico tipo e sono tutti utilizzabili per trasportare bambini fino a 1,50 metri di altezza', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '724', '29007', '5974', '7', 'È consentito il trasporto di bambini senza che siano assicurati a sistemi di ritenuta, purché su percorsi urbani', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '724', '29007', '5975', '8', 'È consentito il trasporto di bambini senza che siano assicurati a sistemi di ritenuta, purché si circoli alla velocità massima di 40 km/h', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '724', '29007', '5976', '9', 'È consentito il trasporto di bambini senza che siano assicurati a sistemi di ritenuta, purché sui sedili anteriori e a portata di braccio del conducente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '724', '29007', '5977', '10', 'È consentito il trasporto di bambini senza che siano assicurati a sistemi di ritenuta, purché sui sedili anteriori in braccio a persona maggiorenne', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '724', '29007', '5978', '11', 'Si possono utilizzare le cinture di sicurezza al posto dei sistemi di ritenuta per trasportare un bambino, purché sia di altezza superiore a 1,30 metri', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '724', '29007', '5979', '12', 'I seggiolini per bambini possono anche essere di costruzione artigianale, purché approvati da esperto della ASL', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '725', '29002', '', '', 'Cautele da adottare su veicoli dotati di airbag ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '725', '29002', '5980', '1', 'Nei veicoli dotati di airbag con comando di disabilitazione, prima di sistemare sul sedile  anteriore  un seggiolino per bambini (schienale rivolto verso il parabrezza), bisogna disattivare l''airbag', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '725', '29002', '5981', '2', 'Nei veicoli che non sono muniti del comando per disabilitare l''airbag dal lato passeggero, il seggiolino  per bambini non deve essere sistemato sul sedile anteriore, con lo schienale rivolto verso la strada                                   ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '725', '29002', '5982', '3', 'Se un veicolo è dotato di air-bag, non vi è l''obbligo di indossare le cinture di sicurezza durante la marcia', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '726', '29008', '', '', 'Airbag ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '726', '29008', '5983', '1', 'L''airbag è un cuscino gonfiabile di nylon o altro materiale sintetico', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '726', '29008', '5984', '2', 'L''airbag entra in funzione in caso di incidente stradale con urto violento', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '726', '29008', '5985', '3', 'L''airbag serve a limitare i danni agli occupanti del veicolo per urti contro superfici interne del veicolo in caso di urto violento a seguito di incidente', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '726', '29008', '5986', '4', 'L''airbag si gonfia automaticamente grazie al comando di un sensore', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '726', '29008', '5987', '5', 'L''airbag si gonfia in pochi millisecondi', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '726', '29008', '5988', '6', 'L''airbag può essere pericoloso se si attiva mentre non si indossa la cintura di sicurezza', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '726', '29008', '5989', '7', 'L''airbag può essere talvolta disattivabile', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '726', '29008', '5990', '8', 'L''airbag si gonfia in pochi secondi', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '726', '29008', '5991', '9', 'L''airbag si gonfia premendo un pulsante posto sul cruscotto', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '726', '29008', '5992', '10', 'L''airbag deve essere gonfiato periodicamente per verificarne l''efficienza', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '726', '29008', '5993', '11', 'L''airbag è utile soltanto nei percorsi extraurbani', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '726', '29008', '5994', '12', 'Il conducente può tarare, con apposito comando, la velocità minima d''urto al di sopra della quale far attivare l''airbag', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '726', '29008', '5995', '13', 'L''airbag non va usato insieme alle cinture di sicurezza', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '726', '29008', '5996', '14', 'L''airbag non può essere montato su vetture con meno di 4 porte', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '727', '29005', '', '', 'Obbligo di indossare il casco ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '727', '29005', '5997', '1', 'Il casco è obbligatorio per i conducenti di ciclomotori a due ruote', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '727', '29005', '5998', '2', 'Il casco è obbligatorio per i conducenti di motocicli non dotati di cellula di sicurezza e di dispositivi di ritenuta', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '727', '29005', '5999', '3', 'Il casco è obbligatorio per il passeggero di un motociclo privo di cellula di sicurezza e sistemi di ritenuta', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '727', '29005', '6000', '4', 'Il casco è obbligatorio per il passeggero di un ciclomotore a due ruote, anche se maggiorenne', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '727', '29005', '6001', '5', 'Il casco è obbligatorio per i conducenti di ciclomotori a due ruote, solo se minorenni', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '727', '29005', '6002', '6', 'Il casco non è obbligatorio per i conducenti di ciclomotori a due ruote, titolari di patente della categoria B', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '727', '29005', '6003', '7', 'Il casco non è obbligatorio per i conducenti di ciclomotori a due ruote', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '728', '29009', '', '', 'Uso del casco ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '728', '29009', '6004', '1', 'Il casco subisce un processo di invecchiamento dovuto a luce e calore', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '728', '29009', '6005', '2', 'Il casco non deve essere pulito con solventi', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '728', '29009', '6006', '3', 'Il casco non deve essere pulito con benzina', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '728', '29009', '6007', '4', 'Il casco integrale protegge anche il viso da traumi e lesioni in caso di incidente', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '728', '29009', '6008', '5', 'Il casco deve essere di taglia corretta', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '728', '29009', '6009', '6', 'Il casco deve essere sostituito dopo che abbia subito un forte urto, anche se non mostra deformazioni sulla calotta esterna', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '728', '29009', '6010', '7', 'Il casco deve essere indossato anche per spostamenti brevi e a bassa velocità', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '728', '29009', '6011', '8', 'Il casco deve essere indossato anche a bordo dei quadricicli a motore privi di carrozzeria (Quad)', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '728', '29009', '6012', '9', 'Il casco deve essere periodicamente pulito con benzina o acquaragia per sgrassarlo ed eliminare resti di insetti', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '728', '29009', '6013', '10', 'Il casco conserva invariate le sue caratteristiche protettive per periodi lunghissimi, di oltre venti anni', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '728', '29009', '6014', '11', 'Il casco deve essere completamente allacciato solo sui percorsi extraurbani', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '728', '29009', '6015', '12', 'Il casco integrale non può essere utilizzato quando si circola nei centri urbani ad alta densità di circolazione', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '728', '29009', '6016', '13', 'Il casco integrale è inutile se si circola a bordo di un ciclomotore a due ruote', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '728', '29009', '6017', '14', 'I caschi da utilizzare alla guida dei ciclomotori non necessitano di omologazione', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '729', '29009', '', '', 'Uso del casco ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '729', '29009', '6018', '1', 'La visiera del casco protegge il viso e gli occhi da impatti con insetti', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '729', '29009', '6019', '2', 'La visiera del casco deve essere sostituita se rigata o danneggiata', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '729', '29009', '6020', '3', 'La visiera del casco deve garantire la perfetta visione con qualunque condizione di luce o illuminazione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '729', '29009', '6021', '4', 'La visiera del casco peggiora notevolmente la visibilità e quindi va usata il meno possibile', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '729', '29009', '6022', '5', 'La visiera del casco può essere riparata con nastro adesivo, purché sia di tipo ultra trasparente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '730', '29009', '', '', 'Uso del casco ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '730', '29009', '6023', '1', 'La pulizia del casco deve essere effettuata con detersivi non corrosivi', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '730', '29009', '6024', '2', 'L''uso del casco è necessario per ridurre gli effetti di eventuali urti alla testa', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '730', '29009', '6025', '3', 'È bene che il casco sia dotato di visiera', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '730', '29009', '6026', '4', 'La robustezza della calotta esterna del casco potrebbe essere compromessa da solventi presenti in vernici o colle', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '730', '29009', '6027', '5', 'Durante la marcia, il casco deve essere sempre correttamente allacciato', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '730', '29009', '6028', '6', 'Indossare il casco in modo errato o senza averlo correttamente allacciato può comportare gravi rischi alla salute in caso di caduta o incidente', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '730', '29009', '6029', '7', 'Il sistema di aerazione del casco consente di limitare il pericolo di appannamento della visiera', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '730', '29009', '6030', '8', 'Il conducente che non indossa il casco è soggetto alla decurtazione di punti dalla patente di guida', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '730', '29009', '6031', '9', 'Sul casco possono essere applicati adesivi di qualunque tipo, purché non coprano la visiera', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '730', '29009', '6032', '10', 'La calotta del casco può essere riparata con nastro adesivo di tipo speciale, se presenta una rottura inferiore ai tre centimetri', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '730', '29009', '6033', '11', 'Quando si circola nelle zone a velocità controllata inferiore a 30 km/h, non è obbligatorio l''uso del casco', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '730', '29009', '6034', '12', 'L''obbligo dell''uso del casco non riguarda i maggiorenni con più di tre anni di esperienza di guida', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '730', '29009', '6035', '13', 'L''uso del casco durante la marcia diventa facoltativo quando la temperatura dell''aria è superiore ai trenta gradi', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '730', '29009', '6036', '14', 'L''uso del casco è obbligatorio su qualunque quadriciclo a motore', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '731', '29012', '', '', 'Giacche ad uso motociclistico ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '731', '29012', '6037', '1', 'Le giacche ad uso motociclistico garantiscono un''adeguata protezione all''abrasione in caso di scivolamento sull''asfalto', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '731', '29012', '6038', '2', 'Le giacche ad uso motociclistico hanno inserti rigidi protettivi in corrispondenza delle articolazioni', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '731', '29012', '6039', '3', 'Gli indumenti ad uso motociclistico omologati sono sottoposti a prove di resistenza al taglio e all''abrasione da impatto', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '731', '29012', '6040', '4', 'Le giacche ad uso motociclistico non sono previste per utilizzo estivo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '731', '29012', '6041', '5', 'Le giacche ad uso motociclistico, per essere omologate devono essere unicamente di colore nero', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '731', '29012', '6042', '6', 'Gli indumenti ad uso motociclistico omologati sono sottoposti a prove di resistenza al lavaggio', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '732', '29011', '', '', 'Guanti ad uso motocilcistico ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '732', '29011', '6043', '1', 'Guidando ciclomotori o motocicli è consigliabile utilizzare guanti protettivi per le mani', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '732', '29011', '6044', '2', 'I guanti da motociclisti omologati proteggono in particolar modo il dorso della mano in caso di  impatto con corpi duri', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '732', '29011', '6045', '3', 'I guanti da motociclisti proteggono il palmo della mano in caso di caduta con strisciamento  sull''asfalto', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '732', '29011', '6046', '4', 'I guanti da motociclisti garantiscono la giusta sensibilità durante la guida e una presa più sicura sui comandi', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '732', '29011', '6047', '5', 'I guanti da motociclisti possono avere imbottiture diverse a seconde della stagione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '732', '29011', '6048', '6', 'Quando si circola a bordo di un motociclo, è opportuno indossare gli specifici guanti anche d''estate', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '732', '29011', '6049', '7', 'I guanti ad uso motociclistico hanno speciali sistemi di aggancio per impedire il loro sfilamento in caso di caduta', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '732', '29011', '6050', '8', 'Non esistono guanti ad uso motociclistico di tipo omologato', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '732', '29011', '6051', '9', 'I guanti ad uso motociclistico possono essere sostituiti dai guanti da sci, ma solo nella stagione fredda', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '732', '29011', '6052', '10', 'Se si guida un motociclo senza guanti si può intervenire più rapidamente sui comandi', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '732', '29011', '6053', '11', 'I guanti ad uso motociclistico si riconoscono perché di colore nero e con una sagoma di moto stampigliata sul polso', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '732', '29011', '6054', '12', 'I guanti ad uso motociclistico coprono anche il gomito per proteggere meglio in caso di caduta', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '732', '29011', '6055', '13', 'I guanti ad uso motociclistico non sono idonei alla guida di ciclomotori a due ruote', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '732', '29011', '6056', '14', 'I guanti ad uso motociclistico hanno come principale scopo quello di proteggere dal freddo, pertanto è inutile usarli l''estate', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '733', '29010', '', '', 'Stivali ad uso motociclistico ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '733', '29010', '6057', '1', 'Gli stivali ad uso motociclistico permettono una giusta protezione di piedi e caviglie quando si è alla guida di veicolo a due ruote', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '733', '29010', '6058', '2', 'Gli stivali ad uso motociclistico riducono gli effetti di distorsioni in caso di appoggio impreciso dei piedi e proteggono le articolazioni della caviglia dal freddo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '733', '29010', '6059', '3', 'Gli stivali ad uso motociclistico sono privi di nastri liberi o stringhe per evitarne l''aggancio alle pedivelle dei motocicli', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '733', '29010', '6060', '4', 'Gli stivali ad uso motociclistico proteggono i piedi da abrasioni sull''asfalto in caso di caduta', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '733', '29010', '6061', '5', 'Gli stivali ad uso motociclistico sono omologati secondo norme internazionali', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '733', '29010', '6062', '6', 'Le calzature omologate per l''uso motociclistico sono progettate per non scalzarsi in caso di caduta', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '733', '29010', '6063', '7', 'Al posto degli stivali ad uso motociclistico si possono utilizzare scarpe da trekking in alta montagna, perché sono di analoga concezione', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '733', '29010', '6064', '8', 'Gli stivali ad uso motociclistico sono dotati di doppie stringhe per evitare scalzamenti', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '733', '29010', '6065', '9', 'Gli stivali ad uso motociclistico coprono la gamba fin sopra il ginocchio per proteggere meglio il conducente in caso di caduta', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '733', '29010', '6066', '10', 'Gli stivali ad uso motociclistico devono essere interamente realizzati in gomma ignifuga', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '733', '29010', '6067', '11', 'Gli stivali ad uso motociclistico hanno la suola in acciaio', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('19', '733', '29010', '6068', '12', 'Non esistono specifici stivali ad uso motociclistico, pertanto è bene utilizzare calzature robuste del tipo utilizzato per caccia, pesca o alpinismo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '734', '30004', '', '', 'Patente AM ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '734', '30004', '6069', '1', 'Con la patente di categoria AM si possono condurre i quadricicli leggeri', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '734', '30004', '6070', '2', 'Con la patente di categoria AM si possono condurre i ciclomotori a due ruote', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '734', '30004', '6071', '3', 'Con la patente di categoria AM si possono condurre i ciclomotori a tre ruote', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '734', '30004', '6072', '4', 'La patente AM si può conseguire a 14 anni di età', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '734', '30004', '6073', '5', 'La patente AM abilita alla guida di ciclomotori con motore termico non superiore a 50 cm3', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '734', '30004', '6074', '6', 'Con la patente di categoria AM si possono condurre i ciclomotori a due ruote mentre per condurre i ciclomotori a tre ruote occorre la patente A1', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '734', '30004', '6075', '7', 'La patente AM consente, a chi ha compiuto 14 anni, di condurre i ciclomotori in tutti i paesi dell''Unione Europea', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '734', '30004', '6076', '8', 'Con la patente di categoria AM si possono condurre i quadricicli non leggeri con potenza fino a 7 kW', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '734', '30004', '6077', '9', 'La patente AM abilita alla guida di motocicli, purché dotati di motore elettrico', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '734', '30004', '6078', '10', 'La patente AM può essere rilasciata senza dover sostenere gli esami teorici e pratici, purché il richiedente sia maggiorenne', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '734', '30004', '6079', '11', 'La patente AM conseguita da almeno 3 anni abilita alla guida dei motocicli di potenza fino a 11 kW', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '735', '30005', '', '', 'Patente A1 ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '735', '30005', '6080', '1', 'Con la patente di categoria A1 si possono condurre i motocicli (con o senza carrozzetta) di cilindrata fino a 125 cm3, potenza non superiore a 11 kW e rapporto potenza/peso non superiore a 0,1 kW/kg', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '735', '30005', '6081', '2', 'Con la patente di categoria A1 si possono condurre i tricicli a motore di potenza non superiore a 15 kW', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '735', '30005', '6082', '3', 'Con la patente di categoria A1 si possono condurre le macchine agricole che rientrano nei limiti di sagoma e di massa previsti per i motoveicoli', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '735', '30005', '6083', '4', 'La patente di categoria A1 può essere conseguita da chi ha almeno 16 anni di età', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '735', '30005', '6084', '5', 'La patente di categoria A1 abilita anche alla guida dei veicoli che si possono condurre con la patente AM', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '735', '30005', '6085', '6', 'La patente di categoria A1 può essere conseguita solo da chi è già in possesso della patente AM', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '735', '30005', '6086', '7', 'La patente di categoria A1 abilita alla guida di tutti i motocicli di cilindrata fino a 125 cm3', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '735', '30005', '6087', '8', 'Con la patente di categoria A1 si possono condurre i tricicli di potenza superiore a 15 kW, ma solo se il conducente è maggiorenne', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '735', '30005', '6088', '9', 'Con la patente di categoria A1 si possono condurre le macchine operatrici non eccezionali', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '735', '30005', '6089', '10', 'La patente di categoria A1 consente al conducente che ha meno di 18 anni di trasportare un passeggero, ma solo se lo stesso è maggiorenne', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '735', '30005', '6090', '11', 'La patente di categoria A1, trascorsi i primi due anni dal conseguimento, diventa automaticamente patente di categoria A2 e, dopo ulteriori due anni, diventa patente di categoria A che abilita alla guida di qualunque motociclo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '736', '30006', '', '', 'Patente A2 ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '736', '30006', '6091', '1', 'Con la patente di categoria A2 si possono condurre i motocicli (con o senza carrozzetta) di potenza fino a 35 kW, rapporto potenza/peso non superiore a 0,2 kW/kg purché non siano derivati da una versione dello stesso modello che sviluppi oltre il doppio della potenza massima', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '736', '30006', '6092', '2', 'La patente di categoria A2 abilita anche alla guida dei veicoli che si possono condurre con le patenti A1 e AM', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '736', '30006', '6093', '3', 'La patente di categoria A2 può essere conseguita da chi ha almeno 18 anni di età', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '736', '30006', '6094', '4', 'La patente di categoria A2 consente al titolare di trasportare un passeggero solo se il posto del passeggero è previsto sulla carta di circolazione del veicolo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '736', '30006', '6095', '5', 'Il titolare di patente di categoria A2 da almeno due anni, può conseguire la patente A che abilita alla guida di tutti i motocicli, anche se ha meno di 24 anni', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '736', '30006', '6096', '6', 'Il titolare di patente di categoria A2 può condurre anche le macchine agricole che rientrano nei limiti di sagoma e di massa dei motoveicoli', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '736', '30006', '6097', '7', 'La patente di categoria A2 può essere conseguita da chi ha almeno 20 anni di età', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '736', '30006', '6098', '8', 'Con la patente di categoria A2 si possono condurre i motocicli (con o senza carrozzetta) di potenza fino a 25 kW, rapporto potenza/peso non superiore a 0,2 kW/kg purché non siano derivati da una versione che sviluppa oltre il triplo della potenza massima', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '736', '30006', '6099', '9', 'Il titolare di patente di categoria A2 da almeno due anni, può conseguire la patente A che abilita alla guida di tutti i motocicli, solo se è già in possesso della patente di categoria AM ed ha più di 24 anni di età', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '736', '30006', '6100', '10', 'Il titolare di patente di categoria A2 non può condurre motocicli di cilindrata superiore a 150 cm3 e di potenza superiore a 25 kW', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '736', '30006', '6101', '11', 'La patente di categoria A2 può essere conseguita solo se si è già in possesso della patente di categoria A1', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '736', '30006', '6102', '12', 'La patente di categoria A2 consente di guidare solo i ciclomotori e i quadricicli leggeri', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '736', '30006', '6103', '13', 'La patente di categoria A2 non consente di condurre alcun tipo di triciclo a motore', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '737', '30007', '', '', 'Patente A ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '737', '30007', '6104', '1', 'Con la patente di categoria A si possono condurre tutti i motocicli (con o senza carrozzetta)', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '737', '30007', '6105', '2', 'Con la patente di categoria A si possono condurre tutti i tricicli a motore, ma solo se il conducente ha almeno 21 anni', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '737', '30007', '6106', '3', 'La patente di categoria A abilita a guidare i motocicli di cilindrata superiore a 50 cm3 anche se sviluppano una velocità superiore a 45 km/h', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '737', '30007', '6107', '4', 'La patente di categoria A può essere conseguita, con accesso diretto, da chi non è già titolare di patente A2 da almeno 2 anni, solo se ha compiuto 24 anni di età', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '737', '30007', '6108', '5', 'La patente di categoria A può essere conseguita a 20 anni di età, con accesso graduale, da chi è titolare di patente A2 da almeno 2 anni', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '737', '30007', '6109', '6', 'La patente di categoria A abilita anche alla guida dei veicoli che si possono condurre con le patenti A1, A2 e AM', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '737', '30007', '6110', '7', 'Con la patente di categoria A si possono condurre anche i tricicli di potenza superiore a 15 kW, ma solo se il conducente ha almeno 21 anni di età', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '737', '30007', '6111', '8', 'Con la patente di categoria A si possono condurre i motocicli (con o senza carrozzetta) di qualsiasi cilindrata, purché abbiano una potenza inferiore a 35 kW per i primi 2 anni dal conseguimento', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '737', '30007', '6112', '9', 'Con la patente di categoria A si possono guidare i quadricicli non leggeri, qualunque sia l''età del conducente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '737', '30007', '6113', '10', 'La patente di categoria A può essere conseguita con accesso graduale a 18 anni di età da chi è già titolare di patente A1 da almeno 2 anni', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '737', '30007', '6114', '11', 'Con la patente di categoria A si possono condurre anche i tricicli di potenza superiore a 15 kW, qualunque sia l''età del conducente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '737', '30007', '6115', '12', 'La patente di categoria A può essere conseguita con accesso diretto anche da chi non è già titolare di patente A2 da almeno 2 anni, purché abbia compiuto 21 anni di età', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '737', '30007', '6116', '13', 'La patente di categoria A conseguita per accesso diretto ad almeno 24 anni di età, consente di condurre tutti i motocicli ma solo in Italia, per guidare all''estero è necessario sostenere un ulteriore esame', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '737', '30007', '6117', '14', 'Per conseguire la patente di categoria A è in ogni caso necessario aver compiuto almeno 24 anni di età', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '738', '30008', '', '', 'Patente B1 ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '738', '30008', '6118', '1', 'La patente di categoria B1 può essere conseguita a 16 anni di età', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '738', '30008', '6119', '2', 'La patente di categoria B1 abilita a condurre alcuni tipi di quadricicli non leggeri', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '738', '30008', '6120', '3', 'La patente di categoria B1 abilita a condurre i quadricicli non leggeri con manubrio (Quad)', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '738', '30008', '6121', '4', 'La patente di categoria B1 abilita a condurre i motocicli (con o senza carrozzetta) di cilindrata fino a 125 cm3, di potenza non superiore a 11 kW e con rapporto potenza/peso non superiore a 0,1kW/kg', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '738', '30008', '6122', '5', 'Con la patente di categoria B1 si possono condurre tutti i tricicli a motore, ma solo se il conducente ha già compiuto i 18 anni di età', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '738', '30008', '6123', '6', 'La patente di categoria B1 abilita a condurre le autovetture monoposto, o con un massimo di tre posti, compreso il conducente, purché si circoli senza passeggeri a bordo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '738', '30008', '6124', '7', 'La patente di categoria B1 abilita anche alla guida dei veicoli che si possono condurre con la patente A1', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '738', '30008', '6125', '8', 'Il titolare della patente di categoria B1 che intende conseguire la patente di categoria B dovrà sostenere solo un esame teorico integrativo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '739', '30009', '', '', 'Patente B ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '739', '30009', '6126', '1', 'La patente di categoria B abilita a condurre gli autoveicoli di massa massima autorizzata non superiore a 3,5 tonnellate destinati al trasporto di non più di otto persone oltre al conducente', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '739', '30009', '6127', '2', 'La patente di categoria B abilita a condurre le autovetture, anche se trainanti un rimorchio leggero (massa fino a 750 chilogrammi)', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '739', '30009', '6128', '3', 'La patente di categoria B abilita a condurre gli autocaravan di massa massima autorizzata non superiore a 3,5 tonnellate', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '739', '30009', '6129', '4', 'La patente di categoria B abilita a condurre le macchine agricole, comprese quelle definite eccezionali', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '739', '30009', '6130', '5', 'La patente di categoria B abilita a condurre le autovetture trainanti un rimorchio non leggero (massa oltre 750 chilogrammi) purché la massa massima autorizzata del complesso non superi 3,5 tonnellate', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '739', '30009', '6131', '6', 'La patente di categoria B consente di guidare, ma solo sul territorio nazionale (Italia), i veicoli che si possono condurre con la patente A1', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '739', '30009', '6132', '7', 'La patente di categoria B abilita anche alla guida dei veicoli che si possono condurre con le patenti AM e B1', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '739', '30009', '6133', '8', 'La patente di categoria B consente di guidare, solo sul territorio nazionale (Italia) e se il conducente ha almeno 21 anni, i tricicli a motore di potenza superiore a 15 kW', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '739', '30009', '6134', '9', 'La patente di categoria B abilita a condurre gli autocarri di massa massima autorizzata non superiore a 3.500 chilogrammi anche se trainanti un rimorchio leggero (massa fino a 750 chilogrammi)', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '739', '30009', '6135', '10', 'La patente di categoria B consente di condurre tutti i tipi di quadricicli a motore', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '739', '30009', '6136', '11', 'La patente di categoria B abilita anche alla guida dei veicoli che si possono condurre con le patenti A2 e A', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '739', '30009', '6137', '12', 'La patente di categoria B abilita a guidare gli autobus destinati al trasporto di al massimo 16 posti, escluso il conducente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '739', '30009', '6138', '13', 'La patente di categoria B abilita a guidare gli autocarri di massa massima autorizzata superiore a 3,5 tonnellate ma non superiore a 7,5 tonnellate', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '739', '30009', '6139', '14', 'La patente di categoria B, per i primi tre anni dal suo conseguimento, non consente la guida di autovetture aventi una potenza specifica, riferita alla tara, superiore a 55 kW/t o una potenza massima di 70 kW', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '739', '30009', '6140', '15', 'La patente di categoria B abilita a condurre le macchine operatrici eccezionali', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '739', '30009', '6141', '16', 'La patente di categoria B abilita a condurre solo autovetture con al massimo 9 posti compreso il conducente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '739', '30009', '6142', '17', 'La patente di categoria B abilita a condurre le autovetture, anche se trainano rimorchi aventi massa superiore a 3.500 chilogrammi', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '739', '30009', '6143', '18', 'La patente di categoria B consente di guidare un''autovettura che traina un rimorchio, con massa del complesso veicolare pari a 4.500 chilogrammi', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '739', '30009', '6144', '19', 'La patente di categoria B, conseguita dopo i 24 anni di età, abilita alla guida di tutti i motocicli', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '739', '30009', '6145', '20', 'La patente di categoria B consente di guidare tutti gli autocaravan (camper)', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '740', '30010', '', '', 'Patente B con codice 96 ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '740', '30010', '6146', '1', 'La patente di categoria B con codice armonizzato 96, consente di guidare le autovetture trainanti un rimorchio non leggero (oltre 750 chilogrammi), purché la massa massima autorizzata del complesso non sia superiore a 4.250 chilogrammi', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '740', '30010', '6147', '2', 'La patente di categoria B con codice armonizzato 96, può essere conseguita anche successivamente al conseguimento della patente di categoria B, superando una prova di guida su specifico veicolo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '740', '30010', '6148', '3', 'La patente di categoria B con codice armonizzato 96 può essere conseguita anche contestualmente alla patente di categoria B, sostenendo una prova di guida su specifico veicolo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '740', '30010', '6149', '4', 'Il codice armonizzato 96, apposto sulla patente di categoria B, abilita alla guida di autoveicoli trainanti un rimorchio non leggero (oltre 750 chilogrammi), purché la massa massima autorizzata del complesso non sia superiore a 4.250 chilogrammi', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '740', '30010', '6150', '5', 'Per guidare un''autovettura che traina un rimorchio non leggero (oltre 750 chilogrammi), con massa massima autorizzata del complesso superiore a 3.500 chilogrammi ma non superiore a 4.250 chilogrammi, è sufficiente aver conseguito la patente di categoria B con codice armonizzato 96', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '740', '30010', '6151', '6', 'La patente di categoria B con codice armonizzato 96, abilita alla guida di autosnodati con massa massima autorizzata superiore a 4.250 chilogrammi, ma non superiore a 7.500 chilogrammi', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '740', '30010', '6152', '7', 'La patente di categoria B con codice armonizzato 96, abilita alla guida di motocicli di potenza fino a 35 kW', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '740', '30010', '6153', '8', 'La patente di categoria B con codice armonizzato 96, può essere conseguita solo se si è già in possesso della patente A', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '740', '30010', '6154', '9', 'Il codice armonizzato 96, apposto sulla patente di categoria B, abilita alla guida di autoveicoli trainanti un rimorchio non leggero (oltre 750 chilogrammi), con massa massima autorizzata del complesso superiore a 4.250 chilogrammi ma non superiore a 6.000 chilogrammi', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '740', '30010', '6155', '10', 'Il codice armonizzato 96, apposto sulla patente di categoria B, indica che il titolare può condurre un''autovettura che traina un rimorchio solo in Italia', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '741', '30011', '', '', 'Patente BE ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '741', '30011', '6156', '1', 'La patente di categoria BE abilita a condurre complessi di veicoli composti da un autoveicolo di massa massima autorizzata fino a 3.500 chilogrammi e da un rimorchio di massa fino a 3.500 chilogrammi, anche se la massa del complesso veicolare è superiore a 4.250 chilogrammi', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '741', '30011', '6157', '2', 'Per guidare un''autovettura che traina un rimorchio di massa massima autorizzata di 2.000 chilogrammi e massa massima autorizzata del complesso veicolare pari a 4.500 chilogrammi, è necessario aver conseguito la patente di categoria BE ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '741', '30011', '6158', '3', 'La patente di categoria BE abilita a condurre autotreni composti da un autoveicolo di massa massima autorizzata fino a 3.500 chilogrammi e da un rimorchio di massa fino a 3.500 chilogrammi ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '741', '30011', '6159', '4', 'La patente di categoria BE abilita anche alla guida dei veicoli che si possono condurre con le patenti di categoria AM, B1, B e B con codice armonizzato 96 ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '741', '30011', '6160', '5', 'Per condurre un autoveicolo di massa massima autorizzata pari a 3.500 chilogrammi che traina un rimorchio di massa massima autorizzata di 1.000 chilogrammi è necessario aver conseguito la patente di categoria BE ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '741', '30011', '6161', '6', 'Per condurre un autoveicolo di massa massima autorizzata pari a 4.000 chilogrammi che traina un rimorchio di massa massima autorizzata di 2.000 chilogrammi è sufficiente aver conseguito la patente di categoria BE ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '741', '30011', '6162', '7', 'La patente di categoria BE abilita a condurre complessi di veicoli composti da un minibus che traina un rimorchio di massa fino a 1.500 chilogrammi ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '741', '30011', '6163', '8', 'La patente di categoria BE abilita anche alla guida dei veicoli che si possono condurre con le patenti di categoria A, B e C1', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '741', '30011', '6164', '9', 'La patente di categoria BE abilita anche alla guida dei veicoli che si possono condurre con le patenti di categoria A, B e C1', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '741', '30011', '6165', '10', 'La patente di categoria BE abilita a condurre autotreni composti da un autoveicolo di massa massima autorizzata fino a 3.500 chilogrammi che traina un rimorchio di massa superiore a 3.500 chilogrammi ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '741', '30011', '6166', '11', 'La patente di categoria BE si può conseguire anche contestualmente alla patente di categoria B, sostenendo la prova di guida su specifico veicolo, ma bisogna aver compiuto almeno 21 anni ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '742', '30018', '', '', 'Validità della patente ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '742', '30018', '6167', '1', 'La patente di guida è confermata se il titolare della stessa è ancora in possesso dei requisiti fisici e psichici prescritti', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '742', '30018', '6168', '2', 'La patente di guida è confermata a seguito di esito favorevole dell''apposito accertamento sanitario eseguito da medico autorizzato', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '742', '30018', '6169', '3', 'La patente di guida è confermata dal Ministero delle infrastrutture e dei trasporti a seguito di accertamento di idoneità psicofisica svolto da un medico autorizzato', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '742', '30018', '6170', '4', 'La patente di guida di categoria B, salvo diversa limitazione riportata sul documento stesso o salvo allineamento alla data del compleanno del titolare, ha validità di dieci anni se rilasciata o confermata a chi non ha superato i 50 anni di età', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '742', '30018', '6171', '5', 'La patente di guida di categoria B, salvo diversa limitazione riportata sul documento stesso o salvo allineamento alla data del compleanno del titolare, ha validità di cinque anni se rilasciata o confermata a chi ha una età compresa fra i 50 e i 70 anni', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '742', '30018', '6172', '6', 'La patente di guida di categoria B, salvo diversa limitazione riportata sul documento stesso o salvo allineamento alla data del compleanno del titolare, ha validità di tre anni se rilasciata o confermata a chi ha una età compresa fra i 70 e gli 80 anni', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '742', '30018', '6173', '7', 'La patente di guida di categoria B, salvo diversa limitazione riportata sul documento stesso o salvo allineamento alla data del compleanno del titolare, ha validità di due anni se rilasciata o confermata a chi ha superato gli 80 anni  di età', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '742', '30018', '6174', '8', 'La patente di guida di categoria BE, salvo diversa limitazione riportata sul documento stesso o salvo allineamento alla data del compleanno del titolare, ha validità di dieci anni se rilasciata o confermata a chi non ha superato i 50 anni di età', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '742', '30018', '6175', '9', 'La patente di guida di categoria A, salvo diversa limitazione riportata sul documento stesso o salvo allineamento alla data del compleanno del titolare, ha validità di dieci anni se rilasciata o confermata a chi non ha superato i 50 anni di età', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '742', '30018', '6176', '10', 'La patente di guida di categoria B SPECIALE (BS), salvo diversa limitazione riportata sul documento stesso o salvo allineamento alla data del compleanno del titolare, ha validità di cinque anni se rilasciata o confermata a chi non ha superato i 70 anni di età', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '742', '30018', '6177', '11', 'La patente di guida di categoria B è confermata dal Prefetto a seguito di visita di accertamento dei requisiti morali', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '742', '30018', '6178', '12', 'Con la patente di guida scaduta di validità è possibile comunque continuare a guidare per un tempo massimo di tre giorni', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '742', '30018', '6179', '13', 'La patente di guida è confermata a seguito di esito favorevole della prova tecnica di guida (esame di guida) periodica', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '742', '30018', '6180', '14', 'Il titolare abilitato a condurre veicoli conducibili con patente della categoria A e della categoria B, per confermarne la validità, deve sostenere due specifiche visite mediche, ognuna abilitante alla singola categoria', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '742', '30018', '6181', '15', 'La patente di guida di categoria B, salvo diversa limitazione riportata sul documento stesso o salvo allineamento alla data del compleanno del titolare, vale otto anni se rilasciata o confermata a chi ha una età compresa fra i 50 e i 70 anni', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '742', '30018', '6182', '16', 'La patente di guida di categoria BE, salvo diversa limitazione riportata sul documento stesso o salvo allineamento alla data del compleanno del titolare, vale cinque anni se rilasciata o confermata a chi non ha superato i 50 anni di età', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '742', '30018', '6183', '17', 'La patente di guida di categoria B, non può essere rinnovata a chi ha superato gli 80 anni di età', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '742', '30018', '6184', '18', 'La patente di guida di categoria B, salvo diversa limitazione riportata sul documento stesso o salvo allineamento alla data del compleanno del titolare, vale cinque anni se rilasciata o confermata a chi non ha superato i  50 anni di età', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '742', '30018', '6185', '19', 'La patente di guida di categoria B, salvo diversa limitazione riportata sul documento stesso o salvo allineamento alla data del compleanno del titolare, vale dieci anni in ogni caso di rilascio o di conferma', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '742', '30018', '6186', '20', 'La patente di guida di categoria B, salvo diversa limitazione riportata sul documento stesso, non ha scadenza di validità fino al compimento dei 50 anni di età', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '743', '30016', '', '', 'Punteggio sulla patente ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '743', '30016', '6187', '1', 'Ogni patente di guida ha un''attribuzione iniziale di 20 punti', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '743', '30016', '6188', '2', 'Il punteggio sulla patente diminuisce quando si commettono particolari infrazioni al codice della strada', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '743', '30016', '6189', '3', 'Quando il conducente ha esaurito la sua dotazione di punti sulla patente deve sottoporsi a revisione della stessa', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '743', '30016', '6190', '4', 'La frequenza di apposito corso e il superamento di uno specifico esame consentono al titolare di patente A o B di recuperare 6 punti, arrivando al massimo a 20 punti', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '743', '30016', '6191', '5', 'Le violazioni che comportano decurtazione di punti, commesse entro i primi tre anni dal rilascio della patente della categoria B, sono sanzionate con il doppio dei punti previsti', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '743', '30016', '6192', '6', 'Se si commettono contemporaneamente più violazioni che comportano perdita di punti sulla patente, possono essere decurtati al massimo 15 punti, salvo che si tratti di infrazioni che comportano la sospensione della patente', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '743', '30016', '6193', '7', 'Se per due anni non commette infrazioni che comportano perdita di punti, il conducente che abbia meno di 20 punti rientra in possesso di tutti i 20 punti', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '743', '30016', '6194', '8', 'Quando ha esaurito la sua dotazione di punti sulla patente, al conducente viene revocata la patente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '743', '30016', '6195', '9', 'Ogni patente di guida ha un''attribuzione iniziale di 30 punti', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '743', '30016', '6196', '10', 'Il punteggio sulla patente diminuisce quando si commette una qualunque infrazione al codice della strada', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '743', '30016', '6197', '11', 'Il punteggio sulla patente diminuisce esclusivamente quando si commette un''infrazione al codice della strada che comporta la sospensione o il ritiro della patente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '743', '30016', '6198', '12', 'Le violazioni che comportano decurtazione di punti, commesse entro i primi tre anni dal rilascio della prima patente, sono sanzionate con il triplo dei punti previsti', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '743', '30016', '6199', '13', 'La frequenza di apposito corso e il superamento di uno specifico esame consentono al titolare di patente A o B di recuperare 9 punti, arrivando al massimo a 30 punti', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '743', '30016', '6200', '14', 'Se si commettono contemporaneamente più violazioni che comportano perdita di punti sulla patente, possono essere decurtati al massimo 10 punti, salvo che si tratti di infrazioni che comportano la revoca della patente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '743', '30016', '6201', '15', 'Se per due anni non commette infrazioni che comportano perdita di punti, il conducente che abbia meno di 20 punti entra in possesso di 30 punti', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '744', '12025', '', '', 'Dia la precedenza ai pedoni ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '744', '12025', '6202', '1', 'In presenza del segnale raffigurato, qualora non si dia la precedenza ai pedoni che attraversano sulle apposite strisce, si incorre nella sottrazione di punti della patente', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '745', '23037', '', '', 'Mancato rispetto delle norme per immettersi nel flusso della circolazione e per fermarsi ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '745', '23037', '6203', '1', 'Il mancato rispetto delle norme prescritte per immettersi nel flusso della circolazione comporta, tra l''altro, la perdita di punti dalla patente', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '745', '23037', '6204', '2', 'Il mancato rispetto delle norme prescritte per immettersi nel flusso della circolazione non comporta la sottrazione di punti dalla patente, ma sanzione pecuniaria', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '745', '23037', '6205', '3', 'In caso di fermata sul margine destro della carreggiata senza segnalare con sufficiente anticipo l''intenzione di effettuare tale manovra, è disposta la sottrazione di 4 punti dalla patente del conducente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '746', '26021', '', '', 'Conseguenze dell’infrazione delle norme sul divieto di sosta ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '746', '26021', '6206', '1', 'La sosta negli spazi riservati allo stazionamento e alla fermata di autobus, filobus e veicoli circolanti su rotaia comporta, tra l''altro, la sottrazione di punti dalla patente', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '746', '26021', '6207', '2', 'La sosta, a meno di 15 metri dal segnale di fermata degli autobus, qualora gli spazi di fermata non siano delimitati, comporta tra l''altro, la sottrazione di punti dalla patente', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '746', '26021', '6208', '3', 'La sosta negli spazi riservati alla fermata e alla sosta dei veicoli per persone invalide, comporta, tra l''altro, la sottrazione di punti dalla patente', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '746', '26021', '6209', '4', 'La sosta in corrispondenza degli scivoli o dei raccordi tra i marciapiedi e la carreggiata utilizzati dai veicoli per persone invalide comporta, tra l''altro, la sottrazione di punti dalla patente', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '746', '26021', '6210', '5', 'La sosta nelle corsie o carreggiate riservate ai mezzi pubblici comporta, tra l''altro, la sottrazione di punti dalla patente', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '746', '26021', '6211', '6', 'La sosta negli spazi riservati alla fermata e alla sosta dei veicoli per persone invalide comporta una sanzione pecuniaria, ma non la sottrazione di punti dalla patente del conducente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '746', '26021', '6212', '7', 'La sosta in corrispondenza degli scivoli o dei raccordi tra i marciapiedi e la carreggiata utilizzati dai veicoli per persone invalide, comporta la sospensione della patente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '747', '27022', '', '', 'Sottrazione punti della patente ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '747', '27022', '6213', '1', 'Qualora, in situazione di emergenza, il conducente traini con il suo veicolo un altro veicolo senza rispettare le prescrizioni relative alla solidità del collegamento è punito con la sottrazione di 2 punti dalla patente', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '747', '27022', '6214', '2', 'Qualora, in situazione di emergenza, il conducente traini con il suo veicolo un altro veicolo senza rispettare le prescrizioni relative alla solidità del collegamento è punito con la sottrazione di 4 punti dalla patente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '748', '30017', '', '', 'Perdita di punti sulla patente ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '748', '30017', '6215', '1', 'Il superamento dei limiti di velocità di oltre 10 km/h durante la guida comporta una perdita di punti sulla patente', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '748', '30017', '6216', '2', 'Il mancato rispetto di alcune norme relative alla manovra di sorpasso comporta una perdita di punti sulla patente', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '748', '30017', '6217', '3', 'L''uso di telefono cellulare senza auricolare o viva voce durante la guida comporta una perdita di punti sulla  patente', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '748', '30017', '6218', '4', 'Circolare senza casco durante la guida quando previsto, o con casco mal allacciato, comporta una perdita di punti sulla patente', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '748', '30017', '6219', '5', 'Il mancato rispetto della distanza di sicurezza da cui sia derivato un incidente, comporta una perdita di punti sulla patente', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '748', '30017', '6220', '6', 'Il mancato utilizzo o l''uso improprio delle luci durante la guida, comporta una perdita di punti sulla patente', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '748', '30017', '6221', '7', 'Il trasporto in sovraccarico o in sovrannumero durante la guida, comporta una perdita di punti sulla patente', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '748', '30017', '6222', '8', 'L''omesso uso di lenti durante la guida, se prescritte, comporta una perdita di punti sulla patente', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '748', '30017', '6223', '9', 'Circolare sulla corsia di emergenza in autostrada, al di fuori dei casi previsti, comporta una perdita di punti sulla patente', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '748', '30017', '6224', '10', 'Guidare sotto l''effetto di sostanze stupefacenti comporta una perdita di punti sulla patente', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '748', '30017', '6225', '11', 'Gareggiare in velocità su piste private, avendo meno di 24 anni e la sola patente B, comporta una perdita di punti sulla patente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '748', '30017', '6226', '12', 'Lasciare in sosta l''auto davanti a un passo carrabile comporta una perdita di punti sulla patenteGareggiare in velocità su piste private, avendo meno di 24 anni e la sola patente B, comporta una perdita di punti sulla patente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '748', '30017', '6227', '13', 'Guidare senza avere con sé la patente comporta una perdita di punti sulla patente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '748', '30017', '6228', '14', 'Circolare senza avere con sé la carta di circolazione del veicolo di cui si è alla guida comporta una perdita di punti sulla patente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '748', '30017', '6229', '15', 'Guidare senza avere con sé il certificato di proprietà del veicolo di cui si è alla guida comporta una perdita di punti sulla patente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '748', '30017', '6230', '16', 'Circolare con un''autovettura dotata di otto posti compreso il conducente con patente di categoria C comporta una perdita di punti sulla patente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '748', '30017', '6231', '17', 'Circolare con un''autovettura su autostrada o su strada extraurbana principale avendo conseguito la patente di categoria B da meno di sei mesi comporta una perdita di punti sulla patente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '748', '30017', '6232', '18', 'Circolare, in un giorno festivo, con un autocarro carico, di massa complessiva a pieno carico inferiore a 3,5 tonnellate, comporta una perdita di punti sulla patente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '748', '30017', '6233', '19', 'Circolare con veicolo che non sia stato sottoposto alla prescritta revisione periodica comporta una perdita di punti sulla patente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '748', '30017', '6234', '20', 'La perdita di punti sulla patente per infrazione al codice della strada è raddoppiata se effettuata alla guida di motociclo di potenza superiore a 35 kW', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '749', '30012', '', '', 'Casi in cui viene ritirata la patente ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '749', '30012', '6235', '1', 'La patente di guida viene immediatamente ritirata, durante la circolazione, a seguito di violazione che comporti le sanzioni accessorie della sospensione della patente', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '749', '30012', '6236', '2', 'La patente di guida viene immediatamente ritirata, durante la circolazione, quando si accerti che il conducente guida con patente la cui validità è scaduta', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '749', '30012', '6237', '3', 'La patente di guida viene immediatamente ritirata, durante la circolazione, quando il conducente sia alla guida in stato di ebbrezza', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '749', '30012', '6238', '4', 'La patente di guida viene immediatamente ritirata, durante la circolazione, se il conducente non sistema correttamente il carico mal disposto, dopo invito degli organi di polizia', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '749', '30012', '6239', '5', 'La patente di guida viene immediatamente ritirata, durante la circolazione, se a seguito di incidente, sono derivate lesioni a persone', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '749', '30012', '6240', '6', 'La patente di guida viene immediatamente ritirata, durante la circolazione, quando il conducente, obbligato a sostenere un esame di revisione, non vi si è sottoposto nei termini prescritti', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '749', '30012', '6241', '7', 'La patente di guida viene immediatamente ritirata, durante la circolazione, quando il conducente trasporta un passeggero in stato di ebbrezza', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '749', '30012', '6242', '8', 'La patente di guida viene immediatamente ritirata, durante la circolazione, quando il conducente circola in Italia con patente rilasciata da uno stato estero in cui è residente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '749', '30012', '6243', '9', 'La patente di guida viene immediatamente ritirata, durante la circolazione, se si circola con pneumatici di dimensioni diverse da quelle riportate sulla carta di circolazione', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '749', '30012', '6244', '10', 'La patente di guida viene immediatamente ritirata, durante la circolazione, tutte le volte che il conducente commette una violazione che comporta una decurtazione di punti dalla patente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '749', '30012', '6245', '11', 'La patente di guida viene immediatamente ritirata, durante la circolazione, quando il conducente circola senza avere con sé la carta di circolazione del veicolo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '749', '30012', '6246', '12', 'La patente di guida viene immediatamente ritirata, durante la circolazione, quando il conducente circola senza avere con sé il certificato di proprietà del veicolo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '750', '30015', '', '', 'Sospensione della patente ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '750', '30015', '6247', '1', 'La sospensione della patente è una sanzione accessoria che deriva dalla violazione di alcuni articoli del codice della strada', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '750', '30015', '6248', '2', 'La sospensione della patente è disposta, ad esempio, quando il neopatentato circola senza rispettare le limitazioni sui veicoli e sulle velocità', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '750', '30015', '6249', '3', 'La sospensione della patente è disposta quando si superano di oltre 40 km/h i limiti massimi di velocità', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '750', '30015', '6250', '4', 'La sospensione della patente può essere disposta quando si circola, in autostrada, sulla corsia di emergenza in casi non previsti dal codice della strada', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '750', '30015', '6251', '5', 'La sospensione della patente è disposta quando il conducente guida sotto l''effetto di sostanze stupefacenti', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '750', '30015', '6252', '6', 'La sospensione della patente è disposta quando il conducente guida in stato di ebbrezza alcolica', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '750', '30015', '6253', '7', 'La sospensione della patente è disposta quando il conducente rifiuta di sottoporsi agli accertamenti sul suo eventuale stato di ebbrezza alcolica', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '750', '30015', '6254', '8', 'La sospensione della patente è disposta quando il conducente fugge senza prestare soccorso alla persona investita', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '750', '30015', '6255', '9', 'La sospensione della patente è disposta quando il conducente circola abusivamente con veicolo sottoposto a sequestro', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '750', '30015', '6256', '10', 'La sospensione della patente può essere disposta nei confronti di chi abbia falsificato o contraffatto i documenti relativi all''assicurazione del veicolo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '750', '30015', '6257', '11', 'La sospensione della patente è disposta quando non si è pagato nei termini il bollo dell''auto', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '750', '30015', '6258', '12', 'La sospensione della patente è disposta quando il conducente circola con patente sospesa', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '750', '30015', '6259', '13', 'La sospensione della patente è disposta quando il titolare della patente perde definitivamente i requisiti fisici', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '750', '30015', '6260', '14', 'La sospensione della patente è disposta quando sorgono dubbi sulla persistenza dei requisiti prescritti', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '750', '30015', '6261', '15', 'La sospensione della patente è disposta contestualmente alla comunicazione di azzeramento dei punti', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '750', '30015', '6262', '16', 'La sospensione della patente può essere disposta quando si superano di 20 km/h i limiti massimi di velocità', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '750', '30015', '6263', '17', 'La sospensione della patente può essere disposta quando si circola senza avere con sé la patente di guida', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '750', '30015', '6264', '18', 'La sospensione della patente è disposta quando si circola con patente illeggibile o deteriorata', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '750', '30015', '6265', '19', 'La sospensione della patente è disposta quando il titolare non è più in possesso dei requisiti morali', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '750', '30015', '6266', '20', 'La sospensione della patente è disposta quando si lascia il veicolo in sosta negli spazi destinati ai disabili', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '750', '30015', '6267', '21', 'La sospensione della patente è disposta quando, a richiesta degli agenti, non si è in grado di esibire il segnale mobile di pericolo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '751', '30014', '', '', 'Revoca della patente ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '751', '30014', '6268', '1', 'La revoca della patente può essere disposta quando il conducente, in autostrada, inverte il senso di marcia o percorre la carreggiata contro mano', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '751', '30014', '6269', '2', 'La revoca della patente è disposta quando il titolare perde permanentemente (per sempre) i requisiti fisici richiesti', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '751', '30014', '6270', '3', 'La revoca della patente è disposta quando il titolare perde permanentemente (per sempre) i requisiti psichici richiesti', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '751', '30014', '6271', '4', 'La revoca della patente è disposta quando la patente stessa viene sostituita con altra rilasciata da uno stato estero', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '751', '30014', '6272', '5', 'La revoca della patente è disposta quando il titolare, sottoposto ad esame di revisione, non risulti più idoneo alla guida', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '751', '30014', '6273', '6', 'La revoca della patente è disposta quando il titolare guida nonostante abbia la patente sospesa', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '751', '30014', '6274', '7', 'La revoca della patente è disposta quando il conducente guida con patente scaduta di validità', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '751', '30014', '6275', '8', 'La revoca della patente può essere disposta quando il conducente guida senza averla con sé', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '751', '30014', '6276', '9', 'La revoca della patente è disposta quando il conducente perde temporaneamente i requisiti fisici', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '751', '30014', '6277', '10', 'La revoca della patente può essere disposta quando il conducente guida senza lenti correttive quando prescritte', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '751', '30014', '6278', '11', 'La revoca della patente può essere disposta dagli organi di polizia', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '751', '30014', '6279', '12', 'La revoca della patente può essere disposta quando il conducente guida un veicolo intestato ad altra persona senza avere apposita dichiarazione scritta', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '752', '30013', '', '', 'Revisione della patente ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '752', '30013', '6280', '1', 'La revisione della patente di guida può essere disposta quando sorgono dubbi che il conducente sia ancora in possesso dei requisiti fisici e psichici prescritti', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '752', '30013', '6281', '2', 'La revisione della patente di guida può essere disposta dall''Ufficio Motorizzazione civile o dal Prefetto', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '752', '30013', '6282', '3', 'La revisione della patente di guida può essere disposta a seguito di incidente stradale che ha procurato lesioni gravi a persone', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '752', '30013', '6283', '4', 'La revisione della patente di guida può essere disposta qualora sorgano dubbi che il conducente sia ancora in possesso dell''idoneità tecnica alla guida', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '752', '30013', '6284', '5', 'La revisione della patente di guida può essere disposta con l''obbligo di sottoporsi a visita medica e/o ad esame di idoneità tecnica', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '752', '30013', '6285', '6', 'La revisione della patente di guida può essere disposta a seguito di guida in stato di ebbrezza o sotto l''effetto di sostanze stupefacenti', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '752', '30013', '6286', '7', 'La revisione della patente di guida è disposta a seguito di azzeramento dei puntidella patente di guida', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '752', '30013', '6287', '8', 'Il conducente che non supera l''esame di revisione della patente, subisce la revoca della patente stessa', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '752', '30013', '6288', '9', 'La revisione della patente di guida può essere disposta quando il titolare non è più in possesso dei requisiti morali richiesti', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '752', '30013', '6289', '10', 'La revisione della patente di guida può essere disposta quando il titolare incorre, per due volte in un anno, in una infrazione che comporta la perdita di almeno 5 punti', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '752', '30013', '6290', '11', 'La revisione della patente di guida può essere disposta quando il titolare perde permanentemente (per sempre) i requisiti fisici richiesti', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '752', '30013', '6291', '12', 'La revisione della patente di guida può essere disposta quando il titolare circola con la revisione del veicolo scaduta da oltre un anno', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '752', '30013', '6292', '13', 'La revisione della patente di guida può essere disposta quando il titolare circola su un''auto storica senza la prescritta documentazione', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '752', '30013', '6293', '14', 'La revisione della patente di guida può essere disposta quando il titolare circola su un veicolo che non è di sua proprietà e non ha un''autorizzazione scritta del proprietario', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '752', '30013', '6294', '15', 'La revisione della patente di guida è disposta quando si accerti che il conducente è alla guida senza aver con sé la patente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '752', '30013', '6295', '16', 'Il conducente che non supera l''esame di revisione della patente, può ripeterlo altre due volte entro un anno, continuando a guidare con uno speciale permesso rilasciato dalla Motorizzazione Civile', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '753', '30001', '', '', 'Documenti e obblighi per poter condurre un autoveicolo sulle strade pubblichè ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '753', '30001', '6296', '1', 'È vietato utilizzare un autoveicolo sulle strade pubbliche quando il veicolo non è coperto dall''assicurazione per la responsabilità civile', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '753', '30001', '6297', '2', 'È vietato condurre un autoveicolo sulle strade pubbliche quando non si possiede la patente di guida necessaria per quel tipo di veicolo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '753', '30001', '6298', '3', 'È di norma vietato condurre sulle strade pubbliche un autoveicolo non immatricolato', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '753', '30001', '6299', '4', 'È vietato circolare sulle strade pubbliche con un autoveicolo dotato della targa prove', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '753', '30001', '6300', '5', 'È vietato utilizzare un autoveicolo sulle strade pubbliche quando la carta di circolazione è intestata a persona diversa dal conducente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '753', '30001', '6301', '6', 'Sulle strade pubbliche è vietato utilizzare un autoveicolo se la garanzia è scaduta di validità', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '753', '30001', '6302', '7', 'È vietato utilizzare un autoveicolo sulle strade pubbliche quando non si ha con sé il certificato di proprietà', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '754', '30002', '', '', 'Documenti e dispositivi di cui un autoveicolo deve essere munito per poter circolare ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '754', '30002', '6303', '1', 'Un autoveicolo per poter circolare deve essere munito delle targhe identificative, affinché sia possibile risalire all''intestatario del mezzo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '754', '30002', '6304', '2', 'Un autoveicolo per poter circolare deve essere munito della carta di circolazione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '754', '30002', '6305', '3', 'Un autoveicolo per poter circolare deve essere munito del segnale mobile di pericolo (triangolo)', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '754', '30002', '6306', '4', 'Un''autovettura per poter circolare deve avere la polizza assicurativa del tipo kasko', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '755', '30003', '', '', 'Targhe dei veicoli ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '755', '30003', '6307', '1', 'Le targhe dei veicoli devono essere apposte saldamente e in posizione corretta', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '755', '30003', '6308', '2', 'Le targhe dei veicoli devono essere sempre ben leggibili', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '755', '30003', '6309', '3', 'Le targhe dei veicoli non devono essere sporche', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '755', '30003', '6310', '4', 'Le targhe dei veicoli non devono essere manomesse o contraffatte', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '755', '30003', '6311', '5', 'Gli autoveicoli usati per le esercitazioni di guida devono essere muniti, sia anteriormente che posteriormente, di appositi pannelli retroriflettenti recanti la lettera P(principiante)', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '755', '30003', '6312', '6', 'Le targhe dei veicoli possono essere piegate per adattarle meglio al loro supporto', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '756', '32021', '', '', 'Vista o le lenti a contatto ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '756', '32021', '6313', '1', 'Durante la guida, si devono usare gli occhiali da vista o le lenti a contatto se nella patente si trova indicato tale obbligo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '756', '32021', '6314', '2', 'Durante la guida, si devono usare gli occhiali da vista o le lenti a contatto se nella patente è stato inserto il codice armonizzato 01', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '756', '32021', '6315', '3', 'Durante la guida, si devono usare gli occhiali da vista o le lenti a contattose nella patente figura, per esteso o sotto forma di codice, tale obbligo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '756', '32021', '6316', '4', 'Durante la guida, si devono usare gli occhiali da vista o le lenti a contatto quando è indicato sulla patente', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '756', '32021', '6317', '5', 'Durante la guida, si devono usare gli occhiali da vista o le lenti a contatto soltanto in caso di scarsa visibilità', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '756', '32021', '6318', '6', 'Durante la guida, si deve fare uso di occhiali da vista o di lenti a contatto da mezz''ora dopo il tramonto del sole a mezz''ora prima del suo sorgere', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '756', '32021', '6319', '7', 'Durante la guida, si deve fare uso di occhiali da vista o di lenti a contatto solo quando la miopia supera 6 diottrie', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '756', '32021', '6320', '8', 'Durante la guida, si deve fare uso di occhiali da vista o di lenti a contatto soltanto sulle autostrade e sulle strade extraurbane principali', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '757', '35017', '', '', 'L''uso del telefono ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '757', '35017', '6321', '1', 'L''uso del telefono cellulare senza l''ausilio del viva-voceo dell''auricolare è vietato durante la guida', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('20', '757', '35017', '6322', '2', 'I pannelli a messaggio variabile forniscono indicazioni di pericolo ma non possono avere valore prescrittivo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '758', '31001', '', '', 'Cause di incidenti dovuti alla struttura della strada ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '758', '31001', '6323', '1', 'Causa probabile di incidenti dovuti alla struttura della strada può essere ristrettezza della strada', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '758', '31001', '6324', '2', 'Causa probabile di incidenti dovuti alla struttura della strada può essere mancata segnalazione degli incroci', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '758', '31001', '6325', '3', 'Causa probabile di incidenti dovuti alla struttura della strada può essere mancanza di segnaletica orizzontale', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '758', '31001', '6326', '4', 'Causa probabile di incidenti dovuti alla struttura della strada può essere fondo stradale deformato', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '758', '31001', '6327', '5', 'Causa probabile di incidenti dovuti alla struttura della strada può essere fondo stradale scivoloso', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '758', '31001', '6328', '6', 'Causa probabile di incidenti dovuti alla struttura della strada può essere presenza di strettoie non segnalate', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '758', '31001', '6329', '7', 'Causa probabile di incidenti dovuti alla struttura della strada può essere strada suddivisa in carreggiate', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '758', '31001', '6330', '8', 'Causa probabile di incidenti dovuti alla struttura della strada può essere strada suddivisa in corsie', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '758', '31001', '6331', '9', 'Causa probabile di incidenti dovuti alla struttura della strada può essere carreggiata a senso unico di circolazione', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '758', '31001', '6332', '10', 'Causa probabile di incidenti dovuti alla struttura della strada può essere strada a percorso pianeggiante', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '758', '31001', '6333', '11', 'Causa probabile di incidenti dovuti alla struttura della strada può essere presenza di barriere laterali ad assorbimento d''urto (guardrails)', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '758', '31001', '6334', '12', 'Causa probabile di incidenti dovuti alla struttura della strada può essere regolamentazione semaforica', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '758', '31001', '6335', '13', 'Causa probabile di incidenti dovuti alla struttura della strada può essere mancanza di curve pericolose', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '759', '31011', '', '', 'Comportamenti da adottare percorrendo un tunnel o un traforo ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '759', '31011', '6336', '1', 'Percorrendo un lungo tunnel o traforo, in caso di formazione di lunghe file di veicoli, è opportuno aumentare la distanza di sicurezza, per scongiurare, in caso d''incendio, il rapido propagarsi delle fiamme', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '759', '31011', '6337', '2', 'Percorrendo un lungo tunnel o traforo, in caso d''incendio, bisogna abbandonare velocemente il veicolo, seguendo la segnaletica che porta alle vie di fuga o ai luoghi sicuri protetti da porte tagliafuoco', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '759', '31011', '6338', '3', 'Percorrendo un lungo tunnel o traforo, in caso d''incendio, bisogna attenersi alle istruzioni vocali del personale di servizio o dei vigili del fuoco, per evitare di incamminarsi nella direzione sbagliata', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '759', '31011', '6339', '4', 'Percorrendo un lungo tunnel o traforo, in caso d''incendio, bisogna seguire la segnaletica di emergenza, anche se in netto contrasto con gli ordini vocali impartiti dai vigili del fuoco', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '760', '31011', '', '', 'Comportamenti da adottare percorrendo un tunnel o un traforo ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '760', '31011', '6340', '1', 'Il pannello in figura, posto all''interno di una galleria, indica la presenza di una via di fuga ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '760', '31011', '6341', '2', 'Il pannello in figura obbliga a tornare indietro all''imbocco della galleria ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '761', '31011', '', '', 'Comportamenti da adottare percorrendo un tunnel o un traforo ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '761', '31011', '6342', '1', 'Il pannello in figura, posto all''interno di una galleria, indica la presenza di un estintore ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '762', '31002', '', '', ' Comportamenti da adottare in caso di pioggia ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '762', '31002', '6343', '1', 'In caso di pioggia occorre ridurre la velocità', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '762', '31002', '6344', '2', 'In caso di pioggia occorre tenere in funzione i tergicristalli', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '762', '31002', '6345', '3', 'In caso di pioggia occorre aumentare la distanza di sicurezza', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '762', '31002', '6346', '4', 'In caso di pioggia occorre evitare l''appannamento dei vetri', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '762', '31002', '6347', '5', 'In caso di pioggia occorre manovrare con prudenza lo sterzo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '762', '31002', '6348', '6', 'In caso di pioggia occorre passare velocemente sulle pozzanghere per evitare di rimanere impantanati', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '762', '31002', '6349', '7', 'In caso di pioggia occorre coprire il radiatore con l''apposita mascherina', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '762', '31002', '6350', '8', 'In caso di pioggia occorre frenare energicamente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '762', '31002', '6351', '9', 'In caso di pioggia occorre utilizzare, durante la marcia, la segnalazione  luminosa di pericolo (quattro frecce lampeggianti simultaneamente)', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '762', '31002', '6352', '10', 'In caso di pioggia occorre procedere, di norma, con il pedale della frizione abbassato', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '763', '31003', '', '', 'Aquaplaning ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '763', '31003', '6353', '1', 'Il fenomeno dell''aquaplaning fa scivolare le ruote sullo strato di acqua', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '763', '31003', '6354', '2', 'Il fenomeno dell''aquaplaning inizia a velocità più bassa se il pneumatico è molto consumato', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '763', '31003', '6355', '3', 'Il fenomeno dell''aquaplaning si verifica più facilmente nei veicoli più leggeri', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '763', '31003', '6356', '4', 'Il fenomeno dell''aquaplaning si verifica più facilmente a bassa velocità', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '763', '31003', '6357', '5', 'Il fenomeno dell''aquaplaning riduce lo sbandamento del veicolo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '763', '31003', '6358', '6', 'Il fenomeno dell''aquaplaning aumenta nelle carreggiate a forte pendenza laterale', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '763', '31003', '6359', '7', 'Il fenomeno dell''aquaplaning aumenta l''aderenza sul fondo stradale', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '763', '31003', '6360', '8', 'Il fenomeno dell''aquaplaning rafforza il contatto fra pneumatici e asfalto', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '763', '31003', '6361', '9', 'Il fenomeno dell''aquaplaning non dipende dallo spessore e dal disegno del battistrada', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '764', '31004', '', '', 'Comportamenti da adottare su strade coperte di neve ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '764', '31004', '6362', '1', 'Su strade coperte di neve occorre montare pneumatici invernali su tutte le ruote oppure catene sulle ruote motrici', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '764', '31004', '6363', '2', 'Su strade coperte di neve occorre moderare la velocità', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '764', '31004', '6364', '3', 'Su strade coperte di neve occorre aumentare la distanza di sicurezza', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '764', '31004', '6365', '4', 'Su strade coperte di neve occorre evitare brusche manovre', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '764', '31004', '6366', '5', 'Su strade coperte di neve occorre evitare brusche accelerazioni', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '764', '31004', '6367', '6', 'Su strade coperte di neve occorre frenare con maggiore energia', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '764', '31004', '6368', '7', 'Su strade coperte di neve occorre alla partenza, accelerare al massimo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '764', '31004', '6369', '8', 'Su strade coperte di neve occorre sterzare bruscamente per evitare di urtare veicoli fermi', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '764', '31004', '6370', '9', 'Su strade coperte di neve occorre frenare a fondo in caso di sbandamento del veicolo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '765', '31005', '', '', 'Comportamenti da adottare in presenza di tratti di strada ghiacciati ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '765', '31005', '6371', '1', 'In presenza di tratti di strada ghiacciati è opportuno procedere a velocità moderata', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '765', '31005', '6372', '2', 'In presenza di tratti di strada ghiacciati è opportuno aumentare la distanza di sicurezza', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '765', '31005', '6373', '3', 'In presenza di tratti di strada ghiacciati è opportuno distanziarsi dalla traiettoria dei veicoli che si incrociano', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '765', '31005', '6374', '4', 'In presenza di tratti di strada ghiacciati è opportuno, in discesa, procedere con marce basse', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '765', '31005', '6375', '5', 'In presenza di tratti di strada ghiacciati è opportuno evitare brusche accelerazioni', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '765', '31005', '6376', '6', 'In presenza di tratti di strada ghiacciati è opportuno usare maggiore attenzione nel transito su zone in ombra', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '765', '31005', '6377', '7', 'In presenza di tratti di strada ghiacciati è opportuno innestare la doppia trazione, se il veicolo ne è provvisto', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '765', '31005', '6378', '8', 'In presenza di tratti di strada ghiacciati è opportuno marciare molto vicino alla barriera laterale', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '765', '31005', '6379', '9', 'In presenza di tratti di strada ghiacciati è opportuno non usare le catene, ma gomme chiodate sulle ruote anteriori', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '765', '31005', '6380', '10', 'In presenza di tratti di strada ghiacciati è opportuno frenare energicamente ogni volta che si incrocia un altro veicolo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '765', '31005', '6381', '11', 'In presenza di tratti di strada ghiacciati è opportuno procedere con il cambio in folle', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '765', '31005', '6382', '12', 'Su una strada innevata l''aderenza del veicolo è minore che su una strada ghiacciata', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '766', '31009', '', '', 'Comportamenti da adottare in caso di infossamento su neve o sabbia ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '766', '31009', '6383', '1', 'In caso di infossamento su neve o sabbia, non riuscendo a spostare il veicolo, è consigliabile inserire sotto la ruota che slitta qualcosa che faccia attrito (pezzi di legno, tappeti del veicolo, ecc.)', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '766', '31009', '6384', '2', 'In caso di infossamento su neve o sabbia, non riuscendo a partire con la prima marcia è opportuno innestare una marcia superiore', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '767', '31006', '', '', 'Comportamenti da adottare in caso di nebbia fitta ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '767', '31006', '6385', '1', 'In caso di nebbia fitta è opportuno lasciarsi guidare dalla segnaletica orizzontale', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '767', '31006', '6386', '2', 'In caso di nebbia fitta è opportuno procedere ad una velocità adeguata alla visibilità', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '767', '31006', '6387', '3', 'In caso di nebbia fitta è opportuno fermarsi, se necessario, fuori dalla carreggiata', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '767', '31006', '6388', '4', 'In caso di nebbia fitta è opportuno se costretti a fermarsi sulla carreggiata, usare la segnalazione luminosa di pericolo (quattro frecce lampeggianti simultaneamente)', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '767', '31006', '6389', '5', 'In caso di nebbia fitta è opportuno evitare di fermarsi sulla carreggiata, se non per cause di forza maggiore', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '767', '31006', '6390', '6', 'In caso di nebbia fitta è opportuno accendere i proiettori fendinebbia o, in mancanza, quelli anabbaglianti', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '767', '31006', '6391', '7', 'In caso di nebbia fitta è opportuno accendere la luce posteriore per nebbia', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '767', '31006', '6392', '8', 'In caso di nebbia fitta è opportuno guidare con la massima prudenza e concentrazione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '767', '31006', '6393', '9', 'In caso di nebbia fitta è opportuno mantenere in funzione l''indicatore di direzione sinistro', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '767', '31006', '6394', '10', 'In caso di nebbia fitta è opportuno procedere a zig zag per far meglio notare la propria presenza', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '767', '31006', '6395', '11', 'In caso di nebbia fitta è opportuno accodarsi al veicolo che precede quanto più vicino possibile', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '767', '31006', '6396', '12', 'In caso di nebbia fitta è opportuno suonare continuativamente il clacson', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '767', '31006', '6397', '13', 'In caso di nebbia fitta è opportuno tenere la cintura di sicurezza sganciata per essere più pronti ad abbandonare il veicolo in caso di incidente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '767', '31006', '6398', '14', 'In caso di nebbia fitta è opportuno circolare con le sole luci di posizioni accese', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '768', '31010', '', '', 'Cause dei tamponamenti ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '768', '31010', '6399', '1', 'I tamponamenti stradali avvengono principalmente per la forte velocità', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '768', '31010', '6400', '2', 'I tamponamenti stradali avvengono maggiormente se non si rispetta la distanza di sicurezza', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '768', '31010', '6401', '3', 'I tamponamenti stradali avvengono principalmente per la disattenzione dei conducenti', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '768', '31010', '6402', '4', 'I tamponamenti stradali avvengono principalmente per l''improvviso guasto del freno di stazionamento', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '768', '31010', '6403', '5', 'I tamponamenti stradali avvengono principalmente tra autoveicoli dotati di servosterzo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '769', '31008', '', '', 'Comportamenti da tenere quando si deve far salire o scendere un passeggero ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '769', '31008', '6404', '1', 'Quando bisogna far salire o scendere un passeggero, è opportuno che ciò avvenga dal lato del marciapiede o, in mancanza, dal lato opposto al traffico', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '769', '31008', '6405', '2', 'Quando si deve far salire o scendere un passeggero dal veicolo, bisogna aprire la portiera solamente quando non si causa pericolo agli altri utenti della strada', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '769', '31008', '6406', '3', 'Quando si deve far salire o scendere un passeggero dal veicolo, bisogna farlo attendere fino a che il veicolo sia completamente fermo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '769', '31008', '6407', '4', 'Quando si deve far salire o scendere un bambino dal veicolo, è opportuno che vi sia il controllo di un adulto', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '769', '31008', '6408', '5', 'Quando si deve far scendere un passeggero, bisogna tenere presente che l''apertura della portiera di destra è priva di qualsiasi rischio e pericolo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '770', '27023', '', '', 'Rischi derivanti dalla presenza di ciclisti ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '770', '27023', '6409', '1', 'Il conducente di un''autovettura o di un motociclo deve adattare la propria guida anche ai rischi legati alla presenza di velocipedi nel traffico', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '770', '27023', '6410', '2', 'Il conducente di un''autovettura deve considerare i rischi che, sulla strada, sono legati alla presenza di velocipedi, mezzi dotati di massa modesta e caratterizzati da basse velocità e possibili oscillazioni trasversali', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '770', '27023', '6411', '3', 'Il conducente di un''autovettura deve prestare particolare attenzione per la presenza nel traffico di velocipedi utilizzati da conducenti giovanissimi e inesperti o da anziani', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '770', '27023', '6412', '4', 'Per un ciclista che si accinge ad effettuare una manovra è molto difficile valutare la velocità di avvicinamento dei veicoli a motore presenti nel traffico', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '770', '27023', '6413', '5', 'Il conducente di un''autovettura deve prevedere la difficoltà di valutazione della velocità del proprio veicolo da parte di un ciclista che, trovandosi sulla stessa corsia di marcia, si accinge a svoltare a sinistra', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '770', '27023', '6414', '6', 'Il conducente deve esercitare particolare prudenza guidando in presenza di ciclistaanziano che, per ispezionare la strada alle proprie spalle, esegue con difficoltà e lentezza la torsione del busto a causa dell''età avanzata', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '770', '27023', '6415', '7', 'La presenza nel traffico extraurbano di bambini in bicicletta, anche se affiancati, non rappresenta un rischio per chi guida un autoveicolo, data la loro modesta velocità', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '770', '27023', '6416', '8', 'I ciclisti possono viaggiare in tutta sicurezza nel traffico extraurbano perché i conducenti dei veicoli a motore sono sempre capaci di prevedere eventuali loro cadute o pericolosi sbandamenti dovuti ad irregolarità del fondo stradale', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '770', '27023', '6417', '9', 'La velocità di avvicinamento di un''autovettura, soprattutto su strade extraurbane, è facilmente valutabile a distanza da parte di un ciclista che procede lentamente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '771', '27024', '', '', 'Rischi derivanti dalla presenza di ciclomotoristi e ciclisti ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '771', '27024', '6418', '1', 'Il conducente di un''autovettura deve costantemente ricordare il rischio derivante dalla presenza nel traffico di ciclomotoristi dotati di limitata esperienza di guida a causa della giovane età ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '771', '27024', '6419', '2', 'La manovra di svolta a sinistra è particolarmente pericolosa per un ciclomotore che deve interferire con la traiettoria di una o più colonne di veicoli più veloci sopraggiungenti nella stessa direzione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '771', '27024', '6420', '3', 'Il conducente di autoveicolo deve valutare con prudenza, in relazione alla propria velocità, la distanza che lo separa da un ciclomotorista che si accinge a svoltare', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '771', '27024', '6421', '4', 'Il conducente di autoveicolo deve prevedere manovre improvvise altrui, come il procedere a zig-zag di un ciclomotore', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '771', '27024', '6422', '5', 'Il conducente di autoveicolo o motoveicolo deve rispettare la distanza di sicurezza laterale da ciclisti o ciclomotoristi per evitare di metterne a repentaglio l''equilibrio nella guida', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '771', '27024', '6423', '6', 'Il conducente di autoveicolo deve evitare deviazioni trasversali, anche modeste, del proprio veicolo soprattutto in presenza di ciclomotoristi o ciclisti per evitarne la possibile caduta a terra', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '771', '27024', '6424', '7', 'Il conducente di autoveicolo deve rispettare le norme della circolazione, ma non è tenuto a prevedere le possibili imprudenze del ciclomotorista che lo affianca sulla strada', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '771', '27024', '6425', '8', 'Risulta facile al conducente di un autoveicolo valutare la distanza che lo separa da un ciclomotorista che rallenta bruscamente per svoltare in una strada non sufficientemente segnalata', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '771', '27024', '6426', '9', 'Il conducente di un''autovettura può superare a velocità sostenuta, pur rispettando i limiti, un ciclomotorista nonostante la scarsa distanza di sicurezza laterale', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '772', '27025', '', '', 'Rischi derivanti dalla presenza di veicoli lenti ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '772', '27025', '6427', '1', 'Il conducente di autovettura deve conoscere i rischi derivanti dalla presenza, soprattutto su strade extraurbane, di mezzi particolarmente lenti, come ciclomotori a tre ruote', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '772', '27025', '6428', '2', 'Per un''autovettura che procede a velocità sostenuta, pur rispettando i limiti su strada extraurbana a due sole corsie, un ciclomotore a tre ruote costituisce un potenziale pericolo, soprattutto se effettua manovre improvvise', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '772', '27025', '6429', '3', 'Per il conducente di un''autovettura che procede a velocità regolare su strada extraurbana, può risultare difficoltosa la valutazione della velocità di una macchina agricola che, in genere, procede lentamente', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '772', '27025', '6430', '4', 'La presenza di veicoli che possono raggiungere velocità molto diverse tra loro, soprattutto su strada extraurbana, costituisce un fattore di sicurezza', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '772', '27025', '6431', '5', 'Risulta facile la valutazione dei tempi di avvicinamento ad un veicolo lento per un''autovettura che, specialmente su strada extraurbana, viaggia a velocità sostenuta pur rispettando i limiti', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '773', '31012', '', '', 'Cautele da adottare nei confronti dei veicoli a due ruote ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '773', '31012', '6432', '1', 'I motocicli, avendo una ridotta sagoma frontale, sono meno visibili in distanza', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '773', '31012', '6433', '2', 'I conducenti degli autoveicoli devono, in generale, prestare particolare attenzione ai veicoli a motore a due ruote, in quanto meno facilmente visibili', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '773', '31012', '6434', '3', 'I conducenti degli autoveicoli, nell''aprire le portiere, devono prestare particolare attenzione ai veicoli a motore a due ruote, in quanto meno visibili', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '773', '31012', '6435', '4', 'Nell''attraversamento degli incroci si deve sempre considerare la possibilità che sopraggiunga, senza che sia visibile a distanza, un veicolo a motore a due ruote che ha sorpassato i veicoli fermi', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '773', '31012', '6436', '5', 'In caso di intenso traffico con incolonnamento di veicoli, prima di aprire le portiere per scendere dal proprio veicolo in sosta, si deve sempre considerare la possibilità che sopraggiunga un veicolo a motore a due ruote che sta sorpassando a destra i veicoli fermi in coda', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '773', '31012', '6437', '6', 'Durante il sorpasso di un veicolo a motore a due ruote, soprattutto se ad elevata velocità, si deve mantenere una buona distanza laterale di sicurezza', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '773', '31012', '6438', '7', 'I conducenti di motocicli sono più facilmente percepibili dagli altri conducenti di autoveicoli, perché più rumorosi', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '773', '31012', '6439', '8', 'I conducenti di veicoli a due ruote, avendo una ridotta sagoma frontale, sono più visibili in distanza', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '774', '27028', '', '', 'Rischi derivanti dalla presenza di autocarri, autotreni, autoarticolati e autobus ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '774', '17028', '6440', '1', 'Alla guida di un''autovettura è opportuno valutare costantemente la potenziale pericolosità dei veicoli industriali e degli autobus, a causa della loro massa notevolmente maggiore', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '774', '17028', '6441', '2', 'Il conducente di motociclo o di autovettura deve saper valutare nella guida gli alti rischi derivanti da autocarri, autotreni o autoarticolati che non rispettano i limiti di velocità', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '774', '17028', '6442', '3', 'Autocarri, autotreni e autoarticolati, per la loro massa notevole, non costituiscono, per un''autovettura o per un motociclo, rischi potenziali maggiori rispetto a qualsiasi altro veicolo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '775', '27026', '', '', 'Rischi derivanti dalla possibilità di collisione tra veicoli di masse differenti ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '775', '27026', '6443', '1', 'Risulta maggiormente pericolosa la collisione tra veicoli dotati di masse molto diverse tra loro', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '775', '27026', '6444', '2', 'Guidando una piccola autovettura è necessario valutare i maggiori rischi derivanti da possibili tamponamenti o collisioni con autovettura di massa superiore', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '775', '27026', '6445', '3', 'Un''autovettura dotata di notevole massa costituisce, nella circolazione, una garanzia di sicurezza anche per gli altri veicoli di dimensioni e di massa inferiori', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '775', '27026', '6446', '4', 'I veicoli sono tanto più sicuri, nella circolazione, quanto più le loro masse sono differenziate', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '775', '27026', '6447', '5', 'Le grandi differenze di prestazioni e di masse fra le autovetture facilitano una buona sicurezza stradale', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '776', '27032', '', '', 'Comportamenti da tenere circolando dietro ad un veicolo che trasporta un carico instabile o sporgente ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '776', '27032', '6448', '1', 'Circolando dietro ad un veicolo che trasporta un carico in pericolo di cadere, è opportuno aumentare la distanza di sicurezza, specie se si è in salita', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '776', '27032', '6449', '2', 'Circolando dietro ad un veicolo che ha un carico instabile, è opportuno aumentare la distanza di sicurezza e sorpassarlo appena possibile', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '776', '27032', '6450', '3', 'Circolando dietro ad un veicolo che trasporta un carico che sta per cadere, è opportuno avvertire il conducente del potenziale pericolo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '776', '27032', '6451', '4', 'Circolando dietro ad un veicolo che ha un carico poco sicuro, conviene non stargli troppo vicino, così da riuscire a fermarsi in tempo, in caso di caduta della merce trasportata', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '776', '27032', '6452', '5', 'Circolando dietro ad un veicolo che ha un carico instabile, bisogna stare attenti, soprattutto quando frena o svolta, per la possibile caduta della merce trasportata', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '776', '27032', '6453', '6', 'Circolando dietro ad un veicolo che ha un carico sporgente, può risultare difficile accorgersi che intende svoltare, se gli indicatori di direzione sono nascosti dalla merce penzolante', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '776', '27032', '6454', '7', 'Circolando dietro ad un veicolo che trasporta un carico che sta per cadere, conviene stringersi il più vicino possibile al margine sinistro della carreggiata', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '776', '27032', '6455', '8', 'Circolando dietro ad un veicolo che trasporta un carico poco sicuro, conviene spostarsi lungo il margine sinistro della carreggiata', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '776', '27032', '6456', '9', 'Circolando dietro ad un veicolo che trasporta un carico che sta per cadere, conviene sorpassarlo subito, anche se in curva', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '776', '27032', '6457', '10', 'Circolando dietro ad un veicolo che trasporta un carico che sta per cadere, conviene arrestarsi bruscamente, ripartendo solo quando gli altri veicoli ci abbiano superato', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '776', '27032', '6458', '11', 'Circolando dietro ad un veicolo che trasporta un carico instabile, bisogna diminuire la distanza di sicurezza, in modo che gli altri veicoli rinuncino al sorpasso, non essendovi spazio sufficiente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '777', '27027', '', '', 'Rischi che un conducente di fuoristrada (SUV) può far correre agli altri utenti della strada ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '777', '27027', '6459', '1', 'Il conducente di un fuoristrada (Suv) deve guidare con la massima prudenza, consapevole che l''altezza e la massa del proprio veicolo costituiscono un potenziale pericolo per pedoni, ciclisti e autovetture di modeste dimensioni', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '777', '27027', '6460', '2', 'I conducenti dei fuoristrada (Suv) debbono considerare i gravi danni che i paraurti sporgenti e rafforzati del proprio mezzo possono determinare nel caso di impatto con veicoli più piccoli', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '777', '27027', '6461', '3', 'Il conducente di fuoristrada (Suv) deve ricordare che la guida di tale veicolo è particolarmente impegnativa in città e che richiede particolari cautele soprattutto nelle manovre di emergenza', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '777', '27027', '6462', '4', 'La guida di un fuoristrada (Suv), dotato di massa notevole e di altezza superiore alle autovetture, garantisce la massima sicurezza per gli occupanti del veicolo stesso e per gli altri utenti della strada', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '777', '27027', '6463', '5', 'Nel caso di collisione o di tamponamento, i paraurti sporgenti e rafforzati (bull bars) rendono i fuoristrada meno pericolosi per gli altri veicoli', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '777', '27027', '6464', '6', 'La guida di un fuoristrada (Suv) risulta particolarmente indicata all''interno dei centri abitati', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '778', '31013', '', '', 'Pericoli per i veicoli a due ruote derivanti dalla presenza di rotaie, tombini e strisce pedonali ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '778', '31013', '6465', '1', 'Le rotaie del tram sono un elemento stradale che può essere pericoloso per la circolazione dei veicoli a due ruote', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '778', '31013', '6466', '2', 'Le rotaie del tram sono particolarmente pericolose per la circolazione dei veicoli a due ruote quando il fondo stradale è bagnato', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '778', '31013', '6467', '3', 'Le strisce pedonali dipinte sulla carreggiata sono un elemento stradale che può essere pericoloso per la circolazione dei veicoli a due ruote', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '778', '31013', '6468', '4', 'Le strisce pedonali dipinte sulla carreggiata sono particolarmente pericolose per la circolazione dei veicoli a due ruote quando il fondo stradale è bagnato', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '778', '31013', '6469', '5', 'I tombini presenti sulla carreggiata sono un elemento stradale che può essere pericoloso per la circolazione dei veicoli a due ruote', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '778', '31013', '6470', '6', 'I tombini presenti sulla carreggiata sono particolarmente pericolosi per la circolazione dei veicoli a due ruote quando il fondo stradale è bagnato', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '778', '31013', '6471', '7', 'Circolando su un veicolo a due ruote con fondo stradale bagnato, la frenata è particolarmente pericolosa se le ruote del veicolo sono su rotaie del tram, strisce pedonali o tombini', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '778', '31013', '6472', '8', 'Il passaggio in velocità su tombini o rotaie del tram a bordo di un veicolo a due ruote può far perdere tenuta di strada e dirigibilità al veicolo, causando sbandamenti o cadute', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '778', '31013', '6473', '9', 'Le rotaie del tram o i tombini presenti sulla carreggiata stradale non costituiscono un pericolo per un conducente di veicolo a due ruote', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '778', '31013', '6474', '10', 'Quando, in un giorno di pioggia, si deve transitare su rotaie del tram o su tombini presenti sulla carreggiata stradale a bordo di un veicolo a due ruote, occorre accelerare decisamente prima di transitarvi sopra', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '778', '31013', '6475', '11', 'Quando, in un giorno di pioggia, si deve transitare su rotaie del tram o su tombini presenti sulla carreggiata stradale a bordo di un veicolo a due ruote, è bene alzarsi in piedi sulle pedivelle prima di transitarvi sopra', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '778', '31013', '6476', '12', 'Quando, in un giorno di pioggia, si deve transitare su rotaie del tram o su tombini presenti sulla carreggiata stradale a bordo di un veicolo a due ruote, è bene farlo in impennata in modo da non farvi transitare sopra la ruota anteriore che è quella direttrice', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '778', '31013', '6477', '13', 'Le rotaie del tram o i tombini presenti sulla carreggiata stradale costituiscono un pericolo per un conducente di veicolo a due ruote, nella sola eventualità che il suo veicolo sia dotato di cambio automatico', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '778', '31013', '6478', '14', 'Se si deve circolare con veicolo a due ruote su una carreggiata a circolazione promiscua veicoli/tram, è bene se possibile circolare con entrambe le ruote nel solco della rotaia del tram', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '779', '27029', '', '', 'Campo di visibilità di un veicolo ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '779', '27029', '6479', '1', 'Nonostante le caratteristiche costruttive del veicolo rendano possibile un adeguato campo di visibilità, durante la guida il conducente deve attentamente visualizzare l''area antistante e limitrofa', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '779', '27029', '6480', '2', 'Per il conducente il campo di visibilità può essere ridotto sia da uno dei poggiatesta sia da elementi della carrozzeria della propria autovettura che possono impedire accidentalmente l''avvistamento di altri utenti della strada', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '779', '27029', '6481', '3', 'Per consentire una adeguata visibilità è vietato applicare pellicole adesive sul parabrezza e sui vetri laterali anteriori dei veicoli', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '779', '27029', '6482', '4', 'I pedoni e soprattutto i bambini rischiano di rimanere fuori dal campo visivo del conducente di un veicolo fuoristrada (Suv) a causa delle dimensioni del mezzo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '779', '27029', '6483', '5', 'Il conducente può limitarsi ad un controllo visivo affrettato dell''area immediatamente antistante il veicolo poiché il suo mezzo dispone per costruzione di unadeguato campo di visibilità', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '779', '27029', '6484', '6', 'È consentito applicare sui vetri laterali anteriori e sul parabrezza pellicole adesive, a condizione che siano di materiale rimovibile', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '779', '27029', '6485', '7', 'I veicoli fuoristrada presentano un''altezza tale da permettere al conducente una adeguata visibilità di tutti i possibili ostacoli in basso (ad esempio, bambini)', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '780', '27030', '', '', 'Campo di visibilità di un autocarro ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '780', '27030', '6486', '1', 'Prima di riprendere la marcia, il conducente di un autocarro deve prestare particolare attenzione alla possibile presenza di un pedone o di un ciclista nella zona immediatamente antistante il proprio veicolo fermo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '780', '27030', '6487', '2', 'Il conducente di un autocarro fermo al semaforo deve prevedere la presenza di pedoni e ciclisti il cui avvistamento è precluso dalla sagoma del proprio veicolo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '780', '27030', '6488', '3', 'La limitata visuale offerta dal parabrezza degli autocarri nella zona immediatamente antistante il veicolo rende pericolosa la fermata di tali mezzi in colonna, qualora siano presenti pedoni o mezzi a due ruote', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '780', '27030', '6489', '4', 'Il campo di visibilità degli autocarri permette il controllo sicurodi tutta la zona immediatamente antistante e laterale', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '780', '27030', '6490', '5', 'Un pedone può attraversare la strada in tutta sicurezza passando a distanza ravvicinata rispetto alla parte frontale di un autocarro fermo in colonna che sta per ripartire, in quanto sicuramente avvistabile dal conducente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '780', '27030', '6491', '6', 'Quando stanno per riprendere la marcia dopo un arresto al semaforo, i conducenti di autocarri possono facilmente avvistare pedoni o ciclisti che affiancano e sfiorano la parte anteriore dei propri veicoli', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '781', '31007', '', '', 'Comportamenti da tenere quando si è coinvolti in un incidente stradale ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '781', '31007', '6492', '1', 'Il conducente coinvolto in un incidente stradale ha l''obbligo di fermarsi e di prestare assistenza agli eventuali feriti', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '781', '31007', '6493', '2', 'Il conducente coinvolto in un incidente stradale deve fornire le proprie generalità e gli estremi della patente, della targa e dell''assicurazione del veicolo alle persone danneggiate', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '781', '31007', '6494', '3', 'Il conducente coinvolto in un incidente stradale deve evitare che vengano modificate le tracce, se occorre ricostruire la dinamica dell''incidente', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '781', '31007', '6495', '4', 'Il conducente coinvolto in un incidente stradale per la denunzia all''assicurazione può avvalersi degli appositi moduli prestampati forniti dalla propria assicurazione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '781', '31007', '6496', '5', 'Dopo un incidente stradale è obbligatorio apporre il segnale mobile di pericolo sul lunotto posteriore del veicolo, prima di andar via', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '781', '31007', '6497', '6', 'Dopo un incidente stradale è obbligatorio coprire il veicolo con un telo di plastica', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '781', '31007', '6498', '7', 'Dopo un incidente stradale è obbligatorio, solo di notte, apporre il segnale mobile di pericolo in prossimità del veicolo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '781', '31007', '6499', '8', 'Dopo un incidente stradale è obbligatorio collocare subito il veicolo sul marciapiede', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '781', '31007', '6500', '9', 'Dopo un incidente stradale, è obbligatorio segnalare il veicolo fermo con il segnale mobile di pericolo anche nei centri abitati', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '782', '', '', '', 'Comportamenti da tenere per evitare incidenti', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '782', 'oooo', '6501', '1', 'È opportuno che il conducente esegua personalmente la manutenzione della pompa di iniezione del proprio veicolo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '782', 'oooo', '6502', '2', 'La distanza percorsa durante il tempo di reazione non varia con la velocità', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '782', 'oooo', '6503', '3', 'Il corretto funzionamento del motore è garantito dal fatto che la spia rossa dell''olio di lubrificazione rimane costantemente accesa', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '782', 'oooo', '6504', '4', 'Su strada sdrucciolevole, per garantire le migliori condizioni di aderenza in frenata, è opportuno premere a fondo il pedale del freno', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '782', 'oooo', '6505', '5', 'È consentito migliorare le prestazioni del proprio veicolo aumentando la cilindrata del motore', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '782', 'oooo', '6506', '6', 'Non è necessario mettere a punto i freni squilibrati, se il conducente è in grado di correggere l''anomalo comportamento del veicolo agendo sullo sterzo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '782', 'oooo', '6507', '7', 'È consentito aumentare le prestazioni del proprio veicolo modificandone le impostazioni elettroniche della centralina che controlla l''iniezione di carburante', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '783', '', '', '', 'Cautele che devono adottare i conducenti dei veicoli a due ruote', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '783', 'oooo', '6508', '1', 'Durante la circolazione nei centri abitati, i motociclisti possono in ogni caso utilizzare l''avvisatore acustico giungendo in prossimità degli incroci', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '783', 'oooo', '6509', '2', 'Di notte, i conducenti di veicoli a due ruote sono obbligati a circolare con un giubbotto giallo rifrangente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '783', 'oooo', '6510', '3', 'Sulle strade in cui vige l''obbligo di catene, in caso di nevicata, ai conducenti dei veicoli a due ruote è comunque concesso di circolare', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '783', 'oooo', '6511', '4', 'I conducenti di veicoli a due ruote, essendo meno facilmente visibili in distanza, durante le ore notturne devono in ogni caso circolare coi proiettori abbaglianti accesi', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('21', '783', 'oooo', '6512', '5', 'L''abbigliamento del motociclista non incide sulla sua visibilità nel traffico', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '784', '32001', '', '', 'Comportamento che deve adottare un conducente che accusa segni di stanchezza ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '784', '32001', '6513', '1', 'È opportuno, per il conducente che accusa segni di stanchezza, raggiungere la più vicina piazzola di sosta e riposare', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '784', '32001', '6514', '2', ' È opportuno, per il conducente che accusa segni di stanchezza, avvicinarsi il più possibile al veicolo che lo precede nella marcia', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '785', '32005', '', '', 'Effetti dell’alimentazione sulla guida', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '785', '32005', '6515', '1', 'Un''alimentazione pesante ed eccessiva è sconsigliata per chi deve guidare.', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '785', '32005', '6516', '2', ' L''alimentazione con cibi di difficile digestione può provocare una diminuzione dell''attenzione e un aumento dei tempi di reazione.', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '785', '32005', '6517', '3', 'L''assunzione di bevande superalcoliche, durante un pasto abbondante non compromette la facoltà di guida.', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '786', '32002', '', '', 'Recupero dell’idoneità alla guida dopo l’assunzione di bevande alcoliche', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '786', '32002', '6518', '1', 'È necessario che trascorrano alcune ore affinché un conducente che abbia assunto una quantità eccessiva di bevande alcoliche recuperi l''idoneità alla guida', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '786', '32002', '6519', '2', 'Un conducente che ha assunto una quantità eccessiva di bevande alcoliche non può recuperare velocemente l''idoneità alla guida, specie se ha assunto anche dei farmaci', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '786', '32002', '6520', '3', 'Un conducente che ha assunto una quantità eccessiva di bevande alcoliche può recuperare velocemente l''idoneità alla guida se assume cibi piuttosto salati.', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '786', '32002', '6521', '4', 'Un conducente che ha assunto una quantità eccessiva di bevande alcoliche può recuperare velocemente l''idoneità alla guida se assume caffè amaro', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '787', '32003', '', '', 'Uso di sostanze che possono compromettere l’idoneità alla guida', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '787', '32003', '6522', '1', 'L''idoneità alla guida può essere compromessa se si fa uso di alcuni farmaci sedativi', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '787', '32003', '6523', '2', 'L''idoneità alla guida può essere compromessa se si fa uso di farmaci antidepressivi', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '787', '32003', '6524', '3', 'L''idoneità alla guida può essere compromessa se si fa uso di allucinogeni (LSD, ecstasy, ecc.) ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '787', '32003', '6525', '4', 'L''idoneità alla guida può essere compromessa se si fa uso di bevande alcoliche', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '787', '32003', '6526', '5', 'L''idoneità alla guida può essere compromessa se si fa uso di caffè amaro', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '787', '32003', '6527', '6', 'L''idoneità alla guida può essere compromessa se si fa uso di bevande gassate', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '787', '32003', '6528', '7', 'L''idoneità alla guida può essere compromessa se si fa uso di integratori di sali minerali', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '787', '32003', '6529', '8', 'L''idoneità alla guida può essere compromessa se si fa uso di integratori alimentari', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '788', '32004', '', '', 'Effetti dell’assunzione di droghe o farmaci sulla guida', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '788', '32004', '6530', '1', 'L''assunzione di hashish o di marijuana può compromettere la facoltà di guida', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '788', '32004', '6531', '2', 'L''assunzione di eroina o di cocaina può compromettere la facoltà di guida', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '788', '32004', '6532', '3', 'Nei farmaci sedativi sono presenti alcune sostanze che possono compromettere la facoltà di guida', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '788', '32004', '6533', '4', 'L''assunzione di LSD o l''anfetamina non compromette la facoltà di guida', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '788', '32004', '6534', '5', 'L''assunzione di hashish o di marijuana non compromette la facoltà di guida', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '789', '32006', '', '', 'Effetti dell’assunzione di droghe o farmaci sulla guida', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '789', '32006', '6535', '1', 'L''alcool può causare uno stato di euforia, con conseguente sottovalutazione del pericolo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '789', '32006', '6536', '2', 'Chi guida in stato di ebbrezza alcolica rischia l''arresto', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '789', '32006', '6537', '3', 'È sconsigliabile guidare quando si ha uno stato emotivo alterato', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '789', '32006', '6538', '4', 'Chi guida un veicolo senza motore, con una concentrazione di alcool nel sangue superiore al limite stabilito, non è soggetto ad alcuna sanzione', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '789', '32006', '6539', '5', ' Chi guida in stato di ebbrezza alcoolica ha tempi di reazione più rapidi', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '790', '32020', '', '', 'Effetti dell’assunzione di alcool sulla guida', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '790', '32020', '6540', '1', 'L''assunzione di alcool rende meno sicura la guida, poiché può provocare eccitazione e imprudenza', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '790', '32020', '6541', '2', 'L''assunzione di alcool rende meno sicura la guida, perché può rallentare i riflessi', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '790', '32020', '6542', '3', 'L''assunzione di alcool rende meno sicura la guida, perché può provocare sonnolenza', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '790', '32020', '6543', '4', 'L''assunzione di alcool rende meno sicura la guida, perché può modificare pericolosamente la capacità di concentrazione e attenzione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '790', '32020', '6544', '5', 'L''assunzione di alcool può far diminuire il livello di attenzione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '790', '32020', '6545', '6', 'L''assunzione di alcool rende più sicura la guida, perché annulla la stanchezza', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '790', '32020', '6546', '7', 'L''assunzione di alcool facilita la guida, perché fa aumentare la capacità di concentrazione', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '790', '32020', '6547', '8', 'L''assunzione di alcool facilita la guida, perché rende più vigili e attenti', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '790', '32020', '6548', '9', 'L''assunzione di alcool rende la guida più sicura, più allegra e meno noiosa', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '790', '32020', '6549', '10', 'L''assunzione di alcool migliora la vista', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '791', '32007', '', '', 'Obbligo di soccorrere i feriti ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '791', '32007', '6550', '1', 'Soccorrere un ferito della strada è obbligatorio per legge', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '791', '32007', '6551', '2', 'Soccorrere un ferito della strada può significare salvargli la vita', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '791', '32007', '6552', '3', 'Soccorrere subito un ferito della strada può evitare che subisca ulteriori danni fisici', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '791', '32007', '6553', '4', 'Soccorrere un ferito della strada non è un obbligo imposto dalla legge, ma solo un dovere morale', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '791', '32007', '6554', '5', 'Bisogna soccorrere un ferito della strada solo se si considera grave l''incidente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '791', '32007', '6555', '6', 'Non è obbligatorio soccorrere un ferito della strada, se per farlo, si crea intralcio alla circolazione', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '791', '32007', '6556', '7', 'Soccorrere un ferito della strada è obbligatorio solo per i medici e il personale sanitario', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '791', '32007', '6557', '8', 'Non è obbligatorio soccorrere un ferito della strada, per non mettere a repentaglio la propria sicurezza', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '792', '32008', '', '', 'Scopo e limiti del primo soccorso', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '792', '32008', '6558', '1', 'Il fine del primo soccorso è quello di cercare di mantenere in vita il ferito con semplici e immediati interventi', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '792', '32008', '6559', '2', 'Il fine del primo soccorso è quello di assistere il ferito, come meglio possibile, in attesa che arrivi il soccorso medico', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '792', '32008', '6560', '3', 'Nell''intervento di primo soccorso bisogna evitare, per quanto possibile, che il ferito subisca ulteriori danni', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '792', '32008', '6561', '4', 'Nelle operazioni di primo soccorso bisogna evitare che al ferito vengano praticati interventi sbagliati o dannosi', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '792', '32008', '6562', '5', 'Il fine del primo soccorso è quello di chiedere subito al ferito come è avvenuto l''incidente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '792', '32008', '6563', '6', 'Nell''intervento di primo soccorso bisogna allontanare in ogni caso il ferito dal posto in cui si trova', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '792', '32008', '6564', '7', 'Durante un intervento di primo soccorso non bisogna mai toccare il ferito se non è ancora arrivata la polizia ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '793', '32018', '', '', 'Intervento in soccorso degli infortunati dopo un incidente', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '793', '32018', '6565', '1', 'Dopo un incidente stradale, il soccorritore che interviene deve valutare bene la situazione, per proteggere gli infortunati da altri pericoli', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '793', '32018', '6566', '2', 'Dopo un incidente stradale, il soccorritore che interviene deve assistere gli infortunati al meglio delle sue possibilità, senza però prendere iniziative di specifica competenza medica', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '793', '32018', '6567', '3', 'Dopo un incidente stradale, il soccorritore che interviene deve prestare assistenza agli infortunati, secondo le proprie possibilità e conoscenze', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '793', '32018', '6568', '4', 'Chi è coinvolto in un incidente stradale deve allontanarsi sempre e subito dal luogo in cui si è verificato', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '793', '32018', '6569', '5', 'In caso di incidente stradale, bisogna aspettare solo l''arrivo dei soccorsi, senza fare nulla ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '793', '32018', '6570', '6', 'In caso di incidente stradale, la prima cosa che deve fare il soccorritore è identificare l''infortunato, chiedendogli nome e cognome', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '793', '32018', '6571', '7', 'In caso di incidente stradale, il soccorritore deve somministrare farmaci alla vittima, se la stessa non ha specifica competenza medica', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '794', '32019', '', '', 'Stato di shock', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '794', '32019', '6572', '1', 'Lo stato di shock può essere causato da un forte dolore', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '794', '32019', '6573', '2', 'Lo stato di shock può essere causato da una forte emozione (gioia, rabbia)','V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '794', '32019', '6574', '3', 'Lo stato di shock può essere causato da una forte perdita di sangue', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '794', '32019', '6575', '4', 'Lo stato di shock può essere causato da un forte trauma ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '794', '32019', '6576', '5', 'Lo stato di shock non può dipendere da forti emozioni', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '794', '32019', '6577', '6', 'Lo stato di shock non si verifica mai nelle persone che hanno avuto un trauma', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '794', '32019', '6578', '7', 'Lo stato di shock non si verifica mai nelle persone che hanno perso molto sangue ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '794', '32019', '6579', '8', 'Lo stato di shock non rappresenta un pericolo per la salute', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '795', '32012', '', '', 'Sintomi dello stato di shock', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '795', '32012', '6580', '1', 'Lo stato di shock si manifesta con pelle molto pallida e fredda', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '795', '32012', '6581', '2', 'Lo stato di shock si manifesta con presenza di sudore freddo alla fronte', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '795', '32012', '6582', '3', 'Lo stato di shock si manifesta con presenza di brividi, pelle molto pallida e sudorazione fredda alla fronte', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '795', '32012', '6583', '4', 'Lo stato di shock si manifesta con pelle molto pallida, sudorazione fredda, frequenti battiti del cuore', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '795', '32012', '6584', '5', 'Lo stato di shock può manifestarsi con improvvisa pronuncia di frasi e parole senza senso', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '795', '32012', '6585', '6', 'Lo stato di shock si manifesta sempre con un forte aumento della temperatura corporea', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '795', '32012', '6586', '7', 'Lo stato di shock si manifesta con ampie macchie scure sulla pelle', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '795', '32012', '6587', '8', 'Lo stato di shock si manifesta con comparsa di colorito rosso della pelle e forti vampate di calore', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '796', '32013', '', '', 'Trattamento di un ferito in stato di shock', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '796', '32013', '6588', '1', 'Un ferito in stato di shock va messo a terra e, se possibile, con le gambe sollevate', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '796', '32013', '6589', '2', 'Un ferito in stato di shock va coperto come meglio possibile, per evitare che si raffreddi', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '796', '32013', '6590', '3', 'Un ferito in stato di shock va disteso, tenendo possibilmente le gambe sollevate', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '796', '32013', '6591', '4', 'In presenza di un ferito in stato di shock, occorre farlo camminare, per riattivargli la circolazione del sangue', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '796', '32013', '6592', '5', 'Un ferito in stato di shock va messo in posizione seduta', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '796', '32013', '6593', '6', 'In presenza di un ferito in stato di shock, occorre dargli piccoli schiaffi su guance e gambe, per riattivargli la circolazione del sangue', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '796', '32013', '6594', '7', 'In presenza di un ferito in stato di shock, occorre fargli bere piccole quantità di superalcolici', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '797', '32014', '', '', 'Trattamento di un ferito in stato di incoscienza', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '797', '32014', '6595', '1', 'Se un ferito della strada è in stato di incoscienza, bisogna verificare se respira ancora, mettendo una mano sulla parte laterale bassa del torace o sull''addome', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '797', '32014', '6596', '2', 'Se un ferito della strada è in stato di incoscienza, può essere mosso solo se si è certi che non abbia traumi alla colonna vertebrale', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '797', '32014', '6597', '3', 'Se un ferito della strada è in stato di incoscienza e fa fatica a respirare, bisogna liberare subito il naso e la bocca da eventuali ostruzioni', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '797', '32014', '6598', '4', 'Se un ferito della strada è in stato di incoscienza, bisogna controllare spesso le sue condizioni di salute fino all''arrivo dell''ambulanza', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '797', '32014', '6599', '5', 'Se un ferito della strada è in stato di incoscienza, bisogna somministrargli bevande alcooliche', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '797', '32014', '6600', '6', 'Se un ferito della strada è in stato di incoscienza bisogna bloccargli le mani', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '797', '32014', '6601', '7', 'Se un ferito della strada è in stato di incoscienza bisogna fasciargli la testa', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '797', '32014', '6602', '8', 'Se un ferito della strada è in stato di incoscienza bisogna metterlo in posizione semiseduta', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '798', '32017', '', '', 'Trattamento di un ferito che presenta ustioni ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '798', '32017', '6603', '1', 'Se un ferito della strada si è ustionato e sono ancora presenti delle residue fiamme, bisogna spegnerle, soffocandole, ad esempio, con una coperta', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '798', '32017', '6604', '2', 'Se un ferito della strada si è ustionato, è consigliabile non togliere i vestiti rimasti attaccati alla pelle bruciata', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '798', '32017', '6605', '3', 'Per diminuire il dolore delle ustioni si deve, se possibile, fare uso di acqua fredda', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '798', '32017', '6606', '4', 'Se un ferito della strada ha riportato delle ustioni al busto, conviene coprirle con materiale pulito, possibilmente sterile', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '798', '32017', '6607', '5', 'Se un ferito della strada si è ustionato e sono ancora presenti delle residue fiamme, bisogna aspettare che si spengano da sole, prima di intervenire', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '798', '32017', '6608', '6', 'Se un ferito della strada si è ustionato, bisogna sempre togliere le parti dei vestiti che sono rimaste attaccate alla pelle', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '798', '32017', '6609', '7', 'Se un ferito della strada si è ustionato, bisogna disinfettare le bruciature con l''alcool', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '799', '32009', '', '', 'Trattamento di un infortunato che presenta una ferita sanguinante', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '799', '32009', '6610', '1', 'Se un infortunato della strada ha una ferita sanguinante la si deve coprire con materiale sterile o quanto più possibile pulito, per bloccare il sanguinamento', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '799', '32009', '6611', '2', 'Se un infortunato della strada ha una ferita sanguinante, sporca di terra, bisogna lavarla quanto prima con acqua pulita', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '799', '32009', '6612', '3', 'Durante un intervento di primo soccorso, se l''infortunato ha una ferita sanguinante, con presenza di corpi estranei conficcati, non bisogna toglierli dalla ferita', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '799', '32009', '6613', '4', 'Se un infortunato della strada ha una ferita sanguinante, bisogna tamponarla con garze sterili o panni puliti', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '799', '32009', '6614', '5', 'Durante un intervento di primo soccorso, se l''infortunato ha una ferita sanguinante, si devono togliere subito gli eventuali corpi estranei in essa presenti e, successivamente, coprire la ferita', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '799', '32009', '6615', '6', 'Se un infortunato della strada ha una ferita sanguinante ed è incosciente, bisogna dargli da bere acqua o tè', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '799', '32009', '6616', '7', 'Se un infortunato della strada ha una ferita sanguinante non bisogna coprirla, per facilitare l''arresto spontaneo del sangue', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '799', '32009', '6617', '8', 'Se un infortunato della strada ha una ferita sanguinante si deve versarvi sopra acqua calda, per fermare il sanguinamento ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '800', '32010', '', '', 'Trattamento di un infortunato che presenta un’emorragia esterna', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '800', '32010', '6618', '1', 'Se un infortunato della strada ha un''abbondante emorragia esterna, bisogna tamponare la ferita e chiamare subito i soccorsi', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '800', '32010', '6619', '2', 'Se un infortunato della strada ha un''abbondante emorragia esterna, si deve tamponare subito la ferita, facendo pressione con del materiale pulito o possibilmente sterile', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '800', '32010', '6620', '3', 'Se un infortunato della strada ha un''abbondante emorragia esterna, bisogna cercare di arrestare la fuoriuscita del sangue', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '800', '32010', '6621', '4', 'Se un infortunato della strada ha un''abbondante emorragia esterna è opportuno, se possibile, mettere il ferito in posizione seduta o sdraiata, in attesa che arrivino i soccorsi', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '800', '32010', '6622', '5', 'Se un infortunato della strada ha un''abbondante emorragia esterna, bisogna solo medicare la ferita', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '800', '32010', '6623', '6', 'Se un infortunato della strada ha un''abbondante emorragia esterna, si deve coprire la ferita e far camminare l''infortunato', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '800', '32010', '6624', '7', 'Se un infortunato della strada ha un''abbondante emorragia esterna non si può assolutamente dargli da bere acqua', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '801', '32011', '', '', 'Trattamento di un ferito che presenta un corpo estraneo in un occhio', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '801', '32011', '6625', '1', 'Se ad un infortunato della strada è entrato un corpo estraneo in un occhio (scheggia di vetro, legno o altro), bisogna bendarglielo, senza procedere ad altre manovre ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '801', '32011', '6626', '2', 'Se ad un infortunato della strada è entrato un corpo estraneo in un occhio, bisogna consigliargli di non toccare l''occhio danneggiato, per evitare danni maggiori', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '801', '32011', '6627', '3', 'Se ad un infortunato della strada è entrato un corpo estraneo in un occhio, bisogna bendarglielo, evitando di toccarlo fino all''intervento dello specialista', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '801', '32011', '6628', '4', 'Se ad un infortunato della strada è entrato un corpo estraneo in un occhio, bisogna cercare sempre e comunque di toglierglielo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '801', '32011', '6629', '5', 'Se ad un infortunato della strada è entrato un corpo estraneo in un occhio, bisogna costringerlo a tenere gli occhi aperti, per evitare altre lesioni', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '801', '32011', '6630', '6', 'Se ad un infortunato della strada è entrato un corpo estraneo in un occhio, bisogna massaggiargli delicatamente la palpebra, per favorire la lacrimazione', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '801', '32011', '6631', '7', 'Se ad un infortunato della strada è entrato un corpo estraneo in un occhio non bisogna mai bendargli l''occhio ferito ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '802', '32015', '', '', 'Trattamento di un infortunato che presenta un trauma alla gabbia toracica', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '802', '32015', '6632', '1', 'Se un infortunato della strada è cosciente, ma ha avuto un trauma della gabbia toracica, bisogna cercare di facilitargli la respirazione mettendolo semi-seduto', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '802', '32015', '6633', '2', 'Se un infortunato della strada è cosciente, ma ha avuto un trauma della gabbia toracica, con ferita visibile e profonda, bisogna comprimere subito con un panno pulito ', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '802', '32015', '6634', '3', 'Se un infortunato della strada ha avuto un trauma della gabbia toracica, bisogna assisterlo fino all''arrivo del soccorso medico', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '802', '32015', '6635', '4', 'Se un infortunato della strada ha avuto un trauma della gabbia toracica, bisogna fargli fare profondi respiri', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '802', '32015', '6636', '5', 'Se un infortunato della strada ha avuto un trauma della gabbia toracica, bisogna metterlo in posizione distesa, con le gambe sollevate', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '802', '32015', '6637', '6', 'Se un infortunato della strada ha avuto un trauma della gabbia toracica, bisogna farlo stare alzato, per diminuire la fuoriuscita di sangue', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '803', '32016', '', '', 'Trattamento di un infortunato che ha riportato fratture agli arti', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '803', '32016', '6638', '1', 'Se un infortunato della strada ha riportato fratture agli arti non bisogna muovere gli arti fratturati', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '803', '32016', '6639', '2', 'Se un infortunato della strada ha riportato fratture agli arti, bisogna bloccarglieli, senza procedere ad altre manovre', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '803', '32016', '6640', '3', 'Se un infortunato della strada ha riportato fratture agli arti, bisogna coprire le eventuali ferite con materiale pulito', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '803', '32016', '6641', '4', 'Se un infortunato della strada ha riportato fratture agli arti, bisogna bloccarglieli e subito dopo, se necessario, attuare le normali misure anti-shock', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '803', '32016', '6642', '5', 'Se un infortunato della strada ha riportato fratture agli arti, bisogna cercare di farlo camminare, per diminuire il dolore', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '803', '32016', '6643', '6', 'Se un infortunato della strada ha riportato fratture agli arti, bisogna chiedergli di muoverli, per valutare il tipo di lesione', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '803', '32016', '6644', '7', 'Se un infortunato della strada ha riportato fratture agli arti, con fuoriuscita di un osso, bisogna cercare di riposizionarlo nei tessuti', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('22', '803', '32016', '6645', '8', 'Se un infortunato della strada ha riportato fratture agli arti bisogna dargli da bere una bevanda superalcolica', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '804', '33007', '', '', 'Responsabilità per danni connessi alla circolazione dei veicoli ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '804', '33007', '6646', '1', 'La responsabilità per danni connessi alla circolazione dei veicoli è a carico del conducente e del proprietario del veicolo, se si tratta di responsabilità civile', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '804', '33007', '6647', '2', 'La responsabilità per danni connessi alla circolazione dei veicoli è a carico del conducente del veicolo, se si tratta di responsabilità penale', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '804', '33007', '6648', '3', 'La responsabilità per danni connessi alla circolazione dei veicoli non è a carico del proprietario del veicolo se prova che la circolazione è avvenuta contro la sua volontà', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '804', '33007', '6649', '4', 'La responsabilità per danni connessi alla circolazione dei veicoli è a carico del proprietario del veicolo, se si tratta di responsabilità penale', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '804', '33007', '6650', '5', 'La responsabilità per danni connessi alla circolazione dei veicoli è a carico solo del conducente del veicolo se ci sono stati danni alle cose', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '805', '33005', '', '', 'Responsabilità civile connessa al sinistro stradale ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '805', '33005', '6651', '1', 'La responsabilità civile connessa al sinistro stradale consiste nell''obbligo di risarcire i danni causati sia alle persone che alle cose', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '805', '33005', '6652', '2', 'La responsabilità civile connessa al sinistro stradale grava sul proprietario e sul conducente del veicolo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '805', '33005', '6653', '3', 'La responsabilità civile connessa al sinistro stradale obbliga il responsabile al risarcimento del danno', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '805', '33005', '6654', '4', 'La responsabilità civile connessa al sinistro stradale è indipendente dalla responsabilità penale e dalla responsabilità amministrativa', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '805', '33005', '6655', '5', 'La responsabilità civile connessa al sinistro stradale è basata sul principio che ogni danno causato deve essere risarcito', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '805', '33005', '6656', '6', 'La responsabilità civile connessa al sinistro stradale è esclusa in caso di morte del danneggiato', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '805', '33005', '6657', '7', 'La responsabilità civile connessa al sinistro stradale si limita al risarcimento dei danni alle sole cose', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '805', '33005', '6658', '8', 'La responsabilità civile connessa al sinistro stradale è esclusa per danni di lieve entità', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '805', '33005', '6659', '9', 'La responsabilità civile connessa al sinistro stradale è aggravata dalla mancata copertura assicurativa', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '805', '33005', '6660', '10', 'La responsabilità civile connessa al sinistro stradale grava sull''impresa assicuratrice anche per danni superiori ai massimali', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '806', '33001', '', '', 'Risarcimento dei danni in sede civile ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '806', '33001', '6661', '1', 'Chi è civilmente obbligato a risarcire i danni provocati da un sinistro stradale si libera dall''obbligo se risarcisce il danneggiato', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '806', '33001', '6662', '2', 'Chi è civilmente obbligato a risarcire i danni provocati da un sinistro stradale può accordarsi con il danneggiato circa il valore del risarcimento', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '806', '33001', '6663', '3', 'Chi è civilmente obbligato a risarcire i danni provocati da un sinistro stradale può far riparare il proprio autoveicolo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '806', '33001', '6664', '4', 'Chi è civilmente obbligato a risarcire i danni provocati da un sinistro stradale può farsi assistere da un consulente d''infortunistica stradale', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '806', '33001', '6665', '5', 'Chi è civilmente obbligato a risarcire i danni provocati da un sinistro stradale può, in alternativa, chiedere che la propria patente di guida sia revocata', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '806', '33001', '6666', '6', 'Chi è civilmente obbligato a risarcire i danni provocati da un sinistro stradale si libera da ogni forma di responsabilità penale pagandone l''ammontare del danno', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '807', '33004', '', '', 'Responsabilità penale connessa al sinistro stradale ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '807', '33004', '6667', '1', 'La responsabilità penale connessa al sinistro stradale sorge qualora esso provochi lesioni gravi a persone', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '807', '33004', '6668', '2', 'La responsabilità penale connessa al sinistro stradale sorge anche in caso di omicidio colposo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '807', '33004', '6669', '3', 'La responsabilità penale connessa al sinistro stradale sorge allorché vengano violate norme contemplate dal Codice Penale', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '807', '33004', '6670', '4', 'La responsabilità penale connessa al sinistro stradale sorge in ogni caso in cui sia violato il Codice della strada', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '807', '33004', '6671', '5', 'La responsabilità penale connessa al sinistro stradale sorge quando il veicolo vada completamente distrutto', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '807', '33004', '6672', '6', 'La responsabilità penale connessa al sinistro stradale sorge in tutti i casi di danni alle persone o alle cose', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '807', '33004', '6673', '7', 'La responsabilità penale connessa al sinistro stradale sorge nei confronti del proprietario del veicolo se il conducente non è stato identificato', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '808', '33003', '', '', 'Responsabilità penale connessa al sinistro stradale ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '808', '33003', '6674', '1', 'La responsabilità penale connessa al sinistro stradale è esclusa quando vi siano danni involontari alle sole cose', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '808', '33003', '6675', '2', 'La responsabilità penale connessa al sinistro stradale è esclusa quando il fatto non sia considerato un reato', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '808', '33003', '6676', '3', 'La responsabilità penale connessa al sinistro stradale è esclusa quando il fatto dipende da causa di forza maggiore', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '808', '33003', '6677', '4', 'La responsabilità penale connessa al sinistro stradale è esclusa quando si sia prestato soccorso alla vittima', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '808', '33003', '6678', '5', 'La responsabilità penale connessa al sinistro stradale è esclusa ogni qualvolta ci sia stato il risarcimento del danno', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '808', '33003', '6679', '6', 'La responsabilità penale connessa al sinistro stradale è esclusa qualora intervenga l''impresa di assicurazione che risarcisca adeguatamente il danneggiato', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '809', '33002', '', '', 'Responsabilità civile e penale in un sinistro stradale ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '809', '33002', '6680', '1', 'Chi è penalmente e civilmente responsabile di un sinistro stradale è soggetto alle pene previste dal Codice Penale', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '809', '33002', '6681', '2', 'Chi è penalmente e civilmente responsabile di un sinistro stradale è tenuto al risarcimento dei danni', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '809', '33002', '6682', '3', 'Chi è penalmente e civilmente responsabile di un sinistro stradale qualora abbia violato norme del Codice della strada è anche soggetto alle relative sanzioni', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '809', '33002', '6683', '4', 'Chi è penalmente e civilmente responsabile di un sinistro stradale può incorrere nella sospensione della patente di guida', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '809', '33002', '6684', '5', 'Chi è penalmente e civilmente responsabile di un sinistro stradale può incorrere nella revoca della patente di guida', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '809', '33002', '6685', '6', 'Chi è penalmente e civilmente responsabile di un sinistro stradale può incorrere nella revisione della patente di guida', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '809', '33002', '6686', '7', 'Chi è penalmente e civilmente responsabile di un sinistro stradale è sempre soggetto all''arresto preventivo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '809', '33002', '6687', '8', 'Chi è penalmente e civilmente responsabile di un sinistro stradale non può continuare a guidare veicoli della stessa categoria, ma solo di categoria inferiore', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '809', '33002', '6688', '9', 'Chi è penalmente e civilmente responsabile di un sinistro stradale è in ogni caso assoggettato alla revoca della patente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '809', '33002', '6689', '10', 'Chi è penalmente e civilmente responsabile di un sinistro stradale non è assoggettato alle sanzioni del codice della strada', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '810', '33014', '', '', 'Assicurazione obbligatoria RCA ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '810', '33014', '6690', '1', 'L''assicurazione per la responsabilità civile auto (R.C.A.) è obbligatoria per legge', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '810', '33014', '6691', '2', 'L''assicurazione per la responsabilità civile auto (R.C.A.), obbligatoria per legge, comprende il risarcimento dei danni provocati dal veicolo, purché guidato dal proprietario', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '811', '33006', '', '', 'Assicurazione obbligatoria RCA ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '811', '33006', '6692', '1', 'L''assicurazione obbligatoria RCA copre i danni subiti dai terzi trasportati se il veicolo è autorizzato al trasporto di persone', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '811', '33006', '6693', '2', 'L''assicurazione obbligatoria RCA copre i danni subiti da persone e/o cose che si trovano in aree pubbliche o aperte al pubblico', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '811', '33006', '6694', '3', 'L''assicurazione obbligatoria copre i danni subiti solo da terzi trasportati sui taxi', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '812', '33008', '', '', 'Divieto di circolazione per veicoli privi copertura assicurativa o con assicurazione scaduta ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '812', '33008', '6695', '1', 'È vietato utilizzare il veicolo privo di copertura assicurativa sulle strade pubbliche', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '812', '33008', '6696', '2', 'Quando l''assicurazione per la responsabilità civile auto è scaduta di validità si incorre in una sanzione se si circola o si lascia in sosta il veicolo sulle strade pubbliche', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '812', '33008', '6697', '3', 'Quando l''assicurazione per la responsabilità civile auto è scaduta di validità si può incorrere nella confisca del veicolo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '812', '33008', '6698', '4', 'Quando l''assicurazione per la responsabilità civile auto è scaduta di validità si può continuare a usare il veicolo solo per brevi tragitti', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '812', '33008', '6699', '5', 'Quando l''assicurazione per la responsabilità civile auto è scaduta di validità si può utilizzare il veicolo solo sul territorio italiano', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '812', '33008', '6700', '6', 'Quando l''assicurazione per la responsabilità civile auto è scaduta di validità è possibile lasciare il veicolo in sosta sulle strade pubbliche', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '812', '33008', '6701', '7', 'Quando l''assicurazione per la responsabilità civile auto è scaduta di validità, il veicolo deve essere sottoposto a collaudo tecnico prima di essere rimesso in circolazione', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '812', '33008', '6702', '8', 'Quando l''assicurazione per la responsabilità civile auto è scaduta di validità è possibile continuare ad usare il veicolo, solo se si è disposti a pagare personalmente gli eventuali danni arrecati', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '813', '33013', '', '', 'Assicurazione obbligatoria RCA ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '813', '33013', '6703', '1', 'L''assicurazione per la responsabilità civile auto (R.C.A.) è valida anche per gli incidenti avvenuti nei Paesi dell''Unione Europea', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '813', '33013', '6704', '2', 'L''assicurazione per la responsabilità civile auto (R.C.A.) copre anche la responsabilità penale in base al premio pagato ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '813', '33013', '6705', '3', 'L''assicurazione per la responsabilità civile auto (R.C.A.) copre anche le sanzioni amministrative pecuniarie, previste dal codice della strada ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '813', '33013', '6706', '4', 'L''assicurazione per la responsabilità civile auto (R.C.A.) copre ogni danno causato, anche se superiore al massimale stabilito ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '813', '33013', '6707', '5', 'L''assicurazione per la responsabilità civile auto (R.C.A.) copre solo la responsabilità del conducente, ma non quella del proprietario del veicolo ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '813', '33013', '6708', '6', 'L''assicurazione per la responsabilità civile auto (R.C.A.) copre solo i danni arrecati alle persone, perché per i danni alle cose occorre un altro tipo di contratto ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '814', '33011', '', '', 'Massimali della polizza di assicurazione RCA ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '814', '33011', '6709', '1', 'I massimali della polizza di assicurazione R.C.A. (responsabilità civile auto) sono gli importi massimi che l''impresa assicuratrice si impegna a pagare, in caso di incidente', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '814', '33011', '6710', '2', 'La cifra minima dei massimali della polizza di assicurazione R.C.A. (responsabilità civile auto) viene stabilita dalla legge', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '814', '33011', '6711', '3', 'I massimali della polizza di assicurazione R.C.A. (responsabilità civile auto) possono essere aumentati, a scelta del cliente, pagando una maggiore somma di denaro (premio assicurativo)', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '814', '33011', '6712', '4', 'I massimali della polizza di assicurazione R.C.A. (responsabilità civile auto) sono le somme di denaro che l''impresa assicuratrice riscuote ogni anno dal proprio cliente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '814', '33011', '6713', '5', 'I massimali della polizza di assicurazione R.C.A. (responsabilità civile auto) aumentano ogni anno', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '814', '33011', '6714', '6', 'I massimali della polizza di assicurazione R.C.A. (responsabilità civile auto) vengono restituiti al cliente alla fine dell''anno, se non si causano incidenti', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '815', '33012', '', '', 'Contratto di assicurazione RCA con formula bonus-malus ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '815', '33012', '6715', '1', 'Nell''assicurazione per la responsabilità civile auto (R.C.A.), il contratto con la formula bonus maluscomporta una variazione annuale del premio di assicurazione, in base al numero di incidenti causati', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '815', '33012', '6716', '2', 'La formula assicurativa bonus malusnon si applica per i motocicli', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '815', '33012', '6717', '3', 'Nell''assicurazione per la responsabilitàcivile auto (R.C.A.), il contratto con la formula bonus malusviene automaticamente annullato se si provocano più di due incidenti', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '815', '33012', '6718', '4', 'Nell''assicurazione per la responsabilità civile auto (R.C.A.), il contratto con la formula bonus malusviene stabilito dal giudice', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '816', '33006', '', '', 'Risarcimento diretto dei sinistri stradali ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '816', '33006', '6719', '1', 'Il risarcimento diretto nei sinistri stradali si applica se vi sono danni al veicolo e lesioni di lieve entità al conducente', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '816', '33006', '6720', '2', 'La procedura di risarcimento diretto non è applicabile se vi sono più di due veicoli coinvolti', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '816', '33006', '6721', '3', 'Il risarcimento diretto è escluso se nel sinistro è coinvolto un motociclo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '816', '33006', '6722', '4', 'In caso di sinistro con danni al veicolo, la richiesta di risarcimento diretto deve essere obbligatoriamente corredata di fotografie', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '817', '33014', '', '', 'Documenti che l’impresa assicuratrice deve rilasciare all’assicurato o che è obbligatorio tenere a bordo ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '817', '33014', '6723', '1', 'Dopo la firma del contratto di assicurazione R.C.A. (responsabilità civile auto), l''impresa assicuratrice deve rilasciare al cliente il certificato di assicurazione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '817', '33014', '6724', '2', 'Il certificato di assicurazione obbligatoria deve essere sempre tenuto a bordo del veicolo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '817', '33014', '6725', '3', 'Dopo la firma del contratto di assicurazione R.C.A. (responsabilità civile auto), l''impresa assicuratrice deve rilasciare al cliente un elenco delle autocarrozzerie convenzionate, da contattare in caso d''incidente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '817', '33014', '6726', '4', 'Dopo la firma del contratto di assicurazione R.C.A. (responsabilità civile auto), l''impresa assicuratrice deve rilasciare al cliente un libretto sulle nozioni di pronto soccorso', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '817', '33014', '6727', '5', 'Dopo la firma del contratto di assicurazione R.C.A. (responsabilità civile auto), l''impresa assicuratrice deve rilasciare al cliente un manuale su come evitare incidenti', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '818', '33009', '', '', 'Fondo di garanzia per le vittime della strada ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '818', '33009', '6728', '1', 'Il fondo di garanzia per le vittime della stradaprevede il risarcimento dei danni arrecati alle persone da veicoli non coperti da assicurazione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '818', '33009', '6729', '2', 'Il fondo di garanzia per le vittime della stradaprevede il risarcimento dei danni arrecati alle persone da veicoli non identificati', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '818', '33009', '6730', '3', 'Il fondo di garanzia per le vittime della stradaè un premio per chi non ha provocato incidenti', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '818', '33009', '6731', '4', 'Il fondo di garanzia per le vittime della stradaè una convenzione facoltativa', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '818', '33009', '6732', '5', 'Il fondo di garanzia per le vittime della stradagarantisce un vitalizio al conducente infortunato', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '818', '33009', '6733', '6', 'Il fondo di garanzia per le vittime della stradarisarcisce i danni arrecati dai conducenti minorenni', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '819', '33010', '', '', 'Polizze assicurative facoltative ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '819', '33010', '6734', '1', 'Le polizze assicurative stipulate di solito per coprire i danni da furto, incendio, atti vandalici, eventi naturali o danni al proprio veicolo (polizza kasko) non sono obbligatorie', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '819', '33010', '6735', '2', 'Nella polizza assicurativa contro il rischio di furto o incendio, il risarcimento dovuto non può essere superiore al valore che il veicolo aveva al momento in cui è stato rubato o ha preso fuoco', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '819', '33010', '6736', '3', 'Nel caso in cui il veicolo venga rubato in uno Stato estero, per poter attivare l''assicurazione bisogna ripetere la denuncia nello Stato italiano', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '819', '33010', '6737', '4', 'La polizza assicurativa contro il rischio di furto o incendio garantisce una rendita vitalizia al proprietario del veicolo, nel caso in cui venga rubato o prenda fuoco', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '819', '33010', '6738', '5', 'Per poter stipulare una polizza assicurativa contro il rischio d''incendio occorre che il veicolo sia munito di apposito meccanismo staccabatteria automatico', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '819', '33010', '6739', '6', 'Per poter stipulare una polizza assicurativa contro il rischio di furto, occorre che il veicolo sia dotato di antifurto', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '819', '33010', '6740', '7', 'Le polizze assicurative contro il rischio d''incendio del veicolo coprono, normalmente, solo i danni causati da incendi dolosi (intenzionali) e non quelli provocati da incendi accidentali (casuali)', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '820', '33015', '', '', 'Percezione delle proprie capacità di guida e rispetto delle norme ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '820', '33015', '6741', '1', 'Un conducente deve considerare che non sempre è in grado di valutare correttamente le proprie capacità di guida', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '820', '33015', '6742', '2', 'Se non si è certi di attraversare in tutta sicurezza un incrocio, è necessario arrestarsi ed aspettare che transitino tutti i veicoli con cui vi può essere un pericolo di collisione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '820', '33015', '6743', '3', 'Il conducente non deve mai sopravvalutare la propria abilità di guida', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '820', '33015', '6744', '4', 'Le norme di circolazione sono stabilite per garantire una maggior sicurezza di guida per tutti gli utenti della strada', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '820', '33015', '6745', '5', 'Il rispetto delle norme di circolazione permette di limitare i rischi connessi alla guida di tutti i veicoli, sia di quelli a motore, sia di quelli che ne sono privi', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '820', '33015', '6746', '6', 'Il rispetto delle norme permette di limitare l''intralcio alla circolazione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '820', '33015', '6747', '7', 'A volte ci si espone a situazioni più rischiose di quelle che si è in grado di gestire', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '820', '33015', '6748', '8', 'Durante la guida, la percezione di un pericolo e la conseguente reazione del conducente non sono influenzate dalle condizioni ambientali', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '820', '33015', '6749', '9', 'Il conducente che intende passare per primo in un incrocio deve sopraggiungervi più velocemente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '820', '33015', '6750', '10', 'Un conducente deve sempre sottostimare le situazioni di pericolo per non farsi travolgere dall''ansia durante la guida', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '820', '33015', '6751', '11', 'Il conducente deve accogliere gli inviti dei passeggeri a guidare più velocemente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '820', '33015', '6752', '12', 'Lo specchio retrovisore posto sul lato destro del veicolo deve essere utilizzato solo da conducenti esperti', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '820', '33015', '6753', '13', 'Il rispetto delle norme di circolazione è importante solo se la loro violazione comporta una sanzione economica elevata', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '820', '33015', '6754', '14', 'Il rispetto delle norme di circolazione è importante per gli utenti deboli, ma non per i conducenti degli autoveicoli', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '820', '33015', '6755', '15', 'Il rispetto delle norme di circolazione è importante quando il traffico è molto intenso, ma non quando si è in condizioni di circolazione scorrevole', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '820', '33015', '6756', '16', 'Il divieto di sorpasso può non essere osservato se si è in coda dietro a un veicolo lento da più di 10 minuti e si ritiene di poter sorpassare senza pericoli', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '820', '33015', '6757', '17', 'Il rispetto della posizione sulla carreggiata è una norma secondaria che può, se del caso, non essere osservata', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '821', '33015', '', '', 'Attenzione durante la guida ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '821', '33015', '6758', '1', 'Distrarsi alla guida può causare incidenti stradali che possono comportare gravi conseguenze', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '821', '33015', '6759', '2', 'Quando si attraversa un incrocio è necessario mantenere la massima concentrazione alla guida', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '821', '33015', '6760', '3', 'È pericoloso, durante la guida, distogliere lo sguardo dalla strada, anche per pochi millisecondi', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '821', '33015', '6761', '4', 'Per prevenire il rischio di incidenti, il conducente dovrà valutare attentamente il comportamento degli altri utenti della strada', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '821', '33015', '6762', '5', 'Durante la guida, la percezione di un pericolo e la conseguente reazione del conducente sono influenzate dalle condizioni ambientali', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '821', '33015', '6763', '6', 'La distrazione durante la guida è una delle principali cause di incidenti stradali', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '821', '33015', '6764', '7', 'Si può allenarel''attenzione alla guida', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '821', '33015', '6765', '8', 'Il conducente può distogliere lo sguardo dalla strada quando ritiene che non ci sia pericolo imminente di incidente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '821', '33015', '6766', '9', 'Il comportamento degli altri utenti della strada è ininfluente per una guida sicura', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '821', '33015', '6767', '10', 'Il conducente può non tenere conto del comportamento degli altri utenti della strada, se questi ultimi non rispettano le regole del codice della strada', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '821', '33015', '6768', '11', 'Il conducente deve concentrarsi prevalentemente sul tratto di strada non superiore a tre metri antistante il suo veicolo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '821', '33015', '6769', '12', 'L''attenzione alla guida è importante solo quando si compiono manovre pericolose quali, ad esempio, un sorpasso', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '822', '33015', '', '', 'Motivi che influenzano l''attenzione durante la guida', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '822', '33015', '6770', '1', 'L''ansia può ridurre la capacità di prestare attenzione ai pericoli', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '822', '33015', '6771', '2', 'Quando si è alla guida, la capacità di attenzione può essere influenzata anche dall''ascolto di musica a volume molto alto', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '822', '33015', '6772', '3', 'Parlare animosamente con altre persone a bordo del veicolo può influenzare negativamente il comportamento di guida', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '822', '33015', '6773', '4', 'La presenza di passeggeri che schiamazzano a bordo del veicolo può influenzare la concentrazione del conducente', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '822', '33015', '6774', '5', 'Durante la guida il conducente deve prestare attenzione, sia a gli utenti che si trovano davanti al proprio veicolo, sia a coloro che si trovano ai bordi della strada', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '822', '33015', '6775', '6', 'Concentrare la propria attenzione sulle indicazioni del navigatore satellitare può rappresentare un elemento di distrazione alla guida', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '822', '33015', '6776', '7', 'Utilizzare il cellulare alla guida è pericoloso perché riduce l''attenzione alla strada', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '822', '33015', '6777', '8', 'Utilizzare il cellulare alla guida, anche se con impianto viva-voce, può ridurre l''attenzione alla strada', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '822', '33015', '6778', '9', 'L''attenzione alla guida diminuisce quando si è affaticati', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '822', '33015', '6779', '10', 'Impostare il navigatore satellitare mentre si guida riduce l''attenzione alla strada', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '822', '33015', '6780', '11', 'I nostri sensi non riescono a cogliere tutti gli stimoli presenti nell''ambiente', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '822', '33015', '6781', '12', 'Le emozioni influenzano la nostra attenzione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '822', '33015', '6782', '13', 'In prossimità di un incrocio un conducente può arrestarsi e, mentre attende il passaggio di altri veicoli, può inviare un rapido messaggio con il cellulare', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '822', '33015', '6783', '14', 'Durante la guida, qualora il conducene ritenga non vi sia pericolo imminente, può comporre un numero di telefono con il cellulare, senza fermarsi', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '822', '33015', '6784', '15', 'Mentre il veicolo è in marcia, il conducente può programmare il navigatore in qualsiasi', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '822', '33015', '6785', '16', 'Gli specchi retrovisori devono essere regolati mentre il veicolo è in movimento', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '822', '33015', '6786', '17', 'Bere un liquore ad alta gradazione alcolica permette di migliorare l''attenzione dopo circa 10 minuti dall''ingestione', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '822', '33015', '6787', '18', 'Sintonizzare manualmente l''autoradio non comporta perdita di attenzione alla strada, perché è un compito che si è abituati a svolgere', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '822', '33015', '6788', '19', 'Se si è stato di ebbrezza lieve, si riesce a guidare comunque in sicurezza', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '822', '33015', '6789', '20', 'La quantità di attenzione che si può prestare alla strada mentre si guida è illimitata', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '822', '33015', '6790', '21', 'Con 0,5 grammi per litro di alcool nel sangue si riesce a concentrarsi meglio', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '822', '33015', '6791', '22', 'I nostri sensi riescono a cogliere tutti gli stimoli presenti nell''ambiente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '822', '33015', '6792', '23', 'I nostri sensi sono in grado di percepire stimoli di qualunque intensità', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '822', '33015', '6793', '24', 'In condizioni di traffico intenso lo stress può aiutare il conducente a mantenersi vigile', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '822', '33015', '6794', '25', 'Durante la guida è consentito utilizzare il cellulare per comporre sms', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '822', '33015', '6795', '26', 'I sistemi di navigazione satellitare presenti su alcuni veicoli devono essere programmati mentre il veicolo è in movimento', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('23', '822', '33015', '6796', '27', 'L''udito non è un senso importante quando si guida', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '823', '35027', '', '', 'Impianto di scarico ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '823', '35027', '6797', '1', 'L''impianto di scarico ha lo scopo di convogliarei gas di scarico verso l''esterno dopo averne abbassato la tossicità', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '823', '35027', '6798', '2', 'L''impianto di scarico ha lo scopo di convogliare i gas di scarico verso l''esterno dopo averne abbassato la pressione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '823', '35027', '6799', '3', 'L''impianto di scarico ha lo scopo di convogliare i gas di scarico verso l''esterno dopo averne abbassato la temperatura', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '823', '35027', '6800', '4', 'L''impianto di scarico ha lo scopo di ridurre la rumorosità del veicolo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '823', '35027', '6801', '5', 'L''impianto di scarico ha lo scopo di ridurre l''inquinamento atmosferico causato dai gas di scarico del veicolo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '823', '35027', '6802', '6', 'La marmitta deteriorata aumenta l''inquinamento acustico prodotto dai veicoli a motore', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '823', '35027', '6803', '7', 'La marmitta deteriorata deve essere sostituita con altra di tipo approvato per quel tipo di veicolo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '823', '35027', '6804', '8', 'L''impianto di scarico deve essere integralmente sostituito a ogni cambio di lubrificante', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '823', '35027', '6805', '9', 'L''impianto di scarico dei veicoli elettrici necessita di manutenzione più frequente rispetto a quello dei veicoli con motore a scoppio', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '823', '35027', '6806', '10', 'Fuori dei centri abitati, i veicoli possono circolare anche senza silenziatore', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '823', '35027', '6807', '11', 'Se il tubo di scarico del silenziatore si fora e non si intende sostituirlo a breve, lo si può riparare avvolgendolo con lana di vetro e nastro isolante', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '823', '35027', '6808', '12', 'La sostituzione del tubo di scarico può essere fatta anche personalmente, senza rivolgersi ad un''officina autorizzata', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '823', '35027', '6809', '13', 'È possibile modificare l''impianto di scarico, purché si autocertifichi che ciò non comporta aumenti del consumo di carburante', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '823', '35027', '6810', '14', 'La manomissione dell''impianto di scarico non comporta alcuna sanzione, purché si circoli solo nei centri urbani', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '824', '34001', '', '', 'Riduzione dei consumi e dell’inquinamento ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '824', '34001', '6811', '1', 'Gli apparecchi e i dispositivi che funzionano a corrente elettrica (radio, luci, lunotto termico, ecc.) fanno aumentare i consumi di carburante del veicolo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '824', '34001', '6812', '2', 'Mantenendo il più possibile costante la velocità, si riduce il consumo di carburante', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '824', '34001', '6813', '3', 'Il consumo di carburante aumenta con l''aumentare del peso del veicolo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '824', '34001', '6814', '4', 'Diminuendo opportunamente la velocità diminuisce il consumo di carburante', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '824', '34001', '6815', '5', 'Per consumare meno carburante è opportuno tenere i finestrini chiusi, specie quando si viaggia ad alta velocità', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '824', '34001', '6816', '6', 'Per consumare meno carburante conviene scegliere, per quanto possibile, strade pianeggianti e più scorrevoli', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '824', '34001', '6817', '7', 'L''insufficiente pressione di gonfiaggio degli pneumatici comporta un maggior consumo di carburante', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '824', '34001', '6818', '8', 'Collocare i bagagli sul tetto del veicolo comporta un maggior consumo di carburante', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '824', '34001', '6819', '9', 'Tenere aperto il tettuccio scorrevole del veicolo comporta un minor consumo di carburante', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '824', '34001', '6820', '10', 'L''uso del climatizzatore comporta minor consumo di carburante', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '824', '34001', '6821', '11', 'I veicoli ibridi (con due motori, a scoppio ed elettrico) inquinano di più perché azionati da due motori', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '824', '34001', '6822', '12', 'Per consumare meno carburante è opportuno collocare i bagagli sul tetto del veicolo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '824', '34001', '6823', '13', 'Per consumare meno carburante conviene accelerare e frenare energicamente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '825', '34011', '', '', 'Riduzione dei consumi e dell’inquinamento ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '825', '34011', '6824', '1', 'Per risparmiare carburante e salvaguardare l''ambiente dall''inquinamento atmosferico, è consigliabile azionare il climatizzatore il meno possibile e soltanto quando serve effettivamente', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '825', '34011', '6825', '2', 'Per salvaguardare l''ambiente dall''inquinamento atmosferico, bisogna premere più volte il pedale dell''acceleratore quando il veicolo è freddo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '825', '34011', '6826', '3', 'Per salvaguardare l''ambiente dall''inquinamento atmosferico, bisogna premere diverse volte e a fondo il pedale dell''acceleratore, affinché il veicolo raggiunga in fretta la temperatura di esercizio ', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '825', '34011', '6827', '4', 'Per salvaguardare l''ambiente dall''inquinamento atmosferico e acustico, quando si cambia marcia bisogna premere a fondo il pedale dell''acceleratore', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '826', '34012', '', '', 'Riduzione dei consumi e dell’inquinamento ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '826', '34012', '6828', '1', 'Per limitare i consumi di carburante, conviene chiudere i finestrini se si viaggia a velocità medio alte, perché le turbolenze all''interno dell''abitacolo possono frenare la marcia del veicolo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '826', '34012', '6829', '2', 'Per limitare i consumi di carburante non conviene fare uso del portapacchi, ma sistemare i bagagli all''interno del veicolo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '826', '34012', '6830', '3', 'Viaggiare con uno spesso strato di neve sul tetto del veicolo, comporta un aumento dei consumi di carburante', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '826', '34012', '6831', '4', 'Viaggiare a forte velocità con il tettuccio del veicolo aperto comporta un aumento dei consumi di carburante', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '826', '34012', '6832', '5', 'In caso di arresto prolungato in galleria, per la formazione di una fila di veicoli, è dannoso per la salute azionare il comando del ricircolo dell''aria all''interno del veicolo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '826', '34012', '6833', '6', 'I veicoli che rispettano la normativa antinquinamento Euro 4emettono una quantità di gas inquinanti maggiore di quelli omologati secondo la normativa Euro 0', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '826', '34012', '6834', '7', 'Trainare un rimorchio non comporta un aumento dei consumi di carburante', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '827', '34006', '', '', 'Riduzione dell’inquinamento atmosferico provocato dai veicoli ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '827', '34006', '6835', '1', 'Per diminuire l''inquinamento provocato dai veicoli non bisogna accelerare bruscamente durante le partenze', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '827', '34006', '6836', '2', 'Per diminuire l''inquinamento, bisogna evitare di tenere inserita per troppo tempo una marcia bassa, se non è necessaria', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '827', '34006', '6837', '3', 'Per diminuire l''inquinamento dell''aria provocato dai veicoli con motore a scoppio, bisogna sostituire la marmitta catalitica quando la stessa è deteriorata o esausta', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '827', '34006', '6838', '4', 'Per diminuire l''inquinamento dell''aria provocato dai veicoli, bisogna controllare spesso la frizione e il differenziale', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '827', '34006', '6839', '5', 'Per diminuire l''inquinamento dell''aria provocato dai veicoli, bisogna controllare spesso gli organi di sospensione (ammortizzatori)', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '827', '34006', '6840', '6', 'Per diminuire l''inquinamento dell''aria provocato dai veicoli, bisogna ridurre la pressione di gonfiaggio dei pneumatici', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '827', '34006', '6841', '7', 'Per diminuire l''inquinamento dell''aria provocato dai veicoli, bisogna accelerare ripetutamente da fermo, per tenere caldo il motore e migliorarne il rendimento', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '827', '34006', '6842', '8', 'Nei veicoli dotati di marmitta catalitica, l''inquinamento dell''aria è minore subito dopo l''accensione del motore, quando la temperatura è ancora bassa', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '828', '34007', '', '', 'Inquinamento atmosferico provocato dai veicoli ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '828', '34007', '6843', '1', 'L''inquinamento dell''aria prodotto dai veicoli può dipendere da un funzionamento irregolare del motore', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '828', '34007', '6844', '2', 'L''inquinamento dell''aria può essere ridotto, controllando che il veicolo sia sempre nelle condizioni ottimali di consumo e rendimento', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '828', '34007', '6845', '3', 'Adottando uno stile di guida che limiti i consumi di carburante, è possibile ridurre l''inquinamento dell''aria', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '828', '34007', '6846', '4', 'L''inquinamento atmosferico prodotto dai veicoli può essere ridotto, controllando che il filtro dell''aria non sia sporco o intasato', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '828', '34007', '6847', '5', 'L''inquinamento atmosferico prodotto dai veicoli scompare del tutto accelerando a fondo, perché l''alta temperatura fa consumare in fretta il carburante', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '828', '34007', '6848', '6', 'L''inquinamento dell''aria prodotto dai veicoli diminuisce se si lascia acceso il climatizzatore', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '828', '34007', '6849', '7', 'L''inquinamento atmosferico è nullo nei veicoli che rispettano la normativa antinquinamentoEuro 0', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '829', '34009', '', '', 'Riduzione dell’inquinamento atmosferico provocato dai veicoli ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '829', '34009', '6850', '1', 'L''inquinamento dell''aria prodotto dai veicoli a motore può diminuire se si alleggerisce il peso del veicolo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '829', '34009', '6851', '2', 'L''inquinamento dell''aria prodotto dai veicoli a motore può essere ridotto, scegliendo veicoli con alimentazione elettrica, a metano o GPL', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '829', '34009', '6852', '3', 'L''inquinamento dell''aria prodotto dai veicoli a motore può diminuire se si spegne il motore, in caso di arresto prolungato', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '829', '34009', '6853', '4', 'L''inquinamento dell''aria prodotto dai veicoli a motore può essere ridotto, evitando di usare il veicolo per brevi percorsi', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '829', '34009', '6854', '5', 'Guidando un veicolo che emette troppi gas di scarico inquinanti si può essere puniti con una sanzione amministrativa e il ritiro della carta di circolazione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '829', '34009', '6855', '6', 'L''inquinamento dell''aria prodotto dai veicoli a motore può diminuire se si sistemano i bagagli sul tetto dell''autovettura', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '829', '34009', '6856', '7', 'L''inquinamento dell''aria prodotto dai veicoli a motore può diminuire se si circola sempre con marce basse', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '829', '34009', '6857', '8', 'L''inquinamento dell''aria prodotto dai veicoli a motore può diminuire se si accelera tenendo premuto il pedale della frizione', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '829', '34009', '6858', '9', 'L''inquinamento dell''aria prodotto dai veicoli a motore può diminuire se si accelera con il cambio in folle', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '830', '34010', '', '', 'Riduzione dell’inquinamento atmosferico provocato dai veicoli ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '830', '34010', '6859', '1', 'Per diminuire l''inquinamento atmosferico provocato dai veicoli, bisogna far sostituire periodicamente il filtro dell''aria', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '830', '34010', '6860', '2', 'Per diminuire l''inquinamento dell''aria provocato dai veicoli con motore diesel, bisogna far controllare periodicamente il sistema di alimentazione (pompa di iniezione ed iniettori)', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '830', '34010', '6861', '3', 'Per diminuire l''inquinamento dell''aria provocato dai veicoli, bisogna far controllare il motore, se il fumo dello scarico risulta troppo scuro', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '830', '34010', '6862', '4', 'Per diminuire l''inquinamento dell''aria non bisogna accelerare e frenare spesso durante la marcia del veicolo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '830', '34010', '6863', '5', 'Per diminuire l''inquinamento dell''aria provocato dai veicoli, bisogna dare un breve colpo di acceleratore, prima di abbassare la frizione per cambiare marcia', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '830', '34010', '6864', '6', 'Per diminuire l''inquinamento atmosferico provocato dai veicoli, bisogna utilizzare il motore al massimo numero di giri', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '830', '34010', '6865', '7', 'Per diminuire l''inquinamento dell''aria provocato dai gas di scarico del veicolo, bisogna sostituire periodicamente il filtro antipolline', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '830', '34010', '6866', '8', 'Per diminuire l''inquinamento dell''aria provocato dai veicoli, bisogna sostituire il filtro dell''olio ad ogni cambio di stagione', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '831', '34008', '', '', 'Inquinamento atmosferico prodotto dai veicoli ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '831', '34008', '6867', '1', 'L''inquinamento dell''aria prodotto dai veicoli aumenta se gli iniettori sono difettosi', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '831', '34008', '6868', '2', 'Una maggiore emissione di fumo dallo scarico è sintomo di maggior inquinamento causato dal motore', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '831', '34008', '6869', '3', 'L''inquinamento dell''aria prodotto dai veicoli è maggiore se il fumo di scarico presenta colore scuro', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '831', '34008', '6870', '4', 'L''inquinamento dell''aria prodotto dai veicoli può causare danni alle vie respiratorie e irritazione agli occhi', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '831', '34008', '6871', '5', 'L''inquinamento dell''aria prodotto dai veicoli fa aumentare lo smog, le piogge acide e l''effetto serra', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '831', '34008', '6872', '6', 'L''inquinamento dell''aria prodotto dai veicoli è dovuto per la maggior parte ai gas di scarico, generati dalla combustione all''interno del motore', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '831', '34008', '6873', '7', 'L''inquinamento atmosferico prodotto dai veicoli dipende principalmente dall''umidità presente nell''aria', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '832', '34002', '', '', 'Inquinamento acustico provocato dai veicoli ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '832', '34002', '6874', '1', 'Per evitare rumori fastidiosi durante la guida, occorre far controllare che il dispositivo silenziatore (marmitta) sia in buone condizioni di efficienza', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '832', '34002', '6875', '2', 'L''inquinamento acustico aumenta se si usano in modo eccessivo i dispositivi di segnalazione acustica (clacson, trombe)', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '832', '34002', '6876', '3', 'Per evitare rumori fastidiosi durante la guida non si deve frenare bruscamente, se non è strettamente necessario', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '832', '34002', '6877', '4', 'Per evitare rumori fastidiosi non bisogna accelerare inutilmente e ripetutamente se il veicolo è fermo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '832', '34002', '6878', '5', 'Per evitare rumori fastidiosi durante la guida, si deve usare il clacson solamente per motivi di sicurezza stradale (trasporto feriti o ammalati, pericolo immediato)', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '832', '34002', '6879', '6', 'Per evitare rumori fastidiosi durante le fermate dovute al traffico, occorre mantenere il motore acceso, accelerando ripetutamente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '832', '34002', '6880', '7', 'Per evitare rumori fastidiosi, occorre chiudere con forza le portiere dell''autovettura, in modo da non doverle richiudere', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '832', '34002', '6881', '8', 'Per evitare rumori fastidiosi durante la guida, occorre sostituire il clacson con le luci ad intermittenza, ma solamente di notte', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '833', '34003', '', '', 'Inquinamento acustico provocato dai veicoli ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '833', '34003', '6882', '1', 'Il rumore prodotto dai veicoli a motore aumenta man mano che si va rovinando il silenziatore (marmitta)', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '833', '34003', '6883', '2', 'Il rumore prodotto dai veicoli a motore si può diminuire con una guida corretta, che eviti brusche frenate o forti accelerazioni', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '833', '34003', '6884', '3', 'Il rumore prodotto dai veicoli a motore è causato solo dal motore', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '834', '34004', '', '', 'Riduzione dell’inquinamento acustico provocato dai veicoli ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '834', '34004', '6885', '1', 'Per diminuire il rumore emesso dai veicoli a motore è necessario che il dispositivo silenziatore (marmitta) sia efficiente e di tipo approvato', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '834', '34004', '6886', '2', 'Per diminuire il rumore è necessario far sostituire la marmitta rovinata con una approvata per quel tipo di veicolo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '834', '34004', '6887', '3', 'Per diminuire il rumore non si deve accelerare quando il veicolo è fermo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '834', '34004', '6888', '4', 'Per diminuire il rumore prodotto dai veicoli a motore è necessario far controllare i freni se emettono rumore durante le frenate, sostituendoli se necessario', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '834', '34004', '6889', '5', 'Il controllo dei freni che producono rumore, oltre ad aumentare la sicurezza può ridurre la rumorosità del veicolo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '834', '34004', '6890', '6', 'Per diminuire il rumore emesso dai veicoli a motore si deve suonare il clacson il meno possibile', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '834', '34004', '6891', '7', 'Per diminuire il rumore emesso dai veicoli a motore è opportuno praticare dei fori nel dispositivo silenziatore (marmitta)', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '834', '34004', '6892', '8', 'Per diminuire il rumore emesso dai veicoli a motore si deve sostituire il tubo di scarico, usandone uno di diametro più piccolo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '834', '34004', '6893', '9', 'Per diminuire il rumore emesso dai veicoli a motore basta eliminare la parte finale della marmitta', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '835', '34013', '', '', 'Modifiche a motore e marmitta ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '835', '34013', '6894', '1', 'Guidando un veicolo a cui sono stati modificati il motore e la marmitta, si può incorrere in pesanti sanzioni amministrative', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '835', '34013', '6895', '2', 'È consentito modificare liberamente il motore e la marmitta di un veicolo per diminuire l''inquinamento atmosferico', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '836', '34005', '', '', 'Inquinamento acustico provocato dai veicoli ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '836', '34005', '6896', '1', 'Nelle curve percorse a forte velocità, lo strisciamento dei pneumatici aumenta la rumorosità del veicolo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '836', '34005', '6897', '2', 'I rimorchi vuoti, che sobbalzano eccessivamente, possono essere causa di rumore', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '837', '34014', '', '', 'Smaltimento dei rifiuti prodotti dal veicolo e rottamazione ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '837', '34014', '6898', '1', 'È vietato scaricare nella fognatura il liquido antigelo del circuito di raffreddamento del veicolo, poiché potrebbe non essere biodegradabile', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '837', '34014', '6899', '2', 'L''abbandono di una batteria per auto, sulla strada o sul terreno, può arrecare gravi danni all''ambiente e alle falde acquifere sottostanti', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '837', '34014', '6900', '3', 'Se si acquista una batteria per auto si può consegnare quella esausta (usata) al rivenditore, che provvederà a ritirarla, senza ulteriori costi aggiuntivi', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '837', '34014', '6901', '4', 'Il proprietario di un veicolo che intende demolirlo o rottamarlo, deve consegnarlo ad un centro di raccolta per la messa in sicurezza (autodemolitore autorizzato)', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '837', '34014', '6902', '5', 'Al momento dello smaltimento dell''olio del motore, bisogna evitare che venga a contatto con la pelle per un tempo prolungato', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '837', '34014', '6903', '6', 'Lo scarico dei residui organici e delle acque sporche delle autocaravan deve avvenire negli appositi impianti di smaltimento igienico-sanitari', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '837', '34014', '6904', '7', 'L''abbandono sul terreno di rifiuti pericolosi o tossici (olii, liquidi, residui di riparazioni o di sostituzioni) non viene sanzionato', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('24', '837', '34014', '6905', '8', 'Per demolire o rottamare un veicolo, lo si deve consegnare esclusivamente al concessionario della casa costruttrice', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '838', '35020', '', '', 'Impianti di frenatura ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '838', '35020', '6906', '1', 'Gli impianti di frenatura dei veicoli leggeri sono spesso dotati di servofreno', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '838', '35020', '6907', '2', 'Negli impianti di frenatura, l''energia cinetica dell''autoveicolo viene trasformata in calore grazie all''attrito', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '838', '35020', '6908', '3', 'Le parti dell''impianto frenante maggiormente sollecitate dalle alte temperature sono le guarnizioni di attrito', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '838', '35020', '6909', '4', 'Il freno di stazionamento deve essere utilizzato per bloccare il veicolo in sosta o per situazioni di emergenza', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '838', '35020', '6910', '5', 'Il livello del liquido idraulico dei freni deve essere periodicamente controllato', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '838', '35020', '6911', '6', 'Il freno di stazionamento deve essere in grado di tenere bloccato il veicolo in sosta anche su strade in pendenza', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '838', '35020', '6912', '7', 'Il servofreno a depressione non può funzionare a motore spento', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '838', '35020', '6913', '8', 'Il freno di stazionamento non deve essere utilizzato per la frenatura di servizio', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '838', '35020', '6914', '9', 'Il circuito frenante dei veicoli leggeri è di tipo a circuito sigillato, pertanto non è necessario un periodico controllo del livello del liquido idraulico', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '838', '35020', '6915', '10', 'Il freno di stazionamento deve essere utilizzato per la frenatura di servizio quando si circola su strade a forte pendenza, in modo da ridurre il surriscaldamentodegli elementi frenanti', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '838', '35020', '6916', '11', 'Il servofreno a depressione può funzionare anche a motore spento, purché sia inserita la chiave di accensione nel quadro', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '839', '35021', '', '', 'Frenatura poco efficiente o squilibrata ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '839', '35021', '6917', '1', 'Una frenatura poco efficiente o squilibrata può essere causata dalla presenza d''aria o vapore nel circuito frenante idraulico', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '839', '35021', '6918', '2', 'Una frenatura poco efficiente o squilibrata può essere causata dal consumo eccessivo delle guarnizioni frenanti', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '839', '35021', '6919', '3', 'Una frenatura poco efficiente o squilibrata può essere causata dal consumo irregolare dei tamburi o dei dischi', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '839', '35021', '6920', '4', 'Una frenatura poco efficiente o squilibrata può essere causata dalla differente pressione di gonfiaggio dei pneumatici dello stesso asse', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '839', '35021', '6921', '5', 'Una frenatura poco efficiente può essere causata dall''eccessivo riscaldamento delle guarnizioni frenanti', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '839', '35021', '6922', '6', 'Una frenatura poco efficiente o squilibrata può essere causata dal carico eccessivo o mal ripartito', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '839', '35021', '6923', '7', 'Una frenatura poco efficiente può essere causata da eccessive e ripetute frenate', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '839', '35021', '6924', '8', 'Con freni squilibrati può avvenire che, in frenata, si blocchino soltanto le ruote di un lato', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '839', '35021', '6925', '9', 'Con freni squilibrati può avvenire che, in frenata, il veicolo sbandi, dirigendosi verso il lato della ruota che si blocca per prima', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '839', '35021', '6926', '10', 'Con freni squilibrati si manifesta, in genere, un''usura irregolare e non omogenea dei battistrada', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '839', '35021', '6927', '11', 'Una frenatura poco efficiente o squilibrata può essere causata dal consumo eccessivo dei meccanismi del freno di stazionamento', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '839', '35021', '6928', '12', 'Una frenatura poco efficiente o squilibrata può essere causata dall''insufficiente lubrificazione dei tamburi o dei dischi', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '839', '35021', '6929', '13', 'Una frenatura poco efficiente o squilibrata può essere causata dall''eccessivo gioco del pedale della frizione', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '839', '35021', '6930', '14', 'Una frenatura poco efficiente o squilibrata può essere causata dalla presenza del servosterzo su veicoli di piccola cilindrata', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '839', '35021', '6931', '15', 'Una frenatura poco efficiente o squilibrata può essere causata dalla presenza di freni a disco sull''asse posteriore', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '839', '35021', '6932', '16', 'Una frenatura poco efficiente o squilibrata può essere causata da pneumatici invernali montati su tutte e quattro le ruote', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '839', '35021', '6933', '17', 'Una frenatura poco efficiente o squilibrata può essere causata da pneumatici invernali utilizzati su strade non innevate', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '839', '35021', '6934', '18', 'Una frenatura poco efficiente o squilibrata può essere causata dall''utilizzo di veicoli con alimentazione a metano su strade di montagna', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '839', '35021', '6935', '19', 'Con freni squilibrati si manifesta in genere un surriscaldamento del liquidorefrigerante del motore', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '840', '35010', '', '', 'Sistema frenante A.B.S. ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '840', '35010', '6936', '1', 'L''A.B.S. Impedisce alle ruote di bloccarsi', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '840', '35010', '6937', '2', 'L''A.B.S. consente di correggere la traiettoria del veicolo anche durante una frenata di emergenza', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '840', '35010', '6938', '3', 'L''A.B.S. consente di utilizzare la massima forza aderente, in frenata', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '840', '35010', '6939', '4', 'L''A.B.S. è in grado di agiresu tutte le ruote, intervenendo di volta in volta su quelle che stanno per bloccarsi durante la frenata', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '840', '35010', '6940', '5', 'L''A.B.S. agisce anche quando gli pneumatici sono usurati', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '840', '35010', '6941', '6', 'L''A.B.S. agisce anche quando l''asfalto è bagnato', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '840', '35010', '6942', '7', 'L''A.B.S. rende la frenata più sicura perché impedisce alle ruote di bloccarsi', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '840', '35010', '6943', '8', 'L''A.B.S. favorisce il bloccaggio delle ruote', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '840', '35010', '6944', '9', 'L''A.B.S. impedisce, durante la frenata, di modificare la traiettoria del veicolo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '840', '35010', '6945', '10', 'L''A.B.S. impedisce di utilizzare la massima forza aderente, in frenata', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '840', '35010', '6946', '11', 'L''A.B.S. non agisce sulle ruote motrici', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '840', '35010', '6947', '12', 'L''A.B.S. non si attiva se gli pneumatici sono usurati', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '841', '35015', '', '', 'Cautele da adottare nell’uso dei freni ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '841', '35015', '6948', '1', 'L''azione frenante esercitata dal motore è massima se si inserisce la prima marcia del cambio di velocità', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '841', '35015', '6949', '2', 'In caso di frenata di emergenza, con entrata in azione dell''ABS, bisogna continuare a premere con forza il pedale, senza allentare la pressione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '841', '35015', '6950', '3', 'L''uso prolungato e ripetuto dei freni, provocandone il surriscaldamento, comporta la diminuzione dell''efficienza frenante', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '841', '35015', '6951', '4', 'Percorrendo lunghe e ripide discese è necessario inserire una marcia bassa per evitare di surriscaldare i freni', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '841', '35015', '6952', '5', 'Dopo il lavaggio del veicolo, è opportuno eseguire una prova di frenata', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '841', '35015', '6953', '6', 'In caso di frenata di emergenza, con entrata in azione dell''ABS, bisogna immediatamente diminuire la pressione esercitata sul pedale del freno', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '841', '35015', '6954', '7', 'Tutti i veicoli sono dotati del freno di stazionamento che si aziona con comando manuale', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '841', '35015', '6955', '8', 'Il freno di stazionamento si utilizza durante la marcia, per frenare o arrestare il veicolo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '841', '35015', '6956', '9', 'Percorrendo lunghe e ripide discese è opportuno marciare con il cambio in folle per risparmiare carburante e freni', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '841', '35015', '6957', '10', 'In caso di frenata di emergenza, il conducente deve inserire rapidamente la retromarcia', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '841', '35015', '6958', '11', 'Su discese molto ripide è bene viaggiare con il freno a mano inserito parzialmente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '841', '35015', '6959', '12', 'Il cambio delle pastiglie dei freni è un''operazione di manutenzione ordinaria che può essere svolta da qualunque conducente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '842', '35012', '', '', 'Controllo, manutenzione e sostituzione degli pneumatici ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '842', '35012', '6960', '1', 'Lo spessore minimo del battistrada degli pneumatici di un autoveicolo deve essere di 1,6 millimetri', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '842', '35012', '6961', '2', 'È consigliabile sostituire gli pneumatici dopo alcuni anni, anche se non sono consumati', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '842', '35012', '6962', '3', 'Lo spessore minimo del battistrada degli pneumatici di un motociclo deve essere di 1,0 millimetri', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '842', '35012', '6963', '4', 'Se in uno pneumatico del tipo tubelesssi è conficcato un chiodo, è possibile, con le necessarie cautele, continuare a guidare, fermandosi quanto prima per riparare il danno', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '842', '35012', '6964', '5', 'Gli pneumatici tubeless, in caso di foratura, si sgonfiano lentamente', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '842', '35012', '6965', '6', 'Gli pneumatici tubelesspossono essere riparati solo con apposite bombolette contenenti uno speciale mastice, che chiudono il foro dall''interno', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '842', '35012', '6966', '7', 'Gli pneumatici con camera d''aria, in caso di foratura, si sgonfiano lentamente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '842', '35012', '6967', '8', 'Se in uno pneumatico del tipo tubelesssi è conficcato un chiodo, bisogna estrarlo subito, per evitare che il pneumatico possa esplodere', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '842', '35012', '6968', '9', 'Gli pneumatici gonfiati con l''azoto devono essere tenuti a minore pressione rispetto a quelli con aria compressa', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '843', '35019', '', '', 'Scelta e manutenzione degli pneumatici ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '843', '35019', '6969', '1', 'Per una maggiore sicurezza, bisogna utilizzare esclusivamente pneumatici delle dimensioni previste dall''omologazione del veicoli.', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '843', '35019', '6970', '2', 'Per una maggiore sicurezza, sulle ruote dello stesso asse bisogna utilizzare pneumatici uguali', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '843', '35019', '6971', '3', 'Per una maggiore sicurezza, bisogna controllare le condizioni dei pneumatici anche sui lati, per prevenire improvvisi scoppi o sgonfiamenti', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '843', '35019', '6972', '4', 'Per garantire l''aderenza e la stabilità del veicolo, bisogna controllare che la pressione di gonfiaggio degli pneumatici sia quella consigliata dalla casa costruttrice', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '843', '35019', '6973', '5', 'La pressione di gonfiaggio troppo bassa degli pneumatici ne provoca l''anomalo consumo ai bordi', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '843', '35019', '6974', '6', 'Non bisogna mai invertire la posizione degli pneumatici, per permettere loro di adattarsi meglio', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '843', '35019', '6975', '7', 'Per garantire l''aderenza e la stabilità del veicolo, bisogna controllare solo la pressione di gonfiaggio degli pneumatici delle ruote motrici', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '843', '35019', '6976', '8', 'Per migliorare il raffreddamento degli pneumatici, bisogna diminuire la pressione di gonfiaggio', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '843', '35019', '6977', '9', 'La pressione di gonfiaggio troppo bassa degli pneumatici ne provoca l''anomalo consumo della parte centrale e dei bordi', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '844', '35011', '', '', 'Controllo della pressione di gonfiaggio degli pneumatici ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '844', '35011', '6978', '1', 'Si deve controllare periodicamente la pressione di gonfiaggio dei pneumatici, compresa quella della ruota di scorta', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '844', '35011', '6979', '2', 'La pressione di gonfiaggio degli pneumatici va adeguata alle condizioni di peso del veicolo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '844', '35011', '6980', '3', 'La corretta pressione di gonfiaggio degli pneumatici dipende dal carico del veicolo e dal numero dei passeggeri trasportati', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '844', '35011', '6981', '4', 'Un''insufficiente pressione di gonfiaggio degli pneumatici fa aumentare l''attrito e anche i consumi di carburante', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '844', '35011', '6982', '5', 'La pressione di gonfiaggio dei pneumatici deve essere controllata quando la gomma è fredda', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '844', '35011', '6983', '6', 'Il sistema automatico di rilevamento della pressione di gonfiaggio dei pneumatici fa aumentare la sicurezza dei veicoli che ne sono dotati', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '844', '35011', '6984', '7', 'Se si misura la pressione di gonfiaggio degli pneumatici quando la gomma è calda, si ha una corretta lettura dei valori di pressione', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '844', '35011', '6985', '8', 'La pressione di gonfiaggio degli pneumatici va controllata dopo aver percorso alcuni chilometri, appena la gomma si è surriscaldata', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '844', '35011', '6986', '9', 'La pressione di gonfiaggio degli pneumatici va stabilita in base al materiale con cui sono realizzati i cerchioni(acciaio, lega leggera, ecc.)', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '844', '35011', '6987', '10', 'Prima di iniziare un lungo viaggio, è opportuno aumentare la pressione di gonfiaggio degli pneumatici', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '844', '35011', '6988', '11', 'Nei veicoli con rimorchio, la pressione di gonfiaggio degli pneumatici del veicolo trainante deve essere uguale a quella degli pneumatici del rimorchio', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '845', '35013', '', '', 'Uso di pneumatici termici, chiodati e di catene da neve ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '845', '35013', '6989', '1', 'Gli pneumatici chiodati possono essere usati limitatamente ad un predeterminato periodo dell''anno', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '845', '35013', '6990', '2', 'È consigliabile che gli pneumatici invernali vengano montati su tutte le ruote', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '845', '35013', '6991', '3', 'Chi fa uso di catene da neve non omologate è sanzionabile come se ne fosse sprovvisto', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '845', '35013', '6992', '4', 'Gli pneumatici chiodati vanno montati solo su due ruote dello stesso asse', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '845', '35013', '6993', '5', 'Nei veicoli a doppia trazione (cosiddetti 4x4), le catene da neve devono essere montate sulle ruote posteriori', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '845', '35013', '6994', '6', 'Gli organi della sospensione non si danneggiano se nei tratti di strada privi di neve si fa uso di pneumatici con catene', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '845', '35013', '6995', '7', 'I veicoli muniti di pneumatici invernali devono rispettare limiti di velocità  massima inferiori a quelli ordinari', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '846', '35014', '', '', 'Sostituzione di una ruota ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '846', '35014', '6996', '1', 'Per la sostituzione di una ruota conviene stazionare il veicolo su un terreno orizzontale e stabile', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '846', '35014', '6997', '2', 'Prima di procedere alla sostituzione di una ruota, bisogna azionare il freno di stazionamento e inserire la prima marcia o la retromarcia', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '846', '35014', '6998', '3', 'Prima di sollevare il veicolo per la sostituzione di una ruota, è conveniente allentare leggermente i bulloni di fissaggio', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '846', '35014', '6999', '4', 'Durante le operazioni di sostituzione di una ruota tramite il martinetto (cric), bisogna evitare di mettersi sotto il veicolo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '846', '35014', '7000', '5', 'Per sostituire una ruota, dopo aver sollevato il veicolo e inserito la ruota di scorta, bisogna stringere leggermente i bulloni a veicolo sollevato e completarne il bloccaggio solo dopo averlo abbassato', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '846', '35014', '7001', '6', 'In caso di foratura, prima di procedere alla sostituzione della ruota, è opportuno far scendere dal veicolo tutti i passeggeri, facendoli sistemare, per ragioni di sicurezza, fuori dalla carreggiata', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '846', '35014', '7002', '7', 'Per smontare una ruota da sostituire bisogna togliere i bulloni, prima di sollevare il veicolo con il martinetto (cric)', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '846', '35014', '7003', '8', 'Per smontare una ruota da sostituire bisogna fissare il martinetto (cric) in un punto qualsiasi del longherone del veicolo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '846', '35014', '7004', '9', 'Per smontare una ruota bisogna fissare il martinetto (cric) il più lontano possibile dalla ruota da sostituire', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '846', '35014', '7005', '10', 'L''applicazione del ruotino(ruota di scorta di ridotte dimensioni), consente di raggiungere la velocità di 120 km/h', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '846', '35014', '7006', '11', 'Nei veicoli con il ruotinoapplicato (ruota di scorta di ridotte dimensioni) non è consentito trasportare passeggeri', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '847', '35022', '', '', 'Sospensioni ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '847', '35022', '7007', '1', 'Le sospensioni di un veicolo a motore sono poste tra il telaio e le ruote', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '847', '35022', '7008', '2', 'Le sospensioni di un veicolo a motore servono a garantire un buon confortdi marcia', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '847', '35022', '7009', '3', 'Le sospensioni di un veicolo a motore servono a garantire che le ruote rimangano aderenti al fondo stradale', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '847', '35022', '7010', '4', 'Le sospensioni di un veicolo a motore servono a smorzare gli urti trasmessi dalle asperità del terreno', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '847', '35022', '7011', '5', 'Le sospensioni di un veicolo a motore collaborano a rendere più confortevole e sicura la guida', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '847', '35022', '7012', '6', 'Le sospensioni di un veicolo a motore servono a mantenere il veicolo fermo anche su strada in pendenza', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '847', '35022', '7013', '7', 'Le sospensioni di un veicolo a motore sono in genere poste sulle ruote anteriori, ma non su quelle posteriori', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '847', '35022', '7014', '8', 'Le sospensioni di un veicolo a motore servono a irrigidire il veicolo, in modo che il conducente percepisca chiaramente tutti i saltellamenti del veicolo dovuti ad irregolarità della strada', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '847', '35022', '7015', '9', 'Le sospensioni di un veicolo a motore non entrano in funzione su strada rettilinea', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '848', '35023', '', '', 'Usura e rottura delle sospensioni ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '848', '35023', '7016', '1', 'Le sospensioni possono diventare inefficienti per usura o per aver subito urti troppo forti', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '848', '35023', '7017', '2', 'Un veicolo con sospensioni inefficienti ha problemi di tenuta di strada', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '848', '35023', '7018', '3', 'Il sovraccarico del veicolo può comportare la rottura delle sospensioni', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '848', '35023', '7019', '4', 'Il sovraccarico del veicolo può comportare la temporanea inefficienza delle sospensioni', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '848', '35023', '7020', '5', 'Il sovraccarico ripetuto del veicolo può comportare la rapida usura delle sospensioni', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '848', '35023', '7021', '6', 'Un veicolo con sospensioni inefficienti riduce il confort di marcia', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '848', '35023', '7022', '7', 'Un veicolo con sospensioni inefficienti aumenta l''affaticamento alla guida del conducente', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '848', '35023', '7023', '8', 'Un veicolo con sospensioni inefficienti può avere come conseguenza l''anomalo orientamento dei proiettori anabbaglianti', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '848', '35023', '7024', '9', 'Il sovraccarico del veicolo non comporta problemi per le sospensioni', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '848', '35023', '7025', '10', 'Se il veicolo è sovraccarico, per evitare problemi per le sospensioni è sufficiente aumentare la pressione dei pneumatici di un bar', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '848', '35023', '7026', '11', 'Le sospensioni del veicolo devono essere sostituite all''incirca ogni trentamila chilometri', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '848', '35023', '7027', '12', 'Il sovraccarico del veicolo comporta maggiori difficoltà di guida, mentre non comporta problemi per le sospensioni', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '848', '35023', '7028', '13', 'Le sospensioni di un veicolo a motore, essendo realizzate con materiali elastici, non subiscono danni per gli urti subiti durante la marcia', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '848', '35023', '7029', '14', 'Le sospensioni di un veicolo a motore sono realizzate in modo da non necessitare di verifiche e manutenzioni per tutta la durata della garanzia del veicolo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '848', '35023', '7030', '15', 'Le sospensioni non collaborano alla dirigibilità del veicolo, perché questa è assicurata unicamente dagli organi di direzione', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '848', '35023', '7031', '16', 'Poiché le autovetture sono dotate di sospensioni su ogni ruota, la rottura di una sola delle quattro non comporta problemi per la guida', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '849', '35024', '', '', 'Ammortizzatori ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '849', '35024', '7032', '1', 'Gli ammortizzatori di un veicolo a motore servono a ridurre le oscillazioni delle sospensioni', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '849', '35024', '7033', '2', 'Gli ammortizzatori collaborano alla tenuta di strada del veicolo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '849', '35024', '7034', '3', 'Gli ammortizzatori servono, tra l''altro, a garantire la tenuta di strada del veicolo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '849', '35024', '7035', '4', 'Il sovraccarico del veicolo può rendere inefficienti gli ammortizzatori', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '849', '35024', '7036', '5', 'Il sovraccarico del veicolo può comportare un''usura precoce degli ammortizzatori', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '849', '35024', '7037', '6', 'Gli ammortizzatori di un veicolo a motore non necessitano di verifiche e manutenzioni fino a quando dura la garanzia del veicolo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '849', '35024', '7038', '7', 'Il sovraccarico non causa problemi agli ammortizzatori di un veicolo a motore se il conducente adotta uno stile di guida prudente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '849', '35024', '7039', '8', 'Se il veicolo è sovraccarico, per evitare problemi agli ammortizzatori è sufficiente aumentare la pressione dei pneumatici di mezzo bar', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '849', '35024', '7040', '9', 'Il conducente può in ogni caso regolare la rigidità degli ammortizzatori', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '849', '35024', '7041', '10', 'La rigidità degli ammortizzatori di un veicolo a motore può essere controllata dal proprietario del veicolo, senza ricorrere ad un''officina specializzata', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '849', '35024', '7042', '11', 'Un veicolo a motore è dotato di sospensioni oppure di ammortizzatori, ma difficilmente di tutti e due', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '850', '35025', '', '', 'Ammortizzatori scarichi ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '850', '35025', '7043', '1', 'Gli ammortizzatori scarichi provocano un comportamento anomalo del veicolo in frenata', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '850', '35025', '7044', '2', 'Gli ammortizzatori scarichi provocano un comportamento anomalo del veicolo, particolarmente in curva', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '850', '35025', '7045', '3', 'In un veicolo a motore, gli ammortizzatori scarichi provocano l''usura non uniforme del battistrada dei pneumatici', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '850', '35025', '7046', '4', 'In un veicolo a motore, gli ammortizzatori scarichi aumentano il rischio di rottura delle molle delle sospensioni', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '850', '35025', '7047', '5', 'In un veicolo a motore, gli ammortizzatori scarichi provocano una diminuzione del comfort di marcia dei passeggeri', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '850', '35025', '7048', '6', 'In un veicolo a motore, gli ammortizzatori scarichi peggiorano la tenuta di strada', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '850', '35025', '7049', '7', 'In un veicolo, gli ammortizzatori scarichi provocano un anomalo funzionamento del motore', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '850', '35025', '7050', '8', 'In un veicolo a motore, gli ammortizzatori scarichi provocano un maggior consumo di lubrificante', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '850', '35025', '7051', '9', 'In un veicolo a motore, gli ammortizzatori scarichi, abbassando l''altezza da terra del telaio, migliorano la tenuta di strada del veicolo, specie in curva', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '850', '35025', '7052', '10', 'In un veicolo a motore, gli ammortizzatori possono essere sostituiti con una sospensione rinforzata', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '850', '35025', '7053', '11', 'Gli ammortizzatori di un veicolo, se sono del tipo a circuito sigillato, non necessitano di manutenzione', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '851', '35026', '', '', 'Uso e funzionamento dello sterzo ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '851', '35026', '7054', '1', 'In caso di mal funzionamento del servosterzo, lo sterzo diventa pesante', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '851', '35026', '7055', '2', 'In caso di foratura di un pneumatico, lo sterzo tira nella direzione della ruota forata', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '851', '35026', '7056', '3', 'Al fine di garantire la sicurezza della circolazione occorre verificare periodicamente l''assenza di giochi anomali allo sterzo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '851', '35026', '7057', '4', 'La durezzadello sterzo può essere indice di insufficiente pressione dei pneumatici', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '851', '35026', '7058', '5', 'Il servosterzo idraulico non funziona se il motore è spento', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '851', '35026', '7059', '6', 'Eccessivi giochi allo sterzo rendono imprecisa la traiettoria del veicolo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '851', '35026', '7060', '7', 'La convergenza delle ruote ha influenza sulla precisione della sterzata', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '851', '35026', '7061', '8', 'Se lo sterzo vibra è bene far controllare la bilanciatura delle ruote anteriori', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '851', '35026', '7062', '9', 'Il servosterzo aiuta a controllare la traiettoria del veicolo in caso di foratura di un pneumatico', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '851', '35026', '7063', '10', 'Il servosterzo migliora l''azione frenante del veicolo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '851', '35026', '7064', '11', 'In caso di foratura di un pneumatico, lo sterzo tira nella direzione opposta a quella della ruota forata', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '851', '35026', '7065', '12', 'Se lo sterzo vibra si deve far controllare la bilanciatura delle ruote posteriori', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '851', '35026', '7066', '13', 'La rottura del servosterzo si manifesta con un''eccessiva facilità di manovra dello sterzo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '851', '35026', '7067', '14', 'Il volante deve essere impugnato nella sua parte bassa, con entrambe le mani sotto le razze', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '851', '35026', '7068', '15', 'Il volante non può essere regolato in altezza', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '851', '35026', '7069', '16', 'La precisione della sterzata non è influenzata dalle cattive condizioni delle sospensioni e degli ammortizzatori', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '851', '35026', '7070', '17', 'La precisione della sterzata è influenzata dalla scarsa pressione dell''olio di lubrificazione', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '851', '35026', '7071', '18', 'La precisione della sterzata è influenzata dall''eccessiva temperatura del liquido di raffreddamento', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '852', '35028', '', '', 'Interruttore di emergenza per lo spegnimento del motore nei veicoli a due ruote ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '852', '35028', '7072', '1', 'Nei veicoli a motore a due ruote è possibile spegnere il motore anche tramite l''interruttore di emergenza posto sul manubrio', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '852', '35028', '7073', '2', 'Nei veicoli a motore a due ruote, per poter avviare il motore, è necessario che l''interruttore di emergenza posto sul manubrio sia posizionato su ON/Avviamento', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '852', '35028', '7074', '3', 'Nei veicoli a motore a due ruote, l''interruttore di emergenza permette lo spegnimento del motore senza staccare le mani dal manubrio', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '852', '35028', '7075', '4', 'Nei veicoli a motore a due ruote, l''interruttore di emergenza, permette lo spegnimento del motore qualora ci si arresti su strade a forti pendenze avendo le mani occupate sulle leve di comando', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '852', '35028', '7076', '5', 'L''interruttore di emergenza di cui sono muniti i veicoli a motore a due ruote, se guasto, deve essere riparato al più presto', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '852', '35028', '7077', '6', 'Su un motociclo, per lo spegnimento del motore nelle situazioni ordinarie, è preferibile utilizzare la chiave di accensione anziché l''interruttore di emergenza', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '852', '35028', '7078', '7', 'Nei veicoli a motore a due ruote, l''interruttore di emergenza posto sul manubrio sostituisce il dispositivo di accensione a chiave', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '852', '35028', '7079', '8', 'Per facilitare lo spegnimento del motore, i veicoli a motore a due ruote sono equipaggiati di un interruttore d''emergenza posto sulla pedivella del cambio', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '852', '35028', '7080', '9', 'Nei veicoli a motore a due ruote, l''interruttore d''emergenza permette il blocco istantaneo delle ruote', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '852', '35028', '7081', '10', 'L''interruttore di emergenza posto sul cavalletto laterale, serve per spegnere il motore del motociclo sulle strade in pendenza', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '852', '35028', '7082', '11', 'Per facilitare lo spegnimento del motore, i veicoli a motore a due ruote sono equipaggiati di un interruttore d''emergenza azionabile con la pressione del piede destro', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '852', '35028', '7083', '12', 'Nei veicoli a motore a due ruote, l''interruttore d''emergenza ha la funzione di freno di stazionamento', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '852', '35028', '7084', '13', 'Nei veicoli a motore a due ruote, l''interruttore d''emergenza è collegato con l''antifurto del veicolo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '853', '35029', '', '', 'Catena di trasmissione nei veicoli a due ruote ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '853', '35029', '7085', '1', 'Ai fini della sicurezza della circolazione, nei veicoli a motore a due ruote è opportuno verificare periodicamente l''usura della catena di trasmissione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '853', '35029', '7086', '2', 'La catena di trasmissione dei veicoli a motore a due ruote, se non lubrificata, può essere soggetta a rottura', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '853', '35029', '7087', '3', 'Nei veicoli a motore a due ruote, la catena di trasmissione collega il pignone motore alla corona della ruota', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '853', '35029', '7088', '4', 'Ai fini della sicurezza, nei veicoli a motore a due ruote, è opportuno verificare periodicamente che la catena di trasmissione sia ben lubrificata', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '853', '35029', '7089', '5', 'Nei veicoli a motore a due ruote, è opportuno verificare periodicamente la corretta tensione della catena di trasmissione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '853', '35029', '7090', '6', 'Nei veicoli a motore a due ruote, la rottura improvvisa della catena di trasmissione può causare il bloccaggio della ruota posteriore, con conseguente perdita del controllo del veicolo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '853', '35029', '7091', '7', 'Nei veicoli a motore a due ruote, se la catena di trasmissione mostra segni di usura occorre sostituirla', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '853', '35029', '7092', '8', 'Nei veicoli a motore a due ruote, la catena di trasmissione non necessita di lubrificazione perché è realizzata in acciaio auto lubrificante', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '853', '35029', '7093', '9', 'Nei veicoli a motore a due ruote, la rottura della catena di trasmissione pregiudica la velocità di marcia, ma non la sicurezza', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '853', '35029', '7094', '10', 'Nei veicoli a motore a due ruote, se la catena di trasmissione mostrasegni di usura occorre rinforzarla con filo di ferro di tipo idoneo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '853', '35029', '7095', '11', 'Nei veicoli a motore a due ruote, in caso di sostituzione della catena di trasmissione per usura, non è mai necessario sostituire anche il pignone motore e la corona della ruota', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '853', '35029', '7096', '12', 'Nei veicoli a motore a due ruote non è necessario il periodico controllo della tensione della catena di trasmissione, perché la stessa è dotata di tenditore automatico', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '853', '35029', '7097', '13', 'Nei veicoli a motore a due ruote, la catena di trasmissione deve essere pulita con detergente sgrassante, ma assolutamente non lubrificata per non far slittare il cambio', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '853', '35029', '7098', '14', 'Nei veicoli a motore a due ruote, è possibile sostituire il pignone motore e la corona della ruota in modo da modificare il rapporto di trasmissione', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '854', '35030', '', '', 'Controllo del livello dell''olio e del liquido dei freni nei veicoli a due ruote ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '854', '35030', '7099', '1', 'Sui motocicli è necessario verificare periodicamente i livelli di olio motore e liquido freni', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '854', '35030', '7100', '2', 'Nei veicoli a motore a due ruote, il livello dell''olio deve essere controllato a veicolo perfettamente orizzontale, poggiato con le ruote a terra e non sul cavalletto', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '854', '35030', '7101', '3', 'Sui motocicli, il livello del liquido freni si verifica controllando l''apposita vaschetta trasparente', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '854', '35030', '7102', '4', 'In alcuni motori di motocicli, il livello dell''olio motore può essere controllato da un''apposita finestrella posta sulla coppa motore', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '854', '35030', '7103', '5', 'Sui motocicli, il livello del lubrificante va controllato con il veicolo in piano e dopo aver spento il motore da alcuni minuti', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '854', '35030', '7104', '6', 'Sui veicoli a motore a due ruote, non vi sono liquidi di cui occorre fare un controllo dei livelli perché i serbatoi sono sigillati', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '854', '35030', '7105', '7', 'Sui motocicli, il livello del l''olio motore deve essere controllato con il motore in funzione al minimo e la moto sistemata sul cavalletto', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '854', '35030', '7106', '8', 'I motocicli non sono dotati di serbatoio del liquido freni perché la frenatura è di tipo puramente meccanico', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '854', '35030', '7107', '9', 'Sui motocicli raffreddati ad aria, il livello del liquido di raffreddamento può essere controllato da un''apposita finestrella posta tra le alettature del motore', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '855', '35017', '', '', 'Il climatizzatore ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '855', '35017', '7108', '1', 'Il climatizzatore può essere utilmente impiegato anche nel periodo invernale, per sbrinare velocemente il parabrezza', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '855', '35017', '7109', '2', 'Quando la strada è bagnata, se il veicolo che mi precede solleva dell''acqua che mi sporca il parabrezza, devo sorpassarlo immediatamente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '856', '35018', '', '', 'Trasparenza e pulizia dei vetri ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '856', '35018', '7110', '1', 'Adesivi, ciondoli o altri oggetti applicati sui vetri del veicolo possono ridurre la visibilità del  conducente', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '856', '35018', '7111', '2', 'La trasparenza e la pulizia dei vetri dei finestrini del veicolo garantiscono una migliore visibilità esterna', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '856', '35018', '7112', '3', 'Occorre verificare le spazzole del tergicristallo per sostituirle se sono consumate', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '856', '35018', '7113', '4', 'Occorre verificare che nel serbatoio del dispositivo lavacristallo sia sempre presente liquido per detergere i vetri', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '856', '35018', '7114', '5', 'In caso di lesioni o rottura del vetro anteriore del veicolo, bisogna ripararlo con del nastro adesivo trasparente', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '856', '35018', '7115', '6', 'Se il parabrezza è coperto dalla polvere, bisogna utilizzare il dispositivo di disappannamento', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '857', '35016', '', '', 'Comportamenti da adottare dopo un incidente se c’è rischio o principio di incendio ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '857', '35016', '7116', '1', 'Dopo un incidente, se c''è un rischio d''incendio del veicolo è opportuno spegnere il quadro elettrico girando la chiave di accensione', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '857', '35016', '7117', '2', 'In caso di principio d''incendio del veicolo, in mancanza di estintore, si può gettare sabbia o terra alla base delle fiamme', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '857', '35016', '7118', '3', 'Dopo un incidente, se c''è un principio d''incendio del veicolo, in mancanza di estintore si può utilizzare una coperta bagnata per spegnere le fiamme', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '857', '35016', '7119', '4', 'Dopo un incidente, se c''è un principio d''incendio del veicolo occorre svuotarne immediatamente il serbatoio del carburante', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '857', '35016', '7120', '5', 'Dopo un incidente, il rischio che scoppi un incendio è maggiore se il veicolo è dotato di interruttore inerziale, che blocca l''afflusso di carburante al motore', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '857', '35016', '7121', '6', 'Dopo un incidente, se c''è un principio d''incendio del veicolo alimentato a GPL, occorre smontare subito il serbatoio contenente il carburante', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '858', '35001', '', '', 'Forza aderente (aderenza) ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '858', '35001', '7122', '1', 'La forza aderente consente il movimento del veicolo', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '858', '35001', '7123', '2', 'La forza aderente si oppone allo slittamento verso l''esterno della curva', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '858', '35001', '7124', '3', 'La forza aderente consente al veicolo di percorrere la traiettoria voluta', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '858', '35001', '7125', '4', 'La forza aderente si oppone allo slittamento dello pneumatico sull''asfalto', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '858', '35001', '7126', '5', 'La forza aderente non si oppone allo slittamento causato dal vento laterale', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '858', '35001', '7127', '6', 'La forza aderente, se eccessiva, comporta il surriscaldamento dei freni', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '859', '35002', '', '', 'Forza aderente (aderenza) ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '859', '35002', '7128', '1', 'La forza aderente aumenta se l''asfalto è rugoso', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '859', '35002', '7129', '2', 'La forza aderente diminuisce se l''asfalto è bagnato', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '859', '35002', '7130', '3', 'La forza aderente aumenta se l''asfalto è bagnato', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '859', '35002', '7131', '4', 'La forza aderente aumenta se l''asfalto è coperto di foglie', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '859', '35002', '7132', '5', 'La forza aderente aumenta se l''asfalto è coperto di ghiaccio', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '859', '35002', '7133', '6', 'La forza aderente aumenta se l''asfalto è coperto dal fango', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '860', '35006', '', '', 'Forza aderente (aderenza) ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '860', '35006', '7134', '1', 'La forza aderente diminuisce se la strada è sporca di olio', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '860', '35006', '7135', '2', 'La forza aderente risulta maggiore se l''asfalto è asciutto e pulito', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '860', '35006', '7136', '3', 'La forza aderente aumenta se l''asfalto è coperto di ghiaccio', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '860', '35006', '7137', '4', 'La forza aderente aumenta se l''asfalto è coperto dal fango', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '860', '35006', '7138', '5', 'La forza aderente non dipende dalla pressione dei pneumatici', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '861', '35003', '', '', 'Coefficiente di aderenza ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '861', '35003', '7139', '1', 'Se il coefficiente di aderenza diminuisce le ruote motrici potrebbero pattinare', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '861', '35003', '7140', '2', 'Se il coefficiente di aderenza diminuisce lo spazio di frenatura aumenta', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '861', '35003', '7141', '3', 'Se il coefficiente di aderenza è basso bisogna ridurre la velocità', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '861', '35003', '7142', '4', 'Se il coefficiente di aderenza diminuisce lo spazio di frenatura si riduce', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '861', '35003', '7143', '5', 'Se il coefficiente di aderenza è basso bisogna ridurre la velocità solo in curva', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '862', '35004', '', '', 'Coefficiente di aderenza ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '862', '35004', '7144', '1', 'Il coefficiente di aderenza basso limita la velocità alla quale è possibile percorrere una curva in sicurezza', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '862', '35004', '7145', '2', 'Il coefficiente di aderenza basso favorisce lo slittamento in curva', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '862', '35004', '7146', '3', 'Il coefficiente di aderenza basso aumenta lo spazio di frenatura', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '862', '35004', '7147', '4', 'Il coefficiente di aderenza basso rende pericolose le frenate brusche', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '862', '35004', '7148', '5', 'Il coefficiente di aderenza basso non condiziona la velocità del veicolo', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '862', '35004', '7149', '6', 'Il coefficiente di aderenza basso riduce lo spazio di frenatura', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '862', '35004', '7150', '7', 'Il coefficiente di aderenza basso diminuisce i consumi di carburante', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '862', '35004', '7151', '8', 'Il coefficiente di aderenza basso riduce lo slittamento in curva', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '863', '35005', '', '', 'Aderenza dei motocicli ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '863', '35005', '7152', '1', 'Chi conduce un motociclo deve considerare che l''aderenza diminuisce sulle rotaie specialmente in caso di pioggia', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '863', '35005', '7153', '2', 'Chi conduce un motociclo deve considerare che l''aderenza diminuisce sulle strisce della segnaletica orizzontale in caso di pioggia', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '863', '35005', '7154', '3', 'Il conducente di un motociclo deve considerare che l''aderenza non dipende dal tipo di pavimentazione stradale', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '863', '35005', '7155', '4', 'Chi conduce un motociclo deve considerare che l''aderenza diminuisce quando trasporta un passeggero', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '864', '35007', '', '', 'Forze che agiscono sul veicolo in curva ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '864', '35007', '7156', '1', 'Quando il veicolo percorre una curva è assoggettato ad una forza che tende a spostarlo verso l''esterno', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '864', '35007', '7157', '2', 'Quando il veicolo percorre una curva, la velocità eccessiva può provocarne lo sbandamento', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '864', '35007', '7158', '3', 'Quando il veicolo percorre una curva, la velocità eccessiva può provocarne il ribaltamento', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '864', '35007', '7159', '4', 'Quando il veicolo percorre una curva è assoggettato ad una forza che tende a spostarlo verso l''interno', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '864', '35007', '7160', '5', 'Quando il veicolo percorre una curva si deve aumentare la velocità per evitare lo slittamento', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '864', '35007', '7161', '6', 'Quando il veicolo percorre una curva si deve aumentare la velocità per evitare il ribaltamento', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '865', '35008', '', '', 'Fattori che favoriscono il ribaltamento di un veicolo in curva ', '');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '865', '35008', '7162', '1', 'Il ribaltamento in curva del veicolo è favorito da un carico posto in alto.', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '865', '35008', '7163', '2', 'Il ribaltamento in curva del veicolo è favorito dal carico non stabilmente fissato', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '865', '35008', '7164', '3', 'Il ribaltamento in curva del veicolo è favorito da una ridotta distanza fra ruote dello stesso asse (carreggiata stretta)', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '865', '35008', '7165', '4', 'Il ribaltamento in curva del veicolo è favorito da un raggio di curvatura della strada piccolo (curva stretta)', 'V');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '865', '35008', '7166', '5', 'Il ribaltamento in curva del veicolo è favorito da una ampia distanza fra ruote dello stesso asse (carreggiata larga)', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '865', '35008', '7167', '6', 'Il ribaltamento in curva del veicolo è favorito da un carico posto in basso', 'F');
INSERT INTO B (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('25', '865', '35008', '7168', '7', 'Il ribaltamento in curva del veicolo è favorito da un raggio di curvatura della strada ampio (curva larga)', 'F');
