INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '1', '', '', 'Coppia motrice', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '1', '1', '1', 'La coppia motrice è generata dalla forza con la quale il pistone mette in rotazione l’albero motore attraverso la manovella', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '1', '2', '2', 'La coppia di un motore endotermico è il prodotto tra la forza che spinge il pistone e il braccio di manovella', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '1', '3', '3', 'La coppia motrice è generata dalla forza applicata dal sistema frenante sui mozzi delle ruote', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '1', '4', '4', 'La coppia di un motore endotermico è una somma tra una forza e una lunghezza', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '2', '', '', ' Curve di coppia e di potenza del motore ', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '2', '5', '1', 'L’andamento delle curve di coppia e di potenza di un motore a combustione interna è legato al numero di giri del motore', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '2', '6', '2', 'La curva di coppia di un motore endotermico descrive il valore della coppia al variare del numero di giri', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '2', '7', '3', 'Il rendimento ottimale del motore si ha in corrispondenza di un numero di giri vicino a quello della coppia massima', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '2', '8', '4', 'Il miglior compromesso tra velocità e consumo di carburante si ha in corrispondenza di un numero di giri del motore a combustione interna vicino a quello della coppia massima', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '2', '9', '5', 'Nelle curve di potenza e di coppia di un motore a combustione interna il numero di giri corrispondente alla coppia massima è inferiore a quello di potenza massima', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '2', '10', '6', 'Il regime di rotazione di coppia massima di un motore a combustione interna corrisponde a quello di potenza massima', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '2', '11', '7', 'La curva di coppia di un motore endotermico descrive il valore della coppia al variare del peso del veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '2', '12', '8', 'La curva di potenza di un motore endotermico descrive il valore della coppia al variare delle dimensioni del veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '2', '13', '9', 'In un motore endotermico, l’andamento della curva di coppia all’aumentare del numero di giri, è dapprima decrescente, raggiunge un valore minimo e poi cresce', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '2', '14', '10', 'Nella curva di coppia, il numero di giri corrispondente ad un valore vicino a quello della coppia massima coincide con quello di massimo consumo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '3', '', '', 'Curve di coppia e di potenza del motore ', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '3', '15', '1', 'Nella curva di coppia il numero di giri corrispondenti ad un valore vicino a quello della coppia massima coincide con quello di massimo rendimento', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '3', '16', '2', 'La curva di potenza di un motore endotermico descrive il valore della potenza al variare del numero di giri', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '3', '17', '3', 'Il funzionamento dei motori è descrivibile attraverso le curve caratteristiche di coppia e di potenza', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '3', '18', '4', 'In un motore endotermico, l’andamento della curva di coppia all’aumentare del numero di giri, è dapprima crescente, raggiunge un valore massimo e poi decresce', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '3', '19', '5', 'In un motore endotermico, l’andamento della curva di potenza all’aumentare del numero di giri, è dapprima crescente, raggiunge un valore massimo e poi decresce', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '3', '20', '6', 'In un motore endotermico, l’andamento della curva di potenza all’aumentare del numero di giri, è dapprima decrescente, raggiunge un valore minimo e poi cresce', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '3', '21', '7', 'Nella curva di potenza, il numero di giri corrispondente alla massima potenza coincide con quello di coppia massima', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '4', '', '', 'Resistenza aerodinamica', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '4', '22', '1', 'Anche aumentando di poco la velocità aumenta molto la resistenza aerodinamica all’avanzamento', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '4', '23', '2', 'Il deflettore (spoiler) posto sopra la cabina di un autocarro diminuisce il coefficiente di resistenza aerodinamico (CX)', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '4', '24', '3', ' La forza di resistenza aerodinamica all’avanzamento del veicolo è proporzionale al quadrato della velocità', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '4', '25', '4', 'La forza di resistenza aerodinamica è direttamente proporzionale alla superficie frontale del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '4', '26', '5', 'La resistenza aerodinamica all’avanzamento del veicolo influisce sul consumo di carburante', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '4', '27', '6', ' La resistenza aerodinamica all’avanzamento del veicolo è inversamente proporzionale alla superficie della sua sezione frontale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '4', '28', '7', 'La resistenza aerodinamica all’avanzamento è direttamente proporzionale alla potenza massima del motore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '4', '29', '8', 'La resistenza aerodinamica all’avanzamento è direttamente proporzionale al peso del veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '4', '30', '9', 'La resistenza aerodinamica all’avanzamento aumenta sulla superficie stradale bagnata', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '4', '31', '10', 'La resistenza aerodinamica all’avanzamento del veicolo è indipendente dalla sua velocità', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '4', '32', '11', 'Il coefficiente di penetrazione aerodinamica (CX) dipende dal peso del veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '5', '', '', 'Campo di stabilità - uso delle marce ', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '5', '33', '1', 'L’ampiezza del campo di stabilità del motore endotermico è legata all’andamento delle curve di coppia e di potenza', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '5', '34', '2', 'L’ampiezza del campo di stabilità definisce l’elasticità del motore endotermico', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '5', '35', '3', 'L’ampiezza del campo di stabilità del motore endotermico determina l’”elasticità del motore”', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '5', '36', '4', 'Il campo di stabilità del motore endotermico è l’intervallo tra il numero di giri della coppia massima e il numero di giri della potenza massima', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '5', '37', '5', 'Il campo di stabilità del motore endotermico è la zona nella quale il motore non consuma carburante', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '5', '38', '6', 'Il campo di stabilità del motore endotermico è la zona nella quale il conducente è obbligato ad intervenire sul cambio di velocità', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '5', '39', '7', 'L’ampiezza del campo di stabilità del motore endotermico è l’intervallo tra il numero di giri della coppia massima e quello della potenza minima', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '5', '40', '8', 'Nelle salite percorse con il veicolo carico occorre fare attenzione a non superare il regime di coppia massima', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '6', '', '', 'Uso del cambio di velocità ', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '6', '41', '1', 'Il cambio di velocità serve a modificare il rapporto di trasmissione tra motore e ruote motrici', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '6', '42', '2', 'Il cambio è un componente della trasmissione', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '6', '43', '3', 'Il cambio di velocità consente di equilibrare forza di trazione e resistenza totale all’avanzamento del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '6', '44', '4', 'La corretta scelta del rapporto del cambio consente di mantenere il motore endotermico nel campo di stabilità', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '6', '45', '5', 'L’uso del cambio consente di adeguare la velocità alle variazioni di pendenza della strada', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '6', '46', '6', 'In salita, a parità di pendenza, all’aumentare del carico è necessario inserire una marcia più bassa', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '6', '47', '7', 'All’aumentare della pendenza in salita è necessario inserire una marcia più bassa al fine di aumentare la forza di trazione', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '6', '48', '8', 'In salita, a parità di pendenza, all’aumentare del carico è necessario inserire una marcia più alta', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '6', '49', '9', 'Passando dalla terza alla quarta marcia aumenta la forza di trazione', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '6', '50', '10', 'La forza di trazione aumenta se inseriamo marce sempre più alte', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '6', '51', '11', 'È necessario inserire la marcia più alta per aumentare la forza di trazione', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '6', '52', '12', 'Nelle discese ripide le marce alte consentono un miglior controllo del veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '7', '', '', 'Forza di trazione ', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '7', '53', '1', 'In salita deve aumentare la forza di trazione', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '7', '54', '2', 'Aumentando il peso del veicolo, aumenta la forza di trazione necessaria', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '7', '55', '3', 'La forza di trazione alla periferia delle ruote è più alta quando sono inserite marce basse', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '7', '56', '4', 'Per avere una forza di trazione più elevata, occorre utilizzare una marcia più bassa', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '7', '57', '5', 'È necessario aumentare la forza di trazione con l’aumentare del carico', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '7', '58', '6', 'La forza di trazione alla periferia delle ruote deve aumentare se aumentano le resistenze che si oppongono al moto del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '7', '59', '7', 'L’aumento della forza di trazione si ottiene inserendo marce sempre più basse', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '7', '60', '8', 'L’aumento della forza di trazione si ottiene inserendo le marce ridotte', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '7', '61', '9', 'L’aumento della forza di trazione si ottiene inserendo marce sempre più alte', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '7', '62', '10', 'L’aumento della forza di trazione si ottiene aumentando la lunghezza del veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '7', '63', '11', 'L’aumento della forza di trazione si può ottenere inserendo rapporti di trasmissione più alti', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '7', '64', '12', 'La forza di trazione alla periferia delle ruote è più bassa quando sono inserite marce basse', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '7', '65', '13', 'La forza di trazione alla periferia delle ruote è costante e non dipende dalla marcia inserita', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '7', '66', '14', 'Percorrendo le discese è necessaria una maggiore forza di trazione', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '8', '', '', 'Consumo di carburante ed emissioni', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '8', '67', '1', 'Marciare a velocità costante consente di risparmiare sul consumo di carburante', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '8', '68', '2', 'La scelta di una marcia appropriata al carico del veicolo consente di diminuire il consumo di carburante', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '8', '69', '3', 'Con il motore in regime di “fuori giri” si determina un consumo eccessivo di carburante', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '8', '70', '4', 'Il sovraccarico del veicolo comporta un aumento delle emissioni inquinanti', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '8', '71', '5', 'La riduzione delle emissioni inquinanti non dipende dall’aerodinamica del veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '8', '72', '6', 'Su un tratto di strada pianeggiante, a parità di velocità, il consumo di carburante è inferiore se si usa una marcia bassa', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '8', '73', '7', 'Il consumo di carburante dipende dalla velocità del veicolo ma è indipendente dal numero di giri del motore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '8', '74', '8', 'Per ridurre le emissioni inquinanti, il motore deve costantemente girare al massimo numero di giri', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '8', '75', '9', 'L’uso frequente del cambio di velocità comporta un minor consumo di carburante', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '9', '', '', 'Inerzia e frenatura ', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '9', '76', '1', 'L’inerzia è la forza che si oppone alla variazione del moto del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '9', '77', '2', 'L’inerzia del veicolo può essere utilmente sfruttata per raggiungere il punto d’arresto', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '9', '78', '3', 'Lo sfruttamento dell’inerzia del motore e della trasmissione consente di risparmiare carburante e freni', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '9', '79', '4', 'Un veicolo a pieno carico ha minor inerzia dello stesso veicolo scarico', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '9', '80', '5', 'L’utilizzo di una marcia bassa consente di sfruttare meglio l’inerzia del veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '9', '81', '6', 'Un veicolo ha maggiore inerzia in salita che in discesa', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '10', '', '', 'Elementi frenanti', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '10', '82', '1', 'Gli elementi frenanti possono essere del tipo a tamburo o a disco', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '10', '83', '2', 'Gli elementi frenanti producono una azione di attrito', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '10', '84', '3', 'Gli elementi frenanti dissipano l’energia cinetica del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '11', '', '', 'Frenatura di servizio, soccorso e freno di stazionamento - 01', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '11', '85', '1', 'Il distributore a mano dell’impianto frenante pneumatico comanda il freno di stazionamento', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '11', '86', '2', 'Il distributore a mano dell’impianto frenante pneumatico viene attivato dal conducente per mezzo di un apposito comando manuale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '11', '87', '3', 'Il distributore a mano dell’impianto frenante pneumatico comanda il bielemento frenante agendo sull’elemento a molla', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '11', '88', '4', 'Il distributore a mano dell’impianto frenante pneumatico invia aria al bielemento frenante per effettuare la frenatura di stazionamento', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '11', '89', '5', 'Il distributore a mano dell’impianto frenante pneumatico invia aria compressa nei freni a molla sia dell’assale anteriore che posteriore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '11', '90', '6', 'Il distributore a mano dell’impianto frenante pneumatico agisce sul rallentatore di velocità', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '12', '', '', 'Frenatura di servizio, soccorso e freno di stazionamento - 02', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '12', '91', '1', 'Il freno di soccorso interviene in caso di guasto del freno di servizio', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '12', '92', '2', 'L’impianto frenante di un autocarro o di un autobus è munito di freno di soccorso', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '12', '93', '3', 'L’impianto frenante di un autocarro o di un autobus realizza la frenatura di servizio e quella di soccorso agendo, di norma, sul solo pedale del freno', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '12', '94', '4', 'L’insufficiente pressione dell’aria nei serbatoi di un impianto frenante pneumatico impedisce al veicolo munito di freno di stazionamento a molla di mettersi in movimento', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '12', '95', '5', 'L’inserimento del freno di stazionamento è segnalato da un’apposita spia sul cruscotto', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '13', '', '', 'Impianto di frenatura pneumatica integrale e organi principali - 01', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '13', '96', '1', 'Il circuito frenante nei mezzi pesanti muniti di freno pneumatico utilizza l’aria accumulata in appositi serbatoi', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '13', '97', '2', 'Il freno pneumatico utilizza l’aria compressa preventivamente accumulata in appositi serbatoi', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '13', '98', '3', 'Nel freno pneumatico la forza frenante è fornita dalla pressione di aria', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '13', '99', '4', 'I serbatoi dell’impianto frenante pneumatico accumulano aria destinata ai freni', 'V');
