INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '24', '198', '12', 'Il convertitore pneumoidraulico dell’impianto di frenatura è posto fra i serbatoi dell’aria e il distributore duplex', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '25', '', '', 'Impianto frenante oleopneumatico e organi principali - 03', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '25', '199', '1', 'L’impianto frenante oleopneumatico è munito di distributore a mano', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '25', '200', '2', 'L’impianto frenante oleopneumatico è munito di un distributore duplex', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '25', '201', '3', 'Il circuito frenante pneumoidraulico è dotato di un regolatore di pressione', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '25', '202', '4', 'Il circuito frenante pneumoidraulico è dotato di deumidificatore', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '25', '203', '5', 'Il circuito frenante pneumoidraulico dell’impianto di frenatura è dotato di serbatoio dell’acqua', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '26', '', '', 'Sistema di antibloccaggio delle ruote ABS', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '26', '204', '1', 'Per arrestare un veicolo nello spazio minimo il conducente deve evitare il bloccaggio delle ruote', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '26', '205', '2', 'L’intervento dell’ABS (dispositivo antibloccaggio del sistema frenante) non è comandato dal conducente', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '26', '206', '3', 'L’ABS (dispositivo antibloccaggio del sistema frenante) serve a ridurre gli spazi totali di arresto e a manovrare il veicolo durante una frenata di emergenza', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '26', '207', '4', 'L’ABS (dispositivo antibloccaggio del sistema frenante) evita il bloccaggio delle ruote durante la frenatura del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '26', '208', '5', 'L’ABS (dispositivo antibloccaggio del sistema frenante) serve a ridurre i consumi di carburante durante la fase di accelerazione del veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '26', '209', '6', 'L’ABS (dispositivo antibloccaggio del sistema frenante) non è mai necessario trattandosi di un optional', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '26', '210', '7', 'L’ABS (dispositivo antibloccaggio del sistema frenante) è obbligatoria in tutti i veicoli in circolazione', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '26', '211', '8', 'L’ABS (dispositivo antibloccaggio del sistema frenante) è obbligatorio solo per i veicoli delle categorie M2, M3, N2, N3', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '26', '212', '9', 'Per ottenere una frenata graduale occorre agire ad intermittenza sul pedale del freno', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '26', '213', '10', 'Negli impianti di frenatura la forza frenante è amplificata dall’ABS (dispositivo antibloccaggio del sistema frenante)', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '27', '', '', 'Freno motore', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '27', '214', '1', 'L’utilizzo del freno motore ha un effetto notevole soprattutto a bassa velocità', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '27', '215', '2', 'L’inserimento del freno motore riduce la sezione dei condotti di scarico mediante una valvola a farfalla', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '27', '216', '3', 'L’uso prolungato del freno motore provoca un innalzamento della temperatura del motore', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '27', '217', '4', 'Il freno motore annulla la mandata del gasolio', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '27', '218', '5', 'Il freno motore è efficace in quanto agisce sull’albero di trasmissione', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '27', '219', '6', 'Il freno motore è particolarmente efficiente alle alte velocità', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '27', '220', '7', 'L’uso del freno motore evita un innalzamento della temperatura del motore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '27', '221', '8', 'Nelle lunghe discese è consigliabile non usare il freno motore per evitarne il suo deterioramento', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '27', '222', '9', 'Nelle lunghe discese è opportuno limitare l’uso del freno motore per evitare un eccessivo consumo di carburante', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '27', '223', '10', 'Per effettuare una frenata brusca è opportuno l’impiego del freno motore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '28', '', '', 'Rallentatore', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '28', '224', '1', 'È opportuno utilizzare il rallentatore percorrendo lunghe discese', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '28', '225', '2', 'L’uso del solo rallentatore non garantisce un rapido arresto del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '28', '226', '3', 'Il rallentatore è comandato dal conducente', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '28', '227', '4', 'Il rallentatore è di tipo elettromagnetico o idraulico', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '28', '228', '5', 'Il rallentatore è particolarmente efficiente alle velocità più alte', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '28', '229', '6', 'Il rallentatore è efficace in quanto agisce direttamente sul sistema di scarico del motore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '28', '230', '7', 'Il rallentatore è particolarmente efficiente alle velocità più basse possibili', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '28', '231', '8', 'Il rallentatore interviene automaticamente quando si supera la velocità di 80 km/h', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '28', '232', '9', 'L’uso del rallentatore annulla la mandata del gasolio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '29', '', '', 'Freno motore e rallentatore', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '29', '233', '1', 'Il freno di servizio consente un rapido arresto del veicolo mentre il rallentatore e il freno motore consentono il solo rallentamento del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '29', '234', '2', 'L’uso combinato del rallentatore e del freno comporta un minor usura delle ganasce', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '29', '235', '3', 'L’impiego del freno motore e/o del rallentatore limita il surriscaldamento dei freni', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '29', '236', '4', 'L’impiego del freno motore e/o del rallentatore limita il consumo delle guarnizioni d’attrito dell’impianto dei freni', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '29', '237', '5', 'L’impiego del freno motore e/o del rallentatore limita l’usura degli elementi frenanti', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '29', '238', '6', 'L’impiego del freno motore e/o del rallentatore provoca il surriscaldamento dei freni', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '29', '239', '7', 'Per arrestare un veicolo nello spazio minimo il conducente deve intervenire energicamente sul rallentatore e non sul freno', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '29', '240', '8', 'Il contemporaneo uso del rallentatore e del freno motore provoca il bloccaggio delle ruote', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '30', '', '', 'Avaria dell’impianto frenante e frenatura di soccorso -01', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '30', '241', '1', 'La rottura della tubazione dell’aria compressa di una dei due circuiti dell’impianto frenante, non compromette l’efficienza dell’altra', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '30', '242', '2', 'Il conducente che verifica che vi sia un’avaria di uno dei due circuiti dell’impianto frenante, deve arrestarsi quanto prima in condizioni di sicurezza', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '30', '243', '3', 'L’eccessiva pressione d’aria nei serbatoi dell’impianto frenante è indicata dal manometro presente sul cruscotto', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '30', '244', '4', 'L’indicazione di un eccesso di pressione dell’aria nei serbatoi dell’impianto frenante può essere causata da un difettoso funzionamento del gruppo di regolazione e controllo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '30', '245', '5', 'In caso di eccessiva sovrappressione dell’aria dei serbatoi dell’impianto frenante, il conducente deve astenersi dall’utilizzare i freni e ricorrere maggiormente al rallentatore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '30', '246', '6', 'In caso di scarsa pressione dell’aria dei serbatoi dell’impianto frenante, è opportuno agire sul pedale del freno per far salire la pressione', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '30', '247', '7', 'In caso di malfunzionamento del distributore duplex è opportuno marciare con il motore ad alto numero di giri', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '31', '', '', 'Avaria dell’impianto frenante e frenatura di soccorso - 02 ', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '31', '248', '1', 'L’improvvisa caduta di pressione dell’aria di un serbatoio dei freni, può essere causata dalla rottura della tubazione tra il serbatoio e il distributore duplex', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '31', '249', '2', 'L’indicazione di un eccesso di pressione dell’aria nei serbatoi dell’impianto frenante può essere causata dal cattivo funzionamento del gruppo di regolazione e controllo e/o di una valvola', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '31', '250', '3', 'La scarsa pressione dell’aria nei serbatoi dell’impianto frenante può essere causata dal difettoso funzionamento del compressore', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '31', '251', '4', 'In presenza di un’avaria di uno dei due circuiti dell’impianto frenante, si può continuare a circolare purché l’altro circuito sia efficiente', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '31', '252', '5', 'Il distributore duplex non è in grado di funzionare se il veicolo è sovraccarico', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '32', '', '', 'ITS (Intelligent Transport System) ', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '32', '253', '1', 'I sistemi elettronici avanzati, servono ad aumentare la sicurezza attiva e passiva del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '32', '254', '2', 'Un veicolo connesso è, in generale, un veicolo che usa un insieme di tecnologie di comunicazione per scambiare informazioni direttamente con altri veicoli o con l’infrastruttura, o mediante il Cloud', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '32', '255', '3', 'La guida automatica non può mai essere considerata un’evoluzione della guida assistita', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '33', '', '', 'Sistemi avanzati di assistenza alla guida (Adas) - 01 ', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '33', '256', '1', 'Tra le funzioni principali degli ADAS (sistemi avanzati di assistenza alla guida) vi rientra Driver Alert Control', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '33', '257', '2', 'Gli ADAS (sistemi avanzati di assistenza alla guida) sono sistemi che tendono a modificare le scelte di guida (comportamento al volante) in funzione del contesto che influenza la marcia del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '33', '258', '3', 'Gli ADAS (sistemi avanzati di assistenza alla guida) rappresentano il primo passo verso i veicoli a guida completamente autonoma', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '33', '259', '4', 'Gli ADAS (sistemi avanzati di assistenza alla guida) intervengono sull’accelerazione del veicolo, sulla frenatura e sulla manovra di sterzata, in condizioni di pericolo imminente', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '33', '260', '5', 'Gli ADAS (sistemi avanzati di assistenza alla guida) possono adattare la marcia del veicolo alle variazioni delle condizioni della strada e dell’ambiente esterno ma, in caso di emergenza, non possono intervenire in maniera autonoma per evitare una collisione', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '33', '261', '6', 'Gli ADAS (sistemi avanzati di assistenza alla guida) sono sistemi che influenzano le scelte di viaggio come, ad esempio, le scelte di percorso', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '33', '262', '7', 'Tra le funzioni principali degli ADAS (sistemi avanzati di assistenza alla guida) non vi rientra l’Intelligent Speed Assistance (ISA)', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '34', '', '', 'Sistemi avanzati di assistenza alla guida (Adas) - 02', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '34', '263', '1', 'Il sistema ACC o ABA (regolazione adattiva della velocità) serve a mantenere automaticamente la distanza di sicurezza rispetto ai veicoli che procedono nella stessa direzione', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '34', '264', '2', 'Il sistema ACC o ABA (regolazione adattiva della velocità) riceve i segnali da un apposito radar per rilevare la distanza e la velocità del veicolo che precede', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '34', '265', '3', 'Il sistema di assistenza della frenata (BAS o BDC o EBS o EBA o AFU) è un sistema elettronico che riconosce una frenata improvvisa e applica la pressione minima sull’impianto frenante', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '34', '266', '4', 'L’AAS (sistema di controllo delle sospensioni) è un sistema elettronico che, per agire, non ha bisogno di rilevare la velocità del veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '34', '267', '5', 'L’ELC (regolazione elettronica del livello) è un sistema elettronico che controlla e regola l’accelerazione dei veicoli', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '34', '268', '6', 'Il LDWS (sistema di avviso di deviazione dalla corsia di marcia) è un sistema elettronico che avvisa il conducente ogni volta che deve svoltare ad un incrocio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '34', '269', '7', 'Il LDWS (sistema di avviso di deviazione dalla corsia di marcia) è un sistema costituito da una centralina elettronica e sensori installati nel paraurti posteriore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '35', '', '', 'Sistemi avanzati di assistenza alla guida (Adas) - 03', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '35', '270', '1', 'L’AAS (sistema di controllo delle sospensioni) è un sistema elettronico che controlla l’effetto ammortizzante delle sospensioni del veicolo per ottimizzare la tenuta di strada e il comfort di marcia', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '35', '271', '2', 'L’ELC (regolazione elettronica del livello) può essere utilizzato anche per l’abbassamento del piano di carico degli autobus', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '35', '272', '3', 'L’ASR (regolazione dello slittamento in accelerazione) blocca la ruota che slitta e consente di trasferire la coppia motrice sull’altra ruota che ha mantenuto l’aderenza', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '35', '273', '4', 'L’ASR (regolazione dello slittamento in accelerazione) serve a ridurre gli spazi totali di arresto e a manovrare il veicolo durante una frenata di emergenza', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '35', '274', '5', 'L’ASCT (sistema di controllo automatico della stabilità e controllo della trazione) è un sistema meccanico di antipattinamento di tutte le ruote del veicolo (motrici e non)', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '35', '275', '6', 'Il sistema ACC o ABA (regolazione adattiva della velocità) serve a ridurre gli spazi totali arresto e a manovrare il veicolo durante una frenata di emergenza', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '36', '', '', 'Sistemi avanzati di assistenza alla guida (Adas) - 04', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '36', '276', '1', 'L’ESP (programma elettronico di stabilità) è un sistema elettronico che serve a stabilizzare il veicolo sulla traiettoria prescelta', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '36', '277', '2', 'L’ESP (programma elettronico di stabilità) interviene quando la centralina rileva una discordanza tra la traiettoria impostata dal conducente e la traiettoria effettiva del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '36', '278', '3', 'Grazie al CDC (sistema di controllo continuo dei sobbalzi), che regola continuamente gli ammortizzatori, vengono garantiti la stabilità del veicolo e il contenimento dei sobbalzi in frenata, nelle curve, nei dossi, ecc.', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '36', '279', '4', 'Il sistema APA (sistema che coadiuva il conducente nella manovra di parcheggio) è munito di sensori installati sui paraurti anteriori e posteriori che rilevano la distanza dagli ostacoli presenti vicino al veicolo (altri veicoli, marciapiede, ecc.)', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '36', '280', '5', 'L’EBD (distribuzione elettronica della frenata) è un sistema elettronico che ripartisce l’azione frenante sulle ruote evitando il bloccaggio delle ruote più scariche', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '36', '281', '6', 'L’EBD (distribuzione elettronica della frenata) è un sistema elettronico che ripartisce l’azione frenante, trasferendo pertanto maggiore forza frenante sulle ruote più scariche', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '36', '282', '7', 'I pneumatici PAX system in caso di perdita di pressione evitano che il pneumatico si stacchi del cerchio e consente di percorrere, a velocità ridotta, circa 2.000 km di strada', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '36', '283', '8', 'Il sistema di avviso di disattenzione e stanchezza del conducente (DDAW) è previsto per l’omologazione dei veicoli a motore delle categorie M e N con una velocità massima di progetto che supera i 200 km/h', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '37', '', '', 'Sistemi avanzati di assistenza alla guida (Adas)', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '37', '284', '1', 'Il TMPS o TPMS (sistema monitoraggio pressione pneumatici) indica in tempo reale al conducente tramite un indicatore o una spia di segnalazione se la pressione dei pneumatici si abbassa', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '37', '285', '2', 'I fari adattivi sono proiettori che adattano il fascio di luce per mantenere la strada sempre correttamente illuminata', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '37', '286', '3', 'Il sistema di avviso di disattenzione e stanchezza del conducente (DDAW) analizza altri sistemi del veicolo per il rilevamento degli indicatori di guida in condizioni di stanchezza', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '37', '287', '4', 'L’HHC (hill holder control -sistema di assistenza alla partenza in salita) è un sistema elettronico che blocca i freni quando il veicolo è fermo in salita, impedendo che arretri', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '37', '288', '5', 'L’HHC (hill holder control -sistema di assistenza alla partenza in salita) è un sistema elettronico che entra in funzione solo quando il veicolo è fermo in salita su una strada con pendenza maggiore del 15%', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '37', '289', '6', 'Il CBC (sistema elettronico controllo imbardata) è un sistema elettronico che favorisce i fenomeni di imbardata durante la marcia dei veicoli', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '37', '290', '7', 'Il CDC (sistema di controllo continuo dei sobbalzi), garantisce condizioni ottimali di aderenza delle ruote e comfort di marcia, intervenendo continuamente sull’accelerazione del veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '37', '291', '8', 'L’APA (active park assist) è un sistema elettronico che coadiuva il conducente a stabilizzare il veicolo nella traiettoria scelta', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '38', '', '', 'Limitatore di velocità', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '38', '292', '1', 'Il limitatore di velocità è obbligatorio per tutti i veicoli delle categorie M2, M3, N2, N3', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '38', '293', '2', 'Il limitatore di velocità è obbligatorio per tutti gli autocarri con massa a pieno carico superiore alle 3,5 t', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '38', '294', '3', 'Il limitatore di velocità è obbligatorio per tutti i veicoli cisterna di massa autorizzata maggiore di 12 t, a prescindere dalla loro data di immatricolazione', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '38', '295', '4', 'Il limitatore di velocità è obbligatorio per tutti i veicoli cisterna immatricolati dopo il 31 dicembre 1987, a prescindere dalla loro massa massima autorizzata', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '38', '296', '5', 'Il limitatore di velocità è obbligatorio per i veicoli delle categorie M2, M3 e consigliato per i veicoli delle categorie N2, N3', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '39', '', '', 'ITS applicati alle infrastrutture - 01', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '39', '297', '1', 'In un contesto ITS i “permessi commerciabili di guida” sono intesi come permessi acquisibili dai viaggiatori in cambio di comportamenti “virtuosi”', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '39', '298', '2', 'I sistemi telematici per i trasporti possono permettere al gestore di flotte di conoscere la situazione dei veicoli o mezzi, determinare le rotte più efficienti', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '39', '299', '3', 'Gli ATIS (Advanced Traveller Information Systems) hanno come carattere distintivo quello di influenzare le scelte di viaggio come, ad esempio, le scelte di percorso', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '39', '300', '4', 'Gli ITS (Intelligent Transport Systems) contribuiscono a migliorare la sicurezza e protezione dei veicoli e delle merci', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '39', '301', '5', 'Con ITS (Intelligent Transport Systems) s’intende “l’integrazione della telematica con l’ingegneria dei trasporti, per la pianificazione, progettazione, esercizio, manutenzione e gestione dei sistemi di trasporto', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '39', '302', '6', 'Gli ITS (Intelligent Transport Systems) contribuiscono a migliorare la sicurezza della guida e all’incolumità delle persone', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '39', '303', '7', 'Gli ITS (Intelligent Transport Systems) non sono rilevanti nell’ambito della efficienza dei sistemi di trasporto per i passeggeri e le merci', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '39', '304', '8', 'I rilevamenti dei dati di traffico possono essere effettuati solo tramite strumenti inseriti nella pavimentazione (intrusivi, GD)', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '39', '305', '9', 'I sistemi telematici per i trasporti possono permettere al conducente di scegliere e utilizzare le varie opportunità di viaggio secondo i suoi desideri e le sue passioni', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '40', '', '', 'ITS applicati alle infrastrutture - 02', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '40', '306', '1', 'Ai fini di un trasporto multimodale ed intermodale di merci senza ostruzioni è importante l’automazione nella movimentazione delle merci, per quanto possibile', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '40', '307', '2', 'Ai fini di un trasporto multimodale ed intermodale di merci senza ostruzioni è importante il servizio di monitoraggio remoto soprattutto per le materie pericolose', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '40', '308', '3', 'I sistemi di localizzazione automatica servono per conoscere la posizione di una persona, di un veicolo o di una merce durante lo spostamento, nel tentativo di soddisfare uno o più requisiti della mobilità o della logistica', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '40', '309', '4', 'Per risolvere i maggiori problemi dei sistemi di trasporto le teorie economiche suggeriscono l’abbandono di approcci “bypricing” (attraverso la tariffazione) e “by-quantity” (attraverso la limitazione diretta all’utilizzo)', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '40', '310', '5', 'Ai fini di un trasporto multimodale ed intermodale di merci senza ostruzioni non è importante la conoscenza, da parte degli operatori, della posizione della merce e di orari, traffico, spazi disponibili', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '41', '', '', 'Sistemi di identificazione automatica AVIS, AVI, AEI', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '41', '311', '1', 'I sistemi di identificazione automatica (AIS, AVI, AEI) sono finalizzati al riconoscimento inequivocabile di un passeggero o della merce', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '41', '312', '2', 'Uno dei sistemi disponibili per l’identificazione automatica finalizzata al riconoscimento di un passeggero è il codice a barre lineare e bidimensionale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '41', '313', '3', 'L’identificazione automatica può essere finalizzata ad esempio alla necessità di associare della documentazione alla merce in transito', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '41', '314', '4', 'L’identificazione automatica non rileva informazioni inerenti al conducente, ai viaggiatori e all’unità di trasporto (mono o multimodale)', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '41', '315', '5', 'L’identificazione automatica può essere finalizzata al riempimento di una unità di carico ma non ai fini di puro controllo e verifica', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '41', '316', '6', 'La gestione delle flotte attraverso la localizzazione automatica non rappresenta uno dei requisiti della mobilità del trasporto di merci', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '42', '', '', 'Sistemi intelligenti di controllo e gestione del del traffico', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '42', '317', '1', 'Gli ATMS (Advanced Traffic Management Systems) sono sistemi che, ad esempio, agiscono attraverso il controllo semaforico delle intersezioni e delle regole di deflusso/circolazione', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '42', '318', '2', 'Nel sistema ATMS (sistema avanzato di gestione del traffico) il sistema di monitoraggio acquisisce, mediante la rete di sensori, informazioni sull’ambiente circostante', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '42', '319', '3', 'Il sistema ATMS si caratterizza e si distingue per un sistema di monitoraggio ma non è caratterizzato da un sistema di sorveglianza', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '42', '320', '4', 'L’ATDM (sistemi avanzati di controllo della domanda di mobilità) sono sistemi che tendono a modificare le scelte di guida (comportamento al volante) in funzione del contesto che influenza la marcia del veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '43', '', '', 'Sistemi di rilevamento dei dati di traffico', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '43', '321', '1', 'Tra i principali strumenti o tecnologie disponibili per i rilevamenti dei dati di traffico possiamo annoverare le spire induttive, microonde, infrarossi e ultrasuoni', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '43', '322', '2', 'Tra le applicazioni dei sistemi di trasporto “intelligenti” al controllo del traffico autostradale, il controllo delle corsie può essere integrato con i sistemi di informazione agli utenti', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '43', '323', '3', 'Nell’ambito della gestione dinamica delle corsie, il controllo delle corsie ha l’obiettivo di indicare conducente la corsia da tenere, quando le condizioni di traffico, di viabilità o ambientali non gli consentono o gli rendono difficile la scelta corretta', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '43', '324', '4', 'Con la gestione dinamica delle corsie in una rete stradale urbana è possibile proteggere i veicoli adibiti al trasporto pubblico dai ritardi dovuti alla congestione', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '43', '325', '5', 'Tra i sensori automatici per il rilievo del traffico, le spire induttive trovano applicazione per il rilievo della velocità del veicolo, ma non della lunghezza e numero di assi', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '43', '326', '6', 'Tra le applicazioni dei sistemi di trasporto “intelligenti” al controllo del traffico autostradale non è previsto il controllo degli accessi alle rampe', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '43', '327', '7', 'Tra le applicazioni dei sistemi di trasporto “intelligenti” al controllo del traffico autostradale, lo scopo del controllo degli accessi alle rampe è la verifica degli incassi dei pedaggi', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '43', '328', '8', 'Il controllo dinamico del flusso sulle corsie autostradali è una forma di regolazione del traffico autostradale che avviene senza disposizioni o indicazioni appropriate ai conducenti', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '43', '329', '9', 'Nei sistemi di regolazione dinamica delle reti semaforiche, la successione e la durata delle diverse fasi è predeterminata in funzione di osservazioni statistiche dei flussi di traffico delle diverse correnti', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '44', '', '', 'Sistemi di diagnosi e previsioni del traffico', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '44', '330', '1', 'I modelli di simulazione del traffico, riproducendo il fenomeno fisico, forniscono stime attendibili ed accurate in situazioni anomale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '44', '331', '2', 'L’ITS (sistemi intelligenti nei trasporti) è costituito da un sistema di controllo e regolazione (ATMS -sistema avanzato di gestione del traffico) e da un sistema di informazione agli utenti (ATIS -sistema avanzato di informazione sul traffico)', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '44', '332', '3', 'Nell’ambito dei sistemi di diagnosi e previsioni del traffico, la stima delle condizioni di traffico attuali -o diagnosi -consiste nell’identificazione, qualitativa o quantitativa, della situazione di traffico', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '44', '333', '4', 'Nell’ambito dei sistemi di diagnosi e previsioni del traffico, la stima delle condizioni di traffico attuali -o diagnosi -è necessaria per fornire le informazioni agli utenti', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '44', '334', '5', 'Nell’ambito dei sistemi di diagnosi e previsioni del traffico, lo sviluppo di tecnologie di identificazione dei veicoli consente la misura diretta dei tempi di percorrenza di un campione di veicoli', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '44', '335', '6', 'Nell’ambito dei sistemi di diagnosi e previsioni del traffico, la prognosi, o previsione delle condizioni di traffico, consiste nell’identificazione, qualitativa o quantitativa, della situazione di traffico attuale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '44', '336', '7', 'Nell’ambito dei sistemi di diagnosi e previsioni del traffico, i metodi statistici di analisi dei dati hanno lo svantaggio di essere complessi e richiedono tempi di elaborazione molto lunghi', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '44', '337', '8', 'I modelli di simulazione del traffico, richiedendo la simulazione della rete o di una parte di essa, hanno tempi di elaborazione rapidi e non necessitano di un accurato processo di calibrazione', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '45', '', '', 'Sorveglianza con riconoscimento automatico degli incidenti', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '45', '338', '1', 'L’obiettivo dei metodi diagnostici per il riconoscimento automatico degli incidenti è di individuare, prontamente e senza la necessità dell’intervento di un operatore, il verificarsi di incidenti veicolari', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '45', '339', '2', 'I benefici attesi dai sistemi di riconoscimento automatico degli incidenti riguardano sia la riduzione della congestione che la sicurezza', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '45', '340', '3', 'Il sistema di sorveglianza con riconoscimento automatico degli incidenti, per essere efficace, deve possedere i requisiti di rapidità, accuratezza e automatismo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '45', '341', '4', 'Un pronto e affidabile riconoscimento degli incidenti non consente di attuare una rapida rimozione degli intralci alla circolazione', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '45', '342', '5', 'Le prestazioni del sistema di sorveglianza con riconoscimento automatico degli incidenti non dipendono dalla qualità e quantità di informazioni fornite dal sistema di sorveglianza', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '46', '', '', 'Uso del cambio di velocità - potenza costante', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '46', '343', '1', 'Il cambio di velocità è necessario al fine di ottimizzare l’utilizzo del motore in funzione della velocità del veicolo, della pendenza della strada, della resistenza aerodinamica e del carico', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '46', '344', '2', 'In generale, il conducente deve scegliere la marcia più alta possibile per la velocità alla quale ha deciso di viaggiare', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '46', '345', '3', 'In generale, il conducente deve scegliere una marcia tale che mantenga i giri del motore nell’intervallo ottimale di minor consumo specifico indicato dal contagiri', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '46', '346', '4', 'In ogni condizione di marcia occorre mantenere un numero di giri del motore prossimo a quello di minor consumo specifico', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '46', '347', '5', 'Occorre inserire una marcia più bassa se il numero di giri del motore scende troppo sotto quello di coppia massima', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '46', '348', '6', 'Occorre inserire una marcia più alta se il numero di giri del motore scende troppo sotto quello di coppia massima', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '46', '349', '7', 'Il motore deve essere utilizzato ad un regime (giri/minuto) compreso tra quello di coppia minima e quello di potenza massima in cui si ottengono il migliore rendimento e il minor consumo specifico di carburante', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '46', '350', '8', 'Inserendo una marcia più alta diminuisce il coefficiente di penetrazione aerodinamica (CX)', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '46', '351', '9', 'Il cambio di velocità consente di aumentare la forza di trazione alle alte velocità', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '47', '', '', 'Uso del cambio di velocità - variazioni di pendenza e di carico - 01', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '47', '352', '1', 'Nelle discese ripide bisogna impiegare le marce basse per sfruttare l’effetto frenante del motore', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '47', '353', '2', 'In discesa, l’uso di una marcia bassa consente di sfruttare l’effetto frenante del motore', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '47', '354', '3', 'In discesa l’uso di una marcia bassa (ridotta) consente di stabilizzare la velocità del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '47', '355', '4', 'In discesa l’uso di una marcia bassa (ridotta) consente di sfruttare l’effetto frenante del motore', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '47', '356', '5', 'In discesa l’uso di una marcia bassa (ridotta) aumenta gli spazi di frenatura', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '47', '357', '6', 'In discesa l’uso di una marcia bassa (ridotta) fa aumentare il consumo delle pastiglie dei freni', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '48', '', '', 'Uso del cambio di velocità - variazioni di pendenza e di carico - 02', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '48', '358', '1', 'Se aumenta la pendenza della strada, può essere opportuno ridurre la marcia', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '48', '359', '2', 'Nelle salite percorse con il veicolo carico è opportuno mantenere il motore a un numero di giri compreso nell’intervallo ottimale indicato dal contagiri', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '48', '360', '3', 'Nelle salite percorse con il veicolo carico è opportuno cambiare la marcia quando l’indicatore del contagiri esce dall’intervallo ottimale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '48', '361', '4', 'Nelle salite percorse con il veicolo carico occorre una marcia bassa, che corrisponde una forza di trazione maggiore', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '48', '362', '5', 'Nelle salite percorse con il veicolo carico non si devono mai usare le marce ridotte', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '48', '363', '6', 'Occorre sempre inserire una marcia più alta per affrontare salite con il veicolo carico', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '48', '364', '7', 'Occorre inserire una marcia più alta per affrontare lunghe discese e sfruttare l’effetto frenante del motore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '48', '365', '8', 'Se aumenta la pendenza della strada è opportuno innestare marce più alte', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '49', '', '', 'Pressione e controllo pneumatici - 01', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '49', '366', '1', 'Prima della partenza vanno controllate le condizioni dei pneumatici', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '49', '367', '2', 'Prima della partenza va controllato che i pneumatici non siano sgonfi', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '49', '368', '3', 'La diversa pressione di gonfiaggio dei pneumatici di uno stesso asse può comportare uno scostamento dalla traiettoria rettilinea', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '49', '369', '4', 'La pressione dei pneumatici va controllata periodicamente', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '49', '370', '5', 'La pressione dei pneumatici va controllata con pneumatici freddi', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '49', '371', '6', 'La pressione dei pneumatici deve essere controllata una volta l’anno', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '49', '372', '7', 'Lo spessore del battistrada dei pneumatici non può essere inferiore a 1,6 mm, ma soltanto sulle ruote posteriori', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '49', '373', '8', 'La corretta pressione di gonfiaggio dei pneumatici è indipendente dal carico del veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '49', '374', '9', 'I pneumatici, se montati su ruote gemellate, devono essere gonfiate ad una pressione inferiore da quella prevista dal costruttore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '50', '', '', 'Pressione e controllo pneumatici - 02', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '50', '375', '1', 'Le ruote gemellate, se sgonfie, possono deteriorarsi a causa del loro reciproco sfregamento', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '50', '376', '2', 'Un’insufficiente pressione di gonfiaggio dei pneumatici provoca un eccessivo consumo dei bordi del battistrada', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '50', '377', '3', 'La mancata equilibratura del pneumatico può provocare un anomalo consumo del battistrada', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '50', '378', '4', 'L’eccessiva pressione di gonfiaggio dei pneumatici ne provoca un maggiore consumo della parte centrale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '50', '379', '5', 'Nel caso di foratura di un pneumatico, la situazione è da considerarsi di emergenza se il veicolo non ha ruote gemellate', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '50', '380', '6', 'L’insufficiente pressione dei pneumatici diminuisce la possibilità dell’insorgenza del fenomeno dell’aquaplaning', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '50', '381', '7', 'Un’insufficiente pressione di gonfiaggio dei pneumatici ne provoca un eccessivo consumo della parte centrale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '50', '382', '8', 'L’eccessiva pressione di gonfiaggio dei pneumatici ne provoca un maggiore consumo sui fianchi', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '51', '', '', 'Angolo di deriva - 01', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '51', '383', '1', 'La deriva dipende dalla pressione dei pneumatici', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '51', '384', '2', 'La deriva dipende dalla forza trasversale che agisce sulle ruote in curva', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '51', '385', '3', 'La deriva dei pneumatici è influenzata dall’angolo di campanatura delle ruote che può favorire il cedimento dei fianchi', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '51', '386', '4', 'La deriva dei pneumatici è lo scostamento della traiettoria del pneumatico, rispetto a quella impostata del conducente', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '51', '387', '5', 'La deriva dei pneumatici dipende dal carico del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '51', '388', '6', 'La deriva dei pneumatici è l’angolo che si crea tra due assi del veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '51', '389', '7', 'La deriva dei pneumatici viene misurata in occasione della revisione periodica del veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '51', '390', '8', 'La maggior deriva delle ruote posteriori rispetto a quelle anteriori diminuisce il comportamento sovrasterzante del veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '51', '391', '9', 'La corretta manutenzione del veicolo impedisce il verificarsi del fenomeno della deriva', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '52', '', '', 'Angolo di deriva - 02', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '52', '392', '1', 'L’angolo di deriva è causato da forze trasversali che agiscono sui pneumatici', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '52', '393', '2', 'L’angolo di deriva è l’angolo che si crea tra la traiettoria ideale e quella reale del pneumatico', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '52', '394', '3', 'L’angolo di deriva è legato alla deformazione della carcassa del pneumatico', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '52', '395', '4', 'L’angolo di deriva dei pneumatici è indipendente dalla pressione di gonfiaggio dei pneumatici', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '52', '396', '5', 'L’angolo di deriva dei pneumatici è causato dalla forza motrice nei veicoli a trazione anteriore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '52', '397', '6', 'L’angolo di deriva dei pneumatici è maggiore nei veicoli muniti di servosterzo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '52', '398', '7', 'L’angolo di deriva non dipende dal carico che grava sui singoli assi', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '53', '', '', 'Sovrasterzo', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '53', '399', '1', 'Un veicolo si dice in sovrasterzo quando, ad esempio, per motivi di carico tende a deviare la traiettoria verso l’interno della curva e curva di più', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '53', '400', '2', 'Un veicolo si dice sovrasterzante quando l’angolo di deriva dei pneumatici dell’asse posteriore è maggiore di quello dei pneumatici dell’asse anteriore', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '53', '401', '3', 'Nel sovrasterzo il veicolo tende a chiudere la traiettoria in curva per l’effetto della maggiore deriva delle ruote posteriori', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '53', '402', '4', 'Nel sovrasterzo il veicolo tende a fare perno sulle ruote anteriori e ad andare in testacoda', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '53', '403', '5', 'Nel sovrasterzo il veicolo curva più di quanto voluto dal conducente', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '53', '404', '6', 'Nel sovrasterzo il veicolo tende a girare su sé stesso facendo perno sulle ruote anteriori', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '53', '405', '7', 'Nel sovrasterzo il veicolo curva meno di quanto voluto dal conducente', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '53', '406', '8', 'Nel sovrasterzo il veicolo tende a proseguire per la tangente allargando la curva', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '53', '407', '9', 'Nel sovrasterzo il veicolo tende a chiudere la traiettoria della curva per effetto della maggiore deriva delle ruote anteriori rispetto a quella delle ruote posteriori', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '53', '408', '10', 'Nel sovrasterzo il veicolo non è in grado di girare a sufficienza', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '54', '', '', 'Per correggere il sovrasterzo di un veicolo', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '54', '409', '1', 'Per correggere il sovrasterzo di un veicolo a trazione posteriore si dovrà rilasciare l’acceleratore e agevolare la ripresa di direzionalità del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '54', '410', '2', 'Per correggere il sovrasterzo occorre applicare correttamente il controsterzo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '54', '411', '3', 'Per correggere il sovrasterzo può essere opportuno sterzare in senso opposto a quello della curva', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '54', '412', '4', 'Per correggere il sovrasterzo può essere richiesta una momentanea sterzata verso l’esterno della curva', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '54', '413', '5', 'Per correggere il sovrasterzo di può essere opportuno accelerare senza agire sul volante', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '54', '414', '6', 'Per correggere il sovrasterzo è necessario un momentaneo e rapido intervento del volante verso l’interno della curva', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '54', '415', '7', 'In caso di comportamento sovrasterzante del veicolo bisogna frenare a fondo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '55', '', '', 'Sottosterzo ', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '55', '416', '1', 'Quando il veicolo ha un comportamento sottosterzante tende a curvare meno di quanto voluto dal conducente', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '55', '417', '2', 'Un veicolo si dice sottosterzante quando l’angolo di deriva dei pneumatici dell’asse anteriore è superiore a quello dell’asse posteriore', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '55', '418', '3', 'Gli ammortizzatori scarichi possono aumentare il comportamento sottosterzante del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '55', '419', '4', 'Il sottosterzo del veicolo in curva aumenta con l’aumentare della velocità', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '55', '420', '5', 'Il sottosterzo è causato dalla maggior deriva delle ruote anteriori rispetto alle ruote posteriori', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '55', '421', '6', 'Il sottosterzo del veicolo compensa l’effetto della deriva dei pneumatici', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '55', '422', '7', 'L’aumento del carico sull’asse posteriore del veicolo aumenta il sottosterzo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '55', '423', '8', 'Il comportamento sottosterzante del veicolo non è influenzato dalla pressione di gonfiaggio dei pneumatici', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '55', '424', '9', 'Quando il veicolo ha un comportamento sottosterzante tende a curvare più di quanto richiesto dal conducente', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '56', '', '', 'Per correggere il sottosterzo di un veicolo', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '56', '425', '1', 'Per correggere il sottosterzo occorre un momentaneo intervento sul volante verso l’interno della curva', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '56', '426', '2', 'Per correggere il sottosterzo occorre intervenire sullo sterzo e sull’acceleratore per ridare la corretta direzionalità al veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '56', '427', '3', 'Nel sottosterzo, il veicolo curva meno di quanto di quanto voluto dal conducente', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '56', '428', '4', 'Nel sottosterzo il veicolo tende ad allargare la curva', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '56', '429', '5', 'Per correggere il sottosterzo occorre frenare a fondo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '56', '430', '6', 'Il sottosterzo aumenta la stabilità del veicolo in curva', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '56', '431', '7', 'Il sottosterzo del veicolo aumenta nelle curve in salita', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '57', '', '', 'Spazio di frenatura', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '57', '432', '1', 'Entro i limiti di aderenza lo spazio di frenata non dipende dal peso del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '57', '433', '2', 'Lo spazio di frenatura è proporzionale al quadrato della velocità', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '57', '434', '3', 'A parità di forza frenante, lo spazio di frenatura è maggiore con l’aumentare dell’inerzia del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '57', '435', '4', 'A parità di forza frenante, lo spazio di frenatura è maggiore con l’aumentare della massa del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '57', '436', '5', 'Lo spazio di frenatura è inversamente proporzionale al quadrato della velocità', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '58', '', '', 'Norme di comportamento', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '58', '437', '1', 'Il conducente di un veicolo pesante, superando pedoni e ciclisti deve tenersi ad adeguata distanza laterale da essi', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '58', '438', '2', 'La manovra di svolta dei veicoli alle intersezioni è condizionata dalla lunghezza del loro passo e del loro sbalzo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '58', '439', '3', 'Per svoltare in corrispondenza di un’intersezione è opportuno che il conducente dell’autobus attenda che sia libera l’area dell’intersezione', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '58', '440', '4', 'Quando si entra in una galleria occorre aver preso nota delle disposizioni particolari previste dall’Ente proprietario', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '58', '441', '5', 'Quando si entra in una galleria autostradale occorre accendere tutte le luci interne della cabina', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '58', '442', '6', 'Percorrendo le rotatorie a due corsie, i veicoli pesanti devono circolare sulla corsia di sinistra e non possono mai impegnare la corsia di destra', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '58', '443', '7', 'Nelle svolte a destra è consentito salire sul marciapiede con la ruota posteriore interna a bassa velocità', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '58', '444', '8', 'Per i veicoli di lunghezza superiore a 7 metri, nella svolta a destra, è obbligatorio spostarsi sul lato sinistro della carreggiata', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '59', '', '', 'Dinamica dello sbalzo', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '59', '445', '1', 'Lo sbalzo posteriore di un veicolo a due assi è la distanza tra il centro dell’asse posteriore e il limite della sagoma posteriore del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '59', '446', '2', 'Il rischio di interferenza tra la parte posteriore della carrozzeria e il piano stradale, al termine di una rampa percorsa in discesa, aumenta con la lunghezza dello sbalzo posteriore', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '59', '447', '3', 'Il rischio di interferenza tra la parte posteriore della carrozzeria e il piano stradale all’inizio di una rampa percorsa in salita aumenta con la lunghezza dello sbalzo posteriore', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '59', '448', '4', 'Il rischio di interferenza tra la parte posteriore della carrozzeria e il piano stradale al termine di una rampa percorsa in discesa aumenta con la lunghezza dello sbalzo anteriore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '59', '449', '5', 'Il rischio di interferenza tra la parte posteriore della carrozzeria e il piano stradale all’inizio di una rampa percorsa in salita aumenta con la lunghezza del passo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '59', '450', '6', 'Aumentando lo sbalzo posteriore aumenta l’effetto sottosterzante del veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '59', '451', '7', 'Lo sbalzo di un veicolo a due assi è la distanza fra gli assi stessi', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '60', '', '', 'Incrocio malagevole  - 01', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '60', '452', '1', 'Quando l’incrocio tra due veicoli non sia possibile per la presenza di un ostacolo, il conducente la cui marcia è ostacolata, deve dare la precedenza ai veicoli provenienti in senso opposto', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '60', '453', '2', 'Il conducente la cui marcia è ostacolata da un cantiere, deve dare la precedenza ai veicoli che prevengono in senso opposto', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '60', '454', '3', 'Su strade a forte pendenza, in caso di incrocio malagevole, il veicolo che procede in salita, se dispone di una piazzola di sosta, deve dare la precedenza', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '60', '455', '4', 'Su strade a forte pendenza, in caso di incrocio malagevole, il veicolo che procede in discesa, deve di regola arrestarsi e dare la precedenza', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '60', '456', '5', 'Se sulle strade di montagna l’incrocio tra un veicolo di m.m.c. inferiore a 3,5 t con altro di m.m.c. superiore a 3,5 t è malagevole, la retromarcia deve essere effettuata dal veicolo più leggero', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '60', '457', '6', 'Su strade a forte pendenza, in caso di incrocio malagevole, il veicolo che procede in salita, deve di regola arrestarsi e dare la precedenza', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '60', '458', '7', 'Su strade a forte pendenza, in caso di incrocio malagevole, il veicolo che procede in salita, se dispone di una piazzola di sosta, ha la precedenza', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '60', '459', '8', 'Quando l’incrocio tra due veicoli non sia possibile per la presenza di un ostacolo, il conducente la cui marcia è ostacolata, ha la precedenza nei confronti dei veicoli provenienti in senso opposto', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '60', '460', '9', 'Il conducente la cui marcia è ostacolata da un cantiere, ha la precedenza nei confronti dei veicoli che prevengono in senso opposto', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '61', '', '', 'Incrocio malagevole - 02', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '61', '461', '1', 'Se un’autovettura e un autotreno si incrociano su strade di montagna dove non sia possibile il loro transito contemporaneo, l’eventuale manovra di retromarcia compete all’autovettura', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '61', '462', '2', 'Se un autotreno ed un autobus si incrociano su una strada in pendenza dove non sia possibile il loro transito contemporaneo, la manovra di retromarcia compete all’autobus', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '61', '463', '3', 'Se un autocarro o un autobus si incrociano su strade di montagna dove non sia possibile il loro transito contemporaneo, l’eventuale manovra di retromarcia compete all’autocarro', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '61', '464', '4', 'Se due autobus si incrociano su strade a forte pendenza, dove non sia possibile il loro transito contemporaneo, la manovra di retromarcia, se necessaria, compete al veicolo che procede in discesa', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '61', '465', '5', 'Se due autobus si incrociano su strade a forte pendenza, dove non sia possibile il loro transito contemporaneo, la manovra di retromarcia, se necessaria, compete al veicolo che lo ritiene opportuno', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '61', '466', '6', 'Se due autobus si incrociano su strade forte pendenza, dove non sia possibile il loro transito contemporaneo, la manovra di retromarcia, se necessaria, compete al veicolo che procede in salita', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '61', '467', '7', 'Se un autotreno ed un autobus si incrociano su una strada in pendenza dove non sia possibile il loro transito contemporaneo, la manovra di retromarcia compete all’autotreno', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '61', '468', '8', 'Se un autocarro o un autobus si incrociano su strade di montagna dove non sia possibile il loro transito contemporaneo, l’eventuale manovra di retromarcia compete all’autobus', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '61', '469', '9', 'Se sulle strade di montagna l’incrocio tra un veicolo di massa massima complessiva è inferiore al 3,5 t con altro di massa massima complessiva superiore a 3,5 t sia malagevole, la retromarcia deve essere effettuata dal veicolo più pesante', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '61', '470', '10', 'Se un’autovettura e un autotreno si incrociano su strade di montagna dove non sia possibile il loro transito contemporaneo, l’eventuale manovra di retromarcia compete all’autotreno', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '62', '', '', 'Comportamento in caso di incidente', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '62', '471', '1', 'Se non si riesce a liberare la carreggiata dal veicolo incidentato, bisogna attendere sul posto segnalando il pericolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '62', '472', '2', 'Se non si riesce a liberare la carreggiata dal veicolo incidentato, bisogna chiamare il soccorso stradale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '62', '473', '3', 'Nel caso di incidente stradale con danni alle sole cose si devono chiamare i Carabinieri', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '62', '474', '4', 'Nel caso di incidente stradale conviene allontanarsi per non farsi identificare', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '62', '475', '5', 'Nel caso di incidente stradale bisogna avvertire il proprio legale prima di qualsiasi altra azione', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '62', '476', '6', 'L’autorità deve essere avvisata se la caduta di materiale sulla strada è avvenuta di notte e non se è avvenuta di giorno', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '63', '', '', 'Comportamento in caso di incidente - 01', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '63', '477', '1', 'In un incidente stradale, se non si può spostare il veicolo, bisogna immediatamente segnalare il pericolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '63', '478', '2', 'In un incidente stradale, in caso di versamento di sostanze viscide sulla carreggiata, si deve segnalare il pericolo ai veicoli che sopraggiungono', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '63', '479', '3', 'In un incidente stradale senza feriti si deve cercare di sgomberare la carreggiata per non arrecare pericolo per sé o per gli altri', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '63', '480', '4', 'In un incidente stradale senza feriti si deve cercare di non intralciare la circolazione', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '63', '481', '5', 'In caso di incidente senza feriti è vietato ai conducenti di scendere dai veicoli fino all’arrivo del carro attrezzi', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '63', '482', '6', 'In caso di incidente senza feriti, i conducenti devono annotare il numero dei chilometri percorsi dai veicoli', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '63', '483', '7', 'In un incidente stradale senza feriti è obbligatorio chiamare gli agenti del traffico', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '63', '484', '8', 'In un incidente stradale senza feriti, bisogna comunque chiamare l’ambulanza telefonando al numero 118', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '64', '', '', 'Comportamento in caso di incidente - 02', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '64', '485', '1', 'In caso di incidente, se non si riesce a liberare la carreggiata dai veicoli, si può chiamare la Polizia stradale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '64', '486', '2', 'In caso di incidente, i conducenti dei veicoli coinvolti devono cercare di mantenere la calma', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '64', '487', '3', 'In caso di incidente i conducenti dei veicoli coinvolti devono rilevare tutti gli elementi utili per ricostruirne la dinamica', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '64', '488', '4', 'In caso di incidente, i conducenti dei veicoli coinvolti devono constatare i danni dopo aver liberato la carreggiata', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '64', '489', '5', 'In caso di incidente i conducenti dei veicoli coinvolti devono rilevare i danni visibili', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '64', '490', '6', 'In caso di incidente i conducenti dei veicoli coinvolti devono prima compilare il modulo di constatazione amichevole di incidente e poi rimuovere i veicoli anche se intralciano il traffico', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '64', '491', '7', 'In caso di incidente i conducenti dei veicoli coinvolti devono sottoscrivere il modulo di constatazione amichevole di incidente in bianco, facendolo poi compilare dall’agenzia di assicurazione', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '64', '492', '8', 'In caso di incidente stradale è vietato scendere dal veicolo prima che sopraggiunga la polizia', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '64', '493', '9', 'In caso di incidente stradale, è obbligatoria la presenza di un agente della polizia per compilare il modulo di constatazione amichevole di incidente', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '65', '', '', 'Comportamento in caso di incidente', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '65', '494', '1', 'Una delle cose da fare in situazioni di emergenza e di cercare di evitare che altri conducenti vengano coinvolti', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '65', '495', '2', 'In caso di grosse perdite di materie pericolose occorre avvisare l’autorità fornendo tutte le indicazioni possibili sulla materia fuoriuscita', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '65', '496', '3', 'Negli incidenti stradali una situazione può essere di emergenza anche se non vi sono feriti', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '65', '497', '4', 'In un incidente, se si sono versate sostanze viscide, è opportuno spargerle su tutta la sede stradale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '65', '498', '5', 'In caso di situazioni di emergenza, se si è in autostrada, è sufficiente attendere il passaggio del primo veicolo di servizio o di polizia stradale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '66', '', '', 'La segnalazione di pericolo tramite il segnale di veicolo fermo', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '66', '499', '1', 'La segnalazione di pericolo può consistere nella collocazione del segnale di veicolo fermo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '66', '500', '2', 'La segnalazione di pericolo serve ad allertare i conducenti dei veicoli che sopraggiungono', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '66', '501', '3', 'La segnalazione di pericolo tramite il segnale di veicolo fermo ha lo scopo di rallentare il transito sulla zona pericolosa', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '66', '502', '4', 'La segnalazione di pericolo tramite il segnale di veicolo fermo si rivolge ai conducenti che sopraggiungono sul luogo dell’incidente', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '66', '503', '5', 'La segnalazione di pericolo tramite il segnale di veicolo fermo è vietata nei dossi', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '66', '504', '6', 'La segnalazione di pericolo tramite il segnale di veicolo fermo è vietata nelle ore diurne', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '66', '505', '7', 'La segnalazione di pericolo tramite il segnale di veicolo fermo è inutile in caso di incidente stradale senza feriti', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '66', '506', '8', 'La segnalazione di pericolo può essere attuata alzando al massimo il volume dell’autoradio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '66', '507', '9', 'La segnalazione di pericolo ha lo scopo di impedire il transito nei cantieri stradali', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '67', '', '', ' Comportamento in caso di incidente', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '67', '508', '1', 'Nel caso di incidente stradale è necessario cercare di contenere i pericoli ambientali, potendolo fare in condizioni di sicurezza', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '67', '509', '2', 'Nel caso di incidente stradale il conducente deve verificare che non vi siano fumo o fiamme dai veicoli coinvolti', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '67', '510', '3', 'Nel caso di incidente stradale occorre segnalare il pericolo ai veicoli che sopraggiungono', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '67', '511', '4', 'Nel caso di incidente stradale bisogna avvertire il proprio assicuratore prima di qualsiasi altra azione', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '67', '512', '5', 'Nel caso di incidente stradale è sufficiente segnalare il pericolo con il clacson', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '67', '513', '6', 'Nel caso di incidente stradale si deve chiamare l’ambulanza, anche se non ci sono feriti', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '67', '514', '7', 'In caso di incidente stradale, la prima cosa da fare è allontanarsi per recarsi dai Carabinieri', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '68', '', '', 'Comportamento in caso di incidente - danni a persone', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '68', '515', '1', 'Nel caso di infortunio o malore è opportuno richiedere l’intervento di un medico o di una autoambulanza', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '68', '516', '2', 'Nel caso di infortunio o malore si deve cercare di mantenere la calma', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '68', '517', '3', 'Nel caso di incidente stradale bisogna proteggere un ferito che non si può spostare dalla carreggiata', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '68', '518', '4', 'Una situazione è considerata di emergenza solo quando l’incidente stradale ha provocato più di due feriti', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '68', '519', '5', 'In situazioni di emergenza, nel caso di elevato valore del veicolo o della merce trasportata, il conducente può occuparsi prima delle cose e dopo dei feriti', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '69', '', '', 'Documenti da esibire in caso di incidente stradale - 01', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '69', '520', '1', 'In caso di incidente occorre sempre fornire le proprie generalità agli altri conducenti coinvolti', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '69', '521', '2', 'In caso di incidente senza feriti, i conducenti devono annotare nome della compagnia assicurativa e numero dell’assicurazione degli altri conducenti coinvolti', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '69', '522', '3', 'In caso di incidente senza feriti, i conducenti devono prendere nota degli estremi di patente, veicolo e assicurazione dell’altro conducente coinvolto', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '69', '523', '4', 'In caso di incidente senza feriti, i conducenti coinvolti devono annotare numero di patente con data e luogo di rilascio', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '69', '524', '5', 'In caso di incidente senza feriti, i conducenti devono annotare la data di prima immatricolazione del veicolo indicando giorno, mese e anno', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '69', '525', '6', 'In ogni incidente stradale senza feriti è obbligatorio compilare il modulo di constatazione amichevole di incidente', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '69', '526', '7', 'Nel caso di incidente stradale bisogna fornire le proprie generalità al conducente dell’altro veicolo coinvolto solo in presenza delle forze dell’ordine', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '70', '', '', 'Documenti da esibire in caso di incidente stradale - 02', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '70', '527', '1', 'Quando si verifica un incidente stradale deve essere esibito, tra gli altri, il certificato di assicurazione', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '70', '528', '2', 'Quando si verifica un incidente stradale deve essere esibita, tra l’altro, la carta di circolazione, per identificare l’intestatario del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '70', '529', '3', 'Quando si verifica un incidente stradale devono essere esibiti, tra gli altri, la patente e la carta di circolazione, per identificare, rispettivamente, il conducente e l’intestatario del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '70', '530', '4', 'Quando si verifica un incidente stradale deve essere esibita, tra l’altro, la patente di guida, per l’identificazione del conducente', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '70', '531', '5', 'Quando si verifica un incidente stradale deve essere esibito, tra gli altri, il libretto di uso e manutenzione del veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '70', '532', '6', 'Quando si verifica un incidente stradale deve essere esibito, tra gli altri, il certificato di proprietà', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '70', '533', '7', 'Quando si verifica un incidente stradale deve essere esibita, tra gli altri, l’attestazione di pagamento del bollo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '71', '', '', 'Convenzione di indennizzo diretto (CID) - 01', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '71', '534', '1', 'Per compilare correttamente il modulo CID (constatazione amichevole di incidente) è necessario riportare i numeri di polizza delle assicurazioni', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '71', '535', '2', 'Per compilare correttamente il modulo CID (Convenzione indennizzo diretto) è necessario riportare il nome delle rispettive compagnie di assicurazione', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '71', '536', '3', 'Per compilare correttamente il modulo CID (Convenzione indennizzo diretto) è necessario riportare i nomi dei conducenti, se diversi dagli assicurati', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '71', '537', '4', 'Per compilare correttamente il modulo CID (Convenzione indennizzo diretto) è necessario riportare una descrizione accurata dell’incidente', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '71', '538', '5', 'Per compilare correttamente il modulo CID (Convenzione indennizzo diretto) è necessario riportare i nomi degli assicurati', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '71', '539', '6', 'Per compilare il modulo di constatazione amichevole di incidente correttamente, è necessario riportare il codice fiscale di tutte le persone coinvolte e che hanno assistito all’incidente', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '71', '540', '7', 'Per compilare il modulo di constatazione amichevole di incidente correttamente, è necessario riportare i dati della licenza comunitaria', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '71', '541', '8', 'Per compilare il modulo di constatazione amichevole di incidente correttamente, è necessario riportare gli estremi della carta di qualificazione del conducente', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '71', '542', '9', 'Per compilare il modulo di constatazione amichevole di incidente correttamente, è necessario riportare i dati della carta tachigrafica', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '71', '543', '10', 'Per compilare il modulo di constatazione amichevole correttamente, è necessario riportare il numero della tessera sanitaria dei conducenti dei veicoli coinvolti', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '72', '', '', 'Convenzione di indennizzo diretto (CID) - 02', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '72', '544', '1', 'In caso di incidente senza feriti in Italia, se è coinvolto un veicolo immatricolato in Stato non UE, i conducenti devono annotare anche il numero della carta verde, le generalità del conducente e del proprietario del mezzo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '72', '545', '2', 'In caso di incidente senza feriti, i conducenti devono compilare il modulo di constatazione amichevole di incidente e, se possibile, farla sottoscrivere dalla controparte', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '72', '546', '3', 'La Convenzione indennizzo diretto (CID) può essere attivata quando i veicoli sono regolarmente immatricolati e assicurati', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '72', '547', '4', 'La Convenzione indennizzo diretto (CID) può essere attivata nel caso di un sinistro con soli due veicoli coinvolti', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '72', '548', '5', 'La Convenzione indennizzo diretto (CID) può essere attivata se non sono coinvolti motocicli', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '72', '549', '6', 'La Convenzione indennizzo diretto (CID) può essere attivata se non sono coinvolti autobus', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '72', '550', '7', 'In caso di incidente senza feriti, il modulo di constatazione amichevole di incidente deve essere sottoscritto dagli agenti di polizia municipale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '72', '551', '8', 'In caso di incidente senza feriti, i conducenti non devono compilare il modulo di constatazione amichevole di incidente solamente se uno dei veicoli coinvolti è dotato di rilevatore satellitare', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '72', '552', '9', 'Le firme apposte sul modulo di constatazione amichevole di incidente devono essere autenticate dagli agenti del traffico', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '72', '553', '10', 'Sul modulo di constatazione amichevole di incidente non devono essere riportate le generalità di eventuali testimoni', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '73', '', '', 'Assicurazione RCA - 01', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '73', '554', '1', 'Il contratto di assicurazione per la responsabilità civile copre la responsabilità per danni causati a terzi', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '73', '555', '2', 'Il contratto di assicurazione per la responsabilità civile è stipulato tra la compagnia di assicurazione e l’assicurato', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '73', '556', '3', 'Il Codice delle assicurazioni private prevede il risarcimento da parte della propria compagnia assicuratrice con risarcimento diretto', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '73', '557', '4', 'Il Codice delle assicurazioni private prevede il risarcimento da parte dell’assicurazione della controparte con azione diretta', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '73', '558', '5', 'In un incidente i passeggeri trasportati possono fare da testimoni per mezzo di una dichiarazione scritta', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '73', '559', '6', 'La mancanza di copertura assicurativa RCA (responsabilità civile automobilistica) non comporta alcuna sanzione', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '73', '560', '7', 'I danni per atti vandalici sono risarcibili dall’assicurazione RCA (responsabilità civile automobilistica)', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '73', '561', '8', 'Il furto del veicolo è risarcibile dall’assicurazione RCA (responsabilità civile automobilistica)', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '74', '', '', 'Assicurazione RCA - 02', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '74', '562', '1', 'La mancanza di copertura assicurativa (RCA) comporta una sanzione a carico del proprietario e dell’utilizzatore del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '74', '563', '2', 'I danni derivanti alle cose dall’incidente stradale sono risarcibili dall’assicurazione RCA (responsabilità civile automobilistica)', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '74', '564', '3', 'Il furto del carico del veicolo è risarcibile dall’assicurazione RCA (responsabilità civile automobilistica)', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '74', '565', '4', 'I costi di manutenzione ordinaria del veicolo sono risarcibili dall’assicurazione RCA (responsabilità civile automobilistica)', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '74', '566', '5', 'Le spese di giudizio connesse all’incidente stradale sono risarcibili dall’assicurazione RCA (responsabilità civile automobilistica)', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '75', '', '', 'Assicurazione RCA – risarcimento danni', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '75', '567', '1', 'I soggetti non trasportati che subiscono un danno dalla circolazione del veicolo sono risarcibili dall’assicurazione RCA (responsabilità civile automobilistica)', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '75', '568', '2', 'Il conducente che provoca danni solo materiali in un incidente si libera dall’obbligo del risarcimento rimborsando direttamente il danneggiato', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '75', '569', '3', 'Il conducente che provoca danni solo materiali in un incidente ha una responsabilità civile verso il danneggiato', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '75', '570', '4', 'Il conducente che provoca danni solo materiali in un incidente si può fare assistere da un consulente d’infortunistica stradale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '75', '571', '5', 'Il conducente che provoca danni solo materiali in un incidente può risarcire il danneggiato attraverso l’assicurazione RCA (responsabilità civile automobilistica) obbligatoria per legge', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '75', '572', '6', 'Il conducente che provoca danni solo materiali in un incidente si deve fare assistere da un consulente di investimenti', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '75', '573', '7', 'Il conducente che provoca danni solo materiali in un incidente non è responsabile verso i conducenti danneggiati', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '75', '574', '8', 'Il conducente che provoca danni solo materiali in un incidente non è responsabile verso i terzi trasportati', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '75', '575', '9', 'Il conducente che provoca danni solo materiali in un incidente non è responsabile verso i pedoni danneggiati', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '75', '576', '10', 'Il Codice delle assicurazioni private prevede il risarcimento esclusivamente a seguito di sentenza passata in giudicato', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '76', '', '', 'Soccorrere un infortunato della strada', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '76', '577', '1', 'È sempre necessario soccorrere un infortunato della strada', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '76', '578', '2', 'L’unica eccezione all’obbligo di soccorrere un infortunato è costituita dalle cause di forza maggiore', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '76', '579', '3', 'In caso di incidente, non bisogna spostare il ferito a meno che la posizione non sia pericolosa per la sua incolumità', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '76', '580', '4', 'Il conducente deve prestare il soccorso solo ai passeggeri infortunati del proprio autoveicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '76', '581', '5', 'Può fornire il primo soccorso a un infortunato solo chi abbia frequentato un corso da soccorritore e abbia superato il relativo esame', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '77', '', '', ' Condotta in caso di infortunio o malore', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '77', '582', '1', 'Nel caso di infortunio o malore non si debbono somministrare bevande al soggetto in stato di incoscienza', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '77', '583', '2', 'Nel caso di infortunio o malore si deve restare ad assistere l’infortunato, tranquillizzandolo e rassicurandolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '77', '584', '3', 'Nel caso di infortunio o malore si deve somministrare all’infortunato qualunque tipo di farmaco', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '77', '585', '4', 'Nel caso di infortunio o malore bisogna somministrare bevande alcoliche agli infortunati', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '77', '586', '5', 'Nel caso di infortunio si deve stringere l’arto fratturato con un laccio emostatico', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '78', '', '', 'Condotta in caso di incidente stradale', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '78', '587', '1', 'Nel caso di incidente stradale si deve prestare soccorso ai feriti', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '78', '588', '2', 'Nel caso di incidente stradale non bisogna spostare i feriti che non sono in grado di muoversi, a meno che non siano in grave e imminente pericolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '78', '589', '3', 'Nel caso di incidente stradale bisogna attendere il personale sanitario per lo spostamento dei feriti a meno che non siano in grave ed imminente pericolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '78', '590', '4', 'Nel caso di incidente stradale il conducente deve prima di tutto assicurarsi della propria incolumità', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '78', '591', '5', 'Nel caso di incidente stradale bisogna somministrare alcool agli infortunati in stato di shock', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '78', '592', '6', 'Nel caso di incidente stradale si deve rimuovere subito l’infortunato dalla strada indipendentemente dalle lesioni che presenta', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '78', '593', '7', 'Nel caso in cui l’infortunato riprenda conoscenza, viene meno la necessità di chiamare il 118', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '78', '594', '8', 'In caso di incidente, si ha l’obbligo di spostare il ferito che lo richiede perentoriamente', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '78', '595', '9', 'In caso di incidente, non bisogna in nessun caso spostare il ferito, prima dell’arrivo delle forze dell’ordine', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '78', '596', '10', 'In caso di incidente, lo stato di ebbrezza dell’infortunato comporta il divieto di spostarlo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '79', '', '', 'Condotta primo soccorso', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '79', '597', '1', 'Nel primo soccorso, tra i controlli prioritari vi è quello delle funzioni vitali dell’infortunato', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '79', '598', '2', 'Il primo soccorso è l’aiuto che chiunque può prestare alla persona infortunata', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '79', '599', '3', 'Il primo soccorso è l’aiuto che chiunque può prestare all’infortunato, con molte precauzioni, per non peggiorarne lo stato', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '79', '600', '4', 'Il primo soccorso è l’aiuto immediato ed urgente prestato all’infortunato', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '79', '601', '5', 'Il primo soccorso deve comunque evitare di causare peggioramenti all’infortunato', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '79', '602', '6', 'Il primo soccorso è l’attuare semplici ed immediate misure sanitarie nei riguardi di una persona infortunata', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '79', '603', '7', 'Il primo soccorso inizia con la richiesta di documenti all’infortunato', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '79', '604', '8', 'Il primo soccorso non è un obbligo giuridico', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '79', '605', '9', 'Il primo soccorso deve essere prestato solo da operatori sanitari', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '79', '606', '10', 'Per prestare il primo soccorso è necessario il consenso scritto dell’infortunato', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '80', '', '', ' Primo soccorso ad un infortunato cosciente ', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '80', '607', '1', 'Se l’infortunato è cosciente mantiene tutte le funzioni vitali primarie', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '80', '608', '2', 'Se l’infortunato è cosciente risponde alle domande', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '80', '609', '3', 'Se l’infortunato è cosciente ha delle reazioni a sollecitazioni verbali', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '80', '610', '4', 'Se l’infortunato è cosciente il suo cuore batte e può respirare', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '80', '611', '5', 'Se l’infortunato è cosciente è esclusa la presenza di lesioni interne', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '80', '612', '6', 'Se l’infortunato è cosciente ci si deve accertare che muova in maniera coordinata gli arti', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '80', '613', '7', 'Se l’infortunato è cosciente, il soccorritore può anche abbandonare il luogo dell’incidente', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '81', '', '', 'Primo soccorso ad un infortunato incosciente', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '81', '614', '1', 'Chi presta soccorso ad un infortunato incosciente deve immediatamente accertare se respira', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '81', '615', '2', 'Chi presta soccorso all’infortunato incosciente deve immediatamente verificare se il cuore batte', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '81', '616', '3', 'Se l’infortunato è incosciente bisogna far intervenire quanto prima l’ambulanza telefonando al 118', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '81', '617', '4', 'Se l’infortunato è incosciente e non respira, bisogna attuare la respirazione artificiale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '81', '618', '5', 'Se l’infortunato è incosciente e non respira, bisogna liberare il naso e la bocca da eventuali ostruzioni', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '81', '619', '6', 'Se l’infortunato è incosciente ma respira è necessario porlo nella posizione laterale di sicurezza', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '81', '620', '7', 'Se l’infortunato è incosciente, bisogna coprirgli il volto con un panno pulito', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '81', '621', '8', 'Se l’infortunato è incosciente, bisogna capire se riesce a muovere gli arti inferiori', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '81', '622', '9', 'Se l’infortunato è incosciente, il primo intervento di soccorso consiste nello spostarlo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '81', '623', '10', 'Se l’infortunato è incosciente, bisogna immobilizzarlo anche con l’uso di bastoni rigidi', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '81', '624', '11', 'Per accertarsi se l’infortunato è cosciente bisogna verificare che ricordi a memoria la targa del suo veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '81', '625', '12', 'Bisogna togliere immediatamente il casco al conducente di motociclo che ha perso i sensi in seguito all’incidente', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '82', '', '', ' Per valutare se l’infortunato respira', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '82', '626', '1', 'Per valutare se l’infortunato respira si deve guardare il torace per vedere se si solleva e si abbassa', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '82', '627', '2', 'Per valutare se l’infortunato respira bisogna ascoltare il rumore prodotto dall’aria che passa dalla gola e esce dalla bocca e dal naso', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '82', '628', '3', 'Per valutare se l’infortunato respira il soccorritore deve porre la mano sul suo torace per vedere se si solleva e si abbassa', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '82', '629', '4', 'Per valutare se l’infortunato respira il soccorritore deve porre la mano sotto il suo naso per sentire il respiro sulla propria pelle', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '82', '630', '5', 'Per valutare se l’infortunato respira il soccorritore deve guardare il torace per vedere se è illeso', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '82', '631', '6', 'Per valutare se l’infortunato respira occorre seguire un corso di pronto soccorso', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '82', '632', '7', 'Per valutare se l’infortunato respira è necessario aspettare l’intervento di un professionista', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '83', '', '', 'Primo soccorso - se l’infortunato non respira', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '83', '633', '1', 'Se l’infortunato non respira, bisogna aprirgli la bocca per controllare se la lingua o un oggetto estraneo ostacolano le vie respiratorie', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '83', '634', '2', 'Se l’infortunato non respira, bisogna portargli la testa all’indietro per facilitare il passaggio dell’aria', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '83', '635', '3', 'Se l’infortunato non respira, bisogna porlo in posizione supina mantenendo il suo collo in estensione', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '83', '636', '4', 'Se l’infortunato non respira, bisogna liberarlo da tutto ciò che potrebbe ostacolare il respiro (ad esempio cravatta, cintura)', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '83', '637', '5', 'Se l’infortunato non respira, bisogna rianimarlo scuotendolo violentemente', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '83', '638', '6', 'Se l’infortunato non respira, bisogna cercare di rimetterlo in piedi', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '83', '639', '7', 'Se l’infortunato non respira, bisogna sollevargli le gambe', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '83', '640', '8', 'Se l’infortunato non respira, bisogna buttargli acqua fresca sul viso', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '83', '641', '9', 'Se l’infortunato non respira, bisogna somministrargli aria compressa se disponibile', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '84', '', '', 'Primo soccorso ad un infortunato - respirazione artificiale', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '84', '642', '1', 'Nel primo soccorso, tra i controlli prioritari vi è quello delle vie aeree, del respiro e del battito cardiaco', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '84', '643', '2', 'La respirazione artificiale è necessaria se l’infortunato non respira più autonomamente', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '84', '644', '3', 'La respirazione artificiale serve ad inalare ossigeno nei polmoni dell’infortunato', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '84', '645', '4', 'La respirazione artificiale consiste nell’inspirare e poi espirare nella bocca dell’infortunato', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '84', '646', '5', 'La respirazione artificiale consiste nel fare delle insufflazioni', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '84', '647', '6', 'La respirazione artificiale deve essere effettuata il più tempestivamente possibile se l’infortunato non respira più autonomamente', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '84', '648', '7', 'La respirazione artificiale si pratica con l’uso di elettrodi', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '84', '649', '8', 'La respirazione artificiale serve per introdurre anidride carbonica nell’infortunato', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '84', '650', '9', 'La respirazione artificiale serve per stimolare meccanicamente tutto il corpo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '84', '651', '10', 'Nel primo soccorso, la respirazione viene facilitata facendo bere all’infortunato', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '85', '', '', 'Primo soccorso ad un infortunato e posizione laterale di sicurezza', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '85', '652', '1', 'La posizione laterale di sicurezza facilita la respirazione e la fuoriuscita di eventuali liquidi presenti in bocca', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '85', '653', '2', 'La posizione laterale di sicurezza è la posizione più appropriata per un infortunato incosciente che respira', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '85', '654', '3', 'La posizione laterale di sicurezza accelera il battito cardiaco', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '85', '655', '4', 'La posizione laterale di sicurezza funziona come stimolante in caso di shock', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '86', '', '', 'Primo soccorso ad un infortunato - arresto cardiaco - 01', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '86', '656', '1', 'Per verificare la presenza di circolo, si possono verificare le pulsazioni della carotide mantenendo il collo in estensione', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '86', '657', '2', 'Per verificare la presenza di circolo sanguigno, possono essere accertate le pulsazioni della carotide', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '86', '658', '3', 'Per accertare se il cuore dell’infortunato batte, occorre pungere un dito per verificare se il sangue esce ritmicamente', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '86', '659', '4', 'Per accertare se il cuore dell’infortunato batte, occorre osservare i capillari dell’occhio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '87', '', '', 'Primo soccorso ad un infortunato - arresto cardiaco - 02', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '87', '660', '1', 'Il massaggio cardiaco effettuato nel primo soccorso, laddove consentito, può essere alternato alla respirazione artificiale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '87', '661', '2', 'Il massaggio cardiaco effettuato nel primo soccorso consiste in una serie di compressioni dello sterno', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '87', '662', '3', 'Il massaggio cardiaco effettuato nel primo soccorso stimola la circolazione sanguigna', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '87', '663', '4', 'Il massaggio cardiaco effettuato nel primo soccorso consiste nel fare delle insufflazioni', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '87', '664', '5', 'Il massaggio cardiaco effettuato nel primo soccorso è una procedura da effettuarsi in qualsiasi situazione di emergenza', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '87', '665', '6', 'Il massaggio cardiaco effettuato nel primo soccorso consiste in una serie di compressioni della carotide', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '87', '666', '7', 'Il massaggio cardiaco deve essere evitato sui soggetti ipertesi', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '88', '', '', 'Primo soccorso ad un infortunato - stato di shock 01', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '88', '667', '1', 'Se l’infortunato è in stato di shock è pallido, presenta brividi e manifesta sudorazione fredda', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '88', '668', '2', 'Se l’infortunato è in stato di shock presenta battiti del cuore deboli ma frequenti', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '88', '669', '3', 'Se l’infortunato è in stato di shock occorre porlo supino a terra e alzargli le gambe', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '88', '670', '4', 'Se l’infortunato è in stato di shock occorre tamponare la fronte con bendaggi di acqua fredda', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '88', '671', '5', 'Se l’infortunato è in stato di shock occorre somministrargli bevande alcoliche', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '89', '', '', ' Primo soccorso ad un infortunato - stato di shock 02', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '89', '672', '1', 'Un’abbondante perdita di sangue può provocare lo stato di shock', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '89', '673', '2', 'Un sintomo dello stato di shock è la condizione di confusione mentale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '89', '674', '3', 'Un infortunato è in stato di shock quando c’è una caduta della pressione arteriosa', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '89', '675', '4', 'Un infortunato è in stato di shock quando il sangue arriva poco al cervello e al cuore', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '89', '676', '5', 'Un infortunato è in stato di shock quando ha un marcato rossore del viso', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '89', '677', '6', 'Un infortunato è in stato di shock quando ha riportato abrasioni superficiali', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '89', '678', '7', 'Un infortunato è in stato di shock quando urla in continuazione', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '90', '', '', 'Primo soccorso - trauma cranico', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '90', '679', '1', 'In presenza di infortunato con trauma cranico occorre tener conto che possono essere presenti lesioni molto gravi con danni cerebrali', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '90', '680', '2', 'In caso di trauma cranico, il soccorritore deve chiamare l’ambulanza anche se l’infortunato non lo ritiene necessario', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '90', '681', '3', 'È utile somministrare bevande alcoliche ad un infortunato con trauma cranico', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '90', '682', '4', 'Nel caso di infortunio con frattura della base cranica si deve tamponare l’emorragia dall’orecchio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '90', '683', '5', 'L’ infortunato con trauma cranico si soccorre sdraiandolo sulla schiena con le gambe alzate', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '91', '', '', 'Primo soccorso quando presente una ferita', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '91', '684', '1', 'Se la ferita ha tracce di terra, occorre lavarla con acqua', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '91', '685', '2', 'È opportuno che il soccorritore si protegga dal contatto con il sangue dell’infortunato utilizzando guanti o altri mezzi', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '91', '686', '3', 'Se la vittima di un incidente presenta una ferita sanguinante occorre tener conto del rischio di infezione', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '91', '687', '4', 'Se la vittima di un incidente presenta una abbondante emorragia bisogna chiamare al più presto l’ambulanza', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '91', '688', '5', 'Se la vittima di un incidente stradale presenta una ferita sanguinante bisogna intervenire con materiale quanto più possibile pulita', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '91', '689', '6', 'Se la vittima di un incidente stradale presenta una ferita sanguinante bisogna evitare infezioni usando insetticidi', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '91', '690', '7', 'Se la vittima di un incidente stradale presenta una ferita sanguinante bisogna evitare che il sangue sporchi la strada', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '91', '691', '8', 'Se la vittima di un incidente stradale presenta una ferita sanguinante non bisogna intervenire se l’infortunato è in stato di ebbrezza', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '91', '692', '9', 'Se la vittima di un incidente stradale presenta una ferita sanguinante bisogna allargare la ferita per fare uscire il pus', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '91', '693', '10', 'Nel caso di emorragia esterna bisogna evitare qualsiasi intervento', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '92', '', '', 'Primo soccorso - emorragia esterna arteriosa', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '92', '694', '1', 'L’emorragia esterna arteriosa è caratterizzata dal fatto che il sangue esce zampillando a schizzi intermittenti', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '92', '695', '2', 'Cinture o cravatte sono utilizzabili come laccio emostatico da porre a monte della ferita per ridurre una emorragia arteriosa', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '92', '696', '3', 'Nel caso di una emorragia esterna arteriosa bisogna arrestare la fuoriuscita del sangue il prima possibile', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '92', '697', '4', 'Nel caso di una emorragia esterna arteriosa occorre comprimere con forza l''arteria principale interessata', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '92', '698', '5', 'Nel caso di una emorragia esterna arteriosa si determina il rischio di una rapida morte per dissanguamento', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '92', '699', '6', 'Per ridurre una emorragia esterna venosa, è utile tamponare a pressione la ferita con materiale possibilmente sterile', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '92', '700', '7', 'L''emorragia esterna venosa è caratterizzata dal fatto che il sangue esce senza zampillare', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '92', '701', '8', 'Nel caso di una emorragia esterna arteriosa bisogna lasciare defluire tutto il sangue', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '92', '702', '9', 'Per ridurre una emorragia esterna venosa sono utili impacchi di acqua fredda o ghiaccio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '92', '703', '10', 'Nel caso di emorragia esterna venosa il soccorritore deve astenersi da qualsiasi intervento di primo soccorso, limitandosi a chiamare l''ambulanza', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '92', '704', '11', 'Nel caso di emorragia esterna venosa, bisogna immediatamente anestetizzare l''infortunato', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '92', '705', '12', 'Nel caso di emorragia venosa il sangue esce zampillando', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '92', '706', '13', 'L''emorragia esterna venosa comporta il rischio di soffocamento', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '92', '707', '14', 'Per arrestare un’emorragia esterna arteriosa è sufficiente un cerotto', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '92', '708', '15', 'Nel caso di emorragia arteriosa il sangue fluisce con continuità', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '92', '709', '16', 'L’emorragia esterna arteriosa, pur se abbondante, non comporta pericolo di vita', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '93', '', '', ' Primo soccorso - fratture 01', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '93', '710', '1', 'Se la vittima presenta fratture di un arto si può tentare di immobilizzare l’articolazione con mezzi di fortuna', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '93', '711', '2', 'Se la vittima presenta fratture non esposte di un arto non bisogna fare niente, limitarsi solo a spostare l’infortunato', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '93', '712', '3', 'Se la vittima presenta fratture ad un arto, bisogna somministrarle bevande alcoliche', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '93', '713', '4', 'Se la vittima presenta fratture ad un arto bisogna misurarle immediatamente la temperatura corporea', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '94', '', '', 'Primo soccorso - fratture 02', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '94', '714', '1', 'In presenza di frattura non esposta di un arto, bisogna impedire la mobilità dell’arto infortunato', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '94', '715', '2', 'In caso di incidente, non bisogna spostare il ferito se si sospettano lesioni alla colonna vertebrale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '94', '716', '3', 'Le fratture devono essere coperte con panni sterili, solo se esposte', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '94', '717', '4', 'Le fratture sono lesioni delle ossa e possono essere composte o scomposte', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '94', '718', '5', 'Le fratture sono emorragie interne', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '94', '719', '6', 'Le fratture vanno sempre immobilizzate', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '94', '720', '7', 'Nel caso di frattura l’infortunato non deve essere coperto', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '94', '721', '8', 'Solo le fratture esposte richiedono l’intervento del medico', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '95', '', '', ' Primo soccorso - Frattura delle costole', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '95', '722', '1', 'In caso di frattura delle costole l’infortunato deve essere tenuto in posizione semiseduta se è cosciente', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '95', '723', '2', 'In caso di frattura delle costole la vittima proverà dolore nella respirazione', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '95', '724', '3', 'In caso di frattura delle costole bisogna evitare il più possibile che l’infortunato si muova', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '95', '725', '4', 'In caso di frattura delle costole bisogna indurre l’infortunato a parlare o a tossire', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '95', '726', '5', 'In caso di frattura delle costole, ove non ci siano lesioni alla schiena, bisogna spostare la vittima', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '95', '727', '6', 'In caso di frattura delle costole, se la vittima non respira, bisogna astenersi dall’intervenire', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '95', '728', '7', 'In caso di frattura delle costole è necessario mettere la vittima in posizione laterale di sicurezza', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '96', '', '', 'Primo soccorso  - corpo estraneo in un occhio', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '96', '729', '1', 'Chi effettua il primo soccorso non deve rimuovere il corpo estraneo dall’occhio dell’infortunato', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '96', '730', '2', 'Nel caso di infortunio, se nella ferita è penetrato un corpo estraneo bisogna evitare di rimuoverlo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '96', '731', '3', 'Se la vittima presenta un corpo estraneo in un occhio è opportuno che la vittima non si tocchi l’occhio ferito', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '96', '732', '4', 'Se la vittima presenta un corpo estraneo in un occhio bisogna coprirlo ed aspettare l’intervento dello specialista', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '96', '733', '5', 'Se la vittima presenta un corpo estraneo in un occhio bisogna coprirlo con materiale possibilmente sterile', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '96', '734', '6', 'Se la vittima presenta un corpo estraneo in un occhio bisogna togliere il corpo estraneo anche con mezzi di fortuna', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '96', '735', '7', 'Se la vittima presenta un corpo estraneo in un occhio bisogna fare prendere aria all’occhio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '96', '736', '8', 'Se la vittima presenta un corpo estraneo in un occhio bisogna versare acqua nell’occhio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '96', '737', '9', 'Se la vittima presenta un corpo estraneo in un occhio chi effettua il primo soccorso deve rimuovere il corpo estraneo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '97', '', '', 'Primo soccorso  - la vittima ustionata', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '97', '738', '1', 'Il soccorritore deve spegnere le fiamme che avvolgono la vittima soffocandole con una coperta', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '97', '739', '2', 'In caso di incendio, il soccorritore deve coprire la vittima ustionata con materiale possibilmente sterile per evitare infezioni', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '97', '740', '3', 'In caso di incendio, il soccorritore deve evitare di staccare residui di tessuto aderenti alle parti ustionate della vittima', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '97', '741', '4', 'In caso di incendio, il soccorritore deve bagnare le ustioni con acqua fredda', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '97', '742', '5', 'In caso di incendio, il soccorritore deve astenersi dall’intervenire per proteggersi e chiamare i vigili del fuoco', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '97', '743', '6', 'In caso di incendio, il soccorritore deve staccare i residui di tessuto aderenti alle parti ustionate della vittima', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '97', '744', '7', 'In caso di incendio, il soccorritore deve bagnare le ustioni con acqua calda', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '97', '745', '8', 'Nel caso di gravi ustioni provocati dalla bruciatura dei vestiti bisogna spogliare immediatamente l’infortunato', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '98', '', '', 'Elementi che caratterizzano gli infortuni sul lavoro ', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '98', '746', '1', 'Gli elementi che caratterizzano gli infortuni sul lavoro sono: l’occasione di lavoro, la causa violenta e la lesione invalidante', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '98', '747', '2', 'Ogni danneggiamento organico o funzionale del lavoratore conseguente all’attività lavorativa costituisce infortunio sul lavoro', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '98', '748', '3', 'La lesione alla vista determinata dall’accidentale contatto con il materiale corrosivo trasportato costituisce infortunio sul lavoro', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '98', '749', '4', 'Durante l’attività professionale, il conducente è esposto a lesioni conseguenti a incendio', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '98', '750', '5', 'Non costituisce infortunio sul lavoro la lesione subita dal lavoratore nel caso di mancato rispetto delle norme di sicurezza comunicategli dal datore di lavoro', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '98', '751', '6', 'Il Codice delle assicurazioni private prevede il risarcimento di tutti i danni da parte dell’INAIL', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '98', '752', '7', 'Sono considerati infortuni sul lavoro dei conducenti professionali solo quelli che accadono con veicolo in movimento', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '98', '753', '8', 'Il danno occorso alla merce trasportata a bordo di un veicolo è considerato infortunio sul lavoro', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '99', '', '', 'Cause degli incidenti stradali - 01', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '99', '754', '1', 'La maggior parte degli incidenti stradali è attribuibile a comportamenti scorretti del conducente', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '99', '755', '2', 'Una velocità non adeguata è tra le cause più ricorrenti di incidenti stradali', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '99', '756', '3', 'La distrazione alla guida è tra le cause più ricorrenti di incidenti stradali', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '99', '757', '4', 'Il mancato rispetto delle norme sulla precedenza è tra le cause più ricorrenti di incidenti stradali', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '99', '758', '5', 'Il mancato rispetto della distanza di sicurezza è tra le cause più ricorrenti di incidenti stradali', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '99', '759', '6', 'La mancanza di estintori a bordo del veicolo aumenta il rischio di incidenti stradali', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '99', '760', '7', 'Il guasto del tachigrafo è tra le cause più ricorrenti di incidenti stradali', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '99', '761', '8', 'L’avaria alla pompa dell’acqua aumenta il rischio di incidenti stradali ', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '100', '', '', 'Cause degli incidenti stradali - 02', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '100', '762', '1', 'I pneumatici con battistrada usurato aumentano il rischio di incidenti stradali', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '100', '763', '2', 'La stanchezza del conducente aumenta il rischio di incidente stradale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '100', '764', '3', 'Il malfunzionamento delle sospensioni è tra le cause di incidenti stradali', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '100', '765', '4', 'Il malfunzionamento dei freni è tra le cause di incidenti stradali', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '100', '766', '5', 'Il malfunzionamento della pompa di iniezione è tra le cause più ricorrenti di incidenti stradali', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '100', '767', '6', 'Il filtro dell’aria intasato aumenta il rischio di incidente stradale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '100', '768', '7', 'La tipologia di combustibile (gasolio, GPL, metano) aumenta il rischio di incidente stradale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '101', '', '', 'Infortunio sul lavoro - 01', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '101', '769', '1', 'L’infortunio sul lavoro è l’evento traumatico, avvenuto per una causa violenta sul posto di lavoro o anche semplicemente in occasione di lavoro, che comporta l’impossibilità di svolgere l’attività lavorativa', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '101', '770', '2', 'L’infortunio sul lavoro, per definirsi tale, è caratterizzato tra l’altro, da una causa violenta', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '101', '771', '3', 'L’infortunio sul lavoro, per definirsi tale, è caratterizzato tra l’altro, dal fatto che deve determinarsi un’inabilità al lavoro (permanente o temporanea)', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '101', '772', '4', 'L’infortunio sul lavoro, per definirsi tale, è caratterizzato tra l’altro, dall’occasione di lavoro', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '101', '773', '5', 'L’infortunio sul lavoro, per definirsi tale, non deve necessariamente essere caratterizzato tra l’altro, da una causa violenta', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '101', '774', '6', 'L’infortunio sul lavoro non è tutelato dall’INAIL (Istituto nazionale assicurazione contro gli infortuni sul lavoro)', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '101', '775', '7', 'L’assunzione eccessiva di alcool durante i periodi di riposo obbligatorio costituisce infortunio sul lavoro', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '101', '776', '8', 'L’infortunio occorso ad un turista a bordo di un autobus durante il trasporto è considerato infortunio sul lavoro', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '101', '777', '9', 'Non costituisce infortunio sul lavoro la lesione subita dal lavoratore nel caso in cui non utilizzi, per negligenza, i dispositivi di protezione individuale imposti dal datore di lavoro', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '102', '', '', 'Infortunio sul lavoro 02', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '102', '778', '1', 'È considerato infortunio sul lavoro la lesione che subisce il conducente durante le operazioni di carico e scarico delle merci', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '102', '779', '2', 'Il danneggiamento all’apparato muscolo scheletrico durante l’operazione di sostituzione di un pneumatico costituisce infortunio sul lavoro', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '102', '780', '3', 'Durante l’attività lavorativa la frattura riportata dal conducente che scivoli nel salire sulla cabina sull’autocarro costituisce infortunio sul lavoro', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '102', '781', '4', 'La lesione ad una mano subita dal conducente durante l’operazione di aggancio del rimorchio è considerata infortunio sul lavoro', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '102', '782', '5', 'La lesione subita dal conducente in conseguenza di cadute accidentali dal piano di carico dell’autocarro costituisce infortunio sul lavoro', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '102', '783', '6', 'Una lesione fisica che si verifica durante la manutenzione del veicolo è un infortunio sul lavoro', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '102', '784', '7', 'Il danno fisico subito dal conducente che cade accidentalmente nella cisterna costituisce infortunio sul lavoro', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '102', '785', '8', 'La caduta del conducente dal pianale di carico non rientra tra i rischi professionali', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '102', '786', '9', 'Gli infortuni derivanti dalla movimentazione del carico non rientrano tra i rischi professionali del conducente', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '103', '', '', 'Infortunio sul lavoro 03', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '103', '787', '1', 'L’incidente stradale che ha provocato lesioni all’autista durante la sua attività professionale è considerato infortunio sul lavoro', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '103', '788', '2', 'Le lesioni riportate, durante l’attività lavorativa, dal conducente che scivoli nel salire sulla cabina sull’autocarro sono considerate come infortunio sul lavoro correlato al settore dei trasporti', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '103', '789', '3', 'Le lesioni (specie agli arti superiori) riportate durante l’operazione di aggancio/sgancio di rimorchi sono considerate come infortunio sul lavoro correlato al settore dei trasporti', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '103', '790', '4', 'Le lesioni subite in conseguenza di cadute accidentali dal piano di carico dell’autocarro non sono considerate come infortunio sul lavoro correlato al settore dei trasporti', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '103', '791', '5', 'Le lesioni determinate dall’accidentale contatto con materiale corrosivo trasportato non sono considerate come infortunio sul lavoro correlato al settore dei trasporti', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '103', '792', '6', 'L’incidente che coinvolge il conducente professionale, avvenuto all’interno della rimessa al termine del turno di lavoro, non è considerato infortunio sul lavoro', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '104', '', '', ' Psicologia del traffico 01', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '104', '793', '1', 'La psicologia del traffico riguarda anche lo studio dei fattori umani e sociali associati alla promozione di condotte di guida sicure e mobilità sostenibile', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '104', '794', '2', 'La concentrazione del conducente è tra i fattori cognitivi che sembrano influire maggiormente sulle condotte di guida', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '104', '795', '3', 'La quantità di risorse cognitive del conducente richieste per la guida dei veicoli è soggetta a variazioni, se per esempio la guida avviene in condizioni di visibilità scarsa, o traffico elevato', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '104', '796', '4', 'L’attenzione che si richiede all’automobilista deve essere di tipo diffuso e deve rivolgersi, in successione rapidissima, su tutti gli elementi del traffico che lo circondano', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '104', '797', '5', 'L’attenzione del conducente non rientra tra i fattori cognitivi che sembrano influire maggiormente sulle condotte di guida', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '104', '798', '6', 'La quantità di risorse cognitive del conducente richieste per la guida dei veicoli è indipendente dalle condizioni di visibilità scarsa o di traffico elevato', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '104', '799', '7', 'Un maggiore equilibrio psicologico non viene mai raggiunto in seguito ad una maggiore esperienza di guida del conducente', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '104', '800', '8', 'Tra le variabili che influenzano le reazioni alla guida del conducente non devono mai essere prese in considerazione le abitudini, le motivazioni e gli atteggiamenti, la scuola, la famiglia, l’esperienza di vita', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '105', '', '', ' Psicologia del traffico 02', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '105', '801', '1', 'Nel valutare le proprie competenze alla guida o una determinata situazione su strada il conducente può incorrere nei cosiddetti biases socio cognitivi, quali l’ottimismo irrealistico, l’illusione di controllo, il rischio reale e il rischio percepito', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '105', '802', '2', 'I conducenti dei veicoli e, più in generale le persone, compresi anche i pedoni, sono le cause del 95% dei sinistri', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '105', '803', '3', 'Durante la guida, il comportamento stradale è influenzato anche dalle variabili socio-culturali dell’ambiente in cui il conducente si trova ad agire', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '105', '804', '4', 'Durante la guida, il conducente deve imparare a regolare gli stati di tensione e affaticamento', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '105', '805', '5', 'Durante la guida, il conducente può mettere in atto comportamenti trasgressivi su strada, dettati da uno stato d’animo particolare, quale frustrazione, rabbia, ansia o aggressività indotta dall’ambiente sociale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '105', '806', '6', 'Durante la guida, le situazioni di traffico intenso o condizioni di guida critiche non possono mai creare stress e incidere sulle reazioni emotive del conducente', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '105', '807', '7', 'Durante la guida, i biases socio cognitivi, quali l’ottimismo irrealistico non portano mai il conducente a un errore di valutazione o a mancanza di oggettività di giudizio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '105', '808', '8', 'Gli incidenti dovuti alle cause oggettive (ad es., guasti meccanici, scoppio dei pneumatici) non sono mai, seppur indirettamente, da attribuirsi all’uomo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '106', '', '', 'Psicologia del traffico 03', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '106', '809', '1', 'Tra le attitudini richieste per la guida è importante la rapidità di reazione', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '106', '810', '2', 'Tra le attitudini richieste per la guida è importante la resistenza alla emotività', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '106', '811', '3', 'Tra le cause di distrazione che possono avere gravi conseguenze sulla guida di veicoli non vi è la ricerca dei comandi di radio, lettore CD/MP3 o navigatore satellitare', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '106', '812', '4', 'Tra le cause di distrazione che possono avere gravi conseguenze sulla guida di veicoli non vi è l’accensione della sigaretta o la ricerca del posacenere', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '106', '813', '5', 'Tra le cause di distrazione che possono avere gravi conseguenze sulla guida di veicoli non è considerato importante il guardarsi nello specchio retrovisore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '106', '814', '6', 'Tra le attitudini richieste per la guida non è rilevante la capacità di tener sotto controllo tutti gli elementi del traffico', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '106', '815', '7', 'Tra le attitudini richieste per la guida è inutile la coordinazione sensorio motrice', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '107', '', '', 'Dispositivi di protezione ', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '107', '816', '1', 'Occhiali, maschere e visiere proteggono gli occhi da schegge e materie corrosive', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '107', '817', '2', 'I guanti consentono la protezione delle mani da rischi meccanici, elettrici e chimici', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '107', '818', '3', 'I dispositivi di protezione individuale sono strumenti che consentono di salvaguardare l’incolumità dei conducenti', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '107', '819', '4', 'Nel caso in cui l’attività di trasporto comporti saltuariamente la movimentazione di merce pericolosa e potenzialmente biologicamente impattante non vi è l’obbligo di utilizzare dispositivi di protezione individuale (DPI)', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '107', '820', '5', 'I giubbotti retroriflettenti non costituiscono dispositivi di protezione individuale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '108', '', '', 'Gestione della sicurezza sul lavoro', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '108', '821', '1', ' Tra le figure coinvolte nella gestione della sicurezza sul lavoro vi è il datore di lavoro', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '108', '822', '2', 'Tra le figure coinvolte nella gestione della sicurezza sul lavoro vi è il preposto', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '108', '823', '3', 'Tra le figure coinvolte nella gestione della sicurezza sul lavoro vi è il rappresentante dei lavoratori per la sicurezza', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '108', '824', '4', 'Tra le figure coinvolte nella gestione della sicurezza sul lavoro non vi è il rappresentate dei lavoratori per la sicurezza di sito produttivo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '108', '825', '5', 'Tra le figure coinvolte nella gestione della sicurezza sul lavoro non vi è il dirigente aziendale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '108', '826', '6', 'Tra le figure coinvolte nella gestione della sicurezza sul lavoro non vi è il medico competente', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '109', '', '', 'Sicurezza e della salute dei lavoratori', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '109', '827', '1', 'I criteri generali applicabili a tutela della sicurezza e della salute dei lavoratori stabiliti dalla normativa riguardano, tra l’altro, l’uso di segnaletica di avvertimento e di sicurezza', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '109', '828', '2', 'I criteri generali applicabili a tutela della sicurezza e della salute dei lavoratori stabiliti dalla normativa riguardano, tra l’altro, la limitazione al minimo del numero di lavoratori esposti al rischio', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '109', '829', '3', 'I criteri generali applicabili a tutela della sicurezza e della salute dei lavoratori stabiliti dalla normativa riguardano, tra l’altro, il controllo sanitario dei lavoratori (dove necessario a seguito della valutazione dei rischi)', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '109', '830', '4', 'I criteri generali applicabili a tutela della sicurezza e della salute dei lavoratori stabiliti dalla normativa non riguardano la riduzione dei rischi alla fonte, con la sostituzione di ciò che è pericoloso con ciò che non lo è o è meno pericoloso', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '109', '831', '5', 'I criteri generali applicabili a tutela della sicurezza e della salute dei lavoratori stabiliti dalla normativa non riguardano l’utilizzo limitato degli agenti chimici, fisici e biologici sul luogo di lavoro', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '110', '', '', 'Ambito della sicurezza sul lavoro', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '110', '832', '1', 'Nell’ambito della sicurezza sul lavoro per rischio si intende la probabilità che un incidente o un infortunio si verifichi, determinando un danno', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '110', '833', '2', 'Nell’ambito della sicurezza sul lavoro valutare il rischio significa valutare, tra l’altro, la gravità del danno potenzialmente possibile', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '110', '834', '3', 'Nell’ambito della sicurezza sul lavoro nel settore trasporti, assumono un altissimo rilievo gli indici infortunistici legati a incidenti gravi e/o mortali', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '110', '835', '4', 'Nell’ambito della sicurezza sul lavoro per valutare il rischio non occorre entrare nel merito della probabilità che il danno possa accadere', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '110', '836', '5', 'Nell’ambito della sicurezza sul lavoro, il datore di lavoro non è tenuto all’effettuazione di azioni di formazione, informazione e addestramento agli addetti', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '111', '', '', 'Principale cause incidenti ', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '111', '837', '1', 'È necessaria una corretta valutazione dei rischi da parte del datore di lavoro quando l’attività di trasporto comporta (anche saltuariamente) movimentazione di merce pericolosa e potenzialmente biologicamente impattante', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '111', '838', '2', 'È necessaria una corretta valutazione dei rischi da parte del datore di lavoro quando sussiste la necessità per gli operatori di accedere alla parte superiore del veicolo per attivare/agevolare le operazioni di carico e scarico', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '111', '839', '3', 'Nell’ambito dell’incidentalità stradale si può individuare tra i principali fattori di rischio legati all’errore umano la distrazione connessa all’uso di dispositivi tecnologici alla guida (telefoni cellulari, ecc.)', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '111', '840', '4', 'Nell’ambito dell’incidentalità stradale si può individuare tra i principali fattori di rischio legati all’errore umano la stanchezza alla guida', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '111', '841', '5', 'Nell’ambito dell’incidentalità stradale si può individuare tra i principali fattori di rischio legati all’errore umano l’uso di alcool', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '111', '842', '6', 'Con riferimento ai trasporti stradali un primo fattore di rischio, tutt’altro che trascurabile, è legato all’incidentalità stradale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '111', '843', '7', 'Con riferimento ai trasporti stradali non è considerato un fattore di rischio quello legato all’incidentalità stradale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '111', '844', '8', 'Nell’ambito dell’incidentalità stradale non rientra tra i principali fattori di rischio legati all’errore umano l’uso di alcool', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '111', '845', '9', 'Nell’ambito dell’incidentalità stradale non rientra tra i principali fattori di rischio legati all’errore umano l’uso saltuario di sostanze psicotrope', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('5', '111', '846', '10', 'Nel caso in cui gli operatori devono accedere alla parte superiore del veicolo per attivare/agevolare le operazioni di carico e scarico non è necessaria la valutazione dei rischi se la carrozzeria è del tipo a cisterna', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '112', '', '', 'Regolamento n.561/2006/CE (normativa sociale per i conducenti)', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '112', '847', '1', 'La normativa che disciplina la durata massima di guida nell’autotrasporto di merci, si applica, salve le eccezioni di legge, ai conducenti di veicoli di massa massima ammissibile superiore alle 3,5 t', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '112', '848', '2', 'La normativa che disciplina la durata massima di guida nell’autotrasporto di persone si applica, salve le eccezioni di legge, ai conducenti di autobus', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '112', '849', '3', 'La normativa vigente in materia di durata massima di guida, si applica per i trasporti su autobus o su veicoli di massa massima ammissibile superiore a 3, 5 t svolti tra Stati UE', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '112', '850', '4', 'Il regolamento n. 561/2006(CE) in materia di durata massima di guida, si applica per i trasporti su autobus o su veicoli di massa massima ammissibile superiore a 3, 5 t svolti tra l’UE e Stati dello Spazio economico europeo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '112', '851', '5', 'I conducenti di autocarri di massa massima ammissibile inferiore a 3,5 t, sono assoggettati alle disposizioni UE in materie di durata massima di guida sull’autotrasporto di merci', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '113', '', '', 'Regolamento n.561/2006/CE - Definizioni relative alle norme del cronotachigrafo', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '113', '852', '1', 'Il tachigrafo è lo strumento che registra i tempi di guida, di lavoro, di disponibilità, le interruzioni di guida e i periodi di riposo giornaliero di un conducente soggetto al controllo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '113', '853', '2', 'Ai fini dell’obbligatorietà dell’uso del tachigrafo, si definisce conducente chiunque sia addetto alla guida di veicolo, anche per un breve periodo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '113', '854', '3', 'Ai fini dell’obbligatorietà dell’uso del tachigrafo, si definisce conducente chiunque si trovi a bordo di un veicolo con la mansione, all’occorrenza, di guidarlo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '113', '855', '4', 'Ai fini dell’obbligatorietà dell’uso del tachigrafo, si definisce conducente chiunque sia addetto alla guida di veicolo per almeno 100 km', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '113', '856', '5', 'Ai fini dell’obbligatorietà dell’uso del tachigrafo, non si definisce conducente chiunque si trovi a bordo di un veicolo con la mansione, all’occorrenza, di guidarlo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '114', '', '', ' Regolamento n.561/2006/CE - Determinazione dei tempi massimi di guida e di riposo', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '114', '857', '1', 'La determinazione, per legge, dei tempi massimi di guida e di riposo, ha lo scopo di tutelare il conducente obbligato ad utilizzare il tachigrafo, dai rischi che possono derivare da eccessivo affaticamento', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '114', '858', '2', 'La determinazione, per legge, dei tempi massimi di guida e di riposo, mira a migliorare le condizioni sociali dei conducenti obbligati ad utilizzare il tachigrafo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '114', '859', '3', 'La determinazione, per legge, dei tempi di guida e di riposo ha lo scopo di tutelare gli altri utenti della strada dal pericolo di incidenti causati da conducenti eccessivamente stanchi', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '114', '860', '4', 'La determinazione per legge, dei tempi di guida e di riposo ha lo scopo di ridurre l’usura dei veicoli', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '114', '861', '5', 'La determinazione, per legge, dei tempi di guida e di riposo ha lo scopo di ridurre le emissioni inquinanti dei veicoli', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '114', '862', '6', 'La determinazione, per legge, dei tempi di guida e di riposo ha lo scopo di contenere i livelli stipendiali dei conducenti', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '114', '863', '7', 'La normativa vigente in materia di durata massima di guida non si applica ai conducenti di autobus immatricolati al servizio di noleggio con conducente', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '114', '864', '8', 'La normativa vigente in materia di durata massima di guida non si applica ai conducenti di autoveicoli adibiti per il soccorso stradale che operano sulle autostrade', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '115', '', '', 'Esenzione del Regolamento n.561/2006/CE ', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '115', '865', '1', 'La normativa vigente in materia di durata massima di guida non si applica ai conducenti di veicoli di massa massima non superiore a 7,5 t, adibiti al trasporto non commerciale di cose per uso privato', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '115', '866', '2', 'La normativa vigente in materia di durata massima di guida non si applica ai conducenti di veicoli sottoposti a prova su strada di riparazione o di manutenzione', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '115', '867', '3', 'La normativa vigente in materia di durata massima di guida non si applica ai conducenti di veicoli utilizzati in situazioni di emergenza o di operazioni di salvataggio', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '115', '868', '4', 'La normativa vigente in materia di durata massima di guida non si applica ai conducenti di veicoli sui quali il tachigrafo sia stato sottoposto a revisione periodica', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '115', '869', '5', 'La normativa vigente in materia di durata massima di guida non si applica ai conducenti di veicoli utilizzati da società sportive', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '115', '870', '6', 'La normativa vigente in materia di durata massima di guida non si applica ai conducenti di veicoli di massa massima superiore a 3,5 t che trainano un rimorchio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '115', '871', '7', 'La normativa vigente in materia di durata massima di guida non si applica ai conducenti di veicoli la cui velocità massima autorizzata è di 50 km/h', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '116', '', '', 'Applicazione del regolamento n.561/2006/CE - ', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '116', '872', '1', 'La normativa vigente in materia di durata massima di guida non si applica ai conducenti di autoveicoli adibiti per il soccorso stradale che operano entro un raggio di 100 chilometri dalla propria base operativa', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '116', '873', '2', 'La normativa in materia di durata massima di guida non si applica ai conducenti di autobus in servizio regolare di linea, il cui percorso non supera i 50 chilometri', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '116', '874', '3', 'La normativa in materia di durata massima di guida non si applica ai conducenti di veicoli la cui velocità massima autorizzata è di 40 km/h', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '116', '875', '4', 'La normativa vigente in materia di durata massima di guida, si applica anche ai conducenti dei veicoli immatricolati al di fuori dell’UE se il trasporto avviene totalmente nell’ambito dell’unione stessa', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '116', '876', '5', 'La normativa in materia di durata massima di guida non si applica ai conducenti di veicoli delle forze armate', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '116', '877', '6', 'La normativa vigente in materia di durata massima di guida, si applica per i trasporti su autobus o su veicoli di massa massima ammissibile superiore a 3, 5 t svolti tra Stati dell’UE e la Svizzera', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '116', '878', '7', 'Il conducente di un autobus immatricolato in Italia che si rechi in Francia (Stato dell’UE), deve osservare i tempi massimi di guida previsti dall’accordo AETR', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '116', '879', '8', 'La normativa vigente in materia di durata massima di guida nell’autotrasporto di merci, si applica ai conducenti di veicoli di qualsiasi massa massima ammissibile', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '116', '880', '9', 'La normativa vigente in materia di durata massima di guida nell’autotrasporto di persone si applica ai conducenti di autobus qualunque sia il servizio svolto', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '116', '881', '10', 'La normativa vigente in materia di durata massima di guida si applica ai conducenti di veicoli dei vigili del fuoco', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '116', '882', '11', 'Le norme UE in materia di durata massima di guida non si applicano ai conducenti di autobus in servizio regolare di linea, il cui percorso non supera gli 80 km', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '117', '', '', 'Periodo di guida giornaliero', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '117', '883', '1', 'Le norme sul tachigrafo, definiscono “periodo di guida giornaliero” il periodo complessivo di guida tra il termine di un periodo di riposo giornaliero e l’inizio del periodo di riposo giornaliero seguente o tra un periodo di riposo giornaliero e un periodo di riposo settimanale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '117', '884', '2', 'La normativa in materia di tempi di guida e di riposo, prevede un periodo di guida continuato non superiore a quattro ore e mezza', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '117', '885', '3', 'La normativa in materia di tempi di guida e di riposo, prevede che il periodo massimo di guida non possa eccedere le 9 ore giornaliere aumentabili a 10 ore per non più di due giorni alla settimana', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '117', '886', '4', 'La normativa in materia di tempi di guida e di riposo, prevede un periodo di guida giornaliero di 9 ore che può essere esteso a 10 ore, al massimo tre volte la settimana', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '117', '887', '5', 'Secondo quanto stabilito dalla normativa relativa al tachigrafo, un conducente può guidare per più di 4 ore e mezza consecutive, se a bordo c’è un altro conducente', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '117', '888', '6', 'Secondo quanto stabilito dalla normativa relativa al tachigrafo, il conducente può superare le 4 ore e mezza di guida consecutive, se è in condizioni psicofisiche tali da non compromettere la sicurezza stradale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '117', '889', '7', 'Il periodo di guida giornaliero, secondo quanto stabilito dalla normativa relativa al tachigrafo, è di 9 ore consecutive', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '117', '890', '8', 'Il periodo di guida giornaliero, secondo quanto stabilito dalla normativa relativa al tachigrafo, può essere al massimo di 8 ore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '117', '891', '9', 'I tempi massimi di guida giornalieri, per i conducenti soggetti alla normativa relativa al tachigrafo, sono di 4 ore e mezza aumentabili ad 9 ore per due giorni la settimana', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '118', '', '', 'Periodo di guida settimanale', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '118', '892', '1', 'La normativa in materia di tempi di guida e di riposo, prevede che il periodo massimo di guida non possa eccedere le 56 ore settimanali', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '118', '893', '2', 'La normativa in materia di tempi di guida e di riposo, prevede che il periodo massimo di guida non possa eccedere le 90 ore bisettimanali', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '118', '894', '3', 'I tempi massimi di guida bisettimanali per conducenti di veicoli soggetti alla normativa relativa al tachigrafo sono di 112 ore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '118', '895', '4', 'I tempi massimi di guida settimanali soggetti alla normativa relativa al tachigrafo, sono di 45 ore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '119', '', '', 'Interruzione della guida', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '119', '896', '1', 'La normativa sull’uso del tachigrafo definisce interruzione ogni periodo in cui il conducente non può guidare o svolgere altre mansioni e che serve unicamente al suo riposo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '119', '897', '2', 'Ai fini della normativa sull’uso del tachigrafo, si definisce “riposo” ogni periodo ininterrotto durante il quale il conducente può disporre liberamente del suo tempo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '119', '898', '3', 'Il conducente di un autobus immatricolato in Germania (Stato UE) che si rechi in Russia (Stato AETR), deve osservare i tempi massimi di guida previsti dall’accordo AETR', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '119', '899', '4', 'In tema di durata della guida dei conducenti di veicoli adibiti al trasporto di persone o di cose, si applica l’accordo AETR quando il trasporto non si esaurisce all’interno del territorio dell’UE, della Svizzera o dello Spazio economico europeo, ma interessa il territorio di altri Stati', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '119', '900', '5', 'Secondo quanto stabilito dalla normativa relativa al tachigrafo, la reperibilità non può essere considerata riposo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '119', '901', '6', 'Secondo quanto stabilito dalla normativa relativa al tachigrafo, l’interruzione della guida è facoltativa', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '119', '902', '7', 'Secondo quanto stabilito dalla normativa relativa al tachigrafo, durante un periodo di interruzione alla guida, è possibile effettuare “altre mansioni”', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '119', '903', '8', 'Secondo quanto stabilito dalla normativa relativa al tachigrafo, la reperibilità può essere considerata riposo solo se il conducente si trova nel proprio domicilio abituale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '119', '904', '9', 'La normativa sull’uso del tachigrafo definisce interruzione ogni periodo in cui il conducente può svolgere altre mansion ma non può guidare', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '119', '905', '10', 'Ai fini della normativa sull’uso del tachigrafo, si definisce “riposo” ogni periodo durante il quale il conducente non può guidare ma può svolgere “altre mansioni”', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '120', '', '', 'Periodo di riposo giornaliero 01', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '120', '906', '1', 'Il periodo di riposo giornaliero stabilito dal regolamento 561/2006(CE) è di almeno 11 ore', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '120', '907', '2', 'Il periodo di riposo giornaliero stabilito dalla normativa relativa al tachigrafo può essere minimo di 11 ore riducibili a nove ore per tre giorni la settimana', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '120', '908', '3', 'In base alla normativa relativa al tachigrafo, mentre il conducente fruisce del periodo di interruzione di 45 minuti della guida, dopo aver guidato per 4 ore e mezza non può svolgere nessuna attività lavorativa', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '120', '909', '4', 'In base alla normativa relativa al tachigrafo, l’interruzione periodica di 45 minuti può essere frazionata in 2 periodi: la prima di almeno 15 minuti e la seconda di almeno 30 minuti', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '120', '910', '5', 'La normativa in materia di tempi di guida e di riposo, prevede un’interruzione di 45 minuti dopo quattro ore e mezza di guida', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '120', '911', '6', 'La normativa in materia di tempi di guida e di riposo prevede un’interruzione obbligatoria ogni 4 ore di guida', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '120', '912', '7', 'Il conducente di un veicolo soggetto alla disciplina relativa al tachigrafo, mentre fruisce del periodo di interruzione di 45 minuti della guida, dopo aver guidato per 4 ore e mezza, può effettuare la manutenzione del veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '121', '', '', 'Periodo di riposo giornaliero 02', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '121', '913', '1', 'Ai fini della normativa sull’uso del tachigrafo, si definisce “periodo di riposo giornaliero ridotto” ogni tempo di riposo ininterrotto di almeno 9 ore, ma inferiore a 11 ore', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '121', '914', '2', ' Ai fini della normativa sull’uso del tachigrafo, si definisce “periodo di riposo giornaliero regolare” ogni tempo di riposo ininterrotto di almeno 11 ore', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '121', '915', '3', 'Ai fini della normativa sull’uso del tachigrafo, si definisce “periodo di riposo giornaliero regolare” ogni tempo di riposo che può essere preso in due periodi, il primo dei quali di almeno 3 ore senza interruzione e il secondo di almeno 9 ore senza interruzione', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '121', '916', '4', 'Secondo quanto stabilito dalla normativa relativa al tachigrafo, il periodo di riposo giornaliero, deve essere effettuato entro 24 ore dal termine del precedente riposo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '121', '917', '5', 'Secondo quanto stabilito dalla normativa relativa al tachigrafo, il periodo di riposo giornaliero può essere effettuato su veicolo se munito di cuccetta e in sosta', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '121', '918', '6', 'Secondo quanto stabilito dalla normativa relativa al tachigrafo, il conducente può superare le 4 ore e mezza di guida, per raggiungere il luogo di sosta appropriato per la sicurezza del veicolo, a condizione di non compromettere la sicurezza stradale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '121', '919', '7', 'Secondo quanto stabilito dalla normativa relativa al tachigrafo, il periodo di riposo giornaliero può essere effettuato su veicolo solamente se il conducente si trova a bordo di un traghetto ', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '121', '920', '8', 'Ai fini della normativa sull’uso del tachigrafo, si definisce “periodo di riposo giornaliero ridotto” ogni tempo di riposo ininterrotto di almeno 6 ore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '121', '921', '9', 'Ai fini della normativa sull’uso del tachigrafo, si definisce “periodo di riposo giornaliero regolare” ogni tempo di riposo ininterrotto di almeno 7 ore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '122', '', '', 'Periodo di riposo settimanale', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '122', '922', '1', 'Ai fini della normativa sull’uso del tachigrafo, si definisce “periodo di riposo settimanale regolare” ogni tempo di riposo di almeno 45 ore', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '122', '923', '2', 'Ai fini della normativa sull’uso del tachigrafo, si definisce “periodo di riposo settimanale ridotto” ogni tempo di riposo inferiore a 45 ore, che può essere ridotto, a determinate condizioni, a una durata minima di 24 ore continuative', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '122', '924', '3', 'Secondo quanto stabilito dalla normativa relativa al tachigrafo, è vietato guidare per più di 6 giorni consecutivi', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '122', '925', '4', 'Secondo quanto stabilito dalla normativa relativa al tachigrafo, il conducente può, in accordo con il datore di lavoro, guidare per più di sei giorni alla settimana', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '122', '926', '5', 'Ai fini della normativa sull’uso del tachigrafo, si definisce “periodo di riposo settimanale regolare” ogni tempo di riposo di almeno 24 ore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '123', '', '', 'Altre mansioni', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '123', '927', '1', 'Ai fini della normativa sull’uso del tachigrafo, il carico e lo scarico del veicolo sono considerate “altre mansioni”', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '123', '928', '2', 'Ai fini della normativa sull’uso del tachigrafo, il controllo della pressione dei pneumatici del veicolo è considerato “altra mansione”', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '123', '929', '3', 'Ai fini della normativa sull’uso del tachigrafo, è considerata “altra mansione” ogni attività svolta per altro datore di lavoro, nell’ambito o al di fuori del settore dei trasporti', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '123', '930', '4', 'Ai fini della normativa sull’uso del tachigrafo, la pulizia del veicolo non è considerata “altra mansione”', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '123', '931', '5', 'Ai fini della normativa sull’uso del tachigrafo, l’adempimento alle formalità amministrative di dogana non è considerato “altra mansione”', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '124', '', '', 'Cronotachigrafo - norme, obbligo ed esenzioni', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '124', '932', '1', 'Il tachigrafo deve essere montato e manutenuto solo da officine autorizzate', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '124', '933', '2', 'Il tachigrafo deve essere sottoposto a controllo biennale della funzionalità', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '124', '934', '3', 'Il tachigrafo non impedisce al veicolo di superare il limite massimo di velocità', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '124', '935', '4', 'Il conducente di autobus privo di tachigrafo adibito al servizio regolare in ambito nazionale, deve esser munito di un estratto del registro di servizio e di una copia dell’orario di servizio', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '124', '936', '5', 'Il conducente di autobus privo di tachigrafo adibito al servizio regolare in ambito nazionale, deve esser munito di una copia del contratto di lavoro', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '124', '937', '6', 'Il conducente di autobus privo di tachigrafo adibito al servizio regolare in ambito nazionale deve essere munito di carta tachigrafica', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '124', '938', '7', 'Il tachigrafo deve essere sottoposto al controllo di funzionalità ogni 3 anni', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '124', '939', '8', 'Il tachigrafo è obbligatorio su tutti i veicoli con massa complessiva superiore alle 3,5 t', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '124', '940', '9', 'Il tachigrafo impedisce al veicolo di superare i limiti massimi di velocità', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '125', '', '', 'Cronotachigrafo analogico - fogli di registrazione (disco)', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '125', '941', '1', 'Il foglio di registrazione del tachigrafo analogico è personale, e quindi, in caso di avvicendamento dell’autista, il foglio deve essere sostituito con quello del nuovo conducente', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '125', '942', '2', 'I fogli di registrazione del tachigrafo analogico devono essere omologati secondo disposizioni UE', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '125', '943', '3', 'I fogli di registrazione del tachigrafo analogico devono essere forniti dal datore di lavoro', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '125', '944', '4', 'Il foglio di registrazione del tachigrafo analogico deve essere tolto al momento della riconsegna del veicolo oppure dopo 24 ore dal suo inserimento', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '125', '945', '5', 'Il foglio di registrazione del tachigrafo analogico deve riportare la targa del veicolo o dei veicoli che il conducente ha guidato nella giornata lavorativa', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '125', '946', '6', 'Il foglio di registrazione del tachigrafo analogico, deve essere sostituito ogni 36 ore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '125', '947', '7', 'Il foglio di registrazione del tachigrafo analogico deve essere sostituito al termine di ogni interruzione obbligatoria della guida giornaliera', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '125', '948', '8', 'Il foglio di registrazione del tachigrafo analogico deve essere compilato solo alla fine del periodo lavorativo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '125', '949', '9', 'Il foglio di registrazione del tachigrafo non specifica i tempi di disponibilità', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '125', '950', '10', 'I fogli di registrazione del tachigrafo analogico sono consegnati al conducente dalla camera di commercio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '125', '951', '11', 'In caso di emergenza si possono utilizzare fotocopie dei fogli di registrazione del tachigrafo analogico', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '126', '', '', 'Cronotachigrafo analogico - fogli di registrazione (disco)', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '126', '952', '1', 'Prima dell’inizio del viaggio, nel foglio di registrazione di un tachigrafo analogico, il conducente deve scrivere la data di partenza', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '126', '953', '2', 'Prima dell’inizio del viaggio, nel foglio di registrazione di un tachigrafo analogico, il conducente deve scrivere i chilometri percorsi dal veicolo fino al momento della partenza', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '126', '954', '3', 'Prima dell’inizio del viaggio, nel foglio di registrazione di un tachigrafo analogico, il conducente deve scrivere la targa del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '126', '955', '4', 'Prima dell’inizio del viaggio, nel foglio di registrazione di un tachigrafo analogico, il conducente deve scrivere il proprio nome e cognome', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '126', '956', '5', 'Prima dell’inizio del viaggio, nel foglio di registrazione di un tachigrafo analogico, il conducente deve scrivere il luogo di partenza', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '126', '957', '6', 'Prima dell’inizio del viaggio, nel foglio di registrazione di un tachigrafo analogico, il conducente deve scrivere numero di omologazione del tachigrafo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '126', '958', '7', 'Prima dell’inizio del viaggio, nel foglio di registrazione di un tachigrafo analogico, il conducente deve scrivere numero della carta tachigrafica', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '126', '959', '8', 'Prima dell’inizio del viaggio, nel foglio di registrazione di un tachigrafo analogico, il conducente deve scrivere data dell’ultima revisione del tachigrafo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '127', '', '', 'Cronotachigrafo analogico - uso dei fogli di registrazione (disco)', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '127', '960', '1', 'I dischi del tachigrafo riportano i simboli che indicano anche i tempi di guida, di riposo e di disponibilità', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '127', '961', '2', 'Sul foglio di registrazione del tachigrafo analogico sono registrate tutte le aperture del tachigrafo stesso', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '127', '962', '3', 'Se un veicolo è dotato di tachigrafo analogico, il conducente, quando il veicolo è in sosta, deve azionare il dispositivo di commutazione', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '127', '963', '4', 'In caso di cambio del veicolo, durante una stessa giornata di servizio, il conducente deve utilizzare, se compatibile, lo stesso foglio di registrazione del tachigrafo, riportandovi le dovute annotazioni', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '127', '964', '5', 'Il conducente di veicolo soggetto alla normativa relativa al tachigrafo, munito di dispositivo analogico, deve conservare i fogli di registrazione della giornata in corso e quelli degli ultimi 28 giorni', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '127', '965', '6', 'Il conducente di veicolo soggetto alla normativa relativa al tachigrafo, munito di dispositivo analogico, deve conservare i fogli di registrazione della giornata in corso e degli ultimi tre mesi', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '127', '966', '7', 'Sul foglio di registrazione del tachigrafo analogico deve essere indicato il numero della patente e della carta di qualificazione del conducente', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '127', '967', '8', 'In caso di cambio del veicolo, durante una stessa giornata di servizio, il conducente deve utilizzare un nuovo foglio di registrazione del tachigrafo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '127', '968', '9', 'Sul veicolo soggetto alla normativa relativa al tachigrafo, munito di dispositivo analogico, devono essere conservati i fogli di registrazione relativi ai trasporti di lunghezza superiore a 50 km', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '128', '', '', 'Cronotachigrafo digitale - memoria dati', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '128', '969', '1', 'Nel tachigrafo digitale, l’apparecchio di controllo visualizza sul display l’erroneo inserimento della carta tachigrafica', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '128', '970', '2', 'Il display del tachigrafo digitale segnala il superamento dei limiti dei tempi massimi di guida', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '128', '971', '3', 'La funzione out of scope (disattivazione del tachigrafo digitale) deve essere utilizzata quando il veicolo è impiegato in attività escluse dalla normativa relativa al tachigrafo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '128', '972', '4', 'Nella memoria di un tachigrafo digitale sono registrati i dati relativi all’inserimento e all’estrazione della carta del conducente', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '128', '973', '5', 'Nella memoria di un tachigrafo digitale sono registrati e memorizzati i dati relativi alla velocità', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '128', '974', '6', 'Nella memoria di un tachigrafo digitale sono registrati gli eventuali trasferimenti di dati ad unità esterne', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '128', '975', '7', 'Nella memoria di un tachigrafo digitale sono registrati i dati relativi alla manutenzione ordinaria del veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '128', '976', '8', 'Nella memoria di un tachigrafo digitale il conducente può inserire manualmente il seguente dato: velocità media tenuta durante il viaggio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '128', '977', '9', 'Il tachigrafo digitale indica sul display la presenza di autovelox sulla strada', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '129', '', '', ' Cronotachigrafo digitale - carte tachigrafiche', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '129', '978', '1', 'La carta tachigrafica è di quattro tipi', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '129', '979', '2', 'La carta tachigrafica del conducente è rilasciata dalla Camera di Commercio', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '129', '980', '3', 'La carta tachigrafica del conducente è personale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '129', '981', '4', 'La carta tachigrafica del conducente può essere prestata ad altro autista purché appartenente alla medesima impresa', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '129', '982', '5', 'Se un veicolo è dotato di tachigrafo digitale, la carta tachigrafica del conducente deve essere utilizzata solo nei periodi effettivi di guida e deve essere estratta durante le interruzioni o i periodi di riposo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '129', '983', '6', 'Se un veicolo è dotato di tachigrafo digitale, la stessa carta tachigrafica può essere utilizzata da tutti i conducenti che si alternano alla guida di quel veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '129', '984', '7', 'I quattro tipi di carta tachigrafica sono i seguenti: carta del conducente, carta dell’impresa, carta dell’officina e carta della Camera di Commercio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '130', '', '', 'Cronotachigrafo digitale - inserimento e disinserimento della carta tachigraficha', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '130', '985', '1', 'Quando due persone si alternano alla guida di un veicolo munito di tachigrafo digitale entrambe le carte tachigrafiche devono essere inserite negli appositi alloggiamenti (slot)', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '130', '986', '2', 'Al termine della giornata lavorativa, la carta tachigrafica del conducente deve essere estratta a veicolo fermo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '130', '987', '3', 'Se un veicolo è dotato di tachigrafo digitale la carta tachigrafica del conducente deve essere utilizzata per ciascun giorno di guida a partire dal momento in cui il conducente prende in consegna un veicolo e fino a quando non lo riconsegna', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '130', '988', '4', 'La carta tachigrafica del conducente deve essere inserita nell’apposito alloggiamento al momento della presa in consegna del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '130', '989', '5', 'La carta tachigrafica del conducente non può essere estratta dal tachigrafo digitale prima che siano trascorse 24 ore dal suo inserimento', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '130', '990', '6', 'La carta tachigrafica del conducente può essere inserita nel tachigrafo digitale con veicolo in movimento', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '130', '991', '7', 'Quando due persone si alternano alla guida di un veicolo munito di tachigrafo digitale solo la persona che guida deve inserire la carta nell’apparecchio, mentre il secondo conducente deve inserire la propria carta solo quando inizia effettivamente il suo periodo di guida', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '130', '992', '8', 'Il tachigrafo digitale arresta il veicolo in marcia qualora la carta tachigrafica non sia stata correttamente inserita', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '131', '', '', 'Cronotachigrafo digitale - smarrimento, avaria o furto', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '131', '993', '1', 'In caso di avaria del tachigrafo digitale, il conducente deve annotare manualmente i tempi ed i chilometri percorsi', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '131', '994', '2', 'In caso di smarrimento della carta tachigrafica, il conducente deve stampare il resoconto dell’attività di guida registrata nella memoria del tachigrafo, firmandolo e conservandolo per i successivi viaggi', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '131', '995', '3', 'In caso di smarrimento della carta tachigrafica, il conducente può guidare al massimo per 15 giorni lavorativi, ovvero per il periodo necessario a riportare in sede il veicolo, se il viaggio ha durata superiore a 15 giorni', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '131', '996', '4', 'In caso di smarrimento della sua carta tachigrafica, il conducente deve sporgere denuncia all’autorità di polizia', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '131', '997', '5', 'In caso di smarrimento della carta tachigrafica, il conducente è obbligato ad interrompere immediatamente l’attività lavorativa, e attendere l’arrivo di un sostituto', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '131', '998', '6', 'In caso di smarrimento della carta tachigrafica, il conducente può farsela prestare da un collega', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '131', '999', '7', 'In caso di avaria del tachigrafo digitale, il conducente deve chiedere il permesso provvisorio di guida alla camera di commercio che ha rilasciato la carta tachigrafica', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '132', '', '', 'Cronotachigrafo digitale - guasti ', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '132', '1000', '1', 'Se il tachigrafo analogico si rompe durante un viaggio, il conducente deve annotare manualmente i dati relativi ai periodi di guida e di riposo utilizzando gli appositi spazi del foglio di registrazione', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '132', '1001', '2', 'In caso di guasto del tachigrafo, se il viaggio dura meno di sette giorni, si può procedere alla riparazione al ritorno del veicolo in sede', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '132', '1002', '3', 'In caso di guasto del tachigrafo si deve procedere alla sua riparazione, al massimo entro sette giorni, se il veicolo non rientra prima in sede', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '132', '1003', '4', 'In caso di guasto del tachigrafo, il conducente deve provvedere alla registrazione manuale sul retro del disco', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '132', '1004', '5', 'In caso di guasto del tachigrafo, il conducente è obbligato a effettuare le registrazioni manuali, soltanto se il viaggio è superiore a sette giorni', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '132', '1005', '6', 'In caso di guasto del tachigrafo, il conducente deve immediatamente interrompere il viaggio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '132', '1006', '7', 'Se il tachigrafo analogico si rompe durante un viaggio, il conducente deve immediatamente arrestarsi, e comunicare al datore di lavoro e all’ispettorato del lavoro i dati relativi ai periodi di guida e di riposo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '133', '', '', 'Sanzioni per omissione, uso improprio o manomissione del cronotachigrafo 01', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '133', '1007', '1', 'Chiunque alteri il tachigrafo è soggetto a sanzione amministrativa pecuniaria e a sospensione della patente di guida', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '133', '1008', '2', 'Chiunque, avendo l’obbligo di registrare i periodi di guida ai sensi delle norme vigenti, conduce un autoveicolo munito di tachigrafo non funzionante, è soggetto a sanzione amministrativa pecuniaria e a sospensione della patente di guida', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '133', '1009', '3', 'Chiunque, avendo l’obbligo di registrare i periodi di guida ai sensi delle norme vigenti, non inserisce il foglio di registrazione o la scheda del conducente, è soggetto a decurtazione dei punti della carta di qualificazione del conducente', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '133', '1010', '4', 'Chiunque circola con veicolo munito di tachigrafo avente caratteristiche non rispondenti a quelle fissate dalle norme vigenti è soggetto a sanzione amministrativa pecuniaria e a sospensione della patente di guida', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '133', '1011', '5', 'Chiunque circola con veicolo non munito di tachigrafo, nei casi in cui è esso è previsto, è soggetto a sanzione amministrativa pecuniaria e a sospensione della patente di guida', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '133', '1012', '6', 'Chiunque, avendo l’obbligo di registrare i periodi di guida ai sensi delle norme vigenti, non inserisce il foglio di registrazione o la scheda del conducente, è soggetto a sanzione amministrativa pecuniaria e a sospensione della patente di guida', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '133', '1013', '7', 'Chiunque manometta i sigilli del tachigrafo è soggetto a sanzione amministrativa pecuniaria e a sospensione della patente di guida', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '133', '1014', '8', 'Chiunque manometta i sigilli, ma non alteri il tachigrafo è soggetto alla sanzione amministrativa pecuniaria ma non alla sospensione della patente', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '133', '1015', '9', 'Chiunque, avendo l’obbligo di registrare i periodi di guida ai sensi delle norme vigenti, conduce un autoveicolo munito di tachigrafo non funzionante, è soggetto alla confisca del veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '133', '1016', '10', 'La funzione out of scope (disattivazione del tachigrafo digitale) deve essere utilizzata quando il tachigrafo è danneggiato', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '133', '1017', '11', 'Chiunque circola con veicolo non munito di tachigrafo, nei casi in cui è esso è previsto, è soggetto alla sanzione dell’arresto', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '133', '1018', '12', 'Chiunque manometta i sigilli del tachigrafo è soggetto alla sanzione amministrativa pecuniaria e alla revoca della patente di guida', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '134', '', '', 'Sanzioni per omissione, uso improprio o manomissione del cronotachigrafo 02', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '134', '1019', '1', 'Per la guida di veicoli di massa superiore a 3,5 adibiti ad uso di terzi il conducente deve essere in possesso della patente della categoria C o CE e della carta di qualificazione del conducente', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '134', '1020', '2', 'Il titolare della licenza per il trasporto di persone che mette in circolazione un veicolo sprovvisto di tachigrafo, nei casi in cui esso è previsto, è soggetto a sanzione amministrativa pecuniaria', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '134', '1021', '3', 'Il titolare dell’autorizzazione al trasporto di cose che mette in circolazione un veicolo con tachigrafo manomesso è soggetto alla sospensione della patente', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '134', '1022', '4', 'La pena per aver alterato il tachigrafo, per due volte entro due anni, è stabilita dal Tribunale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '135', '', '', 'Carta di qualificazione del conducente 01', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '135', '1023', '1', 'La carta di qualificazione del conducente è obbligatoria per i conducenti professionali che svolgono la loro attività alle dipendenze di un’impresa di autotrasporto di persone o di cose avente sede in Italia', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '135', '1024', '2', 'La carta di qualificazione del conducente è obbligatoria per i conducenti professionali cittadini di Stati extra UE che svolgono la loro attività alle dipendenze di un’impresa di autotrasporto di persone o di cose avente sede in Italia', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '135', '1025', '3', 'La carta di qualificazione del conducente non è richiesta per la guida di veicoli utilizzati per trasporti non commerciali di cose qualora il conducente non sia stato assunto come autista', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '135', '1026', '4', 'La carta di qualificazione del conducente non è richiesta per la guida di autobus adibiti a servizio regolare', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '135', '1027', '5', 'La carta di qualificazione del conducente non è richiesta per la guida di scuolabus', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '136', '', '', 'Carta di qualificazione del conducentedi autobus', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '136', '1028', '1', 'Per la guida di autobus adibiti a servizio occasionale (noleggio con conducente) è necessaria la patente di categoria D e la carta di qualificazione del conducente', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '136', '1029', '2', 'Per la guida di autobus adibiti a servizio regolare (servizio pubblico di linea) è necessaria la patente di categoria D e la carta di qualificazione del conducente', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '136', '1030', '3', 'Per la guida di autobus adibiti per servizio regolari (servizio pubblico di linea) è necessaria la patente di categoria D e il certificato di abilitazione professionale di tipo KB', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '136', '1031', '4', 'La carta di qualificazione del conducente consente ad un conducente di guidare anche nei casi in cui la sua patente di guida venga sospesa di validità', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '136', '1032', '5', 'Il conducente, se munito di patente D o DE, è esentato dal possesso della carta di qualificazione del conducente', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '137', '', '', 'Rilascio, validità e rinnovo', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '137', '1033', '1', 'La carta di qualificazione del conducente è rilasciata dall’Ufficio Motorizzazione Civile', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '137', '1034', '2', 'La carta di qualificazione del conducente ha validità 5 anni', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '137', '1035', '3', 'La carta di qualificazione del conducente si rinnova frequentando un corso di formazione periodica', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '137', '1036', '4', 'La carta di qualificazione del conducente ha una dotazione di punti distinti e ulteriori a quelli della patente di guida', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '137', '1037', '5', 'La carta di qualificazione del conducente è rilasciata dalla Camera di Commercio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '137', '1038', '6', 'La carta di qualificazione del conducente scade contestualmente alla patente di guida', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '137', '1039', '7', 'La carta di qualificazione del conducente si rinnova dimostrando di aver svolto l’attività di autista negli ultimi 5 anni', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '137', '1040', '8', 'La carta di qualificazione del conducente per il trasporto di persone non può essere rinnovata a chi ha compiuto sessanta anni di età', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '137', '1041', '9', 'La carta di qualificazione del conducente scaduta da oltre due anni si rinnova frequentando un corso di formazione iniziale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '137', '1042', '10', 'Alla carta di qualificazione del conducente è attribuito un punteggio iniziale di 30 punti', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '138', '', '', 'Ingresso, soggiorno e circolazione in UE di cittadini comunitari - 1', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '138', '1043', '1', 'L''ingresso in Italia di cittadini UE non è soggetto a particolari limitazioni', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '138', '1044', '2', 'L''ingresso in Italia di cittadini UE può essere soggetto a limitazioni per motivi di ordine pubblico', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '138', '1045', '3', 'I cittadini UE possono entrare, circolare e soggiornare in Italia fino a 3 mesi solo con la carta di identità e dichiarazione di presenza', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '138', '1046', '4', 'I cittadini UE non hanno bisogno di permesso per soggiornare in Italia', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '138', '1047', '5', 'I cittadini di Stati membri UE hanno diritto di entrare, circolare e soggiornare in Italia per non più di tre mesi senza limitazioni purché possiedano una carta d’identità in corso di validità', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '138', '1048', '6', 'I cittadini di Stati membri UE hanno diritto di entrare, circolare e soggiornare in Italia per non più di tre mesi, a condizione che abbiano la carta di soggiorno rilasciata dalla questura', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '138', '1049', '7', 'I cittadini di Stati membri UE hanno diritto di entrare, circolare e soggiornare in Italia per non più di tre mesi, a condizione che abbiano un passaporto munito di visto d’ingresso', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '138', '1050', '8', 'Il cittadino di uno Stato membro che soggiorna in un altro Stato membro può essere allontanato per motivi religiosi', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '138', '1051', '9', 'I cittadini UE, per soggiornare in Italia, devono richiedere il visto alle rappresentanze diplomatico consolari dello Stato di provenienza', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '139', '', '', ' Ingresso, soggiorno e circolazione in UE di cittadini comunitari - 2', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '139', '1052', '1', 'Ai cittadini UE è riconosciuto il diritto di circolare liberamente nell’UE senza bisogno di richiedere alcun visto', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '139', '1053', '2', 'Ai cittadini UE è riconosciuto il diritto di circolare liberamente nel territorio dello Stato membro, salve eccezioni', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '139', '1054', '3', 'Il soggiorno di cittadini UE in altro Stato membro per un periodo inferiore a tre mesi è subordinato al possesso di un valido documento d’identità', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '139', '1055', '4', 'Il cittadino di uno Stato membro che soggiorna in un altro Stato membro può essere allontanato solo per motivi di ordine pubblico, di pubblica sicurezza o di sanità', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '140', '', '', 'Ingresso in UE di cittadini extra-UE', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '140', '1056', '1', 'I cittadini extra UE possono entrare in Italia se sono in possesso di passaporto e visto di ingresso validi', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '140', '1057', '2', 'Gli stranieri extra UE in ingresso in Italia sono soggetti a controlli di frontiera, doganali, valutari e sanitari', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '140', '1058', '3', 'L’ingresso nel territorio italiano è consentito agli stranieri extra UE che si presentano attraverso un valico di frontiera e siano in possesso di un passaporto o di altro documento di viaggio equivalente riconosciuto valido per l’attraversamento delle frontiere', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '140', '1059', '4', 'L’ingresso nel territorio italiano è consentito agli stranieri extra UE che non siano considerati pericolosi per l’ordine pubblico, la sicurezza nazionale, la salute pubblica o le relazioni internazionali', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '140', '1060', '5', 'L’ingresso nel territorio italiano è consentito senza altre formalità, agli stranieri extra UE che dimostrino di avere parenti incensurati', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '140', '1061', '6', 'L’ingresso nel territorio italiano è consentito agli stranieri extra UE che siano stati segnalati al sistema informativo Schengen ai fini della non ammissione', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '140', '1062', '7', 'L’attraversamento delle frontiere con Stati aderenti alla convenzione di Schengen ma non alla UE, comporta controlli sull’identità delle persone ma non comporta controlli doganali e limitazioni all’importazione ed esportazione di denaro e beni', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '141', '', '', 'Ingresso, soggiorno e circolazione in UE di cittadini extra-UE', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '141', '1063', '1', 'L''ingresso nel territorio italiano è consentito agli stranieri extra UE che abbiano un visto di ingresso o di transito, nei casi in cui è richiesto', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '141', '1064', '2', 'L’ingresso nel territorio italiano è consentito agli stranieri extra UE che dimostrino di avere mezzi finanziari per il loro sostentamento e abbiano a disposizione la somma necessaria al rimpatrio, eventualmente dimostrabile con l’esibizione del biglietto di ritorno', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '141', '1065', '3', 'Tutti i cittadini, che non appartengono a Stati UE, possono entrare in Italia presentando il passaporto e, nei casi in cui è richiesto, il visto rilasciato nel loro Stato di provenienza', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '141', '1066', '4', 'Il visto è l’autorizzazione concessa allo straniero extra UE per l’ingresso nel territorio della Repubblica italiana, è stampato su carta adesiva e si applica sul passaporto o altro documento di viaggio del richiedente', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '141', '1067', '5', 'I cittadini di alcuni Stati non sono obbligati a richiedere il visto d’ingresso per soggiorni per turismo, missione, affari, invito e gara sportiva purché non superiori a 90 giorni', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '141', '1068', '6', 'Lo straniero extra UE, che entra legalmente in Italia e intende soggiornarvi per più di 3 mesi, dovrà richiedere il permesso di soggiorno entro otto giorni lavorativi', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '141', '1069', '7', 'Lo straniero extra UE che entra legalmente in Italia, entro 30 giorni lavorativi, dovrà richiedere il permesso di soggiorno', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '141', '1070', '8', 'I cittadini di alcuni Stati non sono obbligati a richiedere il visto d’ingresso per soggiorni per turismo, missione, affari, invito e gara sportiva purché superiori a 120 giorni', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '141', '1071', '9', 'Gli stranieri extra UE che vengono in Italia per visite, affari, turismo e studio per periodi non superiori ai tre mesi, devono chiedere il permesso di soggiorno', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '141', '1072', '10', 'Gli stranieri extra UE in ingresso in Italia non sono sottoposti ai controlli di frontiera, doganali, valutari e sanitari', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '141', '1073', '11', 'L’attraversamento delle frontiere con Stati non appartenenti alla UE e non aderenti alla convenzione di Schengen, è vietato per i veicoli che trasportano merci', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '142', '', '', 'Visto d’ingresso', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '142', '1074', '1', 'Il passaporto può essere individuale o collettivo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '142', '1075', '2', 'Non è possibile il rilascio del visto (né la proroga) allo straniero che già si trovi in Italia', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '142', '1076', '3', 'Il visto è un’autorizzazione concessa allo straniero per l’ingresso nel territorio della Repubblica italiana', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '142', '1077', '4', 'Il visto è rilasciato dalle ambasciate e dai consolati italiani nello Stato di origine o della stabile residenza dello straniero', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '142', '1078', '5', 'Il visto rilasciato dalle ambasciate e dai consolati italiani nello Stato di origine o della stabile residenza dello straniero, consente la libera circolazione in tutti gli Stati Europei', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '142', '1079', '6', 'Il visto Schengen uniforme consente il transito o il breve soggiorno fino a 150 giorni', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '142', '1080', '7', 'Il visto può essere individuale o collettivo e se è di tipo collettivo ha la durata massima è di 60 giorni', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '142', '1081', '8', 'Il visto è rilasciato dal tribunale italiano allo straniero che intende soggiornarvi', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '142', '1082', '9', 'Il visto è una firma di convalida del timbro di entrata effettuata al momento dell’ingresso nel territorio della Repubblica italiana', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '142', '1083', '10', 'Il passaporto collettivo può essere rilasciato a gruppi che viaggiano con finalità diverse e che sono composti da almeno cento persone', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '142', '1084', '11', 'La carta d’identità valida per l’espatrio, sostituisce il passaporto all’attraversamento di tutte le frontiere', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '143', '', '', 'Accordo di Schengen', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '143', '1085', '1', 'Nello spazio di Schengen le frontiere interne possono essere attraversate in qualunque luogo senza che venga effettuato il controllo delle persone', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '143', '1086', '2', 'Ogni Stato sottoscrittore dell’accordo di Schengen può sospendere l’uso del trattato per un limitato periodo e per specifici motivi', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '143', '1087', '3', 'Negli Stati dello spazio Schengen si applicano regole e procedure comuni in materia di visti, soggiorni brevi, richieste d’asilo e controlli alle frontiere', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '143', '1088', '4', 'Secondo i principi dell’accordo di Schengen, le frontiere interne possono essere attraversate in qualunque luogo senza che venga effettuato il controllo delle persone', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '143', '1089', '5', 'L’accordo di Schengen comporta l’abolizione dei controlli sistematici delle persone alle frontiere interne al suo spazio (spazio Schengen)', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '143', '1090', '6', 'L’accordo di Schengen comporta il rafforzamento dei controlli alle frontiere esterne al suo spazio (spazio Schengen)', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '143', '1091', '7', 'L’accordo di Schengen comporta il rafforzamento dei controlli alle frontiere interne al suo spazio (spazio Schengen)', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '143', '1092', '8', 'L’accordo di Schengen prevede ingenti sgravi fiscali per i conducenti di veicoli adibiti al trasporto di merci che circolano all’interno del suo spazio (spazio Schengen)', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '143', '1093', '9', 'L’accordo di Schengen comporta l’abolizione dei controlli sistematici delle persone alle frontiere esterne al suo spazio (spazio Schengen)', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '143', '1094', '10', 'Secondo i principi dell’accordo di Schengen, le frontiere interne possono essere attraversate in qualunque luogo senza che sia necessario essere in possesso di un documento d’identità', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '144', '', '', 'Accordo di Schengen - paesi aderenti ', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '144', '1095', '1', 'La Polonia è uno Stato membro UE ed è interno allo spazio Schengen', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '144', '1096', '2', 'La Slovenia è uno Stato membro UE ed è interno allo spazio Schengen', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '144', '1097', '3', 'La Lettonia è uno Stato membro UE ed è interno allo spazio Schengen', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '144', '1098', '4', 'Il Regno Unito non è più uno Stato membro UE', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '144', '1099', '5', 'L’Irlanda è uno Stato membro UE ma non è interno allo spazio Schengen', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '144', '1100', '6', 'All’Accordo di Schengen aderiscono anche Stati extra UE', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '144', '1101', '7', 'All’Accordo di Schengen aderiscono tutti gli Stati UE', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '144', '1102', '8', 'La Serbia è uno Stato membro UE ed è interno allo spazio Schengen', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '144', '1103', '9', 'L’Irlanda è uno Stato membro UE ed è interno allo spazio Schengen', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '144', '1104', '10', 'L’Ucraina non è uno Stato membro UE ma è interno allo spazio Schengen', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '144', '1105', '11', 'Tutti gli Stati membri UE sono interni allo spazio Schengen', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '145', '', '', 'Attraversamento delle frontiere con Stati aderenti alla UE', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '145', '1106', '1', 'Alle frontiere con Stati UE aderenti alla convenzione di Schengen, non si applica, in generale, alcun tipo di controllo, né di tipo doganale, né relativo alle persone ai fini della sicurezza', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '145', '1107', '2', 'L’attraversamento delle frontiere con Stati aderenti alla convenzione di Schengen ma non alla UE, comporta controlli doganali e limitazioni all’importazione ed esportazione di denaro e beni, ma non comporta controlli sull’identità delle persone', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '145', '1108', '3', 'L’attraversamento delle frontiere con Stati aderenti alla UE ma non alla convenzione di Schengen, comporta controlli sull’identità delle persone ma non comporta controlli doganali', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '145', '1109', '4', 'L’attraversamento delle frontiere con Stati non appartenenti alla UE e non aderenti alla convenzione di Schengen, comporta sempre controlli doganali con limitazioni di esportazione e importazione di beni e di valuta', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '145', '1110', '5', 'L’attraversamento delle frontiere con Stati non appartenenti alla UE e non aderenti alla convenzione di Schengen, comporta sempre controlli sull’identità delle persone', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '145', '1111', '6', 'L’attraversamento delle frontiere con Stati UE aderenti alla convenzione di Schengen, comporta sempre controlli doganali con limitazioni di esportazione e importazione di beni e di valuta', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '145', '1112', '7', 'L’attraversamento delle frontiere con Stati aderenti alla UE ma non alla convenzione di Schengen, comporta controlli doganali ma non comporta controlli sull’identità delle persone', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '145', '1113', '8', 'Alle frontiere con Stati UE non aderenti alla convenzione di Schengen, non si applica, in generale, alcun tipo di controllo né di tipo doganale né relativo alle persone ai fini della sicurezza', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '145', '1114', '9', 'L’accordo di Schengen deve essere obbligatoriamente mostrato dal conducente agli organi di Polizia durante l’attraversamento delle frontiere interne al suo spazio (spazio Schengen)', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '146', '', '', 'Obbighi del vettore', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '146', '1115', '1', 'Il vettore deve accertarsi che gli stranieri extra UE trasportati siano in possesso dei documenti richiesti per l’ingresso nell’area Schengen', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '146', '1116', '2', 'Il vettore è obbligato a riprendere a bordo gli stranieri extra UE rifiutati dall’area Schengen ed a ricondurli nello Stato di provenienza', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '146', '1117', '3', 'I vettori devono accertarsi che gli stranieri extra UE siano in possesso dei documenti di viaggio richiesti per l’ingresso negli Stati dell’Accordo di Schengen', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '146', '1118', '4', 'I vettori devono accertarsi che gli stranieri extra UE siano in possesso dei mezzi finanziari richiesti per l’ingresso negli Stati dell’Accordo di Schengen', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '147', '', '', 'Immigrazione clandestina - 01', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '147', '1119', '1', 'L’immigrazione clandestina comporta il traffico di esseri umani che spesso vengono sfruttati dalle organizzazioni criminali per compiere attività illecite (prostituzione, lavoro nero, traffico di stupefacenti ecc.)', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '147', '1120', '2', 'L’immigrazione clandestina è l’ingresso illegale in altro Stato di persone che non sono cittadini o non hanno, in esso, titolo per soggiornarvi', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '147', '1121', '3', 'L’immigrazione clandestina coinvolge anche il traffico di esseri umani e può alimentare il mercato del lavoro nero', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '147', '1122', '4', 'L’immigrazione clandestina può avvenire via mare (prevalente in Italia), via terra e via aria', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '147', '1123', '5', 'L’immigrazione clandestina è un reato ed è punito con una pesante sanzione pecuniaria', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '147', '1124', '6', 'L’immigrazione clandestina non è reato ed è punita con una diffida', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '147', '1125', '7', 'L’immigrazione clandestina è consentita negli Stati membri UE', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '147', '1126', '8', 'L’immigrazione clandestina riguarda l’ingresso legale in altro Stato di persone che non sono cittadini o non hanno titolo di residenza permanente', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '148', '', '', 'Immigrazione clandestina - 02', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '148', '1127', '1', 'L’immigrato clandestino è il cittadino di Stato extra UE entrato in Italia senza rispettare le norme in materia di ingresso', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '148', '1128', '2', 'L’immigrato clandestino è il cittadino di Stato UE entrato in Italia senza rispettare l’obbligo di avere con sé un valido documento d’identità', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '148', '1129', '3', 'L’ingresso illegale di persone in Italia è consentito se a scopo lavorativo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '148', '1130', '4', 'L''ingresso forza di trazione di persone in Italia è consentito se a scopo lavorativo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '148', '1131', '5', 'In caso di immigrazione clandestina, il mezzo di trasporto che ha portato lo straniero alla frontiera è obbligato a condurlo alla Prefettura più vicina', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '148', '1132', '6', 'L''ingresso illegale di persone non comporta rischi per la sicurezza nazionale e l''ordine pubblico', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '149', '', '', 'Aree maggiormente interessate dal fenomeno dell’immigrazione clandestina', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '149', '1133', '1', 'Una delle aree maggiormente interessate dal fenomeno dell’immigrazione clandestina come punto di ingresso in Italia è la costa siciliana, in particolare le isole di Lampedusa e Pantelleria', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '149', '1134', '2', 'Una delle aree maggiormente interessate dal fenomeno dell’immigrazione clandestina come punto di ingresso in Italia è la costa adriatica pugliese', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '149', '1135', '3', 'Una delle aree maggiormente interessate dal fenomeno dell’immigrazione clandestina come punto di ingresso in Italia è la costa ionica calabrese', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '149', '1136', '4', 'Una delle aree maggiormente interessate dal fenomeno dell’immigrazione clandestina come punto di ingresso in Italia è la città di Gorizia', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '149', '1137', '5', 'Tra le aree maggiormente interessate dal fenomeno dell’immigrazione clandestina come punto di ingresso in Italia vi sono le frontiere terrestri di Trieste e Tarvisio', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '149', '1138', '6', 'Una delle aree maggiormente interessate dal fenomeno dell’immigrazione clandestina come punto di ingresso in Italia è la città di Roma ed in particolare la zona di Fregene', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '149', '1139', '7', 'Una delle aree maggiormente interessate dal fenomeno dell’immigrazione clandestina come punto di ingresso in Italia è la città di Milano', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '149', '1140', '8', 'Tra le aree maggiormente interessate dal fenomeno dell''immigrazione clandestina come punto di ingresso in Italia vi sono le coste della Sardegna', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '149', '1141', '9', 'Tra le aree maggiormente interessate dal fenomeno dell''immigrazione clandestina come punto di ingresso in Italia vi sono le coste della Versilia', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '149', '1142', '10', 'Tra le aree maggiormente interessate dal fenomeno dell''immigrazione clandestina come punto di ingresso in Italia vi sono le coste liguri di ponente', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '149', '1143', '11', 'La costa tirrenica è una delle principali vie d''accesso per i clandestini in Italia', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '150', '', '', ' Strumenti di controllo alle frontiere dei immigrati clandestini -01', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '150', '1144', '1', 'Tra le misure adottate per scongiurare l’ingresso illegittimo di cittadini stranieri vi è l’uso di rilevatori di battito cardiaco (heartbeat) che consentono di rilevare la presenza di persone all’interno dei veicoli', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '150', '1145', '2', 'Tra le tecnologie utilizzate dalle autorità di frontiera per contrastare il fenomeno dell’immigrazione clandestina a bordo di veicoli vi sono i rilevatori di anidride carbonica', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '150', '1146', '3', 'Tra le tecnologie utilizzate dalle autorità di frontiera per contrastare il fenomeno dell’immigrazione clandestina a bordo di veicoli si utilizzano sistemi a raggi gamma', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '150', '1147', '4', 'Tra le tecnologie usate per i controlli alle frontiere ci sono anche rilevatori di battito cardiaco', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '150', '1148', '5', 'Tra le tecnologie usate per i controlli alle frontiere ci sono anche i rilevatori di ossigeno', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '150', '1149', '6', 'Il controllo dei veicoli, al fine di scongiurare l’ingresso illegittimo di cittadini stranieri, comporta l’uso di cariche esplosive che spaventano gli eventuali clandestini presenti a bordo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '150', '1150', '7', 'Il controllo dei veicoli, al fine di rilevare la presenza di eventuali clandestini, comporta inevitabilmente il completo scarico della merce presente sul veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '151', '', '', 'Strumenti di controllo alle frontiere dei immigrati clandestini - 02', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '151', '1151', '1', 'Tra le misure adottate per scongiurare l’ingresso illegittimo di cittadini stranieri vi è l’uso di sistemi ad onde passive che consentono di rilevare immagini/sagome di persone all’interno dei veicoli', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '151', '1152', '2', 'Per il controllo dei veicoli, al fine di scongiurare l’ingresso illegittimo di cittadini stranieri si utilizzano, tra l’altro, unità cinofile', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '151', '1153', '3', 'Per il controllo dei veicoli, al fine di scongiurare l’ingresso illegittimo di cittadini stranieri, si utilizzano onde sonore ad alta frequenza che costringono gli eventuali clandestini ad uscire dal veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '151', '1154', '4', 'Il controllo dei veicoli, al fine di scongiurare l’ingresso illegittimo di cittadini stranieri, avviene normalmente tramite sistemi satellitari', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '151', '1155', '5', 'Tra le misure adottate per scongiurare l’ingresso illegittimo di cittadini stranieri vi è l’uso di raggi www che consentono, tramite internet, di vedere all’interno dei veicoli', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '152', '', '', 'Prevenzione', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '152', '1156', '1', 'Le imprese di autotrasporto ed i loro conducenti devono predisporre ed attuare un piano di controllo adeguato al fine di evitare, nella massima misura possibile, che i controlli effettuati dalle forze dell’ordine possano dare riscontri positivi in ordine alla presenza a bordo di clandestini', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '152', '1157', '2', 'Gli agenti di pubblica sicurezza possono procedere ai controlli dei mezzi e delle cose trasportate anche se soggetti a  speciale regime doganale se sussistono fondati motivi o sospetti che i veicoli possano essere utilizzati per compiere trasporti illegali di merci o persone', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '152', '1158', '3', 'Le aziende non sono tenute ad informare i conducenti circa i controlli da effettuare ed il comportamento da tenere nel caso si riscontri la presenza di clandestini a bordo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '152', '1159', '4', 'Il controllo dei mezzi e delle cose trasportate, se soggetti a speciale regime doganale, non possano essere controllati anche se si sospetta che possano essere utilizzati per compiere trasporti illegali di merci o persone', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '153', '', '', 'Precauzioni e vigilanza dei conducenti - 01', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '153', '1160', '1', 'Possono essere indicativi della presenza di clandestini a bordo del veicolo segni di effrazione e danni al veicolo anche se lievi', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '153', '1161', '2', 'Può essere indicativo della presenza di clandestini a bordo del veicolo la rottura delle serrature del vano di carico', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '153', '1162', '3', 'Per evitare la salita sul veicolo di immigrati clandestini è opportuno parcheggiare in aree custodite e chiudere il veicolo a chiave', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '153', '1163', '4', 'Per evitare la salita sul veicolo di immigrati clandestini il conducente di un autobus deve effettuare un controllo generale e approfondito del veicolo e del rimorchio', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '153', '1164', '5', 'Per evitare la salita sul veicolo di immigrati clandestini è opportuno effettuare il riconoscimento documentale dei passeggeri', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '153', '1165', '6', 'Per evitare di favorire l’immigrazione di clandestini, in particolare nelle zone a rischio, il veicolo deve essere lasciato incustodito per un tempo massimo di dieci minuti', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '154', '', '', 'Precauzioni e vigilanza dei conducenti - 02', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '154', '1166', '1', 'Per evitare di favorire l’immigrazione di clandestini è necessaria la custodia continua del veicolo, in particolare nelle zone a rischio', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '154', '1167', '2', 'Per evitare di favorire l’immigrazione di clandestini è necessario esaminare attentamente le parti del veicolo a rischio ed in caso di sospetto dare immediata denuncia alle autorità competenti', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '154', '1168', '3', 'Per evitare di trasportare clandestini in modo inconsapevole, in particolare nel trasporto merci, è necessario controllare anche sul tetto, all’interno del veicolo, dietro la cabina e tra gli assi delle ruote', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '154', '1169', '4', 'Per evitare di trasportare clandestini in modo inconsapevole, in particolare nel trasporto persone, è necessario controllare anche all’interno del veicolo per verificare eventuali infiltrati tra i passeggeri regolari', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '154', '1170', '5', 'Per evitare di trasportare clandestini in modo inconsapevole, le aziende vietano l’uso di lucchetti o sigilli apposti sulle parti del veicolo a rischio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '154', '1171', '6', 'Per evitare di favorire l’immigrazione di clandestini è necessario esaminare attentamente le parti a rischio del proprio veicolo ed in caso di sospetto abbandonare immediatamente il veicolo per evitare di essere coinvolti', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '155', '', '', 'Sanzioni per agevolare il tranporto dei clandestini - 01', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '155', '1172', '1', 'Oltre alle sanzioni personali (fino a 15 anni di reclusione), a carico di coloro che, in qualche modo, agevolano l’ingresso illegale di stranieri in Italia, viene disposta anche la confisca del mezzo di trasporto utilizzato per compiere il fatto', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '155', '1173', '2', 'Per favoreggiamento dell’immigrazione clandestina è prevista: la reclusione, la multa e la confisca del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '155', '1174', '3', 'In mancanza di prove inconfutabili che dimostrino che è stato fatto tutto il possibile per non agevolare il trasporto di clandestini, il conducente, può essere sanzionato in maniera estremamente pesante, sia penalmente che amministrativamente', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '155', '1175', '4', 'Lo straniero extra UE che viene trovato senza permesso di soggiorno o con permesso scaduto viene espulso', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '155', '1176', '5', 'Lo straniero extra UE che viene trovato senza permesso di soggiorno o con permesso scaduto viene subito regolarizzato', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '155', '1177', '6', 'Il reato di favoreggiamento dell’immigrazione clandestina prevede la confisca del veicolo ma non la carcerazione', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '155', '1178', '7', 'Il reato di favoreggiamento dell’immigrazione clandestina prevede pesanti sanzioni solo a favore del conducente del veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '156', '', '', 'Sanzioni per agevolare il tranporto dei clandestini - 02', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '156', '1179', '1', 'Anche i vettori sono ritenuti responsabili del reato di immigrazione clandestina se ritenuti corresponsabili per aver organizzato, permesso o anche semplicemente perché erano a conoscenza del trasporto illegale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '156', '1180', '2', 'In caso di reiterate o gravi infrazioni, inerenti al trasporto illegale di persone (clandestini), può essere disposta la sospensione o la revoca dell’autorizzazione a svolgere attività di autotrasporto', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '156', '1181', '3', 'Per mancato accertamento della presenza a bordo di clandestini sono previste: sanzione amministrativa pecuniaria e sospensione o revoca dell’autorizzazione', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '156', '1182', '4', 'Le norme italiane puniscono severamente tutti coloro che compiono attività dirette a favorire l’ingresso illegale degli stranieri nel territorio italiano', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '156', '1183', '5', 'Le norme italiane prevedono sanzioni per i clandestini ma non per tutti coloro che compiono attività dirette a favorire l’ingresso illegale degli stranieri nel territorio italiano', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '156', '1184', '6', 'Le norme che gli autotrasportatori sono tenuti a rispettare per contribuire a debellare l’immigrazione clandestina sono contenute in appositi patti bilaterali tra l’Italia e lo Stato da cui proviene l’immigrato irregolare', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '156', '1185', '7', 'Le norme che gli autotrasportatori sono tenuti a rispettare per contribuire a debellare l’immigrazione clandestina, consigliano al vettore di riferire alle autorità di frontiera dell’eventuale presenza a bordo di stranieri in posizione irregolare', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '156', '1186', '8', 'Se il vettore riferisce alle autorità di frontiera dell’eventuale presenza a bordo di stranieri in posizione irregolare rischia di incorrere nel reato di delazione', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '157', '', '', 'Sanzioni per agevolare il tranporto dei clandestini - 03', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '157', '1187', '1', 'In caso di immigrazione clandestina, il mezzo di trasporto che ha portato lo straniero alla frontiera è obbligato a ricondurlo nello Stato di provenienza o in quello dove è stato rilasciato il biglietto', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '157', '1188', '2', 'In mancanza di prove inconfutabili che dimostrino che è stato fatto tutto il possibile per non agevolare il trasporto di clandestini, l’impresa, può essere sanzionata in maniera estremamente pesante, sia penalmente che amministrativamente', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '157', '1189', '3', 'In mancanza di prove inconfutabili che dimostrino che è stato fatto tutto il possibile per non agevolare il trasporto di clandestini, solo il conducente, può essere sanzionato in maniera estremamente pesante, sia penalmente che amministrativamente', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '157', '1190', '4', 'In mancanza di prove inconfutabili che dimostrino che è stato fatto tutto il possibile per non agevolare il trasporto di clandestini, il conducente ma non l’impresa, possono essere sanzionati in maniera estremamente pesante, sia penalmente che amministrativamente', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '157', '1191', '5', 'Il favoreggiamento dell’immigrazione clandestina prevede solo pesanti sanzioni pecuniarie non essendo considerato reato', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '157', '1192', '6', 'Il favoreggiamento dell’immigrazione clandestina è un illecito amministrativo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '158', '', '', 'Rapine e aggressioni', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '158', '1193', '1', 'In caso di aggressioni, furti, rapine occorre chiamare le forze dell’ordine', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '158', '1194', '2', 'Nel settore dell’autotrasporto sono frequenti i furti di veicoli e di merci', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '158', '1195', '3', 'Nel settore dell’autotrasporto, le rapine sono più frequenti nelle aree poco frequentate e illuminate', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '158', '1196', '4', 'Nel settore dell’autotrasporto sono frequenti le rapine a danno del conducente', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '158', '1197', '5', 'Nel settore dell’autotrasporto sono frequenti i sequestri dei viaggiatori', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '158', '1198', '6', 'Nel settore dell’autotrasporto le rapine sono più frequenti durante le soste nelle aree custodite', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '158', '1199', '7', 'Nel settore dell’autotrasporto le rapine più frequenti sono finalizzate a sottrarre il motore dal veicolo lasciato in sosta', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '159', '', '', ' Prevenzione di aggressioni, furti e rapine - 01', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '159', '1200', '1', 'Per prevenire le aggressioni è bene programmare un itinerario opportunamente studiato', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '159', '1201', '2', 'Per prevenire le aggressioni è bene conoscere l’ubicazione dei luoghi malsicuri dell’itinerario', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '159', '1202', '3', 'Per prevenire le aggressioni è opportuno parcheggiare in aree custodite', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '159', '1203', '4', 'Per prevenire le aggressioni è opportuno parcheggiare in luoghi ben illuminati', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '159', '1204', '5', 'Per prevenire le aggressioni è opportuno evitare, se possibile, di transitare in aree isolate', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '159', '1205', '6', 'Per prevenire le aggressioni è opportuno parcheggiare il veicolo in luoghi isolati', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '159', '1206', '7', 'Per prevenire aggressioni o rapine è legittimo esporre armi da fuoco sul cruscotto del veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '159', '1207', '8', 'Per prevenire le aggressioni è consentito superare i limiti di velocità quando si transita in aree a rischio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '160', '', '', 'Prevenzione di aggressioni, furti e rapine - 02', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '160', '1208', '1', 'Per ridurre il rischio di furto dei veicoli e delle merci è opportuno parcheggiare il veicolo in luoghi non isolati', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '160', '1209', '2', 'Per ridurre il rischio di furti del carico del veicolo è opportuno accertarsi della chiusura del vano di carico', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '160', '1210', '3', 'Per ridurre i furti dei veicoli è opportuno dotarli di antifurto', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '160', '1211', '4', 'Per evitare i furti di veicoli è opportuno togliere sempre la chiave di avviamento', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '160', '1212', '5', 'Per ridurre il rischio di rapina ai danni di un conducente, è opportuno sostare in luoghi non isolati', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '160', '1213', '6', 'Per ridurre il rischio di rapine ai conducenti è consentito non rispettare i tempi di guida e di riposo per arrivare a destinazione prima possibile', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '160', '1214', '7', 'Per ridurre il rischio di rapine ai conducenti occorre sostare in aree isolate o poco illuminate per passare inosservati', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '160', '1215', '8', 'Per evitare i furti del carico è necessario lasciare il veicolo in sosta con il freno a mano inserito', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '160', '1216', '9', 'Per evitare i furti di veicoli e del carico è opportuno sostare in luoghi poco frequentati mantenendo attivata la segnalazione luminosa del veicolo (indicatori di direzione lampeggianti simultaneamente)', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '160', '1217', '10', 'In caso di furti o rapine è consentito in ogni caso usare le armi per difendere il carico', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '160', '1218', '11', 'I cunei fermaruote costituiscono un valido sistema antifurto del veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '161', '', '', 'Comportamento del conducente in caso di aggressioni', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '161', '1219', '1', 'Il conducente non deve reagire a comportamenti aggressivi per non mettere a rischio l’integrità fisica dei passeggeri', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '161', '1220', '2', 'Il conducente non deve reagire con violenza alle aggressioni verbali', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '161', '1221', '3', 'In caso di aggressioni, furti, rapine occorre fornire alle forze dell’ordine tutti i particolari rilevanti', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '161', '1222', '4', 'In caso di aggressioni, furti, rapine si può presentare denuncia o alla compagnia di assicurazione o, in alternativa, alle forze dell’ordine', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '161', '1223', '5', 'In caso di aggressioni, furti, rapine bisogna reagire ai malviventi se sono armati', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '161', '1224', '6', 'In caso di aggressioni, furti, rapine occorre non far intervenire le forze dell’ordine per non peggiorare la situazione', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '162', '', '', ' Immagine dell’azienda di autotrasporto - 01', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '162', '1225', '1', 'L’immagine di un’azienda di autotrasporto è determinata dalla modernità dei mezzi di trasporto utilizzati11', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '162', '1226', '2', 'L’immagine di un’azienda di autotrasporto è determinata dalla sensibilità ecologica e dall’attenzione alla tutela dell’ambiente', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '162', '1227', '3', 'L’immagine di un’azienda di autotrasporto è determinata anche dalla puntualità nelle consegne', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '162', '1228', '4', 'L’immagine di un’azienda di autotrasporto è determinata anche dalla qualità del servizio', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '162', '1229', '5', 'L’immagine di un’azienda di autotrasporto è determinata dal continuo miglioramento e rinnovamento del servizio', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '162', '1230', '6', 'L’immagine di un’azienda di autotrasporto è condizionata dal fatto che i conducenti fumino durante la sosta nelle aree di servizio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '162', '1231', '7', 'L’immagine di un’azienda di autotrasporto è determinata dal numero di veicoli in leasing, in disponibilità dell’impresa.', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '162', '1232', '8', 'L’immagine di un’azienda di autotrasporto è determinata dal tipo di contratto collettivo applicato al personale dipendente', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '162', '1233', '9', 'L’immagine di un’azienda di autotrasporto è determinata dall’età dei conducenti', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '163', '', '', 'Immagine di un’impresa di autotrasporto - 01', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '163', '1234', '1', 'L’immagine di un’impresa di autotrasporto è determinata anche dai comportamenti dei conducenti che hanno un contatto diretto con i clienti', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '163', '1235', '2', 'L’immagine di un’impresa di autotrasporto è determinata anche dai giudizi dei dipendenti', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '163', '1236', '3', 'L''immagine di un''impresa di autotrasporto condiziona le opinioni dei clienti', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '163', '1237', '4', 'L’immagine di un’impresa di autotrasporto dipende anche dalla capacità del conducente di interagire con i committenti', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '163', '1238', '5', 'L’immagine di un’impresa di autotrasporto dipende anche dalla capacità del conducente di interagire con i viaggiatori', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '163', '1239', '6', 'L’immagine di un’impresa di autotrasporto dipende anche dalla qualità della prestazione lavorativa dei conducenti', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '163', '1240', '7', 'L’immagine di un’impresa di autotrasporto dipende dalla nazionalità del conducente', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '163', '1241', '8', 'L’immagine di un’impresa di autotrasporto è determinata dal tipo di prodotti che trasporta', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '164', '', '', ' Immagine di un’impresa di autotrasporto - 02', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '164', '1242', '1', 'L’immagine di un’impresa di autotrasporto è influenzata anche da come i conducenti si presentano ai clienti', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '164', '1243', '2', 'L''immagine di un''impresa di autotrasporto è anche influenzata dalla manutenzione e dalla pulizia dei mezzi di trasporto', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '164', '1244', '3', 'L’immagine di un’impresa di autotrasporto è rappresentata dall’insieme delle opinioni e dalla visione che i clienti hanno di essa', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '164', '1245', '4', 'L’immagine di un’impresa di autotrasporto incide sul suo fatturato', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '164', '1246', '5', 'L’immagine di un’impresa di autotrasporto non è condizionata dai comportamenti dei conducenti che vi lavorano', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '164', '1247', '6', 'L’immagine di un’impresa di autotrasporto non è condizionata dal decoro e dall’abbigliamento dei conducenti', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '164', '1248', '7', 'L’immagine di un’impresa di autotrasporto non è condizionata in alcun modo dal lavoro dei conducenti', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '164', '1249', '8', 'L’immagine di un’impresa di autotrasporto non è essenziale perché il settore non è soggetto a concorrenza', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '164', '1250', '9', 'L’immagine di un’impresa di autotrasporto non è influenzata dai rapporti dei conducenti con gli utenti', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '164', '1251', '10', 'L’immagine di un’impresa di autotrasporto può anche non essere curata in quanto questo settore non è soggetto a concorrenza', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '164', '1252', '11', 'L’immagine di un’impresa di autotrasporto si forma solo con campagne pubblicitarie', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '165', '', '', 'Ruolo del conducente all’interno dell’impresa - 01', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '165', '1253', '1', 'Il conducente ha un ruolo importante all’interno dell’impresa di autotrasporto perché ha contatti con numerosi interlocutori tra cui le autorità doganali', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '165', '1254', '2', 'Il conducente ha un ruolo importante all’interno dell’impresa di autotrasporto perché ha contatti con numerosi interlocutori tra cui i clienti', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '165', '1255', '3', 'Il conducente ha un ruolo importante all’interno dell’impresa di autotrasporto perché ha contatti con numerosi interlocutori tra cui gli spedizionieri o gli agenti di viaggio', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '165', '1256', '4', 'Il conducente ha un ruolo importante all’interno dell’impresa di autotrasporto perché ha contatti con numerosi interlocutori tra cui le autorità che hanno rilasciato le autorizzazioni per lo svolgimento del servizio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '165', '1257', '5', 'Il conducente ha un ruolo importante all’interno dell’impresa di autotrasporto perché ha contatti con numerosi interlocutori tra cui le banche', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '166', '', '', 'Ruolo del conducente all’interno dell’impresa - 02', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '166', '1258', '1', 'Il conducente ha l’obbligo di avvisare per tempo, nel caso in cui non possa recarsi al lavoro, al fine di consentire all’azienda di prendere provvedimenti e garantire il servizio', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '166', '1259', '2', 'Il conducente ha l’obbligo di vestirsi in maniera consona e professionale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '166', '1260', '3', 'Il conducente di un’impresa di autotrasporto ha l’obbligo di presentarsi al lavoro in orario', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '166', '1261', '4', 'Al conducente viene richiesta la perfetta conoscenza di almeno quattro lingue', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '167', '', '', 'Ruolo del conducente all’interno dell’impresa - 03', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '167', '1262', '1', 'Il conducente, per contribuire alla qualità del servizio, deve svolgere il servizio di trasporto garantendo la massima sicurezza', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '167', '1263', '2', 'Il conducente di un’impresa di autotrasporto ha l’obbligo di collaborare con i colleghi', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '167', '1264', '3', 'Al conducente di un’impresa di autotrasporto viene richiesta obbedienza', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '167', '1265', '4', 'Al conducente di un’impresa di autotrasporto viene richiesto l’obbligo di stipulare un’assicurazione sulla vita', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '168', '', '', 'Ruolo del conducente all’interno dell’impresa - 04', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '168', '1266', '1', 'Il conducente di un’impresa di autotrasporto, per contribuire alla qualità del servizio, deve tenere comportamenti cordiali e rispettosi nei confronti dei clienti', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '168', '1267', '2', 'Il conducente di un’impresa di autotrasporto, per contribuire alla qualità del servizio, deve comunicare in azienda eventuali problemi che possono insorgere durante l’esecuzione del servizio', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '168', '1268', '3', 'Il conducente di un’impresa di autotrasporto, per contribuire alla qualità del servizio, deve affrontare gli imprevisti, secondo le indicazioni dell’azienda, al fine di assolvere agli obblighi contrattuali', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '168', '1269', '4', 'Al conducente di un’impresa di autotrasporto viene richiesta fedeltà', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '168', '1270', '5', 'Al conducente di un’impresa di autotrasporto viene richiesta diligenza', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '168', '1271', '6', 'Tra gli obblighi del conducente, che non sia anche titolare dell’impresa, vi è quello di investire dei soldi propri all’interno dell’azienda', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '168', '1272', '7', 'Tra le mansioni del conducente che non sia anche titolare dell’impresa di trasporto, rientra il compito di istituire nuovi servizi di sua iniziativa', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '168', '1273', '8', 'Tra le mansioni del conducente che non sia anche titolare dell’impresa di trasporto, rientra la selezione del personale da assumere nell’impresa', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '168', '1274', '9', 'Tra le mansioni del conducente, che non sia anche titolare dell’impresa di trasporto, rientra la determinazione del costo del servizio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '168', '1275', '10', 'Tra le mansioni del conducente che non sia anche titolare dell’impresa di trasporto, rientra il recupero dei crediti', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '168', '1276', '11', 'Tra le mansioni del conducente, che non sia anche titolare dell’impresa di trasporto, rientra la tenuta dei registri contabili dell’impresa', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '169', '', '', ' L’elemento distintivo del piccolo imprenditore', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '169', '1277', '1', 'L’elemento distintivo del piccolo imprenditore è il lavoro che deve essere personale o familiare e prevalente rispetto a quello di eventuali collaboratori salariati', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '169', '1278', '2', 'Coloro che esercitano impresa di autotrasporto sono soggetti all’iscrizione nel registro delle imprese', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '169', '1279', '3', 'Coloro che esercitano impresa di autotrasporto sono soggetti all’iscrizione nel registro delle imprese solo se trasportano merci in conto proprio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '169', '1280', '4', 'Coloro che esercitano impresa di autotrasporto non sono soggetti all’iscrizione nel registro delle imprese presso la Camera di commercio, ma in quello del PRA (pubblico registro automobilistico)', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '169', '1281', '5', 'Coloro che esercitano impresa di autotrasporto sono soggetti all’iscrizione nel registro delle imprese solo se esercitano in ambito internazionale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '169', '1282', '6', 'L’elemento distintivo del piccolo imprenditore è il capitale che deve essere prevalente rispetto al lavoro', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '169', '1283', '7', 'L’elemento distintivo del piccolo imprenditore è l’assenza di collaboratori', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '170', '', '', 'Controversie tra vettore e cliente', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '170', '1284', '1', 'In caso di disaccordo tra il vettore e il cliente per questioni inerenti al prezzo di trasporto, il conducente deve mantenere un comportamento corretto e professionale anche se il cliente avanza delle critiche direttamente a lui', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '170', '1285', '2', 'In caso di disaccordo tra il vettore e il cliente per questioni inerenti al prezzo di trasporto, il conducente non è tenuto a prendere posizione a favore di una o dell’altra parte', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '170', '1286', '3', 'In caso di disaccordo tra il vettore e il cliente per questioni inerenti al prezzo di trasporto, il conducente non deve intervenire perché non è parte in causa', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '170', '1287', '4', 'In caso di disaccordo tra il vettore e il cliente per questioni inerenti al prezzo di trasporto, il conducente deve svolgere la funzione di intermediario cercando di mettere d’accordo le parti', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '170', '1288', '5', 'In caso di disaccordo tra il vettore e il cliente per questioni inerenti al prezzo di trasporto, il conducente deve intervenire prendendo le difese del datore di lavoro', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '170', '1289', '6', 'In caso di disaccordo tra il vettore e il cliente per questioni inerenti al prezzo di trasporto, il conducente deve intervenire e prendere contatti direttamente con il cliente se il problema riguarda un trasporto da lui effettuato', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '170', '1290', '7', 'In caso di disaccordo tra il vettore e il cliente per questioni inerenti al prezzo di trasporto, il conducente deve assentarsi dal lavoro se è direttamente coinvolto', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '170', '1291', '8', 'In caso di disaccordo tra il vettore e il cliente per questioni inerenti al prezzo di trasporto, il conducente può intervenire se ritiene che il cliente abbia ragione', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '171', '', '', 'Logistica - 01', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '171', '1292', '1', 'Le piattaforme logistiche sono finalizzate alla razionalizzazione del processo logistico e al raggiungimento della massima efficienza e rapidità nell’espletamento dei servizi stessi', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '171', '1293', '2', 'La logistica è l’insieme delle attività organizzative, gestionali e strategiche che governano nell’azienda i flussi dei materiali dalle origini, presso i fornitori, fino alla consegna dei prodotti finiti ai clienti', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '171', '1294', '3', 'La logistica sostenibile favorisce la riduzione delle emissioni durante tutte le fasi e differenzia una logistica diretta ed una inversa', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '171', '1295', '4', 'La logistica è esclusivamente lo stoccaggio e movimentazione delle merci nei magazzini', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '171', '1296', '5', 'Le piattaforme logistiche sono finalizzate ad armonizzare i costi delle spedizioni', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '171', '1297', '6', 'Nelle piattaforme logistiche avviene esclusivamente lo stoccaggio e movimentazione delle merci', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '172', '', '', 'Logistica - 02', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '172', '1298', '1', 'La logistica deve soddisfare la richiesta di elevata puntualità delle consegne', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '172', '1299', '2', 'La logistica deve soddisfare la rapidità di consegna attesa dal cliente finale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '172', '1300', '3', 'La logistica è influenzata dalla globalizzazione dei mercati', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '172', '1301', '4', 'La logistica risolve una serie di problematiche legate a puntualità delle consegne', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '172', '1302', '5', 'La logistica risolve una serie di problematiche legate a tutela legale del consumatore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '172', '1303', '6', 'La logistica risolve una serie di problematiche legate a manutenzione dei veicoli', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '172', '1304', '7', 'La logistica non risolve le problematiche che interessano il sistema produttivo e le nuove abitudini dei consumatori', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '172', '1305', '8', 'La logistica non è influenzata dalla globalizzazione dei mercati', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '172', '1306', '9', 'La logistica non è influenzata dall’incremento della frequenza delle consegne', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '173', '', '', 'Sistema logistico e ottimizzazione dei costi - 01', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '173', '1307', '1', 'La visione integrata del sistema logistico consente di ottenere riduzioni di costo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '173', '1308', '2', 'La terziarizzazione dei servizi logistici e la concentrazione delle imprese, in termini di risorse e di network, sono due fenomeni strettamente collegati', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '173', '1309', '3', 'Tramite l’outsourcing (esternalizzazione) dei servizi logistici l’impresa sopperisce alla mancanza al proprio interno di know-how specialistico e di tecnologie innovative', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '173', '1310', '4', 'Il sistema logistico si configura come un sistema costituito da un insieme di sottosistemi interdipendenti', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '173', '1311', '5', 'Il sistema logistico dell’impresa non deve essere necessariamente interfacciato con la produzione, con il marketing e le vendite', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '173', '1312', '6', 'Il sistema logistico si configura come un unico sistema non suddivisibile in attività', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '173', '1313', '7', 'Per ottenere una riduzione dei costi aumentando la gamma e la qualità dei servizi offerti non occorre l’esternalizzazione dei servizi logistici', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '173', '1314', '8', 'Con l’outsourcing (esternalizzazione) non varia la flessibilità operativa dell’impresa', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '173', '1315', '9', 'Tramite l’outsourcing (esternalizzazione) dei servizi logistici l’impresa non ottiene una riduzione dei costi', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '174', '', '', 'Sistema logistico e ottimizzazione dei costi - 02', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '174', '1316', '1', 'Tramite l’outsourcing (esternalizzazione) l’impresa assegna per un periodo contrattualmente definito a fornitori esterni la gestione operativa di una o più funzioni logistiche', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '174', '1317', '2', 'L’esternalizzazione trasforma di fatto un’attività marginale o non strategica di un’impresa nell’attività principale (core business) di un’altra', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '174', '1318', '3', 'Il costo del trasporto primario dipende dal modo di trasporto, dalla tratta considerata e da alcune proprietà della merce trasportata (densità, pericolosità, valore, ecc.)', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '174', '1319', '4', 'Il costo del trasporto secondario è influenzato dal numero e dalla densità dei punti di consegna e dal quantitativo medio per consegna', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '174', '1320', '5', 'Il costo del trasporto primario dipende dal modo di trasporto e dalla tratta considerata, ma non dalle proprietà della merce trasportata (densità, pericolosità, valore, ecc.)', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '174', '1321', '6', 'Il costo del trasporto secondario è indipendente dal numero e dalla densità dei punti di consegna', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '174', '1322', '7', 'Il costo delle attività di picking (selezionamento) non rientra tra i costi delle attività logistiche', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '174', '1323', '8', 'Con l’outsourcing (esternalizzazione) non aumenta il livello e gli standard di servizio, verso l’interno e l’esterno, dell’impresa', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '174', '1324', '9', 'L’esternalizzazione dei servizi logistici non comporta rischi per l’impresa', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '175', '', '', 'Sistema logistico e ottimizzazione dei costi - 03', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '175', '1325', '1', 'L’impresa può delegare le parti meramente esecutive delle attività logistiche (movimentazione, trasporto, magazzinaggio, ecc.) mantenendone comunque il controllo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '175', '1326', '2', 'L’affidamento delle merci presso la sede dell’operatore logistico consente a quest’ultimo di attivare tutte le leve di miglioramento e ottimizzazione in suo possesso', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '175', '1327', '3', 'L’ultimo miglio è uno dei punti più critici della supply chain (filiera di distribuzione)', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '175', '1328', '4', 'L’outsourcing (esternalizzazione) logistico può comportare la perdita di know-how (competenza) sui processi logistici interni all’impresa', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '175', '1329', '5', 'L’outsourcing (esternalizzazione) logistico non può essere realizzato “in house” (internamente)', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '175', '1330', '6', 'Un operatore logistico strutturalmente troppo grande o troppo piccolo non influenza nel tempo il rapporto di outsourcing (esternalizzazione)', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '175', '1331', '7', 'La strategia dei centri di micro distribuzione (micro-fulfillment) non conferisce una maggiore velocità nelle fasi di consegna degli ordini', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '175', '1332', '8', 'I centri di micro distribuzione (micro-fulfillment) dispongono di ampie aree di stoccaggio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '175', '1333', '9', 'I centri di micro distribuzione (micro-fulfillment) seppur ubicati nei pressi del centro urbano non riducono i tempi delle consegne dell’ultimo miglio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '176', '', '', 'Ottimizzazione dei costi di esercizio e canale distributivo', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '176', '1334', '1', 'Il canale distributivo è caratterizzato dalla cooperazione di più imprese aventi come scopo comune quello di far arrivare i prodotti sul mercato nell’assortimento, luogo e tempo richiesti', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '176', '1335', '2', 'Con il termine picking (selezionamento) si intende l’attività di selezione e prelievo parziale di merci (appartenenti a diverse unità di carico)', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '176', '1336', '3', 'Il sistema logistico è l’insieme delle infrastrutture, delle attrezzature, delle risorse e delle politiche operative che permettono il flusso delle merci e delle relative informazioni', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '176', '1337', '4', 'Il picking (selezionamento) è l’attività per raggruppare colli, componenti, prodotti o materiali selezionati da unità di carico più grandi, che riuniti sono elaborati e spediti', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '176', '1338', '5', 'Il picking (selezionamento) è l’attività di consegna della spedizione all’utente finale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '176', '1339', '6', 'La logistica risolve una serie di problematiche legate a qualità dei prodotti finiti', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '176', '1340', '7', 'L’errore di spedizione non rientra tra i fattori d’insoddisfazione del cliente finale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '176', '1341', '8', 'Per il cliente finale la rapidità della disponibilità del prodotto è un fattore di rilevanza minore di quanto non siano la qualità e il prezzo del prodotto', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '176', '1342', '9', 'Per sistema logistico si intende il parco veicolare dell’impresa di trasporto', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '177', '', '', ' E-logistica e logistica sostenibile - 01', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '177', '1343', '1', 'Nella e-logistica è fondamentale integrare la gestione del magazzino e la consegna dei prodotti ai clienti implementando un software che funga da collegamento con gli operatori incaricati del trasporto', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '177', '1344', '2', 'L’e-commerce (commercio elettronico) ha normalizzato le consegne ultraveloci (in meno di 24 ore), le spedizioni a costo minimo e i resi gratuiti', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '177', '1345', '3', 'Per il cliente finale è importante conoscere in ogni momento lo stato del proprio ordine (preparazione, spedizione, trasporto, distribuzione e consegna)', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '177', '1346', '4', 'Uno dei principi fondamentali dell’e-logistica è la digitalizzazione', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '177', '1347', '5', 'La e-logistica è l’insieme delle operazioni di logistica che ruotano attorno al mondo dell’e-commerce (commercio elettronico)', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '177', '1348', '6', 'La e-logistica è l’operazione per effettuare i resi da parte del cliente finale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '177', '1349', '7', 'I processi logistici di e-commerce (commercio elettronico) possono essere gestiti senza sistemi di etichettatura', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '177', '1350', '8', 'L’aumento del traffico dovuto alle consegne che percorrono l’ultimo miglio non ha risvolti ambientali', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '177', '1351', '9', 'Il picking (selezionamento) non è una fase critica dell’e-commerce (commercio elettronico)', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '178', '', '', 'E-logistica e logistica sostenibile - 02', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '178', '1352', '1', 'La e-logistica è l’unione sempre più stretta tra la logistica, l’e-commerce e l’uso delle ultime tecnologie nella supply chain', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '178', '1353', '2', 'La logistica sostenibile assume un ruolo rilevante nella transizione da un modello economico lineare verso un modello di economia circolare', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '178', '1354', '3', 'Per logistica sostenibile si intendono tutte quelle pratiche e processi volti a migliorare la sostenibilità delle attività d’impresa legate alla supply chain (filiera di distribuzione)', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '178', '1355', '4', 'La corretta programmazione del settore logistico diminuisce l’impatto ambientale e i costi aziendali', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '178', '1356', '5', 'Nella logistica sostenibile non ha rilevanza il risparmio energetico e la riduzione di scarti e rifiuti', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '178', '1357', '6', 'Nella logistica sostenibile non ha rilevanza la sharing economy (economia condivisa) applicata ai processi logistici (condivisione di flotte, magazzini e dati)', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '178', '1358', '7', 'Essendo la logistica, nel suo insieme di attività e processi operativi, invisibile agli occhi dei clienti finali non è necessario applicare politiche di sostenibilità', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '178', '1359', '8', 'La corretta programmazione del settore logistico non ha alcuna correlazione con l’impatto ambientale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '178', '1360', '9', 'La corretta programmazione del settore logistico diminuisce i costi per l’azienda', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '179', '', '', 'E-Commercio e logistica', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '179', '1361', '1', 'La logistica sostenibile prevede l’adozione di sistemi di pianificazione delle attività di trasporto', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '179', '1362', '2', 'Il trasporto intermodale favorisce la sostenibilità ambientale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '179', '1363', '3', 'Il packaging sostenibile è un imballaggio concepito in modo da creare il minor impatto ambientale possibile e che al contempo svolga al meglio le sue funzioni di protezione e informazione', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '179', '1364', '4', 'Lo sviluppo dell’e-commerce (commercio elettronico) sta influenzando il settore logistico che deve progettare una gestione sostenibile delle risorse, degli spazi e del packaging (imballaggi)', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '179', '1365', '5', 'Lo sviluppo dell’e-commerce (commercio elettronico) non ha influenzato il settore logistico', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '179', '1366', '6', 'Lo sviluppo dell’e-commerce (commercio elettronico) ha influenzato poco il settore logistico', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '179', '1367', '7', 'La logistica sostenibile si sviluppa attraverso il commercio a chilometro zero', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '179', '1368', '8', 'La corretta programmazione del settore logistico diminuisce i costi per l’azienda ma aumenta l’impatto ambientale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '179', '1369', '9', 'Il trasporto intermodale favorisce lo sviluppo del trasporto elettrico', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '180', '', '', 'Imballaggo', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '180', '1370', '1', 'Le caratteristiche del packaging (imballaggio) sostenibile sono: riduzione, riutilizzo e riciclo.', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '180', '1371', '2', 'La reverse logistics (logistica di ritorno) genera un vantaggio ambientale poiché il prodotto reso può essere rigenerato', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '180', '1372', '3', 'La reverse logistics (logistica di ritorno) permette il recupero di beni ancora utilizzabili', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '180', '1373', '4', 'Gli imballaggi devono essere progettati per favorire tutti i processi della logistica in previsione di un loro riutilizzo e recupero di materia', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '180', '1374', '5', 'Gli imballaggi devono essere progettati solo per favorire il trasporto', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '180', '1375', '6', 'Gli imballaggi non possono essere soggetti al sistema di logistica di ritorno', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '180', '1376', '7', 'Le caratteristiche del packaging (imballaggio) sostenibile sono: modularità, micronizzazione e moralità', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '180', '1377', '8', 'I pallets non possono essere soggetti al sistema di logistica di ritorno', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8A', '180', '1378', '9', 'Non conviene generalmente utilizzare la reverse logistics (logistica di ritorno) se un bene è stato danneggiato nel trasporto o risulta difettoso', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '181', '', '', 'Manutenzione dei veicoli', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '181', '1379', '1', 'È importante tenere sempre in efficienza i veicoli durante la circolazione per garantire che il trasporto si svolga in condizioni di sicurezza', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '181', '1380', '2', 'È importante tenere sempre in efficienza i veicoli durante la circolazione per contenere l’inquinamento', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '181', '1381', '3', 'È importante tenere sempre in efficienza i veicoli durante la circolazione per contenere la rumorosità', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '181', '1382', '4', 'È importante tenere sempre in efficienza i veicoli durante la circolazione per evitare l’usura del tachigrafo digitale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '181', '1383', '5', 'È importante tenere sempre in efficienza i veicoli durante la circolazione per evitare segnalazioni alla Camera di Commercio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '181', '1384', '6', 'È importante tenere sempre in efficienza i veicoli durante la circolazione per pagare un premio annuale assicurativo più basso', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '181', '1385', '7', 'È importante tenere sempre in efficienza i veicoli durante la circolazione per evitare segnalazioni al Comitato Centrale dell’Albo degli Autotrasportatori', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '182', '', '', 'Controllo “a vista” - Manutenzione dei veicoli', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '182', '1386', '1', 'Il conducente, tramite controllo “a vista”, può accertare la presenza di lacerazioni dei pneumatici', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '182', '1387', '2', 'Il conducente, tramite il controllo “a vista”, può accertare perdite d’olio del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '182', '1388', '3', 'Il conducente, tramite controllo “a vista”, può accertare il mancato funzionamento dei dispositivi di illuminazione', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '182', '1389', '4', 'Il conducente, tramite il controllo “a vista”, può accertare la percentuale di ossido di carbonio contenuta nei fumi di scarico del veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '182', '1390', '5', 'Il conducente, tramite il controllo “a vista”, può accertare la viscosità dell’olio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '183', '', '', 'Individuazione dei guasti', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '183', '1391', '1', 'L’individuazione di un guasto da parte di un conducente può evitare di aggravare la situazione', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '183', '1392', '2', 'Il conducente deve conoscere il significato delle spie presenti sul cruscotto', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '183', '1393', '3', 'Il conducente deve conoscere approfonditamente il funzionamento della strumentazione di bordo del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '183', '1394', '4', 'Il conducente deve conoscere e saper consultare il libretto d’uso e manutenzione del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '183', '1395', '5', 'L’individuazione della causa dei guasti da parte del conducente è indispensabile ai fini del contenimento dei consumi di carburante', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '183', '1396', '6', 'Il conducente deve fermarsi non appena riscontra un’anomalia del navigatore satellitare', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '183', '1397', '7', 'Se il veicolo stenta ad avviarsi si devono collegare tra loro, con un cavo, i due poli della batteria', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '184', '', '', 'Operazioni di manutenzione ordinaria - 01', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '184', '1398', '1', 'Rientrano tra le operazioni di manutenzione ordinaria del veicolo la sostituzione dell’olio della trasmissione', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '184', '1399', '2', 'Rientrano tra le operazioni di manutenzione ordinaria del veicolo il controllo periodico delle parti meccaniche', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '184', '1400', '3', 'Rientrano tra le operazioni di manutenzione ordinaria del veicolo la sostituzione dell’olio motore', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '184', '1401', '4', 'Rientrano tra le operazioni di manutenzione ordinaria del veicolo la sostituzione del filtro dell’aria', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '184', '1402', '5', 'Rientrano tra le operazioni di manutenzione ordinaria del veicolo la sostituzione ogni 10.000 chilometri delle fasce elastiche', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '184', '1403', '6', 'Rientrano tra le operazioni di manutenzione ordinaria del veicolo la sostituzione del motore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '184', '1404', '7', 'Rientrano tra le operazioni di manutenzione ordinaria del veicolo la registrazione semestrale della fascia raschia olio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '184', '1405', '8', 'Rientrano tra le operazioni di manutenzione ordinaria del veicolo il lavaggio annuale del filtro dell’olio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '185', '', '', ' Operazioni di manutenzione ordinaria - 02', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '185', '1406', '1', 'Il conducente deve sapere quando è necessario ricorrere a personale specializzato per riparare l’avaria del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '185', '1407', '2', 'Il conducente deve segnalare tempestivamente all’impresa eventuali malfunzionamenti del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '185', '1408', '3', 'Per garantire l’efficienza del veicolo è necessario effettuare la periodica manutenzione preventiva', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '185', '1409', '4', 'Per garantire l’efficienza del veicolo è necessario effettuare il ripristino di dispositivi meccanici usurati anche se ancora funzionanti', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '185', '1410', '5', 'Per garantire l’efficienza del veicolo è necessario effettuare la sostituzione periodica delle cinghie e di trasmissione', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '185', '1411', '6', 'Per garantire l’efficienza del veicolo è necessario effettuare la lucidatura annuale della carrozzeria', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '185', '1412', '7', 'Per garantire l’efficienza del veicolo è necessario superare i limiti di velocità nel traffico urbano', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '185', '1413', '8', 'Per garantire l’efficienza del veicolo è necessario effettuare il lavaggio della carrozzeria almeno una volta a settimana', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '186', '', '', 'Manutenzione del cambio', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '186', '1414', '1', 'Per verificare l’efficienza del cambio, è necessario porre attenzione alla rumorosità degli innesti delle marce', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '186', '1415', '2', 'Periodicamente è necessario controllare il livello dell’olio della scatola del cambio per non danneggiare e/o usurare seriamente gli ingranaggi', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '186', '1416', '3', 'Periodicamente deve essere controllato il livello dell’olio del differenziale per consentire la corretta lubrificazione dei satelliti e dei planetari', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '186', '1417', '4', 'Periodicamente deve essere controllato il livello dell’olio del cambio di velocità per garantire la corretta lubrificazione degli ingranaggi', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '186', '1418', '5', 'Periodicamente deve essere controllato il livello dell’olio del cambio di velocità per garantire una maggiore scorrevolezza della frizione', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '186', '1419', '6', 'Periodicamente deve essere verificato il livello dell’olio del giunto cardanico dell’albero della trasmissione', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '186', '1420', '7', 'Periodicamente deve essere verificato il livello dell’olio del giunto elastico dell’albero della trasmissione', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '186', '1421', '8', 'Per verificare l’efficienza del cambio, periodicamente è necessario controllare il gioco della corsa del pedale della frizione', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '186', '1422', '9', 'Per verificare l’efficienza del cambio, periodicamente è necessario controllare l’assenza di perdite di olio dai tubi dell’impianto frenante', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '186', '1423', '10', 'Nel periodo invernale è opportuno aggiungere acqua all’olio del differenziale per renderlo più scorrevole', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '187', '', '', ' Efficienza e manutenzione dell’impianto frenante - 01', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '187', '1424', '1', 'Periodicamente deve essere controllato il livello del liquido dei freni per consentire il corretto funzionamento dell’impianto frenante del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '187', '1425', '2', 'Gli elementi frenanti si controllano verificando lo stato di usura delle pastiglie dei freni', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '187', '1426', '3', 'Gli elementi frenanti si controllano verificando lo stato di usura delle ganasce', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '187', '1427', '4', 'Gli elementi frenanti del veicolo si controllano verificando il colore delle pastiglie dei freni', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '187', '1428', '5', 'La presenza dell’aria nel serbatoio dell’impianto frenante diminuisce lo spazio di frenata', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '188', '', '', 'Equilibratura dei freni', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '188', '1429', '1', 'L’equilibratura non corretta dei freni può dipendere da una differente pressione di gonfiaggio dei pneumatici sullo stesso asse', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '188', '1430', '2', 'L’equilibratura non corretta dei freni può dipendere dal diverso consumo dei ferodi di uno stesso asse', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '188', '1431', '3', 'La frenatura squilibrata è causata dalla diversa forza frenante esercitata dagli elementi frenanti di uno stesso asse', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '188', '1432', '4', 'Una frenatura poco efficiente può dipendere da ferodi o tamburi e/o dischi fortemente usurati', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '188', '1433', '5', 'Una frenatura poco efficiente può dipendere da tamburi o dischi imbrattati d’olio', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '188', '1434', '6', 'Una frenatura poco efficiente può dipendere da un consumo irregolare del tamburo o dei dischi', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '188', '1435', '7', 'Una frenatura poco efficiente può dipendere dalla rottura del segnalatore di pressione del circuito frenante', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '188', '1436', '8', 'Una frenatura poco efficiente può dipendere dal filtro antiparticolato intasato', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '188', '1437', '9', 'Una frenatura poco efficiente può dipendere da scarso livello dell’olio nella coppa del motore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '188', '1438', '10', 'L’equilibratura non corretta dei freni può dipendere da una eccessiva pressione sul pedale del freno', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '188', '1439', '11', 'L’equilibratura non corretta dei freni può dipendere da un eccessivo gioco del pedale del freno', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '188', '1440', '12', 'L’equilibratura non corretta dei freni può dipendere da una frenatura effettuata con il cambio in folle', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '188', '1441', '13', 'Il conducente, tramite il controllo “a vista”, può accertare l’efficienza frenante del veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '189', '', '', 'Efficienza e manutenzione dell’impianto frenante - 02', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '189', '1442', '1', 'Periodicamente deve essere controllato il livello dell’olio del servofreno dell’impianto frenante del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '189', '1443', '2', 'L’indicazione di una scarsa pressione dell’aria nei serbatoi dell’impianto frenante può essere causata da una perdita d’aria nell’impianto frenante', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '189', '1444', '3', 'Il veloce movimento della lancetta del manometro dell’aria durante la marcia può essere determinata dalla presenza di acqua nel serbatoio dell’aria dell’impianto frenante', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '189', '1445', '4', 'La presenza di acqua nel serbatoio dell’aria dell’impianto frenante diminuisce il volume dell’aria a disposizione', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '189', '1446', '5', 'È necessario controllare periodicamente il livello dell’olio lubrificante del motore ma non il livello dell’olio dei freni', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '189', '1447', '6', 'Gli elementi frenanti del veicolo si controllano verificando la durezza delle ganasce', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '189', '1448', '7', 'Per verificare l’efficienza dell’impianto di frenatura pneumatico, periodicamente è necessario controllare il livello dell’olio del serbatoio dei freni', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '189', '1449', '8', 'Per verificare l’efficienza dell’impianto di frenatura pneumatico, periodicamente è necessario controllare la percentuale di anidride carbonica contenuta nei serbatoi dell’aria compressa', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '189', '1450', '9', 'Per verificare l’efficienza dell’impianto di frenatura pneumoidraulico, periodicamente è necessario controllare il livello dell’olio lubrificante tramite astina graduata', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '190', '', '', 'Efficienza e manutenzione dell’impianto frenante - 03', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '190', '1451', '1', 'Se il manometro dell’impianto dei freni segnala un abbassamento di pressione, si deve controllare l’efficienza di tutto l’impianto e, in particolare, l’integrità delle tubazioni dell’aria dei freni', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '190', '1452', '2', 'Per verificare l’efficienza dell’impianto di frenatura pneumatico, si deve controllare che le tubazioni dei freni non perdano aria', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '190', '1453', '3', 'Per verificare l’efficienza dell’impianto di frenatura pneumatico, periodicamente è necessario controllare l’integrità dei serbatoi dell’aria compressa', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '190', '1454', '4', 'Per verificare l’efficienza dell’impianto di frenatura pneumoidraulico, periodicamente è necessario controllare lo stato di usura delle guarnizioni delle ganasce o dei pattini dei freni', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '190', '1455', '5', 'Per verificare l’efficienza dell’impianto di frenatura pneumoidraulico, periodicamente è necessario controllare il livello dell’acqua', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '190', '1456', '6', 'Per verificare l’efficienza dell’impianto di frenatura pneumatico, periodicamente è necessario controllare la tenuta della pompa idraulica', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '190', '1457', '7', 'Periodicamente si deve verificare il livello dell’olio dei freni per consentire il corretto funzionamento del distributore duplex', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '190', '1458', '8', 'La pressione dell’aria nel serbatoio dell’impianto frenante può aumentare oltre il limite di esercizio, se il conducente, durante la marcia, utilizza poco i freni', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '190', '1459', '9', 'La presenza di acqua nel serbatoio dell’aria dell’impianto frenante aumenta il volume dell’aria a disposizione', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '191', '', '', ' Efficienza e manutenzione dell’impianto frenante - 04', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '191', '1460', '1', 'Per verificare l’efficienza dell’impianto di frenatura pneumatico, periodicamente è necessario controllare lo stato di usura delle guarnizioni delle ganasce o dei pattini degli elementi frenanti', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '191', '1461', '2', 'Per verificare l’efficienza dell’impianto di frenatura pneumoidraulico, periodicamente è necessario controllare il livello dell’olio del serbatoio della sezione idraulica dei freni', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '191', '1462', '3', 'L’indicazione di un’eccessiva pressione dell’aria nel serbatoio dell’impianto frenante può essere causata da un prolungato uso dei freni', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '191', '1463', '4', 'Per verificare l’efficienza dell’impianto di frenatura pneumoidraulico, periodicamente è necessario controllare la viscosità dell’olio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '192', '', '', 'Manutenzione della frizione', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '192', '1464', '1', 'Per verificare l’efficienza del la frizione, periodicamente è necessario controllare lo stato d’usura del disco', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '192', '1465', '2', 'Per verificare l’efficienza della frizione, periodicamente è necessario controllare il gioco della corsa del pedale della frizione', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '192', '1466', '3', 'Per verificare l’efficienza della frizione, periodicamente è necessario controllare la scatola del cambio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '192', '1467', '4', 'Per verificare l’efficienza della frizione, periodicamente è necessario controllare la temperatura del disco della frizione', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '193', '', '', 'Malfunzionamento della frizione ', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '193', '1468', '1', 'Lo slittamento della frizione può essere dovuto allo spingidisco danneggiato', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '193', '1469', '2', 'Lo slittamento della frizione può essere dovuto al disco della frizione usurato', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '193', '1470', '3', 'Lo slittamento della frizione può essere dovuto al disco della frizione imbrattato d’olio', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '193', '1471', '4', 'Lo slittamento della frizione può essere dovuto alla mancanza di olio nella scatola del cambio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '193', '1472', '5', 'Lo slittamento della frizione può essere dovuto al fatto che il veicolo è ancora in rodaggio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '193', '1473', '6', 'Lo slittamento della frizione può essere dovuto alla cattiva equilibratura del disco della frizione', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '194', '', '', 'Manutenzione degli organi della distribuzione', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '194', '1474', '1', 'Bisogna controllare periodicamente il filtro dell’aria del motore al fine di garantire una corretta combustione', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '194', '1475', '2', 'Per verificare l’efficienza degli organi di distribuzione, periodicamente è necessario accertarsi che l’albero ad eccentrici sia correttamente lubrificato', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '194', '1476', '3', 'Per verificare l’efficienza degli organi di distribuzione, periodicamente è necessario controllare la cinghia di distribuzione', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '194', '1477', '4', 'Per verificare l’efficienza degli organi di distribuzione, periodicamente è necessario controllare il livello dell’olio della scatola del cambio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '194', '1478', '5', 'Per verificare l’efficienza degli organi di distribuzione, periodicamente è necessario controllare la corretta lubrificazione degli elementi della trasmissione ', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '194', '1479', '6', 'Il conducente può omettere di segnalare all’impresa eventuali anomalie del veicolo: è compito dell’officina di riparazione accertarle', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '195', '', '', 'Manutenzione dell’impianto di alimentazione - filtro del carburante', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '195', '1480', '1', 'L’intasamento del filtro antipolline può deteriorare la salubrità dell’abitacolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '195', '1481', '2', 'Il filtro del carburante intercetta le impurità contenute nel carburante', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '195', '1482', '3', 'Per verificare l’efficienza dell’impianto di alimentazione, periodicamente è necessario controllare la pompa di iniezione', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '195', '1483', '4', 'L’intasamento del filtro del carburante comporta perdita di efficienza del motore', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '195', '1484', '5', 'L’intasamento del filtro del carburante può comportare un irregolare afflusso di carburante alla pompa', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '195', '1485', '6', 'L’intasamento del filtro antipolline diminuisce le prestazioni del veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '195', '1486', '7', 'L’intasamento del filtro del carburante comporta la insufficiente lubrificazione dei cilindri del motore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '195', '1487', '8', 'L’intasamento del filtro del carburante comporta l’emissione di fumo nero allo scarico', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '195', '1488', '9', 'L’intasamento del filtro del carburante comporta maggior usura degli iniettori dell’impianto di alimentazione', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '195', '1489', '10', 'Per verificare l’efficienza dell’impianto di alimentazione, periodicamente è necessario controllare la pompa dell’olio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '195', '1490', '11', 'Per verificare l’efficienza dell’impianto di alimentazione, periodicamente è necessario controllare la pompa dell’acqua', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '196', '', '', ' Manutenzione dell’impianto di alimentazione - filtro dell’aria', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '196', '1491', '1', 'Per verificare l’efficienza dell’impianto di alimentazione, periodicamente è necessario controllare il filtro dell’aria', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '196', '1492', '2', 'Occorre mantenere pulito il filtro dell’aria per evitare che esso si intasi', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '196', '1493', '3', 'L’intasamento del filtro dell’aria del motore provoca irregolarità nella combustione', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '196', '1494', '4', 'L’intasamento del filtro dell’aria del motore provoca l’emissione di fumo nero allo scarico', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '196', '1495', '5', 'L’intasamento del filtro dell’aria del motore provoca la riduzione dell’efficienza del motore', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '196', '1496', '6', 'L’intasamento del filtro dell’aria del motore provoca l’inefficienza della frenatura', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '196', '1497', '7', 'L’intasamento del filtro dell’aria del motore provoca l’emissione di fumo bianco allo scarico', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '196', '1498', '8', 'L’intasamento del filtro dell’aria del motore provoca l’ingresso di sostanze estranee nella camera di combustione', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '196', '1499', '9', 'Bisogna controllare periodicamente il filtro dell’aria del motore al fine di garantire il corretto raffreddamento del motore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '197', '', '', 'Manutenzione dell’impianto di avviamento e fusibile ', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '197', '1500', '1', 'Il guasto di un fusibile è un’avaria dell’impianto elettrico', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '197', '1501', '2', 'Il guasto di un fusibile è un’avaria dell’impianto di trasmissione', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '197', '1502', '3', 'Il guasto di un fusibile comporta lo spegnimento del motore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '198', '', '', 'Manutenzione dell’impianto di avviamento e elettrico - 01', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '198', '1503', '1', 'Per verificare l’efficienza dell’impianto elettrico, periodicamente è necessario controllare la carica della batteria', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '198', '1504', '2', 'Per verificare l’efficienza dell’impianto elettrico, periodicamente è necessario controllare l’efficienza dell’alternatore', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '198', '1505', '3', 'Per verificare l’efficienza dell’impianto di avviamento, periodicamente è necessario controllare il corretto serraggio dei morsetti della batteria', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '198', '1506', '4', 'Per verificare l’efficienza dell’impianto di avviamento, periodicamente è necessario controllare le spazzole del motorino di avviamento', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '198', '1507', '5', 'Per verificare l’efficienza dell’impianto di avviamento, periodicamente è necessario controllare il corretto ingrassaggio della coppia conica', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '198', '1508', '6', 'Per verificare l’efficienza dell’impianto di avviamento, periodicamente è necessario controllare il corretto funzionamento del ventilatore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '198', '1509', '7', 'Per verificare l’efficienza dell’impianto elettrico di un autoveicolo alimentato a gasolio, periodicamente è necessario controllare lo stato delle candele', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '198', '1510', '8', 'Per verificare l’efficienza dell’impianto elettrico di un autoveicolo alimentato a gasolio, periodicamente è necessario controllare lo stato dello spinterogeno', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '199', '', '', 'Manutenzione dell’impianto di avviamento e elettrico - 02', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '199', '1511', '1', 'È opportuno verificare periodicamente lo stato d’usura della cinghia dell’alternatore sostituendola se necessario', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '199', '1512', '2', 'Il guasto di un fusibile si ripara, di norma, sostituendo il fusibile stesso', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '199', '1513', '3', 'La mancata ricarica della batteria può essere determinata dalla rottura della cinghia trapezoidale che trascina l’alternatore', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '199', '1514', '4', 'L’avaria dell’alternatore impedisce alla batteria di ricaricarsi', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '199', '1515', '5', 'L’avaria del raddrizzatore di corrente dell’alternatore impedisce alla batteria di ricaricarsi', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '199', '1516', '6', 'L’avaria del raddrizzatore di corrente impedisce alla dinamo di ricaricarsi', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '199', '1517', '7', 'L’anomalo funzionamento dell’alternatore compromette l’efficienza dell’impianto frenante pneumoidraulico', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '199', '1518', '8', 'L’accensione della spia della batteria indica che la pressione del liquido della batteria ha superato il valore massimo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '200', '', '', 'Manutenzione dell’impianto di avviamento e elettrico - 03', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '200', '1519', '1', 'L’accensione della spia della batteria indica che la stessa non viene caricata correttamente', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '200', '1520', '2', 'L’anomalo funzionamento dell’alternatore non compromette l’efficienza dell’impianto frenante pneumoidraulico', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '200', '1521', '3', 'L’alternatore genera corrente alternata ed il suo malfunzionamento impedisce che la batteria si ricarichi', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '200', '1522', '4', 'L’alternatore è il dispositivo che trasporta l’elettricità dalla batteria agli altri organi del veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '200', '1523', '5', 'L’avaria della pompa dell’acqua di raffreddamento impedisce all’alternatore di ricaricare la batteria', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '200', '1524', '6', 'L’alternatore è collegato direttamente ai proiettori', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '201', '', '', 'Manutenzione dell’impianto di avviamento - accensione spia dell’alternatore', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '201', '1525', '1', 'L’accensione della spia dell’alternatore può indicare che l’alternatore non carica più la batteria', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '201', '1526', '2', 'L’accensione della spia dell’alternatore può essere determinata dalla rottura della cinghia trapezoidale', 'V');
