INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '201', '1528', '4', 'L’accensione della spia dell’alternatore può essere determinata da difettoso funzionamento della spia stessa', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '201', '1529', '5', 'L’accensione della spia dell’alternatore può essere determinata dall’uso contemporaneo di più utilizzatori (radio, accendisigari, aria condizionata)', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '201', '1530', '6', 'L’accensione della spia dell’alternatore può essere determinata dalla rottura del fusibile dell’impianto di apertura delle porte dell’autobus', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '201', '1531', '7', 'L’accensione della spia dell’alternatore può essere determinata dalla rottura della lampadina di un proiettore guasto', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '201', '1532', '8', 'L’accensione della spia dell’alternatore può essere determinata dalla temperatura atmosferica eccessivamente bassa', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '202', '', '', 'Manutenzione dell’impianto di lubrificazione - 01', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '202', '1533', '1', 'L’intasamento del filtro dell’olio dell’impianto di lubrificazione può provocare il danneggiamento dei cilindri del motore', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '202', '1534', '2', 'L’omessa sostituzione del filtro dell’olio dell’impianto di lubrificazione ne provoca l’intasamento', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '202', '1535', '3', 'Periodicamente deve essere controllato il livello dell’olio del motore per evitare gravi danni di quest’ultimo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '202', '1536', '4', 'Per verificare l’efficienza dell’impianto di lubrificazione, periodicamente è necessario fare riferimento al chilometraggio al quale è avvenuto il precedente cambio dell’olio', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '202', '1537', '5', 'Per verificare l’efficienza dell’impianto di lubrificazione, periodicamente è necessario controllare il livello del lubrificante tramite astina graduata', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '202', '1538', '6', 'Per verificare l’efficienza dell’impianto di lubrificazione, periodicamente è necessario verificare il corretto funzionamento dell’elettroventola', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '202', '1539', '7', 'Per verificare l’efficienza dell’impianto di lubrificazione, periodicamente è necessario ispezionare internamente il filtro a cartuccia dell’olio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '202', '1540', '8', 'Periodicamente deve essere controllato il livello dell’olio del motore per evitare che esso vada in fuorigiri', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '202', '1541', '9', 'Il codice della strada impone di verificare periodicamente il livello dell’olio di lubrificazione del motore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '202', '1542', '10', 'Periodicamente deve essere controllato il livello dell’olio del differenziale per consentire il corretto funzionamento dell’impianto di lubrificazione del motore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '203', '', '', ' Manutenzione dell’impianto di lubrificazione - 02', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '203', '1543', '1', 'Bisogna sostituire periodicamente il filtro dell’olio al fine di trattenere le impurità dell’olio di lubrificazione', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '203', '1544', '2', 'L’intasamento del filtro dell’olio dell’impianto di lubrificazione è causa di presenza di impurità nell’olio', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '203', '1545', '3', 'L’intasamento del filtro dell’olio dell’impianto di lubrificazione può causare fumo nero allo scarico', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '203', '1546', '4', 'L’intasamento del filtro dell’olio dell’impianto di lubrificazione provoca il malfunzionamento del servofreno', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '203', '1547', '5', 'L’intasamento del filtro dell’olio dell’impianto di lubrificazione può provocare l’interruzione del circuito di lubrificazione', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '204', '', '', ' Accensione spia dell’olio motore', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '204', '1548', '1', 'Prima della partenza è opportuno verificare che le spie che indicano il malfunzionamento di un organo del veicolo siano spente', 'V');
