INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '204', '1551', '4', 'L’accensione della spia dell’olio del motore può essere determinata dall’insufficiente quantitativo di olio', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '204', '1552', '5', 'L’accensione della spia dell’olio del motore può indicare il difettoso funzionamento della pompa ad ingranaggi', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '204', '1553', '6', 'L’accensione della spia dell’olio del motore può essere determinata dalla scarsa fluidità del liquido di raffreddamento', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '204', '1554', '7', 'L’accensione della spia dell’olio del motore può essere determinata dalla mancata sostituzione dell’olio del motore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '204', '1555', '8', 'L’accensione della spia dell’olio del motore può essere determinata dall’insufficiente quantitativo di olio nel convertitore pneumoidraulico', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '204', '1556', '9', 'In caso di accensione della spia dell’olio bisogna viaggiare al massimo numero di giri del motore per aumentare la pressione dell’olio stesso', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '205', '', '', 'Manutenzione dell’impianto di raffreddamento ', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '205', '1557', '1', 'È opportuno controllare periodicamente il liquido di raffreddamento del motore per evitare il surriscaldamento del motore', 'V');
