INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '205', '1559', '3', 'Per verificare l’efficienza dell’impianto di raffreddamento, periodicamente è necessario controllare il corretto funzionamento dell’elettroventola', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '205', '1560', '4', 'Per verificare l’efficienza dell’impianto di raffreddamento, periodicamente è necessario controllare il livello del liquido di raffreddamento', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '205', '1561', '5', 'Per verificare l’efficienza dell’impianto di raffreddamento, periodicamente è necessario controllare la temperatura all’interno della cabina', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '205', '1562', '6', 'Per verificare l’efficienza dell’impianto di raffreddamento, periodicamente è necessario controllare il filtro dell’aria', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '205', '1563', '7', 'È opportuno controllare periodicamente il liquido di raffreddamento del motore per evitare che si abbassi eccessivamente la temperatura dell’olio del motore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '205', '1564', '8', 'L’insufficiente temperatura del motore impedisce al veicolo di mettersi in movimento', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '205', '1565', '9', 'Bisogna controllare periodicamente il filtro dell’olio per evitare che aumenti la temperatura del liquido di raffreddamento', 'F');
