INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '206', '1569', '4', 'L’accensione della spia della temperatura del liquido di raffreddamento del motore può essere causata da un’alta pressione del liquido di raffreddamento', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '206', '1570', '5', 'L’accensione della spia della temperatura del liquido di raffreddamento del motore può essere causata dal sovraccarico del veicolo', 'F');
