INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '206', '1572', '7', 'L’accensione della spia della temperatura del liquido di raffreddamento del motore può essere causata dalla mancanza dell’olio del motore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '207', '', '', 'Impianto di raffreddamento - accensione spia della temperatura del liquido - 02', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '207', '1573', '1', 'L’accensione della spia della temperatura del liquido di raffreddamento del motore può essere causata dalla rottura della pompa dell’acqua', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '207', '1574', '2', 'L’accensione della spia della temperatura del liquido di raffreddamento del motore può essere causata dalla rottura di una cinghia', 'V');
