INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '211', '1598', '7', 'L’emissione allo scarico di fumo bianco altamente inquinante può dipendere dal filtro dell’aria intasato', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '211', '1599', '8', 'L’emissione allo scarico di fumo bianco altamente inquinante può dipendere dagli iniettori starati', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '212', '', '', 'Impianto di scarico - inquinamento acustico', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '212', '1600', '1', 'Il rumore emesso dai veicoli muniti di motore termico può dipendere dal silenziatore di scarico danneggiato', 'V');
