INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '212', '1602', '3', 'Per ridurre il rumore emesso dai veicoli a motore è necessario intervenire sugli elementi frenanti che stridono', 'V');
