INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '213', '1606', '1', 'L’anomalo funzionamento del motore termico può essere evidenziato dal “minimo” irregolare', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '213', '1607', '2', 'L’anomalo funzionamento del motore termico può essere evidenziato da detonazione del carburante', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '213', '1608', '3', 'L’anomalo funzionamento del motore può essere evidenziato dal surriscaldamento del liquido refrigerante', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '213', '1609', '4', 'L’anomalo funzionamento del motore può essere evidenziato da malfunzionamento del differenziale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '213', '1610', '5', 'L’anomalo funzionamento del motore può essere evidenziato dal guasto del tachigrafo digitale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '213', '1611', '6', 'L’anomalo funzionamento del motore può essere evidenziato da rumorosità degli ammortizzatori', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '214', '', '', 'Organi di sospensione  - ammortizzatori scarichi', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '214', '1612', '1', 'Gli ammortizzatori scarichi provocano maggiore rumorosità del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '214', '1613', '2', 'Gli ammortizzatori scarichi provocano anomale oscillazioni del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '214', '1614', '3', 'Gli ammortizzatori scarichi provocano un anomalo comportamento del cambio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '214', '1615', '4', 'Gli ammortizzatori scarichi provocano un anomalo comportamento del differenziale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '215', '', '', 'Organi di sospensione - instabilità di marcia del veicolo', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '215', '1616', '1', 'L’efficienza delle sospensioni influisce sul comportamento in curva del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '215', '1617', '2', 'L’instabilità di marcia del veicolo può dipendere dalle balestre o dalle molle elicoidali danneggiate', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '215', '1618', '3', 'L’instabilità di marcia del veicolo può dipendere dagli ammortizzatori scarichi', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '215', '1619', '4', 'L’instabilità di marcia del veicolo può dipendere dal sistema di regolazione delle sospensioni pneumatiche danneggiato', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '215', '1620', '5', 'L’instabilità di marcia del veicolo può dipendere dalla presenza di asfalto drenante', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '215', '1621', '6', 'L’instabilità di marcia del veicolo può dipendere dai finestrini aperti', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '215', '1622', '7', 'La stabilità del veicolo è indipendente dalla posizione del carico', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '216', '', '', 'Organi di direzione -  La vibrazione dello sterzo - 01 ', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '216', '1623', '1', 'La vibrazione dello sterzo può dipendere dalla mancanza di equilibratura delle ruote direttrici', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '216', '1624', '2', 'La vibrazione dello sterzo può dipendere da avarie dello sterzo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '216', '1625', '3', 'La vibrazione dello sterzo può dipendere da eccessivi giochi dei comandi dello sterzo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '216', '1626', '4', 'La vibrazione dello sterzo può dipendere dalle ruote di dimensioni non regolamentari', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '216', '1627', '5', 'La vibrazione dello sterzo può dipendere da un’eccessiva usura dei pneumatici', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '216', '1628', '6', 'La vibrazione dello sterzo può dipendere da un’errata pressione di gonfiaggio dei pneumatici anteriori', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '217', '', '', 'Organi di direzione -  La vibrazione dello sterzo - 01', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '217', '1629', '1', 'Uno sterzo troppo duro può essere indice di malfunzionamento del servosterzo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '217', '1630', '2', 'Se lo sterzo si presenta più duro del normale, ciò può dipendere dall’errata regolazione degli angoli di assetto', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '217', '1631', '3', 'Se lo sterzo si presenta più duro del normale, ciò può dipendere dai pneumatici anteriori sgonfi', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '217', '1632', '4', 'Se lo sterzo si presenta più duro del normale, ciò può dipendere dal sovraccarico dell’asse anteriore', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '217', '1633', '5', 'Se lo sterzo si presenta più duro del normale, ciò può dipendere da sovraccarico dell’asse posteriore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '217', '1634', '6', 'Se lo sterzo si presenta più duro del normale, ciò può dipendere dalla presenza del servosterzo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '217', '1635', '7', 'Se lo sterzo si presenta più duro del normale, ciò può dipendere dalla eccessiva pressione di gonfiaggio dei pneumatici anteriori', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '218', '', '', 'Organi di direzione - eccessivo gioco dello sterzo', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '218', '1636', '1', 'L’eccessivo gioco dello sterzo provoca una difficoltà nel mantenere la traiettoria voluta', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '218', '1637', '2', 'L’eccessivo gioco dello sterzo può dipendere dall’insufficiente pressione di gonfiaggio dei pneumatici anteriori', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '218', '1638', '3', 'L’eccessivo gioco dello sterzo può dipendere dall’usura del giunto cardanico dell’albero di trasmissione', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '218', '1639', '4', 'L’eccessivo gioco dello sterzo si riduce mantenendo saldamente lo sterzo con entrambe le mani', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '218', '1640', '5', 'L’eccessivo gioco dello sterzo pregiudica il funzionamento delle sospensioni anteriori', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '218', '1641', '6', 'L’eccessivo gioco dello sterzo deteriora precocemente i pneumatici posteriori ', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '218', '1642', '7', 'Il gioco dello sterzo dipende dal cattivo stato di manutenzione dei semiassi', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '219', '', '', 'Organi di direzione ', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '219', '1643', '1', 'Sterzare a veicolo fermo danneggia lo sterzo e aumenta precocemente il gioco dello sterzo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '219', '1644', '2', 'Lo sfarfallamento dello sterzo può essere causato dalla non corretta equilibratura delle ruote anteriori', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '219', '1645', '3', 'Periodicamente deve essere controllato che lo sterzo non abbia un gioco eccessivo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '219', '1646', '4', 'Periodicamente deve essere controllato il livello dell’olio del servosterzo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '219', '1647', '5', 'L’errata regolazione della convergenza delle ruote comporta che il veicolo tende a discostarsi dalla traiettoria rettilinea', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '219', '1648', '6', 'Sterzare a veicolo fermo può provocare una usura anomala dei pneumatici', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '219', '1649', '7', 'Le sterzate a veicolo fermo possono danneggiare lo sterzo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '219', '1650', '8', 'Le sterzate a veicolo fermo possono danneggiare i pneumatici', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '219', '1651', '9', 'Le sterzate a veicolo fermo possono danneggiare il giunto elastico degli organi della trasmissione', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '219', '1652', '10', 'Lo sfarfallamento dello sterzo può essere causato dalla non corretta convergenza delle ruote anteriori', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '220', '', '', 'Pneumatici - 01', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '220', '1653', '1', 'La pressione dei pneumatici deve essere verificata frequentemente perché se non è corretta può rendere instabile la marcia del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '220', '1654', '2', 'La pressione dei pneumatici deve essere verificata frequentemente perché se gonfiati non correttamente, si usurano precocemente', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '220', '1655', '3', 'La non corretta regolazione della convergenza delle ruote anteriori determina l’asimmetrico consumo dei pneumatici', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '220', '1656', '4', 'La non corretta regolazione della convergenza del veicolo diminuisce il rischio di sbandamento del veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '220', '1657', '5', 'La non corretta equilibratura delle ruote diminuisce lo spazio di frenatura del veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '220', '1658', '6', 'Una perdita di equilibratura delle ruote comporta che il veicolo tende a discostarsi dalla traiettoria rettilinea', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '220', '1659', '7', 'È consentito montare sullo stesso asse pneumatici di tipo diverso, purché lo spessore del battistrada sia superiore a 1,6 mm', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '221', '', '', 'Pneumatici - 02', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '221', '1660', '1', 'I pneumatici devono essere controllati periodicamente perché soggetti a usura', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '221', '1661', '2', 'I pneumatici devono essere controllati dopo aver percorso una strada in cattive condizioni o dissestata perché possono essere stati danneggiati', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '221', '1662', '3', 'I pneumatici devono essere controllati per accertare che il battistrada non abbia uno spessore inferiore a 1,6 mm', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '221', '1663', '4', 'I pneumatici devono essere controllati per accertare che il battistrada non abbia uno spessore inferiore a 6 mm', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '221', '1664', '5', 'I pneumatici devono essere controllati periodicamente perché l’insufficiente pressione di gonfiaggio determina una precoce usura della zona centrale del battistrada', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '221', '1665', '6', 'I pneumatici devono essere controllati periodicamente perché l’eccessiva pressione di gonfiaggio determina una precoce usura delle zone laterali del battistrada', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '222', '', '', 'Verifiche generiche - 01', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '222', '1666', '1', 'Il conducente deve verificare che sia stata effettuata la revisione periodica dell’estintore', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '222', '1667', '2', 'Il conducente deve verificare che il veicolo sia in regola con la prescritta revisione periodica', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '222', '1668', '3', 'Il conducente deve verificare che sia stata effettuata la prescritta revisione periodica del tachigrafo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '222', '1669', '4', 'Il conducente deve azionare l’estintore per verificarne l’efficienza', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '222', '1670', '5', 'Il conducente deve firmare il documento di circolazione dopo aver sottoposto il veicolo alla revisione periodica', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '222', '1671', '6', 'Il conducente deve saper rifasare il motore diesel', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '222', '1672', '7', 'Il conducente può provvedere da solo alla sostituzione delle pasticche dei freni', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '222', '1673', '8', 'Il conducente deve personalmente procedere allo smontaggio del tachigrafo digitale nel caso di malfunzionamento', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '223', '', '', 'Verifiche generiche - 02', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '223', '1674', '1', 'Periodicamente deve essere controllato il livello del liquido detergente del parabrezza', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '223', '1675', '2', 'Periodicamente è opportuno verificare il corretto serraggio dei morsetti dei due poli della batteria', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '223', '1676', '3', 'Prima della partenza è opportuno verificare il funzionamento delle luci di corridoio degli autobus', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '223', '1677', '4', 'Prima della partenza è opportuno controllare il corretto funzionamento dell’impianto di illuminazione', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '223', '1678', '5', 'Prima della partenza il conducente deve controllare il corretto funzionamento dell’apertura delle porte dell’autobus', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '223', '1679', '6', 'Prima della partenza il conducente deve controllare che la carrozzeria del veicolo non presenti zone scolorite', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '223', '1680', '7', 'Il controllo della corretta accensione dell’impianto di illuminazione può essere svolto solo da elettrauto', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '224', '', '', 'Orientamento del fascio di luce del proiettore', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '224', '1681', '1', 'Il non corretto orientamento del fascio di luce del proiettore può dipendere dalla lampada montata non correttamente', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '224', '1682', '2', 'Il non corretto orientamento del fascio di luce del proiettore può dipendere dal dispositivo di regolazione del proiettore danneggiato', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '224', '1683', '3', 'Il non corretto orientamento del fascio di luce del proiettore può dipendere dalla mancata registrazione del proiettore', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '224', '1684', '4', 'Il non corretto orientamento del fascio di luce del proiettore può dipendere dal fusibile difettoso', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '224', '1685', '5', 'Il non corretto orientamento del fascio di luce del proiettore può dipendere dalla spia del cruscotto difettosa', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '224', '1686', '6', 'Il non corretto orientamento del fascio di luce del proiettore può dipendere dalla parabola leggermente ossidata', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '225', '', '', 'Avaria dei proiettori', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '225', '1687', '1', 'L’avaria dei proiettori può essere segnalata da spie sul cruscotto o dal computer di bordo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '225', '1688', '2', 'L’avaria dei proiettori può dipendere da un fusibile danneggiato', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '225', '1689', '3', 'L’avaria dei proiettori può essere risolta subito accendendo le luci di ingombro', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '225', '1690', '4', 'L’avaria dei proiettori può essere risolta subito accendendo le luci di emergenza', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '225', '1691', '5', 'Il raddrizzatore è incorporato nell’alternatore e compensa gli squilibri di peso nel veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '226', '', '', 'Visuale ottimale e parabrezza - 01', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '226', '1692', '1', 'I vetri e il parabrezza devono essere integri', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '226', '1693', '2', 'I vetri e il parabrezza devono essere di tipo omologato', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '226', '1694', '3', 'I vetri e il parabrezza devono essere tenuti costantemente puliti', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '226', '1695', '4', 'I vetri e il parabrezza possono presentare punti di rottura, purché modesti', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '226', '1696', '5', 'I vetri o il parabrezza lesionati devono essere sostituiti con altri, anche se non sono di tipo omologato', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '227', '', '', 'Visuale ottimale e parabrezza - 02', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '227', '1697', '1', 'La visibilità attraverso il parabrezza deve essere ottimale in ogni condizione meteorologica', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '227', '1698', '2', 'La visibilità ottimale si ottiene anche mantenendo il tergicristallo in perfetta efficienza', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '227', '1699', '3', 'La visibilità ottimale si ottiene verificando periodicamente il livello del serbatoio del liquido lavavetri', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '227', '1700', '4', 'La visibilità ottimale si ottiene applicando pellicole autoadesive al parabrezza', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '227', '1701', '5', 'La visibilità ottimale si ottiene aggiungendo sabbia al liquido lavavetri', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('8B', '227', '1702', '6', 'La visibilità ottimale si ottiene detergendo i vetri con olio lubrificante', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '228', '', '', 'Rischi per la salute del conducente', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '228', '1703', '1', 'Le vibrazioni trasmesse dal veicolo aumentano il rischio per la salute del conducente professionale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '228', '1704', '2', 'L’esposizione continuata alle vibrazioni espone il conducente a sofferenze a carico della colonna vertebrale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '228', '1705', '3', 'Un eccessivo rumore all’interno della cabina di guida incide sul benessere del conducente', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '228', '1706', '4', 'Una posizione di guida scorretta comporta l’affaticamento dell’apparato scheletrico ma non di quello muscolare del conducente', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '228', '1707', '5', 'La prolungata esposizione alle vibrazioni non produce danni alla salute del conducente se questi fa uso di integratori alimentari', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '228', '1708', '6', 'La prolungata esposizione alle vibrazioni non produce danni alla salute del conducente se questi fa uso di antidolorifici', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '228', '1709', '7', 'La prolungata esposizione alle vibrazioni non è causa di malattie professionali ', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '229', '', '', 'Guida e idoneità fisica e mentale', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '229', '1710', '1', 'Per poter guidare occorre essere riconosciuti idonei per requisiti fisici e psichici', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '229', '1711', '2', 'Deve astenersi dalla guida chi ha mangiato cibi molto pesanti da digerire', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '229', '1712', '3', 'Deve astenersi dalla guida chi si trova in stato di forte emotività', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '229', '1713', '4', 'Deve astenersi dalla guida chi accusa segni di stanchezza', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '229', '1714', '5', 'Deve astenersi dalla guida chi ha assunto farmaci che possono compromettere le capacità di guida', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '229', '1715', '6', 'Deve astenersi dalla guida chi è soggetto ad un malore temporaneo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '230', '', '', 'Stress psicofisico - 01', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '230', '1716', '1', 'Durante l’attività professionale, il conducente è esposto a traumi conseguenti a incidente stradale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '230', '1717', '2', 'Durante l’attività professionale il conducente è esposto a distorsioni degli arti dovute alla salita o discesa dalla cabina', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '230', '1718', '3', 'Durante l’attività professionale il conducente può essere esposto a intossicazione per inquinamento ambientale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '230', '1719', '4', 'Lo stress comporta tensione muscolare che influisce sulla corretta postura del conducente', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '230', '1720', '5', 'Lo stress psicologico non ha influenza negativa sulla guida', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '230', '1721', '6', 'La frequenza ai corsi di aggiornamento organizzati dall’azienda può essere causa di stress psicofisico per il conducente professionale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '231', '', '', 'Stress psicofisico -02', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '231', '1722', '1', 'Svolgere attività lavorative durante i periodi di riposo obbligatorio può essere causa di stress psicofisico per il conducente professionale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '231', '1723', '2', 'Una buona condizione psicofisica è importante perché permette buoni tempi di reazione', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '231', '1724', '3', 'L’eccessivo rumore dell’ambiente può essere causa di stress psicofisico per il conducente professionale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '231', '1725', '4', 'La guida in condizioni di traffico eccessivo, può essere causa di stress psicofisico per il conducente professionale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '231', '1726', '5', 'L’affaticamento visivo, in particolare in caso di guida notturna, aumenta il rischio per la salute del conducente professionale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '231', '1727', '6', 'L’utilizzo degli occhiali da sole da parte del conducente è sconsigliabile perché affatica la vista', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '232', '', '', 'Riduzione stress - 01', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '232', '1728', '1', 'Per ridurre lo stress alla guida è consigliabile utilizzare tecniche di rilassamento', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '232', '1729', '2', 'Per ridurre i danni provocati dall’esposizione continuata alle vibrazioni, è utile fare esercizio fisico per rinforzare il tratto lombare', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '232', '1730', '3', 'Per ridurre i danni provocati dall’esposizione continuata alle vibrazioni, è utile fare esercizio fisico per rinforzare i muscoli del collo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '232', '1731', '4', 'L’insonorizzazione della cabina di guida migliora viaggiando con i finestrini chiusi', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '232', '1732', '5', 'L’insonorizzazione della cabina di guida migliora viaggiando con i finestrini aperti', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '232', '1733', '6', 'Per migliorare l’insonorizzazione nell’abitacolo, bisogna tenere alto il volume della radio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '233', '', '', 'Riduzione stress - 02', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '233', '1734', '1', 'Per limitare i rischi alla propria salute il conducente deve limitare al massimo il numero di guide notturne', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '233', '1735', '2', 'Per limitare i rischi alla propria salute il conducente deve rispettare il più possibile i ritmi di sonno-veglia abituali', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '233', '1736', '3', 'Osservare le pause prescritte dalla normativa contribuisce a prevenire le malattie professionali', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '233', '1737', '4', 'Rispettare i tempi di guida prescritti contribuisce a prevenire le malattie professionali', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '233', '1738', '5', 'Dormire un sufficiente numero di ore consente di ridurre i rischi connessi all’attività di guida', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '233', '1739', '6', 'Per migliorare la salute nella guida professionale occorre aumentare il consumo di sigarette', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '233', '1740', '7', 'Per migliorare la salute nella guida professionale occorre guidare almeno 12 ore ininterrotte per arrivare prima a destinazione', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '233', '1741', '8', 'Per migliorare la salute nella guida professionale occorre ascoltare la radio ad alto volume', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '233', '1742', '9', 'Per limitare i rischi di malattie professionali occorre guidare ininterrottamente almeno sei ore per arrivare prima a destinazione', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '233', '1743', '10', 'Per evitare rischi alla salute, il conducente non deve svolgere esercizio di rilassamento dei muscoli', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '234', '', '', 'sonnolenza e stanchezza al conducente', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '234', '1744', '1', 'Una laboriosa digestione dopo un pasto abbondante, può provocare sonnolenza al conducente', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '234', '1745', '2', 'La variazione continua di orari dei pasti e del sonno può essere causa di stress psicofisico per il conducente professionale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '234', '1746', '3', 'Quando si è stanchi il tempo di reazione è più lungo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '234', '1747', '4', 'Quando si è stanchi può essere difficile concentrarsi', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '234', '1748', '5', 'Quando si è stanchi si acuiscono le facoltà visive', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '234', '1749', '6', 'Quando si è stanchi bisogna aumentare la temperatura dell’abitacolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '234', '1750', '7', 'Quando si è stanchi è obbligatorio viaggiare con il finestrino aperto', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '234', '1751', '8', 'Prima di iniziare un lungo viaggio è consigliabile dormire non più di quattro ore la notte precedente', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '234', '1752', '9', 'L’effettuazione di pause durante la guida notturna può essere causa di stress psicofisico per il conducente professionale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '234', '1753', '10', 'Ci si può mettere alla guida anche se molto stanchi, se a bordo del veicolo si dispone di una sufficiente scorta di caffè forte', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '235', '', '', 'Colpo di sonno', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '235', '1754', '1', 'l colpo di sonno è un fattore di rischio per la sicurezza stradale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '235', '1755', '2', 'Il colpo di sonno colpisce soprattutto i conducenti stanchi o affaticati', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '235', '1756', '3', 'Il colpo di sonno coglie, di solito, chi non ha dormito abbastanza', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '235', '1757', '4', 'Il colpo di sonno interviene con minor probabilità dopo un pasto abbondante', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '235', '1758', '5', 'Il “colpo di sonno” si verifica solamente nelle ore notturne', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '235', '1759', '6', 'Il colpo di sonno si manifesta più frequentemente nei conducenti miopi', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '235', '1760', '7', 'Il colpo di sonno si contrasta assumendo tranquillanti', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '236', '', '', 'Cause di rischio incidente', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '236', '1761', '1', 'Il rischio di incidenti aumenta nel caso di maggior stanchezza del conducente', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '236', '1762', '2', 'Guidare in una situazione di traffico intenso può essere fonte di stress e quindi aumentare il rischio di incidenti stradali', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '236', '1763', '3', 'Guidare sistematicamente oltre gli orari prescritti, può essere fonte di stress del conducente, e quindi aumentare il rischio di incidenti stradali', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '236', '1764', '4', 'Lo stress del conducente aumenta il rischio di incidenti stradali', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '236', '1765', '5', 'La pratica regolare di esercizio fisico può essere fonte di stress del conducente, e quindi aumentare il rischio di incidenti stradali', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '236', '1766', '6', 'Dormire per otto ore consecutive è causa di stress per il conducente e quindi aumenta il rischio di incidenti stradali', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '237', '', '', 'postura non corretta alla guida', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '237', '1767', '1', 'Una postura non corretta alla guida può comportare danni a carico della colonna vertebrale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '237', '1768', '2', 'Una postura non corretta alla guida può comportare uno sforzo eccessivo delle articolazioni, dei tendini e dei muscoli', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '237', '1769', '3', 'Una postura corretta consente al conducente di ridurre l’affaticamento alla guida', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '237', '1770', '4', 'Durante l’attività professionale il conducente è esposto a patologie all’apparato muscolo-scheletrico a causa di posture scorrette', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '237', '1771', '5', 'Per evitare rischi di traumi alla colonna vertebrale, il conducente deve mantenere le cinture di sicurezza non aderenti al corpo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '238', '', '', 'Postura di guida - 01', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '238', '1772', '1', 'La corretta postura al posto di guida consente la prevenzione di alcune malattie professionali', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '238', '1773', '2', 'La corretta postura consente al conducente di avere una buona visibilità attraverso gli specchi retrovisori esterni, senza spostarsi sul sedile', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '238', '1774', '3', 'La corretta postura consente al conducente di azionare i pedali senza distendere completamente la gamba', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '238', '1775', '4', 'Una corretta postura comporta che il busto resti eretto durante la guida', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '238', '1776', '5', 'La corretta postura di guida del conducente si ottiene regolando la posizione del sedile', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '238', '1777', '6', 'La corretta postura di guida consente al conducente di derogare dalla normativa in materia di guida e di riposo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '238', '1778', '7', 'La corretta postura di guida consente al conducente di ridurre l’uso del cambio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '238', '1779', '8', 'La corretta posizione di guida consente al conducente di non utilizzare lo specchio retrovisore destro', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '239', '', '', ' Postura di guida - 02', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '239', '1780', '1', 'La postura è la posizione che assume il conducente durante la guida', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '239', '1781', '2', 'Il permanere nella stessa postura per un tempo prolungato durante la guida aumenta il rischio per la salute del conducente professionale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '239', '1782', '3', 'Il conducente ha assunto una corretta postura quando la schiena è appoggiata allo schienale del sedile', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '239', '1783', '4', 'La postura del conducente è corretta se occorre distendere le gambe completamente per azionare i pedali', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '239', '1784', '5', 'La postura del conducente è corretta se durante la guida le ginocchia toccano il volante', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '239', '1785', '6', 'La postura del conducente è corretta se durante la guida la schiena è distaccata dallo schienale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '239', '1786', '7', 'La postura del conducente è corretta se durante la guida il busto è inclinato in avanti', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '239', '1787', '8', 'La postura del conducente è corretta se comporta un affaticamento degli arti inferiori, ma non di quelli superiori', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '240', '', '', 'Ergonomia e posto di guida - 01', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '240', '1788', '1', 'L’ergonomia garantisce sicurezza e comfort di chi esegue un lavoro', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '240', '1789', '2', 'Il posto di guida è ergonomico se la permanenza prolungata alla guida non provoca affaticamento eccessivo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '240', '1790', '3', 'Il posto di guida è ergonomico se i movimenti necessari per la guida non comportano sforzo eccessivo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '240', '1791', '4', 'Il posto di guida è ergonomico se gli apparati muscolare e scheletrico del conducente non sono sottoposti ad affaticamento eccessivo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '240', '1792', '5', 'Il posto di guida è ergonomico se il veicolo è munito di navigatore satellitare', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '240', '1793', '6', 'Il posto di guida è ergonomico se la temperatura dell’abitacolo è mantenuta costantemente sopra i 28 gradi', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '240', '1794', '7', 'Il posto di guida è ergonomico se il veicolo è munito di impianto stereo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '240', '1795', '8', 'L’ergonomia studia il bilancio dell’azienda di trasporto', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '241', '', '', 'Ergonomia e posto di guida - 02', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '241', '1796', '1', 'L’ergonomia mira alla progettazione di strumenti e dispositivi che consentano al lavoratore di mantenere una corretta postura durante l’attività professionale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '241', '1797', '2', 'Il posto di guida è ergonomico se spazi e comandi sono adattabili alle differenti corporature dei conducenti', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '241', '1798', '3', 'Il sedile è regolato correttamente se il piede sinistro può appoggiare a sinistra della pedaliera quando è a riposo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '241', '1799', '4', 'Il posto di guida è ergonomico se spazi, comandi e strumentazioni sono facilmente raggiungibili ed azionabili', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '241', '1800', '5', 'L’ergonomia mira ad ottimizzare i consumi di carburante dei veicoli', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '241', '1801', '6', 'Il posto di guida è ergonomico se il conducente non deve azionare frequentemente gli indicatori di direzione', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '241', '1802', '7', 'Il posto di guida è ergonomico se il veicolo è dotato di collegamento satellitare antirapina', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '242', '', '', ' Aerazione e microclima in cabina', '');
