INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '255', '', '', 'Assunzione di ecstasy - 02', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '255', '1908', '1', 'L’ecstasy si presenta sotto forma di pasticche o tavolette', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '255', '1909', '2', 'L’ecstasy è una droga sintetica', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '255', '1910', '3', 'L’ecstasy è una droga che può provocare anche la morte', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '255', '1911', '4', 'L’ecstasy è una droga che causa dipendenza', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '255', '1912', '5', 'L’ecstasy è una droga a base di zafferano', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '255', '1913', '6', 'L’ecstasy è uno stato mistico', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '255', '1914', '7', 'L’ecstasy è un integratore alimentare', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '255', '1915', '8', 'L’ecstasy è un integratore energetico', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '255', '1916', '9', 'L’ecstasy può essere assunta prima della guida', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '256', '', '', 'Assunzione di marijuana', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '256', '1917', '1', 'L’assunzione di marijuana porta a perdita di memoria', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '256', '1918', '2', 'L’assunzione di marijuana allunga il tempo di reazione', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '256', '1919', '3', 'L’assunzione di marijuana migliora la reattività del conducente', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '256', '1920', '4', 'La marijuana è un medicinale per curare il mal di testa', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '256', '1921', '5', 'La marijuana accorcia il tempo di reazione', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '257', '', '', 'Alcool, Droghe e guida', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '257', '1922', '1', 'L’abuso di alcool può essere causa di licenziamento dei conducenti professionali', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '257', '1923', '2', 'Il conducente è considerato sotto l’effetto di sostanze stupefacenti se le analisi del sangue rilevano tracce di tali sostanze', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '257', '1924', '3', 'È possibile viaggiare per brevi tragitti in cattive condizioni psicofisiche, se al termine del viaggio si osserva un periodo di riposo supplementare', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '257', '1925', '4', 'Sostanze stupefacenti possono essere assunte durante la guida perché potenziano i riflessi', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '257', '1926', '5', 'L’assunzione di marijuana durante la guida non è sanzionata dal codice della strada', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '257', '1927', '6', 'Guidare sotto l’effetto di sostanze stupefacenti è consentito per i conducenti di veicoli adibiti al trasporto di merci ma non di persone', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '258', '', '', 'Effetto di sostanze stupefacenti', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '258', '1928', '1', 'Il conducente è considerato sotto l’effetto di sostanze stupefacenti solo dopo essersi sottoposto ad analisi presso strutture sanitarie accreditate', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '258', '1929', '2', 'Il conducente è considerato sotto l’effetto di sostanze stupefacenti solo se le analisi di laboratorio lo attestano', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '258', '1930', '3', 'Il conducente è considerato sotto l’effetto di sostanze stupefacenti se non risponde correttamente al questionario proposto dalle forze dell’ordine', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '258', '1931', '4', 'Il conducente è considerato sotto l’effetto di sostanze stupefacenti se si trovano tracce di fumo nell’abitacolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '258', '1932', '5', 'Il conducente è considerato sotto l’effetto di sostanze stupefacenti solo se ci sono tracce di queste sostanze nei suoi liquidi biologici', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '259', '', '', 'Veicoli per trasporto specifico - 01', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '259', '1933', '1', 'Per il trasporto di merce liquida non pericolosa (ad esempio acqua) tramite veicoli si utilizzano normali cisterne o serbatoi idonei allo scopo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '259', '1934', '2', 'Per il trasporto di rifiuti solidi urbani, è richiesto l’uso di veicoli per trasporto specifico di tali merci', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '259', '1935', '3', 'Per il trasporto di merci deperibili in regime ATP, è richiesto l’uso di veicoli per trasporto specifico muniti di carrozzeria in regime di temperatura controllata', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '259', '1936', '4', 'Per il trasporto di animali vivi è richiesto l’uso di appositi veicoli per trasporto specifico', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '259', '1937', '5', 'Per il trasporto di mobili d’arredamento è richiesto l’uso di veicoli per trasporto specifico muniti di porta container o casse mobili', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '259', '1938', '6', 'Per il trasporto di carta con pallet è richiesto l’uso di veicoli per trasporto specifico muniti di carrozzeria in regime ATP (accordo internazionale trasporto derrate deperibili)', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '259', '1939', '7', 'Per il trasporto di collettame è richiesto l’uso di veicoli per trasporto specifico muniti di carrozzeria per trasporto di granulati e pulverulenti', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '260', '', '', 'Veicoli per trasporto specifico - 02', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '260', '1940', '1', 'Gli autoveicoli muniti di carrozzeria del tipo a cisterne sono classificati per trasporto specifico', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '260', '1941', '2', ' Gli autoveicoli muniti di furgoni isotermici per il trasporto di derrate in regime ATP (accordo internazionale trasporto derrate deperibili) sono classificati per trasporto specifico', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '260', '1942', '3', 'Gli autoveicoli muniti di betoniera sono classificati per trasporto specifico', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '260', '1943', '4', 'Gli autoveicoli muniti di carrozzeria del tipo a furgone di massa massima complessiva superiore a 3,5 t sono classificati per trasporto specifico', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '260', '1944', '5', 'Gli autocarri di massa massima complessiva inferiore a 3,5 t sono classificati per trasporto specifico', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '260', '1945', '6', 'I veicoli per trasporto specifico possono esser utilizzati per il trasporto di persone', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '261', '', '', 'Veicoli per uso special', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '261', '1946', '1', 'Gli autoveicoli per soccorso stradale sono dotati di carrozzeria idonea al trasporto di veicoli', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '261', '1947', '2', 'Gli autoveicoli per soccorso stradale sono classificati per uso speciale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '261', '1948', '3', 'Gli autoveicoli muniti di autopompe per calcestruzzo sono classificati per uso speciale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '261', '1949', '4', 'Gli autoveicoli muniti di betoniere sono classificati per uso speciale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '261', '1950', '5', 'I veicoli muniti di attrezzature per il trasporto di container sono classificati per uso speciale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '261', '1951', '6', 'I veicoli attrezzati per il trasporto esclusivo di animali vivi sono classificati per uso speciale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '262', '', '', 'Trasporto di terra, sabbia e inerti', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '262', '1952', '1', 'Per il trasporto di terra, sabbia e materiali inerti si utilizzano, di norma, veicoli muniti di cassone ribaltabile', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '262', '1953', '2', 'Per il trasporto di terra, sabbia e materiali inerti si utilizzano, di norma, veicoli classificati mezzo d’opera muniti di cassone ribaltabile', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '262', '1954', '3', 'Per il trasporto di terra, sabbia e materiali inerti sfusi si utilizzano, di norma, veicoli muniti di piano di carico continuo senza sponde laterali', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '262', '1955', '4', 'Per il trasporto di terra, sabbia e materiali inerti si debbono utilizzare esclusivamente veicoli muniti di attrezzatura per lo scarramento delle carrozzerie', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '262', '1956', '5', 'Per il trasporto di terra, sabbia e materiali inerti sfusi si devono utilizzare veicoli muniti di sponde montacarichi', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '263', '', '', 'Trasporto di collettame', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '263', '1957', '1', 'Il trasporto di collettame può essere effettuato con veicolo munito di carrozzeria furgone', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '263', '1958', '2', 'Il trasporto di collettame può essere effettuato con veicolo munito di carrozzeria con cassone aperto', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '263', '1959', '3', 'Il trasporto di collettame può essere effettuato con veicolo munito di betoniera', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '263', '1960', '4', 'Il trasporto di collettame può essere effettuato con veicolo munito di cisterna', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '264', '', '', 'Veicoli muniti di attrezzature di scarramento', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '264', '1961', '1', 'I veicoli muniti di attrezzature di scarramento sono utili per evitare lunghe soste del cassone mentre il veicolo è inattivo o mentre si provvede al carico della merce', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '264', '1962', '2', 'I veicoli muniti di attrezzature di scarramento sono utili per evitare lunghe soste necessarie per caricare o scaricare il vano di carico', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '264', '1963', '3', 'I veicoli muniti di attrezzature di scarramento consentono la ottimizzazione dei modi e dei tempi di carico e scarico', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '264', '1964', '4', 'I veicoli muniti di attrezzature di scarramento sono utili per evitare lunghe soste al veicolo mentre il cassone è a terra per operazioni di carico e scarico della merce', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '264', '1965', '5', 'I veicoli muniti di attrezzature di scarramento sono utilizzati per il trasporto dei veicoli in avaria', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '264', '1966', '6', 'I veicoli muniti di attrezzature di scarramento sono studiati dal punto di vista aerodinamico per ridurre il consumo di carburante del veicolo durante il trasporto', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '264', '1967', '7', 'I veicoli muniti di attrezzature di scarramento sono necessari per il trasporto delle merci pericolose', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '264', '1968', '8', 'L’attrezzatura scarrabile aumenta la stabilità di marcia del veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '264', '1969', '9', 'I veicoli muniti di attrezzature di scarramento sono utili per ridurre il carico sull’asse posteriore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '265', '', '', 'Gru e sponde caricatrici', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '265', '1970', '1', 'Le gru caricatrici installate sui veicoli consentono di caricare e scaricare colli pesanti e di notevoli dimensioni', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '265', '1971', '2', 'Le gru caricatrici vengono installate, solitamente, dietro la cabina di guida ma possono essere installate anche sullo sbalzo posteriore del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '265', '1972', '3', 'Le sponde caricatrici sono installate quasi sempre nella parte posteriore del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '265', '1973', '4', 'Periodicamente è necessario verificare lo stato di efficienza dell’attrezzatura (gru o sponda montacarichi) installata sul veicolo ed eseguire appropriati interventi di manutenzione', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '265', '1974', '5', 'Le gru caricatrici vengono installate, solitamente, davanti alla cabina di guida oppure sullo sbalzo posteriore del veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '265', '1975', '6', 'Le sponde caricatrici sono attrezzature costituite da una pedana mobile che agevola le operazioni di carico ma non quelle di scarico', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '266', '', '', 'Massa complessiva', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '266', '1976', '1', 'Per i complessi di veicoli costituiti da una motrice e un rimorchio è necessario verificare che la massa complessiva del rimorchio sia compatibile con la massa rimorchiabile della motrice', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '266', '1977', '2', 'La massa complessiva a pieno carico è sempre indicata sul documento di circolazione del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '266', '1978', '3', 'La massa complessiva a pieno carico coincide con la massa del veicolo in ordine di marcia', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '266', '1979', '4', 'La massa complessiva a pieno carico è la differenza tra la portata e la tara', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '267', '', '', 'Carico/Portata', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '267', '1980', '1', 'Nel caso di veicoli muniti di carrozzeria a furgone, il volume di carico è pari al prodotto della superficie del piano di appoggio e dell’altezza utile interna della carrozzeria', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '267', '1981', '2', 'Quando si trasportano merci su un veicolo è indispensabile verificare accuratamente che il carico sia compatibile con portata e volume utili del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '267', '1982', '3', 'La portata è il carico che può essere effettivamente trasportato sul veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '267', '1983', '4', 'La portata degli autocarri è indicata sul documento di circolazione', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '267', '1984', '5', 'Il carico che può essere effettivamente trasportato sul veicolo è individuato dalla portata del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '267', '1985', '6', 'Il carico effettivamente trasportato su un veicolo non deve causare il superamento della sua massa complessiva a pieno carico', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '267', '1986', '7', 'I veicoli che trasportano merci pericolose possono essere caricati oltre i limiti della portata massima del veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '267', '1987', '8', 'La portata di un veicolo aumenta all’aumentare della tara del veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '267', '1988', '9', 'La portata del rimorchio corrisponde alla sua massa massima a pieno carico', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '267', '1989', '10', 'La portata è pari al peso della carrozzeria installata sul veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '267', '1990', '11', 'La portata di un autoveicolo coincide con la sua massa complessiva a pieno carico', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '267', '1991', '12', 'La portata di un veicolo corrisponde alla sua massa in ordine di marcia', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '267', '1992', '13', 'Nel caso di veicoli muniti di carrozzeria a furgone, il volume di carico è pari al prodotto della lunghezza del piano di appoggio della carrozzeria e dell’altezza utile interna della carrozzeria', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '267', '1993', '14', 'Nel caso di veicoli muniti di carrozzeria a cassone, il volume di carico è pari al prodotto della lunghezza del piano di appoggio e del limite massimo di altezza imposto dalle norme per autoveicoli (4 m)', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '268', '', '', 'Sovraccarico', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '268', '1994', '1', 'La tolleranza relativa al carico pari al 5% si applica anche ai veicoli che trasportano merci pericolose come, ad esempio, il GPL', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '268', '1995', '2', 'È ammessa la tolleranza di sovraccarico per il trasporto delle merci pericolose purché nel limite del 5% della massa complessiva a pieno carico del veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '268', '1996', '3', 'È ammessa la tolleranza di sovraccarico del 5% del peso complessivo a pieno carico ammissibile del veicolo per il trasporto di benzina in cisterne', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '268', '1997', '4', 'Per un autocarro mezzo d’opera non è consentito un sovraccarico del 3% della sua massa complessiva a pieno carico', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '268', '1998', '5', 'È ammessa la tolleranza di sovraccarico per il trasporto di benzina purché nel limite del 5% della massa complessiva a pieno carico ammissibile del veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '268', '1999', '6', 'È consentito un sovraccarico massimo del 15% rispetto alla massa complessiva a pieno carico ammissibile del veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '269', '', '', 'Il sovraccarico del veicolo incide', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '269', '2000', '1', 'Il sovraccarico del veicolo incide sulla stabilità di marcia', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '269', '2001', '2', 'Il sovraccarico del veicolo incide sull’efficienza delle sospensioni', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '269', '2002', '3', 'Il sovraccarico del veicolo incide sull’efficienza dell’impianto frenante', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '269', '2003', '4', 'Il sovraccarico del veicolo incide sull''efficienza del limitatore di velocità', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '269', '2004', '5', 'Il sovraccarico del veicolo incide sull’efficienza dei dispositivi di illuminazione e segnalazione visiva', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '269', '2005', '6', 'Il sovraccarico del veicolo incide sul corretto funzionamento del tachigrafo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '270', '', '', 'Sanzioni e Responsabilità condivisa', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '270', '2006', '1', 'Del sovraccarico di un veicolo sono responsabili il conducente, il proprietario del veicolo ed il committente', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '270', '2007', '2', 'Il sovraccarico del veicolo di oltre il 5% della massa complessiva a pieno carico ammissibile comporta l’applicazione di sanzioni a carico del proprietario del veicolo ma non del conducente', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '270', '2008', '3', 'Il sovraccarico del veicolo fino al 15% della massa complessiva a pieno carico ammissibile non comporta l’applicazione di sanzioni', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '271', '', '', 'Definizione merci', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '271', '2009', '1', 'Le merci si distinguono in base allo stato naturale che può essere di tipo solido, liquido e gassoso', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '271', '2010', '2', ' Le merci di provenienza nazionale sono quelle prodotte nel territorio nazionale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '271', '2011', '3', 'Le merci non deperibili richiedono un rapido consumo o attenzioni particolari per la conservazione durante il trasporto', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '271', '2012', '4', 'Le merci semilavorate sono quelle che hanno subito tutti i processi lavorativi necessari per renderle atte all’uso da parte dei consumatori', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '272', '', '', 'Sistemazione della merce', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '272', '2013', '1', 'La merce deve essere sistemata sul veicolo in modo tale che sia assicurata la piena visibilità in tutte le direzioni e sia garantita la piena libertà di guida del conducente', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '272', '2014', '2', 'La merce deve essere sistemata sul veicolo in modo tale che non siano superati i limiti di massa e di sagoma previsti dalle vigenti norme', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '272', '2015', '3', 'La sistemazione della merce sui veicoli ha notevoli riflessi sulla sicurezza della circolazione stradale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '272', '2016', '4', ' Le operazioni di carico del veicolo devono essere effettuate con la massima attenzione avendo cura di posizionare e ancorare correttamente la merce da trasportare', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '272', '2017', '5', 'Nel trasporto di merci, il carico può essere sistemato sul veicolo anche se viene compromessa la stabilità di marcia', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '273', '', '', 'Sistemazione del carico', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '273', '2018', '1', 'Il carico deve essere sistemato in modo da distribuire il più uniformemente possibile la massa sulla struttura portante', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '273', '2019', '2', 'Il carico deve essere sistemato in modo da evitare il sovraccarico su uno dei due assi del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '273', '2020', '3', 'Il carico deve essere sistemato in modo che i colli più pesanti siano sistemati sullo sbalzo posteriore della carrozzeria', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '274', '', '', 'Errato sistemazione del carico - 01', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '274', '2021', '1', 'Un’errata sistemazione del carico su un autocarro può causare il sovraccarico del veicolo e/o di uno o più assi', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '274', '2022', '2', 'Un errata sistemazione del carico può aumentare il pericolo di ribaltamento del veicolo per il cedimento di una banchina laterale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '274', '2023', '3', 'Un’errata sistemazione del carico può causarne la caduta sulla sede stradale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '274', '2024', '4', 'Un’errata sistemazione del carico su un autocarro può compromettere la stabilità di marcia del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '274', '2025', '5', 'Un’errata sistemazione del carico su un autocarro può avere conseguenze sulla stabilità di marcia', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '274', '2026', '6', 'Un’errata sistemazione del carico su un autocarro è sanzionata con il sequestro del veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '274', '2027', '7', 'Un’errata sistemazione del carico su un autocarro può provocare danni all’impianto elettrico del veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '275', '', '', ' Errato posizionamento del carico - 02', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '275', '2028', '1', 'Un errato posizionamento del carico può determinare il sovraccarico di uno o più assi', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '275', '2029', '2', 'Un errato posizionamento del carico può compromettere la stabilità di marcia del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '275', '2030', '3', ' Un errato posizionamento del carico non incide sulla stabilità di marcia del veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '275', '2031', '4', 'Un errato posizionamento del carico può diminuire il rendimento del motore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '276', '', '', 'Ai fini della sicurezza', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '276', '2032', '1', 'Ai fini della sicurezza non si debbono superare le masse massime ammesse sugli assi del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '276', '2033', '2', 'Ai fini della sicurezza occorre ripartire correttamente il carico per evitare il sovraccarico sugli assi del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '276', '2034', '3', 'Ai fini della sicurezza non si deve concentrare il carico sullo sbalzo posteriore del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '276', '2035', '4', 'Ai fini della sicurezza del trasporto di merci bisogna evitare di agganciare un rimorchio alla motrice', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '276', '2036', '5', 'Ai fini della sicurezza, il carico deve essere disposto verticalmente lontano dal baricentro geometrico del piano di appoggio della carrozzeria', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '277', '', '', 'Più colli - Unico collo', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '277', '2037', '1', 'Nel disporre più colli sul piano di carico della carrozzeria bisogna distribuirli in maniera il più possibile uniforme', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '277', '2038', '2', 'Più colli di piccole dimensioni devono essere distribuiti nel modo più omogeneo possibile sul piano di carico della carrozzeria', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '277', '2039', '3', 'Un collo molto pesante deve essere adeguatamente fissato al veicolo mediante uno o più metodi di ritenuta', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '277', '2040', '4', 'Un unico collo molto pesante deve essere posizionato in corrispondenza della parte centrale del piano di carico della carrozzeria, prendendo le dovute precauzioni', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '277', '2041', '5', 'Un unico collo molto pesante deve essere sistemato sul piano di carico della carrozzeria utilizzando, ove possibile, lunghe travi di appoggio', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '277', '2042', '6', 'Un unico collo molto pesante deve essere posizionato in corrispondenza dell’estremità anteriore del piano di carico della carrozzeria', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '277', '2043', '7', 'Un unico collo molto pesante può essere posizionato indifferentemente in corrispondenza dell’estremità anteriore o dell’estremità posteriore del piano di carico della carrozzeria', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '277', '2044', '8', 'Un unico collo molto pesante deve essere posizionato in corrispondenza dell’estremità posteriore del piano di carico della carrozzeria', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '277', '2045', '9', 'Più colli di piccole dimensioni devono essere concentrati nella parte posteriore del piano di carico della carrozzeria', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '277', '2046', '10', 'Più colli di piccole dimensioni devono essere concentrati nella parte anteriore del piano di carico della carrozzeria', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '278', '', '', 'Baricentro e spostamento del carico', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '278', '2047', '1', 'Per evitare lo spostamento del carico durante la marcia del veicolo è opportuno agire su sterzo, freni ed acceleratore con gradualità', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '278', '2048', '2', 'Per evitare lo spostamento del carico durante la marcia del veicolo si devono ancorare correttamente le merci al piano di carico', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '278', '2049', '3', 'Il carico deve essere sistemato in modo che il baricentro del veicolo risulti posizionato verso il basso rispetto alla sede stradale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '278', '2050', '4', 'Ai fini della verifica di stabilità del carico occorre individuare la posizione del baricentro della merce sistemata sul piano di appoggio della carrozzeria', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '278', '2051', '5', 'Se il baricentro del veicolo risulta posizionato verso l’alto rispetto alla sede stradale il veicolo rischia più facilmente il ribaltamento in curva', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '278', '2052', '6', 'Se il baricentro del veicolo risulta posizionato verso l’alto rispetto alla sede stradale può essere compromessa la stabilità di marcia in curva', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '278', '2053', '7', 'Se il baricentro del veicolo risulta posizionato verso l’alto rispetto alla sede stradale aumenta la stabilità di marcia in curva', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '278', '2054', '8', 'Se il baricentro del veicolo risulta posizionato verso l’alto rispetto alla sede stradale diminuisce l’effetto della forza centrifuga in curva', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '278', '2055', '9', 'Il carico deve essere sistemato in modo che il baricentro del veicolo risulti posizionato il più in alto possibile rispetto alla sede stradale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '278', '2056', '10', 'Il carico deve essere sistemato in modo che i colli pesanti siano posizionati il più in alto possibile rispetto alla sede stradale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '278', '2057', '11', 'La verifica della posizione del baricentro di un veicolo carico è irrilevante ai fini della stabilità di marcia del veicolo in curva', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '279', '', '', 'Evitare lo spostamento/scivolamento del carico', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '279', '2058', '1', 'I colli devono essere sistemati in modo che non si spostino durante la marcia del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '279', '2059', '2', 'Il carico sistemato sul veicolo deve essere protetto contro le intemperie e la dispersione', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '279', '2060', '3', 'Il carico sistemato sul veicolo deve essere trattenuto in senso orizzontale per evitare che scivoli sul piano di appoggio della carrozzeria', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '279', '2061', '4', 'Per evitare lo spostamento (scivolamento o ribaltamento) del carico sul piano di appoggio della carrozzeria del veicolo è vietato utilizzare cinghie', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '279', '2062', '5', 'Per evitare lo scivolamento del carico sul piano di appoggio della carrozzeria è sempre sufficiente l’attrito tra il carico e il piano di appoggio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '279', '2063', '6', 'Per evitare il sollevamento del carico sistemato sul veicolo è sempre sufficiente la forza peso del carico che lo spinge verso il piano di appoggio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '279', '2064', '7', 'L’eventuale perdita del carico deve essere tempestivamente segnalata e, ove non sia possibile l’immediata rimozione, occorre immediatamente informare dell’accaduto i funzionari del PRA (pubblico registro automobilistico)', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '279', '2065', '8', 'Per evitare il rotolamento del carico occorre accertarsi che la risultante della forza peso e della forza di inerzia cada all’interno del perimetro della base di appoggio della carrozzeria', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '279', '2066', '9', 'Per evitare lo spostamento del carico durante la marcia del veicolo è indispensabile controllare frequentemente la pressione dei pneumatici', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '279', '2067', '10', 'Per evitare lo spostamento del carico durante la marcia del veicolo è opportuno ancorarlo alla gru caricatrice', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '279', '2068', '11', 'Non occorre assicurarsi che le forze che agiscono sul carico durante la marcia del veicolo possano ribaltarlo sul piano di appoggio in senso longitudinale e trasversale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '279', '2069', '12', 'Le forze che agiscono sul carico durante la marcia del veicolo (forza di inerzia, forza centrifuga, ecc.) possono causare il ribaltamento del collo sul piano di carico in avanti e all’indietro ma non lateralmente', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '280', '', '', ' Cinghie', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '280', '2070', '1', 'Le cinghie di ancoraggio del carico devono essere posizionate in modo da evitare lo scorrimento dei colli in senso longitudinale e trasversale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '280', '2071', '2', 'Le cinghie (nastri) di tessuto e relativi cricchetti tenditori sono utilizzati generalmente per l’ancoraggio a terra o diretto del carico sistemato sul piano di appoggio della carrozzeria', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '280', '2072', '3', 'Le cinghie utilizzate per fissare il carico sul veicolo non devono essere lacerate, usurate o presentare dispositivi di serraggio (cricchetti tenditori) danneggiati', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '280', '2073', '4', 'Le cinghie utilizzate per fissare il carico sono ancorate al piano di appoggio del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '280', '2074', '5', 'Le cinghie utilizzate per l’ancoraggio del carico sul veicolo sono munite di apposita etichetta sulla quale viene riportato anche il materiale costitutivo del dispositivo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '280', '2075', '6', 'Sull’etichetta delle cinghie utilizzate per l’ancoraggio del carico sul veicolo viene riportato il valore della tensione massima di utilizzo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '280', '2076', '7', 'Sull’etichetta delle cinghie utilizzate per l’ancoraggio del carico sul veicolo viene riportato il valore della tara massima ammessa del veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '280', '2077', '8', 'Sull’etichetta delle cinghie utilizzate per l’ancoraggio del carico sul veicolo viene riportato il valore della massa massima complessiva del veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '281', '', '', 'Corretto ancoraggio - 01', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '281', '2078', '1', 'Ai fini del corretto ancoraggio dei colli sul veicolo si deve tenere conto del numero e della tensione delle cinghie di ancoraggio', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '281', '2079', '2', 'Ai fini del corretto ancoraggio dei colli sul veicolo si deve tenere conto del coefficiente di attrito tra i colli e il piano di appoggio della carrozzeria', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '281', '2080', '3', 'Ai fini del corretto ancoraggio dei colli sul veicolo si deve tenere conto dell’angolo tra la cinghia e il piano di appoggio dei colli', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '281', '2081', '4', 'È vietato fissare il carico sul veicolo con cinghie serrate tramite apposito cricchetto tenditore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '281', '2082', '5', 'Non è ammesso utilizzare cinghie (nastri) di tessuto e relativi cricchetti tenditori per l’ancoraggio diretto del carico sistemato sul piano di appoggio della carrozzeria', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '282', '', '', 'Corretto ancoraggio - 02', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '282', '2083', '1', 'Nel trasporto di grossi colli cilindrici è indispensabile fissare il carico oltre che tramite cinghie anche con appositi cunei che ne impediscano il rotolamento sul piano di appoggio della carrozzeria', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '282', '2084', '2', 'I veicoli adibiti al trasporto di merci possono essere muniti di accessori rimovibili quali le coperture telate, le sovrasponde e altri', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '282', '2085', '3', 'Nel caso di colli molto pesanti (veicoli, macchine operatrici, ecc.) è indispensabile utilizzare sistemi di ancoraggio a catena o a fune appositamente realizzati', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '282', '2086', '4', 'Le principali caratteristiche delle catene utilizzate per fissare il carico al veicolo sono lo spessore degli anelli e la qualità del materiale costitutivo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '282', '2087', '5', 'È vietato l’uso di attrezzature ausiliarie quali travi, assi, montanti e pareti mobili per evitare lo spostamento del carico durante il trasporto', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '282', '2088', '6', 'L’interposizione di materiale riempitivo (cartone, gomma piuma, ecc.) tra colli vicini o in contatto tra di loro è vietata', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '282', '2089', '7', 'Ai fini del corretto ancoraggio dei colli sul veicolo si deve tenere conto del tempo di guida del conducente', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '282', '2090', '8', 'È vietato l’uso di catene per ancorare direttamente al veicolo carichi molto pesanti', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '282', '2091', '9', 'È vietato l’uso di funi di acciaio per ancorare direttamente al veicolo carichi molto pesanti', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '283', '', '', 'Sistemi fissaggio coperture telate', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '283', '2092', '1', 'I sistemi di fissaggio delle coperture telate delle carrozzerie dei veicoli possono essere del tipo a corde elastiche e anelli passanti', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '283', '2093', '2', 'I sistemi di fissaggio delle coperture telate delle carrozzerie dei veicoli possono essere del tipo a teli scorrevoli su guide verticali', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '283', '2094', '3', 'L’utilizzo di coperture telate sui veicoli impiegati nei trasporti in regime TIR è vietato', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '284', '', '', 'Capacità di carico di una cisterna', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '284', '2095', '1', 'La capacità della cisterna per trasporto di merci non pericolose, a pressione atmosferica, è commisurata alla portata utile del veicolo sul quale è installata', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '284', '2096', '2', 'La capacità di carico di una cisterna per merci non pericolose dipende dalla percentuale di vuoto ammesso', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '284', '2097', '3', 'La capacità di carico di una autocisterna per merci non pericolose dipende dal peso specifico del liquido e dalla percentuale di vuoto ammesso', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '284', '2098', '4', 'La capacità di carico di una cisterna per merci non pericolose dipende dalle prescrizioni delle norme dell’accordo TIR', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '284', '2099', '5', 'La capacità di carico di una cisterna per merci non pericolose dipende dalla tipologia dell’impianto frenante di cui è munito il veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '284', '2100', '6', 'Durante il trasporto di liquidi con cisterne è ammesso il superamento della massa complessiva del veicolo e delle masse massime ammesse sugli assi', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '284', '2101', '7', 'I veicoli cisterna non possono viaggiare a pieno carico per problemi di stabilità di marcia', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '285', '', '', 'Stabilità di marcia dei veicoli cistern', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '285', '2102', '1', 'Prima di partire occorre assicurarsi che i portelli dei passi d’uomo collocati nella parte superiore della cisterna siano correttamente chiusi per evitare la fuoriuscita della merce trasportata', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '285', '2103', '2', 'Nelle cisterne, appositi tramezzi o frangiflutto trasversali e longitudinali, conformi alle norme ADR riducono le sollecitazioni del liquido in movimento in presenza di serbatoio non completamente riempito', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '285', '2104', '3', 'I veicoli cisterna sono soggetti ad instabilità a causa del fenomeno dello sbattimento del liquido trasportato', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '285', '2105', '4', 'Nell’attraversamento delle rotatorie, i veicoli cisterna sono soggetti al fenomeno dello sbattimento laterale del liquido trasportato', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '285', '2106', '5', 'I veicoli cisterna sono soggetti al fenomeno dello sbattimento laterale del liquido trasportato durante le brusche variazioni di traiettorie', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '285', '2107', '6', 'Il mancato rispetto del vuoto massimo del volume totale per trasporto di merci non pericolose diminuisce la fluttuazione della merce liquida trasportata e aumenta la stabilità di marcia del veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '285', '2108', '7', 'I veicoli cisterna sono soggetti al fenomeno del sovraccarico dell’asse posteriore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '285', '2109', '8', 'Percorrendo una curva, i veicoli cisterna sono soggetti al fenomeno della dispersione del carico a causa dello sbattimento laterale del liquido trasportato', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '286', '', '', 'Bisarche', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '286', '2110', '1', 'Nel caso di trasporto di veicoli tramite veicoli specificamente attrezzati con guide carrabili (cd. bisarche) si utilizzano, di norma, specifici sistemi di ancoraggio', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '286', '2111', '2', 'Nel caso di trasporto di veicoli tramite veicoli specificamente attrezzati con guide carrabili (cd. bisarche) si deve aver cura di sistemare i veicoli più pesanti sul piano di carico più vicino alla sede stradale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '286', '2112', '3', 'Nel caso di t rasporto di veicol i tramite veicoli specificamente attrezzati con guide carrabili (cd. bisarche) si deve aver cura di caricare il primo veicolo con la parte posteriore rivolta nella direzione di marcia', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '286', '2113', '4', 'Nel caso di trasporto di veicoli tramite veicoli specificamente attrezzati con guide carrabili (cd. bisarche) i''ingombro massimo in altezza non deve superare 3 metri', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '287', '', '', 'Carico sporgente', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '287', '2114', '1', 'Nel caso di carico sporgente nella parte posteriore, la sporgenza, anche di un solo centimetro, deve essere segnalata in modo idoneo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '287', '2115', '2', 'Nella parte posteriore dei veicoli possono sporgere solo oggetti indivisibili, entro precisi limiti', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '287', '2116', '3', 'È ammessa la sporgenza del carico nella parte posteriore non oltre i 3/10 della lunghezza del veicolo, entro i limiti di sagoma previsti per la categoria del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '287', '2117', '4', 'La sporgenza del carico nella parte posteriore deve essere segnalata mediante uno o due pannelli quadrangolari grandi almeno 50x50 cm, a strisce diagonali bianche e rosse retroriflettenti', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '287', '2118', '5', 'Il trasporto di carichi sporgenti sui veicoli è sempre vietato', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '287', '2119', '6', 'Il carico sporgente nella parte posteriore del veicolo può strisciare sul terreno', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '287', '2120', '7', 'È ammessa la sporgenza del carico nella parte posteriore della carrozzeria fino a 30 cm, in deroga ai limiti di sagoma della categoria di veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '287', '2121', '8', 'Quando il carico sporge longitudinalmente per l’intera larghezza della parte posteriore del veicolo, i pannelli di segnalazione devono essere almeno 4', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '287', '2122', '9', 'I veicoli muniti di piano di carico aggiuntivo sopra la cabina del conducente possono raggiungere 4,4 m di altezza dal suolo, compreso il carico', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '288', '', '', 'Unità di trasporto', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '288', '2123', '1', 'La normativa internazionale ADR intende per unità di trasporto un autocarro con o senza rimorchio', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '288', '2124', '2', 'La normativa internazionale ADR intende per unità di trasporto un complesso veicolare costituito da trattore e un semirimorchio', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '288', '2125', '3', 'Per unità di trasporto s’intende un contenitore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '288', '2126', '4', 'Per unità di trasporto s’intende un autocarro al quale sono agganciati due rimorchi', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '288', '2127', '5', 'Per unità di trasporto s’intende un trattore stradale senza semirimorchio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '288', '2128', '6', 'Per unità di trasporto s’intende un rimorchio o semirimorchio quando sono sganciati sulla strada', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '289', '', '', 'Unità di carico', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '289', '2129', '1', 'L’unità di carico può essere utilizzata per il trasporto intermodale delle merci', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '289', '2130', '2', 'L’unità di carico può essere costituita da un container', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '289', '2131', '3', 'L’unità di carico è una unità logistica utilizzata per la movimentazione e lo stoccaggio delle merci', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '289', '2132', '4', 'L’unità di carico denominata pallet è costituita da un semirimorchio realizzato per il trasporto combinato strada-rotaia', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '289', '2133', '5', 'L’unità di carico è il singolo lavoratore impegnato nel carico e scarico delle merci', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '289', '2134', '6', ' L’unità di carico non può essere costituita da un semirimorchio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '289', '2135', '7', 'L’unità di carico è sempre del tipo a movimentazione manuale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '289', '2136', '8', 'Gli autocarri con centine e telone sono considerati unità di carico nel traffico intermodale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '289', '2137', '9', 'Le rampe di carico della carrozzeria del veicolo si considerano unità di carico', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '289', '2138', '10', 'L’unità di carico è un sindacato che tutela gli interessi dei caricatori professionali', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '289', '2139', '11', 'I cunei di stazionamento che equipaggiano il veicolo sono da considerarsi unità di carico', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '290', '', '', 'Container/Casse mobili', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '290', '2140', '1', 'I container sono unità di carico usati nel traffico intermodale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '290', '2141', '2', 'I container sono considerati unità di carico', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '290', '2142', '3', 'I container e le casse mobili hanno dimensioni e caratteristiche unificate su base internazionale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '290', '2143', '4', 'Le casse mobili sono unità di carico utilizzate nel traffico intermodale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '290', '2144', '5', 'Le casse mobili sono considerate unità di carico', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '290', '2145', '6', 'I container e le casse mobili trovano largo impiego nei trasporti combinati', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '290', '2146', '7', 'I container e le casse mobili possono non essere di proprietà delle aziende che le utilizzano', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '290', '2147', '8', 'Le navi porta container sono considerate unità di carico nel traffico intermodale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '290', '2148', '9', 'I container e le casse mobili sono trasportati esclusivamente tramite veicoli stradali', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '290', '2149', '10', 'I container e le casse mobili non hanno dimensioni standard e le caratteristiche costruttive sono libere', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '290', '2150', '11', 'I container e le casse mobili hanno dimensioni e caratteristiche stabilite dalle singole aziende di trasporto', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '291', '', '', 'Pallet - 01', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '291', '2151', '1', 'I pallet devono poter sopportare il peso delle merci eventualmente stipate sopra di essi', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '291', '2152', '2', 'Il pallet consiste in una piattaforma sulla quale vengono raggruppate le merci', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '291', '2153', '3', 'Il pallet è definito anche pedana o bancale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '291', '2154', '4', 'Il pallet consente rapide operazioni di trasbordo della merce', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '291', '2155', '5', 'Il pallet è una piattaforma di legno o anche di plastica o di acciaio dotata di traverse e appoggi', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '291', '2156', '6', 'I pallet caricati con merci sono ancorabili tramite corde, catene, fasce in nylon e tenditori', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '291', '2157', '7', 'I pallet possono essere movimentati con operazioni di trasbordo meccanizzato tramite i transpallets', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '291', '2158', '8', 'Il pallet è un’attrezzatura adatta alla movimentazione e al trasporto della merce', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '291', '2159', '9', 'Il pallet è una gabbia di ferro avente dimensioni di 120 x 80 x 90 cm', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '291', '2160', '10', 'Il pallet è un carrello elevatore munito di forche per la movimentazione della merce', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '291', '2161', '11', 'Il pallet consiste in bombole ad alta pressione per il trasporto di liquidi', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '291', '2162', '12', 'Il pallet è un’attrezzatura utilizzata per la movimentazione dei container ', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '292', '', '', ' Pallet - 02', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '292', '2163', '1', 'Le merci movimentate con pallet sono raggruppate in modo da essere possibilmente sovrapponibili', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '292', '2164', '2', 'Le merci movimentate con pallet non possono mai essere sovrapposte', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '292', '2165', '3', 'Le merci movimentate con pallet non devono essere fissate con cinghie perché il il loro eventuale movimento potrebbe rompere le cinghie stesse', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '292', '2166', '4', 'Le merci movimentate con pallet non devono essere stabilizzate con cinghie o altri sistemi per consentire maggiore flessibilità della movimentazione', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '293', '', '', 'Trasporto di fusti, bombole,  bidoni e mistri', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '293', '2167', '1', 'Nel trasporto di fusti, bombole o bidoni è necessario utilizzare accorgimenti per evitarne il rotolamento sul piano di carico del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '293', '2168', '2', 'Nel trasporto di fusti, bombole o bidoni, se non si dispone di veicoli appositamente attrezzati, occorre evitare che i contenitori rotolino sul piano di appoggio della carrozzeria', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '293', '2169', '3', 'Nel trasporto di fusti, bombole o bidoni è obbligatorio utilizzare tappetini per aumentare il coefficiente d’attrito tra il piano di carico e il recipiente', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '293', '2170', '4', 'Per motivi di sicurezza, nel trasporto di fusti, bombole o bidoni si debbono utilizzare veicoli attrezzati con sponde montacarichi', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '293', '2171', '5', 'Nel trasporto di fusti, bombole o bidoni è obbligatorio utilizzare teli di copertura per evitare la dispersione del materiale trasportato', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '293', '2172', '6', 'Durante la fase di carico del veicolo, le merci che devono essere consegnate per prime devono essere caricate prima delle altre', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '293', '2173', '7', 'Quando si caricano le merci sul veicolo è indifferente tener conto dell’ordine di consegna dei singoli colli ', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '294', '', '', 'Trasporti combinati', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '294', '2174', '1', 'Per effettuare trasporti combinati si utilizzano unità di carico', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '294', '2175', '2', 'Per effettuare trasporti combinati si possono utilizzare casse mobili', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '294', '2176', '3', 'Per effettuare trasporti combinati si possono utilizzare container', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '294', '2177', '4', 'Per effettuare trasporti combinati si utilizzano autoveicoli muniti permanentemente di carrozzeria cisterna', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '294', '2178', '5', 'Per effettuare trasporti combinati è obbligatorio utilizzare autoveicoli muniti di gru o sponda montacarichi', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '295', '', '', 'Trasporto con autoarticolati o autotreni', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '295', '2179', '1', 'L’uso di autoarticolati è particolarmente indicato in caso di frazionamento del viaggio grazie all’interscambiabilità del semirimorchio', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '295', '2180', '2', 'Il vantaggio dell’autoarticolato è dato dall’interscambiabilità del semirimorchio', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '295', '2181', '3', 'L’uso di autoarticolati è particolarmente indicato nel caso di trasporto intermodale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '295', '2182', '4', 'L’uso dell’autotreno è particolarmente indicato nel caso di frazionamento del carico', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '295', '2183', '5', 'Tra le carrozzerie permanentemente ancorate alla struttura portante del veicolo vi rientrano le carrozzerie intercambiabili', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '295', '2184', '6', 'È vietato installare, sui veicoli adibiti al trasporto di merci, attrezzature che consentono di caricare, scaricare e trasportare la merce', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '295', '2185', '7', 'È preferibile utilizzare autoarticolati anziché autotreni se le merci trasportate devono essere recapitate a destinatari con sede in un altro Stato', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '295', '2186', '8', 'L’uso dell’autotreno è indicato quando si trasportano imbarcazioni lunghe oltre dodici metri', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '295', '2187', '9', 'L’uso dell’autoarticolato è particolarmente indicato quando si trasporta un’esigua quantità di merce', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '296', '', '', 'Baricentro', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '296', '2188', '1', 'Il baricentro è il punto nel quale, per convenzione, sono da considerarsi applicate le forze agenti sul veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '296', '2189', '2', 'Il baricentro di un veicolo adibito al trasporto di cose con motore anteriore e carrozzeria del tipo a cassone è spostato verso la cabina di guida quando il veicolo è scarico', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '296', '2190', '3', 'L’altezza baricentrica del veicolo scarico è costante', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '296', '2191', '4', 'Lo spostamento del baricentro influenza la stabilità del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '296', '2192', '5', 'La posizione del baricentro varia in funzione sia del carico che del suo posizionamento sul veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '296', '2193', '6', 'Il posizionamento del carico del veicolo influenza la posizione del baricentro', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '296', '2194', '7', 'La posizione del baricentro di un veicolo varia con la sua anzianità', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '296', '2195', '8', 'La differente posizione del carico non modifica il baricentro del veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '296', '2196', '9', 'Il baricentro di un veicolo adibito al trasporto di cose con motore anteriore e carrozzeria del tipo a cassone è spostato verso la parte anteriore quando il veicolo è carico', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '296', '2197', '10', 'Il baricentro di un veicolo è ubicato al centro dell’asse delle ruote motrici', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '296', '2198', '11', 'Il baricentro varia a seconda che il veicolo percorra un rettilineo o una curva', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '297', '', '', 'Forza peso', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '297', '2199', '1', 'La forza peso di un veicolo cambia se il veicolo è carico o scarico', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '297', '2200', '2', 'La forza peso di un veicolo è proporzionale alla sua massa', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '297', '2201', '3', 'La forza peso non dipende dall’inclinazione del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '297', '2202', '4', 'La forza peso di un veicolo è uguale al prodotto della sua massa per l’accelerazione di gravità', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '297', '2203', '5', 'La forza peso dipende dal fatto che il corpo posto sulla superficie terrestre è attratto dalla Terra', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '297', '2204', '6', 'La forza peso di un veicolo dipende dall’inclinazione della strada', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '297', '2205', '7', 'La forza peso è inversamente proporzionale alla massa del veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '297', '2206', '8', 'La forza peso di un veicolo è uguale alla somma della sua massa e dell’accelerazione di gravità', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '297', '2207', '9', 'La forza peso è proporzionale al numero di assi del veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '297', '2208', '10', 'La forza peso di un veicolo non varia al variare del carico trasportato', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '298', '', '', 'Forza motrice', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '298', '2209', '1', 'La forza motrice, grazie agli organi di trasmissione, consente il moto del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '298', '2210', '2', 'La forza motrice viene trasmessa a terra grazie all’aderenza tra le ruote motrici e l’asfalto', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '298', '2211', '3', 'La forza motrice viene in parte assorbita dai componenti meccanici dei veicoli', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '298', '2212', '4', 'La forza motrice è la forza propulsiva generata dal motore e trasmessa alle ruote', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '298', '2213', '5', 'La forza motrice è dovuta alla forza propulsiva generata dal motore endotermico-elettrico', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '298', '2214', '6', 'La forza motrice è la forza sterzante generata dal motore endotermico o elettrico', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '298', '2215', '7', 'La forza motrice mantiene il veicolo in moto grazie agli organi di direzione', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '298', '2216', '8', 'La forza motrice è la forza di gravità', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '298', '2217', '9', 'La forza motrice viene assorbita completamente dal motore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '298', '2218', '10', 'La forza motrice è la forza centrifuga generata dal motore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '299', '', '', 'Forza di inerzia', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '299', '2219', '1', 'La forza di inerzia è minore per un veicolo più leggero', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '299', '2220', '2', 'La forza di inerzia si oppone al rallentamento del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '299', '2221', '3', 'La forza di inerzia si oppone alla variazione di velocità del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '299', '2222', '4', 'La forza di inerzia agisce su un veicolo ogni volta che intervengano variazioni al suo movimento', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '299', '2223', '5', 'La forza di inerzia aumenta con la massa del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '299', '2224', '6', 'La forza di inerzia varia con il mutare del coefficiente dell’aderenza del fondo stradale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '299', '2225', '7', 'La forza di inerzia non varia al variare della velocità del veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '299', '2226', '8', 'La forza di inerzia non dipende dalla massa del veicolo ma solo dalla velocità', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '299', '2227', '9', 'La forza di inerzia agevola la frenatura del veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '299', '2228', '10', 'La forza di inerzia è uguale per i veicoli che marciano alla stessa velocità', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '299', '2229', '11', 'La forza di inerzia permette una migliore tenuta di strada del veicolo in curva', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '300', '', '', ' Energia cinetica - Forze resistenti', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '300', '2230', '1', 'Un veicolo in movimento è dotato di energia cinetica tanto maggiore quanto maggiore è la sua velocità', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '300', '2231', '2', 'Un veicolo in movimento è dotato di energia cinetica proporzionale alla sua massa', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '300', '2232', '3', 'In fase di frenatura l’energia cinetica viene dissipata in calore attraverso il sistema frenante', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '300', '2233', '4', 'La frenatura del veicolo comporta la dissipazione di energia cinetica posseduta dal veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '300', '2234', '5', 'Le forze resistenti che agiscono su un veicolo sono quelle che si oppongono al suo avanzamento', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '300', '2235', '6', 'L’energia cinetica influisce sullo spazio di frenata del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '300', '2236', '7', 'L’energia cinetica aumenta nella fase di frenatura del veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '300', '2237', '8', 'L’energia cinetica è proporzionale al coefficiente di resistenza aerodinamica del veicolo (CX)', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '300', '2238', '9', 'L’energia cinetica aumenta con l’aumentare dei tempi di reazione del conducente', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '300', '2239', '10', 'All’aumentare dell’energia cinetica diminuisce lo spazio di arresto del veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '300', '2240', '11', 'La presenza dell’ABS (dispositivo antibloccaggio del sistema frenante) compensa cattivi tempi di reazione', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '301', '', '', 'Forza di attrito', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '301', '2241', '1', 'La forza di attrito è una forza che si genera quando due superficie a contatto strisciano tra loro', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '301', '2242', '2', 'La forza di attrito contrasta lo scorrimento di due superfici a contatto', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '301', '2243', '3', 'La forza di attrito è una forza dissipativa', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '301', '2244', '4', 'La forza di attrito genera calore', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '301', '2245', '5', 'La forza di attrito che si sviluppa tra pneumatici e asfalto favorisce la diminuzione dei consumi', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '301', '2246', '6', 'La forza di attrito si oppone alla forza frenante del veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '301', '2247', '7', 'La forza di attrito aumenta il rendimento del veicolo in quanto non è una forza dissipativa', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '301', '2248', '8', 'La forza di attrito tende a mantenere il veicolo in movimento costante', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '301', '2249', '9', 'La forza di attrito comporta l’abbassamento del baricentro del veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '302', '', '', 'Coefficiente di aderenza', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '302', '2250', '1', 'Il coefficiente di aderenza varia fra zero e uno', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '302', '2251', '2', 'Il coefficiente di aderenza fra pneumatico e asfalto aumenta se l’asfalto è rugoso', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '302', '2252', '3', 'Il coefficiente di aderenza fra pneumatico e asfalto è basso se l’asfalto è ghiacciato', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '302', '2253', '4', 'Il coefficiente di aderenza tra pneumatico e sede stradale è più alto sulla ghiaia che sull’asfalto', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '302', '2254', '5', 'Il coefficiente di aderenza varia fra uno e due', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '302', '2255', '6', 'Il coefficiente di aderenza aumenta se la superficie di contatto tra due corpi è bagnata', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '303', '', '', 'Forza centripeta', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '303', '2256', '1', 'La forza centripeta opponendosi alla forza centrifuga consente di percorrere una traiettoria curvilinea', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '303', '2257', '2', 'La forza centripeta interviene quando un veicolo percorre una curva', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '303', '2258', '3', 'La forza centripeta dipende dall’aderenza dei pneumatici al suolo stradale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '303', '2259', '4', 'La forza centripeta è proporzionale alla velocità e alla massa del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '303', '2260', '5', 'La forza centripeta dipende dall’aderenza dei pneumatici', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '303', '2261', '6', 'La forza centripeta è la forza che permette ad un corpo di percorrere una traiettoria circolare', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '303', '2262', '7', 'La forza centripeta è applicata al veicolo quando percorre una curva', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '303', '2263', '8', 'La forza centripeta è inversamente proporzionale alla massa del veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '303', '2264', '9', 'La forza centripeta interviene durante la discesa del veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '303', '2265', '10', 'La forza centripeta è generata dagli organi di trasmissione', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '303', '2266', '11', 'La forza centripeta interviene durante la marcia in salita del veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '303', '2267', '12', 'La forza centripeta è generata dalla centralina elettrica del veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '304', '', '', 'Forza centrifuga', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '304', '2268', '1', 'La forza centrifuga tende a proiettare un corpo verso l’esterno della curva', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '304', '2269', '2', 'La forza centrifuga è maggiore quanto maggiori sono la velocità e la massa del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '304', '2270', '3', 'La forza centrifuga si modifica variando la traiettoria del veicolo in curva', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '304', '2271', '4', 'La forza centrifuga è inversamente proporzionale al raggio della curva', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '304', '2272', '5', 'La forza centrifuga si modifica variando la velocità del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '304', '2273', '6', 'La forza centrifuga può fare sbandare un veicolo in curva', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '304', '2274', '7', 'La forza centrifuga può far ribaltare un veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '304', '2275', '8', 'La forza centrifuga deve essere bilanciata dalla forza centripeta', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '304', '2276', '9', 'La forza centrifuga agisce su un corpo quando percorre una curva', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '304', '2277', '10', 'La forza centrifuga tende a portare il veicolo verso l’interno della curva', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '304', '2278', '11', 'La forza centrifuga è direttamente proporzionale al raggio della curva', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '304', '2279', '12', 'La forza centrifuga agisce su un veicolo quando percorre una salita', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '304', '2280', '13', 'La forza centrifuga è massima quando il veicolo percorre una salita', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '304', '2281', '14', 'La forza centrifuga può essere bilanciata dalla forza frenante', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '304', '2282', '15', 'La forza centrifuga è maggiore in rettilineo che non in curva', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '304', '2283', '16', 'La forza centrifuga non dipende dal raggio della curva che percorre il veicolo ', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '305', '', '', 'Rischi di ribaltamento', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '305', '2284', '1', 'Il rischio di ribaltamento del veicolo in curva aumenta quando la curva è più stretta', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '305', '2285', '2', 'Il rischio di ribaltamento del veicolo in curva aumenta quando il baricentro è più alto', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '305', '2286', '3', 'Il rischio di ribaltamento del veicolo in curva aumenta quando aumenta la forza centrifuga', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '305', '2287', '4', 'Il rischio di ribaltamento del veicolo in curva aumenta quando aumenta la velocità', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '305', '2288', '5', 'Il rischio di ribaltamento del veicolo in curva aumenta quando aumenta la forza peso', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '305', '2289', '6', 'Il rischio di ribaltamento del veicolo in curva aumenta se aumenta la carreggiata del veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '305', '2290', '7', 'La mancata revisione periodica del veicolo provoca il suo ribaltamento in curva', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '305', '2291', '8', 'Entrando in una curva stretta (piccolo raggio di curvatura) è necessario inserire una marcia più alta', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '306', '', '', 'Veicoli ad uso speciale - Mezzi d’opera', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '306', '2292', '1', 'I veicoli ad uso speciale sono esenti dalla disciplina dell’autotrasporto merci e consentono il trasporto dei materiali connessi col ciclo operativo delle attrezzature', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '306', '2293', '2', 'I veicoli ad uso speciale sono esenti dalla disciplina dell’autotrasporto merci', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '306', '2294', '3', 'I mezzi d’opera in dotazione alle Regioni per il soddisfacimento delle esigenze interne dell’Ente, sono esenti dalla disciplina dell’autotrasporto di merci', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '306', '2295', '4', 'I mezzi d’opera non sono esenti dalla disciplina dell’autotrasporto di merci', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '306', '2296', '5', 'I mezzi d’opera non muniti di targa civile, sono esentati dalla disciplina dell’autotrasporto di merci', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '306', '2297', '6', 'I mezzi d’opera sono esenti dalla disciplina dell’autotrasporto di merci se eccedenti le masse legali', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '306', '2298', '7', 'I mezzi d’opera sono immatricolabili soltanto in conto proprio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '306', '2299', '8', 'Tutti i mezzi d’opera sono esenti dalla disciplina dell’autotrasporto di merci', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '306', '2300', '9', 'I veicoli ad uso speciale sono soggetti dalla disciplina dell’autotrasporto merci', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '307', '', '', 'Veicoli ad uso speciale', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '307', '2301', '1', 'Gli autoveicoli per uso speciale sono sempre esenti dalla disciplina dell’autotrasporto di merci', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '307', '2302', '2', 'Gli autoveicoli ad uso speciale sono esenti dalla disciplina dell’autotrasporto di cose in conto proprio', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '307', '2303', '3', ' Gli autoveicoli per uso speciale adibiti a soccorso stradale sono esenti dalla disciplina dell’autotrasporto di merci', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '307', '2304', '4', 'Gli autoveicoli di proprietà dello Stato, Regioni, Province e Comuni, ecc., destinati esclusivamente al trasporto di cose necessarie al soddisfacimento delle esigenze dell’Ente, sono esenti dalla disciplina dell’autotrasporto di cose in conto proprio', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '307', '2305', '5', 'Gli autoveicoli adibiti al carico, compattazione trasporto e scarico di rifiuti solidi urbani, sono esenti dalla disciplina dell’autotrasporto di merci perché ad uso speciale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '307', '2306', '6', 'Gli autoveicoli adibiti al trasporto di cose di massa complessiva pari a 7,5 t sono esenti dalla disciplina dell’autotrasporto di cose in conto proprio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '307', '2307', '7', 'Gli autoveicoli adibiti al trasporto di cose prodotte in uno Stato estero sono esenti dalla disciplina dell’autotrasporto di cose in conto proprio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '307', '2308', '8', 'Gli autoveicoli adibiti al trasporto di cose aventi portata complessiva superiore a 6 t, sono esenti dalla disciplina dell’autotrasporto di cose in conto proprio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '308', '', '', 'Esenzioni dalla disciplina dell’autotrasporto merci', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '308', '2309', '1', 'Gli autocarri aventi massa complessiva non superiore a 6 t adibiti ad uso proprio sono esenti dalla disciplina dell’autotrasporto merci', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '308', '2310', '2', 'Gli autocarri aventi tara di 6 t sono esenti dalla disciplina dell’autotrasporto merci', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '309', '', '', 'Trasporto cose in conto proprio - 01', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '309', '2311', '1', 'Il trasporto di cose in conto proprio è quello eseguito con veicoli immatricolati a nome di persone fisiche, giuridiche o enti per esigenze proprie', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '309', '2312', '2', 'Il trasporto di cose in conto proprio costituisce attività complementare rispetto a quella economicamente prevalente, effettuata dall’impresa intestataria dei veicoli', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '309', '2313', '3', 'Il trasporto di cose in conto proprio è quello eseguito da imprese per le quali l’attività di trasporto è un’attività accessoria a quella economicamente prevalente', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '309', '2314', '4', 'Il trasporto di cose in conto proprio è quello eseguito per merci prodotte e/o trasformate dalla stessa impresa che effettua il trasporto', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '309', '2315', '5', 'Il trasporto di cose in conto proprio può essere effettuato con un veicolo di proprietà o in leasing, o in usufrutto, o in patto di riservato dominio', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '309', '2316', '6', 'Il trasporto di cose in conto proprio è eseguito soltanto con veicoli immatricolati a nome di una persona fisica', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '309', '2317', '7', 'Il trasporto di cose in conto proprio è eseguito esclusivamente con veicoli immatricolati a nome di imprese artigiane o agricole', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '309', '2318', '8', 'Chiunque guidi un autocarro immatricolato in uso proprio deve essere titolare di carta di qualificazione del conducente', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '310', '', '', 'Trasporto cose in conto proprio - 02', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '310', '2319', '1', 'Per effettuare il trasporto di cose in conto proprio le cose trasportate devono avere stretta attinenza con l’attività', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '310', '2320', '2', 'Per effettuare il trasporto di cose in conto proprio il veicolo deve avere una massa superiore alle 3,5 t', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '310', '2321', '3', 'Un veicolo è adibito al trasporto in conto proprio quando l’imprenditore si obbliga, dietro corrispettivo, a prestare i servizi di trasporto ordinati dal mittente', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '310', '2322', '4', 'Il conducente di un veicolo adibito a trasporto in conto proprio non deve dimostrare il rapporto che lo lega all’impresa proprietaria del veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '310', '2323', '5', 'Nel trasporto di cose in conto proprio, le merci da trasportare non devono avere attinenza merceologica con l’attività principale dell’impresa proprietaria del veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '311', '', '', 'Guida veicolo per trasporto cose in conto proprio ', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '311', '2324', '1', 'La guida di un veicolo adibito al trasporto di cose in conto proprio è consentita ad un socio della società di persone intestataria della licenza', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '311', '2325', '2', 'La guida di un veicolo adibito al trasporto di cose in conto proprio è consentita al titolare della licenza se trattasi di impresa individuale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '311', '2326', '3', 'La guida di un veicolo adibito al trasporto di cose in conto proprio è consentita al dipendente dell’azienda titolare della licenza', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '311', '2327', '4', 'La guida di un veicolo adibito al trasporto di cose in conto proprio è consentita ad un collaboratore esterno all’impresa', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '311', '2328', '5', 'La guida di un veicolo adibito al trasporto di cose in conto proprio è consentita anche ad un parente del titolare della licenza', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '311', '2329', '6', 'La guida di un veicolo adibito al trasporto di cose in conto proprio è consentita a qualsiasi persona purché munita di patente di categoria C', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '311', '2330', '7', 'La guida di un veicolo adibito al trasporto di cose in conto proprio è consentita esclusivamente al titolare dell’impresa', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '312', '', '', 'Licenza trasporto cose in conto proprio - 01', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '312', '2331', '1', 'Con un veicolo munito della licenza in conto proprio possono essere trasportate soltanto cose attinenti all’attività del titolare', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '312', '2332', '2', 'Con un veicolo munito della licenza in conto proprio possono essere trasportate cose connesse al ciclo produttivo dell’impresa', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '312', '2333', '3', 'Con veicolo munito della licenza in conto proprio possono essere effettuati trasporti purché alla guida vi sia un titolare o da un suo dipendente', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '312', '2334', '4', 'Con un veicolo munito della licenza in conto proprio possono essere trasportate cose prodotte dalla stessa impresa intestataria della licenza, anche se destinate ad un terzo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '312', '2335', '5', 'Con un veicolo munito della licenza in conto proprio possono essere trasportate solo le cose elencate nella licenza', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '312', '2336', '6', 'Con un veicolo munito della licenza in conto proprio possono essere trasportate esclusivamente cose di proprietà del conducente', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '313', '', '', 'licenza di trasporto in conto proprio - 02', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '313', '2337', '1', 'Non è ammesso il rilascio di licenza per trasporto di cose in conto proprio se l’attività di trasporto sia economicamente prevalente nell’impresa', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '313', '2338', '2', 'Non è ammesso il rilascio di licenza per trasporto di cose in conto proprio se il trasporto avvenga con veicoli noleggiati', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '313', '2339', '3', 'Non è ammesso il rilascio di licenza per trasporto di cose in conto proprio se i veicoli vengano condotti da persona estranea all’impresa', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '313', '2340', '4', 'Non è ammesso il rilascio di licenza per trasporto di cose in conto proprio se le merci sono state trasformate dall’impresa che richiede la licenza', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '313', '2341', '5', 'Non è ammesso il rilascio di licenza per trasporto di cose in conto proprio se il trasporto avvenga con i mezzi di proprietà dell’impresa', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '314', '', '', 'licenza di trasporto in conto proprio - 03', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '314', '2342', '1', 'La licenza per il trasporto in conto proprio contiene la limitazione delle merci che possono essere trasportate', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '314', '2343', '2', 'La licenza per il trasporto in conto proprio è prevista solo per veicoli aventi una massa complessiva superiore a 6 t', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '314', '2344', '3', 'La licenza di trasporto di cose in conto proprio è rilasciata dalla Provincia', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '314', '2345', '4', 'La licenza di trasporto di cose in conto proprio è rilasciata dalla Camera di commercio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '314', '2346', '5', 'La licenza di trasporto di cose in conto proprio è rilasciata dall’Ufficio Motorizzazione civile', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '314', '2347', '6', 'La licenza di trasporto di cose in conto proprio è rilasciata dalla Prefettura', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '314', '2348', '7', 'La licenza di trasporto di cose in conto proprio è rilasciata dal Comitato provinciale Albo autotrasportatori', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '314', '2349', '8', 'La licenza per il trasporto in conto proprio non riporta l’attività svolta dall’intestatario', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '314', '2350', '9', 'La licenza per il trasporto in conto proprio non contiene nessuna limitazione relativamente alle cose trasportate', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '314', '2351', '10', 'La licenza per il trasporto in conto proprio non deve accompagnare la carta di circolazione', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '315', '', '', 'Licenza di trasporto in conto proprio - 04', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '315', '2352', '1', 'Per effettuare trasporto di cose in conto proprio bisogna essere titolari di una licenza rilasciata dalla Provincia', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '315', '2353', '2', 'Per effettuare il trasporto di cose in conto proprio bisogna essere titolari di una licenza per ciascun veicolo di massa complessiva superiore a 6 t', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '315', '2354', '3', 'Per effettuare il trasporto di cose in conto proprio su veicoli di massa complessiva a pieno carico inferiore a 6 t non è necessaria alcuna licenza', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '315', '2355', '4', 'Per l’effettuazione di trasporto di cose in conto proprio, l’impresa deve ottenere, per ciascun autoveicolo di massa complessiva superiore a 6 t, una licenza', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '315', '2356', '5', 'Per effettuare il trasporto di cose in conto proprio bisogna iscriversi all’Albo degli autotrasportatori', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '315', '2357', '6', 'Per effettuare il trasporto di cose in conto proprio il veicolo deve essere contraddistinto da striscia diagonale di colore bianco sulla parte posteriore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '315', '2358', '7', 'Per effettuare il trasporto di cose in conto proprio con un veicolo di massa complessiva a pieno carico pari a 10 t, non serve nessuna licenza particolare', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '316', '', '', 'Licenza di trasporto in conto proprio - 05', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '316', '2359', '1', 'Quando la licenza per trasporto merci in conto proprio è rilasciata per autoveicoli aventi portata utile superiore a 3 t, per ciascun trasporto deve essere emesso il documento delle merci trasportate in conto proprio, contenente l’elencazione delle cose trasportate e la dichiarazione che esse sono nella disponibilità del titolare della licenza', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '316', '2360', '2', 'Quando la licenza per trasporto merci in conto proprio è rilasciata per autoveicoli aventi portata utile superiore a 3 t per ciascun trasporto deve essere emesso il documento di trasporto occasionale qualora le merci, pur appartenenti all’intestatario o nella sua disponibilità, non rientrino fra quelle elencate in licenza ma pur sempre attinenti all’attività da lui svolta', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '316', '2361', '3', 'Quando la licenza per trasporto merci in conto proprio è rilasciata per autoveicoli aventi portata utile superiore a 3 t, per ciascun trasporto deve essere in ogni caso emessa la lettera di vettura', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '316', '2362', '4', 'Quando la licenza per trasporto merci in conto proprio è rilasciata per autoveicoli aventi portata utile superiore a 3 t per ciascun trasporto deve essere emessa la bolla di accompagnamento per tutte le merci ', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '317', '', '', 'Licenza di trasporto in conto proprio - 06', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '317', '2363', '1', 'A bordo di un autocarro avente massa complessiva pari a 7,5 t immatricolato in uso proprio deve trovarsi la licenza per il trasporto in conto proprio', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '317', '2364', '2', 'A bordo di un autocarro avente massa complessiva pari a 26 t immatricolato in uso proprio devono trovarsi i documenti di trasporto in relazione alla tipologia di merce trasportata', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '317', '2365', '3', 'A bordo di un autocarro avente massa complessiva pari a 26 t immatricolato in uso proprio deve trovarsi la licenza conto proprio ove prevista', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '317', '2366', '4', 'A bordo di un autocarro avente massa complessiva pari a 26 t immatricolato in uso proprio deve trovarsi l’autorizzazione conto terzi', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '317', '2367', '5', 'A bordo di un autocarro avente massa complessiva pari a 26 t immatricolato in uso proprio deve trovarsi il contratto d’acquisto del veicolo al fine di dimostrarne la proprietà', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '317', '2368', '6', 'A bordo di un autocarro avente massa complessiva pari a 26 t immatricolato in uso proprio deve trovarsi il certificato di iscrizione all’Albo nazionale degli autotrasportatori di cose per conto di terzi', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '318', '', '', 'Trasporto cose in conto terzi', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '318', '2369', '1', 'L’attività di autotrasporto di cose può essere definita per conto di terzi quando il veicolo è utilizzato, dietro corrispettivo, nell’interesse di persone diverse dall’intestatario della carta di circolazione', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '318', '2370', '2', 'L’attività di autotrasporto di cose può essere definita per conto di terzi quando l’imprenditore si obbliga, dietro corrispettivo, a prestare i servizi di trasporto ordinati dal committente', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '318', '2371', '3', 'Si definisce attività di autotrasporto di cose per conto di terzi, l’attività di trasporto svolta imprenditorialmente dietro corrispettivo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '318', '2372', '4', 'Si definisce autotrasporto di cose per conto di terzi, l’attività di trasporto di merci di proprietà dell’intestatario del veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '318', '2373', '5', 'Gli autoveicoli superiori a 6 t di massa complessiva a pieno carico possono esclusivamente svolgere attività di autotrasporto di cose per conto terzi', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '319', '', '', 'Requisiti trasporto cose in conto terzi ', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '319', '2374', '1', 'Per effettuare trasporto di cose per conto di terzi le imprese con autoveicoli di massa complessiva a pieno carico non superiore a 1,5 t devono dimostrare solo il requisito dell’onorabilità', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '319', '2375', '2', 'Per effettuare trasporto di cose per conto di terzi le imprese con autoveicoli di massa complessiva a pieno carico superiore a 1,5 t devono dimostrare di avere l’idoneità finanziaria', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '319', '2376', '3', 'Per effettuare trasporto di cose per conto di terzi le imprese con autoveicoli di massa complessiva a pieno carico superiore a 3,5 t devono dimostrare di possedere i seguenti requisiti: onorabilità, idoneità finanziaria, idoneità professionale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '319', '2377', '4', 'Per effettuare trasporto di cose per conto di terzi con autoveicoli di massa complessiva a pieno carico superiore a 3,5 t, l’unico requisito necessario è l’onorabilità', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '320', '', '', 'Iscrizione all’Albo Nazionale Autotrasporti - 01', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '320', '2378', '1', 'Per effettuare trasporto di cose per conto di terzi l’imprenditore deve iscriversi all’Albo nazionale degli autotrasportatori di cose per conto di terzi', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '320', '2379', '2', 'L’attività di autotrasporto di merci per conto di terzi può prevedere l’obbligo di iscrizione all’Albo degli autotrasportatori', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '320', '2380', '3', 'Per effettuare trasporto di cose per conto di terzi le imprese debbono iscriversi all’Albo dei noleggiatori', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '320', '2381', '4', 'L’iscrizione all’Albo nazionale degli autotrasportatori consente il rilascio della licenza in conto proprio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '320', '2382', '5', 'I conducenti dipendenti di imprese iscritte all’Albo degli autotrasportatori sono esentati dal rispetto dei tempi di guida e di riposo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '321', '', '', 'Iscrizione all’Albo Nazionale Autotrasporti - 02', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '321', '2383', '1', 'L’iscrizione all’Albo nazionale degli autotrasportatori di cose per conto terzi è prevista per imprese che esercitano l’attività con qualsiasi veicolo adibito ad uso di terzi', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '321', '2384', '2', 'L’iscrizione all’Albo nazionale degli autotrasportatori di cose per conto terzi è prevista per imprese che esercitano l’attività con trattori stradali', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '321', '2385', '3', 'L’iscrizione all’Albo nazionale degli autotrasportatori di cose per conto terzi non è prevista per imprese che esercitanol’attività con autotreni', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '321', '2386', '4', 'L’iscrizione all’Albo nazionale degli autotrasportatori di cose per conto terzi non è prevista per imprese che esercitano l’attività con mezzi d’opera', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '322', '', '', 'Iscrizione all’Albo Nazionale Autotrasporti - 03', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '322', '2387', '1', 'L’iscrizione all’Albo nazionale degli autotrasportatori serve a tutte le imprese che intendono esercitare l’attività del trasporto su strada di cose per conto terzi', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '322', '2388', '2', 'L’iscrizione all’Albo nazionale degli autotrasportatori serve al rilascio della carta di circolazione ad uso terzi', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '322', '2389', '3', 'L’iscrizione all’Albo nazionale degli autotrasportatori serve per effettuare servizio di trasporto per conto di terzi senza incorrere in un trasporto abusivo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '322', '2390', '4', 'L’iscrizione all’Albo nazionale degli autotrasportatori serve per poter essere esentati dal richiedere la carta di circolazione', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '322', '2391', '5', 'L’iscrizione all’Albo nazionale degli autotrasportatori serve solo alle imprese con autoveicoli di massa complessiva a pieno carico superiore a 1,5 t, in quanto le imprese con autoveicoli di massa inferiore sono esentate', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '322', '2392', '6', 'L’iscrizione all’Albo nazionale degli autotrasportatori serve a tutte le imprese che intendono esercitare l’attività del trasporto su strada di cose ad uso proprio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '322', '2393', '7', 'L’iscrizione all’Albo nazionale degli autotrasportatori serve solo alle imprese con autoveicoli di massa complessiva a pieno carico inferiore a 1,5 t, in quanto le imprese con autoveicoli di massa superiore sono esentate', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '323', '', '', 'Trasporto abusivo - 01', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '323', '2394', '1', 'Si configura un trasporto abusivo quando si dispone l’esecuzione di un trasporto con un veicolo non rientrante nei limiti stabiliti nella licenza o nell’autorizzazione', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '323', '2395', '2', 'Si configura un trasporto abusivo quando si violano le condizioni relative all’iscrizione all’Albo degli autotrasportatori', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '323', '2396', '3', 'Si configura l’esercizio abusivo dell’autotrasporto quando si effettua il trasporto senza iscrizione all’Albo nazionale degli autotrasportatori di cose per conto di terzi', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '323', '2397', '4', 'Si configura un trasporto abusivo quando si circola in sovraccarico, si circola nei giorni vietati, non si applicano tariffe obbligatorie', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '323', '2398', '5', 'Si configura un trasporto abusivo quando si trasportano clandestini', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '323', '2399', '6', 'Si configura un trasporto abusivo quando si effettua il trasporto con personale non in regola o senza aver versato il contributo all’Albo nazionale degli autotrasportatori di cose per conto di terzi', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '324', '', '', 'Trasporto abusivo - 02', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '324', '2400', '1', 'L’esecuzione di un trasporto di cose senza che la ditta sia iscritta all’Albo degli autotrasportatori, oppure violandone le condizioni o i limiti di iscrizione è punita con una sanzione amministrativa pecuniaria', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '324', '2401', '2', 'L’esercizio abusivo del trasporto di cose senza che la ditta sia iscritta all’Albo degli autotrasportatori è punito con la perdita dell’onorabilità', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '324', '2402', '3', 'L’esecuzione di un trasporto di cose senza che la ditta sia iscritta all’Albo degli autotrasportatori, oppure violandone le condizioni o i limiti di iscrizione è punita con il fermo amministrativo del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '324', '2403', '4', 'L’esecuzione di un trasporto di cose senza che la ditta sia iscritta all’Albo degli autotrasportatori, oppure violandone le condizioni o i limiti di iscrizione è punita con la revoca dell’iscrizione', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '324', '2404', '5', 'L’esecuzione di un trasporto di cose senza che la ditta sia iscritta all’Albo degli autotrasportatori, oppure violandone le condizioni o i limiti di iscrizione è punita con una sanzione disciplinare', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '324', '2405', '6', 'L’esecuzione di un trasporto di cose senza che la ditta sia iscritta all’Albo degli autotrasportatori, oppure violandone le condizioni o i limiti di iscrizione è punita con una sanzione penale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '325', '', '', 'Sanzioni trasporto abusivo - 02', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '325', '2406', '1', 'Le sanzioni previste per l’esercizio abusivo dell’autotrasporto si applicano a tutti i protagonisti della filiera dell’autotrasporto', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '325', '2407', '2', 'Le sanzioni previste per l’esercizio abusivo dell’autotrasporto si applicano al committente, al caricatore, al proprietario della merce, oltre che al vettore', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '325', '2408', '3', 'Le sanzioni previste per l’esercizio abusivo dell’autotrasporto si applicano solo al vettore e al caricatore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '325', '2409', '4', 'Le sanzioni previste per l’esercizio abusivo dell’autotrasporto si applicano al caricatore e al proprietario della merce', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '325', '2410', '5', 'Le sanzioni previste per l’esercizio abusivo dell’autotrasporto si applicano solo al vettore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '326', '', '', 'Tariffe del trasporto', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '326', '2411', '1', 'Nell’autotrasporto di cose per conto terzi le tariffe del trasporto sono rapportate ai costi sostenuti per l’esecuzione del trasporto stesso', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '326', '2412', '2', 'Nell’autotrasporto di cose per conto terzi, le tariffe del trasporto vengono contrattate liberamente tra le parti contraenti', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '326', '2413', '3', 'Nell’autotrasporto di cose per conto terzi, le tariffe del trasporto sono determinate dagli operatori nel libero mercato', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '326', '2414', '4', 'Nell’autotrasporto di cose per conto terzi, le tariffe del trasporto sono fisse ed obbligatorie', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '326', '2415', '5', 'Il trasporto stradale di merci è soggetto obbligatoriamente al sistema di tariffe a forcella', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '327', '', '', 'Documenti per rasporto cose in conto terzi', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '327', '2416', '1', 'I conducenti di veicoli adibiti al trasporto di merci per conto di terzi, se guidano veicoli di massa a pieno carico superiore a 3,5 t, devono aver conseguito la carta di qualificazione del conducente', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '327', '2417', '2', 'La scheda di trasporto, che accompagnava le cose trasportate per conto terzi in ambito nazionale, è stata soppressa', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '327', '2418', '3', 'A bordo dei veicoli per trasporto di cose per conto di terzi deve trovarsi la carta di circolazione con l’indicazione dell’uso di terzi', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '327', '2419', '4', 'A bordo dei veicoli per trasporto di cose per conto di terzi deve trovarsi la patente di guida del conducente e la carta di qualificazione del conducente', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '327', '2420', '5', 'A bordo dei veicoli per trasporto di cose per conto di terzi deve trovarsi una copia conforme della licenza comunitaria in caso di trasporto di merce da Napoli a Milano', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '327', '2421', '6', 'A bordo dei veicoli per trasporto di cose per conto terzi deve trovarsi copia conforma del documento di controllo-foglio di viaggio.', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '327', '2422', '7', 'A bordo dei veicoli per trasporto di cose per conto di terzi deve in ogni caso trovarsi l’originale della licenza comunitaria', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '327', '2423', '8', 'A bordo dei veicoli per trasporto di cose per conto di terzi deve trovarsi la licenza per il trasporto di cose in conto proprio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '327', '2424', '9', 'A bordo dei veicoli per trasporto di cose per conto di terzi deve sempre trovarsi una copia conforme dell’attestato di conducente', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '328', '', '', 'Contrassegno', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '328', '2425', '1', 'Ogni autoveicolo destinato al trasporto di cose per conto di terzi deve essere contraddistinto da una striscia diagonale di colore bianco sulla parte anteriore', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '328', '2426', '2', ' Ogni autoveicolo destinato al trasporto di cose per conto di terzi deve essere immatricolato come autoveicolo uso terzi', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '328', '2427', '3', 'Ogni autoveicolo destinato al trasporto di cose per conto di terzi deve essere contraddistinto da una striscia diagonale di colore rosso sulla parte anteriore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '328', '2428', '4', 'Gli autoveicoli destinati al trasporto di cose per conto di terzi devono essere dotati di un pannello posteriore con la scritta “conto terzi”', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '329', '', '', 'Contratto di trasporto  - 01', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '329', '2429', '1', 'Il contratto di trasporto su strada di merci prevede di norma un mittente, un vettore e un destinatario', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '329', '2430', '2', 'Il contratto di trasporto merci ha, come elementi essenziali, le cose da trasferire ed il corrispettivo del trasporto', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '329', '2431', '3', 'Il contratto di trasporto merci riguarda solo il vettore che deve trasferire cose del mittente', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '329', '2432', '4', 'Il contratto di trasporto merci ha come unico elemento essenziale il corrispettivo del trasporto', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '329', '2433', '5', 'Il contratto di trasporto merci per il mittente è il trasferimento delle cose da un luogo ad un altro, mentre per il vettore è la riscossione della prestazione', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '329', '2434', '6', 'Il contratto di trasporto su strada di merci coinvolge solo questi 3 soggetti: vettore, spedizioniere e agente', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '330', '', '', 'Contratto di trasporto - 02', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '330', '2435', '1', 'Il contratto di trasporto su strada di merci prevede l’indicazione precisa delle cose da trasferire e del corrispettivo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '330', '2436', '2', 'Il contratto di trasporto su strada è un contratto con cui un soggetto si impegna con un altro soggetto a trasferire persone o cose da un luogo ad un altro in cambio di un corrispettivo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '330', '2437', '3', 'Il contratto di trasporto di cose può essere concluso a favore di terzi', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '330', '2438', '4', 'Il contratto di trasporto di cose su strada è un contratto consensuale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '330', '2439', '5', 'Il contratto di trasporto di cose su strada è un contratto a titolo oneroso', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '330', '2440', '6', 'Il contratto di trasporto di cose prevede per il vettore l’obbligo del trasferimento delle merci da un luogo ad un altro, mentre per il mittente prevede l’obbligo del pagamento del corrispettivo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '330', '2441', '7', 'Il contratto di trasporto di cose su strada può essere concluso anche senza l’indicazione del corrispettivo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '330', '2442', '8', 'Il contratto di trasporto può avere un oggetto non determinato', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '330', '2443', '9', 'Il contratto di trasporto su strada di merci è un contratto con cui uno spedizioniere assume l’obbligo di concludere, in nome proprio e per conto di colui che l’ha incaricato (committente), un contratto di trasporto e di espletare le operazioni accessorie', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '330', '2444', '10', 'Il contratto di trasporto è un contratto con cui un soggetto (mittente) si impegna nei confronti di un altro soggetto (vettore) a trasferire persone o cose da un luogo ad un altro in cambio di un corrispettivo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '331', '', '', 'Contratto di trasporto - 03', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '331', '2445', '1', 'Con il contratto di trasporto, il vettore si obbliga a trasportare cose o persone dietro pagamento di un corrispettivo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '331', '2446', '2', 'Con il contratto di trasporto, il vettore si obbliga a custodire i bagagli in ambienti adatti', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '331', '2447', '3', 'Con il contratto di trasporto, il vettore si obbliga a rilasciare fattura commerciale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '332', '', '', 'Forma del contratto di trasporto', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '332', '2448', '1', 'In linea generale, durante il trasporto rispondono vettore e committente di violazioni che riguardano limiti di sagoma e di massa, quando il contratto è redatto in forma scritta', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '332', '2449', '2', 'Il contratto di trasporto si può anche negoziare e concludere a mezzo PEC (posta elettronica certificata)', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '332', '2450', '3', 'Il contratto di trasporto si può concludere a mezzo PEC (posta elettronica certificata) avvalendosi di uno dei gestori autorizzati', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '332', '2451', '4', 'Nel contratto di trasporto su strada, la forma scritta è consigliabile', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '332', '2452', '5', 'Nel contratto di trasporto su strada, la forma scritta non è obbligatoria', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '332', '2453', '6', 'Nel contratto di trasporto su strada, la forma scritta pregiudica la validità del contratto', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '332', '2454', '7', 'Nel contratto di trasporto su strada, la forma scritta è indispensabile', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '332', '2455', '8', 'Nel contratto di trasporto su strada, la forma scritta non è discrezionale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '332', '2456', '9', 'Nel contratto di trasporto su strada, la forma scritta è vietata', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '332', '2457', '10', 'Nel contratto di trasporto su strada, la forma scritta è rappresentata dal documento di controllo-foglio di viaggio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '333', '', '', 'Contenuti contratto di trasporto', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '333', '2458', '1', 'Il contratto di trasporto su strada di merci deve contenere elementi prestabiliti che rappresentano anche importanti indicazioni per il vettore', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '333', '2459', '2', 'Il contratto di trasporto su strada di merci deve contenere il nome del destinatario', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '333', '2460', '3', 'Il contratto di trasporto su strada di merci deve contenere indicazioni precise in merito alla natura, quantità, peso e numero delle cose da trasportare', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '333', '2461', '4', 'Il contratto di trasporto su strada di merci deve contenere eventuali estremi necessari per l’esecuzione del trasporto', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '333', '2462', '5', 'Il contratto di trasporto su strada di merci deve contenere il termine di resa', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '333', '2463', '6', 'Il contratto di trasporto su strada di merci deve contenere la Partita IVA del destinatario', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '333', '2464', '7', 'Il contratto di trasporto su strada di merci deve contenere il costo della merce acquistata dal mittente', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '333', '2465', '8', 'Il contratto di trasporto su strada di merci deve contenere il nome dello spedizioniere', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '333', '2466', '9', 'Il contratto di trasporto su strada di merci deve contenere il termine di resa che è formato dalla differenza tra i termini parziali', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '334', '', '', 'Soggetti del contratto di trasporto', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '334', '2467', '1', 'I soggetti del contratto di trasporto di merci sono cinque: mittente, vettore, caricatore, destinatario e proprietario della merce', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '334', '2468', '2', 'I soggetti del contratto di trasporto di merci possono essere solo tre: mittente, destinatario, vettore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '334', '2469', '3', 'I soggetti del contratto di trasporto di merci sono quattro: mittente, destinatario, vettore e autista', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '334', '2470', '4', 'I soggetti del contratto di trasporto di merci possono essere solo quattro: mittente, destinatario, vettore e caricatore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '334', '2471', '5', 'I soggetti del contratto di trasporto di merci possono essere solo tre: mittente, vettore e caricatore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '335', '', '', 'Soggetti giuridici del contratto di trasporto', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '335', '2472', '1', 'I soggetti giuridici coinvolti nel contratto di trasporto su strada comprendono il mittente, cioè colui che spedisce la merce', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '335', '2473', '2', 'I soggetti giuridici coinvolti nel contratto di trasporto su strada comprendono il mittente, che deve preparare la merce per il carico, debitamente imballata ed etichettata', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '335', '2474', '3', 'I soggetti giuridici coinvolti nel contratto di trasporto su strada comprendono il destinatario, che può fare accertare lo stato delle cose trasportate', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '335', '2475', '4', 'I soggetti giuridici coinvolti nel contratto di trasporto su strada comprendono il vettore, che deve accertarsi che il carico sia stato fatto a regola d’arte', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '335', '2476', '5', 'I soggetti giuridici coinvolti nel contratto di trasporto su strada comprendono l’acquirente e il venditore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '335', '2477', '6', 'I soggetti giuridici coinvolti nel contratto di trasporto su strada comprendono solo il mittente e lo spedizioniere', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '335', '2478', '7', 'I soggetti giuridici coinvolti nel contratto di trasporto su strada comprendono il proprietario del deposito della merce', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '336', '', '', 'Le parti del contratto di trasporto', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '336', '2479', '1', 'Tra le parti di un contratto di trasporto di merci vi sono caricatore e destinatario', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '336', '2480', '2', 'Tra le parti di un contratto di trasporto di merci vi sono mittente e destinatario', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '336', '2481', '3', 'Tra le parti di un contratto di trasporto di merci vi sono mittente e vettore', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '336', '2482', '4', 'Tra le parti di un contratto di trasporto di merci vi sono proprietario della merce, caricatore, mittente, vettore e destinatario', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '336', '2483', '5', 'Tra le parti di un contratto di trasporto di merci vi sono proprietario della merce e distributore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '336', '2484', '6', 'Tra le parti di un contratto di trasporto di merci vi sono venditore e acquirente della merce', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '337', '', '', 'Doveri del Mittente ', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '337', '2485', '1', 'Il mittente può decidere di fare effettuare il trasporto verso un destinatario diverso, fino a quando la merce non viene consegnata alla destinazione stabilita inizialmente', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '337', '2486', '2', 'Il mittente può fare effettuare il trasporto verso un destinatario diverso da quello precedentemente indicato sulla lettera di vettura', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '337', '2487', '3', 'Il mittente può fare effettuare il trasporto verso un’altra località', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '337', '2488', '4', 'Il mittente può decidere di cambiare il luogo di destinazione, fino a quando il secondo esemplare della lettera di vettura non viene consegnato al destinatario', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '337', '2489', '5', 'Il mittente può decidere di sospendere il trasporto', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '337', '2490', '6', 'Il mittente può decidere di sospendere il trasporto, anche dopo che il destinatario è entrato in possesso del secondo esemplare della lettera di vettura', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '337', '2491', '7', 'Il mittente può decidere di sospendere il trasporto solo se la destinazione è extra UE', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '337', '2492', '8', 'Il mittente può cambiare la destinazione del trasporto, solo se lo comunica al vettore entro 2 ore dalla presa in carico della merce, indipendentemente dalla durata del viaggio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '337', '2493', '9', 'Il mittente può cambiare la destinazione del trasporto solo se è effettuato in ambito nazionale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '337', '2494', '10', 'Il mittente può decidere di cambiare il destinatario in qualsiasi momento del viaggio, sia all’andata che al ritorno', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '338', '', '', 'Obblighi del mittente', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '338', '2495', '1', 'Qualora per l’esecuzione del trasporto occorrano particolari documenti, il mittente deve rimetterli al vettore all’atto in cui consegna le cose da trasportare', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '338', '2496', '2', ' Qualora per l’esecuzione del trasporto occorrano particolari documenti, il vettore può sostituirli con l’autocertificazione', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '338', '2497', '3', 'Qualora per l’esecuzione del trasporto occorrano particolari documenti, il destinatario deve trasmetterli via fax', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '339', '', '', 'Lettera di vettura', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '339', '2498', '1', 'La lettera di vettura è sottoscritta dal mittente', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '339', '2499', '2', 'La lettera di vettura deve contenere: nome del destinatario, luogo di destinazione, natura, peso, quantità, numero delle cose da trasportare e condizioni convenute per il trasporto', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '339', '2500', '3', 'La lettera di vettura è rilasciata dal mittente su richiesta del vettore', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '339', '2501', '4', 'La lettera di vettura è rilasciata dal vettore su richiesta del mittente', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '339', '2502', '5', 'La lettera di vettura è un documento obbligatorio rilasciato dagli uffici doganali', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '339', '2503', '6', 'La lettera di vettura è un documento obbligatorio rilasciato dagli UMC (uffici motorizzazione civile)', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '339', '2504', '7', 'La lettera di vettura è sottoscritta da un assicuratore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '339', '2505', '8', 'La lettera di vettura è sottoscritta dal vettore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '340', '', '', 'Caricatore', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '340', '2506', '1', 'Il caricatore è l’impresa o la persona giuridica pubblica che consegna la merce al vettore, curando la sua sistemazione sul veicolo adibito alla esecuzione del trasporto', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '340', '2507', '2', 'Il caricatore è l’incaricato a sistemare la merce, ricevuta dal vettore, sugli scaffali o nei pallet', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '340', '2508', '3', 'Il caricatore è l’impresa o la persona giuridica pubblica che riceve la merce dal vettore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '341', '', '', 'Responsabilità caricatore', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '341', '2509', '1', 'Al caricatore può essere attribuita responsabilità civile per i danni procurati a terze persone e cose in conseguenza delle violazioni commesse dal conducente', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '341', '2510', '2', 'Al caricatore può essere attribuita responsabilità civile per i danni negli incidenti stradali', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '341', '2511', '3', 'Il caricatore è responsabile delle violazioni al codice della strada commesse dal conducente quali il superamento dei limiti di sagoma o di massa', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '341', '2512', '4', 'Il caricatore è responsabile delle violazioni al codice della strada commesse dal conducente quali la cattiva sistemazione del carico sul veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '341', '2513', '5', 'Il caricatore è responsabile delle violazioni al codice della strada commesse dal conducente quali l’eccedenza di massa sui veicoli motore e sui rimorchi rispetto a quella riportata nella carta di circolazione', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '341', '2514', '6', 'Il caricatore è responsabile delle violazioni al codice della strada commesse dal conducente quali il superamento dei limiti di velocità e la mancata osservanza dei tempi di guida e di riposo, dovuti alle istruzioni relative al trasporto, fornite allo stesso conducente', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '341', '2515', '7', 'Il caricatore è responsabile delle violazioni al codice della strada commesse dal conducente quali esclusivamente le violazioni inerenti i tempi di guida e di riposo dei conducenti', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '341', '2516', '8', 'Il caricatore è responsabile delle violazioni al codice della strada commesse dal conducente quali esclusivamente le diverse modalità errate di sistemare il carico sul veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '341', '2517', '9', 'Il caricatore è responsabile delle violazioni al codice della strada commesse dal conducente quali esclusivamente il superamento dei limiti di massa consentita sui veicoli motore e sui rimorchi, rispetto a quelli riportati nella carta di circolazione', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '341', '2518', '10', 'Il caricatore è responsabile delle violazioni al codice della strada commesse dal conducente quali quelli esclusivamente dovuti alle istruzioni relative al trasporto, fornite allo stesso conducente, incompatibili con il rispetto dei limiti di velocità e l’osservanza dei tempi di guida e di riposo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '341', '2519', '11', 'Il caricatore è responsabile per il tempo perduto nel caricare la merce con particolare attenzione', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '341', '2520', '12', 'Il caricatore è responsabile per la merce avariata', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '342', '', '', 'Corresponsabilità caricatore', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '342', '2521', '1', 'Il caricatore è corresponsabile, unitamente al mittente, della consegna delle merci al vettore', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '342', '2522', '2', 'Il caricatore è corresponsabile, unitamente al mittente, della eccedenza di massa caricata sui veicoli, rispetto a quella riportata nella carta di circolazione del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '342', '2523', '3', 'Il caricatore è corresponsabile, unitamente al mittente, della cattiva sistemazione del carico sul veicol', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '342', '2524', '4', 'Il caricatore è corresponsabile, unitamente al mittente, della scelta dell’autista', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '342', '2525', '5', 'Il caricatore è corresponsabile, unitamente al mittente, della scelta del vettore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '342', '2526', '6', 'Il caricatore è corresponsabile, unitamente al mittente, della modalità di carico del veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '343', '', '', 'Caricatore - Violazione norme sicurezza', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '343', '2527', '1', 'Se durante la circolazione il veicolo supera la massa limite, il conducente è corresponsabile (obbligato in solido) con il committente se il trasporto è effettuato in suo conto esclusivo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '343', '2528', '2', 'Quando il conducente del veicolo viola le norme di sicurezza della circolazione stradale, è da ritenersi corresponsabile (obbligato in solido) solo il vettore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '343', '2529', '3', 'Quando il conducente del veicolo viola le norme di sicurezza della circolazione stradale, è da ritenersi corresponsabile (obbligato in solido) solo il caricatore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '343', '2530', '4', 'Quando il conducente del veicolo viola le norme di sicurezza della circolazione stradale, sono da ritenersi corresponsabili (obbligati in solido) il vettore, il committente, il caricatore ed il proprietario delle merci trasportate', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '344', '', '', 'Vettore', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '344', '2531', '1', 'I vettori (imprese di trasporto su strada) sono di norma imprese private', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '344', '2532', '2', 'I vettori (imprese di trasporto su strada) sono imprese che possono assumere dimensioni e forme giuridiche diverse', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '344', '2533', '3', 'I vettori (imprese di trasporto su strada) possono essere società per azioni', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '344', '2534', '4', ' I vettori (imprese di trasporto su strada) sono sempre società di capitali', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '344', '2535', '5', 'I vettori (imprese di trasporto su strada) non possono essere società a responsabilità limitata', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '344', '2536', '6', 'Le società di persone non possono effettuare il trasporto di cose per conto terzi', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '344', '2537', '7', 'La forma societaria di un’impresa di autotrasporto non dipende dalla dimensione ma solamente dalla tipologia di trasporto', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '344', '2538', '8', 'Le possibili configurazioni di un’impresa di trasporto non dipendono dalla forma giuridica', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '345', '', '', 'Vettore', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '345', '2539', '1', 'I vettori svolgono attività di autotrasporto dietro corrispettivo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '345', '2540', '2', 'I vettori esercitano attività di autotrasporto per conto di terzi', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '345', '2541', '3', 'I vettori non sono obbligati a disporre di un parco automezzi', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '345', '2542', '4', 'I vettori sono imprese che esercitano attività di autotrasporto in conto proprio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '346', '', '', 'Obblighi del vettore', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '346', '2543', '1', 'Il vettore per i crediti nascenti dal contratto di trasporto, ha il privilegio sulle cose trasportate finché queste rimangono presso di lui', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '346', '2544', '2', 'Il vettore nel momento della presa in carico della merce deve verificare l’esattezza del contenuto della lettera di vettura', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '346', '2545', '3', 'Il vettore nel momento della presa in carico della merce deve verificarne il buono stato', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '346', '2546', '4', 'Il vettore ha l’obbligo di mettere le cose trasportate a disposizione del destinatario nel luogo, nel termine e con le modalità indicati dal contratto', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '346', '2547', '5', 'Il vettore nel momento in cui sulla lettera di vettura non è stato indicato il termine massimo di consegna, non è tenuto a mettere le cose trasportate a disposizione del destinatario', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '346', '2548', '6', 'Il vettore non è tenuto a verificare l’esattezza del contenuto della lettera di vettura', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '346', '2549', '7', 'Il vettore è responsabile anche dopo l’avvenuta consegna della merce al destinatario a patto che questo abbia provveduto a pagargli quanto dovuto', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '346', '2550', '8', 'Il vettore non è tenuto a verificare il buono stato dell’imballaggio nel momento della presa in carico della merce', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '347', '', '', 'Presa in carico', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '347', '2551', '1', 'All’atto del ricevimento della merce, il vettore deve verificare l’esattezza delle indicazioni della lettera di vettura riguardanti la merce', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '347', '2552', '2', 'All’atto del ricevimento della merce, il vettore deve verificare lo stato apparente della merce stessa e del suo imballaggio', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '347', '2553', '3', 'Il vettore nel momento in cui rileva delle differenze con quanto indicato dal mittente, deve annotarle sulla lettera di vettura', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '347', '2554', '4', 'All’atto del ricevimento della merce, il vettore se non può verificare l’esattezza delle indicazioni, deve riportare nella lettera di vettura le sue riserve', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '347', '2555', '5', 'All’atto del ricevimento della merce, il vettore non è obbligato a verificare niente', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '347', '2556', '6', 'All’atto del ricevimento della merce, il vettore non può esprimere riserve', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '347', '2557', '7', 'All’atto del ricevimento della merce, il vettore si deve limitare a pesarla ed etichettarla', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '348', '', '', 'Responsabilità per i danni alle persone o alle merci trasportate', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '348', '2558', '1', 'Nel caso in cui di danni alle persone, al materiale o ad altre merci o si debbano sostenere spese causate dall’imperfezione dell’imballaggio o da un difettoso od inadeguato fissaggio delle merci sul veicolo, la responsabilità è del mittente quando ha effettuato il carico della merce', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '348', '2559', '2', 'Nel caso di danni alle persone, al materiale o ad altre merci o si debbano sostenere spese causate dall’imperfezione dell’imballaggio o da un difettoso od inadeguato fissaggio delle merci sul veicolo, la responsabilità è del mittente quando il vettore ha segnalato sulla lettera di vettura le anomalie riscontrate', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '348', '2560', '3', 'Nel caso di danni alle persone, al materiale o ad altre merci o si debbano sostenere spese causate dall’imperfezione dell’imballaggio o da un difettoso od inadeguato fissaggio delle merci sul veicolo, la responsabilità è del mittente quando le spese o i danni si sono verificati a causa dell’inesattezza o dell’incompletezza delle indicazioni da lui riportate sulla lettera di vettura', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '348', '2561', '4', 'Nel caso di danni alle persone, al materiale o ad altre merci o si debbano sostenere spese causate dall’imperfezione dell’imballaggio o da un difettoso od inadeguato fissaggio delle merci sul veicolo, la responsabilità è del vettore se la lettera di vettura non contiene le sue riserve motivate, in quanto si presume che al momento del ricevimento la merce ed il suo imballaggio si trovassero in buono stato apparente', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '348', '2562', '5', 'Nel caso di danni alle persone, al materiale o ad altre merci o si debbano sostenere spese causate dall’imperfezione dell’imballaggio o da un difettoso od inadeguato fissaggio delle merci sul veicolo, la responsabilità è del vettore quando non ha adottato le precauzioni necessarie nonostante le segnalazioni indicate sulla lettera di vettura', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '348', '2563', '6', 'Nel caso di danni alle persone, al materiale o ad altre merci o si debbano sostenere spese causate dall’imperfezione dell’imballaggio o da un difettoso od inadeguato fissaggio delle merci sul veicolo, la responsabilità è del destinatario se non firma la lettera di vettura al ricevimento della merce', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '348', '2564', '7', 'Nel caso di danni alle persone, al materiale o ad altre merci o si debbano sostenere spese causate dall’imperfezione dell’imballaggio o da un difettoso od inadeguato fissaggio delle merci sul veicolo, la responsabilità è sempre del vettore anche se il carico della merce è stato fatto dal mittente', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '348', '2565', '8', 'Nel caso di danni alle persone, al materiale o ad altre merci o si debbano sostenere spese causate dall’imperfezione dell’imballaggio o da un difettoso od inadeguato fissaggio delle merci sul veicolo, la responsabilità è del vettore quando le spese o i danni si sono verificati a causa dell’inesattezza o dell’incompletezza delle indicazioni riportate sulla lettera di vettura dal mittente', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '348', '2566', '9', 'Nel caso di danni alle persone, al materiale o ad altre merci o si debbano sostenere spese causate dall’imperfezione dell’imballaggio o da un difettoso od inadeguato fissaggio delle merci sul veicolo, la responsabilità è sempre del vettore fino al ritorno in sede, anche con il veicolo vuoto', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '348', '2567', '10', 'Nel caso di danni alle persone, al materiale o ad altre merci o si debbano sostenere spese causate dall’imperfezione dell’imballaggio o da un difettoso od inadeguato fissaggio delle merci sul veicolo, la responsabilità è del vettore anche quando ha segnalato sulla lettera di vettura le anomalie riscontrate', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '349', '', '', 'Problemi nella consegna della merce', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '349', '2568', '1', 'Se il vettore ha problemi a consegnare la merce in quanto il destinatario è irreperibile, se la merce è soggetta a rapido deterioramento, può farla vendere per conto del proprietario', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '349', '2569', '2', 'Se il vettore ha problemi a consegnare la merce perché il destinatario si rifiuta di riceverla, può depositare la merce in un locale di pubblico deposito, in accordo col mittente', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '349', '2570', '3', 'Se il vettore ha problemi a consegnare la merce deve chiedere indicazioni al mittente', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '349', '2571', '4', 'Se il vettore ha problemi a consegnare la merce in tempo, per causa non imputabile a lui, deve comunque provvedere alla custodia della merce', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '349', '2572', '5', 'Se il vettore ha problemi a consegnare la merce perché il destinatario risulta irreperibile, in accordo col mittente, può depositare la merce in un locale di pubblico deposito', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '349', '2573', '6', 'Se il vettore ha problemi a consegnare la merce e la vende per conto del proprietario, anche se le spese risultano superiori ai ricavi, il vettore non ha diritto ad alcun rimborso delle spese sostenute', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '349', '2574', '7', 'Se il vettore ha problemi a consegnare la merce deve chiedere indicazioni al destinatario e non al mittente', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '349', '2575', '8', 'Se il vettore ha problemi a consegnare la merce in tempo, per causa non imputabile a lui, non è tenuto alla custodia della merce', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '349', '2576', '9', 'Se il vettore ha problemi a consegnare la merce può decidere di interrompere il trasporto, nel caso in cui gli impedimenti o i ritardi non siano imputabili a lui', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '350', '', '', 'Contratto di trasporto a “porto affrancato”', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '350', '2577', '1', 'Nel contratto di trasporto a “porto affrancato” il pagamento è effettuato dal mittente', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '350', '2578', '2', 'Il contratto di trasporto a “porto affrancato” prevede che il versamento per il trasporto venga effettuato da chi spedisce la merce', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '350', '2579', '3', 'Il contratto di trasporto a “porto affrancato” prevede che a pagare sia il destinatario', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '350', '2580', '4', 'Nel contratto di trasporto a “porto affrancato” il pagamento è effettuato indifferentemente dal mittente o dal destinatario', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '350', '2581', '5', 'Nel contratto di trasporto a “porto affrancato” il pagamento è effettuato dal vettore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '350', '2582', '6', 'Nel contratto di trasporto a “porto affrancato” il pagamento è effettuato dal destinatario', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '351', '', '', 'Contratto di trasporto a “porto assegnato”', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '351', '2583', '1', 'Nel contratto di trasporto a “porto assegnato” il prezzo è pagato dal destinatario', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '351', '2584', '2', 'Il contratto di trasporto a “porto assegnato” prevede che il versamento per il trasporto venga effettuato da chi riceve la merce', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '351', '2585', '3', 'Il contratto di trasporto a “porto assegnato” prevede l’utilizzo di mezzi navali', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '351', '2586', '4', 'Il contratto di trasporto a “porto assegnato” prevede che a pagare sia il mittente', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '351', '2587', '5', 'Nel contratto di trasporto a “porto assegnato” il prezzo è pagato dal mittente', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '351', '2588', '6', 'Nel contratto di trasporto a “porto assegnato” il prezzo è pagato dal vettore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '351', '2589', '7', 'Nel contratto di trasporto a “porto assegnato” il prezzo è pagato indifferentemente dal mittente o dal destinatario', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '352', '', '', 'Spedizioniere', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '352', '2590', '1', 'Lo spedizioniere per svolgere tale attività, sempre in forma di impresa, deve possedere precisi requisiti morali, professionali e finanziari', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '352', '2591', '2', 'Lo spedizioniere per svolgere tale attività deve presentare una SCIA (segnalazione certificata di inizio attività) alla CCIAA (Camera di commercio, industria, artigianato e agricoltura)', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '352', '2592', '3', 'Lo spedizioniere è un mandatario', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '352', '2593', '4', 'Lo spedizioniere assume l’obbligo di concludere, in nome proprio e per conto del mandante, un contratto di trasporto', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '352', '2594', '5', 'Lo spedizioniere è colui che ha concluso con il mandante un contratto di spedizione', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '352', '2595', '6', 'Lo spedizioniere fa da intermediario tra committente e vettore', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '352', '2596', '7', 'Lo spedizioniere deve iscriversi nell’Elenco interprovinciale degli spedizionieri autorizzati', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '352', '2597', '8', 'Lo spedizioniere deve iscriversi al Ruolo dei conducenti', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '352', '2598', '9', 'Lo spedizioniere deve iscriversi all’albo degli autotrasportatori', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '352', '2599', '10', 'Lo spedizioniere rende conto al mittente del suo operato', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '352', '2600', '11', 'Lo spedizioniere è colui che ha concluso con il mandatario un contratto di spedizione', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '352', '2601', '12', 'Lo spedizioniere non stipula mai un contratto con il committente', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '352', '2602', '13', 'Lo spedizioniere non esige mai corrispettivo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '352', '2603', '14', 'Lo spedizioniere coincide sempre con il vettore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '352', '2604', '15', 'Lo spedizioniere ha gli obblighi e i diritti del vettore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '353', '', '', ' Contratto di agenzia', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '353', '2605', '1', 'Il contratto di agenzia può prevedere che un agente, dietro retribuzione, assuma l’incarico di promuovere, per conto del committente, la conclusione di contratti di trasporto in una determinata zona', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '353', '2606', '2', 'Il contratto di agenzia prevede l’intervento di uno spedizioniere', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '353', '2607', '3', 'Il contratto di deposito può prevedere l’impegno da parte di un soggetto di concludere, in nome proprio e per conto di colui che l’ha incaricato, un contratto di trasporto', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '354', '', '', 'Termini dei diritti del contratto di trasporto', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '354', '2608', '1', 'In materia di contratto di trasporto su strada i diritti si prescrivono in 18 mesi se il trasporto ha inizio fuori UE', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '354', '2609', '2', 'In materia di contratto di trasporto su strada i diritti si prescrivono in 18 mesi se il trasporto ha termine fuori UE', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '354', '2610', '3', 'In materia di contratto di trasporto su strada i diritti si prescrivono in un anno', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '354', '2611', '4', ' Il termine per la prescrizione in materia di contratto di trasporto su strada decorre dal giorno in cui sarebbe dovuta avvenire la riconsegna della merce al luogo di destinazione', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '354', '2612', '5', 'Il termine per la prescrizione in materia di contratto di trasporto su strada decorre dal giorno in cui è avvenuta la riconsegna della merce al luogo di destinazione', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '354', '2613', '6', 'Il termine per la prescrizione in materia di contratto di trasporto decorre dal giorno in cui è stata caricata la merce', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '354', '2614', '7', 'Il termine per la prescrizione in materia di contratto di trasporto decorre dal giorno in cui è stato firmato il contratto', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '354', '2615', '8', 'In materia di contratto di trasporto su strada i diritti si prescrivono il giorno successivo a quello in cui è stata scaricata la merce', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '354', '2616', '9', 'In materia di contratto di trasporto su strada i diritti si prescrivono in 18 mesi se il trasporto si svolge su territorio UE', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '354', '2617', '10', 'In materia di contratto di trasporto su strada i diritti si prescrivono in 5 anni', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '355', '', '', 'Contratto di subtrasporto', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '355', '2618', '1', 'Nel sub-trasporto un soggetto conferisce ad un ulteriore soggetto l’incarico di trasporto con un contratto', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '355', '2619', '2', 'Nel contratto di sub-trasporto il vettore assume nei confronti del mittente l’obbligo dell’esecuzione dell’intero trasporto dal luogo di partenza fino al luogo di destinazione, avvalendosi dell’opera di uno o più subvettori', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '355', '2620', '3', 'Nel contratto di sub-trasporto il vettore assume l’obbligo di stipulare con altre imprese d’autotrasporto autonomi contratti di trasporto', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '355', '2621', '4', 'Nel contratto di sub-trasporto il vettore non assume nessun obbligo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '355', '2622', '5', 'Nel contratto di sub-trasporto il vettore assume nei confronti del mittente l’obbligo di restituire le merci in caso di mancata consegna delle stesse', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '355', '2623', '6', 'Nel contratto di sub-trasporto il vettore assume nei confronti del mittente l’obbligo di assicurare le merci', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '355', '2624', '7', 'Nel sub-trasporto un soggetto conferisce ad un ulteriore soggetto l’incarico di trasporto senza contratto', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '356', '', '', 'Trasporto cumulativo', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '356', '2625', '1', 'Per trasporto cumulativo s’intende il trasporto effettuato da più vettori successivi con un unico contratto', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '356', '2626', '2', 'Per trasporto cumulativo s’intende il trasporto di più partite', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '356', '2627', '3', 'Per trasporto cumulativo s’intende il trasporto effettuato da un unico vettore con più luoghi di scarico', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '356', '2628', '4', 'Per trasporto cumulativo s’intende il trasporto di diverse tipologie di merci', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '357', '', '', 'Trasporto cumulativo', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '357', '2629', '1', 'Il trasporto cumulativo presuppone la stipula di un unico contratto', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '357', '2630', '2', 'Il trasporto cumulativo è effettuato da più vettori successivi', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '357', '2631', '3', 'Il trasporto cumulativo prevede che tutti i vettori coinvolti nel trasporto rispondano sempre in parti proporzionali ai percorsi, anche se è dimostrata la colpa di un singolo vettore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '357', '2632', '4', 'Il trasporto cumulativo, nel caso in cui non si riesca ad identificare il vettore responsabile del fatto dannoso, prevede che a rispondere sia soltanto l’ultimo vettore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '357', '2633', '5', 'Il trasporto cumulativo presuppone la stipula di un contratto per ogni vettore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '358', '', '', 'Responsabilità nel trasporto cumulativo', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '358', '2634', '1', 'Nel trasporto cumulativo, se le merci subiscono danni e non è possibile individuare il vettore che li ha causati, tutti i vettori ne rispondono in parti proporzionali ai rispettivi percorsi', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '358', '2635', '2', 'Nel trasporto cumulativo la responsabilità per i danni diversi da quelli dovuti per ritardo o interruzione del viaggio, spetta ad ogni vettore in ragione solo del proprio percorso', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '358', '2636', '3', 'Nel trasporto cumulativo la responsabilità per i danni diversi da quelli dovuti per ritardo o interruzione del viaggio, spetta a tutti i vettori in solido salva la possibilità di rivalsa contro colui o coloro che hanno causato il danno', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '358', '2637', '4', ' Nel trasporto cumulativo la responsabilità per i danni diversi da quelli dovuti per ritardo o interruzione del viaggio, spetta al singolo vettore in relazione al proprio percorso e di quello che lo segue', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '358', '2638', '5', 'Nel trasporto cumulativo la responsabilità per i danni diversi da quelli dovuti per ritardo o interruzione del viaggio, spetta al singolo vettore in relazione al proprio percorso e di quello che lo precede', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '358', '2639', '6', 'Nel trasporto cumulativo, solamente il primo e l’ultimo vettore rispondono dei danni subiti dalle merci se non è possibile individuare il vettore che li ha causati', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '358', '2640', '7', 'Nel trasporto cumulativo, il vettore che ha compiuto la tratta più lunga risponde dei danni subiti dalle merci se non è possibile individuare il vettore che li ha causati', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '358', '2641', '8', 'Nel trasporto cumulativo, se le merci subiscono danni e non è possibile individuare il vettore che li ha causati, tutti i vettori rispondono in parti uguali', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12A', '358', '2642', '9', 'Nel trasporto cumulativo la responsabilità per i danni diversi da quelli dovuti per ritardo o interruzione del viaggio, spetta ad ogni vettore in ragione dell’intero percorso', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '359', '', '', 'Trasporto internazionale di merci in conto terzi', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '359', '2643', '1', 'Il trasporto di merci per conto di terzi oltre i confini nazionali è da considerarsi trasporto internazionale anche se il veicolo marcia a vuoto', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '359', '2644', '2', 'Il trasporto di merci per conto di terzi oltre i confini nazionali è da considerarsi sempre trasporto internazionale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '359', '2645', '3', 'Il trasporto di merci per conto di terzi oltre i confini nazionali è da considerarsi trasporto combinato', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '359', '2646', '4', 'Il trasporto di merci per conto di terzi oltre i confini nazionali è da considerarsi trasporto in regime di cabotaggio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '359', '2647', '5', 'Il trasporto di merci per conto di terzi oltre i confini nazionali è da considerarsi trasporto internazionale, solo se per il trasporto delle merci in uno degli Stati membri UE è necessario attraversare uno Stato non UE', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '359', '2648', '6', 'Il trasporto di merci per conto di terzi oltre i confini nazionali è da considerarsi trasporto internazionale, solo se la merce deve essere trasportata al di fuori degli Stati membri UE', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '360', '', '', 'Trasporto internazionale di merci in conto terzi in ambito UE', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '360', '2649', '1', 'Un’impresa di autotrasporto, per poter effettuare trasporti conto terzi con veicoli di portata superiore a 3,5 t all’interno UE occorre che sia iscritta all’Albo degli autotrasportatori, già titolare di autorizzazione nazionale ed abilitata per i trasporti internazionali', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '360', '2650', '2', 'Un’impresa di autotrasporto, per poter effettuare trasporti conto terzi con veicoli di portata superiore a 3,5 t all’interno della UE occorre che si doti della licenza comunitaria', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '360', '2651', '3', 'Un’impresa di autotrasporto, per poter effettuare trasporti conto terzi con veicoli di portata superiore a 3,5 tonnellate all’interno della UE occorre che sia iscritta all’Albo degli autotrasportatori, sia già titolare di autorizzazione, ma non è necessario che abbia la licenza comunitaria', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '360', '2652', '4', 'Un’impresa di autotrasporto, per poter effettuare trasporti conto terzi con veicoli di portata superiore a 3,5 t all’interno della UE non deve possedere alcun requisito particolare in quanto il trasporto nell’ambito della UE è libero', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '360', '2653', '5', 'Un’impresa di autotrasporto, per poter effettuare trasporti conto terzi con veicoli di portata superiore a 3,5 t all’interno della UE è sufficiente che sia iscritta all’Albo degli autotrasportatori', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '360', '2654', '6', 'Un’impresa di autotrasporto, per poter effettuare trasporti conto terzi con veicoli di portata superiore a 3,5 t all’interno della UE deve effettuare il trasporto con veicoli di classe d’inquinamento minima EURO 3', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '361', '', '', 'Trasporto internazionale di merci in conto terzi in ambito UE', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '361', '2655', '1', 'Per effettuare il trasporto internazionale di merci su strada per conto di terzi in ambito UE per ogni veicolo con eventuale rimorchio è sufficiente avere a bordo la copia conforme della licenza comunitaria', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '361', '2656', '2', 'Per effettuare il trasporto internazionale di merci su strada per conto di terzi tra uno Stato membro dell’UE e uno Stato terzo, è necessario rifarsi agli accordi bilaterali o multilaterali', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '361', '2657', '3', ' Per effettuare il trasporto internazionale di merci su strada per conto di terzi tra l’Italia e la Russia, per ogni Stato extra UE attraversato, di norma è necessaria un’autorizzazione', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '361', '2658', '4', 'Per effettuare il trasporto internazionale di merci su strada per conto di terzi tra Francia e Spagna è necessario rifarsi agli accordi bilaterali o multilaterali', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '361', '2659', '5', 'Per effettuare il trasporto internazionale di merci su strada per conto di terzi è necessario esclusivamente che i due Stati abbiano firmato un accordo bilaterale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '361', '2660', '6', 'Per effettuare il trasporto internazionale di merci su strada per conto di terzi tra l’Italia e la Romania, è necessario rifarsi all’accordo Interbus', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '362', '', '', 'Trasporto internazionale di merci in conto terzi', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '362', '2661', '1', 'Il trasporto internazionale di cose per conto terzi in ambito UE è liberalizzato per alcune tipologie di trasporto e soggetto solo al possesso della licenza comunitaria per le altre', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '362', '2662', '2', 'Il trasporto internazionale di cose per conto terzi in ambito UE è liberalizzato solo per alcune specifiche tipologie di trasporto e assoggettato ad autorizzazione bilaterale per le altre', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '362', '2663', '3', 'Il trasporto internazionale di cose per conto terzi in ambito UE è totalmente liberalizzato e non occorre alcun documento per esercitarlo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '363', '', '', 'Trasporto in conto terzi in ambito UE, SEE, Svizzera', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '363', '2664', '1', 'A bordo dei veicoli per trasporto merci conto terzi in ambito UE, SEE e Svizzera deve trovarsi la copia certificata conforme della licenza comunitaria', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '363', '2665', '2', 'A bordo dei veicoli per trasporto merci conto terzi in ambito UE, SEE e Svizzera deve trovarsi la licenza comunale per l’esercizio del servizio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '363', '2666', '3', 'A bordo dei veicoli per trasporto merci conto terzi in ambito UE, SEE e Svizzera deve trovarsi facoltativamente il certificato ATP se previsto per la carrozzeria', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '364', '', '', 'Esenzione licenza comunitaria', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '364', '2667', '1', 'Sono esenti dal regime della licenza comunitaria i trasporti effettuati con autoveicoli il cui peso totale a carico autorizzato, compreso quello dei rimorchi, non supera 3,5 t (limite che diventa 2,5 t a decorrere dal 21 maggio 2022)', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '364', '2668', '2', 'Sono liberi da ogni regime di licenza comunitaria i trasporti in ambito UE di merci in conto proprio', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '364', '2669', '3', 'Sono liberi da ogni regime di licenza comunitaria i trasporti in ambito UE di veicoli danneggiati o da riparare', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '364', '2670', '4', 'Sono liberi da ogni regime di licenza comunitaria i trasporti in ambito UE di effetti postali nell’ambito di un regime di pubblico servizio', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '364', '2671', '5', 'Sono esenti dal regime della licenza comunitaria i trasporti postali effettuati nell’ambito di un servizio universale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '364', '2672', '6', 'La licenza comunitaria non è necessaria nel caso di trasporti postali', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '364', '2673', '7', 'La licenza comunitaria non è necessaria nel caso di trasporti di medicinali', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '364', '2674', '8', 'La licenza comunitaria non è necessaria nel caso di trasporti di merci con autoveicoli il cui peso totale a carico autorizzato, compreso quello dei rimorchi, non supera 3,5 tonnellate', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '364', '2675', '9', 'La licenza comunitaria non è necessaria nel caso di trasporti di animali', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '364', '2676', '10', 'Sono liberi da ogni regime di licenza comunitaria i trasporti in ambito UE di merci per conto terzi', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '364', '2677', '11', 'Sono liberi da ogni regime di licenza comunitaria i trasporti in ambito UE, ad eccezione di quelli esenti per massa, per portata o per particolare tipologia', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '365', '', '', 'Licenza comunitari - 01', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '365', '2678', '1', 'Per un trasporto di merci tra Italia e Polonia è necessaria la licenza comunitaria', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '365', '2679', '2', 'Per un trasporto di merci tra Italia e Polonia non serve nessuna autorizzazione', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '365', '2680', '3', 'Per un trasporto di merci tra Italia e Polonia è necessaria un’autorizzazione CEMT', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '365', '2681', '4', 'Per un trasporto di merci tra Italia e Polonia è necessaria un’autorizzazione bilaterale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '366', '', '', 'Licenza comunitaria - 02', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '366', '2682', '1', 'All’interno dell’UE il trasporto cose tra stati membri avviene attraverso la licenza comunitaria', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '366', '2683', '2', 'All’interno dell’UE è garantita la libertà di prestazione del servizio di trasporto attraverso il rilascio della licenza comunitaria', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '366', '2684', '3', 'All’interno dell’UE è garantita la libertà di prestazione del servizio di trasporto attraverso il rilascio della licenza CEMT', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '366', '2685', '4', 'All’interno dell’UE il trasporto cose tra stati membri avviene attraverso la licenza CEMT', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '366', '2686', '5', 'All’interno dell’UE per tutti i conducenti è obbligatorio l’attestato di conducente', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '367', '', '', 'Licenza comunitaria – Esenzioni', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '367', '2687', '1', 'Devono essere in possesso di licenza comunitaria le imprese che intendono effettuare tutti i trasporti infra UE ad eccezione di quelli esenti per massa, per portata o per particolare tipologia', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '367', '2688', '2', 'La licenza comunitaria non è necessaria nel caso di trasporti di veicoli danneggiati o da riparare', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '367', '2689', '3', 'La licenza comunitaria non è necessaria nel caso di traslochi', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '367', '2690', '4', 'La licenza comunitaria è necessaria per il trasporto all’interno dell’UE di merci in conto proprio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '367', '2691', '5', 'La licenza comunitaria è necessaria per il trasporto all’interno dell’UE di medicinali per conto terzi', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '367', '2692', '6', 'La licenza comunitaria è necessaria per il trasporto all’interno dell’UE di effetti postali nell’ambito di un regime di pubblico servizio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '368', '', '', 'Licenza comunitaria - 03', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '368', '2693', '1', 'Devono essere in possesso di licenza comunitaria le imprese che intendono effettuare trasporti internazionali in tutti gli Stati che hanno aderito alla CEMT (Conferenza europea dei Ministri dei trasporti)', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '368', '2694', '2', 'La licenza comunitaria è un documento che permette al titolare di effettuare i trasporti internazionali negli Stati dell’UE', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '368', '2695', '3', 'La licenza comunitaria è valida nei Stati dell’UE, SEE (Spazio economico europeo) e Svizzera', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '368', '2696', '4', 'La licenza comunitaria è il titolo abilitativo necessario per effettuare trasporti in ambito extra UE', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '368', '2697', '5', 'La licenza comunitaria è sufficiente, per un’impresa italiana, per svolgere attività di trasporto su strada per conto terzi in Russia', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '368', '2698', '6', 'La licenza comunitaria è valida negli Stati dell’UE, con eccezione della Spagna', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '369', '', '', 'Licenza comunitaria – Rilascio', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '369', '2699', '1', 'La licenza comunitaria è rinnovabile', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '369', '2700', '2', 'La licenza comunitaria ha una durata di 5 anni', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '369', '2701', '3', 'La licenza comunitaria ha validità di cinque anni', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '369', '2702', '4', 'La licenza comunitaria è rilasciata dalla Direzione generale per la sicurezza stradale e l’autotrasporto del Dipartimento per la mobilità sostenibile del Ministero delle infrastrutture e della mobilità sostenibili', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '369', '2703', '5', 'La licenza comunitaria è rilasciata dalle Province', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '369', '2704', '6', 'La licenza comunitaria deve essere rinnovata solo quando si sostituisce o si aggiunge un veicolo al parco veicolare dell’impresa', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '369', '2705', '7', 'La licenza comunitaria non ha scadenza', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '369', '2706', '8', 'La licenza comunitaria è rilasciata dagli Uffici motorizzazione civile', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '369', '2707', '9', 'La licenza comunitaria ha una durata di 1 anno', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '369', '2708', '10', 'La licenza comunitaria può essere cedibile a terzi', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '369', '2709', '11', ' La licenza comunitaria è rilasciata dalla Camera di Commercio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '369', '2710', '12', 'La licenza comunitaria è rilasciata dalle Regioni', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '370', '', '', 'Licenza comunitaria – Intestatazione', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '370', '2711', '1', 'La licenza comunitaria è intestata ad un solo vettore', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '370', '2712', '2', 'La licenza comunitaria è intestata ad un solo autotrasportatore', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '370', '2713', '3', 'La licenza comunitaria è intestata al singolo autista', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '370', '2714', '4', 'La licenza comunitaria è intestata a più autotrasportatori se trattasi di cooperativa', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '370', '2715', '5', 'La licenza comunitaria è intestata al singolo autocarro con cui si viaggia', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '371', '', '', 'Licenza comunitaria – Veicolo', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '371', '2716', '1', 'La licenza comunitaria se il veicolo a motore ed il rimorchio sono immatricolati in Stati diversi, è necessaria solo per il veicolo a motore', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '371', '2717', '2', 'Per effettuare il trasporto internazionale di merci su strada per conto di terzi con un veicolo a motore e un rimorchio, è necessario essere in possesso della licenza comunitaria per il veicolo a motore e di quella per il rimorchio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '372', '', '', 'Licenza comunitaria - Copia conforma', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '372', '2718', '1', 'Un veicolo adibito al trasporto merci per conto terzi, in ambito UE, non deve essere munito della copia conforme della licenza comunitaria qualora abbia una massa complessiva di 7,5 t', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '372', '2719', '2', 'Un veicolo adibito al trasporto merci per conto terzi, in ambito UE, non deve mai essere munito della copia conforme della licenza comunitaria qualora la portata utile non superi 7,5 t', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '372', '2720', '3', 'Un veicolo adibito al trasporto merci per conto terzi, in ambito UE, non deve mai essere munito della copia conforme della licenza comunitaria qualora la portata utile non superi le 6 t', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '373', '', '', 'Copia conforme della licenza comunitaria - 01', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '373', '2721', '1', 'Per circolare nell’ambito dell’UE, un veicolo che effettua trasporto per conto di terzi deve avere a bordo una copia conforme della licenza comunitaria', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '373', '2722', '2', 'Per circolare nell’ambito dell’UE, un veicolo che effettua trasporto per conto di terzi deve avere a bordo una semplice fotocopia della licenza comunitaria', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '373', '2723', '3', 'Per circolare nell’ambito dell’UE, un veicolo che effettua trasporto per conto di terzi deve avere a bordo la licenza comunitaria in originale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '373', '2724', '4', 'Per circolare nell’ambito dell’UE, un veicolo che effettua trasporto per conto di terzi deve avere a bordo una autorizzazione CEMT', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '373', '2725', '5', 'Per circolare nell’ambito dell’UE, un veicolo che effettua trasporto per conto di terzi deve avere a bordo un certificato che attesti la categoria d’inquinamento del veicolo (EURO 1, 2, 3 ecc.)', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '374', '', '', 'Copia conforme della licenza comunitaria - 02', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '374', '2726', '1', ' L’impresa di autotrasporto deve richiedere copie certificate conformi della licenza comunitaria in numero di una per ogni veicolo che intende utilizzare nei viaggi all’interno dell’UE', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '374', '2727', '2', 'L’impresa di autotrasporto deve richiedere copie certificate conformi della licenza comunitaria in numero di una per ogni autista', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '374', '2728', '3', 'L’impresa di autotrasporto deve richiedere copie certificate conformi della licenza comunitaria in numero di una per ogni autista essendo la stessa intestata a quest’ultimo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '374', '2729', '4', 'L’impresa di autotrasporto deve richiedere copie certificate conformi della licenza comunitaria in numero di una per ogni Stato UE in cui si transita durante il viaggio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '374', '2730', '5', 'L’impresa di autotrasporto deve richiedere copie certificate conformi della licenza comunitaria in numero di una per ogni viaggio all’interno dell’UE', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '375', '', '', 'Copia conforme della licenza comunitaria - 03', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '375', '2731', '1', 'Una copia conforme della licenza comunitaria va tenuta a bordo di ciascun veicolo utilizzato per trasporti internazionali', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '375', '2732', '2', 'La copia certificata conforme della licenza comunitaria è rilasciata anche per veicoli in leasing', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '375', '2733', '3', 'La copia certificata conforme della licenza comunitaria è rilasciata solo per veicoli in proprietà', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '375', '2734', '4', 'La copia certificata conforme della licenza è rilasciata in unico originale per l’impresa', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '375', '2735', '5', 'La copia certificata conforme della licenza comunitaria è rilasciata solo per veicoli a noleggio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '375', '2736', '6', 'La copia certificata conforme della licenza è rilasciata in due originali, uno per l’impresa e uno per il conducente', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '375', '2737', '7', 'La copia certificata conforme della licenza è rilasciata in tanti esemplari quanti sono gli autisti', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '376', '', '', 'Copia conforme della licenza comunitaria - 04', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '376', '2738', '1', 'Le copie della licenza comunitaria vengono rilasciate in numero corrispondente ai veicoli in disponibilità', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '376', '2739', '2', 'Le copie della licenza comunitaria devono essere tenute a bordo dei veicoli in disponibilità dell’impresa', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '376', '2740', '3', 'Le copie della licenza comunitaria devono essere conformi all’originale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '376', '2741', '4', 'Le copie della licenza comunitaria sono rilasciate per i veicoli in disponibilità dell’impresa', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '376', '2742', '5', 'Le copie della licenza comunitaria vengono rilasciate dall’UMC di competenza', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '376', '2743', '6', 'Le copie della licenza comunitaria devono essere conservate presso la sede dell’impresa e non a bordo dei mezzi', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '376', '2744', '7', 'Le copie della licenza comunitaria sono fotocopie non autenticate', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '376', '2745', '8', 'Le copie della licenza comunitaria sono rilasciate per i soli veicoli di proprietà dell’impresa', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '376', '2746', '9', 'Le copie della licenza comunitaria vengono rilasciate in numero corrispondente ai conducenti dipendenti dell’impresa', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '377', '', '', 'Trasporto in regime di cabotaggio - 01', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '377', '2747', '1', 'Nel campo dell’autotrasporto internazionale, il cabotaggio è la possibilità per un trasportatore, appartenente ad uno degli Stati membri, di effettuare trasporti nazionali in altro Stato UE', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '377', '2748', '2', 'Nel campo dell’autotrasporto internazionale, il cabotaggio è l’ammissione di vettori non residenti, ai trasporti nazionali di merci in uno Stato UE diverso da quello in cui il trasportatore è stabilito', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '377', '2749', '3', 'Nel campo dell’autotrasporto internazionale, il cabotaggio è l’ammissione di vettori non residenti, ai trasporti nazionali di merci, in uno Stato facente parte dello Spazio economico europeo (SEE) diverso da quello in cui il trasportatore è stabilito', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '377', '2750', '4', 'Il trasporto in regime di cabotaggio è un servizio che utilizza diverse modalità di trasporto (strada, nave, treno, ecc.) per effettuare il percorso dal luogo di carico a quello di destinazione', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '377', '2751', '5', 'Nel campo dell’autotrasporto internazionale, il cabotaggio è la possibilità che un trasportatore appartenente ad uno degli Stati membri possa attraversare liberamente, durante l’esecuzione di un trasporto, un altro Stato UE', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '377', '2752', '6', 'Nel campo dell’autotrasporto internazionale, il cabotaggio è la possibilità che un trasportatore appartenente ad uno degli Stati membri, possa trasportare merci dal suo ad un altro degli Stati UE', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '377', '2753', '7', 'Nel campo dell’autotrasporto internazionale, il cabotaggio costituisce l’ammissione di vettori residenti ai trasporti nazionali di merci', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '377', '2754', '8', 'Nel campo dell’autotrasporto internazionale, il cabotaggio è ammesso senza alcun condizionamento né regole', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '378', '', '', 'Trasporto in regime di cabotaggio - 02', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '378', '2755', '1', 'Il trasporto in regime di cabotaggio è un trasporto effettuato dal vettore UE all’interno di uno Stato UE diverso da quello in cui è stabilito', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '378', '2756', '2', 'Il trasporto in regime di cabotaggio può essere svolto solo se l’impresa possiede la licenza comunitaria', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '378', '2757', '3', 'Il trasporto in regime di cabotaggio è, ad esempio, quello svolto da un vettore italiano all’interno del territorio della Spagna', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '378', '2758', '4', 'Il trasporto in regime di cabotaggio può essere svolto da un’impresa di autotrasporto italiana su territorio francese a determinate condizioni', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '378', '2759', '5', 'l trasporto in regime di cabotaggio è un servizio che utilizza diverse modalità di trasporto (strada, nave, treno, ecc.) per effettuare il percorso dal luogo di carico a quello di destinazione', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '378', '2760', '6', 'Il trasporto in regime di cabotaggio può essere svolto solo se si compila il libretto dei resoconti dei trasporti di cabotaggio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '378', '2761', '7', 'Il trasporto in regime di cabotaggio non può essere svolto sul territorio UE', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '379', '', '', 'Trasporto in regime di cabotaggio – Limitazioni', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '379', '2762', '1', 'Il cabotaggio per i vettori UE per conto di terzi, stabiliti in Stati membri ammessi al cabotaggio, e del SEE è consentito per tre operazioni nell’arco di sette giorni a determinate condizioni', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '379', '2763', '2', 'L’esercizio del cabotaggio è consentito a titolo temporaneo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '379', '2764', '3', 'L’esercizio del cabotaggio è consentito con l’obbligo di disporre di una sede o di un altro stabilimento nello Stato in cui il trasporto si svolge', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '379', '2765', '4', 'I servizi di cabotaggio, in linea generale, sono consentiti a imprese non abilitate nello Stato membro di stabilimento a effettuare i trasporti esentati da ogni regime licenza o autorizzazione internazionale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '379', '2766', '5', 'I servizi di cabotaggio, in linea generale, sono consentiti a imprese non titolari di licenza comunitaria e proprietarie di attestato del conducente per l’autista, ove previsto', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '379', '2767', '6', 'L’esercizio del cabotaggio è consentito con particolari restrizioni nei trasporti per conto proprio e di medicinali', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '379', '2768', '7', 'Il cabotaggio per i vettori UE per conto di terzi, stabiliti in Stati membri ammessi al cabotaggio, e del SEE è consentito se le operazioni vengono eseguite con veicolo diverso', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '379', '2769', '8', 'Il cabotaggio per i vettori UE per conto di terzi, stabiliti in Stati membri ammessi al cabotaggio, e del SEE è consentito se le operazioni sono successive ad un trasporto nazionale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '379', '2770', '9', 'Il cabotaggio per i vettori UE per conto di terzi, stabiliti in Stati membri ammessi al cabotaggio, e del SEE è consentito se l’ultimo scarico in servizio di cabotaggio prima di lasciare il territorio, avviene entro 4 giorni dall’ultimo scarico nel territorio dello Stato', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '380', '', '', 'Trasporto in regime di cabotaggio – Documenti', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '380', '2771', '1', 'Quale prova della regolarità di esercizio del cabotaggio a bordo deve trovarsi tra l’altro la documentazione relativa ad ogni trasporto di cabotaggio effettuato', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '380', '2772', '2', 'Quale prova della regolarità di esercizio del cabotaggio a bordo deve trovarsi tra l’altro la documentazione di prova del trasporto nazionale con cui è stato raggiunto, scarico, il territorio dello Stato membro di destinazione', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '380', '2773', '3', 'Quale prova della regolarità di esercizio del cabotaggio a bordo deve trovarsi tra l’altro la licenza comunitaria', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '380', '2774', '4', 'Quale prova della regolarità di esercizio del cabotaggio a bordo deve trovarsi tra l’altro qualsiasi documento che contiene nome, indirizzo e firma del mittente', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '381', '', '', 'Attestato del conducente', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '381', '2775', '1', 'Gli autisti dipendenti extra UE, che circolano fuori dai confini nazionali a bordo dei veicoli per trasporto di cose per conto di terzi, devono essere in possesso dell’originale dell’attestato di conducente', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '381', '2776', '2', 'All’interno dell’UE il conducente, nel caso sia cittadino di uno Stato diverso dalla Svizzera e da quelli membri dell’UE e del SEE, deve essere in possesso dell’attestato di conducente', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '382', '', '', 'Accordo di trasporto UE-Svizzera - 01', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '382', '2777', '1', 'I trasporti attraverso la Svizzera sono regolati da un accordo UE-Svizzera', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '382', '2778', '2', 'Il vettore svizzero che effettua un trasporto in ambito UE non deve avere a bordo del veicolo l’attestato di conducente anche se trattasi di vettore extra UE', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '382', '2779', '3', 'Il vettore svizzero che effettua un trasporto in ambito UE non deve avere alcuna autorizzazione o licenza in quanto il trasporto da e per la UE è completamente liberalizzato', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '382', '2780', '4', 'Il vettore svizzero che effettua un trasporto in ambito UE deve avere a bordo del veicolo una autorizzazione di tipo bilaterale rilasciata dal Paese in cui si reca', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '382', '2781', '5', 'Il vettore svizzero che effettua un trasporto in ambito UE deve avere a bordo del veicolo l’attestato di conducente poiché trattasi di vettore extra UE', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '382', '2782', '6', 'I trasporti attraverso la Svizzera sono completamente liberalizzati', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '382', '2783', '7', 'I trasporti attraverso la Svizzera sono regolati da un accordo bilaterale Italia-Svizzera', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '382', '2784', '8', ' I trasporti attraverso la Svizzera sono liberalizzati solo durante le ore diurne', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '383', '', '', ' Accordo di trasporto UE-Svizzera - 02', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '383', '2785', '1', 'I vettori italiani in fase di attraversamento del territorio svizzero devono avere a bordo la copia certificata conforme della licenza comunitaria', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '383', '2786', '2', 'I vettori italiani in fase di attraversamento del territorio svizzero non devono avere a bordo alcuna documentazione in quanto il transito del territorio svizzero è completamente liberalizzato', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '383', '2787', '3', 'I vettori italiani in fase di attraversamento del territorio svizzero devono avere a bordo del veicolo una autorizzazione di transito Svizzera', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '383', '2788', '4', 'I vettori italiani in fase di attraversamento del territorio svizzero devono avere a bordo del veicolo un’autorizzazione occasionale rilasciata dalle autorità svizzere alla frontiera di ingresso', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '384', '', '', 'Transiti doganali Ponte Chiasso', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '384', '2789', '1', 'Per transitare dal territorio Svizzero attraverso il confine di Ponte Chiasso e/o di Gaggiolo oltre al documento doganale di transito necessita la scheda di circolazione azzurra', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '384', '2790', '2', 'Nel transito attraverso il confine Italo-Svizzero di Ponte Chiasso (CO) e di Gaggiolo (VA) la “scheda di circolazione” va compilata e sottoscritta dall’autista del veicolo contenente la merce', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '384', '2791', '3', 'Nel transito attraverso il confine Italo-Svizzero di Ponte Chiasso (CO) e di Gaggiolo (VA) la “scheda di circolazione” va compilata e sottoscritta dal titolare dell’impresa di trasporto', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '384', '2792', '4', 'Nel transito attraverso il confine Italo-Svizzero di Ponte Chiasso (CO) e di Gaggiolo (VA) la “scheda di circolazione” va compilata e sottoscritta dall’esportatore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '384', '2793', '5', 'Nel transito attraverso il confine Italo-Svizzero di Ponte Chiasso (CO) e di Gaggiolo (VA) la “scheda di circolazione” va compilata e sottoscritta dallo spedizioniere', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '384', '2794', '6', 'Per transitare dal territorio Svizzero attraverso il confine di Ponte Chiasso e/o di Gaggiolo oltre al documento doganale di transito necessita il DDT (documento di trasporto)', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '384', '2795', '7', 'Per transitare dal territorio Svizzero attraverso il confine di Ponte Chiasso e/o di Gaggiolo oltre al documento doganale di transito necessita la fattura', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '384', '2796', '8', 'Per transitare dal territorio Svizzero attraverso il confine di Ponte Chiasso e/o di Gaggiolo oltre al documento doganale di transito necessita il carnet TIR', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '385', '', '', 'Trasporto “Paese terzo”', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '385', '2797', '1', 'Un trasporto viene definito “paese terzo” quando le cose sono caricate in uno Stato per essere portate in un altro con un veicolo immatricolato in uno Stato diverso da quello in cui il trasporto comincia o termina', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '385', '2798', '2', 'Un trasporto viene definito “paese terzo” quando avviene fra due Stati che non sono membri dell’UE', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '385', '2799', '3', 'Un trasporto viene definito “paese terzo” quando avviene fra due Stati membri dell’UE', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '385', '2800', '4', 'Un trasporto viene definito “paese terzo” quando il carico è costituito da merci prodotte in uno Stato non membro dell’UE', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '386', '', '', 'Stati CEMT', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '386', '2801', '1', 'Alla CEMT aderisce anche l’Italia', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '386', '2802', '2', 'La CEMT è la Conferenza europea dei ministri dei trasporti', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '386', '2803', '3', 'La CEMT prevede il rilascio di autorizzazioni valide per il trasporto internazionale c/terzi di merci su strada', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '386', '2804', '4', 'Fanno parte della Conferenza europea dei Ministri dei trasporti (CEMT) tutti gli Stati che vi hanno aderito', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '386', '2805', '5', 'Fanno parte della Conferenza europea dei Ministri dei trasporti (CEMT) tutti gli Stati aderenti allo Spazio economico europeo (SEE)', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '386', '2806', '6', 'Fanno parte della Conferenza europea dei Ministri dei trasporti (CEMT) gli Stati membri e non, della UE che vi hanno aderito', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '386', '2807', '7', 'Fanno parte della Conferenza europea dei Ministri dei trasporti (CEMT) solo gli Stati che inizialmente hanno dato vita alla UE', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '386', '2808', '8', 'Fanno parte della Conferenza europea dei Ministri dei trasporti (CEMT) solo gli Stati facenti parte della UE', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '386', '2809', '9', 'La CEMT regolamenta i trasporti solo tra Italia e Francia', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '386', '2810', '10', 'La CEMT ha liberalizzato i trasporti, per conto di terzi, di merci e persone esclusivamente negli Stati UE', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '386', '2811', '11', 'La CEMT è ora trasformata in Foro Internazionale del Trasporto (IFT)', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '386', '2812', '12', 'La CEMT prevede il rilascio di autorizzazioni bilaterali tra Stati membri UE', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '387', '', '', 'Autorizzazione CEMT', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '387', '2813', '1', 'L’autorizzazione CEMT è rilasciata dal paese di stabilimento dell’impresa', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '387', '2814', '2', 'L’autorizzazione CEMT in Italia è rilasciata in base ad una graduatoria', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '387', '2815', '3', 'Le autorizzazioni CEMT sono contingentate', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '387', '2816', '4', ' L’autorizzazione CEMT è contingentata', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '387', '2817', '5', 'L’autorizzazione CEMT non può essere utilizzata contemporaneamente su più veicoli in disponibilità dell’impresa', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '387', '2818', '6', 'L’autorizzazione CEMT può essere affittata ad una impresa di autotrasporto diverso da quello che ne ha la titolarità', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '387', '2819', '7', 'Le autorizzazioni CEMT sono rilasciate agli spedizionieri doganali che le affittano di volta in volta all’impresa che questi incaricano di effettuare il trasporto', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '387', '2820', '8', 'L’autorizzazione CEMT è rilasciata dall’organismo europeo competente', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '387', '2821', '9', 'L’autorizzazione CEMT in Italia è rilasciata dall’ambasciata dello Stato estero', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '387', '2822', '10', 'L’autorizzazione CEMT in Italia è rilasciata a qualsiasi impresa che ne fa richiesta, indipendentemente dal numero di richieste inoltrate', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '388', '', '', 'Autorizzazione CEMT - Rilascio e tipi', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '388', '2823', '1', 'L’autorizzazione CEMT non è valida per un trasporto da un Paese membro a un Paese terzo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '388', '2824', '2', 'L’autorizzazione CEMT vale solo se i veicoli sono immatricolati all’interno di un Paese CEMT', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '388', '2825', '3', 'L’autorizzazione CEMT riguarda i trasporti all’interno degli Stati membri', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '388', '2826', '4', 'L’autorizzazione CEMT accompagna il veicolo a motore', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '388', '2827', '5', 'Le autorizzazioni CEMT sono valide per un determinato limite temporale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '388', '2828', '6', 'Le autorizzazioni CEMT sono valide per un numero illimitato di viaggi', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '388', '2829', '7', 'Le autorizzazioni CEMT sono valide per 100 viaggi', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '388', '2830', '8', 'Le autorizzazioni CEMT sono valide per il numero dei viaggi stabilito in base a quelli effettuati nell’anno precedente', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '388', '2831', '9', 'Le autorizzazioni CEMT sono valide per 50 viaggi', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '388', '2832', '10', 'L’autorizzazione CEMT vale 5 anni', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '388', '2833', '11', 'Le autorizzazioni CEMT di breve durata hanno validità 60 giorni', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '388', '2834', '12', 'Le autorizzazioni CEMT di breve durata hanno validità 90 giorni possono essere utilizzate solo per trasporti in ambito extra UE', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '389', '', '', 'Autorizzazione CEMT – Validità', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '389', '2835', '1', 'Per quanto riguarda l’utilizzo della autorizzazione CEMT è previsto l’obbligo, in alcuni Stati aderenti, di usare veicoli appartenenti a certe categorie d’inquinamento EURO', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '389', '2836', '2', 'Per quanto riguarda l’utilizzo della autorizzazione CEMT sono previste restrizioni di carattere temporale, territoriale, sul numero massimo di viaggi che non coinvolgono lo Stato di stabilimento dell’impresa', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '389', '2837', '3', 'Per quanto riguarda l’utilizzo della autorizzazione CEMT sono previste solo restrizioni di tipo temporale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '389', '2838', '4', 'Per quanto riguarda l’utilizzo della autorizzazione CEMT sono previste restrizioni solo in ordine alla tipologia dei veicoli che l’impresa è obbligata ad utilizzare', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '389', '2839', '5', 'Per quanto riguarda l’utilizzo della autorizzazione CEMT non sono previste restrizioni di utilizzo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '390', '', '', 'Copertura autorizzazione CEMT', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '390', '2840', '1', 'Le autorizzazioni CEMT di breve durata non sono valide per la circolazione in territorio austriaco', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '390', '2841', '2', 'Le autorizzazioni CEMT non consentono di effettuare trasporti di cabotaggio in uno Stato diverso da quello di stabilimento dell’impresa che effettua il trasporto', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '390', '2842', '3', 'L’autorizzazione CEMT consente l’effettuazione di trasporti di tipo bilaterale, di transito, fra Stati diversi da quello di immatricolazione del veicolo, tragitti a vuoto', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '390', '2843', '4', 'L’autorizzazione CEMT consente tra gli altri l’effettuazione di trasporti di tipo bilaterale da un Paese aderente ad un altro aderente', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '390', '2844', '5', 'L’autorizzazione CEMT consente tra gli altri l’effettuazione di trasporti di transito a vuoto fra Stati diversi da quello di immatricolazione del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '390', '2845', '6', 'Le autorizzazioni CEMT consentono di effettuare trasporti di cabotaggio in uno Stato diverso da quello di stabilimento dell’impresa che effettua il trasporto', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '390', '2846', '7', ' L’autorizzazione CEMT consente unicamente l’effettuazione dei trasporti a carico (di tipo bilaterale) da un Paese membro ad un altro Paese membro e viceversa', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '390', '2847', '8', ' L’autorizzazione CEMT consente unicamente l’effettuazione di trasporto a carico o a vuoto nell’ambito degli Stati membri della UE', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '390', '2848', '9', 'L’autorizzazione CEMT consente unicamente l’effettuazione dei trasporti a carico o a vuoto (di tipo bilaterale) da un Paese membro ad un altro Paese membro e viceversa', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '391', '', '', 'CEMT - Regola dei tre viaggi', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '391', '2849', '1', 'L’impresa di autotrasporto in possesso di una autorizzazione CEMT pone, in capo all’impresa assegnataria, l’obbligo di rientro del veicolo nel Paese di stabilimento dopo ogni gruppo di 3 viaggi a carico eseguiti al di fuori di questo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '391', '2850', '2', 'L’impresa di autotrasporto in possesso di una autorizzazione CEMT pone, in capo all’impresa assegnataria, l’obbligo di rientro del veicolo nel Paese di stabilimento dopo ogni gruppo di 10 viaggi a carico eseguiti al di fuori di questo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '391', '2851', '3', 'L’impresa di autotrasporto in possesso di una autorizzazione CEMT prevede delle limitazioni quantitative al numero dei viaggi che l’impresa può eseguire nell’arco di validità dell’autorizzazione', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '391', '2852', '4', 'L’impresa di autotrasporto in possesso di una autorizzazione CEMT può eseguire qualsiasi tipo di trasporto nell’ambito degli Stati aderenti alla CEMT senza vincoli o limitazioni', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '392', '', '', ' L’autorizzazione CEMT  ', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '392', '2853', '1', 'L’autorizzazione CEMT non può essere sostituita da una copia conforme autenticata', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '392', '2854', '2', 'L’autorizzazione CEMT non deve essere obbligatoriamente accompagnata dal libretto dei resoconti di viaggio altrimenti detto libretto statistico in quanto non previsto', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '392', '2855', '3', 'L’autorizzazione CEMT deve essere obbligatoriamente accompagnata dal certificato di iscrizione all’Albo nazionale degli autotrasportatori di cose per conto di terzi', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '392', '2856', '4', 'L’autorizzazione CEMT deve sempre essere accompagnata dalla licenza comunitaria', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '393', '', '', 'Documentazione a board con autorizzazione CEMT ', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '393', '2857', '1', 'Durante un trasporto internazionale l’autorizzazione CEMT è obbligatorio averla a bordo se il veicolo effettua un percorso a vuoto', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '393', '2858', '2', 'Durante un trasporto internazionale l’autorizzazione CEMT deve essere esibita a tutte le dogane che si attraversano', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '393', '2859', '3', 'Durante un trasporto internazionale l’autorizzazione CEMT deve trovarsi a bordo del veicolo dal luogo di carico a quello di scarico e durante i percorsi a vuoto', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '393', '2860', '4', 'Durante un trasporto internazionale l’autorizzazione CEMT è l’unico documento che legittima l’impresa intestataria ad effettuare trasporti fra gli Stati membri e deve trovarsi a bordo del veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '393', '2861', '5', 'Durante un trasporto internazionale l’autorizzazione CEMT va esibita unicamente alle autorità doganali del Paese di arrivo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '393', '2862', '6', 'Durante un trasporto internazionale l’autorizzazione CEMT deve essere esibita solamente alla dogana di partenza', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '393', '2863', '7', 'Durante un trasporto internazionale l’autorizzazione CEMT va conservata in azienda in quanto durante il trasporto è sufficiente che a bordo del veicolo venga conservato il libretto dei resoconti di viaggio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '394', '', '', ' CEMT fuori contingente', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '394', '2864', '1', 'Un’impresa italiana specializzata in traslochi che intende effettuare un trasporto in uno Stato membro della CEMT può chiedere il rilascio di una autorizzazione CEMT fuori contingente', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '394', '2865', '2', 'Un’impresa italiana specializzata in traslochi che intende effettuare un trasporto in uno Stato membro della CEMT non ha l’obbligo di dotarsi di una autorizzazione in quanto i traslochi sono liberalizzati nell’area CEMT', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '394', '2866', '3', 'Un’impresa italiana specializzata in traslochi non può, per nessun motivo, essere autorizzata a svolgere un trasporto in uno Stato membro della CEMT', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '395', '', '', 'Trasporto in ambito CEMT liberalizzato', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '395', '2867', '1', 'In ambito CEMT, fatto salvo l’effetto delle riserve, è liberalizzato il trasporto di animali', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '395', '2868', '2', 'In ambito CEMT, fatto salvo l’effetto delle riserve, è liberalizzato il trasporto di veicoli danneggiati o da riparare e il movimento dei veicoli destinati alla riparazione', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '395', '2869', '3', 'In ambito CEMT, fatto salvo l’effetto delle riserve, è liberalizzato il trasporto di semilavorati destinati all’assemblaggio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '395', '2870', '4', ' In ambito CEMT, fatto salvo l’effetto delle riserve, è liberalizzato il trasporto di prodotti della pesca freschi', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '395', '2871', '5', 'In ambito CEMT, fatto salvo l’effetto delle riserve, è liberalizzato il trasporto di prodotti alimentari deperibili', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '396', '', '', 'Convenzione CMR', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '396', '2872', '1', 'Il contratto di trasporto internazionale di merci su strada può essere regolato dalla CMR (Convenzione sul contratto di autotrasporto internazionale di merci)', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '396', '2873', '2', 'La CMR è la convenzione relativa al contratto di trasporto internazionale di merci su strada', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '396', '2874', '3', 'La CMR è una convenzione relativa al contratto di trasporto di merci su strada', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '396', '2875', '4', 'La CMR si applica ai contratti a titolo oneroso di trasporto di merci su strada quando il luogo di ricevimento della merce e il luogo previsto per la riconsegna sono situati in due Stati diversi di cui almeno uno sia Parte contraente della Convenzione, senza tenere in alcun conto né il domicilio né la cittadinanza delle parti contraenti', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '396', '2876', '5', 'La CMR si applica ai contratti nazionali di merci su strada', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '396', '2877', '6', 'La CMR è la convenzione relativa al trasporto su strada di merci pericolose', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '396', '2878', '7', ' La CMR è la convenzione relativa al trasporto su strada di materiale deperibile', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '396', '2879', '8', 'La CMR è la convenzione relativa al contratto di trasporto nazionale di merci su strada', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '396', '2880', '9', 'La CMR (Convenzione sul contratto di autotrasporto internazionale di merci) è la convenzione relativa al contratto nazionale di merci su strada', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '396', '2881', '10', 'La CMR si applica ai trasporti su strada di merci pericolose', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '397', '', '', 'Convenzione CMR- Scopo', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '397', '2882', '1', 'La CMR (Convenzione sul contratto di autotrasporto internazionale di merci) regola l’esecuzione del trasporto', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '397', '2883', '2', 'La CMR (Convenzione sul contratto di autotrasporto internazionale di merci) regola la presa in consegna della merce da parte del vettore', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '397', '2884', '3', 'La CMR (Convenzione sul contratto di autotrasporto internazionale di merci) regola la responsabilità degli attori del contratto di trasporto, tranne quella del destinatario', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '397', '2885', '4', 'La CMR (Convenzione sul contratto di autotrasporto internazionale di merci) regola la sola responsabilità del vettore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '398', '', '', 'Convenzione CMR- Applicabilità', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '398', '2886', '1', 'La CMR (Convenzione sul contratto di autotrasporto internazionale di merci) si applica quando c’è un contratto di trasporto a titolo oneroso e la merce circola in un Paese che fa parte della Convenzione', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '398', '2887', '2', 'La CMR (Convenzione sul contratto di autotrasporto internazionale di merci) si applica indipendentemente dal domicilio e dalla cittadinanza delle parti', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '398', '2888', '3', 'La CMR (Convenzione sul contratto di autotrasporto internazionale di merci) si applica quando il luogo di ricevimento della merce e il luogo previsto per la consegna sono situati in due Stati diversi, ma almeno uno fa parte della Convenzione', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '398', '2889', '4', 'La CMR (Convenzione sul contratto di autotrasporto internazionale di merci) si applica al trasporto combinato', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '398', '2890', '5', 'La CMR (Convenzione sul contratto di autotrasporto internazionale di merci) si applica ai traslochi', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '398', '2891', '6', 'La CMR (Convenzione sul contratto di autotrasporto internazionale di merci) si applica ai trasporti gratuiti', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '398', '2892', '7', 'La CMR (Convenzione sul contratto di autotrasporto internazionale di merci) si applica ai trasporti funebri', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '398', '2893', '8', 'La CMR (Convenzione sul contratto di autotrasporto internazionale di merci) si applica ai trasporti intermodali anche in caso di rottura del carico', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '398', '2894', '9', 'La CMR (Convenzione sul contratto di autotrasporto internazionale di merci) si applica quando il trasporto è in conto proprio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '399', '', '', 'Convenzione CMR- Copertura', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '399', '2895', '1', 'La CMR (Convenzione sul contratto di autotrasporto internazionale di merci) è applicata dalla presa in consegna della merce', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '399', '2896', '2', 'La CMR (Convenzione sul contratto di autotrasporto internazionale di merci) è applicata dalla stipula del contratto di trasporto', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '399', '2897', '3', 'La CMR (Convenzione sul contratto di autotrasporto internazionale di merci) è applicata dal momento in cui il vettore mette a disposizione il mezzo con il quale verrà effettuato il trasporto', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '399', '2898', '4', 'La CMR (Convenzione sul contratto di autotrasporto internazionale di merci) è applicata da quando il veicolo messo a disposizione è ritenuto idoneo dal mittente', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '400', '', '', 'Esenzione dalla CMR', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '400', '2899', '1', 'I traslochi sono esenti dall’applicazione della CMR (Convenzione sul contratto di autotrasporto internazionale di merci)', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '400', '2900', '2', 'I trasporti effettuati sulla base di convenzioni postali internazionali sono esenti dall’applicazione della CMR (Convenzione sul contratto di autotrasporto internazionale di merci)', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '400', '2901', '3', 'I trasporti a titolo gratuito sono esenti dall’applicazione della CMR (Convenzione sul contratto di autotrasporto internazionale di merci)', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '400', '2902', '4', 'I trasporti funebri sono esenti dall’applicazione della CMR (Convenzione sul contratto di autotrasporto internazionale di merci)', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '400', '2903', '5', 'Tutti i trasporti effettuati nell’UE sono esenti dall’applicazione della CMR (Convenzione sul contratto di autotrasporto internazionale di merci)', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '400', '2904', '6', 'Tutti i trasporti effettuati nel SEE (Spazio economico europeo) sono esenti dall’applicazione della CMR (Convenzione sul contratto di autotrasporto internazionale di merci)', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '400', '2905', '7', 'Tutti i servizi di trasporto di merci sono assoggettati al regime CMR (Convenzione sul contratto di autotrasporto internazionale di merci)', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '400', '2906', '8', 'I trasporti di derrate deperibili sono esenti dall’applicazione della CMR (Convenzione sul contratto di autotrasporto internazionale di merci)', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '401', '', '', 'Lettera di vettura internazionale CMR - 01', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '401', '2907', '1', 'La mancanza, la irregolarità o lo smarrimento della lettera di vettura internazionale CMR non influisce comunque sulla validità del contratto', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '401', '2908', '2', 'La lettera di vettura del trasporto internazionale costituisce una forma scritta di contratto', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '401', '2909', '3', 'La lettera di vettura internazionale CMR anche se non viene compilata non influisce sulla validità del contratto', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '401', '2910', '4', 'La lettera di vettura internazionale CMR quando manca o viene smarrita influisce sulla validità del contratto di trasporto', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '401', '2911', '5', 'La lettera di vettura internazionale CMR è indispensabile ai fini della validità del contratto', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '401', '2912', '6', 'La mancanza, la irregolarità o lo smarrimento della lettera di vettura internazionale CMR rende nullo il contratto', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '401', '2913', '7', 'La mancanza, la irregolarità o lo smarrimento della lettera di vettura internazionale CMR rende annullabile il contratto', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '401', '2914', '8', 'La mancanza, la irregolarità o lo smarrimento della lettera di vettura internazionale CMR rende inefficace il contratto', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '402', '', '', 'Lettera di vettura internazionale CMR – Scopo', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '402', '2915', '1', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), la lettera di vettura può essere chiesta per ogni carico e per ogni genere di merce trasportata', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '402', '2916', '2', 'La lettera di vettura internazionale CMR fa fede della conclusione del contratto e del ricevimento della merce da parte del vettore', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '402', '2917', '3', 'La lettera di vettura internazionale CMR è prevista dalla CMR come forma di regolamentazione del contratto di trasporto', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '402', '2918', '4', 'La lettera di vettura internazionale CMR va sottoscritta sia dal mittente che dal vettore in maniera autografa oppure stampata oppure apposta mediante timbri del mittente e del vettore secondo le regole dello Stato nel quale è emessa', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '402', '2919', '5', 'La lettera di vettura internazionale CMR in Italia deve essere conservata dal destinatario per almeno 2 anni dalla data del trasporto', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '402', '2920', '6', 'La lettera di vettura internazionale CMR va sottoscritta solo dal vettore in maniera autografa oppure stampata oppure apposta mediante timbri', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '402', '2921', '7', 'La lettera di vettura internazionale CMR va sottoscritta solo dal mittente in maniera autografa', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '402', '2922', '8', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), la lettera di vettura può essere chiesta solo per il veicolo usato nel trasporto', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '403', '', '', 'Lettera di vettura internazionale CMR – Compilazione', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '403', '2923', '1', 'La lettera di vettura internazionale CMR è compilata in 3 esemplari originali', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '403', '2924', '2', 'La lettera di vettura internazionale CMR ha un quarto esemplare, di colore bianco, che può servire a fini statistici', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '403', '2925', '3', 'La lettera di vettura internazionale CMR è redatta in un numero minimo di tre esemplari di cui il primo è destinato al mittente', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '403', '2926', '4', 'La lettera di vettura internazionale CMR è redatta in un numero minimo di tre esemplari di cui il secondo accompagna la merce', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '403', '2927', '5', 'La lettera di vettura internazionale CMR è redatta in un numero minimo di tre esemplari di cui il terzo è trattenuto dal vettore', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '403', '2928', '6', 'La lettera di vettura internazionale CMR è formata da 3 esemplari ma non è necessario che siano firmati', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '403', '2929', '7', 'La lettera di vettura internazionale CMR è redatta in un numero minimo di quattro esemplari di cui il secondo è per il proprietario della merce', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '403', '2930', '8', 'La lettera di vettura internazionale CMR è redatta in un numero minimo di due esemplari per mittente e destinatario', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '404', '', '', 'Lettera di vettura internazionale CMR - 02', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '404', '2931', '1', 'La lettera di vettura internazionale CMR deve contenere precise indicazioni obbligatorie', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '404', '2932', '2', 'La lettera di vettura internazionale CMR è libera nella forma', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '404', '2933', '3', 'La lettera di vettura internazionale CMR è libera nella sostanza', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '404', '2934', '4', 'La lettera di vettura internazionale CMR può contenere anche indicazioni contrarie alla CMR (Convenzione sul contratto di autotrasporto internazionale di merci)', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '405', '', '', 'Lettera di vettura internazionale CMR  - 03', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '405', '2935', '1', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), la lettera di vettura andata persa, può essere sostituita da una nuova lettera di vettura in tre esemplari formata dal mittente e dal vettore', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '405', '2936', '2', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), la lettera di vettura andata persa, può essere sostituita da nessun documento', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '405', '2937', '3', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), la lettera di vettura andata persa, può essere sostituita con il consenso del destinatario della merce', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '405', '2938', '4', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), la lettera di vettura andata persa, può essere sostituita dal conducente del veicolo usato per il trasporto', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '406', '', '', 'Lettera di vettura internazionale CMR - 04', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '406', '2939', '1', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), la durata del trasporto può essere indicata nella lettera di vettura', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '406', '2940', '2', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), la durata del trasporto può essere indicata in un documento separato', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '406', '2941', '3', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), la durata del trasporto può essere concordata verbalmente', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '406', '2942', '4', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), la durata del trasporto deve essere indicata solo nella lettera di vettura', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '406', '2943', '5', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), la durata del trasporto non può essere concordata verbalmente', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '406', '2944', '6', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), la durata del trasporto non può essere indicata in un documento separato e diverso dalla lettera di vettura', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '407', '', '', 'Mittente nella CMR – Documenti', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '407', '2945', '1', 'I documenti per la validità della CMR (Convenzione sul contratto di autotrasporto internazionale di merci) consistono nella documentazione per il transito doganale e in ogni altra certificazione prevista (di origine della merce, valutaria, sanitaria, di polizia e consimili) oltre alla lettera di vettura', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '407', '2946', '2', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), prima dell’inizio del trasporto, deve essere fornita dal mittente la documentazione per il transito doganale ed ogni altra certificazione necessaria', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '407', '2947', '3', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), prima dell’inizio del trasporto, deve essere fornita dal mittente soltanto la lettera di vettura', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '407', '2948', '4', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), prima dell’inizio del trasporto, deve essere fornita dal mittente soltanto la certificazione relativa all’origine della merce, valutaria, sanitaria, di polizia e consimili', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '407', '2949', '5', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), prima dell’inizio del trasporto, deve essere fornita dal mittente solo la documentazione che autorizza il veicolo ad attraversare la frontiera', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '407', '2950', '6', 'I documenti per la validità della CMR (Convenzione sul contratto di autotrasporto internazionale di merci) consistono nel DDT (documento di trasporto) e nella bolla di accompagnamento', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '407', '2951', '7', 'I documenti per la validità della CMR (Convenzione sul contratto di autotrasporto internazionale di merci) consistono solo nella lettera di vettura', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '407', '2952', '8', 'I documenti per la validità della CMR (Convenzione sul contratto di autotrasporto internazionale di merci) consistono nella bolla di accompagnamento', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '408', '', '', 'Mittente nella CMR – Responsabilità', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '408', '2953', '1', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), il mittente deve porre il vettore nella condizione di eseguire correttamente il trasporto', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '408', '2954', '2', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), il mittente è responsabile verso il vettore per l’imperfezione dell’imballaggio della merce se il vettore ha tempestivamente proposto riserve in proposito nel caso la circostanza sia apparente o quanto meno nota', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '408', '2955', '3', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), il mittente può rispondere verso il vettore per l’imperfezione dell’imballaggio', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '408', '2956', '4', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), il mittente non è tenuto a segnalare al vettore l’esatta natura del pericolo delle merci', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '408', '2957', '5', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), il mittente non è responsabile se lettera di vettura è, in tutto o in parte, compilata dal vettore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '408', '2958', '6', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), il mittente non è mai responsabile del trasporto', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '408', '2959', '7', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), il mittente risponde sempre verso il vettore per la merce consegnata', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '408', '2960', '8', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), il mittente non è responsabile verso il vettore per lo Stato dell’imballo della merce', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '408', '2961', '9', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), il mittente non è responsabile verso il vettore per l’imballo della merce essendo questo un onere del vettore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '408', '2962', '10', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), il mittente non risponde mai dell’imperfezione dell’imballaggio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '408', '2963', '11', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), il mittente non ha oneri nella valutazione dell’imballaggio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '408', '2964', '12', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), il mittente risponde sempre dell’imperfezione dell’imballaggio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '408', '2965', '13', 'La CMR (Convenzione sul contratto di autotrasporto internazionale di merci) non regola la responsabilità del mittente', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '409', '', '', 'Resposibilità del Mittente nella CMR ', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '409', '2966', '1', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), i danni che derivano dall’omissione e dall’inesattezza delle indicazioni dei documenti di trasporto sono a carico del mittente', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '409', '2967', '2', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), i danni che derivano dall’omissione e dall’inesattezza delle indicazioni dei documenti di trasporto sono a carico del vettore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '409', '2968', '3', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), i danni che derivano dall’omissione e dall’inesattezza delle indicazioni dei documenti di trasporto sono a carico del destinatario', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '409', '2969', '4', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), i danni che derivano dall’omissione e dall’inesattezza delle indicazioni dei documenti di trasporto sono a carico di nessuno', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '410', '', '', 'Diritti del Mittente nella CMR', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '410', '2970', '1', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), il mittente ha facoltà di esigere che il vettore verifichi il peso lordo o la quantità in cui la merce è espressa, a proprie spese', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '410', '2971', '2', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), il mittente può incaricare il vettore di incassare una somma determinata che, per effetto di altro rapporto, sia dovuta al mittente medesimo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '410', '2972', '3', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), il mittente non può incaricare il vettore di altre incombenze oltre quelle proprie del trasporto', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '410', '2973', '4', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), il mittente può incaricare il vettore di riscuotere per suo conto somme di denaro non quantificate', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '410', '2974', '5', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), il mittente può incaricare il vettore di incassare una somma di denaro oppure il suo equivalente in qualsiasi bene di valore non determinabile', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '410', '2975', '6', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), il mittente può far verificare solo il numero dei colli', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '411', '', '', 'Vettore nella CMR - Presa in carico', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '411', '2976', '1', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), la presa in carico è un atto complesso che comprende il carico della merce, il passaggio della sua detenzione al vettore e la verifica sia dello stato apparente della merce e dell’imballaggio sia dell’esattezza delle indicazioni della lettera di vettura', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '411', '2977', '2', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), la presa in carico comprende solo il carico della merce e il passaggio della sua detenzione dal mittente al vettore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '411', '2978', '3', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), la presa in carico comprende solo la verifica dello stato apparente della merce e dell’imballaggio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '411', '2979', '4', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), la presa in carico comprende solo l’esattezza delle indicazioni della lettera di vettura sul numero dei colli', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '412', '', '', 'Riconsegna della merce nella CMR', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '412', '2980', '1', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), la riconsegna della merce è un atto complesso che comprende l’avviso di merce arrivata a destinazione, la richiesta di consegna merce fatta dal destinatario e la consegna materiale della merce', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '412', '2981', '2', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), la riconsegna della merce avviene quando il vettore abbandona la merce presso il destinatario senza che questi ne entri in possesso', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '412', '2982', '3', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), la riconsegna della merce avviene quando il mittente ne riceve comunicazione dal destinatario', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '413', '', '', 'Responsabilità del Vettore nella CMR ', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '413', '2983', '1', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), nel corso del trasporto, il vettore è responsabile della perdita totale o parziale della merce o della sua avaria o del ritardo nella consegna', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '413', '2984', '2', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), la responsabilità del vettore inizia con la presa in carico della merce e termina con la riconsegna a destinazione', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '413', '2985', '3', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), il vettore è responsabile per il lavoro di tutti quelli che intervengono nell’esecuzione del trasporto, senza tenere in alcun conto il rapporto che intercorre fra essi e il vettore medesimo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '413', '2986', '4', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), il vettore è responsabile in modo diretto ed immediato per il lavoro di tutti coloro che esplicano una funzione relativa all’esecuzione del trasporto', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '413', '2987', '5', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), il vettore che non ha la possibilità di eseguire le disposizioni del mittente deve comunicarlo al mittente', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '413', '2988', '6', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), il vettore è responsabile solo della merce', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '413', '2989', '7', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), il vettore è responsabile solo degli addetti regolarmente stipendiati', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '413', '2990', '8', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), il vettore è responsabile per il lavoro di tutti quelli che intervengono nell’esecuzione del trasporto purché retribuiti secondo il contratto', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '413', '2991', '9', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), il vettore è responsabile soltanto per il lavoro del personale addetto al carico della merce', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '413', '2992', '10', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), il vettore è responsabile soltanto per il lavoro dell’autista', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '413', '2993', '11', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), la responsabilità del vettore inizia prima della presa in carico della merce e termina con la riconsegna a destinazione', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '413', '2994', '12', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), la responsabilità del vettore inizia con la stipula del contratto di trasporto e termina con la riconsegna al destinatario', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '413', '2995', '13', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), la responsabilità del vettore inizia dal momento nel quale il veicolo comincia di fatto il trasporto e termina quando il veicolo entra nel luogo di destinazione della merce', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '414', '', '', 'Esonero responsabilità del  vettore nella CMR ', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '414', '2996', '1', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), il vettore è esonerato dalla responsabilità della perdita, dell’avaria o del ritardo nella riconsegna della merce nel caso di vizio proprio della merce', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '414', '2997', '2', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), il vettore è esonerato dalla responsabilità della perdita, dell’avaria o del ritardo nella riconsegna della merce nel caso di circostanze che il vettore poteva evitare', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '414', '2998', '3', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), il vettore è esonerato dalla responsabilità della perdita, dell’avaria o del ritardo nella riconsegna della merce in ogni caso', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '414', '2999', '4', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), il vettore è esonerato dalla responsabilità della perdita, dell’avaria o del ritardo nella riconsegna della merce in nessun caso', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '414', '3000', '5', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), nel corso del trasporto, il vettore è responsabile solo della perdita parziale o totale della merce', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '414', '3001', '6', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), nel corso del trasporto, il vettore è responsabile solo del ritardo nella consegna', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '414', '3002', '7', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), nel corso del trasporto, il vettore non risponde se il ritardo è dovuto a guasto del veicolo utilizzato', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '415', '', '', 'Responsabilità trasporto combinato nella CMR', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '415', '3003', '1', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), nel caso di trasporto combinato, la responsabilità del vettore è esclusa quando l’evento dannoso avviene nella tratta non stradale e non sia dovuta ad atto o ad omissione del vettore', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '415', '3004', '2', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), nel caso di trasporto combinato, la responsabilità del vettore è inclusa anche quando l’evento dannoso avviene nella tratta non stradale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '415', '3005', '3', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), nel caso di trasporto combinato, la responsabilità del vettore è totale e comprende anche i danni provocati da terzi nella tratta non stradale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '415', '3006', '4', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), nel caso di trasporto combinato, la responsabilità del vettore include anche i danni degli altri vettori non stradali se la partenza del carico è avvenuta con il mezzo stradale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '416', '', '', ' Impossibilità a proseguire il viaggio', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '416', '3007', '1', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), nel caso di impossibilità a proseguire il viaggio, il vettore deve darne comunicazione anche al destinatario della merce', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '416', '3008', '2', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), nel caso di impossibilità a proseguire il viaggio, il vettore deve darne comunicazione solo al mittente', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '416', '3009', '3', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), nel caso di impossibilità a proseguire il viaggio, il vettore non è necessario che ne dia comunicazione al mittente e neppure al destinatario', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '416', '3010', '4', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), nel caso di impossibilità a proseguire il viaggio, il vettore deve darne comunicazione all’ambasciata italiana presso lo Stato nel cui territorio si verifica l’impedimento', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '417', '', '', 'Impedimento assoluto', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '417', '3011', '1', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), si ha impedimento assoluto alla realizzazione del viaggio quando il vettore non può realizzare il trasporto secondo la previsione di tempi, modalità e costi risultanti dalla lettera di vettura', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '417', '3012', '2', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), il vettore che dopo avere comunicato al mittente l’impossibilità di portare a termine il viaggio concordato, non riceve alcuna istruzione ha l’obbligo di adottare i provvedimenti più opportuni nell’interesse dell’avente diritto', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '417', '3013', '3', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), il vettore che non ha la possibilità di eseguire le disposizioni del mittente deve riconsegnare la merce al mittente', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '417', '3014', '4', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), il vettore che non ha la possibilità di eseguire le disposizioni del mittente deve interrompere il viaggio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '417', '3015', '5', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), il vettore che non ha la possibilità di eseguire le disposizioni del mittente deve decidere in base alla propria convenienza senza comunicarlo al mittente', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '417', '3016', '6', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), il vettore che dopo avere comunicato al mittente l’impossibilità di portare a termine il viaggio concordato, non riceve alcuna istruzione non ha alcun obbligo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '417', '3017', '7', ' Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), il vettore che dopo avere comunicato al mittente l’impossibilità di portare a termine il viaggio concordato, non riceve alcuna istruzione riporta la merce al luogo di carico', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '417', '3018', '8', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), il vettore che dopo avere comunicato al mittente l’impossibilità di portare a termine il viaggio concordato, non riceve alcuna istruzione scarica la merce nel primo posto adatto', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '417', '3019', '9', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), si ha impedimento assoluto alla realizzazione del viaggio quando il costo del trasporto supera del 10% quello preventivato', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '417', '3020', '10', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), si ha impedimento assoluto alla realizzazione del viaggio quando il tempo previsto per la consegna della merce è inferiore del 10% a quello effettivo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '417', '3021', '11', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), si ha impedimento assoluto alla realizzazione del viaggio quando il vettore smarrisce la lettera di vettura', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '418', '', '', 'CMR - Avaria', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '418', '3022', '1', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), la merce s’intende avariata quando vi è una variazione o differenza nello stato fisico della merce trasportata tale da abbassarne il valore commerciale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '418', '3023', '2', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), l’avaria è il danneggiamento della merce con conseguente riduzione del suo valore economico, pur essendo ancora utilizzabile', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '418', '3024', '3', ' Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), l’avaria è il danneggiamento della merce con riduzione della sua utilizzabilità', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '418', '3025', '4', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), l’avaria è il danneggiamento della merce che diviene inutilizzabile', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '418', '3026', '5', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), l’avaria è la perdita di utilizzabilità della merce', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '418', '3027', '6', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), la merce s’intende avariata quando la variazione o differenza nello stato fisico della merce trasportata non influenza il suo valore commerciale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '418', '3028', '7', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), la merce s’intende avariata quando la variazione o differenza nello stato fisico della merce trasportata è riconducibile ad atti del vettore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '418', '3029', '8', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), la merce s’intende avariata quando la variazione o differenza nello stato fisico della merce trasportata è conseguenza di ordini impartiti dal destinatario', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '419', '', '', 'CMR - perdita totale', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '419', '3030', '1', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), si ha perdita totale e non avaria quando la merce diventa obiettivamente non commerciabile nella sua totalità anche se l’avaria si riferisce ad una sola parte della merce trasportata', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '419', '3031', '2', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), si ha perdita totale e non avaria quando la merce può essere collocata sul mercato di destinazione a prezzo irrisorio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '419', '3032', '3', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), si ha perdita totale e non avaria quando il valore della merce trasportata non è indicato nella lettera di vettura', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '419', '3033', '4', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), si ha perdita totale e non avaria quando risulta avariata una parte della merce trasportata e sono state emesse più lettere di vettura', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '420', '', '', 'CMR – ritardo', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '420', '3034', '1', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), per ritardo s’intende la mancata riconsegna della merce entro il termine convenuto', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '420', '3035', '2', ' Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), in caso di ritardo, il titolare del diritto può chiedere il risarcimento o l’indennità per perdita della merce con rinuncia all’indennità per ritardo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '420', '3036', '3', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), in caso di ritardo, il titolare del diritto può chiedere la riconsegna oltre il termine con diritto all’indennità di ritardo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '420', '3037', '4', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), in caso di ritardo, il titolare del diritto può chiedere sia il risarcimento per perdita della merce che l’indennità per riconsegna della merce oltre il termine', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '420', '3038', '5', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), in caso di ritardo, il titolare del diritto può ricevere la merce con ritardo ma non ha diritto ad alcuna indennità', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '420', '3039', '6', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), in caso di ritardo, il titolare del diritto non può chiedere il risarcimento per perdita della merce né l’indennità per il ritardo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '420', '3040', '7', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), per ritardo s’intende la mancata riconsegna della merce entro 40 giorni dal termine convenuto', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '420', '3041', '8', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), per ritardo s’intende la mancata riconsegna della merce nel luogo di destinazione concordato', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '420', '3042', '9', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), per ritardo s’intende la mancata riconsegna della merce entro 70 giorni dal ricevimento della merce da parte del vettore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '421', '', '', 'Destinatario nella CMR', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '421', '3043', '1', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), il destinatario della merce può sostituire altri destinatari a sé stesso', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '421', '3044', '2', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), il destinatario della merce non ha più interesse alla riconsegna quando la merce non ha più le caratteristiche originarie e non può più essere utilizzata', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '421', '3045', '3', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), il destinatario della merce non può sostituire altri destinatari a sé stesso', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '421', '3046', '4', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), il destinatario della merce può sostituire altri destinatari a sé stesso dopo avere fatto scaricare la merce', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '421', '3047', '5', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), il destinatario della merce può sostituire altri destinatari a sé stesso ma deve comunicarlo all’autista del mezzo utilizzato', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '421', '3048', '6', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), il destinatario della merce non ha più interesse alla riconsegna quando nonostante tutto, la merce può ancora essere utilizzata', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '421', '3049', '7', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), il destinatario della merce non ha più interesse alla riconsegna quando la merce viene consegnata entro 20 giorni dal termine convenuto', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12B', '421', '3050', '8', 'Nella CMR (Convenzione sul contratto di autotrasporto internazionale di merci), il destinatario della merce non ha più interesse alla riconsegna quando non essendo convenuto un termine, la merce viene consegnata entro 50 giorni dal ricevimento della merce da parte del vettore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '422', '', '', 'Accordo bilaterale UE-Regno Unito - 01', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '422', '3051', '1', 'Nell''accordo UE-Regno Unito, i territori delle due parti sono intesi ciascuno in modo distinto e nella loro rispettiva unitarietà', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '422', '3052', '2', 'I trasporti da e per Gibilterra (Regno Unito) non sono regolati da un accordo UE-Regno Unito', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '422', '3053', '3', 'I trasporti da e per il Regno Unito sono regolati da un accordo UE-Regno Unito', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '422', '3054', '4', 'Nell''accordo UE-Regno Unito l''autotrasporto merci è impostato con un sistema di licenze', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '422', '3055', '5', 'Nell''accordo UE-Regno Unito il trasporto di viaggiatori è impostato con un sistema di licenze annuali', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '422', '3056', '6', 'Nell''accordo UE-Regno Unito, per territorio del Regno Unito si intende anche quello dell''Irlanda del Nord', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '422', '3057', '7', 'I trasporti da e per il Regno Unito sono regolati da un accordo bilaterale Italia-Regno Unito', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '422', '3058', '8', ' I trasporti da e per il Regno Unito sono liberalizzati solo durante le ore diurne', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '422', '3059', '9', 'I trasporti da e per il Regno Unito sono completamente liberalizzati', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '422', '3060', '10', 'I trasporti da e per Gibilterra sono regolati da un accordo UE-Regno Unito', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '422', '3061', '11', 'Nell''accordo UE- Regno Unito, per territorio dell''UE si intende anche quello del Regno Unito', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '422', '3062', '12', 'I trasporti da e per Irlanda del Nord sono considerati effettuati in ambito UE', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '423', '', '', 'Accordo bilaterale UE-Regno Unito - 02', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '423', '3063', '1', 'L’accordo di “libero scambio” tra UE e Regno Unito è un accordo bilaterale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '423', '3064', '2', 'L’accordo UE-Regno Unito non pregiudica l’applicazione delle norme stabilite dall’IFT (CEMT)', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '423', '3065', '3', 'L’accordo UE-Regno Unito disciplina esclusivamente trasporti di merci a fini commerciali', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '423', '3066', '4', 'L’accordo UE-Regno Unito non prevede quantità numeriche reciproche limitate di autorizzazioni per svolgere viaggi', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '423', '3067', '5', 'L’accordo UE-Regno Unito ha disapplicato il regime CEMT tra UE e Regno Unito', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '423', '3068', '6', 'L’accordo UE-Regno Unito prevede limiti al numero di trasporti eseguibili (autorizzazioni contingentate)', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '423', '3069', '7', 'Il trasporto merci che non genera alcun reddito è regolato dall’accordo bilaterale UE-Regno Unito', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '424', '', '', ' Accordo bilaterale UE-Regno Unito -03', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '424', '3070', '1', 'L’accordo UE-Regno Unito consente viaggi a carico da punto a punto con transito in un Paese terzo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '424', '3071', '2', 'L’accordo UE-Regno Unito consente viaggi a carico con origine e destinazione UE e transito nel Regno Unito', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '424', '3072', '3', 'L’accordo UE-Regno Unito consente viaggi a carico dal Galles (UK) all’Italia (UE)', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '424', '3073', '4', 'L’accordo UE-Regno Unito consente anche di effettuare ulteriori movimenti all’interno dei rispettivi territori, ma con limiti al numero di movimenti consentiti', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '424', '3074', '5', 'L’accordo UE-Regno Unito autorizza i vettori delle due parti ad effettuare un numero limitato di operazioni supplementari all’interno dei rispettivi territori, senza che i veicoli debbano tornare immediatamente nel Paese di stabilimento', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '424', '3075', '6', 'L’accordo UE-Regno Unito prevede un limite massimo di viaggi eseguibili nel territorio dell’altra parte contraente', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '424', '3076', '7', 'L’accordo UE-Regno Unito non consente viaggi a carico da punto a punto con transito in un Paese terzo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '424', '3077', '8', 'L’accordo UE-Regno Unito non consente viaggi a vuoto', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '424', '3078', '9', 'L’accordo UE-Regno Unito consente viaggi a carico con origine e destinazione UE, con divieto di transito nel Regno Unito', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '424', '3079', '10', 'L’accordo UE-Regno Unito nega ai vettori delle due parti di effettuare operazioni supplementari all’interno dei rispettivi territori prima che i veicoli ritornino nel Paese di stabilimento', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '424', '3080', '11', 'L’accordo UE-Regno Unito non prevede un limite massimo di viaggi eseguibili nel territorio dell’altra parte contraente', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '424', '3081', '12', 'L’accordo UE-Regno Unito non ha garantito continuità dei diritti di accesso al mercato per gli operatori di trasporto su strada del Regno Unito e dell’UE', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '425', '', '', 'licenza del Regno Unito per la Comunit', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '425', '3082', '1', 'È previsto il rilascio di copie conformi della Licenza del Regno Unito per la Comunità', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '425', '3083', '2', 'La Licenza comunitaria UE come la Licenza del Regno Unito per la Comunità ha validità massima di 10 anni', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '425', '3084', '3', 'È ammesso l’aggancio misto tra veicolo trattore italiano e veicolo trainato britannico', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '425', '3085', '4', 'Un vettore britannico per poter effettuare un trasporto in ambito UE deve essere in possesso di licenza del Regno Unito per la Comunità', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '425', '3086', '5', 'Un vettore britannico per poter effettuare un trasporto in ambito UE deve essere in possesso di licenza comunitaria UE', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '425', '3087', '6', 'Il vettore britannico che effettua un trasporto in ambito UE non deve avere alcuna autorizzazione o licenza in quanto il trasporto da e per la UE è completamente liberalizzato', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '425', '3088', '7', 'Il trasporto di merci mediante veicoli a motore la cui velocità massima autorizzata non supera i 40 km/h necessita di licenza in base all’accordo UE-Regno Unito', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '425', '3089', '8', 'Dal 21.5.2022 il trasporto di merci mediante veicoli a motore la cui massa a carico tecnicamente ammissibile, compresa quella dei rimorchi, non superi le 2,5 t necessita di licenza in base all’accordo UE-Regno Unito', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '425', '3090', '9', 'Non è ammesso l’aggancio misto tra veicolo trattore britannico e veicolo trainato italiano', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '425', '3091', '10', 'Un vettore italiano per poter effettuare un trasporto per il Regno Unito deve essere in possesso di licenza del Regno Unito', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '426', '', '', 'Documenti da esibire Vettore Britannico', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '426', '3092', '1', 'Il vettore britannico che effettua un trasporto in ambito UE non deve avere a bordo del veicolo l’attestato di conducente anche se trattasi di vettore extra UE', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '426', '3093', '2', 'I vettori britannici in fase di attraversamento del territorio UE devono avere a bordo la copia certificata conforme della Licenza del Regno Unito per la Comunità', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '426', '3094', '3', 'I vettori britannici hanno l’onere di esibire, a richiesta, la documentazione relativa a ogni trasporto interno all’UE del tipo “cross-trade” (operazioni di scambio incrociato tra due Stati membri UE)', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '426', '3095', '4', 'I vettori britannici hanno l’onere di esibire, a richiesta, la documentazione relativa a ogni trasporto interno all’UE per le verifiche della conformità, del rispetto dei limiti di tempo e delle operazioni ammesse', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '426', '3096', '5', 'Il vettore britannico che effettua un trasporto in ambito UE deve avere a bordo del veicolo una autorizzazione di tipo bilaterale rilasciata dal Paese in cui si reca', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '426', '3097', '6', 'Il vettore britannico che effettua un trasporto in ambito UE deve avere a bordo del veicolo l’attestato di conducente poiché trattasi di vettore extra UE', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '426', '3098', '7', 'I vettori britannici in fase di attraversamento del territorio UE devono avere a bordo la copia certificata conforme della Licenza comunitaria', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '427', '', '', 'Documenti da esibire Vettore Italiano', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '427', '3099', '1', 'I vettori italiani hanno l’onere di esibire, a richiesta, le prove della regolarità del trasporto verso il Regno Unito tenendo a bordo la relativa documentazione (CMR)', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '427', '3100', '2', 'I vettori italiani hanno l’onere di esibire, a richiesta, la documentazione relativa a ogni trasporto interno al territorio del Regno Unito per le verifiche della conformità, del rispetto dei limiti di tempo e delle operazioni ammesse', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '427', '3101', '3', 'I vettori italiani non sono tenuti a esibire, a richiesta, la documentazione relativa a ogni trasporto interno al territorio del Regno Unito', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '428', '', '', ' Liberalizzazioni', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '428', '3102', '1', 'L’accordo UE-Regno Unito non prevede il regime di licenza per il trasporto postale come servizio universale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '428', '3103', '2', 'L’accordo UE-Regno Unito non prevede il regime di licenza per il trasporto di veicoli danneggiati o da riparare', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '428', '3104', '3', 'L’accordo UE-Regno Unito non prevede il regime di licenza per il trasporto di medicinali, apparecchiature, attrezzature e altri articoli necessari per le cure mediche nell’ambito di aiuti di emergenza', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '428', '3105', '4', 'L’accordo UE-Regno Unito non prevede il regime di licenza per il trasporto di merci in conto proprio', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '428', '3106', '5', 'L’attività in regime liberalizzato, prevista dall’accordo UE-Regno Unito, deve rispettare tutti i limiti temporali, quantitativi e i vincoli di documentazione previsti per i trasporti in regime di licenza', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '428', '3107', '6', 'L’attività in regime liberalizzato, prevista dall’accordo UE-Regno Unito, non deve rispettare i limiti temporali, quantitativi e i vincoli di documentazione previsti per i trasporti in regime di licenza', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '428', '3108', '7', 'L’accordo UE-Regno Unito non prevede il regime di licenza, a eccezione di quelli esenti per massa, per portata o per particolare tipologia', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '428', '3109', '8', 'L’accordo UE-Regno Unito non prevede il regime di licenza per il trasporto di merci per conto terzi', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '429', '', '', 'Attraversamento Regno Unito', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '429', '3110', '1', ' I vettori italiani in fase di attraversamento del territorio britannico devono avere a bordo la copia certificata conforme della licenza comunitaria', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '429', '3111', '2', 'Un vettore italiano che carica in Francia e scarica in Irlanda può transitare nel Regno Unito', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '429', '3112', '3', 'Un vettore italiano che carica in Francia e scarica in Irlanda non può transitare nel Regno Unito', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '429', '3113', '4', 'I vettori italiani in fase di attraversamento del territorio britannico devono avere a bordo del veicolo una autorizzazione di transito britannico', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '429', '3114', '5', 'I vettori italiani in fase di attraversamento del territorio britannico devono avere a bordo del veicolo un’autorizzazione occasionale rilasciata dalle autorità britanniche alla frontiera di ingresso', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '429', '3115', '6', 'I vettori italiani in fase di attraversamento del territorio britannico non devono avere a bordo alcuna documentazione in quanto il transito del territorio britannico è completamente liberalizzato', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '430', '', '', 'Cabotaggio e Cross-trade - 01', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '430', '3116', '1', 'Dopo un viaggio a vuoto dal Regno Unito o dall’UE non è ammesso effettuare trasporti del tipo “cross-trade” (operazioni di scambio incrociato tra due Stati membri UE), né di cabotaggio, nel territorio dell’altra Parte', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '430', '3117', '2', 'Un vettore italiano può effettuare fino a due viaggi a carico all’interno del territorio del Regno Unito, senza ritornare nella UE, purché successivi ad un viaggio a carico dal territorio dell’UE a quello del Regno Unito', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '430', '3118', '3', 'Un vettore italiano può effettuare fino a due viaggi a carico all’interno del territorio del Regno Unito, senza ritornare nella UE, purché effettuati entro sette giorni dallo scarico nel territorio del Regno Unito', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '430', '3119', '4', 'Un vettore italiano può effettuare fino a tre viaggi a carico all’interno del territorio del Regno Unito, senza ritornare nella UE, purché successivi ad un viaggio a carico dal territorio dell’UE a quello del Regno Unito', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '430', '3120', '5', 'Un vettore italiano può effettuare trasporti in regime di cabotaggio all’interno del territorio del Regno Unito solo se in possesso di Licenza del Regno Unito per la Comunità', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '430', '3121', '6', 'Un vettore italiano può effettuare fino a due viaggi a carico all’interno del territorio del Regno Unito, senza ritornare nella UE, purché effettuati entro quattro giorni dallo scarico nel territorio del Regno Unito', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '430', '3122', '7', 'Un vettore italiano può effettuare due viaggi a carico all’interno del territorio del Regno Unito, purché successivi a un viaggio a vuoto dal territorio dell’UE', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '430', '3123', '8', 'Un vettore italiano effettua operazioni di scambio incrociato quando svolge attività fra due punti interni al Regno Unito', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '431', '', '', 'Cabotaggio e Cross-trade - 02', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '431', '3124', '1', 'Un vettore britannico deve limitarsi a svolgere, in totale, fino a un massimo di due viaggi all’interno del territorio dell’UE prima di rientrare nel territorio del Regno Unito', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '431', '3125', '2', 'Un vettore britannico, prima di rientrare nel territorio del Regno Unito, può effettuare un solo viaggio del tipo cross-trade (operazione di scambio incrociato) oltre a uno post-scarico interno al Paese UE di arrivo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '431', '3126', '3', 'Un vettore britannico può effettuare l’unico viaggio di cabotaggio permesso entro sette giorni dallo scarico nel territorio del Paese UE di destinazione delle merci trasportate nel viaggio a carico dal territorio del Regno Unito', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '431', '3127', '4', 'Un vettore britannico può effettuare fino a due viaggi a carico da un Paese UE verso altro Paese UE, senza ritornare nel Regno Unito, purché successivi ad un viaggio a carico dal territorio del Regno Unito al territorio dell’UE', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '431', '3128', '5', 'Un vettore britannico può effettuare trasporti del tipo “cross-trade” (operazioni di scambio incrociato tra due Stati membri UE) solo se in possesso di licenza del Regno Unito per la Comunità', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '431', '3129', '6', 'Un vettore britannico può effettuare un unico viaggio a carico nel territorio di un Paese UE, purché successivo a un viaggio a carico autorizzato dal territorio del Regno Unito', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '431', '3130', '7', 'Un vettore britannico effettua operazioni di scambio incrociato quando svolge attività fra due punti appartenenti a due Stati membri UE diversi', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '431', '3131', '8', 'Un vettore britannico può effettuare generalmente cabotaggio in un singolo Paese UE per un massimo di un viaggio', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '431', '3132', '9', 'Un vettore britannico può effettuare due viaggi a carico nel territorio di un Paese UE, purché successivi a un viaggio a carico autorizzato dal territorio del Regno Unito', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '431', '3133', '10', 'Un vettore britannico non ha limiti di viaggi all’interno del territorio dell’UE prima di rientrare nel territorio del Regno Unito, se in possesso di Licenza del Regno Unito per la Comunità', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '431', '3134', '11', 'Un vettore britannico può effettuare l’unico viaggio di cabotaggio permesso entro quattro giorni dallo scarico nel territorio del Paese UE di destinazione delle merci trasportate nel viaggio a carico dal territorio del Regno Unito', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '431', '3135', '12', 'Un vettore britannico può effettuare fino a tre viaggi a carico da un Paese UE verso altro Paese UE, senza ritornare nel territorio del Regno Unito', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '431', '3136', '13', 'Il cabotaggio per i vettori britannici è consentito per tre operazioni nell’arco di sette giorni a determinate condizioni', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '431', '3137', '14', 'Un vettore britannico può effettuare trasporti in regime di cabotaggio solo se in possesso di Licenza comunitaria', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '431', '3138', '15', 'Un vettore britannico, similmente a un vettore italiano, può effettuare un numero massimo di viaggi interni al territorio dell’UE pari a tre', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '432', '', '', 'Dogane - Unione doganale', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '432', '3139', '1', 'Le dogane sono addette al controllo in entrata e in uscita delle merci', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '432', '3140', '2', 'Le dogane controllano le merci per fini fiscali', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '432', '3141', '3', 'Le dogane italiane svolgono un’attività al servizio dell’UE', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '432', '3142', '4', 'Le dogane svolgono un’attività tesa a tutelare l’industria e i consumatori', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '432', '3143', '5', 'L’unione doganale comporta il divieto, fra gli Stati membri, dei dazi doganali', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '432', '3144', '6', 'Le dogane non hanno fini che riguardano l’imposizione fiscale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '432', '3145', '7', 'Le dogane dipendono completamente dall’UE nelle cui casse finiscono tutti i dazi', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '432', '3146', '8', 'Le dogane non esistono più in quanto con l’UE sono state abbattute tutte le frontiere', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '432', '3147', '9', 'L’unione doganale comporta l’adozione di una tariffa doganale libera negli scambi fra Stati membri e Stati terzi', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '432', '3148', '10', 'L’unione doganale comporta l’istituzione, negli scambi fra gli Stati membri, dei dazi doganali', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '432', '3149', '11', 'L’unione doganale non comporta il divieto dei dazi doganali nel movimento di merci fra gli Stati membri', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '433', '', '', 'Abolizione delle  barriere doganali nella UE', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '433', '3150', '1', 'Per il trasporto di merce italiana verso il Portogallo non necessita alcun documento doganale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '433', '3151', '2', 'L’abolizione delle barriere doganali alle frontiere interne alla UE consente la libera circolazione delle persone e delle merci fra gli Stati membri', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '433', '3152', '3', 'L’abolizione delle barriere doganali alle frontiere interne alla UE consente la libera circolazione delle persone ma non delle merci', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '433', '3153', '4', 'L’abolizione delle barriere doganali alle frontiere interne alla UE consente gli scambi commerciali ma non il trasporto tra gli Stati della UE', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '433', '3154', '5', 'L’abolizione delle barriere doganali alle frontiere interne alla UE consente gli scambi commerciali ed il relativo trasporto tra gli Stati UE con appositi documenti doganali', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '433', '3155', '6', 'Per il trasporto di merce italiana verso il Portogallo necessita il documento doganale DDT (documento di trasporto)', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '433', '3156', '7', 'Per il trasporto di merce italiana verso il Portogallo necessita il documento doganale transito T2', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '433', '3157', '8', 'Per il trasporto di merce italiana verso il Portogallo necessita il documento doganale carnet TIR', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '434', '', '', 'Territorio doganale italiano', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '434', '3158', '1', 'Dall’1.1.2020 è mutato lo status doganale di Campione d’Italia', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '434', '3159', '2', 'Per territorio doganale italiano si intende il territorio italiano circoscritto dalla linea doganale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '434', '3160', '3', 'Per territorio doganale italiano si intende tutto il territorio italiano', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '434', '3161', '4', 'Per territorio doganale italiano si intende tutto il territorio italiano e quello della Corsica', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '434', '3162', '5', 'Per territorio doganale italiano si intende tutto il territorio italiano ma non quello dell’isola di Pantelleria', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '434', '3163', '6', 'I territori italiani considerati extra doganali sono quelli della Regione Friuli Venezia-Giulia', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '434', '3164', '7', 'I territori italiani considerati extra doganali sono quelli della Regione Valle d’Aosta', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '434', '3165', '8', 'I territori italiani considerati extra doganali sono quelli della città di Trieste', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '435', '', '', 'Dazi doganali', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '435', '3166', '1', 'I dazi doganali si distinguono in: specifici, sul valore, misti', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '435', '3167', '2', 'I dazi doganali sono sorti per motivi diversi da quelli fiscali', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '435', '3168', '3', 'I dazi doganali colpiscono tutte le merci che vengono trasportate all’interno della UE', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '435', '3169', '4', 'I dazi doganali non incidono sul costo delle merci', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '436', '', '', 'Dichiarazione doganale', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '436', '3170', '1', 'La dichiarazione doganale è un atto unilaterale obbligatorio: l’adempimento deve essere svolto sia in entrata sia in uscita di una merce dal territorio doganale dell’UE', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '436', '3171', '2', 'La dichiarazione doganale è un atto con cui si vincola la merce ad un determinato regime doganale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '436', '3172', '3', 'La dichiarazione doganale può avvenire per iscritto o per via informatica', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '436', '3173', '4', 'La dichiarazione doganale per l’assoggettamento ad un regime doganale di transito o sdoganamento deve essere fatta per iscritto avvalendosi anche di sistemi informatici', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '436', '3174', '5', 'La dichiarazione doganale per l’assoggettamento ad un regime doganale di transito o sdoganamento deve essere sottoscritta davanti al notaio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '436', '3175', '6', 'La dichiarazione doganale per l’assoggettamento ad un regime doganale di transito o sdoganamento deve essere fatta anche verbalmente', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '436', '3176', '7', 'La dichiarazione doganale per l’assoggettamento ad un regime doganale di transito o sdoganamento deve essere fatta anche verbalmente purché con testimoni', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '436', '3177', '8', 'La dichiarazione doganale si perfeziona con il riscontro parziale o totale dell’autorità doganale, che viene attuato al di fuori degli spazi doganali', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '436', '3178', '9', 'In base al codice UE, le dichiarazioni doganali possono essere redatte su qualunque modulo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '436', '3179', '10', 'In base al codice UE, le dichiarazioni doganali debbono essere redatte tramite atto notorio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '436', '3180', '11', 'In base al codice UE, le dichiarazioni doganali debbono essere redatte tramite atto pubblico', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '436', '3181', '12', 'Per controllare le dichiarazioni doganali, l’autorità doganale procede sempre alla visita delle merci', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '437', '', '', 'Obbligo della dichiarazione doganale', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '437', '3182', '1', 'Per le merci che devono essere esportate da Stati UE verso Stati extra UE è necessario presentare una dichiarazione doganale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '437', '3183', '2', 'In base al codice UE, le dichiarazioni doganali debbono essere redatte per iscritto su appositi formulari doganali (DAU)', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '437', '3184', '3', 'Il DAU è la sigla di Documento Amministrativo Unico', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '437', '3185', '4', 'Il DAU è la dichiarazione doganale per tutti i regimi doganali e le destinazioni doganali utilizzati dagli operatori', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '437', '3186', '5', 'Il DAU è utilizzato per le merci UE dirette verso Stati terzi', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '437', '3187', '6', 'Il DAU (Documento Amministrativo Unico) non può essere utilizzato per le merci provenienti da Stati terzi', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '437', '3188', '7', 'Il DAU (Documento Amministrativo Unico) è la dichiarazione doganale utilizzata esclusivamente per i regimi di transito UE', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '437', '3189', '8', 'Il DAU (Documento Amministrativo Unico) è utilizzato per le merci trasportate in Italia esclusivamente in ambito nazionale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '437', '3190', '9', 'Il DAU (Documento Amministrativo Unico) non può essere utilizzato per le merci UE', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '437', '3191', '10', 'Il DAU (Documento Amministrativo Unico) non può essere utilizzato per le merci che non sono in libera pratica', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '437', '3192', '11', 'Il DAU (Documento amministrativo unico) è un regime doganale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '437', '3193', '12', 'La dichiarazione doganale può avvenire solo attraverso la compilazione del formulario DAU', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '437', '3194', '13', 'Per le merci che devono essere esportate da Stati UE verso Stati extra UE non è necessario presentare alcun documento in dogana', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '437', '3195', '14', 'Tutte le merci trasportate da una regione ad un’altra devono essere munite di documenti doganali', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '438', '', '', 'Responsabile presentazione della dichiarazione dogonale', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '438', '3196', '1', 'Al momento dell’introduzione della merce estera nel territorio UE, responsabile della immediata presentazione della dichiarazione doganale all’ufficio doganale designato è chiunque provveda al trasporto', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '438', '3197', '2', 'La dichiarazione doganale può essere presentata negli uffici doganali dallo spedizioniere doganale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '438', '3198', '3', 'La dichiarazione doganale può essere presentata negli uffici doganali dal mittente stesso', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '438', '3199', '4', 'Lo spedizioniere è la persona responsabile della immediata presentazione, all’ufficio doganale designato, della dichiarazione doganale al momento dell’introduzione della merce estera in territorio UE', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '438', '3200', '5', 'Il destinatario della merce è la persona responsabile della immediata presentazione, all’ufficio doganale designato, della dichiarazione doganale al momento dell’introduzione della merce estera in territorio UE', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '438', '3201', '6', 'Il mittente è la persona responsabile della immediata presentazione all’ufficio doganale designato della dichiarazione doganale al momento dell’introduzione della merce estera in territorio UE', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '438', '3202', '7', 'Per le merci che devono essere esportate da Stati UE verso Stati extra UE c’è sempre uno spedizioniere che si occupa della presentazione della dichiarazione di esportazione alla dogana', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '438', '3203', '8', 'Per le merci che devono essere esportate da Stati UE verso Stati extra UE l’impresa di autotrasporto deve necessariamente avvalersi di un conducente extra UE', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '439', '', '', 'Contrabbando', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '439', '3204', '1', 'Il contrabbando è una violazione doganale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '439', '3205', '2', 'Il contrabbando è un reato contro la pubblica amministrazione', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '439', '3206', '3', 'Il contrabbando è un reato contro la pubblica fede', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '439', '3207', '4', 'Il contrabbando è un’attività lecita', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '440', '', '', 'Regimi doganali - Merci UE', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '440', '3208', '1', 'Le merci UE sono definite “in libera pratica”', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '440', '3209', '2', 'Per merci UE si intendono merci importate da Stati o territori che non fanno parte del territorio doganale della UE e immesse in libera pratica', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '440', '3210', '3', 'Per merci UE si intendono merci sdoganate', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '440', '3211', '4', 'Per merci UE si intendono merci interamente ottenute nel territorio doganale della UE', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '440', '3212', '5', 'Per merci UE si intendono merci dette “in libera pratica”, se sono originarie di Stati membri', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '440', '3213', '6', 'Per merci UE si intendono merci dette “non in libera pratica”, se sono originarie di Stati membri', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '440', '3214', '7', 'Per merci UE si intendono merci dette “in libera pratica nell’UE”, se non sono ancora stati pagati i dazi', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '441', '', '', 'Regimi doganali – Tipi', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '441', '3215', '1', 'Il transito tra due Stati UE di tipo esterno è un regime doganale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '441', '3216', '2', 'È un regime doganale l’immissione in libera pratica', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '441', '3217', '3', 'Il perfezionamento attivo è un regime doganale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '441', '3218', '4', 'Il perfezionamento passivo è un regime doganale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '441', '3219', '5', 'Il deposito doganale è un regime doganale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '441', '3220', '6', 'L’importazione temporanea relativa a merci UE è un regime doganale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '441', '3221', '7', 'L’introduzione in deposito, con riferimento ad operazioni di immagazzinaggio di merci UE è un regime doganale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '441', '3222', '8', 'È un regime doganale il TIR', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '441', '3223', '9', 'L’ATA è un regime doganale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '442', '', '', 'Regimi doganali - Transito esterno', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '442', '3224', '1', 'Il regime di transito esterno consente la circolazione delle merci non UE nel territorio doganale della UE', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '442', '3225', '2', 'Il regime di transito UE consente alle merci di circolare nel territorio doganale della UE senza essere assoggettate né a dazi d’importazione e altre imposte né alle misure di politica commerciale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '442', '3226', '3', 'Il regime di transito UE consente alle merci di circolare nel territorio doganale della UE con dazi d’importazione', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '442', '3227', '4', 'Il regime di transito UE consente alle merci di circolare in tutti i territori extra UE senza essere assoggettate a dazi', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '442', '3228', '5', ' Il regime di transito UE consente alle merci di circolare nel territorio extra UE senza essere assoggettate né a dazi d’importazione e altre imposte né alle misure di politica commerciale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '442', '3229', '6', 'Il regime di transito esterno consente la circolazione delle merci UE nel territorio doganale della UE', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '442', '3230', '7', 'Il regime di transito esterno consente la circolazione delle merci non UE nel territorio doganale della UE con dazi all’importazione', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '442', '3231', '8', 'Il regime di transito esterno consente la circolazione delle merci UE nel territorio doganale della UE con dazi all’importazione', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '443', '', '', 'Dichiarazione doganale di esportazione definitiva', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '443', '3232', '1', 'Per le merci che devono essere esportate da Stati UE verso Stati extra UE al termine delle operazioni doganali, l’esportatore entra in possesso della bolletta doganale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '443', '3233', '2', 'Per le merci che devono essere esportate da Stati UE verso Stati extra UE è necessario che le cose siano accompagnate da una bolletta doganale fino all’uscita dal confine doganale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '443', '3234', '3', 'Per le merci che devono essere esportate da Stati UE verso Stati extra UE è necessaria una dichiarazione doganale di esportazione, presentata alla dogana su appositi formulari DAU (Documento Amministrativo Unico)', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '443', '3235', '4', 'Per le merci che devono essere esportate da Stati UE verso Stati extra UE è possibile presentare la dichiarazione doganale avvalendosi di sistemi informatici', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '443', '3236', '5', 'Per le merci che devono essere esportate da Stati UE verso Stati extra UE è possibile presentare la dichiarazione doganale solo avvalendosi di sistemi informatici ', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '444', '', '', 'Dichiarazione doganale T1', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '444', '3237', '1', 'All’arrivo in dogana di merce con dicitura T1 in dichiarazione doganale, la Guardia di finanza appone il visto entrare negli spazi doganali, controlla i piombi se applicati e consegna il documento alla dogana', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '444', '3238', '2', 'All’arrivo in dogana in Italia di merce con dicitura T1 in dichiarazione doganale, al varco di entrata la merce deve essere presentata al posto di guardia della Guardia di finanza che controlla i suggelli ed appone il visto entrare sul documento T1', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '444', '3239', '3', 'Il documento DAU T1, all’ingresso degli spazi doganali, va presentato alla Guardia di finanza', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '444', '3240', '4', 'Il documento DAU T1, all’ingresso degli spazi doganali, va presentato allo spedizioniere doganale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '444', '3241', '5', 'Il documento DAU T1, all’ingresso degli spazi doganali, va presentato al funzionario di dogana', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '444', '3242', '6', 'Il documento DAU T1, all’ingresso degli spazi doganali, va presentato al destinatario', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '444', '3243', '7', 'All’arrivo in dogana della merce vincolata alla dichiarazione doganale T1, la Guardia di finanza non vista nessun documento', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '444', '3244', '8', 'All’arrivo in dogana della merce vincolata alla dichiarazione doganale T1, la Guardia di finanza riceve il documento dal conducente e lo consegna agli uffici doganali senza altra formalità', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '444', '3245', '9', 'All’arrivo in dogana della merce vincolata alla dichiarazione doganale T1, la Guardia di finanza può verificarla a campione', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '444', '3246', '10', 'All’arrivo in dogana in Italia, la merce vincolata alla dichiarazione doganale T1, deve essere presentata al funzionario doganale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '444', '3247', '11', 'All’arrivo in dogana in Italia, la merce vincolata alla dichiarazione doganale T1, deve essere presentata allo spedizioniere doganale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '444', '3248', '12', 'All’arrivo in dogana in Italia, la merce vincolata alla dichiarazione doganale T1, deve essere presentata al Ministero delle infrastrutture e della mobilità sostenibili', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '445', '', '', 'Servizio di riscontro e Orari sdoganamento', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '445', '3249', '1', 'Gli orari per lo sdoganamento delle merci nelle principali dogane italiane di confine terrestre solitamente sono dalle ore 8,00 alle ore 18,00', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '445', '3250', '2', 'Il servizio doganale di riscontro è esercitato da militari della Guardia di finanza', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '445', '3251', '3', 'Il servizio doganale di riscontro è esercitato da funzionari di dogana', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '445', '3252', '4', 'Il servizio doganale di riscontro è esercitato dallo spedizioniere doganale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '445', '3253', '5', 'Il servizio doganale di riscontro è esercitato dai carabinieri', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '445', '3254', '6', 'Gli orari per lo sdoganamento delle merci nelle principali dogane italiane di confine terrestre solitamente sono dalle ore 6,00 alle ore 22,00', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '445', '3255', '7', 'Gli orari per lo sdoganamento delle merci nelle principali dogane italiane di confine terrestre solitamente sono dalle ore 8,00 alle 14,00 e dalle ore 16,00 alle ore 18,00', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '445', '3256', '8', 'Gli orari per lo sdoganamento delle merci nelle principali dogane italiane di confine terrestre solitamente sono dalle ore 8,00 alle 20,00', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '446', '', '', ' Dichiarazione sommaria scritta', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '446', '3257', '1', 'Per le merci extra UE in entrata in Italia non scortate da documenti doganali di transito, la dichiarazione sommaria scritta va presentata dal detentore delle merci o dal suo rappresentante', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '446', '3258', '2', 'Le merci in arrivo nel territorio doganale italiano, qualora non abbiano già ricevuto una destinazione doganale, debbono formare oggetto di una dichiarazione sommaria scritta presso la dogana di entrata', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '446', '3259', '3', 'Le merci in arrivo nel territorio doganale italiano, qualora non abbiano già ricevuto una destinazione doganale, debbono formare oggetto di una dichiarazione sommaria verbale presso qualsiasi dogana', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '446', '3260', '4', 'Le merci in arrivo nel territorio doganale italiano, qualora non abbiano già ricevuto una destinazione doganale, debbono formare oggetto di dichiarazione sommaria verbale al Ministero delle infrastrutture e della mobilità sostenibili', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '446', '3261', '5', 'Le merci in arrivo nel territorio doganale italiano, qualora non abbiano già ricevuto una destinazione doganale, debbono formare oggetto di dichiarazione sommaria verbale al Comune di entrata', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '446', '3262', '6', 'Per le merci extra UE in entrata in Italia non scortate da documenti doganali di transito, la dichiarazione sommaria scritta può essere presentata da qualsiasi persona', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '446', '3263', '7', 'Per le merci extra UE in entrata in Italia non scortate da documenti doganali di transito, la dichiarazione sommaria scritta va presentata dal magazziniere', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '447', '', '', 'Magazzini e recinti di temporanea custodia', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '447', '3264', '1', 'I magazzini di temporanea custodia sono solitamente gestiti dalla Dogana', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '447', '3265', '2', 'I magazzini e i recinti doganali di temporanea custodia sono solitamente situati negli spazi doganali della dogana', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '447', '3266', '3', 'I magazzini e i recinti doganali di temporanea custodia sono solitamente situati in qualsiasi luogo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '447', '3267', '4', 'I magazzini e i recinti doganali di temporanea custodia sono solitamente situati entro 50 km dal confine', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '447', '3268', '5', 'I magazzini e i recinti doganali di temporanea custodia sono solitamente situati nelle grandi città', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '447', '3269', '6', 'I magazzini di cosiddetta temporanea custodia sono solitamente gestiti dai singoli trasportatori', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '447', '3270', '7', 'I magazzini di temporanea custodia sono solitamente gestiti dagli spedizionieri', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '447', '3271', '8', 'I magazzini di temporanea custodia sono solitamente gestiti dal magazziniere', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '448', '', '', 'Ammanchi nei magazzini e recinti di temporanea custodia', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '448', '3272', '1', 'Per eventuali ammanchi di merce nei magazzini doganali di temporanea custodia gestiti da privati, dei diritti di confine risponde il gestore del magazzino', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '448', '3273', '2', 'In mancanza di merce nei magazzini doganali di temporanea custodia gestiti da privati, dei diritti di confine risponde il trasportatore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '448', '3274', '3', 'In mancanza di merce nei magazzini doganali di temporanea custodia gestiti da privati, dei diritti di confine risponde lo spedizioniere', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '448', '3275', '4', 'In mancanza di merce nei magazzini doganali di temporanea custodia gestiti da privati, dei diritti di confine risponde il mittente', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '449', '', '', 'Zone franche', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '449', '3276', '1', 'Nelle zone franche o depositi franchi le merci non UE sono considerate come merci non situate nel territorio doganale della UE', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '449', '3277', '2', 'Nelle zone franche o depositi franchi è di solito conveniente l’acquisto di alcolici, tabacchi, profumi, zucchero e carburanti', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '449', '3278', '3', 'All’uscita delle merci da una zona o deposito franco per il controllo doganale occorre esibire una copia del documento di trasporto o del documento doganale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '449', '3279', '4', 'All’uscita delle merci da una zona o deposito franco per il controllo doganale occorre esibire le fatture delle merci', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '449', '3280', '5', 'All’uscita delle merci da una zona o deposito franco per il controllo doganale non occorre esibire alcun documento', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '449', '3281', '6', 'All’uscita delle merci da una zona o deposito franco per il controllo doganale occorre esibire il DDT (documento di trasporto)', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '449', '3282', '7', 'Nelle zone franche o depositi franchi il versamento per il trasporto viene effettuato da chi spedisce la merce', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '449', '3283', '8', 'Nelle zone franche o depositi franchi vengono depositate le merci in attesa dell’immissione in libera pratica', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '449', '3284', '9', 'Nelle zone franche o depositi franchi è comunque obbligatoria la dichiarazione doganale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '449', '3285', '10', 'Nelle zone franche o depositi franchi occorre pagare una franchigia per le merci depositate', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '450', '', '', 'Convenzione internazionale TIR - 01', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '450', '3286', '1', 'Il transito doganale in regime TIR è applicato alle merci trasportate su veicoli stradali e contenitori che attraversano Stati che aderiscono alla Convenzione TIR', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '450', '3287', '2', 'I veicoli stradali e gli autotreni, per essere impiegati nei trasporti in regime TIR, devono avere specifiche caratteristiche tecniche', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '450', '3288', '3', 'I veicoli stradali e gli autotreni, per essere impiegati nei trasporti in regime TIR, devono essere provvisti di targa rettangolare, di colore azzurro, recante la sigla “TIR”', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '450', '3289', '4', 'Le imprese di autotrasporto, per ottenere il carnet TIR in Italia, devono essere iscritte nell’apposito registro istituito presso Unione italiana delle Camere di Commercio', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '450', '3290', '5', 'I veicoli stradali o gli autotreni impiegati per effettuare un’operazione di trasporto TIR sono provvisti di una targa rettangolare recante l’iscrizione “TIR” apposta nella parte laterale della carrozzeria', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '451', '', '', 'Convenzione internazionale TIR - 02', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '451', '3291', '1', 'I veicoli stradali o gli autotreni impiegati per effettuare un’operazione di trasporto TIR sono provvisti di una targa rettangolare recante l’iscrizione “TIR”', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '451', '3292', '2', 'La targa TIR individua la Convenzione “Transports Internationaux Routiers”', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '451', '3293', '3', 'La targa TIR è costituita da una tabella rettangolare di colore azzurro, posta sul retro di alcuni autoveicoli, indica che il veicolo circola in regime TIR', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '451', '3294', '4', 'La targa TIR, apposta sul veicolo, indica che lo stesso viaggia in regime TIR', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '451', '3295', '5', 'La targa TIR indica che il veicolo su cui è apposta non è dotato di carnet TIR', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '451', '3296', '6', 'La targa TIR indica che il veicolo su cui è apposta è stato costruito in Francia secondo rigide direttive europee', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '451', '3297', '7', 'La targa TIR è posta sul retro di alcuni veicoli destinati al trasporto di merci in conto proprio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '451', '3298', '8', 'La targa TIR individua i grandi veicoli di trasporto su strada', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '451', '3299', '9', 'La targa TIR deve essere esposta su tutti gli autoveicoli di massa complessiva superiore a 3,5 t', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '452', '', '', 'Convenzione internazionale TIR - 03', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '452', '3300', '1', 'Il carnet TIR è un documento riconosciuto a livello internazionale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '452', '3301', '2', 'Il carnet TIR è previsto dalla Convenzione TIR cui aderiscono l’UE e molti Stati extra UE', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '452', '3302', '3', 'Il carnet TIR permette di trasportare merce senza che il trasportatore si debba fermare per controlli tassativi alle dogane', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '452', '3303', '4', 'Il carnet TIR è assegnato al singolo veicolo e al singolo container', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '452', '3304', '5', 'Il carnet TIR è stampato in inglese', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '452', '3305', '6', 'Il carnet TIR ha durata annuale, dal momento in cui viene acceso', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '452', '3306', '7', 'Il carnet TIR in Italia è rilasciato dall’IRU', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '452', '3307', '8', 'Il carnet TIR è rilasciato a qualsiasi trasportatore che ne faccia richiesta, purché dimostri di effettuare solo trasporti in ambito nazionale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '453', '', '', 'Convenzione internazionale TIR - 04', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '453', '3308', '1', 'Il carnet TIR è un documento doganale valido negli Stati aderenti alla Convenzione TIR', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '453', '3309', '2', 'Il carnet TIR permette di semplificare le formalità doganali cui sono soggetti i trasporti su strada tra Stati aderenti', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '453', '3310', '3', 'Il carnet TIR permette di semplificare le formalità doganali cui sono soggetti i trasporti, se effettuati senza rottura di carico', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '453', '3311', '4', 'Il carnet TIR agevola i controlli presso gli uffici doganali di partenza e di destinazione', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '453', '3312', '5', 'Il carnet TIR permette di semplificare le formalità doganali per i trasporti effettuati con rottura di carico', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '453', '3313', '6', 'Il carnet TIR è un documento doganale valido solo negli Stati membri dell’UE', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '453', '3314', '7', 'Il carnet TIR deve essere conservato presso la sede del trasportatore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '453', '3315', '8', ' Il carnet TIR è obbligatorio per effettuare trasporti infracomunitari senza doversi fermare nelle dogane interne', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '453', '3316', '9', 'Il carnet TIR viene rilasciato in due copie originali da cui è poi necessario ottenere le copie conformi', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '453', '3317', '10', 'Il carnet TIR evita i controlli e il versamento dei diritti doganali di transito alle dogane', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '454', '', '', 'Convenzione internazionale TIR - 05', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '454', '3318', '1', 'Il carnet TIR può essere utilizzato solo per trasporti effettuati “senza rottura di carico”', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '454', '3319', '2', 'Il carnet TIR può essere utilizzato solo dalle imprese residenti in Stati che aderiscono alla Convenzione TIR', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '454', '3320', '3', 'Il carnet TIR è un documento doganale che accompagna le merci e permette di agevolare il trasporto', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '454', '3321', '4', 'Il carnet TIR può essere impiegato anche se il trasporto prevede l’attraversamento di Stati terzi', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '454', '3322', '5', 'Il carnet TIR può essere utilizzato solo per trasporti effettuati “senza rottura di carico”, cioè solo nel caso in cui non si verifichino incidenti', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '454', '3323', '6', 'Il carnet TIR non è applicabile ai percorsi combinati in quanto presuppongono sempre una “rottura di carico”', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '454', '3324', '7', 'Il carnet TIR non può essere impiegato se il trasporto prevede l’attraversamento di Stati terzi', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '454', '3325', '8', 'Il carnet TIR può essere impiegato solo per trasporti infracomunitari', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '455', '', '', 'Convenzione internazionale TIR - 06', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '455', '3326', '1', 'La richiesta del carnet va rivolta alla Camera di Commercio competente per territorio o da organizzazioni analoghe, aderenti alla catena di garanzia internazionale ATA (convenzione doganale per temporanea importazione merci)', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '455', '3327', '2', 'Il carnet ATA (convenzione doganale per temporanea importazione merci) è un documento doganale per facilitare le importazioni temporanee', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '455', '3328', '3', 'Il carnet ATA (convenzione doganale per temporanea importazione merci) è un documento doganale internazionale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '455', '3329', '4', 'Il carnet ATA (convenzione doganale per temporanea importazione merci) è un documento doganale internazionale istituito dalla Convenzione di Bruxelles nel 1961', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '455', '3330', '5', 'Il carnet ATA (convenzione doganale per temporanea importazione merci) ha la stessa funzione del carnet TIR', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '455', '3331', '6', ' Il carnet ATA (convenzione doganale per temporanea importazione merci) prevede che le formalità vengano espletate in tutte le dogane attraversate', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '455', '3332', '7', 'Il carnet ATA (convenzione doganale per temporanea importazione merci) è rilasciato dall’IRU', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '455', '3333', '8', 'Il carnet ATA (convenzione doganale per temporanea importazione merci) ha validità di 5 anni', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '456', '', '', 'Documenti del trasporto e del conducente ADR', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '456', '3334', '1', 'A bordo dei veicoli adibiti al trasporto di sostanze pericolose con modalità diverse dalla cisterna deve trovarsi il documento di trasporto per le merci pericolose', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '456', '3335', '2', 'A bordo dei veicoli adibiti al trasporto di sostanze pericolose in cisterna deve trovarsi la carta di qualificazione del conducente (CQC) se dovuta', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '456', '3336', '3', 'A bordo dei veicoli adibiti al trasporto di sostanze pericolose in cisterna devono trovarsi le istruzioni scritte per trasporto di merci pericolose', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '456', '3337', '4', 'A bordo dei veicoli adibiti al trasporto di sostanze pericolose con modalità diverse dalla cisterna deve trovarsi il certificato di carico del container (se ricorre)', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '456', '3338', '5', 'A bordo dei veicoli adibiti al trasporto di sostanze pericolose in cisterna deve trovarsi il certificato di formazione professionale del conducente per trasporto merci pericolose', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '456', '3339', '6', 'A bordo dei veicoli adibiti al trasporto di sostanze pericolose in cisterna deve trovarsi il certificato ATP', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '456', '3340', '7', 'A bordo dei veicoli adibiti al trasporto di sostanze pericolose in cisterna deve trovarsi il giornale di viaggio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '457', '', '', 'Requisiti per trasporto rifiuti', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '457', '3341', '1', 'I rifiuti devono essere accompagnati da particolari documenti', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '457', '3342', '2', 'A bordo dei veicoli adibiti al trasporto di rifiuti deve trovarsi il provvedimento finale di iscrizione all’Albo dei gestori ambientali', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '457', '3343', '3', 'A bordo dei veicoli di enti e imprese che trasportano rifiuti deve trovarsi il formulario di identificazione rifiuti (FIR)', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '457', '3344', '4', 'A bordo dei veicoli adibiti al trasporto di rifiuti deve trovarsi il certificato di carico del container (se ricorre)', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '457', '3345', '5', 'A bordo dei veicoli adibiti al trasporto di rifiuti devono trovarsi le istruzioni scritte per trasporto merci pericolose', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '458', '', '', 'Documenti per trasporti ATP - Alimenti sfusi', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '458', '3346', '1', 'Per trasportare latte ed alimenti in genere è necessaria una dichiarazione di scorta o documento equipollente', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '458', '3347', '2', 'A bordo dei veicoli adibiti al trasporto di alimenti sfusi deve trovarsi l’autorizzazione sanitaria di idoneità del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '458', '3348', '3', 'A bordo dei veicoli adibiti al trasporto di alimenti sfusi non è necessaria alcuna particolare autorizzazione', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '458', '3349', '4', 'A bordo dei veicoli adibiti al trasporto di alimenti sfusi deve trovarsi l’autorizzazione dell’ente proprietario della strada', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '458', '3350', '5', 'A bordo dei veicoli adibiti al trasporto di alimenti sfusi deve trovarsi la licenza comunale per l’esercizio del servizio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '458', '3351', '6', 'A bordo dei veicoli adibiti al trasporto di alimenti sfusi deve trovarsi l’autorizzazione per circolazione di prova', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '458', '3352', '7', 'I prodotti destinati all’alimentazione umana ad eccezione del latte e dei suoi derivati devono essere accompagnati da particolari documenti', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '459', '', '', 'Trasporti Sfarinati e farine in cisterna', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '459', '3353', '1', 'Per il trasporto in cisterna di sfarinati e farine, destinate all’alimentazione umana, è consigliato che sia corredato dalla ricevuta della presentazione della SCIA (segnalazione certificata di inizio attività)', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '459', '3354', '2', 'La cisterna per il trasporto di sfarinati e farine, destinate all’alimentazione umana, deve avere sigilli integri, atti a garantire che il carico non sia stato manomesso', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '459', '3355', '3', 'Il trasporto in cisterna di sfarinati e farine, destinate all’alimentazione umana, deve essere accompagnato da un documento indicante il tipo di merce trasportata, la ragione sociale del fornitore e del destinatario, la sede di partenza e di arrivo della merce', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '459', '3356', '4', 'Il trasporto in cisterna di sfarinati e farine destinate all’alimentazione umana deve essere accompagnato da nessun documento specifico', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '459', '3357', '5', 'Il trasporto in cisterna di sfarinati e farine destinate all’alimentazione umana deve essere accompagnato da una autocertificazione del vettore di avere maturato una esperienza specifica nei suddetti trasporti', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '459', '3358', '6', 'Il trasporto in cisterna di sfarinati e farine destinate all’alimentazione umana richiede il possesso del certificato di formazione professionale -CFP -del conducente', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '460', '', '', 'Documenti per trasporto di alimenti surgelati', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '460', '3359', '1', 'A bordo dei veicoli adibiti al trasporto di alimenti surgelati deve trovarsi la SCIA (segnalazione certificata di inizio attività) per l’attività svolta', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '460', '3360', '2', 'A bordo dei veicoli adibiti al trasporto di alimenti surgelati deve trovarsi l’attestato ATP non scaduto di validità', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '460', '3361', '3', 'A bordo dei veicoli adibiti al trasporto di alimenti surgelati deve trovarsi la carta di circolazione', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '460', '3362', '4', 'Le carni a bordo di un veicolo devono avere il bollo sanitario e i documenti di accompagnamento', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '460', '3363', '5', 'A bordo dei veicoli adibiti al trasporto di alimenti surgelati deve trovarsi la licenza comunale per l’esercizio del servizio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '460', '3364', '6', 'A bordo dei veicoli adibiti al trasporto di alimenti surgelati deve trovarsi il documento di accompagnamento degli animali', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '461', '', '', 'Documenti per trasporto di carni', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '461', '3365', '1', 'A bordo dei veicoli adibiti al trasporto di carni deve trovarsi la SCIA sanitaria di idoneità del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '461', '3366', '2', 'A bordo dei veicoli adibiti al trasporto di carni deve trovarsi la licenza comunale per l’esercizio del servizio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '461', '3367', '3', 'A bordo dei veicoli adibiti al trasporto di carni deve trovarsi il foglio di registrazione (disco) se con tachigrafo digitale ovvero la carta tachigrafica se con tachigrafo analogico', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '462', '', '', ' Documenti per trasporto di animali vivi', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '462', '3368', '1', 'A bordo dei veicoli adibiti al trasporto di animali vivi deve trovarsi il documento di accompagnamento degli animali', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '462', '3369', '2', 'A bordo dei veicoli adibiti al trasporto di animali vivi deve trovarsi il giornale di viaggio', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '462', '3370', '3', 'A bordo dei veicoli adibiti al trasporto di animali vivi deve trovarsi l’autorizzazione dell’ente proprietario della strada', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '462', '3371', '4', 'A bordo dei veicoli adibiti al trasporto di animali vivi deve trovarsi la licenza comunale per l’esercizio del servizio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '462', '3372', '5', 'A bordo dei veicoli adibiti al trasporto di animali vivi deve trovarsi l’autorizzazione per la circolazione di prova', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '463', '', '', 'Documenti per trasporto eccezionale', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '463', '3373', '1', 'A bordo dei veicoli eccezionali deve trovarsi l’autorizzazione dell’ente proprietario della strada', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '463', '3374', '2', 'A bordo dei veicoli eccezionali deve trovarsi anche l’autorizzazione a circolare senza scorta di polizia (se ricorre il caso)', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '463', '3375', '3', 'A bordo dei veicoli eccezionali deve trovarsi la carta di circolazione del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '463', '3376', '4', 'A bordo dei veicoli eccezionali deve trovarsi l’autorizzazione per circolazione di prova', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '463', '3377', '5', 'A bordo dei veicoli eccezionali deve trovarsi la licenza comunale per l’esercizio del servizio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '463', '3378', '6', 'A bordo dei veicoli eccezionali deve trovarsi il foglio di registrazione (disco) se con tachigrafo digitale ovvero la carta tachigrafica se con tachigrafo analogico', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '464', '', '', ' Documenti per merci soggette ad accise', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '464', '3379', '1', 'Non occorre la bolla di accompagnamento (XAB) per il trasporto di acqua', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '464', '3380', '2', 'Per trasportare tabacchi è necessario avere un documento che certifichi la posizione delle merci in merito all’accisa', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '464', '3381', '3', 'Per trasportare fiammiferi è necessario un documento che certifichi la posizione delle merci in merito all’accisa', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '464', '3382', '4', 'Per il trasporto internazionale di benzina occorre e-AD', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '464', '3383', '5', 'Per trasportare benzina non è obbligatorio compilare una scheda tecnica contenente le indicazioni e le precauzioni da usare in caso di incedente o di perdita del carico', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '464', '3384', '6', 'Per trasportare merci di tutti i generi non è più usata la fattura accompagnatoria', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '464', '3385', '7', 'Per trasportare petrolio è obbligatoria la lettera di vettura', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '464', '3386', '8', 'Per trasportare bevande alcoliche non serve il DAS (Documento di accompagnamento semplificato) ma il DAA (Documento di accompagnamento accise)', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '464', '3387', '9', 'Per oli minerali non occorre la bolla di accompagnamento (XAB)', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '464', '3388', '10', 'Le piante ma non le sementi devono essere accompagnati da particolari documenti', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '464', '3389', '11', 'Tutte le merci trasportate devono essere accompagnati da particolari documenti', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '465', '', '', ' Documenti per trasporto in conto proprio extra UE', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '465', '3390', '1', 'A bordo dei veicoli per trasporto merci in conto proprio in ambito extra UE deve trovarsi anche la carta di circolazione', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '465', '3391', '2', 'A bordo dei veicoli per trasporto merci in conto proprio in ambito extra UE deve trovarsi la patente di guida del conducente C o CE in relazione al tipo di veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '465', '3392', '3', 'A bordo dei veicoli per trasporto merci in conto proprio in ambito extra UE deve trovarsi l’autorizzazione internazionale emessa dallo Stato extra UE o autorizzazione CEMT', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '465', '3393', '4', 'A bordo dei veicoli per trasporto merci in conto proprio in ambito extra UE deve trovarsi la copia certificata conforme della licenza comunitaria', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '465', '3394', '5', 'A bordo dei veicoli per trasporto merci in conto proprio in ambito extra UE deve trovarsi l’attestazione per il viaggio rilasciata dall’UMC su modello DTT', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '466', '', '', 'Documenti per trasporto in conto terzi in ambito extra UE', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '466', '3395', '1', 'A bordo dei veicoli per trasporto merci conto terzi in ambito extra UE deve trovarsi la patente di guida del conducente in relazione al veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '466', '3396', '2', 'A bordo dei veicoli per trasporto merci conto terzi in ambito extra UE deve trovarsi la carta di circolazione', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '466', '3397', '3', 'A bordo dei veicoli per trasporto merci conto terzi in ambito extra UE deve trovarsi la copia certificata conforme della licenza comunitaria', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '466', '3398', '4', 'A bordo dei veicoli per trasporto merci conto terzi in ambito extra UE deve sempre trovarsi sempre il certificato di approvazione modello DTT 307 (c.d. barrato rosa)', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('12C ', '466', '3399', '5', 'A bordo dei veicoli per trasporto merci conto terzi in ambito extra UE deve trovarsi la licenza comunale per l’esercizio del servizio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '467', '', '', 'Scelta della modalità di trasporto merci', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '467', '3400', '1', 'La modalità del trasporto delle merci viene scelta anche in base a fattori tecnici relativi al trasporto, tra cui la distanza', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '467', '3401', '2', 'La modalità del trasporto delle merci viene scelta anche in base a fattori economici', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '467', '3402', '3', 'I trasporti delle merci terrestri si possono distinguere in trasporti su strada e su rotaia', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '467', '3403', '4', 'I trasporti delle merci si distinguono in: terrestri, per le vie d’acqua ed aerei', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '467', '3404', '5', 'I trasporti delle merci su lunga distanza avvengono prevalentemente su rotaia', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '467', '3405', '6', 'I trasporti delle merci terrestri possono distinguere in trasporti su strada e su gomma', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '467', '3406', '7', 'I trasporti delle merci possono avvenire con diverse modalità che sono alternative e non complementari', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '468', '', '', 'Crescita del trasporto merci su strada ', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '468', '3407', '1', 'La crescita del trasporto merci su strada aumenta la possibilità di incidenti stradali', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '468', '3408', '2', 'La crescita del trasporto merci su strada è aumentata negli ultimi dieci anni', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '468', '3409', '3', 'La crescita del trasporto merci su strada non influisce sull’effetto serra', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '468', '3410', '4', 'La crescita del trasporto merci su strada non influisce sulla sicurezza', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '468', '3411', '5', 'La crescita del trasporto merci su strada non influisce sull’impatto ambientale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '469', '', '', 'Trasporto stradale merci', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '469', '3412', '1', 'Il trasporto stradale di merci è aumentato con l’abbattimento delle frontiere attuato dall’UE', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '469', '3413', '2', 'Il trasporto stradale di merci è più inquinante di quello marittimo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '469', '3414', '3', 'Il trasporto stradale di merci internazionale è una delle cause principali della congestione dei grandi assi stradali europei', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '469', '3415', '4', 'Il trasporto stradale di merci è aumentato sensibilmente negli ultimi decenni, causando l’incremento delle emissioni inquinanti', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '469', '3416', '5', 'Il trasporto stradale di merci è meno inquinante del trasporto ferroviario', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '469', '3417', '6', 'Il trasporto stradale di merci è la seconda modalità di trasporto utilizzata in ambito europeo, subito dopo il trasporto marittimo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '469', '3418', '7', 'Il trasporto stradale di merci è completamente liberalizzato in tutto il continente europeo, a seguito dell’abbattimento delle frontiere', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '470', '', '', 'Vantaggi del trasporto stradale', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '470', '3419', '1', 'Il trasporto stradale consente la consegna “porta a porta”', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '470', '3420', '2', 'Il trasporto stradale di merci è la modalità di trasporto maggiormente utilizzata', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '470', '3421', '3', 'Il trasporto stradale di merci garantisce, in certi casi, migliore tempestività del servizio', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '470', '3422', '4', 'Il trasporto stradale non crea problemi di inquinamento ambientale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '470', '3423', '5', 'Il trasporto stradale non è rilevante in termini di tributo di vite umane', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '470', '3424', '6', 'Il trasporto stradale ha livelli di sicurezza molto alti rispetto alle altre modalità di trasporto', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '471', '', '', 'Principali vantaggi del trasporto su gomma', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '471', '3425', '1', 'Tra i principali vantaggi del trasporto su gomma vi è l’esecuzione di un servizio “porta a porta”', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '471', '3426', '2', 'Tra i principali vantaggi del trasporto su gomma vi è la tempestività', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '471', '3427', '3', 'Tra i principali vantaggi del trasporto su gomma vi è la possibilità di raggiungere località non raggiungibili con altre modalità di trasporto', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '471', '3428', '4', 'Nel trasporto su gomma vi è la possibilità di ottimizzare l’utilizzo dei veicoli con “carichi di ritorno”', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '471', '3429', '5', 'Tra i principali vantaggi del trasporto su gomma vi è la possibilità di evitare le code', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '471', '3430', '6', 'Tra i principali vantaggi del trasporto su gomma vi è la possibilità di evitare il traffico', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '471', '3431', '7', 'Tra i principali vantaggi del trasporto su gomma vi è l’aumento dei gas serra', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '472', '', '', 'Caratterristiche del trasporto su gomma', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '472', '3432', '1', 'La modalità di trasporto su gomma si caratterizza per il fatto che possono esercitare l’attività solo aziende specializzate nel settore', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '472', '3433', '2', 'La modalità di trasporto su gomma si caratterizza per le due modalità con cui può essere esercitato: in conto proprio o per conto di terzi', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '472', '3434', '3', 'La modalità di trasporto su gomma si caratterizza per la possibilità di raggiungere località ove manca la ferrovia', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '472', '3435', '4', 'La modalità di trasporto su gomma si caratterizza per la possibilità di un migliore utilizzo dei veicoli mediante l’acquisizione di carichi “di ritorno”', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '472', '3436', '5', 'La modalità di trasporto su gomma si caratterizza, in certi casi, per la tempestività e l’efficienza del servizio', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '472', '3437', '6', 'La modalità di trasporto su gomma si caratterizza per l’esecuzione di un servizio “porta a porta”', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '472', '3438', '7', 'La modalità di trasporto su gomma si caratterizza per la possibilità dell’imprenditore di evitare l’emissione delle fatture', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '472', '3439', '8', 'La modalità di trasporto su gomma si caratterizza per l’economicità rispetto a tutti gli altri metodi', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '473', '', '', 'Vantaggi del trasporto ferroviario 01', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '473', '3440', '1', 'Il trasporto ferroviario, rispetto a quello stradale, è più sicuro', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '473', '3441', '2', 'Il trasporto ferroviario, rispetto a quello stradale, è meno costoso in termini di inquinamento', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '473', '3442', '3', 'Il trasporto ferroviario, rispetto a quello stradale, è meno adatto al mercato porta a porta', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '473', '3443', '4', 'Il trasporto ferroviario, rispetto a quello stradale, è meno costoso in termini energetici', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '473', '3444', '5', 'Il trasporto su rotaia di merci comporta dei benefici in materia di consumo di energia e d’impatto sull’ambiente', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '473', '3445', '6', 'Il trasporto ferroviario, rispetto a quello stradale, è più costoso in termini di inquinamento', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '473', '3446', '7', 'Il trasporto ferroviario, rispetto a quello stradale, è meno sicuro', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '473', '3447', '8', 'Il trasporto ferroviario, rispetto a quello stradale, è più adatto al mercato porta a porta', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '473', '3448', '9', 'Il trasporto ferroviario, rispetto a quello stradale, è più costoso in termini energetici', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '474', '', '', 'Vantaggi del trasporto ferroviario  02', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '474', '3449', '1', 'Lo sfruttamento della modalità ferroviaria offre maggiori vantaggi in termini di sicurezza del trasporto', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '474', '3450', '2', 'Lo sfruttamento della modalità ferroviaria di trasporto offre vantaggi in termini di impatto sull’ambiente', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '474', '3451', '3', 'Lo sfruttamento della modalità ferroviaria offre vantaggi in termini di sicurezza del trasporto', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '474', '3452', '4', 'Lo sfruttamento della modalità ferroviaria di trasporto offre vantaggi sul consumo di energia', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '474', '3453', '5', 'Lo sfruttamento della modalità ferroviaria di trasporto offre svantaggi in termini di consumo di energia', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '474', '3454', '6', 'Lo sfruttamento della modalità ferroviaria offre vantaggi in termini di capillarità del trasporto', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '474', '3455', '7', 'Lo sfruttamento della modalità ferroviaria offre vantaggi in termini di rapidità del trasporto', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '474', '3456', '8', 'Lo sfruttamento della modalità ferroviaria offre svantaggi in termini di sicurezza del trasporto', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '475', '', '', 'Trasporto marittimo', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '475', '3457', '1', 'Il trasporto marittimo contribuisce alla riduzione della congestione della rete stradale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '475', '3458', '2', 'Il trasporto marittimo richiede ancora sviluppo e potenziamento delle vie navigabili interne', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '475', '3459', '3', 'Il trasporto marittimo di merci è in grado di contribuire alla riduzione della congestione della rete stradale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '475', '3460', '4', 'I trasporti delle merci avvengono in prevalenza per via d’acqua', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '475', '3461', '5', 'Il trasporto marittimo a corto raggio, non supplisce mai alla congestione di determinate infrastrutture stradali', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '475', '3462', '6', 'Il trasporto marittimo non è interessante per il trasporto combinato multimediale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '475', '3463', '7', 'Il trasporto marittimo non richiede investimenti importanti per il miglioramento dei servizi', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '475', '3464', '8', 'Il trasporto marittimo è economicamente meno conveniente delle altre modalità di trasporto', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '476', '', '', 'Trasporto aereo', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '476', '3465', '1', 'Il trasporto aereo di merci ha registrato un aumento dei costi per la sicurezza a seguito delle disposizioni emanate in materia, dalla UE', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '476', '3466', '2', 'Il trasporto aereo di merci in ambito internazionale non è più consentito per motivi di sicurezza', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '476', '3467', '3', 'Il trasporto aereo delle merci non risente dei controlli che sono veloci', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '476', '3468', '4', 'Il trasporto aereo delle merci ha adeguate infrastrutture logistiche di accesso', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '476', '3469', '5', 'Il trasporto aereo delle merci non soffre di frammentarietà del sistema a livello europeo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '477', '', '', 'Principali difficoltà del sistema dei trasporti europei ', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '477', '3470', '1', 'Le principali difficoltà del sistema dei trasporti europei sono dovute alla crescita squilibrata delle diverse modalità di trasporto', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '477', '3471', '2', 'Le principali difficoltà del sistema dei trasporti europei sono dovute all’inosservanza delle norme sociali e di sicurezza', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '477', '3472', '3', 'Le principali difficoltà del sistema dei trasporti europei sono dovute all’utilizzo insufficiente della modalità di trasporto stradale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '477', '3473', '4', 'Le principali difficoltà del sistema dei trasporti europei sono dovute all’utilizzo insufficiente della modalità di trasporto aereo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '477', '3474', '5', 'Le principali difficoltà del sistema dei trasporti europei sono dovute all’utilizzo insufficiente della modalità di trasporto navale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '478', '', '', 'Crescita ed evoluzione del trasporto stradale ', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '478', '3475', '1', 'Vi è la previsione che il trasporto delle merci su strada abbia un ulteriore notevole incremento rispetto alla modalità ferroviaria', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '478', '3476', '2', 'Vi è la previsione che il trasporto delle merci su strada possa migliorare con l’intermodalità', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '478', '3477', '3', 'Vi è la previsione che il trasporto delle merci su strada possa migliorare con l’uso di sistemi ITS (Intelligent Transport Systems)', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '478', '3478', '4', 'Vi è la previsione che il trasporto delle merci su strada possa diminuire a causa del traffico', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '479', '', '', 'Sostenibilità del sistema trasporti', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '479', '3479', '1', 'La sostenibilità del sistema trasporti richiede lo sviluppo dell’intermodalità', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '479', '3480', '2', 'La sostenibilità del sistema trasporti esige un accordo internazionale per migliorare le infrastrutture', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '479', '3481', '3', 'La sostenibilità del sistema trasporti deve tener conto anche delle emissioni inquinanti', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '479', '3482', '4', 'La sostenibilità del sistema trasporti non è influenzata dalle reti transeuropee di trasporto', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '479', '3483', '5', 'La sostenibilità del sistema trasporti non è legata allo sviluppo della logistica del trasporto merci', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '479', '3484', '6', 'La sostenibilità del sistema trasporti non è legata all’utilizzo di energia verde nei veicoli industriali', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '479', '3485', '7', 'Il trasporto sostenibile non è un obiettivo perseguito in ambito europeo, in quanto di competenza prettamente nazionale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '480', '', '', 'Ottimizzazione gestionale 01', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '480', '3486', '1', 'L’ottimizzazione gestionale ed economica del trasporto delle merci si ottiene impiegando opportunamente le diverse modalità di trasporto', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '480', '3487', '2', 'L’ottimizzazione gestionale ed economica del trasporto delle merci è legata alla possibilità di scelta tra più modalità di trasporto', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '480', '3488', '3', 'L’ottimizzazione gestionale ed economica del trasporto delle merci si ottiene tramite l’utilizzo della modalità di trasporto marittimo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '480', '3489', '4', 'L’ottimizzazione gestionale ed economica del trasporto delle merci è legata esclusivamente all’utilizzo della modalità fluviale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '481', '', '', 'Ottimizzazione gestionale 02', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '481', '3490', '1', 'L’ottimizzazione gestionale ed economica di una rete integrata di trasporto combinato è legata alla scelta tra diverse modalità di trasporto', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '481', '3491', '2', 'L’ottimizzazione gestionale ed economica di una rete integrata di trasporto combinato è legata alla scelta prevalentemente della modalità di trasporto marittimo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '481', '3492', '3', 'L’ottimizzazione gestionale ed economica di una rete integrata di trasporto combinato è legata alla scelta prevalentemente della modalità di trasporto fluviale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '481', '3493', '4', 'L’ottimizzazione gestionale ed economica di una rete integrata di trasporto combinato è legata alla scelta prevalentemente della modalità di trasporto stradale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '482', '', '', 'Sistemi navigazione satellitare', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '482', '3494', '1', 'Il sistema di navigazione satellitare Galileo consente di seguire l’iter delle merci in qualsiasi punto', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '482', '3495', '2', 'Il sistema di navigazione satellitare Galileo non consente di ridurre i costi del trasporto', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '482', '3496', '3', 'Il sistema di navigazione satellitare Galileo consente di verificare la pressione dei contenitori adibiti al trasporto merci', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '482', '3497', '4', 'Il sistema di navigazione satellitare Galileo consente di verificare qualsiasi parametro del veicolo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '483', '', '', 'Veicoli a basso impatto ambientale', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '483', '3498', '1', 'I veicoli a basso impatto ambientale hanno costi iniziali superiori ai veicoli convenzionali', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '483', '3499', '2', 'I veicoli a basso impatto ambientale hanno un minor consumo di carburante', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '483', '3500', '3', 'I veicoli a basso impatto ambientale non hanno un peso rilevante in quanto l’autotrasporto assorbe il 10% del consumo totale di petrolio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '483', '3501', '4', 'I veicoli a basso impatto ambientale hanno alto consumo energetico', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '483', '3502', '5', 'I veicoli a basso impatto ambientale hanno alte emissioni di CO2 (anidride carbonica)', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '484', '', '', 'Ecologica verde', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '484', '3503', '1', 'Nella transizione ecologica è rilevante destinare risorse per l’acquisto di veicoli per il trasporto merci che utilizzano i combustibili alternativi', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '484', '3504', '2', 'Il settore dei trasporti è considerato responsabile di circa un quarto delle emissioni di gas serra', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '484', '3505', '3', 'La transizione verso una mobilità sostenibile (verde) richiede interventi per far crescere i veicoli ad emissioni zero e a basse emissioni', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '484', '3506', '4', 'La transizione verso una mobilità sostenibile (verde) richiede interventi per l’implementazione dei punti di ricarica e di rifornimento per l’utilizzo dei carburanti alternativi', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '484', '3507', '5', 'La transizione verso una mobilità sostenibile (verde) richiede il ricorso all’applicazione dei principi dell’economia circolare', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '484', '3508', '6', 'La transizione verso una mobilità sostenibile (verde) è favorita dall’utilizzo di veicoli pesanti equipaggiati con motori elettrici di trazione per il trasporto professionale', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '484', '3509', '7', 'L''utilizzo di veicoli pesanti equipaggiati con motori elettrici di trazione per il trasporto professionale non ha effetti sulla transizione verso una mobilità sostenibile (verde)', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '484', '3510', '8', 'La transizione verso una mobilità sostenibile (verde) non è legata alla crescita dei veicoli ad emissioni zero e a basse emissioni', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '484', '3511', '9', 'La transizione verso una mobilità sostenibile (verde) è indipendente dall’implementazione dei punti di ricarica e di rifornimento per l’utilizzo dei carburanti alternativi', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '484', '3512', '10', 'È irrilevante destinare risorse per l’acquisto di veicoli per il trasporto merci che utilizzano i combustibili alternativi ai fini della transizione ecologica', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '485', '', '', ' Applicazione dei principi dell’economia circolare', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '485', '3513', '1', 'L’applicazione dei principi dell’economia circolare coinvolge anche il settore dei trasporti destinato alla movimentazione delle merci con particolare riferimento all’evoluzione degli hub logistici', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '485', '3514', '2', 'L’applicazione dei principi dell’economia circolare coinvolge anche il settore dei trasporti destinato alla movimentazione delle merci con particolare riferimento progettazione del veicolo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '485', '3515', '3', 'L’applicazione dei principi dell’economia circolare che riguardano il prolungamento della vita dei prodotti mediante il loro riutilizzo, la separazione, il riciclo, ecc. coinvolge anche il settore dei trasporti', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '485', '3516', '4', 'L’economia circolare riguarda il prolungamento della vita dei prodotti mediante il loro riutilizzo, la separazione, il riciclo, ecc. ma non coinvolge il settore dei trasporti destinato alla movimentazione di merci', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '486', '', '', 'Combustibili meno equinanti', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '486', '3517', '1', 'Lo sviluppo della mobilità sostenibile (veicoli elettrici e ibridi), soprattutto nel medio termine, non può prescindere dall’utilizzo dei combustibili alternativi quali CNG (metano), GPL (gas di petrolio liquefatto), LNG (gas naturale liquefatto)', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '486', '3518', '2', 'Sono stati realizzati anche veicoli pesanti che sfruttano il metano nella forma gassosa (CNG) e in quella liquida (LNG)', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '486', '3519', '3', 'Oltre all’elettricità, al metano, biometano e altri, anche l’idrogeno è considerato un combustibile che contribuisce alla decarbonizzazione', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '486', '3520', '4', 'Il biometano ottenuto da biomasse provenienti da scarti agricoli e deiezioni animali, da scarti alimentari e rifiuti solidi urbani è considerato una fonte rinnovabile e sostenibile', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '486', '3521', '5', 'Lo sviluppo della mobilità sostenibile (veicoli elettrici e ibridi), soprattutto nel medio termine, non può prescindere dall’utilizzo dei motori a gasolio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '486', '3522', '6', 'Lo sviluppo della mobilità sostenibile (veicoli elettrici e ibridi), soprattutto nel medio termine, richiede un adeguato numero di punti di rifornimento che erogano gasolio e benzina', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '486', '3523', '7', 'Il biometano ottenuto da biomasse provenienti da scarti agricoli e deiezioni animali, da scarti alimentari e rifiuti solidi urbani è considerato altamente inquinante per l’ambiente', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '487', '', '', 'Motori diesel di tipo dual-fuel', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '487', '3524', '1', 'Nel campo del trasporto di merci si ricorre anche all’installazione di sistemi di alimentazione di motori ad accensione spontanea (Diesel) del tipo dual-fuel', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '487', '3525', '2', 'Tra i vantaggi dell’utilizzo di sistemi di alimentazione di motori Diesel del tipo dual-fuel (gasolio-metano) spicca il prezzo del metano che confrontato con quello del gasolio risulta nettamente inferiore', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '487', '3526', '3', 'Negli impianti di tipo dual-fuel (gasolio-LNG) il metano è immagazzinato in fase liquida in appositi serbatoi criogenici', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '487', '3527', '4', 'Tra i vantaggi dell’utilizzo di sistemi di alimentazione di motori Diesel del tipo dual-fuel (gasolio-metano) spicca l’impatto ambientale del veicolo che risulta più silenzioso e ha emissioni inquinanti più basse', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '487', '3528', '5', 'Negli impianti di tipo dual-fuel (gasolio-LNG) il metano liquido è immagazzinato in fase gassosa nelle normali bombole di metano', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '487', '3529', '6', 'Nell’utilizzo di sistemi di alimentazione di motori Diesel del tipo dual-fuel (gasolio - metano) occorre considerare l’impatto ambientale del veicolo che risulta più rumoroso e ha emissioni inquinanti più alte', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '488', '', '', 'Sistema di alimentazione del motore gasolio e metano (dual-fuel)', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '488', '3530', '1', 'Nei motori a combustione interna, solamente il 30% circa dell’energia prodotta dalla combustione si trasforma in energia meccanica', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '488', '3531', '2', 'Il sistema di alimentazione del motore che sfrutta GPL e metano può essere presente sul veicolo fin dall’origine oppure può essere installato successivamente all’immatricolazione', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '488', '3532', '3', 'Il sistema di alimentazione del motore dei veicoli può consentire il funzionamento del propulsore alternativamente a benzina e GPL oppure a benzina e metano', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '488', '3533', '4', 'Il sistema di alimentazione del motore dei veicoli può consentire il funzionamento del propulsore a miscela di gasolio e metano (dual-fuel)', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '488', '3534', '5', 'Il sistema di alimentazione del motore dei veicoli può consentire il funzionamento del propulsore a miscela di metano e GPL (dual-fuel)', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '488', '3535', '6', 'Il sistema di alimentazione del motore dei veicoli può consentire il funzionamento del propulsore alternativamente a GPL oppure a metano', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '488', '3536', '7', 'Nei motori a combustione interna, solamente il 60% circa dell’energia prodotta dalla combustione si trasforma in energia meccanica', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '489', '', '', 'Pressione serbatoio a  GPL', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '489', '3537', '1', 'Nei veicoli stradali, il GPL è contenuto in un serbatoio a bassa pressione a circa 6-8 bar', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '489', '3538', '2', 'Nei veicoli stradali, il CNG (metano) è contenuto in un serbatoio a bassa pressione a circa 6-8 bar', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '489', '3539', '3', 'Nei veicoli stradali, non è consentito l’uso di una o più bombole ad alta pressione (circa 200 bar) che contengono metano per l’alimentazione del motore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '490', '', '', 'Veicoli elettrici 01', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '490', '3540', '1', 'I motori elettrici dei veicoli possono essere alimentati da corrente elettrica generata da celle a combustibile (fuel-cells) che utilizzano idrogeno', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '490', '3541', '2', 'Nei veicoli con motore elettrico l’energia elettromagnetica viene trasformata in energia meccanica per la trazione (forza motrice)', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '490', '3542', '3', 'Il motore elettrico è alimentato dalla corrente elettrica accumulata negli accumulatori (batterie) ricaricabili che possono essere del tipo al litio-ione', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '490', '3543', '4', 'Il motore elettrico è alimentato dalla corrente elettrica accumulata negli accumulatori (batterie) ricaricabili che possono essere del tipo al piombo-acido', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '490', '3544', '5', 'I veicoli con motore elettrico possono essere del tipo alimentazione autonoma tradizionale con accumulatori', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '490', '3545', '6', 'I veicoli con motore elettrico possono essere del tipo alimentazione mista a benzina e gasolio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '490', '3546', '7', 'I veicoli con motore elettrico possono essere del tipo alimentazione mista a metano e GPL (dual-fuel)', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '491', '', '', 'Veicoli elettrici 02', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '491', '3547', '1', 'A determinate condizioni è ammessa la trasformazione di alcune categorie di veicoli immatricolati originariamente con motore termico in veicoli con esclusiva trazione elettrica', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '491', '3548', '2', 'Per contenere il rischio per la sicurezza dei pedoni, dovuto alle auto elettriche in avvicinamento, è stato introdotto un sistema denominato AVAS - Acustic vehicle alerting system', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '491', '3549', '3', 'I veicoli elettrici richiedono la sostituzione periodica degli accumulatori', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '491', '3550', '4', 'I veicoli elettrici richiedono brevissimi tempi per la ricarica degli accumulatori di energia elettrica', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '491', '3551', '5', 'I veicoli elettrici richiedono la sostituzione periodica del serbatoio della benzina', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '491', '3552', '6', 'Per contenere il rischio per la sicurezza degli autobus dovuto alle auto ibride in allontanamento è stato introdotto un sistema denominato AVAS (sistema acustico di allerta del veicolo)', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '491', '3553', '7', 'È vietata la produzione di veicoli pesanti elettrici prodotti in serie', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '492', '', '', 'Veicoli a idrogeno', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '492', '3554', '1', 'L’idrogeno può essere utilizzato per alimentare veicoli muniti di celle a combustibile (fuel-cells) che producono energia elettrica per far funzionare un motore elettrico', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '492', '3555', '2', 'Apposite norme tecniche di omologazione disciplinano l’approvazione dei veicoli muniti di propulsione a idrogeno', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '492', '3556', '3', 'Una cella a combustibile funziona secondo un processo elettrochimico che trasforma l’energia chimica (idrogeno) in energia elettrica', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '492', '3557', '4', 'Nei veicoli a idrogeno vengono utilizzate un numero variabile di celle a combustibile collegate in serie in base alla tensione e alla potenza richiesta dall’applicazione', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '492', '3558', '5', 'Non esistono problemi per l’utilizzo dell’idrogeno come combustibile né in relazione al trasporto né per lo stoccaggio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '492', '3559', '6', 'Non è ammessa la realizzazione di veicoli a motore a combustione interna funzionante a idrogeno', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '492', '3560', '7', 'Una cella a combustibile funziona secondo un processo elettrochimico che trasforma l’energia elettrica in energia chimica', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '492', '3561', '8', 'Nelle celle a combustibile utilizzate nel campo dell’autotrazione, il combustibile utilizzato può essere anche gasolio', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '492', '3562', '9', 'Nelle celle a combustibile utilizzate nel campo dell’autotrazione, il combustibile può essere ottenuto mediante la conversione di ossigeno', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '493', '', '', 'Vantaggi dell uso veicoli a idrogeno', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '493', '3563', '1', 'L’idrogeno è un gas che a temperatura ambiente occupa molto più spazio di un carburante normale (ha una bassa densità energetica)', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '493', '3564', '2', 'Un motore a combustione interna alimentato con idrogeno non ha emissioni inquinanti tipiche del motore alimentato con combustibili fossili', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '493', '3565', '3', 'Nel campo del trasporto di merci l’idrogeno è considerato un combustibile che contribuisce alla decarbonizzazione', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '493', '3566', '4', 'Nel campo del trasporto di merci l’idrogeno non è considerato un combustibile che contribuisce alla decarbonizzazione', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '493', '3567', '5', 'Nel campo dell’autotrazione, non è ritenuto importante lo sfruttamento dell’idrogeno per l’alimentazione dei propulsori dei veicoli leggeri e pesanti tramite la tecnologia delle celle a combustibile', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '493', '3568', '6', 'Nell’autotrazione, le attuali tecnologie consentono di trasportare l’idrogeno immagazzinato in serbatoi di plastica', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '493', '3569', '7', 'Un motore a combustione interna alimentato con idrogeno è molto più inquinante di un motore alimentato con combustibili fossili', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '494', '', '', 'Veicoli ibridi', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '494', '3570', '1', 'Il veicolo ibrido sfrutta in modo flessibile e conveniente più sistemi di alimentazione mista (propulsione mista) a seconda delle condizioni di utilizzo', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '494', '3571', '2', 'Nei veicoli ibridi più evoluti, quando il motore elettrico è alimentato da energia elettrica fornisce energia meccanica per la trazione', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '494', '3572', '3', 'Grazie alla gestione intelligente del motore elettrico garantita dall’elettronica di bordo, durante la frenatura del veicolo, il motore elettrico diventa un generatore di corrente elettrica per la ricarica degli accumulatori', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '494', '3573', '4', 'Si stima che i veicoli ibridi emettono nell’atmosfera circa il 53% dell’anidride carbonica emessa da un motore a benzina', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '494', '3574', '5', 'I veicoli ibridi con motori elettrici e motori termici a benzina o Diesel possono funzionare autonomamente e alternativamente (la trazione è garantita dal motore elettrico oppure dal motore termico)', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '494', '3575', '6', 'I veicoli ibridi con motori elettrici e motori termici a benzina o diesel possono funzionare in modo che quello termico alimenti l’altro', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '494', '3576', '7', 'I veicoli ibridi con motori elettrici e motori termici a benzina o Diesel possono funzionare esclusivamente utilizzando il motore termico', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '494', '3577', '8', 'Si stima che i veicoli ibridi emettono nell’atmosfera circa il 10% dell’anidride carbonica emessa da un motore a benzina', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '494', '3578', '9', 'Nei veicoli ibridi più evoluti, quando il motore elettrico è alimentato da energia elettrica diventa un generatore di energia elettrica per la ricarica degli accumulatori', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '495', '', '', 'Veicoli ibridi serie, serie-parallelo, split', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '495', '3579', '1', 'I veicoli ibridi serie sfruttano per la trazione uno o più motori elettrici, sono muniti di accumulatori elettrici (batterie) e di un motore termico', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '495', '3580', '2', 'Nei veicoli ibridi parallelo il motore elettrico non sostituisce il motore termico (che è il motore principale) ma collabora con esso', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '495', '3581', '3', 'Nei veicoli ibridi serie-parallelo il motore elettrico recupera l’energia di frenatura in quanto è reversibile e può funzionare anche da generatore di corrente elettrica', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '495', '3582', '4', 'I veicoli ibridi split sfruttano per la trazione un motore elettrico e un motore termico e sono muniti di quattro ruote motrici', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '495', '3583', '5', 'I veicoli ibridi serie sfruttano per la trazione un motore elettrico e un motore termico collegati entrambi agli organi di trasmissione e quindi alle ruote', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '495', '3584', '6', 'Nei veicoli ibridi serie-parallelo il motore elettrico e il motore termico azionano il veicolo esclusivamente mediante il funzionamento contemporaneo dei due motori', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '495', '3585', '7', 'Nei veicoli ibridi serie-parallelo il motore termico può funzionare esclusivamente da motore di trazione', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '495', '3586', '8', 'I veicoli ibridi split sfruttano per la trazione solo il motore elettrico e sono muniti di sei ruote motrici', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '496', '', '', 'Logisticho', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '496', '3587', '1', 'Le piattaforme logistiche razionalizzano i viaggi di consegna delle merci e riducono l’intensità di traffico', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '496', '3588', '2', 'Il sistema logistico permette di razionalizzare il trasporto delle merci', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '496', '3589', '3', 'Il sistema logistico permette il trasferimento e la distribuzione delle merci', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '496', '3590', '4', 'Il sistema logistico permette il trasferimento esclusivamente di determinate merci', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '496', '3591', '5', 'Il sistema logistico permette il trasferimento delle sole merci', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '496', '3592', '6', 'Il sistema logistico permette la distribuzione delle sole merci', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '496', '3593', '7', 'Nel trasferimento e nella distribuzione delle merci le piattaforme logistiche non forniscono, di norma, un’offerta completa di servizi logistici', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '496', '3594', '8', 'Le piattaforme logistiche non consentono la gestione di imballaggi e prodotti dismessi', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '497', '', '', 'Riduzione costo trasporto', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '497', '3595', '1', 'L’aumento o la diminuzione del costo del trasporto si riflette sul costo dei prodotti trasportati', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '497', '3596', '2', 'La riduzione del costo del trasporto si ottiene anche scegliendo un percorso più pianeggiante', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '497', '3597', '3', 'La riduzione del costo del trasporto si ottiene anche utilizzando un trasporto intermodale sulle lunghe distanze', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '497', '3598', '4', 'La riduzione del costo del trasporto si ottiene anche ottimizzando le potenzialità di ogni modalità di trasporto', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '497', '3599', '5', 'La riduzione del costo del trasporto si ottiene scegliendo il veicolo più adeguato da utilizzare in relazione al tipo di carico', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '497', '3600', '6', 'La riduzione del costo del trasporto si ottiene utilizzando il più possibile i trasporti su rotaia', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '497', '3601', '7', 'La riduzione del costo del trasporto si ottiene utilizzando il più possibile i trasporti su strada', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '497', '3602', '8', 'La riduzione del costo del trasporto si ottiene utilizzando il più possibile i trasporti aerei', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '497', '3603', '9', 'La riduzione del costo del trasporto si ottiene soddisfacendo tutte le domande indipendentemente dalla scelta della modalità del trasporto', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '498', '', '', 'Organizzazione di un’impresa di trasporto merci', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '498', '3604', '1', 'Ai fini dell’organizzazione di un’impresa di trasporto merci è rilevante la tipologia del trasporto', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '498', '3605', '2', 'Ai fini dell’organizzazione di un’impresa di trasporto merci è rilevante il soggetto economico ma non il soggetto giuridico', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '498', '3606', '3', 'Ai fini dell’organizzazione di un’impresa di trasporto merci non è rilevante il profilo organizzativo dell’impresa', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '498', '3607', '4', 'Ai fini dell’organizzazione di un’impresa di trasporto merci è rilevante il soggetto giuridico ma non il soggetto economico', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '499', '', '', ' Normativa ADR', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '499', '3608', '1', 'Per trasportare merci pericolose bisogna attenersi all’accordo ADR', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '499', '3609', '2', 'La normativa internazionale ADR si applica ai trasporti di merci pericolose', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '499', '3610', '3', 'La normativa internazionale ADR si applica al trasporto di carburanti', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '499', '3611', '4', 'La normativa internazionale ADR si applica al trasporto di petrolio', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '499', '3612', '5', 'La normativa internazionale ADR si applica ai trasporti di merci deperibili in regime di temperatura controllata', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '499', '3613', '6', 'La normativa internazionale ADR si applica ai trasporti di macchine operatrici eccezionali', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '499', '3614', '7', 'La normativa internazionale ADR si applica ai trasporti di materiali per l’edilizia', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '499', '3615', '8', 'La normativa internazionale ADR si applica al trasporto di merci alimentari', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '499', '3616', '9', 'La normativa internazionale ADR si applica solamente al trasporto marittimo di merci', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '499', '3617', '10', 'Per trasportare merci pericolose deve essere sempre presente sul veicolo anche una fattura', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '499', '3618', '11', 'Per trasportare merci pericolose è sufficiente essere in possesso di un normale documento di trasporto', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '499', '3619', '12', 'Le merci pericolose escluse quelle della classe 2 e 3 devono essere accompagnati da particolari documenti', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '500', '', '', 'Equipaggiamenti  dei veicoli ADR', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '500', '3620', '1', 'Nel caso di alcuni veicoli a motore, le unità di trasporto ADR in colli devono essere dotate di un dispositivo limitatore di velocità', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '500', '3621', '2', 'L’installazione dell’ABS (dispositivo antibloccaggio del sistema frenante) è obbligatoria sui veicoli ADR in relazione all’anno di immatricolazione, alla massa complessiva e alla modalità di trasporto', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '500', '3622', '3', 'Le unità di trasporto ADR in colli devono essere dotate di idonei estintori', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '500', '3623', '4', 'Le unità di trasporto ADR in colli devono essere dotate degli equipaggiamenti di sicurezza previsti dall’ADR', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '500', '3624', '5', 'Le unità di trasporto ADR in colli devono essere dotate di un dispositivo rallentatore della velocità, qualunque sia la loro massa complessiva a pieno carico', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '500', '3625', '6', 'Le unità di trasporto ADR in colli devono essere dotate obbligatoriamente dell’ABS (dispositivo antibloccaggio del sistema frenante)', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '500', '3626', '7', 'Le unità di trasporto ADR in colli devono essere dotate sempre di un equipaggiamento elettrico speciale secondo le norme ADR', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '500', '3627', '8', 'Il limitatore di velocità è obbligatorio per tutti i veicoli cisterna di massa massima autorizzata maggiore di 3,5 t ma non superiore a 12 t, immatricolati dopo il 31 dicembre 2006', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '501', '', '', 'Normativa ADR per veicolo scoperto', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '501', '3628', '1', 'Secondo la normativa ADR per veicolo scoperto s’intende un veicolo il cui pianale è nudo (privo di sponde)', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '501', '3629', '2', 'Secondo la normativa ADR per veicolo scoperto s’intende un veicolo con pianale nudo oppure munito di sponde', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '501', '3630', '3', 'Secondo la normativa ADR per veicolo scoperto s’intende un veicolo il cui pianale è munito di sponde', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '501', '3631', '4', 'Secondo la normativa ADR per veicolo scoperto si intende un veicolo cisterna sprovvisto di coibentazione', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '501', '3632', '5', 'Secondo la normativa ADR per veicolo scoperto si intende un veicolo il cui pianale è ricoperto con telone', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '501', '3633', '6', 'Secondo la normativa ADR per veicolo scoperto si intende un veicolo con carrozzeria furgone', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '502', '', '', 'Normativa ADR per veicoli (chiuso, telonato)', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '502', '3634', '1', 'Secondo la normativa ADR per veicolo chiuso s’intende un veicolo con un compartimento di carico a pareti e copertura rigide', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '502', '3635', '2', 'Secondo la normativa ADR per veicolo chiuso si intende un veicolo anche frigorifero o isotermico', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '502', '3636', '3', 'Secondo la normativa ADR per veicolo telonato si intende un veicolo il cui pianale nudo oppure munito di sponde, viene coperto da un telone per proteggere la merce trasportata', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '502', '3637', '4', 'Secondo la normativa ADR per veicolo telonato si intende un veicolo scoperto munito di telone per proteggere la merce trasportata', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '502', '3638', '5', 'Secondo la normativa ADR per veicolo telonato si intende un veicolo anche centinato, cioè munito di centine e un telone', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '502', '3639', '6', 'Secondo la normativa ADR per veicolo chiuso si intende un veicolo il cui pianale è munito di sponde', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '502', '3640', '7', 'Secondo la normativa ADR per veicolo chiuso si intende un veicolo il cui pianale è ricoperto con un telone', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '502', '3641', '8', 'Secondo la normativa ADR per veicolo chiuso si intende un veicolo con centine e telone smontabili', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '502', '3642', '9', 'Secondo la normativa ADR per veicolo telonato si intende un veicolo chiuso, con pareti e copertura rigide', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '502', '3643', '10', 'Secondo la normativa ADR per veicolo telonato si intende un veicolo batteria coperto con telone ignifugo', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '502', '3644', '11', 'Secondo la normativa ADR per veicolo telonato si intende un veicolo cisterna con coibentazione in tela', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '503', '', '', 'Idoneità del veicolo al trasporto di merci pericolose su strada', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '503', '3645', '1', 'Sulla carta di circolazione dei veicoli cassonati adibiti al trasporto di benzene (classe 3) in colli non deve figurare nessuna annotazione relativa all’idoneità del mezzo di trasporto', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '503', '3646', '2', 'Per attestare l’idoneità del veicolo al trasporto su strada, in regime nazionale, di merci pericolose in colli non è necessaria nessuna attestazione per carichi diversi da esplosivi e radioattivi', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '503', '3647', '3', 'Per attestare l’idoneità del veicolo al trasporto su strada, in regime nazionale, di merci pericolose in colli la carta di circolazione deve contenere l’annotazione di idoneità al trasporto di merce pericolosa', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '503', '3648', '4', 'Per attestare l’idoneità del veicolo al trasporto su strada, in regime nazionale, di merci pericolose in colli occorre l’attestato ATP', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '503', '3649', '5', 'Per attestare l’idoneità del veicolo al trasporto su strada, in regime nazionale, di merci pericolose in colli necessita il certificato di approvazione ADR (con barra diagonale rosa)', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '503', '3650', '6', 'Sulla carta di circolazione dei veicoli cassonati adibiti al trasporto di benzene in colli deve figurare l’annotazione di idoneità del mezzo di trasporto', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '503', '3651', '7', 'Sulla carta di circolazione dei veicoli cassonati adibiti al trasporto di benzene in colli deve figurare l’annotazione di idoneità solo in regime nazionale', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '503', '3652', '8', 'Sulla carta di circolazione dei veicoli cassonati adibiti al trasporto di benzene in colli deve figurare specifica annotazione', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '504', '', '', 'Requisiti per trasporto rifiuti', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '504', '3653', '1', 'Per trasportare rifiuti è necessario che l’impresa sia iscritta all’Albo nazionale gestori ambientali', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '504', '3654', '2', 'Per trasportare rifiuti è sufficiente che l’impresa sia iscritta all’Albo nazionale degli autotrasportatori', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '504', '3655', '3', 'Il trasporto di rifiuti speciali rientrano nelle disposizioni di trasporto in regime ATP (accordo internazionale trasporto derrate deperibili)', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '505', '', '', 'Trasporto derrate deperibili – ATP', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '505', '3656', '1', 'L’accordo ATP riguarda il trasporto delle derrate deteriorabili', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '505', '3657', '2', 'La sigla ATP si riferisce a accordo internazionale per il trasporto di derrate deperibili', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '505', '3658', '3', 'La sigla ATP si riferisce all’Accordo internazionale per il trasporto delle merci pericolose', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '505', '3659', '4', 'La sigla ATP si riferisce a autorizzazione trasporto prodotti agricoli', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '505', '3660', '5', 'La sigla ATP si riferisce a autorizzazione trasporto prodotti petroliferi', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '505', '3661', '6', 'L’accordo ATP (accordo internazionale trasporto derrate deperibili) riguarda il trasporto delle merci pericolose', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '505', '3662', '7', 'L’accordo ATP (accordo internazionale trasporto derrate deperibili) riguarda il trasporto dei rifiuti', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '505', '3663', '8', 'L’accordo ATP (accordo internazionale trasporto derrate deperibili) riguarda il trasporto delle merci che possono contaminare le acque', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '506', '', '', 'Trasporto prodotti alimentari deperibili – ATP', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '506', '3664', '1', 'Per trasportare prodotti alimentari, è obbligatorio utilizzare veicoli rispondenti alle norme ATP solo nel caso di alcune derrate deperibili', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '506', '3665', '2', 'Per il trasporto di prodotti alimentari non deteriorabili è sufficiente la rispondenza del veicolo alle norme igienico sanitarie stabilite dalle competenti autorità sanitarie', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '506', '3666', '3', 'Per trasportare prodotti alimentari, è obbligatorio utilizzare veicoli rispondenti alle norme ATP solo in caso di trasporti internazionali', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '506', '3667', '4', 'Per trasportare prodotti alimentari, è obbligatorio utilizzare, in ogni caso, veicoli rispondenti alle norme ATP (accordo internazionale trasporto derrate deperibili)', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '506', '3668', '5', 'Per il trasporto di prodotti alimentari non deteriorabili non è necessario il rispetto di particolari prescrizioni', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '506', '3669', '6', 'Per trasportare prodotti alimentari, in nessun caso è obbligatorio utilizzare veicoli rispondenti alle norme ATP (accordo internazionale trasporto derrate deperibili)', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '506', '3670', '7', 'Per il trasporto di prodotti alimentari non deperibili deve essere utilizzato un veicolo conforme alle norme dell’accordo ATP (accordo internazionale trasporto derrate deperibili)', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '506', '3671', '8', 'Rientrano nelle disposizioni di trasporto in regime ATP i prodotti di scarto delle industrie metallurgiche', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '506', '3672', '9', 'Rientrano nelle disposizioni di trasporto in regime ATP le merci pericolose', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '507', '', '', 'le carrozzerie dei veicoli ATP', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '507', '3673', '1', 'La modifica di una carrozzeria per trasporto di derrate deperibili in regime di temperatura controllata comporta specifica approvazione a seguito di visita e prova presso il competente UMC (ufficio motorizzazione civile)', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '507', '3674', '2', 'Le carrozzerie per il trasporto di derrate deperibili in regime ATP sono munite di attestato di conformità ATP', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '507', '3675', '3', 'La modifica di una carrozzeria per trasporto di derrate deperibili in regime di temperatura controllata non comporta l’approvazione a seguito di visita e prova presso il competente UMC (ufficio motorizzazione civile)', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '507', '3676', '4', 'L’installazione di una carrozzeria per trasporto di derrate deperibili in regime di temperatura controllata richiede specifica comunicazione al competente UMC (ufficio motorizzazione civile)', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '507', '3677', '5', 'L’installazione di una carrozzeria per trasporto di derrate deperibili in regime di temperatura controllata non richiede alcuna formalità se il veicolo è già circolante', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '507', '3678', '6', 'Le carrozzerie per il trasporto di derrate deperibili in regime ATP sono esclusivamente di tipo frigorifero', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '507', '3679', '7', 'Le carrozzerie per il trasporto di derrate deperibili in regime ATP sono munite di targhetta VIN', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '507', '3680', '8', 'Le carrozzerie per il trasporto di derrate deperibili in regime ATP sono classificate conformemente alla normativa ADR', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '508', '', '', 'Temperature e tolleranze ATP', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '508', '3681', '1', 'Rientrano nelle disposizioni di trasporto in regime ATP i prodotti surgelati', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '508', '3682', '2', 'Nel trasporto di talune derrate surgelate e congelate con furgone ATP, durante alcune operazioni di breve durata, è ammesso un innalzamento della temperatura sulla superficie del prodotto in una parte del carico per un massimo di 3 gradi centigradi', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '508', '3683', '3', 'Durante il trasporto di derrate surgelate e congelate la temperatura più elevata delle derrate in tutti i punti del carico non deve superare la temperatura prevista per il prodotto', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '508', '3684', '4', 'Durante il trasporto di derrate surgelate e congelate la temperatura più bassa delle derrate in tutti i punti del carico non deve essere inferiore alla temperatura prevista per il prodotto', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '508', '3685', '5', 'Durante il trasporto di derrate surgelate e congelate la temperatura più elevata delle derrate nella zona centrale del carico non deve superare la temperatura prevista per il prodotto', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '508', '3686', '6', 'Durante il trasporto di derrate surgelate e congelate la temperatura più elevata delle derrate nella zona laterale del carico non deve superare la temperatura prevista per il prodotto', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '508', '3687', '7', 'Nel trasporto di talune derrate surgelate e congelate con furgone ATP, durante alcune operazioni di breve durata, è ammesso un innalzamento della temperatura sulla superficie del prodotto in una parte del carico per un massimo di 2 gradi centigradi', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '508', '3688', '8', 'Nel trasporto di talune derrate surgelate e congelate con furgone ATP, durante alcune operazioni di breve durata, è ammesso un innalzamento della temperatura sulla superficie del prodotto in una parte del carico per un massimo di 1 grado centigrado', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '508', '3689', '9', 'Non è necessaria alcuna precauzione se occorre aprire le porte del mezzo di trasporto in regime ATP, per esempio, per effettuare le ispezioni', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '509', '', '', 'Trasporto di animali vivi', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '509', '3690', '1', 'Per trasportare animali vivi è sempre obbligatoria una documentazione che specifichi origine degli animali e caratteristiche del viaggio', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('', '510', '', '', ' Trasporto eccezionale', '');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '510', '3691', '1', 'È trasporto in condizioni di eccezionalità il trasporto di cose indivisibili che, per loro dimensione, determinano eccedenza rispetto ai limiti di sagoma fissati dal codice della strada', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '510', '3692', '2', 'Il trasporto di una trave su un autocarro viene definito eccezionale per dimensioni quando sporge anteriormente', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '510', '3693', '3', 'Il trasporto di una trave su un autocarro viene definito eccezionale per dimensioni quando la lunghezza del veicolo più la parte sporgente della trave supera la lunghezza di 12 metri', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '510', '3694', '4', 'Il trasporto di una trave su un autocarro viene definito eccezionale per dimensioni quando la lunghezza del veicolo compreso il carico supera i limiti massimi di lunghezza fissati dal codice della strada', 'V');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '510', '3695', '5', 'Il trasporto di una trave su un autocarro viene sempre definito eccezionale per dimensioni quando il carico sporge nella parte posteriore', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '510', '3696', '6', 'Il trasporto di una trave su un autocarro viene definito eccezionale per dimensioni quando sporge posteriormente del 20% della lunghezza', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '510', '3697', '7', 'Il trasporto di una trave su un autocarro viene definito eccezionale per dimensioni quando sporge posteriormente del 10% della lunghezza', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '510', '3698', '8', 'Il trasporto di una trave su un autocarro viene definito eccezionale per dimensioni quando sporge posteriormente di un quarto della lunghezza', 'F');
INSERT INTO CQC (Chapter, Topic_No, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '510', '3699', '9', 'Il trasporto di una trave su un autocarro viene definito eccezionale per dimensioni quando la lunghezza del veicolo più la parte sporgente della trave supera la lunghezza di 11 metri', 'F');
