INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '1', '11002', '2', '2', 'Il regolamento 561/2006/CE prevede disposizioni relative al massimo tempo  di guida consentito in una settimana', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '1', '11002', '3', '3', 'Il regolamento 561/2006/CE mira a migliorare le condizioni sociali dei lavoratori a cui si applica', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '1', '11002', '4', '4', 'Il regolamento 561/2006/CE mira ad aumentare il livello di sicurezza stradale', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '1', '11002', '5', '5', 'Il regolamento 561/2006/CE prevede disposizioni relative al tempo di guida massimo per giornata', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '1', '11002', '6', '6', 'Il regolamento 561/2006/CE prevede disposizioni relative ai tempi di guida minimi', 'F');
