INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '1', '11002', '9', '9', 'Il regolamento 561/2006/CE prevede disposizioni sui compensi minimi dei conducenti', 'F');
