INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '2', '11003', '', '', 'Ambito di applicazione del Regolamento 561/2006/CE', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '2', '11003', '11', '1', 'Il regolamento 561/2006/CE si applica al trasporto su strada di cose, effettuato con veicoli di massa massima ammissibile, compresi eventuali rimorchi o semirimorchi, superiore a 3,5 tonnellate', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '2', '11003', '12', '2', 'Il regolamento 561/2006/CE si applica al trasporto su strada di passeggeri effettuato con veicoli atti a trasportare più di nove persone compreso il conducente', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '2', '11003', '13', '3', 'Il regolamento 561/2006/CE si applica ad alcuni trasporti su strada effettuati all''interno dell''Unione Europea', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '2', '11003', '14', '4', 'Il regolamento 561/2006/CE si applica ad alcuni trasporti su strada effettuati fra l''Unione Europea e i Paesi dello Spazio economico europeo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '2', '11003', '15', '5', 'Il regolamento 561/2006/CE si applica al trasporto su strada di merci, effettuato da veicoli di massa massima ammissibile, compresi eventuali rimorchi o semirimorchi, superiore a 2,5 tonnellate', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '2', '11003', '16', '6', 'Il regolamento 561/2006/CE si applica al trasporto su strada di passeggeri effettuato con taxi o autobus', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '2', '11003', '17', '7', 'Il regolamento 561/2006/CE si applica anche al trasporto su ferrovia', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '2', '11003', '18', '8', 'Il regolamento 561/2006/CE si applica ai veicoli destinati al trasporto di cose, ma non a quelli adibiti al trasporto di persone', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '3', '11010', '', '', 'Defininizioni di interruzione, riposo e tempo di disponibilità', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '3', '11010', '19', '1', 'Secondo il regolamento 561/2006/CE, per interruzione si intende ogni periodo in cui il conducente non  può guidare o svolgere altre mansioni', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '3', '11010', '20', '2', 'Secondo il regolamento 561/2006/CE, per riposo si intende ogni periodo ininterrotto durante il quale il conducente può disporre liberamente del suo tempo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '3', '11010', '21', '3', 'Secondo il regolamento 561/2006/CE, il tempo di disponibilità comprende il tempo di attesa sul posto  di lavoro e la multipresenza', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '3', '11010', '22', '4', 'Secondo il regolamento 561/2006/CE, per interruzione si intende ogni fermata effettuata con il veicolo  durante la giornata lavorativa', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '3', '11010', '23', '5', 'Secondo il regolamento 561/2006/CE, per disponibilità si intende l''assenso del lavoratore a svolgere  mansioni al di fuori del normale orario di lavoro', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '4', '11007', '', '', 'Tempi di disponibilità 1', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '4', '11007', '24', '1', 'I tempi di disponibilità sono quelli nei quali il conducente, pur non dovendo necessariamente rimanere sul posto di lavoro, deve tenersi a disposizione per poter prontamente riprendere il lavoro', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '4', '11007', '25', '2', 'I periodi di permanenza a bordo del veicolo a fianco di un altro conducente (multipresenza) sono considerati tempi di disponibilità', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '4', '11007', '26', '3', 'I periodi di permanenza in cuccetta durante la marcia del veicolo sono considerati tempi di disponibilità', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '4', '11007', '27', '4', 'I periodi di tempo impiegati per la piccola manutenzione del veicolo sono considerati altri lavori', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '4', '11007', '28', '5', 'I tempi di disponibilità sono quelli durante i quali il conducente può disporre liberamente del proprio  tempo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '4', '11007', '29', '6', 'I tempi di definiti altri lavori sono considerati tempti di guida.', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '4', '11007', '30', '7', 'I periodi di tempo impiegati per la pulizia del veicolo son considerati tempi di disponibilità', 'F');
