INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '7', '11004', '', '', 'Periodo di guida giornaliero', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '7', '11004', '34', '1', 'Secondo quanto stabilito dal regolamento 561/2006/CE, il periodo di guida giornaliero, di norma, non deve superare le nove ore', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '7', '11004', '35', '2', 'Secondo quanto stabilito dal regolamento 561/2006/CE, il periodo di guida giornaliero può essere esteso fino a dieci ore, non più di due volte nell''arco della settimana', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '7', '11004', '36', '3', 'Secondo quanto stabilito dal regolamento 561/2006/CE, il periodo di guida giornaliero prevede una o più  interruzioni di guida obbligatorie', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '7', '11004', '37', '4', 'Secondo quanto stabilito dal regolamento 561/2006/CE, il periodo di guida giornaliero può essere al  massimo di otto ore', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '7', '11004', '38', '5', 'Secondo quanto stabilito dal regolamento 561/2006/CE, il periodo di guida giornaliero, di norma, è di  nove ore consecutive', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '7', '11004', '39', '6', 'Secondo quanto stabilito dal regolamento 561/2006/CE, il periodo di guida giornaliero può essere di undici ore, per non più di due volte in una settimana', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '7', '11004', '40', '7', 'Secondo quanto stabilito dal regolamento 561/2006/CE, il periodo di guida giornaliero può essere esteso  a dodici ore, purché intervallato da almeno tre interruzioni di guida di quarantacinque minuti', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '8', '11005', '', '', 'Periodo di riposo giornaliero 1', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '8', '11005', '41', '1', 'Secondo quanto stabilito dal regolamento 561/2006/CE, il periodo di riposo giornaliero regolare è di  almeno undici ore', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '8', '11005', '42', '2', 'Secondo quanto stabilito dal regolamento 561/2006/CE, il periodo di riposo giornaliero ridotto è di almeno nove ore non più di tre volte la settimana', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '8', '11005', '43', '3', 'Secondo quanto stabilito dal regolamento 561/2006/CE, è vietato guidare per più di sei giorni consecutivi', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '8', '11005', '44', '4', 'Secondo quanto stabilito dal regolamento 561/2006/CE, non si possono effettuare più di tre periodi di riposo giornaliero ridotto, tra due periodi di riposo settimanale', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '8', '11005', '45', '5', 'Secondo quanto stabilito dal regolamento 561/2006/CE, il periodo di riposo giornaliero regolare è di almeno nove ore', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '8', '11005', '46', '6', 'Secondo quanto stabilito dal regolamento 561/2006/CE, il periodo di riposo giornaliero deve essere pari al periodo di guida giornaliero', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '8', '11005', '47', '7', 'Secondo quanto stabilito dal regolamento 561/2006/CE, il periodo di riposo giornaliero regolare è di otto ore', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '8', '11005', '48', '8', 'Secondo quanto stabilito dal regolamento 561/2006/CE, il periodo di riposo giornaliero è un periodo  durante il quale il conducente non può disporre liberamente del proprio tempo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '8', '11005', '49', '9', 'Secondo quanto stabilito dal regolamento 561/2006/CE, il periodo di riposo giornaliero ridotto è quaranta cinque minuti ogni quattro ore e mezza di guida', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '9', '11009', '', '', 'Deroga alla durata massima del periodo di guida', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '9', '11009', '50', '1', 'Il regolamento 561/2006/CE prevede che, al fine di salvaguardare la sicurezza delle persone e delle cose trasportate, il conducente possa superare i tempi massimi di guida prescritti solo per raggiungere un luogo di sosta appropriato', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '9', '11009', '51', '2', 'Il conducente che deroga alla durata massima della guida al fine di raggiungere un luogo di sosta appropriato, deve annotarne i motivi sul foglio di registrazione o sul retro della stampa giornaliera (ticket)', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '9', '11009', '52', '3', 'Il regolamento 561/2006/CE prevede che il conducente possa superare i tempi massimi di guida prescritti per raggiungere il proprio domicilio', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '9', '11009', '53', '4', 'Il regolamento 561/2006/CE prevede che il conducente possa superare i tempi massimi di guida prescritti per raggiungere il deposito dell''impresa proprietaria del veicolo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '10', '11011', '', '', 'Periodo massimo di guida settimanale ', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '10', '11011', '54', '1', 'Il regolamento 561/2006/CE stabilisce che il periodo massimo di guida settimanale è di cinquantasei ore', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '10', '11011', '55', '2', 'Il regolamento 561/2006/CE stabilisce che il periodo massimo di guida settimanale è di cinquantasei ore  e che la guida non deve superare le novanta ore nell''arco di due settimane consecutive', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '10', '11011', '56', '3', 'Il regolamento 561/2006/CE stabilisce che il periodo massimo di guida settimanale è di quarantotto ore', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '10', '11011', '57', '4', 'Il regolamento 561/2006/CE stabilisce che il periodo massimo di guida 3 settimanale è di cinquantasei ore e che la guida non deve superare le cento ore nell''arco di due settimane consecutive', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '11', '11011', '', '', 'Periodo di riposo giornaliero ', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '11', '11011', '58', '1', 'Secondo il regolamento 561/2006/CE, il periodo di riposo giornaliero può essere frazionato in due parti,  la prima di almeno 3 ore e la seconda di almeno 9 ore', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '11', '11011', '59', '2', 'Secondo il regolamento 561/2006/CE, il conducente, dopo sei periodi di guida, deve effettuare un riposo  di 45 ore consecutive', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '11', '11011', '60', '3', 'Secondo il regolamento 561/2006/CE, il conducente, dopo sei periodi di guida, può effettuare un riposo ridotto di almeno 24 ore consecutive, purché le restanti ore di riposo vengano recuperate entro la fine della terza settimana', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '11', '11011', '61', '4', 'Secondo il regolamento 561/2006/CE, il periodo di riposo giornaliero può essere frazionato in due parti, la prima di almeno 9 ore e la seconda di almeno 3 ore', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '11', '11011', '62', '5', 'Secondo il regolamento 561/2006/CE, il conducente, dopo quattro periodi di guida, deve effettuare un riposo di 48 ore consecutive', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '11', '11011', '63', '6', 'Dopo un periodo di guida di quattro ore e mezza, il conducente deve osservare un periodo di riposo di  almeno 50 minuti consecutivi', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '12', '11011', '', '', 'Definizione di multipresenza', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '12', '11011', '64', '1', 'Secondo il regolamento 561/2006/CE, si può parlare di multipresenza quando a bordo del veicolo sono presenti almeno due conducenti per l''intero periodo, fatta salva la prima ora nella quale la presenza del secondo conducente è facoltativa', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '12', '11011', '65', '2', 'Secondo il regolamento 561/2006/CE, si può parlare di multipresenza quando a bordo del veicolo sono presenti due conducenti per l''intero periodo, fatte salve le prime tre ore dove la presenza del secondo conducente è facoltativa', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '13', '11006', '', '', 'Periodo di riposo settimanale', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '13', '11006', '66', '1', 'Il regolamento 561/2006/CE prevede che il periodo di riposo settimanale  regolare è di almeno 45 ore', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '13', '11006', '67', '2', 'Il regolamento 561/2006/CE prevede che il periodo di riposo settimanale ridotto debba essere recuperato', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '13', '11006', '68', '3', 'Il regolamento 561/2006/CE prevede che il periodo di riposo settimanale ridotto abbia una durata minima di 24 ore ininterrotte', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '13', '11006', '69', '4', 'Il regolamento 561/2006/CE prevede che il periodo di riposo settimanale possa essere di tipo regolare o ridotto', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '13', '11006', '70', '5', 'Il regolamento 561/2006/CE prevede che il periodo di riposo settimanale regolare sia di almeno 30 ore', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '13', '11006', '71', '6', 'Il regolamento 561/2006/CE prevede che il periodo di riposo settimanale ridotto sia al massimo di 24 ore continuative', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '13', '11006', '72', '7', 'Il regolamento 561/2006/CE prevede che il periodo di riposo settimanale regolare non possa essere  inferiore a 90 ore', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '13', '11006', '73', '8', 'Il regolamento 561/2006/CE prevede che il conducente, a fronte di indennità in denaro, possa rinunciare  al periodo di riposo settimanale', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '14', '11001', '', '', 'Sanzioni per chi non rispetta i periodi di guida e riposo prescritti', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '14', '11001', '74', '1', 'È soggetto a sanzione amministrativa il conducente che supera i periodi di guida prescritti', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '14', '11001', '75', '2', 'È soggetto a sanzione amministrativa il conducente che non osserva le interruzioni obbligatorie durante il viaggio', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '14', '11001', '76', '3', 'È soggetto a sanzione amministrativa il conducente che è sprovvisto o tiene in modo incompleto o alterato l''estratto del registro di servizio', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '14', '11001', '77', '4', 'È soggetto a sanzione amministrativa il conducente che non osserva i periodi di riposo minimi', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '14', '11001', '78', '5', 'È soggetto a sanzione amministrativa il conducente che non osserva i periodi di pausa o di riposo prescritti', 'V');
