INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '15', '11008', '', '', 'Responsabilità e obblighi dell''impresa di trasporto', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '15', '11008', '84', '1', 'L''impresa di trasporto non può promettere premi in denaro al conducente basati sulla maggior percorrenza chilometrica', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '15', '11008', '85', '2', 'Il conducente è corresponsabile per le infrazioni al regolamento 561/2006/CE, anche se commesse alla guida di un veicolo di proprietà dell''impresa', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '15', '11008', '86', '3', ' L''impresa di trasporto non deve incoraggiare i propri conducenti a violare le  disposizioni in materia di tempi di guida e di riposo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '15', '11008', '87', '4', 'L''impresa di trasporto può decurtare lo stipendio al conducente che si rifiuti di guidare per più di 10 ore  al giorno', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '15', '11008', '88', '5', 'L''impresa di trasporto non risponde delle violazioni al codice della strada commesse, durante il lavoro, dai conducenti alle proprie dipendenze cui abbia fornito istruzioni in contrasto con il regolamento 561/2006/CE, perché tale responsabilità è esclusivamente del conducente', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('1', '15', '11008', '89', '6', 'L''impresa di trasporto non è responsabile per le violazioni alla normativa in materia di tempi di guida e di  riposo commesse, durante il lavoro, dai conducenti alle proprie dipendenze quando sono all''estero', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '16', '21003', '', '', 'Veicoli che devono essere dotati di cronotachigrafo', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '16', '21003', '90', '1', 'Il cronotachigrafo è obbligatorio per veicoli adibiti al trasporto di merci di massa complessiva a pieno carico superiore a 3,5 tonnellate', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '16', '21003', '91', '2', 'Il cronotachigrafo è obbligatorio per veicoli per trasporto passeggeri con più di 9 posti compreso quello del conducente (tranne quelli che effettuano servizi regolari di linea su percorsi inferiori a 50 km)', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '16', '21003', '92', '3', 'Il cronotachigrafo è obbligatorio per veicoli per trasporto di passeggeri con più di 9 posti compreso quello del conducente che effettuano servizi regolari di linea su percorsi superiori a 50 km', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '16', '21003', '93', '4', 'I veicoli con velocità massima autorizzata inferiore o uguale a 40 km/h non devono essere dotati di cronotachigrafo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '16', '21003', '94', '5', 'Gli autobus in servizio pubblico di linea, ma utilizzati anche in corsa fuori linea, devono essere dotati di cronotachigrafo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '16', '21003', '95', '6', 'Il cronotachigrafo è obbligatorio per i taxi che operano fuori dei centri urbani', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '16', '21003', '96', '7', 'Il cronotachigrafo è obbligatorio per veicoli adibiti al trasporto di merci di massa complessiva a pieno  carico superiore a 2 tonnellate', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '16', '21003', '97', '8', 'Il cronotachigrafo è obbligatorio per tutti gli autobus', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '16', '21003', '98', '9', 'Il cronotachigrafo è obbligatorio per tutti i veicoli adibiti al trasporto di merci', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '16', '21003', '99', '10', 'Gli autobus in servizio pubblico di linea il cui percorso, da capolinea a capolinea, non supera i 50 km devono essere dotati di cronotachigrafo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '16', '21003', '100', '11', 'I veicoli di proprietà delle forze armate e dei vigili del fuoco devono essere dotati di cronotachigrafo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '16', '21003', '101', '12', 'I veicoli di massa complessiva superiore a 3,5 tonnellate adibiti ad uso medico devono essere dotati di cronotachigrafo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '16', '21003', '102', '13', 'I carri attrezzi specializzati che operano entro un raggio di 100 km dalla propria base operativa devono  essere dotati di cronotachigrafo', 'F');
