INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '31', '21007', '155', '1', 'I fogli di registrazione di un cronotachigrafo analogico, devono avere una omologazione CE specifica per il tipo di apparecchio di controllo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '31', '21007', '156', '2', 'Il conducente che viaggia su un veicolo dotato di cronotachigrafo analogico, prima dell''inizio del viaggio, deve compilare il foglio di registrazione indicando: nome e cognome del conducente, luogo e la data di partenza, targa del veicolo e lettura del contachilometri', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '31', '21007', '157', '3', 'Se durante la stessa giornata lavorativa il conducente utilizza due veicoli dotati di cronotachigrafo analogico, deve utilizzare lo stesso  foglio di registrazione compilandolo anche nella parte posteriore', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '31', '21007', '158', '4', 'Se durante la stessa giornata lavorativa il conducente utilizza due veicoli dotati di cronotachigrafo analogico, nella parte posteriore del foglio di registrazione dovrà indicare, tra l’altro, l’ora del cambio, la targa e la lettura del contachilometri del secondo veicolo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '31', '21007', '159', '5', 'Su veicoli dotati di cronotachigrafo analogico, non è necessario utilizzare fogli di registrazione di tipo omologato', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '31', '21007', '160', '6', 'Su veicoli dotati di cronotachigrafo analogico, prima dell''inizio del viaggio, il conducente deve compilare  il foglio di registrazione indicando il suo nome e cognome e gli estremi della patente di guida e della CQC', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '31', '21007', '161', '7', 'Su veicoli dotati di cronotachigrafo analogico, prima dell''inizio del viaggio, il conducente deve compilare  il foglio di registrazione indicando il suo nome e cognome, la targa del veicolo e gli estremi della carta tachigrafica', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '31', '21007', '162', '8', 'Se durante la stessa giornata lavorativa il conducente utilizza due veicoli dotati di cronotachigrafo analogico, egli deve compilare due fogli di registrazione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '32', '21005', '', '', 'Uso del cronotachigrafo analogico 3', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '32', '21005', '163', '1', 'Il conducente di un veicolo dotato di cronotachigrafo analogico, alla partenza, deve scrivere nome e  cognome sul foglio di registrazione che utilizzerà', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '32', '21005', '164', '2', 'Il conducente di un veicolo dotato di cronotachigrafo analogico, prima di iniziare il viaggio, deve scrivere il luogo di partenza sul foglio di registrazione che utilizzerà', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '32', '21005', '165', '3', 'Il conducente di un veicolo dotato di cronotachigrafo analogico, prima di iniziare il viaggio, deve scrivere la data della partenza sul foglio di registrazione che utilizzerà', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '32', '21005', '166', '4', 'Il conducente di un veicolo dotato di cronotachigrafo analogico, prima di iniziare il viaggio, deve scrivere il numero di targa del veicolo sul foglio di registrazione che utilizzerà', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '32', '21005', '167', '5', 'Il conducente di un veicolo dotato di cronotachigrafo analogico, prima di iniziare il viaggio, deve scrivere la lettura del contachilometri al momento della partenza sul foglio di registrazione che utilizzerà', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '32', '21005', '168', '6', 'Il conducente di un veicolo dotato di cronotachigrafo analogico, prima di iniziare il viaggio, deve scrivere il nome dell''azienda di trasporto proprietaria del veicolo sul foglio di registrazione che utilizzerà', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '32', '21005', '169', '7', 'Il conducente di un veicolo dotato di cronotachigrafo analogico, prima di iniziare il viaggio, deve scrivere il numero di telaio del veicolo sul foglio di registrazione che utilizzerà', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '32', '21005', '170', '8', 'Il conducente di un veicolo dotato di cronotachigrafo analogico, prima di iniziare il viaggio, deve scrivere numero e categoria della propria patente di guida sul foglio di registrazione che utilizzerà', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '32', '21005', '171', '9', 'Il conducente di un veicolo dotato di cronotachigrafo analogico, prima di iniziare il viaggio, deve scrivere la data dell''ultimo trasporto effettuato all''estero sul foglio di registrazione che utilizzerà', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '32', '21005', '172', '10', 'Il conducente di un veicolo dotato di cronotachigrafo analogico, prima di iniziare il viaggio, deve scrivere la propria data di nascita sul foglio di registrazione che utilizzerà', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '33', '21006', '', '', 'Uso del cronotachigrafo analogico 4', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '33', '21006', '173', '1', 'Il conducente che viaggia su un veicolo dotato di cronotachigrafo analogico deve indicare le proprie  attività attraverso l''apposito commutatore', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '33', '21006', '174', '2', 'Il conducente che viaggia su un veicolo dotato di cronotachigrafo analogico, in caso di cambio del veicolo, deve riportare sul foglio di registrazione l''ora di cambio del veicolo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '33', '21006', '175', '3', 'Il conducente che viaggia su un veicolo dotato di cronotachigrafo analogico, in caso di cambio del veicolo, deve riportare sul foglio di registrazione la targa del nuovo veicolo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '33', '21006', '176', '4', 'Il conducente che viaggia su un veicolo dotato di cronotachigrafo analogico deve compilare, prima della partenza, il foglio di registrazione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '33', '21006', '177', '5', 'Il conducente che viaggia su un veicolo dotato di cronotachigrafo analogico deve, al termine del viaggio,  riportare sul foglio di registrazione il numero di chilometri percorsi', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '33', '21006', '178', '6', 'Il conducente di un veicolo dotato di cronotachigrafo analogico deve far controfirmare il foglio di registrazione al titolare dell''azienda proprietaria del veicolo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '33', '21006', '179', '7', 'Il conducente di un veicolo dotato di cronotachigrafo analogico, al termine del viaggio, deve lasciare il  foglio di registrazione inserito nel dispositivo di controllo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '33', '21006', '180', '8', 'Il conducente che viaggia su un veicolo dotato di cronotachigrafo analogico, in caso di cambio del veicolo, deve riportare sul foglio di registrazione l''ora di inizio del turno di lavoro', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '34', '21013', '', '', 'Caratteristiche del cronotachigrafo digitale 1', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '34', '21013', '181', '1', 'Il cronotachigrafo digitale è uno strumento di misura che registra le distanze, le velocità e i tempi di guida e di riposo del conducente', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '34', '21013', '182', '2', 'Il tempo registrato sul cronotachigrafo digitale è in UTC (Universal Time Coordinate), cioè fa riferimento all''ora di Greenwich', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '34', '21013', '183', '3', 'Con il cronotachigrafo digitale è possibile memorizzare i dati relativi  alle attività di uno o due conducenti', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '34', '21013', '184', '4', 'È possibile stampare i dati memorizzati dal cronotachigrafo digitale su scontrini cartacei (ticket)', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '34', '21013', '185', '5', 'Il cronotachigrafo digitale va installato su tutti i veicoli, anche su quelli che hanno già montato il cronotachigrafo analogico', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '34', '21013', '186', '6', 'Il cronotachigrafo digitale è uno strumento che registra le attività del conducente, ma non i dati relativi al veicolo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '34', '21013', '187', '7', 'Il cronotachigrafo digitale è uno strumento che può registrare le attività di un solo conducente', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '34', '21013', '188', '8', 'Il cronotachigrafo digitale è uno strumento che memorizza dati relativi agli ultimi 5 anni', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '34', '21013', '189', '9', 'Il cronotachigrafo digitale non permette la stampa dei dati relativi all''attività lavorativa del conducente', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '35', '21008', '', '', 'Caratteristiche del cronotachigrafo digitale 2', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '35', '21008', '190', '1', 'Nel cronotachigrafo digitale i dati sono memorizzati nella memoria interna del dispositivo e nella carta  tachigrafica inserita', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '35', '21008', '191', '2', 'Nel cronotachigrafo digitale è presente un sistema di memorizzazione elettronica in grado di memorizzare  le attività lavorative del conducente relative ad almeno gli ultimi 365 giorni di calendario', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '35', '21008', '192', '3', 'Il cronotachigrafo digitale è dotato di una stampante integrata', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '35', '21008', '193', '4', 'Il cronotachigrafo digitale emette un segnale acustico che avverte se il veicolo viene utilizzato senza che la carta tachigrafica del conducente sia stata inserita', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '35', '21008', '194', '5', 'I dati della giornata lavorativa del conducente sono memorizzati solo nella memoria interna del cronotachigrafo digitale', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '35', '21008', '195', '6', 'Se il veicolo è dotato di cronotachigrafo digitale, i dati della giornata lavorativa del conducente non possono essere stampati', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '35', '21008', '196', '7', 'Se il veicolo è dotato di cronotachigrafo digitale, i dati dell''attività lavorativa del conducente non possono  essere trasferiti su altro dispositivo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '35', '21008', '197', '8', 'Nel cronotachigrafo digitale è presente un sistema di memorizzazione elettronica in grado di memorizzare  al massimo le attività lavorative del conducente relative agli ultimi sette giorni di calendario', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '36', '21017', '', '', 'Dati ricavabili dal cronotachigrafo digitale', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '36', '21017', '198', '1', 'Dalla memoria del cronotachigrafo digitale è possibile stampare le attività di una giornata di lavoro del conducente, gli eventi e le anomalie, gli eccessi di velocità e i dati  tecnici', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '36', '21017', '199', '2', 'Se il veicolo è munito di un cronotachigrafo digitale, dalla stampa dei dati tecnici è possibile ottenere informazioni sul cronotachigrafo digitale: modello, matricola, numero  di omologazione e numero della versione del software', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '36', '21017', '200', '3', 'Se il veicolo è munito di un cronotachigrafo digitale, dalla stampa dei dati tecnici è possibile risalire ai valori caratteristici del veicolo, tra cui lo sviluppo lineare e la marcatura degli pneumatici e la velocità massima ammessa dal limitatore', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '36', '21017', '201', '4', 'Se il veicolo è munito di un cronotachigrafo digitale è possibile verificare se il conducente ha superato i limiti massimi di velocità e determinare, tra l''altro, data e ora dell''evento e velocità massima raggiunta', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '36', '21017', '202', '5', 'Se il veicolo è munito di un cronotachigrafo digitale, dalla memoria dell''apparecchio è possibile stampare gli eccessi di velocità verificatisi negli ultimi 10 anni', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '36', '21017', '203', '6', 'Dalla memoria della carta tachigrafica del conducente è possibile risalire alla sua attività  lavorativa dei 365 giorni precedenti', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '36', '21017', '204', '7', 'Se il veicolo è munito di un cronotachigrafo digitale, dalla stampa dei dati tecnici è possibile risalire al tipo di motore e alla sua alimentazione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '36', '21017', '205', '8', 'Se il veicolo è munito di un cronotachigrafo digitale, dalla stampa dei dati tecnici è possibile risalire alla marca degli pneumatici', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '37', '021014-021012', '', '', 'Caratteristiche delle carte tachigrafiche: Generale', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '37', '021014-021012', '206', '1', 'Le carte tachigrafiche sono delle smart card che interagiscono con il cronotachigrafo  digitale, memorizzando varie informazioni relative all''attività del conducente', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '37', '021014-021012', '207', '2', 'Le carte tachigrafiche sono delle smart card che interagiscono con il cronotachigrafo  digitale memorizzando varie informazioni relative al veicolo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '37', '021014-021012', '208', '3', 'Le carte tachigrafiche utilizzabili nel cronotachigrafo digitale sono di quattro tipi', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '37', '021014-021012', '209', '4', 'Le carte tachigrafiche sono di quattro tipi: carta conducente, carta azienda, carta officina e carta controllo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '37', '021014-021012', '210', '5', 'Le carte tachigrafiche hanno validità di cinque anni, ad eccezione di quella dell''officina', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '37', '021014-021012', '211', '6', 'Il cronotachigrafo digitale utilizza carte tachigrafiche rilasciate dalla Camera di Commercio ', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '37', '021014-021012', '212', '7', 'Le carte tachigrafiche non interagiscono in alcun modo con il cronotachigrafo digitale', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '37', '021014-021012', '213', '8', 'Le carte tachigrafiche sono rilasciate dalla Motorizzazione Civile', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '37', '021014-021012', '214', '9', 'Le carte tachigrafiche utilizzabili nel cronotachigrafo digitale sono di due tipi: una per il conducente e una  per la Motorizzazione Civile', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '37', '021014-021012', '215', '10', 'Le carte tachigrafiche sono di cinque tipi ', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '38', '021014/021009/021012', '', '', 'Caratteristiche della carta tachigrafiche del conducente', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '38', '021014/021009/021012', '216', '1', 'La carta tachigrafica del conducente è personale e non cedibile ad altro conducente', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '38', '021014/021009/021012', '217', '2', 'La carta tachigrafica del conducente è a fondo bianco', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '38', '021014/021009/021012', '218', '3', 'La carta tachigrafica del conducente è a fondo bianco ed è personalizzata con la foto', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '38', '021014/021009/021012', '219', '4', 'La carta tachigrafica del conducente ha validità di 5 anni', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '38', '021014/021009/021012', '220', '5', 'La carta tachigrafica del conducente è rilasciata della camera di commercia', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '38', '021014/021009/021012', '221', '6', 'I requisiti per ottenere la carta tachigrafica del conducente sono: essere titolare di patente di categoria C, C1 o D, D1, essere residente in Italia e non essere già titolare di un''altra carta tachigrafica', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '38', '021014/021009/021012', '222', '7', 'I conducenti che effettuano trasporti internazionali possono ottenere il rilascio di due carte tachigrafiche', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '38', '021014/021009/021012', '223', '8', 'In caso di viaggio effettuato con due autisti (multipresenza), i due conducenti possono scambiarsi la carta tachigrafica', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '38', '021014/021009/021012', '224', '9', 'La carta tachigrafica del conducente ha le stesse funzioni della carta di qualificazione del conducente', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '38', '021014/021009/021012', '225', '10', 'La carta tachigrafica del conducente è di proprietà dell''azienda di cui è dipendente', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '38', '021014/021009/021012', '226', '11', 'La carta tachigrafica del conducente ha validità amministrativa di tre anni', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '38', '021014/021009/021012', '227', '12', 'La carta tachigrafica del conducente ha validità di 10 anni', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '38', '021014/021009/021012', '228', '13', 'La carta tachigrafica del conducente è rilasciata dagli uffici del lavoro', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '38', '021014/021009/021012', '229', '14', 'La carta tachigrafica del conducente è rilasciata dal comune di residenza dell''autista', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '38', '021014/021009/021012', '230', '15', 'La carta tachigrafica del conducente è revocata in caso di perdita di tutti i punti della patente di guida', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '38', '021014/021009/021012', '231', '16', 'La carta tachigrafica del conducente è a fondo giallo ed è valida per 3 anni', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '38', '021014/021009/021012', '232', '17', 'La carta tachigrafica del conducente può essere utilizzata per il pagamento dei pedaggi autostradali', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '38', '021014/021009/021012', '233', '18', 'In caso di superamento dei limiti di velocità di oltre 40 km/h la carta tachigrafica del conducente viene sospesa per un periodo di tre mesi', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '38', '021014/021009/021012', '234', '19', 'In caso di guida in stato di ebbrezza la carta tachigrafica del conducente viene revocata', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '38', '021014/021009/021012', '235', '20', 'La carta tachigrafica del conducente può essere utilizzata anche nei cronotachigrafi analogici di tipo elettronico', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '38', '021014/021009/021012', '236', '21', 'La carta tachigrafica del conducente può memorizzare al massimo i dati relativi a 20 giornate lavorative', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '39', '021014/021009/021012', '', '', 'Caratteristiche della carta tachigraficha dell''officina ', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '39', '021014/021009/021012', '237', '1', 'La carta tachigrafica dell''officina ha validità amministrativa di un anno', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '39', '021014/021009/021012', '238', '2', 'La carta tachigrafica dell''officina è a fondo rosso ed è valida per 1 anno', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '39', '021014/021009/021012', '239', '3', 'La carta tachigrafica dell''officina serve alla calibrazione, attivazione e manutenzione del cronotachigrafo digitale', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '39', '021014/021009/021012', '240', '4', 'La carta tachigrafica dell''officina viene utilizzata dal conducente durante le operazioni di riparazione del veicolo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '40', '021014/021009', '', '', 'Caratteristiche della carta tachigraficha dell'' azienda', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '40', '021014/021009', '241', '1', 'La carta tachigrafica dell''azienda facilita la gestione della flotta veicolare e consente  di ispezionare e stampare i dati di viaggio di tutti i veicoli dell''azienda', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '40', '021014/021009', '242', '2', 'La carta tachigrafica dell''azienda permette di bloccare o sbloccare il cronotachigrafo  digitale dei propri veicoli', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '40', '021014/021009', '243', '3', 'La carta tachigrafica dell''azienda permette di bloccare il cronotachigrafo digitale, ma non di sbloccarlo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '40', '021014/021009', '244', '4', 'La carta tachigrafica dell''azienda può essere utilizzata dal conducente se ha smarrito la propria carta tachigrafica', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '41', '021014/021009', '', '', 'Caratteristiche delle carta tachigrafica di controllo', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '41', '021014/021009', '245', '1', 'La carta tachigrafica di controllo è rilasciata alle autorità deputate ai controlli tecnico-amministrativi in  materia di sicurezza sul lavoro e alle autorità di polizia', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '41', '021014/021009', '246', '2', 'La carta tachigrafica di controllo è a fondo blu ed è valida 5 anni', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '41', '021014/021009', '247', '3', 'La carta controllo serve principalmente per effettuare il controllo del rispetto dei tempi di guida e della velocità', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '41', '021014/021009', '248', '4', 'La carta tachigrafica di controllo viene utilizzata dal conducente durante le operazioni di manutenzione del veicolo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '42', '021015-021009', '', '', 'Uso della carta tachigrafica del conducente', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '42', '021015-021009', '249', '1', 'La carta tachigrafica del conducente deve essere inserita nel cronotachigrafo del veicolo a partire dal momento in cui un conducente lo prende in consegna fino a quando non lo riconsegna', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '42', '021015-021009', '250', '2', 'L''inserimento della carta tachigrafica del conducente nel cronotachigrafo digitale deve avvenire con la chiave d''accensione inserita e il veicolo fermo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '42', '021015-021009', '251', '3', 'La carta tachigrafica del conducente va inserita nello slot 1 e, se il cronotachigrafo digitale la riconosce, visualizza il cognome del conducente', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '42', '021015-021009', '252', '4', 'La carta tachigrafica del conducente può essere estratta dall''apparecchio di controllo solo a veicolo fermo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '42', '021015-021009', '253', '5', 'L''estrazione della carta tachigrafica del conducente può avvenire solo a veicolo  fermo e con azione volontaria', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '42', '021015-021009', '254', '6', 'Prima di estrarre la carta tachigrafica del conducente a fine giornata lavorativa è necessario impostare il  selettore del cronotachigrafo digitale sulla modalità tempi di riposo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '42', '021015-021009', '255', '7', 'Quando il veicolo viene impiegato in attività o servizi che esulano dal campo di applicazione della normativa sociale, il conducente deve selezionare sul cronotachigrafo digitale la modalità OUT OF SCOPE', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '42', '021015-021009', '256', '8', 'L''operazione di inserimento della carta tachigrafica da parte del conducente deve avvenire prima di inserire la chiave d''accensione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '42', '021015-021009', '257', '9', 'La carta tachigrafica deve essere inserita con veicolo in movimento', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '42', '021015-021009', '258', '10', 'Quando il veicolo viene impiegato in attività o servizi che esulano dal campo di applicazione della normativa sociale, il cronotachigrafo digitale si imposta automaticamente sulla modalità OUT OF SCOPE', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '42', '021015-021009', '259', '11', 'Prima di estrarre la carta tachigrafica del conducente a fine giornata lavorativa è necessario impostare il selettore del cronotachigrafo digitale sulla modalità OUT OF SCOPE', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '42', '021015-021009', '260', '12', 'Quando il veicolo viene impiegato in attività o servizi che esulano dal campo di applicazione della normativa sociale, il conducente deve selezionare sul cronotachigrafo digitale la modalità tempi di riposo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '43', '21016', '', '', 'Guasto del cronotachigrafo digitale o smarrimento o furto della carta tachigrafica', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '43', '21016', '261', '1', 'In caso di smarrimento, danneggiamento o furto della propria carta tachigrafica, il conducente ha tempo sette giorni per richiedere alla Camera di Commercio il blocco e la sostituzione della carta stessa', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '43', '21016', '262', '2', 'In caso di smarrimento, danneggiamento o furto della carta tachigrafica è possibile guidare senza la carta per un periodo massimo di quindici giorni di calendario', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '43', '21016', '263', '3', 'In caso di smarrimento, danneggiamento o furto della carta tachigrafica il conducente, all''inizio della giornata lavorativa, deve stampare lo scontrino dei dati tecnici riportandovi manualmente il proprio nome e cognome, numero della patente e indicando eventuali altre attività compiute lontano dal veicolo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '43', '21016', '264', '4', 'In caso di smarrimento, danneggiamento o furto della carta tachigrafica il conducente, al termine dell''attività lavorativa, deve stampare lo scontrino contenente i gruppi di tempi registrati dall''apparecchio, completandolo con il proprio nome e cognome, numero della patente e firma', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '43', '21016', '265', '5', 'In caso di smarrimento, danneggiamento o furto della carta tachigrafica, il conducente ha tempo trenta giorni per richiedere alla Camera di Commercio il blocco e la sostituzione della carta stessa', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '43', '21016', '266', '6', 'In caso di smarrimento, danneggiamento o furto della carta tachigrafica, il conducente ha tempo sette giorni per richiedere alla Motorizzazione Civile il blocco e la sostituzione della carta stessa', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '43', '21016', '267', '7', 'In caso di smarrimento, danneggiamento o furto della carta tachigrafica, è possibile guidare senza la carta per un periodo massimo di quarantacinque giorni di calendario', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '43', '21016', '268', '8', 'In caso di smarrimento, danneggiamento o furto della carta tachigrafica il conducente non ha alcun obbligo particolare', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '43', '21016', '269', '9', 'In caso di smarrimento, danneggiamento o furto della carta tachigrafica il conducente deve presentare denuncia all''ufficio del lavoro', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '44', '21016', '', '', 'Guasto del cronotachigrafo digitale o smarrimento o furto della carta tachigrafica', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '44', '21016', '270', '1', 'In caso di rottura del cronotachigrafo digitale si deve provvedere alla sua riparazione nel più breve tempo possibile; è comunque consentito viaggiare con il dispositivo di controllo inefficiente per un periodo massimo di sette giorni', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '44', '21016', '271', '2', 'In caso di rottura del cronotachigrafo digitale, il conducente deve compilare giornalmente il retro dello scontrino con i seguenti dati: nome e cognome, numero della patente, targa del veicolo, luogo di partenza  e luogo di arrivo, chilometri percorsi, data e firma e deve trascrivere le ore di guida e di riposo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '44', '21016', '272', '3', ' In caso di rottura del cronotachigrafo digitale si deve provvedere alla sua riparazione nel più breve tempo  possibile; è comunque consentito viaggiare con il dispositivo di controllo inefficiente per un periodo massimo di quindici giorni', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '44', '21016', '273', '4', 'In caso di rottura del cronotachigrafo digitale si deve provvedere alla sua riparazione nel più breve tempo possibile; è comunque consentito viaggiare con il dispositivo di controllo inefficiente per un periodo massimo di due giorni lavorativi', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '45', '21011', '', '', 'Guasto del cronotachigrafo', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '45', '21011', '274', '1', 'In caso di guasto del cronotachigrafo, se si sta effettuando un viaggio di durata superiore a sette giorni, il  conducente deve provvedere alla riparazione del guasto nel corso del viaggio', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '45', '21011', '275', '2', 'In caso di guasto del cronotachigrafo, se il viaggio ha una durata inferiore a sette giorni, il conducente può provvedere alla riparazione del guasto al rientro in sede', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '45', '21011', '276', '3', 'In caso di guasto del cronotachigrafo, occorre rivolgersi a un''officina autorizzata', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '45', '21011', '277', '4', 'In caso di guasto del cronotachigrafo, sia analogico che digitale, il conducente deve provvedere alle idonee registrazioni manuali della sua attività', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '45', '21011', '278', '5', 'In caso di guasto del cronotachigrafo il conducente può evitare di effettuare le registrazioni', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '45', '21011', '279', '6', 'In caso di guasto del cronotachigrafo il conducente è obbligato ad effettuare le registrazioni manuali  solamente se il viaggio è superiore a sette giorni', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '45', '21011', '280', '7', 'In caso di guasto del cronotachigrafo, il conducente deve provvedere alla sua riparazione entro le ventiquattro ore successive', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '45', '21011', '281', '8', 'In caso di guasto del cronotachigrafo il conducente deve necessariamente interrompere il viaggio', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '45', '21011', '282', '9', 'In caso di guasto del cronotachigrafo il conducente deve rivolgersi alla sede della Camera di Commercio  più vicina', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '46', '21016', '', '', 'Passando dalla guida di un veicolo munito di cronotachigrafo analogico a uno munito di cronotachigrafo  digitale', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '46', '21016', '283', '1', 'Passando dalla guida di un veicolo munito di cronotachigrafo analogico a uno munito di cronotachigrafo  digitale, il conducente deve terminare la compilazione del foglio di registrazione e inserire manualmente  sul cronotachigrafo digitale i tempi di guida e di riposo già effettuati, dopo aver inserito la propria carta tachigrafica', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '46', '21016', '284', '2', 'Passando dalla guida di un veicolo munito di cronotachigrafo digitale a uno munito di cronotachigrafo analogico, il conducente deve stampare lo scontrino della giornata lavorativa, compilare un foglio di registrazione e inserirlo nel cronotachigrafo analogico', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '46', '21016', '285', '3', 'Passando dalla guida di un veicolo munito di cronotachigrafo analogico a uno munito di cronotachigrafo digitale, non è necessario conservare il foglio di registrazione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '47', '21010', '', '', 'Installazione e manutenzione del cronotachigrafo digitale', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '47', '21010', '286', '1', 'Il cronotachigrafo digitale può essere montato solo da officine autorizzate', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '47', '21010', '287', '2', 'Il cronotachigrafo digitale è munito di sigilli e non deve essere manomesso dal conducente', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '47', '21010', '288', '3', 'Il cronotachigrafo digitale deve essere tarato prima dell''immissione del veicolo in circolazione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '47', '21010', '289', '4', 'Il cronotachigrafo digitale ha una targhetta contenente le informazioni tecniche di montaggio', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '47', '21010', '290', '5', 'Il cronotachigrafo digitale deve essere controllato, di norma, ogni due anni', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '47', '21010', '291', '6', 'Il cronotachigrafo digitale può essere revisionato da qualunque officina idoneamente attrezzata', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '47', '21010', '292', '7', 'Il cronotachigrafo digitale può essere riparato da qualunque officina meccanica', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '47', '21010', '293', '8', 'Il cronotachigrafo digitale deve essere tarato entro quattro settimane dall''installazione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '47', '21010', '294', '9', 'Il cronotachigrafo digitale non deve essere revisionato, di norma, ogni due anni', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('2', '47', '21010', '295', '10', 'In caso di guasto grave, il cronotachigrafo digitale può essere riparato da qualunque officina meccanica', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '48', '35002', '', '', 'Comportamenti in presenza del segnale STRADA DEFORMATA alla guida di un veicolo per il trasporto di persone ', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '48', '35002', '296', '1', 'In presenza del segnale raffigurato, i conducenti di autobus devono ridurre sensibilmente la velocità per evitare eccessive sollecitazioni ai passeggeri', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '48', '35002', '297', '2', 'In presenza del segnale raffigurato, i conducenti di autobus devono ridurre sensibilmente la velocità per evitare cadute dei passeggeri, soprattutto se viaggiano in piedi', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '48', '35002', '298', '3', 'In presenza del segnale raffigurato, i conducenti di autobus devono ridurre sensibilmente la velocità per non peggiorare il confort di marcia dei passeggeri', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '48', '35002', '299', '4', 'In presenza del segnale raffigurato, i conducenti di autobus devono ridurre sensibilmente la velocità per evitare urti tra i passeggeri, soprattutto se viaggiano in piedi', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '48', '35002', '300', '5', 'In presenza del segnale raffigurato, i conducenti di autobus devono chiedere ai passeggeri di slacciare le cinture di sicurezza per evitare che eventuali sobbalzi causino traumi all''addome o al petto', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '48', '35002', '301', '6', 'In presenza del segnale raffigurato, i conducenti di autobus devono dare un annuncio via microfono a tutti i passeggeri del sopraggiungente pericolo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '48', '35002', '302', '7', 'In presenza del segnale raffigurato, i conducenti di autobus devono accelerare per passare il tratto di strada nel minor tempo possibile ', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '49', '35004', '', '', 'Comportamenti in presenza del segnale DOSSO alla guida di un veicolo per il trasporto di persone ', '');
