INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '53', '35012', '323', '3', 'In presenza del segnale raffigurato, il conducente deve prestare particolare attenzione a non scagliare il pietrisco presente sulla carreggiata contro i pedoni', 'F');
