INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '54', '35013', '325', '2', 'In presenza del segnale raffigurato, il conducente di un autotreno può procedere per primo perché, essendo alla guida di un veicolo ingombrante, ha la precedenza sui veicoli di massa minore', 'F');
