INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '70', '33036', '419', '2', 'La segnaletica in figura indica lo spazio per la fermata di autobus e filobus in servizio pubblico di linea', 'V');
