INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('3', '70', '33036', '421', '4', 'La segnaletica in figura vieta la sosta, ma non la fermata, anche nelle parti di strada individuate dalla striscia gialla a zig zag', 'V');
