INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '71', '42001', '430', '3', 'La qualificazione CQC deve essere rinnovata ogni cinque anni', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '71', '42001', '431', '4', 'Per rinnovare la qualificazione CQC occorre seguire un corso di formazione periodica', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '71', '42001', '432', '5', 'Per rinnovare la qualificazione CQC occorre sottoporsi a visita medica', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '72', '42001', '', '', 'La carta tachigrafica ', '');
