INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '72', '42001', '435', '3', 'La carta tachigrafica è rilasciata dalla Camera di commercio', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '72', '42001', '436', '4', 'La carta tachigrafica è rilasciata dall''Ufficio Motorizzazione civile competente del territorio di residenza del richiedente', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '72', '42001', '437', '5', 'La carta tachigrafica del conducente non ha scadenza', 'F');
