INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '73', '42001', '', '', 'Documenti necessari per il trasporto di persone ', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '73', '42001', '439', '1', 'Un autocarro, per circolare, deve essere in regola con gli obblighi assicurativi', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '73', '42001', '440', '2', 'Per circolare con un veicolo a motore in uno Stato non appartenente all''Unione europea occorre il certificato di assicurazione internazionale', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '73', '42001', '441', '3', 'Per guidare un filobus occorre il certificato di idoneità rilasciato dall''USTIF', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '73', '42001', '442', '4', 'Una copia conforme dell''autorizzazione al noleggio con conducente deve essere presente a bordo dell''autobus immatricolato in base ad essa', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '73', '42001', '443', '5', 'Per guidare un autobus occorre essere titolare di certificato di abilitazione professionale di tipo KD', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '73', '42001', '444', '6', 'Per guidare un filobus occorre il certificato di idoneità rilasciato dalla Prefettura competente per territorio', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '73', '42001', '445', '7', 'Per guidare un filobus occorre un certificato professionale ATP rilasciato dall''Ufficio Motorizzazione civile competente per territorio', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '73', '42001', '446', '8', 'Per effettuare un servizio di autotrasporto verso un altro Stato dell''Unione europea è necessario avere il passaporto', 'F');
