INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '74', '42002', '', '', 'Servizi regolari nel territorio di stati UE ', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '74', '42002', '448', '1', 'Un autobus che effettua servizi regolari nel territorio di Stati UE deve avere a bordo l''originale o una copia conforme certificata dell''autorizzazione rilasciata dallo Stato membro sul cui territorio si trova il punto di partenza', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '74', '42002', '449', '2', 'Un autobus che effettua servizi regolari nel territorio di Stati UE deve avere a bordo l''originale del documento di controllo che riporti orari e prezzi del servizio', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '74', '42002', '450', '3', 'Un autobus che effettua servizi regolari nel territorio di Stati UE deve avere a bordo l''originale del documento di controllo che riporti l''elenco d3. Un autobus che effettua servizi regolari nel territorio di Stati UE deve avere a bordo l''originale del documento di controllo che riporti l''elenco dei passeggeri trasportati', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '75', '42002', '', '', 'Servizio di noleggio con conducente in ambito UE ', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '75', '42002', '451', '1', 'Quando si effettua un servizio di noleggio con conducente in ambito UE (servizio occasionale), a bordo dell''autobus che lo effettua deve trovarsi l''originale del foglio di viaggio compilato dall''impresa o dall''autista', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '75', '42002', '452', '2', 'Sul foglio di viaggio di un servizio di noleggio con conducente in ambito UE (servizio occasionale) deve figurare il tipo di servizio svolto', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '75', '42002', '453', '3', 'Sul foglio di viaggio di un servizio di noleggio con conducente in ambito UE (servizio occasionale) deve figurare l''itinerario principale del servizio svolto', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '75', '42002', '454', '4', 'Sul foglio di viaggio di un servizio di noleggio con conducente in ambito UE (servizio occasionale) deve figurare la denominazione dell''impresa che svolge il servizio', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '75', '42002', '455', '5', 'Sul foglio di viaggio di un servizio di noleggio con conducente in ambito UE (servizio occasionale) deve figurare il numero e tipo di patenteCQC dell''autista', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '75', '42002', '456', '6', 'Quando si effettua un servizio di noleggio con conducente in ambito UE (servizio occasionale), a bordo dell''autobus che lo effettua deve trovarsi copia autenticata dell''esito dell''ultima revisione tecnica del veicolo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '76', '42002', '', '', 'Servizi regolari nel territorio di stati UE ', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '76', '42002', '457', '1', 'I trasporti di cabotaggio in Paesi UE, svolti sotto forma di servizi occasionali, sono effettuati in base a un documento di controllo, il foglio di viaggio, che deve trovarsi a bordo del veicolo in originale ed essere esibito su richiesta degli agenti preposti al controllo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '76', '41002', '458', '2', 'I trasporti di cabotaggio in Paesi UE, svolti sotto forma di servizi occasionali, sono effettuati in base a un documento di controllo, il foglio di viaggio, che deve contenere i luoghi di partenza e di destinazione del servizio', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '76', '41002', '459', '3', 'I trasporti di cabotaggio in Paesi UE, svolti sotto forma di servizi occasionali, sono effettuati in base a un documento di controllo, il foglio di viaggio, che deve contenere le date di partenza e di fine servizio ', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '76', '41002', '460', '4', 'I trasporti di cabotaggio in Paesi UE, svolti sotto forma di servizi occasionali, sono effettuati in base a un documento di controllo, il foglio di viaggio, che deve trovarsi a bordo del veicolo in copia ed essere esibito su richiesta degli agenti preposti al controllo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('4', '76', '41002', '461', '5', 'I trasporti di cabotaggio in Paesi UE, svolti sotto forma di servizi occasionali, sono effettuati in base a un documento di controllo, il foglio di viaggio, che deve contenere l''elenco dei passeggeri trasportati', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '77', '92001', '', '', 'Dotazioni di sicurezza degli autobus 1 ', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '77', '92001', '462', '1', 'Gli autobus sono di norma muniti di porte e uscite di sicurezza', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '77', '92001', '463', '2', 'Gli autobus sono di norma muniti di cassetta del pronto soccorso', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '77', '92001', '464', '3', 'Gli autobus fino a 22 posti a sedere devono essere dotati di 3 uscite di emergenza', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '77', '92001', '465', '4', 'Gli autobus fino a 35 posti a sedere devono essere dotati di 4 uscite di emergenza', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '77', '92001', '466', '5', 'Gli autobus oltre i 35 posti a sedere devono essere dotati di 5 uscite di emergenza', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '77', '92001', '467', '6', 'I veicoli aventi massa complessiva a pieno carico maggiore di 3,5 tonnellate devono essere muniti di una coppia di cunei', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '77', '92001', '468', '7', 'Sugli autobus, gli spazi riservati alle sedie a rotelle devono essere obbligatoriamente muniti di sistemi di ritenuta ', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '77', '92001', '469', '8', 'I veicoli aventi massa complessiva a pieno carico maggiore di 3,5 tonnellate devono essere muniti di almeno due coppie di cunei', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '77', '92001', '470', '9', 'Sugli autobus, gli spazi riservati alle sedie a rotelle non devono essere obbligatoriamente muniti di sistemi di ritenuta', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '77', '92001', '471', '10', 'Gli autobus sono di norma muniti di tester per il rilevamento di assunzione sostanze alcoliche', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '77', '92001', '472', '11', 'Gli autobus sono di norma muniti di almeno 3 porte e uscite di sicurezza', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '77', '92001', '473', '12', 'Gli autobus con numero di passeggeri superiore a 17 devono essere muniti di almeno 2 cassette del pronto soccorso', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '77', '92001', '474', '13', 'Gli autobus fino a 30 posti a sedere devono essere muniti di un estintore a schiuma da 2 litri o a neve carbonica da 5 chilogrammi', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '77', '92001', '475', '14', 'Gli autobus oltre i 22 posti a sedere devono essere dotati di 3 uscite di emergenza', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '77', '92001', '476', '15', 'Gli autobus oltre i 35 posti a sedere devono essere dotati di 4 uscite di emergenza', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '77', '92001', '477', '16', 'Gli autobus fino a 22 posti a sedere devono essere dotati di 1 uscita di emergenza ', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '78', '92005', '', '', 'Dotazioni di sicurezza degli autobus 1 ', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '78', '92005', '478', '1', 'Gli autobus devono essere dotati di due cunei fermaruota', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '78', '92005', '479', '2', 'I rimorchi degli autobus, di massa complessiva a pieno carico superiore a 3,5 tonnellate, devono essere dotati di due cunei fermaruota', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '78', '92005', '480', '3', 'Sugli autobus devono essere presenti una o più cassette, in base a quanto previsto dall''autorizzazione, contenenti il materiale di primo soccorso previsto dalle norme', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '78', '92005', '481', '4', 'Sugli autobus devono essere presenti uscite di emergenza idoneamente segnalate', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '78', '92005', '482', '5', 'Le uscite di emergenza presenti sugli autobus possono essere porte, finestrini o botole', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '78', '92005', '483', '6', 'Sugli autobus deve essere presente un pulsante di emergenza posto a portata di mano del conducente', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '78', '92005', '484', '7', 'Il pulsante di emergenza presente sugli autobus, se premuto, consente l''immediato arresto del motore in caso di incidente ', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '78', '92005', '485', '8', 'Le cassette contenenti materiale di primo soccorso non sono necessarie se sull''autobus è presente un contatto radiomobile con un ospedale', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '78', '92005', '486', '9', 'Il pulsante di emergenza presente sugli autobus, se premuto, consente l''immediata apertura di tutte le porte del veicolo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '78', '92005', '487', '10', 'Il pulsante di emergenza presente sugli autobus, se premuto, consente l''immediata espulsione verso l''esterno dei vetri dei finestrini di sicurezza', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '78', '92005', '488', '11', 'Il pulsante di emergenza presente sugli autobus, se premuto, consente di lanciare un messaggio di SOS al numero di telefono 118', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '78', '92005', '489', '12', 'Il pulsante di emergenza presente sull''autobus deve trovarsi sotto il sedile del conducente, per evitare il pericolo di azionamento involontario', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '78', '92005', '490', '13', 'Sugli autobus interregionali con più di trenta posti e percorso superiore a 500 chilometri, deve essere presente a bordo un medico o un paramedico che si occupi di eventuali soccorsi sanitari', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '78', '92005', '491', '14', 'Le uscite di emergenza presenti sugli autobus con più di trenta posti possono essere solo porte', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '79', '92001', '', '', 'Estintori in dotazione agli autobus 1 ', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '79', '92001', '492', '1', 'Gli autobus sono di norma muniti di estintori', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '79', '92001', '493', '2', 'Gli estintori presenti a bordo degli autobus devono essere revisionati ogni 6 mesi', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '79', '92001', '494', '3', 'Gli autobus con un numero di posti a sedere non superiore a 30 posti devono essere muniti di un estintore a schiuma da 5 litri o a neve carbonica da 2 chilogrammi', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '79', '92001', '495', '4', 'Almeno un estintore in dotazione agli autobus deve essere sistemato in prossimità del sedile del conducente ', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '79', '92001', '496', '5', 'Tutti gli estintori in dotazione agli autobus devono essere sistemati in prossimità del sedile del conducente', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '79', '92001', '497', '6', 'Gli estintori presenti a bordo degli autobus devono essere revisionati ogni 12 mesi', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '80', '92005', '', '', 'Estintori in dotazione agli autobus 2 ', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '80', '92005', '498', '1', 'Sugli autobus con al massimo trenta posti, deve trovarsi almeno un estintore da cinque litri se a schiuma o uno da due chili se a neve carbonica', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '80', '92005', '499', '2', 'Sugli autobus con più di trenta posti, deve trovarsi almeno un estintore da cinque litri se a schiuma, oppure due estintori da due chili ciascuno se a neve carbonica', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '80', '92005', '500', '3', 'Sugli autobus, ogni estintore deve essere alloggiato in idonea sede di cui una situata nei pressi del conducente ', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '80', '92005', '501', '4', 'Gli estintori presenti sugli autobus devono essere di tipo omologato', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '80', '92005', '502', '5', 'Gli estintori presenti sugli autobus devono sottostare a controllo periodico a scadenze prefissate', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '80', '92005', '503', '6', 'Gli estintori presenti sugli autobus devono essere controllati ogni sei mesi', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '80', '92005', '504', '7', 'L''avvenuto controllo degli estintori presenti sugli autobus da parte di ditta specializzata, deve essere attestato da annotazione posta su apposita targhetta fissata sull''estintore stesso ', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '80', '92005', '505', '8', 'Gli autobus devono essere dotati di estintori nel caso non abbiano uscite di sicurezza', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '80', '92005', '506', '9', 'Gli estintori degli autobus devono essere sostituiti in occasione della revisione periodica del veicolo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '80', '92005', '507', '10', 'L''avvenuto controllo degli estintori presenti sugli autobus deve essere certificato dal conducente del veicolo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '80', '92005', '508', '11', 'L''avvenuto controllo degli estintori presenti sugli autobus deve essere annotato sulla carta di circolazione del veicolo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '80', '92005', '509', '12', 'Gli estintori presenti sugli autobus non possono che essere del tipo a schiuma', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '80', '92005', '510', '13', 'Ogni sei mesi gli estintori presenti sugli autobus vanno svuotati, puliti internamente e ricaricati', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '80', '92005', '511', '14', 'Sugli autobus con al massimo trenta posti, deve trovarsi almeno un estintore da otto litri se a schiuma o uno da tre chili se a neve carbonica', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '80', '92005', '512', '15', 'Sugli autobus con al massimo trenta posti, deve trovarsi almeno un estintore da cinque chili a polvere', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '80', '92005', '513', '16', 'Sugli autobus interregionali con più di trenta posti, deve trovarsi almeno un estintore da 200 grammi a schiuma per ogni fila di sedili', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '80', '92005', '514', '17', 'Sugli autobus con più di trenta posti, deve trovarsi almeno un estintore da otto chili a schiuma, oppure due estintori da quattro chili se a neve carbonica', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '80', '92005', '515', '18', 'Durante la stagione invernale, gli estintori presenti sugli autobus possono essere tenuti all''interno del portabagagli', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '80', '92005', '516', '19', 'Ogni sei mesi il conducente deve provare l''estintore presente sull''autobus, azionandolo per due secondi, annotando data ed esito della prova sull''apposita targhetta posta sull''estintore', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '80', '92005', '517', '20', 'Gli estintori presenti sugli autobus devono essere controllati ogni dodici mesi', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '80', '92005', '518', '21', 'L''annotazione dell''avvenuto controllo semestrale degli estintori presenti sull''autobus deve essere conservata presso l''azienda titolare del veicolo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '81', '92002', '', '', 'Uso delle cinture di sicurezza 1 ', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '81', '92002', '519', '1', 'Durante la marcia, tutti gli occupanti di età superiore a 3 anni degli autobus senza posti in piedi devono indossare idonei sistemi di ritenuta, se il veicolo ne è dotato', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '81', '92002', '520', '2', 'Durante la marcia, i conducenti degli autocarri adibiti al trasporto di merci sono, di norma, obbligati a indossare le cinture di sicurezza, se il veicolo ne è dotato', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '81', '92002', '521', '3', 'Durante la marcia, il conducente di un autobus in servizio interregionale deve indossare le cinture di sicurezza, se il veicolo ne è dotato', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '81', '92002', '522', '4', 'Durante la marcia in un centro abitato, i conducenti degli autocarri sono, di norma, obbligati a indossare le cinture di sicurezza, ove i veicoli ne siano dotati', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '81', '92002', '523', '5', 'Durante la marcia, i conducenti di autocarri di massa complessiva a pieno carico inferiore a 3,5 t sono, di norma, obbligati a indossare le cinture di sicurezza, ove i veicoli ne siano dotati ', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '81', '92002', '524', '6', 'L''uso delle cinture di sicurezza non è di norma obbligatorio per i passeggeri di autobus in servizio interregionale, anche se gli stessi ne sono dotati', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '81', '92002', '525', '7', 'Di norma, le cinture di sicurezza non sono obbligatorie sugli autocarri di massa complessiva inferiore a 3,5 t', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '81', '92002', '526', '8', 'Di norma, le cinture di sicurezza non sono obbligatorie sugli autobus di categoria M3', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '81', '92002', '527', '9', 'La legge non prevede esenzioni dall''obbligo di indossare le cinture di sicurezza per i passeggeri di autobus che effettuano servizi interregionali', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '81', '92002', '528', '10', 'Di norma la legge non obbliga i conducenti di autocarri che circolano nei centri abitati ad indossare le cinture di sicurezza', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '82', '92003', '', '', 'Uso delle cinture di sicurezza 2 ', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '82', '92003', '529', '1', 'È opportuno che il conducente ricordi ai passeggeri l''obbligo di indossare le cinture di sicurezza', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '82', '92003', '530', '2', 'È opportuno che il conducente verifichi periodicamente l''efficienza delle cinture di sicurezza presenti sul veicolo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '82', '92003', '531', '3', 'Durante la marcia, tutti gli occupanti di età superiore a 3 anni degli autobus senza posti in piedi devono indossare idonei sistemi di ritenuta, se il veicolo ne è dotato', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '82', '92003', '532', '4', 'Il conducente di un autobus NCC è responsabile dei danni eventualmente subiti dal passeggero che, senza giustificato motivo, non abbia indossato le cinture di sicurezza durante la circolazione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '82', '92003', '533', '5', 'Durante la marcia, il conducente di un autobus in servizio interregionale deve indossare le cinture di sicurezza, se il veicolo ne è dotato ', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '82', '92003', '534', '6', 'L''uso delle cinture di sicurezza non è di norma obbligatorio per i passeggeri di autobus in servizio interregionale, anche se gli stessi ne sono dotati', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '82', '92003', '535', '7', 'Di norma, le cinture di sicurezza non sono obbligatorie sugli autobus di categoria M3', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '82', '92003', '536', '8', 'La legge non prevede esenzioni dall''obbligo di indossare le cinture di sicurezza per i passeggeri di autobus che effettuano servizi interregionali', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '82', '92003', '537', '9', 'Il conducente di un autobus non ha l''obbligo di ricordare ai passeggeri di indossare, quando previsto, le cinture di sicurezza', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '82', '92003', '538', '10', 'Le cinture di sicurezza degli autocarri non sono soggette ad omologazione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '82', '92003', '539', '11', 'Le cinture di sicurezza degli autobus non sono soggette ad omologazione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '82', '92003', '540', '12', 'Il conducente di un autobus adibito a trasporto locale in servizio urbano deve ricordare ai passeggeri seduti l''obbligo di indossare le cinture di sicurezza', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '83', '92004', '', '', 'Uso delle cinture di sicurezza 3 ', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '83', '92004', '541', '1', 'Sono previste sanzioni per chi, pur facendo uso della cintura di sicurezza, ne alteri o ostacoli il normale funzionamento', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '83', '92004', '542', '2', 'Il conducente che non abbia indossato la cintura di sicurezza durante la marcia, può essere proiettato contro il parabrezza in caso d''improvvisa frenata', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '83', '92004', '543', '3', 'Le donne in stato di gravidanza con particolari condizioni di rischio, purché opportunamente certificate, non sono obbligate ad indossare le cinture di sicurezza', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '83', '92004', '544', '4', 'Le persone che soffrono di particolari malattie, purché opportunamente certificate, non sono obbligate ad indossare la cintura di sicurezza ', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '83', '92004', '545', '5', 'Se il veicolo è dotato di airbag frontali e laterali, si può fare a meno di indossare la cintura di sicurezza', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '83', '92004', '546', '6', 'Le cinture di sicurezza devono essere obbligatoriamente allacciate solo se si circola a velocità uguale o superiore a 30 km/h', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '83', '92004', '547', '7', 'Le persone di età superiore a 65 anni non sono obbligate ad indossare le cinture di sicurezza', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '83', '92004', '548', '8', 'Coloro che guidano per più di 7 ore al giorno sono esentati dall''uso delle cinture di sicurezza', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '84', '93002', '', '', 'Obblighi di manutenzione, pulizia e verifica ', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '84', '93002', '549', '1', 'Il proprietario o il conducente devono seguire lo scadenzario degli interventi di manutenzione programmata indicato sul manuale di uso e manutenzione fornito dal costruttore del veicolo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '84', '93002', '550', '2', 'La pulizia del parabrezza è rilevante anche ai fini del funzionamento e della durata delle spazzole del tergicristallo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '84', '93002', '551', '3', 'Prima di partire occorre verificare la pulizia delle superfici vetrate e degli specchi retrovisori', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '84', '93002', '552', '4', 'Vanno verificati periodicamente lo stato di usura e la pressione di gonfiaggio di tutti gli pneumatici', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '84', '93002', '553', '5', 'Prima di iniziare il viaggio devono essere verificati i livelli di olio per il motore, liquido di raffreddamento e liquido lavacristalli', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '84', '93002', '554', '6', 'All''inizio del viaggio è necessario verificare il livello del liquido dei freni, del liquido della batteria e lo stato delle spazzole tergicristallo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '84', '93002', '555', '7', 'Prima di iniziare il viaggio, il conducente deve provvedere a verificare il funzionamento di tutti i dispositivi di illuminazione e segnalazione visiva', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '84', '93002', '556', '8', 'Il conducente, prima di iniziare il viaggio, deve provvedere alla verifica di eventuali perdite d''olio dalle condotte degli organi di frenatura ', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '84', '93002', '557', '9', 'La pulizia del parabrezza non è rilevante ai fini del funzionamento e della durata delle spazzole del tergicristallo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '84', '93002', '558', '10', 'Il conducente, prima di partire, non è tenuto a verificare la pulizia delle superfici vetrate in quanto facilmente soggette ad imbrattamento durante il percorso', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '84', '93002', '559', '11', 'La pressione di gonfiaggio degli pneumatici di un autobus deve essere verificata e comparata con i valori minimi indicati sulla carta di circolazione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '84', '93002', '560', '12', 'Prima di iniziare qualunque viaggio con un autobus, devono essere verificati i livelli di olio del cambio di velocità e degli organi del differenziale', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '84', '93002', '561', '13', 'Non è consigliato, ogni qualvolta si inizi un viaggio con un autobus, controllare il livello del liquido dei freni, del liquido della batteria e lo stato delle spazzole tergicristallo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '84', '93002', '562', '14', 'Durante il viaggio, il conducente di un autobus è tenuto a verificare in ogni momento il funzionamento di tutti i dispositivi di illuminazione e segnalazione visiva, con particolare riferimento ai dispositivi di illuminazione delle uscite di servizio', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '84', '93002', '563', '15', 'Il conducente prima di iniziare il viaggio non ha l''obbligo di provvedere alla verifica di eventuali perdite d''olio dalle condotte degli organi di frenatura in quanto tale incombenza spetta in ogni caso al proprietario del mezzo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '85', '93002', '', '', 'Revisione periodica di un autobus ', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '85', '93002', '564', '1', 'Tutti gli autobus hanno l''obbligo di revisione periodica annuale', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '85', '93002', '565', '2', 'La revisione periodica di un autobus deve essere effettuata entro il mese corrispondente a quello in cui è stata sostenuta la revisione nell''anno precedente ', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '85', '93002', '566', '3', 'Tutti gli autobus hanno l''obbligo di revisione periodica biennale', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '85', '93002', '567', '4', 'La revisione periodica di un autobus deve essere effettuata entro il mese successivo a quello in cui è stata sostenuta la revisione nell''anno precedente', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '85', '93002', '568', '5', 'La circolazione di un autobus in difetto di revisione è punita con il solo ritiro della carta di circolazione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '85', '93002', '569', '6', 'La circolazione di un autobus in difetto di revisione è punita con il ritiro della patente di guida dell''autista', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '86', '93001', '', '', 'Verifica degli specchi retrovisori ', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '86', '93001', '570', '1', 'È opportuno che il conducente verifichi, prima di ogni viaggio, la posizione, pulizia e stato generale degli specchi retrovisori del veicolo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '86', '93001', '571', '2', 'Anche se gli specchi retrovisori sono ben regolati, esistono parti della strada che il conducente non riesce a vedere ', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '86', '93001', '572', '3', 'Vi sono parti della strada che il conducente riesce a vedere direttamente e altre che può vedere solo di riflesso attraverso gli specchi retrovisori ', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '86', '93001', '573', '4', 'Se gli specchi retrovisori sono ben regolati, non esistono parti della strada che il conducente non possa vedere chiaramente', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '86', '93001', '574', '5', 'Se gli specchi retrovisori di un lato sono sporchi o danneggiati, non vi sono pericoli se quelli dell''altro lato sono in perfetto ordine', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('6', '86', '93001', '575', '6', 'Gli specchi retrovisori possono essere integralmente sostituiti da telecamere opportunamente disposte', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '87', '904002', '', '', 'Classificazione internazionale dei veicoli per trasporto persone ', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '87', '904002', '576', '1', 'La categoria internazionale M1 comprende veicoli a motore destinati al trasporto di persone, aventi al massimo otto posti a sedere oltre al sedile del conducente', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '87', '904002', '577', '2', 'La categoria internazionale M2 comprende veicoli a motore destinati al trasporto di persone, aventi più di otto posti a sedere oltre al sedile del conducente e massa massima non superiore a 5 tonnellate', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '87', '904002', '578', '3', 'La categoria internazionale M3 comprende veicoli a motore destinati al trasporto di persone, aventi più di otto posti a sedere oltre al sedile del conducente e massa massima superiore a 5 tonnellate', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '87', '904002', '579', '4', 'La categoria internazionale M comprende veicoli a motore destinati al trasporto di persone, aventi almeno quattro ruote', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '87', '904002', '580', '5', 'La categoria internazionale M comprende veicoli a motore destinati al trasporto di persone ', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '87', '904002', '581', '6', 'La categoria internazionale M1 comprende veicoli a motore destinati al trasporto di persone, aventi più di otto posti a sedere oltre al sedile del conducente e massa massima non superiore a 5 tonnellate', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '87', '904002', '582', '7', 'La categoria internazionale M2 comprende veicoli a motore destinati al trasporto di persone, aventi più di otto posti a sedere oltre al sedile del conducente e massa massima non superiore a 10 tonnellate', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '87', '904002', '583', '8', 'La categoria internazionale M3 comprende veicoli a motore destinati al trasporto di persone, aventi più di otto posti a sedere oltre al sedile del conducente e massa massima non superiore a 5 tonnellate', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '87', '904002', '584', '9', 'La categoria internazionale M4 comprende veicoli a motore destinati al trasporto di persone, aventi più di otto posti a sedere oltre al sedile del conducente e massa massima non superiore a 5 tonnellate', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '87', '904002', '585', '10', 'La categoria internazionale M comprende rimorchi e semirimorchi ', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '88', '904004', '', '', 'Classificazione internazionale dei rimorchi  ', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '88', '904004', '586', '1', 'La categoria internazionale O1 comprende i rimorchi di massa massima non superiore a 0,75 tonnellate', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '88', '904004', '587', '2', 'La categoria internazionale O2 comprende i rimorchi di massa massima oltre 0,75 tonnellate ma non oltre 3,5 tonnellate', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '88', '904004', '588', '3', 'La categoria internazionale O3 comprende i rimorchi di massa massima oltre 3,5 tonnellate ma non oltre 10 tonnellate', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '88', '904004', '589', '4', 'La categoria internazionale O4 comprende i rimorchi di massa massima superiore a 10 tonnellate', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '88', '904004', '590', '5', 'La categoria internazionale O comprende rimorchi e semirimorchi', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '88', '904004', '591', '6', 'La categoria internazionale O1 comprende i carrelli appendice', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '88', '904004', '592', '7', 'La categoria internazionale O2 comprende i rimorchi di massa massima superiore a 2 tonnellate', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '88', '904004', '593', '8', 'La categoria internazionale O3 comprende quei rimorchi di massa superiore a 10 tonnellate che sono dotati di frenatura continua e sospensioni pneumatiche', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '88', '904004', '594', '9', 'La categoria internazionale O4 comprende i rimorchi e i semirimorchi che possono essere utilizzati nella marcia fuoristrada', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '88', '904004', '595', '10', 'La categoria internazionale O comprende tutti i veicoli di importazione extra UE', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '88', '904004', '596', '11', 'La categoria internazionale O5 comprende i rimorchi e i semirimorchi con agganciamento automatico', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '89', '94005', '', '', 'Classificazione internazionale dei veicoli', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '89', '94005', '597', '1', 'Nella classificazione internazionale dei veicoli, il simbolo G, associato a quello della categoria internazionale di appartenenza M o N, identifica i veicoli fuoristrada', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '89', '94005', '598', '2', 'Nella classificazione internazionale dei veicoli, il simbolo G, associato a quello della categoria internazionale di appartenenza M o N, identifica i veicoli ad uso militare', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '90', '94001', '', '', 'Classificazione degli autobus ', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '90', '94001', '599', '1', 'Per autobus di classe A si intende un autobus con posti in piedi e con numero totale di passeggeri ammesso inferiore a 22, senza contare il conducente', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '90', '94001', '600', '2', 'Per autobus di classe B si intende un autobus con solo posti a sedere e con numero totale di passeggeri ammesso inferiore a 22, senza contare il conducente ', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '90', '94001', '601', '3', 'Per autobus di classe I si intende un autobus con posti in piedi e con numero totale superiore a 22, senza contare il conducente', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '90', '94001', '602', '4', 'Per autobus di classe III si intende un autobus con solo posti a sedere e con numero totale superiore a 22, senza contare il conducente', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '90', '94001', '603', '5', 'Un autobus con servizio interurbano senza passeggeri in piedi può appartenere alla classe III o alla classe B ', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '90', '94001', '604', '6', 'Un autobus con servizio di NCC può appartenere alla classe III o alla classe B', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '90', '94001', '605', '7', 'Un autobus con servizio urbano può appartenere alla classe I o alla classe A', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '90', '94001', '606', '8', 'Un autobus con servizio suburbano può appartenere alla classe I o alla classe A', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '90', '94001', '607', '9', 'Un autobus con servizio interurbano con posti in piedi può appartenere alla classe II o alla classe A', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '90', '94001', '608', '10', 'Un autobus con servizio interurbano senza passeggeri in piedi può appartenere alla classe III o alla classe B', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '90', '94001', '609', '11', 'Per gli autobus urbani e suburbani non esiste un obbligo di colore particolare ', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '90', '94001', '610', '12', 'Gli autobus con servizio suburbano non possono avere posti in piedi ', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '90', '94001', '611', '13', 'Gli autobus con servizio suburbano possono avere al massimo 10 posti in piedi', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '90', '94001', '612', '14', 'Gli autobus con servizio suburbano non possono avere più del 25% di posti in piedi', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '90', '94001', '613', '15', 'Un autobus con servizio interurbano con posti in piedi può appartenere alla classe I o alla classe B', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '90', '94001', '614', '16', 'Un autobus con servizio interurbano senza passeggeri in piedi può appartenere alla classe II o alla classe A', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '90', '94001', '615', '17', 'Un autobus granturismo può appartenere alla classe I o alla classe A', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '90', '94001', '616', '18', 'Un autobus in uso proprio appartiene alla classe P', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '90', '94001', '617', '19', 'Un autobus con servizio di NCC può appartenere alla classe II o alla classe A', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '90', '94001', '618', '20', 'Gli scuolabus sono veicoli per uso speciale', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '91', '71003', '', '', 'Carreggiata, passo e sbalzo di un veicolo', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '91', '71003', '619', '1', 'La carreggiata di un veicolo è la distanza fra il centro delle impronte a terra degli pneumatici di uno stesso asse', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '91', '71003', '620', '2', 'Per interasse (passo) di un veicolo a due assi si intende la distanza fra i centri dei due assi', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '91', '71003', '621', '3', 'Lo sbalzo di un veicolo è la parte che sporge anteriormente o posteriormente rispetto all''asse più estremo anteriore o posteriore rispettivamente', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '91', '71003', '622', '4', 'La carreggiata di un veicolo è la distanza fra un parafango e l''altro dello stesso asse', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '91', '71003', '623', '5', 'Per interasse (passo) di un veicolo a due assi si intende la distanza fra il centro delle ruote di uno stesso asse', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '91', '71003', '624', '6', 'Per sbalzo di un veicolo si intende la pendenza massima che esso può superare quando circola a vuoto', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '92', '71004', '', '', 'Effetti dello sbalzo di un veicolo sulla guida', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '92', '71004', '625', '1', 'I conducenti che si mettono alla guida di  un mezzo pesante devono tenere presente che, generalmente, tali veicoli presentano sbalzi notevoli', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '92', '71004', '626', '2', 'Su un veicolo dotato di sbalzi notevoli, nell''affrontare una ripida salita o un forte  dislivello stradale occorre conoscere l''angolo massimo di attacco che si può affrontare', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '92', '71004', '627', '3', 'Su un veicolo dotato di sbalzi notevoli, nell''affrontare una salita o un dislivello stradale occorre moderare la velocità', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '92', '71004', '628', '4', 'Su un veicolo dotato di sbalzi notevoli, nell''affrontare una discesa ripida o un forte dislivello stradale occorre conoscere l''angolo massimo di uscita che si può affrontare', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '92', '71004', '629', '5', 'Su un veicolo dotato di sbalzi notevoli, nell''affrontare una discesa o un dislivello stradale occorre  moderare la velocità', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '92', '71004', '630', '6', 'In un veicolo a motore, lo sbalzo anteriore aumenta la larghezza della fascia di ingombro', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '92', '71004', '631', '7', 'Su un veicolo dotato di sbalzi notevoli, aumenta la difficoltà a mantenere la corretta traiettoria in curva e nelle svolte', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '92', '71004', '632', '8', 'Se si effettua una svolta con un veicolo dotato di sbalzi notevoli, occorre tenere presente che, con le estremità anteriore o posteriore del veicolo, si potrebbe invadere il marciapiede', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '92', '71004', '633', '9', 'Se si effettua una svolta con un veicolo dotato di sbalzi notevoli, occorre tenere presente che, con le estremità anteriore o posteriore del veicolo, si potrebbero urtare pedoni o ciclisti in transito nelle vicinanze', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '92', '71004', '634', '10', 'Il conducente alla guida di un mezzo pesante deve tenere presente che, generalmente, tali veicoli presentano sbalzi notevoli anteriormente, ma non posteriormente', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '92', '71004', '635', '11', 'Quando si è alla guida di un mezzo pesante dotato di sbalzi importanti, non si possono percorrere strade dove sono presenti dossi artificiali', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '92', '71004', '636', '12', 'Gli sbalzi di cui sono dotati i veicoli pesanti sono tra l''altro previsti per facilitare le manovre di parcheggio', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '92', '71004', '637', '13', 'Gli autobus urbani non possono avere sbalzi superiori ai dieci centimetri per non rischiare di urtare i pedoni sul marciapiede', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '92', '71004', '638', '14', 'I veicoli pesanti che hanno sbalzi superiori ai quaranta centimetri devono essere di norma dotati di uno specchio retrovisore supplementare', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '92', '71004', '639', '15', 'I veicoli pesanti non possono avere, di norma, sbalzi di lunghezza superiore ai quaranta centimetri', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '92', '71004', '640', '16', 'Gli sbalzi sono vietati sugli autobus extraurbani, perché ne compromettono la tenuta di strada', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '93', '71006', '', '', 'Massa di un veicolo', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '93', '71006', '641', '1', 'La massa in ordine di marcia (tara) si ottiene sommando alla massa a vuoto del veicolo la massa convenzionale del conducente e la massa costituita dal 90% del carburante contenuto nel serbatoio', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '93', '71006', '642', '2', 'La massa complessiva a pieno carico è la massa massima autorizzata e corrisponde alla tara più il carico utile', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '93', '71006', '643', '3', 'La portata (carico utile) rappresenta il carico massimo trasportabile da un veicolo ed è indicata sulla carta di circolazione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '93', '71006', '644', '4', 'La massa complessiva a pieno carico non è indicata nella carta di circolazione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '93', '71006', '645', '5', 'La massa in ordine di marcia cambia se il veicolo percorre una strada in forte pendenza', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '93', '71006', '646', '6', 'La massa complessiva a pieno carico è equivalente al valore della tara, moltiplicato per 1,2', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '93', '71006', '647', '7', 'La tara di un veicolo è equivalente al suo carico utile', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '93', '71006', '648', '8', 'La portata è pari alla somma delle masse massime ammesse sugli assi del veicolo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '93', '71006', '649', '9', 'La portata del veicolo è il carico massimo che può essere sopportato dagli pneumatici', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '94', '71001', '', '', 'Dimensioni massime di un veicolo  ', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '94', '71001', '650', '1', 'Ogni autoveicolo, esclusi autobus e i semirimorchi, deve avere lunghezza massima non superiore a 12 metri', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '94', '71001', '651', '2', 'Salvo eccezioni, ogni veicolo, compreso il suo carico, deve avere altezza massima non superiore a 4 metri', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '94', '71001', '652', '3', 'Salvo i veicoli ATP, ogni veicolo, compreso il suo carico, deve avere larghezza massima non superiore a 2,55 metri', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '94', '71001', '653', '4', 'La lunghezza massima non superiore dei veicoli non è fissata per norma, in quanto dipende dal carico trasportato', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '94', '71001', '654', '5', 'Salvo eccezioni, ogni veicolo, compreso il suo carico, deve avere altezza massima non superiore a 5 metri', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '94', '71001', '655', '6', 'Salvo i veicoli ATP, ogni veicolo, compreso il suo carico, deve avere larghezza massima non superiore a 2,75 metri', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '94', '71001', '656', '7', 'Negli autotreni e autoarticolati, il rimorchio o semirimorchio devono avere una larghezza inferiore alla motrice di almeno 5 centimetri', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '95', '71002', '', '', 'Lunghezza massima di un veicolo 2', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '95', '71002', '657', '1', 'La lunghezza massima di un autoarticolato è di 16,50 metri, purché l''avanzamento ralla abbia particolari caratteristiche', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '95', '71002', '658', '2', 'La lunghezza massima di un autoarticolato con avanzamento ralla di 3 metri  rispetto alla parte anteriore del semirimorchio è di 15,50 metri', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '95', '71002', '659', '3', 'La lunghezza massima di un autosnodato o di un autobus con rimorchio è di 18,75 metri', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '95', '71002', '660', '4', 'La lunghezza massima di un autotreno è di 18,75 metri, purché il complesso dei veicoli abbia particolari misure', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '95', '71002', '661', '5', 'Le bisarche (autoveicoli per trasporto di altri autoveicoli) possono avere  lunghezza massima differente da quella prevista per altri veicoli', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '95', '71002', '662', '6', 'La lunghezza massima di un autoarticolato è di 18,75 metri', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '95', '71002', '663', '7', 'La lunghezza massima di un autotreno è di 19,35 metri, se dotato  di sospensioni pneumatiche sugli assi del rimorchio', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '95', '71002', '664', '8', 'Un rimorchio non deve mai avere lunghezza maggiore deL 60% di quella della motrice', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '95', '71002', '665', '9', 'Un rimorchio non deve mai avere lunghezza maggiore di 9 metri', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '95', '71002', '666', '10', 'Un semirimorchio non deve mai avere lunghezza maggiore di 10,75 metri', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '96', '73001', '', '', 'Dimensioni massime di autobus e filobus  ', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '96', '73001', '667', '1', 'La lunghezza massima di un autobus a 2 assi è di 13,50 metri', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '96', '73001', '668', '2', 'La lunghezza massima di un autobus a 3 o più assi è di 15 metri', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '96', '73001', '669', '3', 'L''altezza massima di autobus e filobus destinati a servizi pubblici di linea urbani e suburbani, circolanti su itinerari prestabiliti, non deve superare 4,30 metri', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '96', '73001', '670', '4', 'L''altezza massima degli autoveicoli adibiti al trasporto di container non deve superare 4,30 metri', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '96', '73001', '671', '5', 'La lunghezza massima di un autobus a 2 assi è di 15 metri', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '96', '73001', '672', '6', 'La lunghezza massima di un autobus a 3 o più assi è di 18,75 metri', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '96', '73001', '673', '7', 'Ogni veicolo, esclusi autobus e i semirimorchi, deve avere lunghezza massima non superiore a 13,50 metri', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '97', '74001', '', '', 'Fascia di ingombro 1', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '97', '74001', '674', '1', 'La fascia di ingombro di un veicolo è la corona circolare occupata dal veicolo stesso quando percorre una curva con il minimo raggio possibile di sterzata', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '97', '74001', '675', '2', 'Le dimensioni della fascia di ingombro di un veicolo non sono indicate nella carta di circolazione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '97', '74001', '676', '3', 'La fascia di ingombro di un veicolo deve rispettare determinati limiti di raggio fissati dalle norme', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '97', '74001', '677', '4', 'La fascia di ingombro di un veicolo deve avere il raggio esterno non superiore a 12,50 metri', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '97', '74001', '678', '5', 'La fascia di ingombro di un veicolo deve avere il raggio interno superiore a 5,30 metri', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '97', '74001', '679', '6', 'Ogni veicolo a motore, o complesso di veicoli, compreso il relativo carico, deve potersi inserire in una corona circolare di raggio esterno 12,50 metri e raggio interno 5,30 metri', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '97', '74001', '680', '7', 'Ogni veicolo a motore o complesso di veicoli deve potersi inscrivere in una corona circolare larga 5,30 metri e di raggio interno pari a 7,20 metri', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '97', '74001', '681', '8', ' La fascia di ingombro di un veicolo figura sempre nella carta di circolazione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '97', '74001', '682', '9', 'La fascia di ingombro di un veicolo diminuisce con l''aumentare dell''angolo di sterzata', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '97', '74001', '683', '10', 'La fascia di ingombro di un veicolo diminuisce con l''aumentare della sua lunghezza', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '97', '74001', '684', '11', 'La fascia di ingombro di un veicolo aumenta con l''aumentare della sua massa', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '97', '74001', '685', '12', 'Ogni veicolo a motore o complesso di veicoli deve potersi inscrivere in una corona circolare larga 5,20 m e di raggio esterno pari a 16,50 metri', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '97', '74001', '686', '13', 'Gli autobus e gli autocarri, a differenza degli altri veicoli, devono potersi inscrivere in una corona circolare larga 8,20 metri e di raggio interno pari 6,30 metri', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '98', '74002', '', '', 'Fascia di ingombro 2', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '98', '74002', '687', '1', 'La fascia d''ingombro è la corona circolare che il veicolo occupa, sul piano stradale, nell''effettuare una curva o una svolta', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '98', '74002', '688', '2', 'La fascia d''ingombro si riduce se il veicolo è dotato di asse autosterzante posteriore', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '98', '74002', '689', '3', 'La fascia d''ingombro aumenta all''aumentare degli sbalzi del veicolo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '98', '74002', '690', '4', 'La fascia d''ingombro è lo spazio in larghezza che il veicolo occupa su strada rettilinea', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '98', '74002', '691', '5', 'La fascia d''ingombro aumenta se il veicolo è dotato di più assi sterzanti', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '98', '74002', '692', '6', 'La fascia d''ingombro non deve essere superiore alla larghezza del veicolo con il suo carico', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '99', '71005', '', '', 'Limite di massa complessiva a pieno carico', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '99', '71005', '693', '1', 'Il limite di massa complessiva a pieno carico per i veicoli a motore isolati a 2 assi è di 18 tonnellate (ad eccezione di particolari autobus)', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '99', '71005', '694', '2', 'Il limite di massa complessiva a pieno carico per i veicoli a motore isolati a 3 assi è di 25  tonnellate (oppure di 26 tonnellate se sono rispettate particolari prescrizioni)', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '99', '71005', '695', '3', 'Il limite di massa complessiva a pieno carico per i veicoli a motore isolati a 4 assi è di 25 tonnellate (oppure di 32 tonnellate se sono rispettate particolari prescrizioni)', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '99', '71005', '696', '4', 'Il limite di massa complessiva a pieno carico per i veicoli a motore isolati dipende dal totale degli assi di cui è dotato il veicolo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '99', '71005', '697', '5', 'Il limite di massa complessiva a pieno carico per i veicoli a motore isolati a 3 o più assi dipende anche dalla presenza o meno di sospensioni pneumatiche e ruote gemellate', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '99', '71005', '698', '6', 'Il limite di massa complessiva a pieno carico per i veicoli a motore isolati a 2 assi è di 8 tonnellate (ad eccezione di particolari autobus)', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '99', '71005', '699', '7', 'Il limite di massa complessiva a pieno carico per i veicoli a motore isolati a 3 assi è di 15 tonnellate (oppure di 16 tonnellate se sono rispettate particolari prescrizioni)', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '99', '71005', '700', '8', 'Il limite di massa complessiva a pieno carico per i veicoli a motore isolati a 4 assi è di 40 tonnellate (oppure di 44 tonnellate se sono rispettate particolari prescrizioni)', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '99', '71005', '701', '9', 'Il limite di massa complessiva a pieno carico per i veicoli a motore isolati dipende dal tipo di pneumatici di cui è dotato il veicolo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('7', '99', '71005', '702', '10', 'Il limite di massa complessiva a pieno carico per i veicoli a motore isolati è di 15 tonnellate, salvo che trasportino liquidi', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '100', '52003', '', '', 'Comportamenti in caso di ingombro della carreggiata 2', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '100', '52003', '703', '1', 'Nel caso di ingombro della carreggiata per caduta accidentale del carico, il conducente deve provvedere a rimuovere il carico, se l''operazione risulta possibile', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '100', '52003', '704', '2', 'Nel caso di ingombro della carreggiata per caduta accidentale del carico, il conducente deve presegnalare l''ostacolo mediante il segnale di veicolo fermo (triangolo)', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '100', '52003', '705', '3', 'Nel caso di ingombro della carreggiata per caduta accidentale del carico, il conducente deve rendere sollecitamente libero il transito, per quanto nelle sue possibilità', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '100', '52003', '706', '4', 'Nel caso di ingombro della carreggiata per caduta accidentale del carico, il conducente deve presegnalare l''ostacolo mediante il segnale di STOP', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '100', '52003', '707', '5', 'Nel caso di ingombro della carreggiata per caduta accidentale del carico, il conducente deve restare a bordo del veicolo in attesa dei soccorsi', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '100', '52003', '708', '6', 'Nel caso di ingombro della carreggiata per caduta accidentale del carico, il conducente deve obbligatoriamente accendere una serie di fiaccole gialle tutto intorno al tratto di strada interessato', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '101', '52004', '', '', 'Comportamenti in caso di ingombro della carreggiata 3', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '101', '52004', '709', '1', 'In caso di ingombro della carreggiata per avaria del veicolo, il conducente deve sollecitamente rendere libero il transito per non ostacolare il traffico sopraggiungente', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '101', '52004', '710', '2', 'In caso di ingombro della carreggiata per avaria del veicolo, il conducente deve spingere il veicolo stesso fuori della carreggiata o, se ciò non è possibile, deve collocarlo sul margine destro della carreggiata', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '101', '52004', '711', '3', 'In caso di ingombro della carreggiata per caduta del carico o per qualsiasi altra causa, il conducente deve rimuovere l''ingombro, per quanto possibile', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '101', '52004', '712', '4', 'In caso di ingombro della carreggiata per avaria del veicolo, il conducente non è tenuto a spostarlo in attesa dei soccorsi, se il traffico sulla strada è scarso', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '101', '52004', '713', '5', 'Nel caso di ingombro della carreggiata per caduta accidentale del carico, il conducente deve evitare di spostarlo per consentire la ricostruzione corretta dell''accaduto', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '101', '52004', '714', '6', 'In caso di ingombro della carreggiata per avaria del veicolo, il conducente deve essere in grado di riparare il guasto rapidamente, se si tratta di un veicolo pesante', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '102', '52005', '', '', 'Comportamenti in caso di ingombro della carreggiata 4', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '102', '52005', '715', '1', 'Nel caso di ingombro della carreggiata per avaria del veicolo o per caduta del carico, il conducente deve, tra l''altro, informare l''ente proprietario della strada o un organo di polizia', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '102', '52005', '716', '2', 'Nel caso di incidente che provochi l''ingombro della carreggiata per la presenza di veicoli danneggiati che non è possibile rimuovere, il conducente deve presegnalare la zona mediante il segnale triangolare mobile di pericolo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '102', '52005', '717', '3', 'Nel caso di ingombro della carreggiata per avaria del veicolo che non è possibile spostare , il conducente deve informare un organo di polizia, astenendosi dal presegnalare la zona', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '102', '52005', '718', '4', 'Nel caso di ingombro della carreggiata per caduta del carico non rimovibile, il conducente può evitare di informare un organo di polizia se presegnala la zona con efficaci segnali manuali ', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '103', '51001', '', '', 'Comportamenti in caso di ingombro della carreggiata 1', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '103', '51001', '719', '1', 'In caso di ingombro della carreggiata per la caduta del carico, il conducente deve cercare, per quanto possibile, di liberare il transito', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '103', '51001', '720', '2', 'In caso di ingombro della carreggiata per avaria del veicolo che non possa essere spostato, il conducente deve segnalare il pericolo agli altri conducenti', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '103', '51001', '721', '3', 'In caso di ingombro della carreggiata per avaria del veicolo, fuori dei centri abitati il conducente deve posizionare sulla carreggiata il segnale mobile triangolare di pericolo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '103', '51001', '722', '4', 'In caso di ingombro della carreggiata con perdite di materie pericolose, il conducente deve, tra l''altro, cercare di bloccare la perdita', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '103', '51001', '723', '5', 'In caso di ingombro della carreggiata con perdite di acidi o sostanze infiammabili, il conducente deve comportarsi come indicato dalle istruzioni di sicurezza', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '103', '51001', '724', '6', 'In caso di ingombro della carreggiata per la caduta del carico, il conducente si libera da ogni obbligo telefonando agli organi di polizia', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '103', '51001', '725', '7', 'In caso di ingombro della carreggiata per avaria del veicolo, il conducente deve riparare subito il veicolo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '103', '51001', '726', '8', 'In caso di ingombro della carreggiata per avaria del veicolo, nei centri abitati il conducente deve sempre posizionare sulla carreggiata il segnale mobile triangolare di pericolo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '103', '51001', '727', '9', 'In caso di ingombro della carreggiata con perdite di materie pericolose bisogna, se possibile, incanalarle verso una fognatura', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '103', '51001', '728', '10', 'In caso di ingombro della carreggiata per avaria del veicolo, il conducente deve prima di tutto segnalare il pericolo all''ente proprietario della strada', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '104', '52006', '', '', 'Comportamenti in caso di spargimento di materie viscide, infiammabili o pericolose', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '104', '52006', '729', '1', 'Chiunque non abbia potuto evitare la caduta o lo spargimento di materie viscide, infiammabili o comunque pericolose deve, tra l''altro, presegnalare la zona con il segnale mobile di pericolo posto, se necessario, anche nel centro della carreggiata', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '104', '52006', '730', '2', 'Chiunque non abbia potuto evitare la caduta o lo spargimento di materie pericolose deve, tra l''altro, eseguire segnali manuali per impedire il transito dei veicoli sulla parte di carreggiata non impedita dal segnale mobile di pericolo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '104', '52006', '731', '3', 'Chiunque non abbia potuto evitare la caduta o lo spargimento di materie viscide, infiammabili o pericolose deve, tra l''altro, rimuoverle o spargere sul terreno, se possibile, sabbia, terra, segatura o altro materiale idoneo a ripristinare l''aderenza', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '104', '52006', '732', '4', 'Il conducente di un autoveicolo che non abbia potuto evitare lo spargimento di materie pericolose, può evitare di presegnalare la zona con il segnale mobile di pericolo se esegue segnali sonori per allontanare gli altri veicoli', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '104', '52006', '733', '5', 'Chiunque non abbia potuto evitare la caduta di materie viscide, deve deviare il traffico in attesa che l''ente proprietario della strada intervenga per ripristinare le condizioni ottimali', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '105', '52008', '', '', 'Uso del segnale triangolare mobile di pericolo 2', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '105', '52008', '734', '1', 'Il segnale mobile triangolare di pericolo deve essere posto sulla corsia occupata dal veicolo fermo o dal carico caduto', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '105', '52008', '735', '2', 'Il segnale mobile triangolare di pericolo deve essere posto sulla carreggiata ad almeno un metro dal bordo esterno di essa', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '105', '52008', '736', '3', 'Nel caso di carreggiata a più corsie, il segnale mobile triangolare di pericolo deve essere posto sulla corsia immediatamente a sinistra del veicolo fermo o del carico caduto', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '105', '52008', '737', '4', 'Il segnale mobile triangolare di pericolo deve essere posto sulla carreggiata ad almeno un metro dalla striscia di separazione dei sensi di marcia', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '106', '52007', '', '', 'Uso del segnale triangolare mobile di pericolo 1', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '106', '52007', '738', '1', 'Fuori dei centri abitati, di notte, quando mancano o sono insufficienti le luci posteriori di posizione o di emergenza, è obbligatorio presegnalare il veicolo, fermo sulla carreggiata, con il segnale triangolare mobile di pericolo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '106', '52007', '739', '2', 'Fuori dei centri abitati, di notte, è obbligatorio presegnalare con il segnale triangolare mobile di pericolo ogni carico caduto accidentalmente dal veicolo sulla carreggiata che non sia stato possibile rimuovere', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '106', '52007', '740', '3', 'Fuori dei centri abitati è obbligatorio, anche di giorno, presegnalare un veicolo fermo sulla carreggiata mediante il segnale triangolare di pericolo, quando il veicolo non può essere visto nettamente a 100 metri di distanza', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '106', '52007', '741', '4', 'Fuori dei centri abitati è obbligatorio, anche di giorno, presegnalare un carico accidentalmente caduto sulla carreggiata mediante il segnale di pericolo, quando il carico non può essere visto nettamente a 100 metri di distanza', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '106', '52007', '742', '5', 'Il segnale mobile triangolare di pericolo fa parte dell''equipaggiamento obbligatorio degli autoveicoli', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '106', '52007', '743', '6', 'Il segnale mobile triangolare di pericolo deve essere posto dietro al veicolo o all''ostacolo da segnalare, ad almeno 50 metri', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '106', '52007', '744', '7', 'Se il veicolo è fermo per avaria a meno di 50 metri dopo una intersezione, il segnale mobile triangolare di pericolo deve essere posto dietro al veicolo nella posizione e alla distanza più idonea ad essere avvistato', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '106', '52007', '745', '8', 'Il segnale mobile triangolare di pericolo deve essere posto sulla carreggiata in modo che sia visibile ad una distanza di almeno 100 metri dai veicoli sopraggiungenti', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '106', '52007', '746', '9', 'Fuori dei centri abitati, di notte, quando mancano o sono insufficienti le luci posteriori di posizione o di emergenza, è consigliabile, ma non obbligatorio, presegnalare il veicolo fermo sulla carreggiata, con il segnale triangolare mobile di pericolo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '106', '52007', '747', '10', 'Di notte, il triangolo mobile di pericolo deve essere obbligatoriamente usato per segnalare il veicolo fermo per avaria nel caso non sia visibile da almeno 150 metri di distanza', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '106', '52007', '748', '11', 'Fuori dei centri abitati, anche di giorno, è facoltativo presegnalare mediante il segnale di pericolo un carico accidentalmente caduto sulla carreggiata che non può essere visto nettamente a 100 metri di distanza, purché il carico occupi una sola corsia', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '106', '52007', '749', '12', 'Di notte, quando mancano o sono insufficienti le luci posteriori di posizione o di emergenza, è obbligatorio presegnalare il veicolo fermo con il segnale triangolare mobile di pericolo anche nei centri abitati', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '106', '52007', '750', '13', 'Il segnale mobile triangolare di pericolo deve essere posto sopra al carico accidentalmente caduto sulla carreggiata', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '106', '52007', '751', '14', 'Il segnale mobile triangolare di pericolo deve essere posto sulla carreggiata in modo che sia visibile ad una distanza di almeno 50 metri dai veicoli sopraggiungenti', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '107', '52009', '', '', 'Uso del giubbotto o delle bretelle riflettenti ad alta visibilità', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '107', '52009', '752', '1', 'Durante le operazioni di presegnalazione con il segnale mobile di pericolo, il conducente deve rendersi visibile indossando il giubbotto o le bretelle retroriflettenti ad alta visibilità', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '107', '52009', '753', '2', 'Il giubbotto o le bretelle retroriflettenti ad alta visibilità, da usare durante le operazioni di presegnalazione con il segnale mobile di pericolo, devono essere di tipo approvato', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '107', '52009', '754', '3', 'Durante le operazioni di presegnalazione con il segnale mobile di pericolo, il conducente deve indossare il giubbotto ad alta visibilità anche se si trova sulle corsie di emergenza o sulle piazzole di sosta', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '107', '52009', '755', '4', 'Per effettuare le operazioni di presegnalazione con il segnale mobile di pericolo, è vietato al conducente scendere dal veicolo e camminare sulla strada senza indossare il giubbotto ad alta visibilità', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '107', '52009', '756', '5', 'Durante le operazioni di presegnalazione con il segnale mobile di pericolo, è facoltativo l''uso del giubbotto o delle bretelle retroriflettenti ad alta visibilità', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '107', '52009', '757', '6', 'Durante le operazioni di presegnalazione con il segnale mobile di pericolo, il conducente non deve indossare il giubbotto ad alta visibilità se si trova sulle corsie di emergenza o sulle piazzole di sosta', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '107', '52009', '758', '7', 'Il conducente deve indossare il giubbotto ad alta visibilità di notte, anche nei centri abitati, qualora il suo veicolo sia fermo sulla carreggiata per un guasto', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '108', '52001', '', '', 'Comportamenti in caso di blocco del veicolo in galleria', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '108', '52001', '759', '1', 'In caso di guasto meccanico al veicolo che lo blocchi in una galleria autostradale, occorre attivare la segnalazione luminosa di pericolo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '108', '52001', '760', '2', 'In caso di guasto meccanico al veicolo che lo blocchi in una galleria autostradale, occorre, se possibile, portare il veicolo su una piazzola di sosta', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '108', '52001', '761', '3', 'In caso di guasto meccanico al veicolo che lo blocchi in una galleria autostradale, occorre fermare il veicolo sulla corsia di emergenza, se presente', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '108', '52001', '762', '4', 'In caso di guasto meccanico al veicolo che lo blocchi in una galleria autostradale, occorre chiamare rapidamente il soccorso stradale', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '108', '52001', '763', '5', 'In caso di guasto meccanico al veicolo che lo blocchi in una galleria autostradale, occorre fermare immediatamente il veicolo sulla corsia di marcia attivando la segnalazione luminosa di pericolo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '108', '52001', '764', '6', 'In caso di guasto meccanico al veicolo che lo blocchi in una galleria autostradale, occorre chiedere aiuto ai conducenti più vicini', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '108', '52001', '765', '7', 'In caso di guasto meccanico al veicolo che lo blocchi in una galleria autostradale, occorre provvedere al traino del veicolo fuori del tunnel con l''aiuto di un altro autista di passaggio', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '108', '52001', '766', '8', 'In caso di guasto meccanico al veicolo che lo blocchi in una galleria autostradale, occorre attendere l''arrivo del proprio meccanico di fiducia', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '109', '52002', '', '', 'Comportamenti in caso di incidente in galleria', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '109', '52002', '767', '1', 'Nel caso ci si imbatta in un incidente in galleria che blocca o rallenta molto la marcia, occorre attivare la segnalazione luminosa di pericolo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '109', '52002', '768', '2', 'In caso di incidente in galleria che blocca il traffico, occorre spegnere il motore lasciando la chiave di accensione inserita', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '109', '52002', '769', '3', 'In caso di incidente in galleria, se si deve scendere dal veicolo, è bene indossare il giubbotto o le bretelle retroriflettenti', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '109', '52002', '770', '4', 'In caso di incidente in galleria occorre avvertire rapidamente i servizi di emergenza', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '109', '52002', '771', '5', 'In caso di incidente in galleria occorre prestare il primo soccorso ad eventuali feriti', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '109', '52002', '772', '6', 'In caso di incidente in galleria occorre suonare a fondo il clacson per avvertire gli altri conducenti', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '109', '52002', '773', '7', 'In caso di incidente in galleria occorre scendere e posizionare i cunei sotto le ruote del proprio veicolo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '109', '52002', '774', '8', 'In caso di incidente in galleria occorre avvertire con brevi colpi di clacson gli altri automobilisti', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '109', '52002', '775', '9', 'In caso di incidente in galleria occorre invertire la marcia e sgomberare il luogo dell''incidente', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '109', '52002', '776', '10', 'In caso di incidente in galleria occorre scaricare immediatamente il carico', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '110', '51002', '', '', 'Uso del modulo di costatazione amichevole in caso di incidente stradale', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '110', '51002', '777', '1', 'In caso di incidente con solo due veicoli coinvolti, è opportuno avvalersi del modulo di Constatazione amichevole (denuncia di sinistro)', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '110', '51002', '778', '2', 'In caso di utilizzo del modulo di Constatazione amichevole, il conducente deve riportare il luogo, le modalità e la data dell''incidente', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '110', '51002', '779', '3', 'In caso di utilizzo del modulo di Constatazione amichevole, il conducente deve riportare le generalità dell''altro conducente', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '110', '51002', '780', '4', 'In caso di utilizzo del modulo di Constatazione amichevole, il conducente deve riportare le generalità del proprietario dell''altro veicolo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '110', '51002', '781', '5', 'Nel modulo di Constatazione amichevole, il conducente deve riportare, ove possibile, le generalità di eventuali testimoni', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '110', '51002', '782', '6', 'Nel modulo di Constatazione amichevole, il conducente deve riportare un semplice disegno schematico indicante la posizione dei veicoli e la dinamica dell''incidente', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '110', '51002', '783', '7', 'Nel modulo di Constatazione amichevole, il conducente deve riportare il numero di targa, il numero di polizza e la compagnia assicurativa dei veicoli coinvolti', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '110', '51002', '784', '8', 'In caso di incidente con più di due veicoli coinvolti, è opportuno avvalersi del modulo di Constatazione amichevole (denuncia di sinistro)', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '110', '51002', '785', '9', 'Nel modulo di Constatazione amichevole, è sufficiente che il conducente riporti il luogo e modalità dell''incidente', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '110', '51002', '786', '10', 'Nel modulo di Constatazione amichevole, il conducente deve riportare solo le generalità dell''altro conducente', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '110', '51002', '787', '11', 'Nel modulo di Constatazione amichevole, non è obbligatorio riportare le generalità del proprietario dell''altro veicolo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '110', '51002', '788', '12', 'Nel modulo di Constatazione amichevole, il conducente deve riportare, qualora ce ne fosse il bisogno, le generalità di eventuali testimoni, purché non siano passeggeri trasportati', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '110', '51002', '789', '13', 'Nel modulo di Constatazione amichevole, il conducente deve obbligatoriamente compilare solo la parte sinistra del modulo, quella destra è facoltativa', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '110', '51002', '790', '14', 'Nel modulo di Constatazione amichevole, il conducente responsabile deve compilare per forza la colonna A, chi ha subito il danno, invece, la colonna B', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '111', '51002', '', '', 'Comportamenti in caso di incidente', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '111', '51002', '791', '1', 'In caso di incidente stradale con soli danni alle cose, il conducente ha l''obbligo di fermarsi e fornire le proprie generalità', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '111', '51002', '792', '2', 'In caso di incidente stradale con soli danni alle cose, il conducente ha l''obbligo di fermarsi e fornire le informazioni utili per l''esatta ricostruzione della dinamica dell''incidente', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '111', '51002', '793', '3', 'Dopo un incidente stradale, se il veicolo coinvolto crea pericolo o intralcio, deve essere portato al più presto fuori dalla carreggiata', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '111', '51002', '794', '4', 'Dopo un incidente stradale, nei casi e con le modalità previste dal Codice della Strada, il conducente deve utilizzare il segnale di veicolo fermo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '111', '51002', '795', '5', 'Il conducente, dopo un incidente stradale, qualora siano cadute dal veicolo delle sostanze viscide, deve rimanere sul posto per segnalare manualmente il pericolo ai veicoli che sopraggiungono', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '111', '51002', '796', '6', 'Il conducente, in caso di incidente stradale ricollegabile ad un suo comportamento, ha l''obbligo di fermarsi e di prestare assistenza a chi ha subito un danno alla persona', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '111', '51002', '797', '7', 'È punibile con la reclusione il conducente, coinvolto in un incidente stradale, che non si fermi a prestare assistenza a chi ha subito un danno alla persona', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '111', '51002', '798', '8', 'In caso di incidente stradale, il conducente che si dia alla fuga è passibile di arresto', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '111', '51002', '799', '9', 'In caso di incidente stradale, il conducente che si fermi e presti assistenza non è, di norma, soggetto all''arresto preventivo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '111', '51002', '800', '10', 'In caso di incidente stradale, si deve richiedere l''intervento dell''autorità quando non sia possibile ripristinare la circolazione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '111', '51002', '801', '11', 'In caso di incidente stradale, si può richiedere l''intervento dell''autorità quando l''altro conducente non sia in grado di esibire il certificato di assicurazione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '111', '51002', '802', '12', 'Si deve richiedere l''intervento dell''autorità quando un incidente stradale ha provocato feriti', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '111', '51002', '803', '13', 'A seguito di un incidente stradale, non si devono spostare i veicoli coinvolti finché non siano stati soccorsi eventuali feriti e la polizia non abbia rilevato tutti gli elementi utili alla ricostruzione dell''incidente', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '111', '51002', '804', '14', 'In caso di incidente stradale con soli danni alle cose, il conducente non ha l''obbligo di fermarsi e fornire le proprie generalità', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '111', '51002', '805', '15', 'In caso di incidente stradale con soli danni alle cose, al conducente è consigliato di fermarsi e fornire le proprie generalità', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '111', '51002', '806', '16', 'Dopo un incidente stradale, se il veicolo coinvolto crea pericolo o intralcio, può essere lasciato sulla carreggiata per al massimo un''ora dopo l''evento', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '111', '51002', '807', '17', 'Dopo un incidente stradale, il conducente deve sempre utilizzare il segnale di veicolo fermo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '111', '51002', '808', '18', 'Il conducente, dopo un incidente stradale, qualora siano cadute dal veicolo delle sostanze viscide, deve fermare tutti i veicoli che sopraggiungono e costringerli ad invertire la marcia', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '111', '51002', '809', '19', 'Il conducente, in caso di incidente ricollegabile ad un suo comportamento, ha l''obbligo di fermarsi, ma non di prestare assistenza', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '111', '51002', '810', '20', 'È punibile con la reclusione il conducente coinvolto in un incidente stradale che non si ferma a prestare assistenza a chi ha subito unicamente un danno alle cose', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '111', '51002', '811', '21', 'In caso di incidente stradale, il conducente che si dia alla fuga non incorre nell''arresto se è opportunamente assicurato contro tale eventualità', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '111', '51002', '812', '22', 'In caso di incidente stradale, il proprietario del veicolo coinvolto è punibile con la reclusione se i danni superano i cinquemila euro di valore', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '111', '51002', '813', '23', 'In caso di incidente stradale, occorre richiedere l''intervento dell''autorità unicamente quando il numero dei feriti è superiore a tre', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '111', '51002', '814', '24', 'In caso di incidente stradale, si deve richiedere l''intervento dell''autorità quando l''altro conducente non sia in grado di esibire la polizza di assicurazione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '111', '51002', '815', '25', 'Non si deve richiedere l''intervento dell''autorità quando un incidente ha provocato solamente un ferito', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '111', '51002', '816', '26', 'A seguito di un incidente stradale, si possono spostare i veicoli coinvolti anche se la polizia non ha finito di rilevare gli elementi utili alla ricostruzione dell''incidente, purché siano stati soccorsi gli eventuali feriti', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '112', '53001', '', '', 'Traino di un veicolo in avaria 1', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '112', '53001', '817', '1', 'Durante le operazioni di traino in situazione di emergenza, il collegamento tra due veicoli può essere realizzato mediante aggancio con catena, purché idoneamente segnalata', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '112', '53001', '818', '2', 'Durante le operazioni di traino in situazione di emergenza, il collegamento tra due veicoli può essere realizzato mediante aggancio con barra rigida, purché idoneamente segnalata', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '112', '53001', '819', '3', 'Durante le operazioni di traino in situazione di emergenza, il collegamento tra due veicoli può essere realizzato mediante aggancio con cavo di acciaio, purché idoneamente segnalato', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '112', '53001', '820', '4', 'Durante le operazioni di traino in situazione di emergenza, il collegamento tra due veicoli può essere realizzato mediante aggancio con fune idonea allo scopo, purché idoneamente segnalata', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '112', '53001', '821', '5', 'Durante le operazioni di traino, il veicolo trainato deve mantenere in funzione la segnalazione luminosa di pericolo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '112', '53001', '822', '6', 'Durante le operazioni di traino, se il veicolo trainato non può attivare la segnalazione luminosa di pericolo, deve mantenere esposto, sul lato rivolto alla circolazione, il segnale mobile di pericolo o il pannello per carichi sporgenti', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '112', '53001', '823', '7', 'Durante le operazioni di traino in situazione di emergenza, il collegamento tra i due veicoli deve essere idoneamente segnalato in modo da essere avvistato e risultare chiaramente percepibile da parte degli altri utenti della strada', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '112', '53001', '824', '8', 'Durante le operazioni di traino, il veicolo trainante deve mantenere attivata la segnalazione luminosa di pericolo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '112', '53001', '825', '9', 'Durante le operazioni di traino, il veicolo trainato deve accendere la luce posteriore per nebbia in mancanza di altra idonea segnalazione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '112', '53001', '826', '10', 'Durante le operazioni di traino in situazioni di emergenza, il collegamento tra due veicoli deve avvenire mediante aggancio con cavo elettrico', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '112', '53001', '827', '11', 'Durante le operazioni di traino in situazioni di emergenza, il collegamento tra i due veicoli può avvenire solo a condizione che il veicolo trainante sia munito di gancio di traino omologato', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '112', '53001', '828', '12', 'Durante le operazioni di traino in situazioni di emergenza, il collegamento tra due veicoli non deve essere più lungo di 1 metro', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '112', '53001', '829', '13', 'Durante le operazioni di traino in situazioni di emergenza, sia il veicolo trainante che quello trainato devono mantenere in funzione l''indicatore di direzione destro', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '113', '53001', '', '', 'Traino di un veicolo in avaria 2', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '113', '53001', '830', '1', 'Di norma, non si può trainare o essere trainati da più di un veicolo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '113', '53001', '831', '2', 'Salvo eccezioni, un veicolo può trainare un altro veicolo in avaria su strade urbane ed extraurbane secondarie', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '113', '53001', '832', '3', 'Il traino di un veicolo in situazione di emergenza deve essere idoneamente segnalato', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '113', '53001', '833', '4', 'Il traino di un veicolo in avaria è vietato in autostrada, salvo che il traino venga eseguito da veicolo autorizzato al soccorso stradale', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '113', '53001', '834', '5', 'Il traino di un veicolo in avaria non deve essere necessariamente segnalato', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '113', '53001', '835', '6', 'Il veicolo trainante un altro veicolo in avaria deve essere di almeno il 60% più pesante', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '113', '53001', '836', '7', 'Un veicolo può sempre trainare un altro veicolo in avaria', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '113', '53001', '837', '8', 'In situazioni di emergenza, il collegamento di traino tra due veicoli può essere realizzato solo attraverso catena o barra rigida', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('8', '113', '53001', '838', '9', 'Il traino di un veicolo in avaria può essere realizzato solo se il veicolo trainante ha cilindrata del motore superiore a quella del veicolo trainato', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '114', '81001', '', '', 'Specchi retrovisori su un veicolo pesante', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '114', '81001', '839', '1', 'In un veicolo pesante, lo specchio retrovisore esterno principale destro permette la visione indiretta di un tratto di strada retrostante, a destra del veicolo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '114', '81001', '840', '2', 'In un veicolo pesante, lo specchio retrovisore grandangolare consente di allargare l''angolo di visibilità controllato dal retrovisore principale', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '114', '81001', '841', '3', 'In un veicolo pesante, lo specchio d''accostamento permette di visualizzare la zona sottostante la portiera lato passeggero', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '114', '81001', '842', '4', 'In un veicolo pesante, lo specchio anteriore permette di vedere meglio la parte bassa della zona anteriore del veicolo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '114', '81001', '843', '5', 'In un veicolo pesante, lo specchio retrovisore esterno principale sinistro permette la visione indiretta di un tratto di strada retrostante, a sinistra del veicolo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '114', '81001', '844', '6', 'In un veicolo pesante, lo specchio retrovisore esterno principale destro permette la visione indiretta anche di un tratto di strada retrostante, a sinistra del veicolo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '114', '81001', '845', '7', 'In un veicolo pesante, lo specchio retrovisore grandangolare consente al conducente di visualizzare il tetto del veicolo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '114', '81001', '846', '8', 'In un veicolo pesante, lo specchio d''accostamento permette di visualizzare la zona sottostante la portiera lato conducente', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '114', '81001', '847', '9', 'In un veicolo pesante, gli specchi retrovisori possono essere integralmente sostituiti da telecamere', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '114', '81001', '848', '10', 'In un veicolo pesante, lo specchio retrovisore esterno deve essere chiuso quando si circola in autostrada', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '115', '81002', '', '', 'Motivi che limitano campo di visibilità', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '115', '81002', '849', '1', 'Il campo di visibilità in un autoveicolo può essere limitato, tra l''altro, dai montanti della carrozzeria', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '115', '81002', '850', '2', 'Il campo di visibilità in un autoveicolo può essere limitato, tra l''altro, a causa dell''altezza del veicolo stesso', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '115', '81002', '851', '3', 'Il campo di visibilità in un autoveicolo può essere limitato, tra l''altro, dalla cattiva disposizione del carico', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '115', '81002', '852', '4', 'Il campo di visibilità in un autoveicolo può essere limitato, tra l''altro, dall''assenza del lunotto posteriore', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '115', '81002', '853', '5', 'Il campo di visibilità in un autoveicolo può essere limitato, tra l''altro, dalla presenza del lunotto posteriore', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '115', '81002', '854', '6', 'Il campo di visibilità in un autoveicolo può essere limitato, tra l''altro, da specchi retrovisori troppo grandi', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '115', '81002', '855', '7', 'Il campo di visibilità in un autoveicolo può essere limitato, tra l''altro, dai tergicristallo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '115', '81002', '856', '8', 'Il campo di visibilità in un autoveicolo può essere limitato, tra l''altro, dalla presenza dell''estintore', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '115', '81002', '857', '9', 'Il campo di visibilità in un autoveicolo può essere limitato, tra l''altro, dal parabrezza realizzato con vetro temperato', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '116', '81003', '', '', 'Per garantire una miglior visibilità diretta della strada', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '116', '81003', '858', '1', 'Per garantire una miglior visibilità diretta della strada, è necessario evitare di lasciare sul cruscotto cartine, agende, appunti, in particolare se di colore chiaro, perché potrebbero creare pericolosi riflessi sul parabrezza', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '116', '81003', '859', '2', 'Per garantire una miglior visibilità diretta della strada, è necessario sostituire le spazzole tergicristallo quando non sono più in buono stato di efficienza', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '116', '81003', '860', '3', 'Per garantire una miglior visibilità diretta della strada, è necessario evitare di tenere accese le luci interne del veicolo nelle ore di buio, perché limitano l''adattamento dell''occhio alla visione notturna', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '116', '81003', '861', '4', 'Per garantire una miglior visibilità diretta della strada, è necessario evitare l''appannamento del parabrezza e delle vetrate laterali', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '116', '81003', '862', '5', 'Per garantire una miglior visibilità diretta della strada, è necessario che il sedile di guida sia regolato correttamente in altezza', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '116', '81003', '863', '6', 'Per garantire una miglior visibilità diretta della strada è necessario evitare di usare le alette parasole', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '116', '81003', '864', '7', 'Per garantire una miglior visibilità diretta della strada è necessario accendere le luci interne del veicolo in ogni condizione di scarsa visibilità', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '116', '81003', '865', '8', 'Per garantire una miglior visibilità diretta della strada è necessario sostituire le spazzole tergicristallo ad ogni cambio del filtro del gasolio', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '116', '81003', '866', '9', 'Per garantire una miglior visibilità diretta della strada è necessario oscurare completamente i finestrini laterali destri quando il sole tramonta da quel lato', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '116', '81003', '867', '10', 'Per garantire una miglior visibilità diretta della strada è necessario pulire la parte interna del parabrezza con un panno leggermente cosparso di grasso siliconato', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '117', '81003', '', '', 'Visibiltà - Regolazione degli specchi retrovisori', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '117', '81003', '868', '1', 'La visibilità indiretta attraverso specchi o telecamere consente di vedere zone altrimenti non visibili direttamente dal conducente', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '117', '81003', '869', '2', 'Alla guida di un mezzo pesante bisogna considerare che la visibilità indiretta attraverso specchi o telecamere, lascia comunque delle zone d''ombra (angoli morti della visuale) dove non è possibile scorgere veicoli o pedoni', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '117', '81003', '870', '3', 'La visibilità indiretta attraverso specchi o telecamere è efficace solo se gli stessi sono orientati correttamente', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '117', '81003', '871', '4', 'La corretta regolazione degli specchietti retrovisori dei mezzi pesanti è un elemento fondamentale per la riduzione degli incidenti che li vedono coinvolti', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '117', '81003', '872', '5', 'Per una corretta regolazione degli specchi retrovisori dei mezzi pesanti, bisogna considerare che la visibilità della parte laterale destra della strada è particolarmente importante', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '117', '81003', '873', '6', 'La visibilità indiretta attraverso specchi o telecamere consente di annullare totalmente le zone d''ombra, coprendo tutti gli angoli morti della visuale della strada', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '117', '81003', '874', '7', 'Gli specchi retrovisori non devono essere orientati prima di partire, ma solo dopo aver percorso alcuni chilometri, per fare in modo che il veicolo si assesti', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '117', '81003', '875', '8', 'Per una corretta regolazione degli specchi retrovisori dei mezzi pesanti occorre considerare le condizioni atmosferiche, in particolar modo il vento', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '117', '81003', '876', '9', 'Per una corretta regolazione degli specchi retrovisori dei mezzi pesanti bisogna tenere presente l''eventualità che il conducente indossi occhiali da vista', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '117', '81003', '877', '10', 'Gli specchi retrovisori dei mezzi pesanti si orientano automaticamente all''atto dell''inserimento della carta tachigrafica nel cronotachigrafo digitale', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '118', '81004', '', '', 'Visibilità esterna ed interna su un autobus ', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '118', '81004', '878', '1', 'Gli specchi interni degli autobus servono per verificare la situazione a bordo del veicolo nei pressi delle porte di discesa e salita dei passeggeri', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '118', '81004', '879', '2', 'Alle fermate, per garantire una sicura discesa o salita dei passeggeri occorre verificare attentamente la posizione dei passeggeri e degli utenti nelle vicinanze, sia attraverso gli specchi esterni che per mezzo di quelli interni all''autobus, prima di aprire o chiudere le porte', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '118', '81004', '880', '3', 'Alle fermate, per garantire la sicurezza dei passeggeri dell''autobus, prima di ripartire, occorre verificarne attentamente la loro posizione attraverso gli specchi interni dell''autobus ', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '118', '81004', '881', '4', 'Se gli specchi interni di un autobus sono mal orientati, potrebbe accadere che il conducente chiuda le porte e riparta quando delle persone non hanno ancora terminato la salita o la discesa dal veicolo ', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '118', '81004', '882', '5', 'Se gli specchi interni di un autobus sono mal orientati, potrebbe accadere che il conducente non veda se ci sono persone alla fermata che attendono l''autobus per salire', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '118', '81004', '883', '6', 'Se gli specchi interni di un autobus sono mal orientati, ciò non ha grande importanza se quelli esterni sono in posizione corretta', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('9', '118', '81004', '884', '7', 'Non esistono mai specchi interni sugli autobus, tutt''al più è presente una telecamera a circuito chiuso su quelli di classe B ', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '119', ' 111001/2/8/15/16', '', '', 'Motori a combustione interna - Combustione', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '119', ' 111001/2/8/15/16', '885', '1', 'Il motore a combustione interna che fornisce potenza agli autoveicoli, trasforma l''energia termica ricavata dal combustibile, in energia meccanica disponibile alle ruote', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '119', ' 111001/2/8/15/16', '886', '2', 'Un motore a combustione interna ha lo scopo di trasformare l''energia termica in energia meccanica', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '119', ' 111001/2/8/15/16', '887', '3', 'Il motore a combustione interna trasforma energia termica in energia meccanica', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '119', ' 111001/2/8/15/16', '888', '4', 'La fumosità di un motore a gasolio è indicativa di una cattiva combustione e può essere causata da incrostazioni presenti sugli iniettori, che impediscono la corretta nebulizzazione del combustibile', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '119', ' 111001/2/8/15/16', '889', '5', 'Durante il funzionamento di un motore, il calore che non viene trasformato in energia meccanica viene ceduto all''esterno, tramite l''impianto di raffreddamento, per evitarne il surriscaldamento', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '119', ' 111001/2/8/15/16', '890', '6', 'I veicoli equipaggiati con motori a combustione interna trasformano l''energia termica prodotta dalla combustione in energia meccanica disponibile alle ruote', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '119', ' 111001/2/8/15/16', '891', '7', 'I motori a combustione interna possono essere indifferentemente a due o a quattro tempi', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '119', ' 111001/2/8/15/16', '892', '8', 'Il motore a quattro tempi è un tipo di motore termico in grado di utilizzare vari tipi di combustibili, come benzina, gasolio, metano, gpl, alcool', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '119', ' 111001/2/8/15/16', '893', '9', 'In un motore a quattro tempi, l''albero motore compie due giri per completare un singolo ciclo di funzionamento', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '119', ' 111001/2/8/15/16', '894', '10', 'Le fasi di funzionamento di un motore a quattro tempi sono: aspirazione, compressione, scoppio (o combustione) e scarico', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '119', ' 111001/2/8/15/16', '895', '11', 'Il motore a combustione interna che fornisce potenza agli autoveicoli, trasforma l''energia meccanica in energia termica', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '119', ' 111001/2/8/15/16', '896', '12', 'I motori a combustione interna trasformano la forza motrice in calore', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '119', ' 111001/2/8/15/16', '897', '13', 'Se il motore è a combustione interna non ha bisogno della marmitta', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '119', ' 111001/2/8/15/16', '898', '14', 'Soltanto il motore Diesel è un motore a combustione interna', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '119', ' 111001/2/8/15/16', '899', '15', 'Soltanto il motore a scoppio è un motore a combustione interna', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '120', ' 111001/2/8/15/16', '', '', 'Motori a combustione interna – Parti Principali', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '120', ' 111001/2/8/15/16', '900', '1', 'I cilindri di un motore sono raggruppati in un unico blocco metallico di fusione che prende il nome di monoblocco', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '120', ' 111001/2/8/15/16', '901', '2', 'I cilindri di un motore hanno la funzione di camera di espansione dei gas e di guida per i pistoni', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '120', ' 111001/2/8/15/16', '902', '3', 'I cilindri sono parti meccaniche caratteristiche dei motori a combustione interna', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '120', ' 111001/2/8/15/16', '903', '4', 'I pistoni sono organi tipici di un motore a combustione interna', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '120', ' 111001/2/8/15/16', '904', '5', 'L''albero a camme comanda l''apertura e la chiusura delle valvole', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '120', ' 111001/2/8/15/16', '905', '6', 'Le valvole della distribuzione sono almeno due per ogni cilindro', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '120', ' 111001/2/8/15/16', '906', '7', 'La rottura della cinghia dentata o della catena di distribuzione può recare gravi danni agli organi interni del motore', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '120', ' 111001/2/8/15/16', '907', '8', 'La catena della distribuzione collega l''albero motore con l''albero della distribuzione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '120', ' 111001/2/8/15/16', '908', '9', 'L''organo principale di un motore è l''albero motore', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '120', ' 111001/2/8/15/16', '909', '10', 'L''aria aspirata dai motori a scoppio e Diesel passa attraverso un filtro di depurazione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '120', ' 111001/2/8/15/16', '910', '11', 'Le valvole di aspirazione e di scarico si trovano sia nei motori a scoppio che nei motori Diesel', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '120', ' 111001/2/8/15/16', '911', '12', 'Gli organi della distribuzione di un motore a scoppio o Diesel comprendono: l''albero a camme, le valvole, una catena o una cinghia dentata', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '120', ' 111001/2/8/15/16', '912', '13', 'Il silenziatore permette l''espansione dei gas di scarico prima che escano all''esterno così da diminuire la rumorosità del motore', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '120', ' 111001/2/8/15/16', '913', '14', 'Le bielle uniscono i pistoni con l''albero a camme', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '120', ' 111001/2/8/15/16', '914', '15', 'Il movimento dei pistoni è un moto rotatorio', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '120', ' 111001/2/8/15/16', '915', '16', 'I cilindri di un motore a combustione interna devono essere sempre in numero pari', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '120', ' 111001/2/8/15/16', '916', '17', 'A seguito della rottura della cinghia della distribuzione, il motore non viene più raffreddato adeguatamente', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '120', ' 111001/2/8/15/16', '917', '18', 'Il movimento dell''albero motore è un moto alternativo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '120', ' 111001/2/8/15/16', '918', '19', 'La cinghia della distribuzione deve essere sostituita ogni 20000 chilometri', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '120', ' 111001/2/8/15/16', '919', '20', 'L''albero motore e l''albero di trasmissione sono due nomi diversi per definire lo stesso organo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '121', '111001/2/8/15/16', '', '', 'Motori a combustione interna – Motore diesel', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '121', '111001/2/8/15/16', '920', '1', 'Il motore Diesel è detto anche ad accensione spontanea', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '121', '111001/2/8/15/16', '921', '2', 'Il motore Diesel funziona aspirando e comprimendo solo aria', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '121', '111001/2/8/15/16', '922', '3', 'Il motore Diesel è un motore a combustione interna', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '121', '111001/2/8/15/16', '923', '4', 'Il motore Diesel funziona aspirando e comprimendo una miscela di aria e gasolio', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '121', '111001/2/8/15/16', '924', '5', 'Il motore Diesel è detto anche motore ad accensione comandata', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '121', '111001/2/8/15/16', '925', '6', 'Un motore a gasolio può funzionare bene anche a benzina, ma in tal caso è meglio non percorrere tragitti oltre i duecento chilometri', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '121', '111001/2/8/15/16', '926', '7', 'I motori Diesel non necessitano di lubrificazione perché il gasolio funge anche da lubrificante', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '122', '111001/2/8/15/16', '', '', 'Motori a combustione interna – Motore scoppio', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '122', '111001/2/8/15/16', '927', '1', 'Il motore a scoppio funziona aspirando e comprimendo una miscela di aria e benzina', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '122', '111001/2/8/15/16', '928', '2', 'La valvola a farfalla di un motore a scoppio fa entrare nei cilindri la quantità di aria richiesta in base alla posizione del pedale dell''acceleratore', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '122', '111001/2/8/15/16', '929', '3', 'Nei motori a scoppio, i cilindri aspirano miscela di aria e benzina oppure aria e metano o aria e GPL', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '122', '111001/2/8/15/16', '930', '4', 'Il motore a scoppio è detto anche ad accensione comandata', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '122', '111001/2/8/15/16', '931', '5', 'Un motore a scoppio può essere alimentato sia con benzina sia con gas metano', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '122', '111001/2/8/15/16', '932', '6', 'Il motore a scoppio è un motore a combustione interna', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '122', '111001/2/8/15/16', '933', '7', 'Il motore a scoppio è detto anche ad accensione spontanea', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '122', '111001/2/8/15/16', '934', '8', 'Un motore ad accensione comandata non può essere del tipo a ciclo Otto (a scoppio)', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '122', '111001/2/8/15/16', '935', '9', 'Il motore a scoppio funziona aspirando e comprimendo solo aria', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '1123', '111001/2/8/15/16', '', '', 'Motori a combustione interna ', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '1123', '111001/2/8/15/16', '936', '1', 'I motori a scoppio e quelli a ciclo Diesel sono ambedue motori a combustione interna', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '1123', '111001/2/8/15/16', '937', '2', 'Il silenziatore permette l''espansione dei gas di scarico prima che escano all''esterno così da diminuire la rumorosità del motore', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '1123', '111001/2/8/15/16', '938', '3', 'Il numero massimo dei giri del motore Diesel è sensibilmente inferiore rispetto a quello possibile nel motore a scoppio', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '1123', '111001/2/8/15/16', '939', '4', 'Sia l''anticipo dell''accensione nel motore a scoppio che l''anticipo dell''iniezione nel motore Diesel sono regolati automaticamente', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '1123', '111001/2/8/15/16', '940', '5', 'Il motore Diesel funziona ad un regime di giri più elevato di quello a scoppio', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '1123', '111001/2/8/15/16', '941', '6', 'A differenza di quello a scoppio, il motore Diesel non necessita di filtro dell''aria', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '1123', '111001/2/8/15/16', '942', '7', 'Il motore Diesel è in genere più silenzioso di quello a scoppio', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '1123', '111001/2/8/15/16', '943', '8', 'Tutti i motori a combustione interna sono dotati di iniettori', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '1123', '111001/2/8/15/16', '944', '9', 'In linea teorica, il motore Diesel ha un rendimento peggiore di quello a scoppio', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '1123', '111001/2/8/15/16', '945', '10', 'Le candele a scintilla sono utilizzate sia nei motori a scoppio che in quelli Diesel', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '1123', '111001/2/8/15/16', '946', '11', 'Sono ad accensione spontanea tutti i motori dotati di candele a scintilla', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '1123', '111001/2/8/15/16', '947', '12', 'Il battito in testa è indice del fatto che i pistoni vanno a colpire la testata a ogni giro', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '1123', '111001/2/8/15/16', '948', '13', 'I motori a gas richiedono una maggior manutenzione rispetto a quelli a benzina o a gasolio', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '124', '111009', '', '', 'Caratteristiche del motore a combustione interna 1', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '124', '111009', '949', '1', 'La potenza rappresenta il lavoro compiuto nell''unità di tempo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '124', '111009', '950', '2', 'Il consumo specifico di un motore rappresenta la quantità di combustibile necessaria per la produzione di una unità di potenza', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '124', '111009', '951', '3', ' Per un rendimento ottimale del motore occorre tenerlo sempre al massimo dei giri', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '124', '111009', '952', '4', 'Per un rendimento ottimale del motore occorre tenerlo sempre al minimo dei girI', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '124', '111009', '953', '5', 'La potenza di un motore si indica in Volt', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '125', '111015', '', '', 'Caratteristiche del motore a combustione interna 2', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '125', '111015', '954', '1', 'Se il rendimento di un motore aumenta, il suo consumo specifico di combustibile diminuisce', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '125', '111015', '955', '2', 'La benzina ha un punto di infiammabilità inferiore a quello del gasolio', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '125', '111015', '956', '3', 'La cilindrata di un motore è data dalla somma dei volumi interni dei singoli cilindri, calcolata nel momento in cui i pistoni si trovano in corrispondenza del punto morto inferiore', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '125', '111015', '957', '4', 'Se il rendimento di un motore aumenta, anche il suo consumo specifico di combustibile cresce', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '125', '111015', '958', '5', 'I cilindri sono elementi mobili del motore che scorrono in apposite sedi ricavate nel monoblocco', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '126', '111003', '', '', 'Motore Diesel 1', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '126', '111003', '959', '1', 'I motori Diesel sono provvisti di un regolatore dei giri a funzionamento automatico', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '126', '111003', '960', '2', 'Il motore Diesel non è autoregolante per quanto riguarda il numero di giri al minuto', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '126', '111003', '961', '3', 'Il numero di giri a cui funziona, in genere, un motore a scoppio è più elevato rispetto a quello di un motore Diesel', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '126', '111003', '962', '4', 'I motori Diesel sono motori ad accensione spontanea', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '126', '111003', '963', '5', 'Non tutti i motori Diesel hanno le stesse modalità di accensione del gasolio', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '126', '111003', '964', '6', 'Nei motori Diesel, in luogo della catena o della cinghia della distribuzione, può trovarsi un collegamento costituito da ingranaggi', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '126', '111003', '965', '7', 'Le termocandele funzionano con corrente a bassa tensione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '126', '111003', '966', '8', 'Il gasolio ha una combustione più lenta rispetto alla benzina', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '126', '111003', '967', '9', 'L''iniezione del gasolio è tanto più anticipata quanto più veloce è la rotazione del motore', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '126', '111003', '968', '10', 'Le termocandele si spengono subito dopo l''avviamento del motore', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '126', '111003', '969', '11', 'Le candele di preaccensione si trovano sul cruscotto di un veicolo con motore Diesel', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '126', '111003', '970', '12', 'Se un cilindro di un motore Diesel ha 4 valvole, generalmente sono tutte di aspirazione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '126', '111003', '971', '13', 'Se il silenziatore di un motore Diesel non funziona bene, è necessario strozzare leggermente il tubo di scarico', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '126', '111003', '972', '14', 'Le termocandele facilitano l''avviamento dei motori a scoppio durante la stagione fredda', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '126', '111003', '973', '15', 'Le termocandele di un motore Diesel rimangono accese finché la temperatura dell''acqua di raffreddamento raggiunge gli 80°C', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '126', '111003', '974', '16', 'In alcuni motori Diesel più performanti, le termocandele sono sostituite da candele a scintilla', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '126', '111003', '975', '17', 'Un motore a ciclo Diesel non può essere ad accensione spontanea', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '126', '111003', '976', '18', 'Un motore Diesel ha, in genere, rendimento molto più basso di uno a benzina', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '127', '111010', '', '', 'Motore Diesel 2', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '127', '111010', '977', '1', 'Il motore Diesel è un motore ad accensione spontanea', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '127', '111010', '978', '2', 'Il motore Diesel utilizza il gasolio come combustibile', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '127', '111010', '979', '3', 'Il motore Diesel è alimentato solo per iniezione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '127', '111010', '980', '4', 'Nel motore Diesel la combustione avviene per autoaccensione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '127', '111010', '981', '5', 'Nel motore Diesel la combustione avviene grazie all''iniezione del gasolio in ambiente ad alta temperatura', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '127', '111010', '982', '6', 'Il motore Diesel può funzionare indifferentemente a gasolio o a benzina', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '127', '111010', '983', '7', 'Il motore Diesel utilizza la miscela creata nel carburatore', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '127', '111010', '984', '8', 'Il motore Diesel utilizza il gasolio perché è molto infiammabile a temperatura ambiente', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '127', '111010', '985', '9', 'Il motore Diesel è un motore in cui l''accensione del carburante avviene per mezzo della scintilla delle candele', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '127', '111010', '986', '10', 'Il motore Diesel è dotato di un impianto di accensione che genera la scintilla agli elettrodi delle candele', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '128', '112002', '', '', 'Motore Diesel 3', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '128', '112002', '987', '1', 'I motori Diesel sono dotati di filtro dell''aria e filtro del gasolio', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '128', '112002', '988', '2', 'Nei motori Diesel l''acceleratore regola la quantità di gasolio che viene iniettata nei cilindri', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '128', '112002', '989', '3', 'Il filtro dell''aria di un motore Diesel deve periodicamente essere controllato, sostituendo se necessario la cartuccia filtrante', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '128', '112002', '990', '4', 'Il Common-rail è un sistema di iniezione a controllo elettronico per motori Diesel, che consente una altissima pressione di iniezione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '128', '112002', '991', '5', 'La cartuccia dei filtri del gasolio deve essere sostituita periodicamente', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '128', '112002', '992', '6', 'Il motore Diesel può anche essere alimentato con BioDiesel', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '128', '112002', '993', '7', 'Le candelette di un motore Diesel servono per permettere le prime accensioni del gasolio nei cilindri a motore freddo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '128', '112002', '994', '8', 'In un motore Diesel la combustione del gasolio avviene grazie al suo surriscaldamento', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '128', '112002', '995', '9', 'In un motore Diesel a iniezione diretta, il gasolio è iniettato direttamente nel cilindro', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '128', '112002', '996', '10', 'In un motore Diesel a iniezione indiretta, il gasolio è iniettato in una precamera esterna al cilindro', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '128', '112002', '997', '11', 'Il motore Diesel può essere alimentato anche con bioetanolo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '128', '112002', '998', '12', 'Le candelette di un motore Diesel servono, a motore caldo, per regolarizzare le accensioni del gasolio quando il motore ruota a regime di potenza massima', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '128', '112002', '999', '13', 'In un comune motore Diesel il gasolio viene iniettato nei cilindri alla pressione di circa 25 Bar', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '128', '112002', '1000', '14', 'La pompa di iniezione del motore Diesel viene fatta funzionare da una cinghia trapezoidale', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '128', '112002', '1001', '15', 'Quando la temperatura esterna è molto fredda è bene alimentare il motore Diesel con una miscela di gasolio (70%) e benzina o alcool (30%)', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '128', '112002', '1002', '16', 'La cartuccia dei filtri del gasolio deve essere sostituita ad ogni revisione periodica del veicolo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '128', '112002', '1003', '17', 'In un motore Diesel a iniezione indiretta, il gasolio è iniettato direttamente nel cilindro', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '128', '112002', '1004', '18', 'Il motore Diesel ha maggiori problemi di rendimento in altitudine rispetto al motore a scoppio', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '128', '112002', '1005', '19', 'In un motore Diesel a iniezione diretta, il gasolio è iniettato in una precamera esterna al cilindro', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '129', '112001', '', '', 'Alimentazione del motore Diesel 1', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '129', '112001', '1006', '1', 'L''impianto di alimentazione del motore Diesel ha il compito di gestire la mandata del gasolio', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '129', '112001', '1007', '2', 'L''impianto di alimentazione del motore Diesel può comprendere iniettori pompa', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '129', '112001', '1008', '3', 'L''impianto di alimentazione del motore Diesel è dotato di un filtro del gasolio', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '129', '112001', '1009', '4', 'L''impianto di alimentazione del motore Diesel comprende anche degli iniettori che polverizzano il gasolio', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '129', '112001', '1010', '5', 'L''impianto di alimentazione del motore Diesel può essere dotato di una pompa di iniezione che invia gasolio ad alta pressione agli iniettori', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '129', '112001', '1011', '6', 'L''impianto di alimentazione del motore Diesel è dotato di una pompa di alimentazione che invia il gasolio ad alta pressione agli iniettori', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '129', '112001', '1012', '7', 'L''impianto di alimentazione del motore Diesel prevede un filtro a cartuccia che deve essere sostituito ad ogni rifornimento di carburante', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '129', '112001', '1013', '8', 'L''impianto di alimentazione del motore Diesel è dotato di pompa di iniezione che aspira gasolio dal serbatoio', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '129', '112001', '1014', '9', 'L''impianto di alimentazione del motore Diesel è dotato di un filtro aria che deve essere sostituito ad ogni rifornimento di carburante', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '129', '112001', '1015', '10', 'L''impianto di alimentazione del motore Diesel è regolato direttamente dal conducente per mezzo di appositi interruttori posti sul cruscotto', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '130', '111011', '', '', 'Fasi di funzionamento nel motore Diesel', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '130', '111011', '1016', '1', 'Nel motore Diesel, le fasi di funzionamento si susseguono nell''ordine: aspirazione, compressione, combustione e scarico', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '130', '111011', '1017', '2', 'Nel motore Diesel l''aspirazione, prodotta dalla discesa del pistone, permette l''ingresso nel cilindro della sola aria', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '130', '111011', '1018', '3', 'Nel motore Diesel la compressione, dovuta alla risalita del pistone, comprime fortemente l''aria facendola surriscaldare', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '130', '111011', '1019', '4', 'Nel motore Diesel, la combustione avviene per iniezione del gasolio in ambiente ad alta temperatura che ne provoca l''autoaccensione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '130', '111011', '1020', '5', 'Nel motore Diesel, le fasi di funzionamento si susseguono nel seguente ordine: aspirazione, combustione, scarico e compressione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '130', '111011', '1021', '6', 'Nel motore Diesel, la fase di aspirazione provoca l''ingresso della miscela nei cilindri', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '130', '111011', '1022', '7', 'Nel motore Diesel, la combustione avviene a bassa temperatura per migliorare il rendimento', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '131', '111012', '', '', 'Iniezione diretta o indiretta del combustibile nel motore Diesel', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '131', '111012', '1023', '1', 'Il motore Diesel può essere progettato con due diverse modalità di iniezione del combustibile', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '131', '111012', '1024', '2', 'Il motore Diesel può essere ad iniezione diretta', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '131', '111012', '1025', '3', 'Il motore Diesel può essere ad iniezione indiretta', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '131', '111012', '1026', '4', 'Nel motore Diesel ad iniezione diretta, il gasolio viene iniettato direttamente nella camera di combustione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '131', '111012', '1027', '5', 'Nel motore Diesel ad iniezione indiretta, il gasolio viene iniettato in una precamera di combustione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '131', '111012', '1028', '6', 'Nel motore Diesel ad iniezione diretta il gasolio viene prima iniettato nella precamera di combustione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '131', '111012', '1029', '7', 'Nel motore Diesel ad iniezione indiretta, il gasolio viene iniettato direttamente sulla parte superiore del pistone', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '131', '111012', '1030', '8', 'Il motore Diesel prevede l''uso delle candele che generano la scintilla che fa accendere il gasolio', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '131', '111012', '1031', '9', 'Il motore Diesel ad iniezione indiretta ha un rendimento migliore di quello ad iniezione diretta', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '131', '111012', '1032', '10', 'Il motore Diesel ad iniezione indiretta è meno inquinante rispetto a quello ad iniezione diretta', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '132', '111014', '', '', 'Differenze tra motore Diesel e motore a benzina', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '132', '111014', '1033', '1', 'Il motore Diesel si differenzia dal motore a benzina poiché, nella fase di aspirazione, aspira solo aria', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '132', '111014', '1034', '2', 'Il motore Diesel si differenzia dal motore a benzina in quanto ha un maggior rapporto di compressione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '132', '111014', '1035', '3', 'Il motore Diesel si differenzia dal motore a benzina poiché la combustione avviene per effetto di accensione spontanea del gasolio', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '132', '111014', '1036', '4', 'Il motore Diesel si differenzia dal motore a benzina poiché, nella fase di aspirazione, non aspira solo aria', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '132', '111014', '1037', '5', 'Il motore Diesel si differenzia dal motore a benzina perché non può funzionare senza candele a scintilla', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '132', '111014', '1038', '6', 'Il motore Diesel si differenzia dal motore a benzina poiché ha un rendimento notevolmente peggiore', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '132', '111014', '1039', '7', 'Il motore Diesel si differenzia dal motore a benzina perché funziona a un numero di giri notevolmente più elevato', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '132', '111014', '1040', '8', 'Il motore Diesel si differenzia dal motore a benzina perché funziona molto bene anche con temperature esterne molto basse', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '132', '111014', '1041', '9', 'A differenza di quello a scoppio, il motore Diesel non necessita di filtro del combustibile', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '133', '111004', '', '', 'Motore a scoppio', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '133', '111004', '1042', '1', 'In un motore a scoppio, il pedale dell''acceleratore influisce sulla posizione della valvola a farfalla', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '133', '111004', '1043', '2', 'I motori a scoppio sono motori ad accensione comandata', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '133', '111004', '1044', '3', 'Il motore a scoppio può funzionare anche con alimentazione a metano o a GPL', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '133', '111004', '1045', '4', 'Le candele a scintilla sono caratteristiche di un motore a scoppio', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '133', '111004', '1046', '5', 'Nel motore a scoppio, l''accensione del carburante (scintilla della candela) è tanto più anticipata quanto più veloce è la rotazione del motore', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '133', '111004', '1047', '6', 'Nei moderni motori a scoppio, in luogo delle candele vengono impiegati gli iniettori', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '133', '111004', '1048', '7', 'In un motore a scoppio la miscela benzina-aria si realizza nel serbatoio del carburante', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '133', '111004', '1049', '8', 'L''anticipo dell''accensione nel motore a scoppio deve diminuire man mano che aumenta il numero dei giri', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '133', '111004', '1050', '9', 'Se il motore a scoppio funziona a metano, non ha bisogno della marmitta catalitica', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '133', '111004', '1051', '10', 'Se il motore a scoppio funziona a GPL, non ha bisogno della marmitta catalitica', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '134', '111005', '', '', 'Motori a metano o a GPL', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '134', '111005', '1052', '1', 'Il motore a gas metano è un motore a combustione interna', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '134', '111005', '1053', '2', 'Il gas metano è meno inquinante della benzina e del gasolio', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '134', '111005', '1054', '3', 'Il motore a GPL è un motore a combustione interna', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '134', '111005', '1055', '4', 'Il GPL è meno inquinante della benzina e del gasolio', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '134', '111005', '1056', '5', 'Sono a combustione interna solo i motori funzionanti a gas metano o a GPL', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '134', '111005', '1057', '6', 'Il metano è più economico, ma è più inquinante della benzina', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '134', '111005', '1058', '7', 'Il GPL è un gas liquido e quindi si può introdurre nel serbatoio della benzina', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '134', '111005', '1059', '8', 'Il GPL è un gas liquido non a pressione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '135', '111007', '', '', 'Motore elettrico', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '135', '111007', '1060', '1', 'Il motore elettrico non è un motore a combustione interna', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '135', '111007', '1061', '2', 'Il motore elettrico viene in genere raffreddato ad aria', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '135', '111007', '1062', '3', 'Un motore elettrico trasforma energia elettromagnetica in forza motrice', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '135', '111007', '1063', '4', 'I filobus sono veicoli con motore elettrico, alimentati da una rete di distribuzione di elettricità installata sulla strada', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '135', '111007', '1064', '5', 'Un autoveicolo con trazione elettrica alimentato da accumulatori, rispetto ad uno a combustione interna, è generalmente meno veloce, meno potente, con un''autonomia più limitata', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '135', '111007', '1065', '6', 'Il motore elettrico è un motore a combustione esterna', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '135', '111007', '1066', '7', 'Un motore elettrico trasforma la forza motrice in energia elettromagnetica', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '135', '111007', '1067', '8', 'I pistoni sono organi tipici dei motori elettrici', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '135', '111007', '1068', '9', 'I motori elettrici sono dotati di cambio di velocità a tre marce', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '135', '111007', '1069', '10', 'I motori elettrici sono motori ad accensione spontanea', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '136', '111006', '', '', 'Veicoli ibridi', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '136', '111006', '1070', '1', 'Un veicolo ibrido può essere dotato di un motore a scoppio', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '136', '111006', '1071', '2', 'Un veicolo ibrido può essere dotato di un motore Diesel', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '136', '111006', '1072', '3', 'Un veicolo ibrido è dotato anche di un motore elettrico', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '136', '111006', '1073', '4', 'Un veicolo ibrido è un veicolo a propulsione mista', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '136', '111006', '1074', '5', 'I veicoli ibridi sono meno inquinanti degli altri tipi, ad eccezione di quelli elettrici', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '136', '111006', '1075', '6', 'Un veicolo ibrido ha due motori, uno a combustione interna (scoppio o Diesel) ed uno elettrico', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '136', '111006', '1076', '7', 'Sono veicoli ibridi quelli che hanno il motore funzionante a idrogeno', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '136', '111006', '1077', '8', 'Un veicolo ibrido è dotato di due motori: uno a scoppio ed uno a gas metano 0', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '136', '111006', '1078', '9', 'Un veicolo ibrido è dotato solo di due motori elettrici: uno ad alta tensione ed uno a bassa tensione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '136', '111006', '1079', '10', 'Un veicolo ibrido è molto economico, ma è più inquinante degli altri veicoli', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '136', '111006', '1080', '11', 'I veicoli ibridi hanno motori adatti a funzionare solo con temperature esterne elevate', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '136', '111006', '1081', '12', 'I veicoli ibridi devono avere trazione a quattro ruote motrici', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '137', '113001', '', '', 'Combustibili per motori a combustione interna', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '137', '113001', '1082', '1', 'I combustibili, miscelati con l''opportuna quantità d''aria e successivamente incendiati, forniscono l''energia necessaria al funzionamento del motore', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '137', '113001', '1083', '2', 'In altitudine, dove la pressione dell''aria è inferiore, diminuisce il rendimento del motore, soprattutto se è di tipo a scoppio', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '137', '113001', '1084', '3', 'Il motore Diesel ha, in linea di principio, un rendimento migliore di quello a scoppio', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '137', '113001', '1085', '4', 'La benzina è più volatile del gasolio', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '137', '113001', '1086', '5', 'Il bioDiesel è una miscela di gasolio ordinario e olio vegetale combustibile, utilizzato nell''ottica di ridurre l''inquinamento atmosferico', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '137', '113001', '1087', '6', 'Con la sigla B10 si individua un bioDiesel composto dal 10% di olio vegetale e dal 90% di gasolio', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '137', '113001', '1088', '7', 'I combustibili, miscelati con l''opportuna quantità di vapore e successivamente incendiati, forniscono l''energia necessaria al funzionamento del motore', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '137', '113001', '1089', '8', 'In altitudine, dove la pressione dell''aria è inferiore, diminuisce il rendimento del motore, soprattutto se è di tipo Diesel', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '137', '113001', '1090', '9', 'Il motore a scoppio ha, in linea di principio, un rendimento migliore di quello Diesel', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '137', '113001', '1091', '10', 'Il gasolio è più volatile della benzina', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '137', '113001', '1092', '11', 'Con la sigla B10 si individua un combustibile composto dal 10% di gasolio ordinario e dal 90% di olio vegetale', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '137', '113001', '1093', '12', 'Il gasolio può essere miscelato con una buona percentuale di alcool, nell''ottica di ridurre l''inquinamento atmosferico', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '137', '113001', '1094', '13', 'Il bioDiesel è un combustibile prodotto in reffinerie che hanno la certificazione ambientale di limitato inquinamento', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '138', '113002', '', '', 'Gas combustibili per motori a combustione interna', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '138', '113002', '1095', '1', 'La sigla GPL identifica il gas di petrolio liquefatto', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '138', '113002', '1096', '2', 'Il GPL è una miscela di idrocarburi che viene resa liquida a bassa pressione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '138', '113002', '1097', '3', 'Il GPL è un carburante poco inquinante', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '138', '113002', '1098', '4', 'Il GPL può avere un avviamento a freddo difficoltoso', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '138', '113002', '1099', '5', 'Il metano, per essere usato quale combustibile per autoveicoli, deve essere fortemente compresso', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '138', '113002', '1100', '6', 'Il metano non dà generalmente problemi nell''avviamento a freddo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '138', '113002', '1101', '7', 'Il metano deve essere immagazzinato in serbatoi costituiti da bombole molto pesanti e resistenti', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '138', '113002', '1102', '8', 'La sigla GPL identifica il gasolio con particolato limitato', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '138', '113002', '1103', '9', 'Il GPL è un carburante più inquinante del gasolio', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '138', '113002', '1104', '10', 'Il GPL è una miscela di idrocarburi che viene resa liquida ad alta pressione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '138', '113002', '1105', '11', 'Il GPL ha un buon avviamento a freddo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '138', '113002', '1106', '12', 'Il metano, per essere usato quale combustibile per autoveicoli, deve essere compresso a circa 4 bar', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '138', '113002', '1107', '13', 'A parità di volume del serbatoio di un autoveicolo, il pieno di metano compresso garantisce una maggior autonomia di viaggio rispetto alla benzina', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '138', '113002', '1108', '14', 'Il metano si miscela male con l''aria e quindi brucia emettendo maggiori residui inquinanti', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '139', '1105010', '', '', 'Volano', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '139', '1105010', '1109', '1', 'Il volano è un pesante disco, collegato all''albero motore, che serve come base di appoggio per il disco della frizione, nonché per avviare e per regolarizzare la rotazione dell''albero motore', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '139', '1105010', '1110', '2', 'Il volano serve ad aumentare la velocità di rotazione del motore quando il veicolo deve effettuare grossi sforzi (ad esempio: salita ripida)', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '140', '112003/111016', '', '', 'Pompa di alimentazione e pompa di iniezione del motore Diesel', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '140', '112003/111016', '1111', '1', 'In un motore Diesel, se la pompa di alimentazione ha funzionamento elettrico, può trovarsi immersa all''interno del serbatoio del combustibile', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '140', '112003/111016', '1112', '2', 'In un motore Diesel, la pompa di alimentazione a bassa pressione spinge il combustibile dal serbatoio alla pompa di iniezione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '140', '112003/111016', '1113', '3', 'In un motore Diesel, la pompa di alimentazione di un motore Diesel è azionata dal motore o dalla pompa di iniezione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '140', '112003/111016', '1114', '4', 'In un motore Diesel, la pompa di iniezione spinge il gasolio in pressione agli iniettori nella quantità richiesta dal pedale dell''acceleratore', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '140', '112003/111016', '1115', '5', 'In un motore Diesel, se la pompa di alimentazione cessa di funzionare, il motore si arresta', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '140', '112003/111016', '1116', '6', 'In un motore Diesel, ogni 10 rifornimenti di combustibile è necessario ripulire il filtro della pompa di alimentazione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '140', '112003/111016', '1117', '7', 'In un motore Diesel, quando il clima è molto freddo, la pompa di alimentazione del combustibile deve essere accesa dal conducente con apposito pulsante, alcuni secondi prima della messa in moto del motore', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '140', '112003/111016', '1118', '8', 'In un motore Diesel, la pompa di alimentazione a bassa pressione deve essere posizionata sul veicolo in un punto lontano da fonti di calore, oppure deve essere opportunamente coibentata', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '140', '112003/111016', '1119', '9', 'In un motore Diesel, la pompa di alimentazione a bassa pressione è dotata di anticongelatore da attivare durante la stagione fredda', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '140', '112003/111016', '1120', '10', 'In un motore Diesel, la pompa di alimentazione del combustibile a funzionamento elettrico non può mai trovarsi nel serbatoio a causa del pericolo d''incendio che ciò comporterebbe', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '140', '112003/111016', '1121', '11', 'In un motore a gasolio, la pompa di iniezione ha il compito di prelevare il gasolio dal serbatoio e inviarlo a bassa pressione agli iniettori', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '141', '112005', '', '', 'Pompa di iniezione in linea del motore Diesel 1', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '141', '112005', '1122', '1', 'In un motore Diesel, la pompa di iniezione in linea serve a inviare il gasolio agli iniettori, sotto forte pressione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '141', '112005', '1123', '2', 'In un motore Diesel, la pompa di iniezione in linea, comandata dal pedale dell''acceleratore, permette di regolare la quantità di gasolio da inviare agli iniettori', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '141', '112005', '1124', '3', 'In un motore Diesel, la pompa di iniezione in linea regola, fra l''altro, il momento opportuno per l''invio del gasolio agli iniettori', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '141', '112005', '1125', '4', 'In un motore Diesel, la pompa di iniezione in linea è dotata di elementi pompanti (uno per ogni cilindro)', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '141', '112005', '1126', '5', 'In un motore Diesel, la pompa di iniezione in linea è dotata di un albero a camme', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '141', '112005', '1127', '6', 'In un motore Diesel, la pompa di iniezione in linea è dotata di un''asta a cremagliera che è comandata dal pedale dell''acceleratore', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '141', '112005', '1128', '7', 'In un motore Diesel, la pompa di iniezione in linea è equipaggiata con un regolatore di giri', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '141', '112005', '1129', '8', 'In un motore Diesel, la pompa di iniezione in linea è dotata di un dispositivo di anticipo automatico', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '141', '112005', '1130', '9', 'In un motore Diesel, la pompa di iniezione in linea aspira il gasolio dal serbatoio', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '141', '112005', '1131', '10', 'In un motore Diesel, la pompa di iniezione in linea invia anche l''olio lubrificante alle parti in movimento del motore', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '141', '112005', '1132', '11', 'In un motore Diesel, la pompa di iniezione in linea prende movimento dagli alberi della distribuzione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '141', '112005', '1133', '12', 'In un motore Diesel, la pompa di iniezione in linea alimenta la portata dell''acqua di raffreddamento', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '141', '112005', '1134', '13', 'In un motore Diesel, la pompa di iniezione in linea ha un solo elemento pompante', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '141', '112005', '1135', '14', 'In un motore Diesel, la pompa di iniezione in linea serve ad inviare, sotto forte pressione, l''olio agli elementi frenanti delle ruote', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '141', '112005', '1136', '15', 'In un motore Diesel, la pompa di iniezione in linea deve essere sostituita ogni 25000 chilometri circa', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '141', '112005', '1137', '16', 'In un motore Diesel, la pompa di iniezione in linea è dotata di una candeletta di preaccensione per ciascun iniettore', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '142', '112006', '', '', 'Pompa di iniezione in linea del motore Diesel 2', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '142', '112006', '1138', '1', 'In un motore Diesel, gli elementi pompanti di una pompa di iniezione in linea prendono movimento dall''albero a camme della pompa', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '142', '112006', '1139', '2', 'In un motore didiel, gli elementi pompanti di una pompa di  iniezione in linea sono in numero uguale ai cilindri', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '142', '112006', '1140', '3', 'In un motore Diesel, gli elementi pompanti di una pompa di iniezione in linea comprimono il gasolio ad alta pressione destinato agli iniettori', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '142', '112006', '1141', '4', 'In un motore Diesel, gli elementi pompanti di una pompa di iniezione in linea sono sempre in numero pari', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '142', '112006', '1142', '5', 'In un motore Diesel, gli elementi pompanti di una pompa di iniezione in linea devono essere smontati e puliti ogni 30 giorni circa', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '142', '112006', '1143', '6', 'In un motore Diesel, gli elementi pompanti non necessitano di manutenzione se si utilizza bioDiesel', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '143', '112008', '', '', 'Iniettori del motore Diesel', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '143', '112008', '1144', '1', 'In un motore Diesel, gli iniettori iniettano nei cilindri gasolio ad alta pressione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '143', '112008', '1145', '2', 'In un motore Diesel, gli iniettori sono dotati di un dispositivo che polverizza il gasolio in ingresso nei cilindri', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '143', '112008', '1146', '3', 'In un motore Diesel, gli iniettori devono essere periodicamente controllati', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '143', '112008', '1147', '4', 'In un motore Diesel, gli iniettori sono spesso controllati elettronicamente da una centralina', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '143', '112008', '1148', '5', 'In un motore Diesel, il controllo elettronico degli iniettori permette di stabilire tempi e durata dell''iniezione del gasolio nei cilindri', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '143', '112008', '1149', '6', 'In un motore Diesel, gli iniettori iniettano gasolio nei cilindri a bassa pressione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '143', '112008', '1150', '7', 'In un motore Diesel, gli iniettori si trovano incorporati nella pompa di iniezione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '143', '112008', '1151', '8', 'In un motore Diesel, gli iniettori iniettano periodicamente olio lubrificante nel cilindro', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '143', '112008', '1152', '9', 'In un motore Diesel, gli iniettori cessano di iniettare gasolio dieci secondi dopo lo spegnimento del motore', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '144', '112007/111009', '', '', 'Anticipo automatico nel motore Diesel', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '144', '112007/111009', '1153', '1', 'Nei motori Diesel, l''anticipo automatico serve ad anticipare la mandata del gasolio agli iniettori', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '144', '112007/111009', '1154', '2', 'Nei motori Diesel, l''anticipo automatico serve a garantire una buona combustione del gasolio anche ad elevato numero di giri del motore', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '144', '112007/111009', '1155', '3', 'Nei motori Diesel, l''anticipo automatico entra in funzione all''aumentare del numero dei giri del motore', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '144', '112007/111009', '1156', '4', 'L''anticipo dell''iniezione del gasolio nei motori Diesel serve a favorire la completa combustione del gasolio stesso', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '144', '112007/111009', '1157', '5', 'Nei motori Diesel, l''anticipo automatico gestisce l''innesto della frizione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '144', '112007/111009', '1158', '6', 'Nei motori Diesel, l''anticipo automatico gestisce l''innesto delle marce alte del cambio di velocità', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '144', '112007/111009', '1159', '7', 'Nei motori Diesel, l''anticipo automatico attiva le candele di accensione del gasolio', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '145', '112009', '', '', 'Alimentazione del motore Diesel 3', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '145', '112009', '1160', '1', 'Il motore Diesel aspira aria mentre il gasolio viene introdotto nei cilindri dagli iniettori', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '145', '112009', '1161', '2', 'I motori Diesel sono sempre dotati di iniettori', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '145', '112009', '1162', '3', 'Se gli iniettori di un motore Diesel gocciolano, il motore emette fumo nero', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '145', '112009', '1163', '4', 'Nel motore Diesel, la combustione avviene in eccesso d''aria', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '145', '112009', '1164', '5', 'In un motore Diesel, si possono trovare gli iniettori-pompa, dispositivi che rendono inutile la presenza della pompa di iniezione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '145', '112009', '1165', '6', 'Il Common-rail è un dispositivo che permette l''iniezione del gasolio nei cilindri ad altissima pressione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '145', '112009', '1166', '7', 'In un motore Diesel, gli iniettori possono essere a comando meccanico od elettronico', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '145', '112009', '1167', '8', 'In un motore Diesel, gli iniettori ricevono il gasolio in pressione dalla pompa di iniezione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '145', '112009', '1168', '9', 'Nel sistema di iniezione Common-rail, gli iniettori sono a comando elettronico', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '145', '112009', '1169', '10', 'Il gasolio iniettato nei cilindri di un motore Diesel deve essere finemente polverizzato', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '145', '112009', '1170', '11', 'Gli iniettori di un motore Diesel hanno una pressione di iniezione che aumenta con l''aumentare della velocità del veicolo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '145', '112009', '1171', '12', 'Il motore Diesel è in genere munito di una candeletta di preriscaldamento posta vicino alla presa d''aria', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '145', '112009', '1172', '13', 'Se gli iniettori di un motore Diesel gocciolano, devono essere puliti con acqua e ammoniaca', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '145', '112009', '1173', '14', 'Gli iniettori ricevono il combustibile da una pompa a bassa pressione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '145', '112009', '1174', '15', 'Se gli iniettori di un motore gocciolano vanno avvitati meglio nelle loro sedi', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '145', '112009', '1175', '16', 'Un motore Diesel è munito di almeno una candela per cilindro', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '145', '112009', '1176', '17', 'Il motore Diesel funziona bene solo con poca aria aspirata dai cilindri', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '145', '112009', '1177', '18', 'I motori Diesel di ultima generazione hanno l''accensione delle candele controllata elettronicamente', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '145', '112009', '1178', '19', 'Nei motori Diesel più potenti, il gasolio viene iniettato a bassa pressione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '145', '112009', '1179', '20', 'I motori Diesel per autoveicoli lavorano normalmente ad alto numero di giri (4000-5000 giri/min)', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '145', '112009', '1180', '21', 'Il controllo elettronico dell''iniezione di combustibile è possibile solo nei motori a scoppio', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '146', '111013', '', '', 'Autoregolatore dei giri nel motore Diesel', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '146', '111013', '1181', '1', 'L''autoregolatore del numero di giri impedisce al motore Diesel di superare il regime massimo di giri del motore', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '146', '111013', '1182', '2', 'L''autoregolatore del numero di giri del motore Diesel impedisce che, in fase di accelerazione, il numero di giri salga ad un valore pericoloso per l''integrità del motore', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '146', '111013', '1183', '3', 'In un motore Diesel, l''autoregolatore del numero di giri serve ad anticipare la mandata del gasolio agli iniettori', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '146', '111013', '1184', '4', 'In un motore Diesel, l''autoregolatore del numero di giri può essere sostituito dal limitatore di velocità', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '147', '112012', '', '', 'Elettroiniettori nel motore Diesel', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '147', '112012', '1185', '1', 'In un motore Diesel, gli elettroiniettori servono a iniettare gasolio nei cilindri in maniera precisa sia nei tempi che nella quantità', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '147', '112012', '1186', '2', 'In un motore Diesel, gli elettroiniettori sono comandati dalla centralina elettronica, e possono iniettare il carburante nei cilindri in più mandate', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '147', '112012', '1187', '3', 'In un motore Diesel, gli elettroiniettori sono più precisi rispetto agli iniettori meccanici', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '147', '112012', '1188', '4', 'In un motore Diesel, gli elettroiniettori effettuano una preiniezione e successivamente l''iniezione principale', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '147', '112012', '1189', '5', 'In un motore Diesel, grazie alla gestione della centralina elettronica, gli elettroiniettori sono montati sulla testata del motore', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '147', '112012', '1190', '6', 'In un motore Diesel, gli elettroiniettori determinano la pressione di iniezione per mezzo di una molla tarata', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '147', '112012', '1191', '7', 'In un motore Diesel, gli elettroiniettori sono comandati meccanicamente dall''albero a camme della pompa', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '147', '112012', '1192', '8', 'In un motore Diesel, gli elettroiniettori, se malfunzionanti, possono essere riparati dal conducente purché sia dotato degli idonei strumenti', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '147', '112012', '1193', '9', 'In un motore Diesel, gli elettroiniettori sono alimentati da una batteria loro dedicata', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '147', '112012', '1194', '10', 'In un motore Diesel, gli elettroiniettori aumentano i consumi del veicolo perché utilizzano elettricità per il loro funzionamento', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '148', '112013', '', '', 'Sistema di iniezione multijet', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '148', '112013', '1195', '1', 'In un motore Diesel, il sistema d''iniezione multijet ottimizza la combustione del gasolio nei cilindri', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '148', '112013', '1196', '2', 'In un motore Diesel, il sistema d''iniezione multijet riduce l''inquinamento acustico causato dal motore', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '148', '112013', '1197', '3', 'In un motore Diesel, il sistema d''iniezione multijet riduce l''inquinamento atmosferico causato dal motore', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '148', '112013', '1198', '4', 'In un motore Diesel, il sistema d''iniezione multijet arriva fino a 8 iniezioni per ciclo di funzionamento', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '148', '112013', '1199', '5', 'In un motore Diesel, il sistema d''iniezione multijet permette una riduzione dei consumi', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '148', '112013', '1200', '6', 'In un motore Diesel, il sistema d''iniezione multijet abbassa l''inquinamento acustico causato dal motore', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '148', '112013', '1201', '7', 'In un motore Diesel, il sistema d''iniezione multijet aumenta la potenza del motore aumentandone notevolmente i consumi', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '148', '112013', '1202', '8', 'I motori che adottano il sistema d''iniezione multijet sono generalmente appartenenti alle categorie antinquinamento euro zero o euro uno', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '148', '112013', '1203', '9', 'In un motore Diesel, il sistema d''iniezione multijet è usato solo nei motori a benzina turbocompressi', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '148', '112013', '1204', '10', 'In un motore Diesel, il sistema d''iniezione multijet ha sempre due iniezioni di gasolio nel ciclo di funzionamento', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '149', '112004', '', '', 'Regolazione dell''iniezione nel motore Diesel', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '149', '112004', '1205', '1', 'Tutti i motori Diesel sono dotati di regolatore del numero di giri', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '149', '112004', '1206', '2', 'In un motore Diesel, l''iniezione del gasolio a controllo elettronico permette di regolare la quantità di gasolio iniettato ed anche la tempistica dell''iniezione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '149', '112004', '1207', '3', 'In un motore Diesel, sui veicoli dotati di iniettori pompa non è montata la pompa di iniezione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '149', '112004', '1208', '4', 'In un motore Diesel, gli iniettori pompa dei motori Diesel di ultima generazione sono a controllo elettronico', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '149', '112004', '1209', '5', 'In un motore Diesel, gli iniettori sregolati causano una cattiva combustione del gasolio', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '149', '112004', '1210', '6', 'In un motore Diesel, gli iniettori sregolati causano un maggior consumo di combustibile', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '149', '112004', '1211', '7', 'In un motore Diesel, gli iniettori sregolati causano un maggior inquinamento atmosferico', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '149', '112004', '1212', '8', 'La regolazione elettronica dell''iniezione è applicabile solo ai motori a scoppio perché il gasolio è troppo lento ad incendiarsi', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '149', '112004', '1213', '9', 'Gli iniettori pompa dei motori Diesel possono essere solo ad azionamento meccanico', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '149', '112004', '1214', '10', 'In un motore Diesel, gli iniettori sregolati causano un peggior comfort di marcia', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '149', '112004', '1215', '11', 'In un motore Diesel, gli iniettori sregolati sono segnalati da una spia verde che si accende sul cruscotto', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '149', '112004', '1216', '12', 'In un motore Diesel, la pulizia degli iniettori sregolati può avvenire marciando in discesa a motore spento per poche centinaia di metri', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '149', '112004', '1217', '13', 'In un motore Diesel, la quantità di gasolio iniettata può essere regolata manualmente dal conducente tramite manopola posta sul cruscotto', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '149', '112004', '1218', '14', 'In un motore Diesel, la pulizia degli iniettori sregolati può avvenire con motore acceso, veicolo in folle e acceleratore premuto a fondo per una decina di secondi', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '150', '112010', '', '', 'Sistema di iniezione Common-rail 2', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '150', '112010', '1219', '1', 'L''iniezione gestita dall''impianto Common-rail fornisce una elevatissima pressione del gasolio, anche a bassi giri del motore', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '150', '112010', '1220', '2', 'L''iniezione gestita dall''impianto Common-rail comporta un''elevatissima pressione del gasolio all''interno di un condotto comune (Rail)', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '150', '112010', '1221', '3', 'In un motore Diesel, l''iniezione gestita dall''impianto Common-rail permette una riduzione dei consumi e delle emissioni nocive', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '150', '112010', '1222', '4', 'Il Common-rail è un impianto di iniezione del gasolio di tipo puramente meccanico', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '150', '112010', '1223', '5', 'In un motore Diesel, l''alimentazione gestita dall''impianto Common-rail determina un''iniezione di gasolio a bassa pressione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '150', '112010', '1224', '6', 'L''iniezione gestita dall''impianto Common-rail penalizza la coppia motrice ai bassi regimi di giri', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '151', '112011', '', '', 'Sistema di iniezione Common-rail 1', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '151', '112011', '1225', '1', 'In un motore Diesel, il sistema di iniezione Common-rail è dotato di due circuiti del combustibile: uno a bassa ed uno ad alta pressione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '151', '112011', '1226', '2', 'In un motore Diesel, il sistema di iniezione Common-rail è dotato di una pompa che invia il gasolio ad elevatissima pressione agli elettroiniettori', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '151', '112011', '1227', '3', 'In un motore Diesel, il sistema di iniezione Common-rail ha un condotto comune (Common-rail o flauto) che contiene il combustibile inviato dalla pompa ad alta pressione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '151', '112011', '1228', '4', 'In un motore Diesel, il sistema di iniezione Common-rail è dotato di una centralina che elabora i dati dei sensori per gestire al meglio gli elettroiniettori', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '151', '112011', '1229', '5', 'In un motore Diesel, il sistema di iniezione Common-rail permette, per mezzo degli elettroiniettori, un''iniezione di carburante estremamente precisa nella quantità e nei tempi', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '151', '112011', '1230', '6', 'In un motore Diesel, il sistema di iniezione Common-rail è dotato di una pompa a bassa pressione che invia direttamente il carburante agli elettroiniettori', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '151', '112011', '1231', '7', 'In un motore Diesel, il sistema di iniezione Common-rail è dotato di una pompa a bassa pressione che determina la quantità di carburante da iniettare', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '151', '112011', '1232', '8', 'In un motore Diesel, il sistema di iniezione Common-rail sfrutta l''aria compressa per la gestione degli iniettori', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '151', '112011', '1233', '9', 'In un motore Diesel, il sistema di iniezione Common-rail permette di aumentare la pressione di iniezione al variare del numero dei giri', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '151', '112011', '1234', '10', 'Il sistema di iniezione Common-rail inietta il combustibile nel condotto di aspirazione per ottenere una miglior miscelazione del gasolio con l''aria', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '152', '111017-111001', '', '', 'Turbocompressore', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '152', '111017-111001', '1235', '1', 'In un motore a gasolio, il turbocompressore, sfruttando i gas di scarico, consente di aumentare la quantità di aria immessa nella camera di combustione al fine di migliorare la combustione ed aumentare la potenza erogata, a parità di consumo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '152', '111017-111001', '1236', '2', 'A parità di potenza erogata, un motore turbocompresso consuma meno combustibile', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '152', '111017-111001', '1237', '3', 'I motori turbocompressi consentono di recuperare parte dell’energia che va normalmente dispersa attraverso i gas di scarico', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '152', '111017-111001', '1238', '4', 'La sovralimentazione aumenta la densità dell’aria immessa nei cilindri', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '152', '111017-111001', '1239', '5', 'Compito dell’intercooler è quello di abbassare la temperatua dell’aria in uscita dal turbocompressore, in modo da aumentare il rendimento del motore', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '152', '111017-111001', '1240', '6', 'I motori dotati di turbocompressore necessitano di un doppio silenziatore (marmitta potenziata)', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '152', '111017-111001', '1241', '7', 'Il turbocompressore aumenta la potenza del motore, ma ne peggiora il rendimento', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '152', '111017-111001', '1242', '8', 'Il turbocompressore aumenta la potenza del motore, ma ne aumenta il consumo specifico', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '152', '111017-111001', '1243', '9', 'Compito dell’intercooler è quello di abbassare la temperatura del liquido di raffreddamento del motore, soprattutto nelle zone con climi caldi', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '152', '111017-111001', '1244', '10', 'Il turbocompressore può essere utilizzato su motori di piccola cilindrata (massimo 2000 cm3)', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '152', '111017-111001', '1245', '11', 'I motori Diesel non possono essere dotati di turbocompressore perché il gasolio è poco infiammabile', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '153', '111018', '', '', 'Organi di scarico', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '153', '111018', '1246', '1', 'Compito degli organi di scarico di un veicolo è quello di convogliare i gas di scarico verso l''esterno', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '153', '111018', '1247', '2', 'Compito degli organi di scarico di un veicolo è quello di abbassare la temperatura dei gas di scarico convogliati verso l''esterno', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '153', '111018', '1248', '3', 'Compito degli organi di scarico di un veicolo è quello di abbassare la pressione dei gas di scarico convogliati verso l''esterno, in modo da ridurre il rumore', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '153', '111018', '1249', '4', 'La marmitta deve essere sempre sostituita quando presenta crepe o rotture, anche se piccole', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '153', '111018', '1250', '5', 'Se la marmitta deve essere sostituita, quella nuova deve sempre essere di tipo omologato per lo specifico veicolo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '153', '111018', '1251', '6', 'Se la marmitta deve essere sostituita, quella nuova può essere di qualunque tipo, purché la sua lunghezza sia uguale o inferiore a quella vecchia', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '153', '111018', '1252', '7', 'La marmitta deve essere sostituita a ogni revisione del veicolo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '153', '111018', '1253', '8', 'In un motore Diesel, la marmitta deve essere sostituita ogni due anni o ogni 25000 chilometri, perché il nerofumo tende a intasarla', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '153', '111018', '1254', '9', 'La marmitta forata può essere riparata con del silicone sigillante, senza che sia necessario sostituirla', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '154', '121001', '', '', 'Olio lubrificante e impianto di lubrificazione 1', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '154', '121001', '1255', '1', 'L''impianto di lubrificazione serve a diminuire l''attrito tra gli organi in movimento del motore e ad evitare il surriscaldamento e l''eventuale grippaggio degli stessi', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '154', '121001', '1256', '2', 'In un impianto di lubrificazione, la pompa pesca l''olio dalla coppa e lo invia, tramite apposite scanalature, alle varie parti in movimento del motore', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '154', '121001', '1257', '3', 'La lubrificazione evita che lo scorrimento tra le superfici a contatto usuri i materiali a contatto', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '154', '121001', '1258', '4', 'La lubrificazione viene garantita dalla presenza di un velo di olio lubrificante tra le superfici', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '154', '121001', '1259', '5', 'L''impianto di lubrificazione può essere del tipo a circolazione forzata oppure a circolazione naturale', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '154', '121001', '1260', '6', 'La pompa di iniezione fa parte del circuito di lubrificazione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '154', '121001', '1261', '7', 'Compito della lubrificazione è aumentare l''attrito, altrimenti le parti meccaniche non potrebbero esercitare la loro funzione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '154', '121001', '1262', '8', 'Un olio lubrificante deve avere un alto grado di acidità per non formare morchie e patine all''interno dei cilindri', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '155', '121002/121003', '', '', 'Parti del impianto di lubrificazione', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '155', '121002/121003', '1263', '1', 'Fanno parte degli organi della lubrificazione: la coppa dell''olio, il filtro a cartuccia e l''indicatore di pressione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '155', '121002/121003', '1264', '2', 'La circolazione dell''olio di lubrificazione all''interno del motore è assicurata da una pompa a ingranaggi', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '155', '121002/121003', '1265', '3', 'Un indicatore di pressione ed una spia luminosa di colore rosso, posti sulla plancia portastrumenti, permettono di tenere sotto controllo lo stato dell''impianto di lubrificazione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '155', '121002/121003', '1266', '4', 'Sull''asta graduata per il controllo del liquido di lubrificazione sono indicati i livelli minimo e massimo che deve avere l''olio nella coppa', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '155', '121002/121003', '1267', '5', 'La pompa ad ingranaggi del sistema di lubrificazione di un motore mette in circolazione l''olio lubrificante sotto pressione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '155', '121002/121003', '1268', '6', 'Il filtro dell''olio serve a trattenere i piccoli corpi estranei in modo che non penetrino nel motore', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '155', '121002/121003', '1269', '7', 'Il manometro posto sul cruscotto indica la quantità di olio contenuto dalla coppa', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '155', '121002/121003', '1270', '8', 'I filtri dell''olio di un veicolo pesante sono tre: uno a cartuccia, uno a carboni attivi, uno a rete metallica', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '155', '121002/121003', '1271', '9', 'La coppa dell''olio contiene un dispositivo per raffreddare l''olio', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '155', '121002/121003', '1272', '10', 'La pompa ad ingranaggi del sistema di lubrificazione di un motore è azionata da un alberino che prende il movimento dal volano motore', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '155', '121002/121003', '1273', '11', 'Esistono due tipi di filtro dell''olio: uno per la stagione invernale e uno per quella estiva', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '156', '121002/121003', '', '', 'Carateristiche dell''olio lubrificante', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '156', '121002/121003', '1274', '1', 'Fanno parte degli organi della lubrificazione: la coppa dell''olio, il filtro a cartuccia e l''indicatore di pressione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '156', '121002/121003', '1275', '2', 'L''olio multigrado è più stabile nei cambiamenti di temperatura ambiente (estate-inverno o inverno-estate)', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '156', '121002/121003', '1276', '3', 'È bene non usare nei motori Diesel olio specifico per motori a scoppio e viceversa', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '156', '121002/121003', '1277', '4', 'Gli olii lubrificanti possono contenere additivi chimici', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '156', '121002/121003', '1278', '5', 'Gli olii lubrificanti possono essere minerali o sintetici', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '156', '121002/121003', '1279', '6', 'Una delle principali trasformazioni che subiscono gli olii lubrificanti durante l''uso è il cambio di viscosità', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '156', '121002/121003', '1280', '7', 'Una delle principali trasformazioni che subiscono gli olii lubrificanti durante l''uso è l''ossidazione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '156', '121002/121003', '1281', '8', 'Una delle principali trasformazioni che subiscono gli olii lubrificanti durante l''uso è l''aumento di acidità', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '156', '121002/121003', '1282', '9', 'L''olio motore ha una viscosità che varia in funzione della sua temperatura', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '156', '121002/121003', '1283', '10', 'Una delle principali trasformazioni che subiscono gli olii lubrificanti durante l''uso è l''aumento di salinità', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '156', '121002/121003', '1284', '11', 'Per raffreddare l''olio motore, una valvola di intercettazione lo fa passare nel radiatore dell''acqua quando supera i 120°C', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '156', '121002/121003', '1285', '12', 'In un motore Diesel si può usare qualunque tipo di olio, l''importante è che sia sufficientemente viscoso', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '156', '121002/121003', '1286', '13', 'In un motore Diesel è indifferente usare olii per motori a benzina, purché siano abbastanza densi', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '156', '121002/121003', '1287', '14', 'A differenza di quello di lubrificazione del motore, l''olio del cambio non si sostituisce mai perché non è soggetto a forte riscaldamento', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '156', '121002/121003', '1288', '15', 'Se la temperatura dell''aria è molto bassa, è bene aggiungere all''olio di lubrificazione un poco di benzina (non più del 5%)', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '156', '121002/121003', '1289', '16', 'Se l''olio nel motore è troppo denso, si deve rimediare aggiungendo una certa quantità di gasolio', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '156', '121002/121003', '1290', '17', 'L''olio esausto può essere diluito in almeno 25 litri di acqua distillata e poi immesso nel circuito fognario', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '156', '121002/121003', '1291', '18', 'In inverno occorre mettere liquido antigelo nell''olio', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '157', '121002/121003', '', '', 'Scopo del impianto di lubrificazione', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '157', '121002/121003', '1292', '1', 'L''impianto di lubrificazione interessa gli organi meccanici di un motore a scoppio o Diesel', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '157', '121002/121003', '1293', '2', 'Scopo della lubrificazione è quello di evitare l''eccessivo attrito di alcune parti del motore che si muovono a contatto tra loro', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '157', '121002/121003', '1294', '3', 'L''olio di lubrificazione ha anche lo scopo di raffreddare le parti metalliche che si muovono a contatto tra loro', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '157', '121002/121003', '1295', '4', 'Il lubrificante protegge dalla corrosione le parti metalliche su cui si deposita', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '157', '121002/121003', '1296', '5', 'Uno degli scopi dell''impianto di lubrificazione di un motore è quello di contribuire al raffreddamento dei vari organi in movimento', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '157', '121002/121003', '1297', '6', 'Uno degli scopi dell''impianto di lubrificazione di un motore è quello di interporre un velo d''olio tra le parti del motore e le parti in movimento', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '157', '121002/121003', '1298', '7', 'Uno degli scopi dell''impianto di lubrificazione di un motore è quello di ridurre l''usura degli organi in movimento all''interno del motore', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '157', '121002/121003', '1299', '8', 'Uno degli scopi dell''impianto di lubrificazione di un motore è quello di ridurre l''attrito delle parti in movimento del motore', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '157', '121002/121003', '1300', '9', 'Il lubrificante comincia a circolare nel motore solo dopo che la temperatura dell''acqua ha superato i 65°C', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '157', '121002/121003', '1301', '10', 'L''impianto di lubrificazione è utile per evitare il blocco degli iniettori intasati', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '157', '121002/121003', '1302', '11', 'Gli organi della lubrificazione dei motori a scoppio e Diesel hanno lo scopo, tra l''altro, di lubrificare la pompa di alimentazione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '157', '121002/121003', '1303', '12', 'È importante che l''impianto di lubrificazione provveda ad una efficace lubrificazione di tutte le cinghie', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '158', '121002/121003', '', '', 'Malfunzionamenti e soluzioni dell''impianto di lubrificazione ', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '158', '121002/121003', '1304', '1', 'Quando la pressione dell''olio è insufficiente, è necessario fermare il motore per evitare danni a varie parti del motore', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '158', '121002/121003', '1305', '2', 'Se dal tubo di scarico di un motore Diesel esce fumo chiaro, ciò può significare che il motore brucia olio che è trafilato nei cilindri', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '158', '121002/121003', '1306', '3', 'Se durante la marcia si accende la spia della lubrificazione, significa che la pressione nell''impianto è troppo bassa ed insufficiente a garantire il corretto funzionamento del motore', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '158', '121002/121003', '1307', '4', 'Se si accende la spia dell''olio, oppure il manometro segna una pressione bassa, la pompa ad ingranaggi potrebbe essere guasta o difettosa', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '158', '121002/121003', '1308', '5', 'Se si accende la spia dell''olio, oppure il manometro segna una pressione bassa, la valvola limitatrice della pressione può essere guasta o il filtro può essere intasato', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '158', '121002/121003', '1309', '6', 'Se si accende la spia dell''olio, oppure il manometro segna una pressione bassa, la quantità d''olio presente nel circuito potrebbe essere troppo scarsa', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '158', '121002/121003', '1310', '7', 'Generalmente, ad ogni cambio dell''olio è bene sostituire anche la cartuccia del relativo filtro', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '158', '121002/121003', '1311', '8', 'È consigliabile sostituire l''olio per la lubrificazione del motore secondo le tempistiche e percorrenze raccomandate dal costruttore del motore stesso', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '158', '121002/121003', '1312', '9', 'L''olio esausto deve essere conferito (portato) ad un soggetto appartenente ad uno specifico consorzio', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '158', '121002/121003', '1313', '10', 'L''accensione della spia dell''olio sul cruscotto del veicolo segnala che la pressione dell''olio è insufficiente', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '158', '121002/121003', '1314', '11', 'L''accensione della spia dell''olio può essere causata dalla rottura della pompa del lubrificante', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '158', '121002/121003', '1315', '12', 'L''accensione della spia dell''olio può essere causata dalle tubazioni dell''impianto di lubrificazione rotte o intasate', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '158', '121002/121003', '1316', '13', 'La spia rossa dell''olio sul cruscotto può segnalare che nella coppa è rimasto così poco olio che la pompa non riesce a mantenerlo in pressione in tutto il circuito', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '158', '121002/121003', '1317', '14', 'Il consumo dell''olio motore aumenta con l''usura delle fasce elastiche', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '158', '121002/121003', '1318', '15', 'Se dal tubo di scarico di un motore Diesel esce fumo nero, significa che sta bruciando olio', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '158', '121002/121003', '1319', '16', 'Se il filtro dell''olio è sporco, dal tubo di scarico esce fumo rossastro', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '158', '121002/121003', '1320', '17', 'Se il motore consuma molto olio significa che nei cilindri si ha troppa pressione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '158', '121002/121003', '1321', '18', 'Se si accende la spia della lubrificazione, significa che l''olio è alla corretta temperatura', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '158', '121002/121003', '1322', '19', 'Se si accende la spia della lubrificazione, significa che bisogna cambiare l''olio entro cinquemila chilometri', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '158', '121002/121003', '1323', '20', 'La cartuccia del filtro dell''olio va sostituita solo quando si accende la spia rossa sul cruscotto', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '158', '121002/121003', '1324', '21', 'L''accensione della spia dell''olio sul cruscotto del veicolo, segnala che si è rabboccato l''olio con uno di tipo diverso da quello precedentemente utilizzato', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '158', '121002/121003', '1325', '22', 'La spia della pressione dell''olio posta sul cruscotto, se accesa, indica sempre un basso livello dell''olio motore', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '159', '121002/121003', '', '', 'Manutenzione dell''impianto di lubrificazione ', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '159', '121002/121003', '1326', '1', 'L''olio della lubrificazione deve venire sostituito con la periodicità indicata sul libretto d''uso e manutenzione del veicolo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '159', '121002/121003', '1327', '2', 'L''olio della lubrificazione deve essere cambiato periodicamente in base ai chilometri percorsi e alle indicazioni del costruttore del veicolo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '159', '121002/121003', '1328', '3', 'L''olio lubrificante del cambio di velocità deve venire periodicamente sostituito', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '159', '121002/121003', '1329', '4', 'È meglio non rabboccare l''olio nella coppa con olio di tipo e caratteristiche diverse da quello già contenuto', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '159', '121002/121003', '1330', '5', 'Negli intervalli tra una sostituzione e l''altra del lubrificante, il suo livello va controllato ad intervalli regolari e rabboccato in caso di necessità', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '159', '121002/121003', '1331', '6', 'Il controllo del livello dell''olio motore va sempre fatto a motore spento da parecchi minuti', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '159', '121002/121003', '1332', '7', 'Il controllo del livello dell''olio motore va sempre fatto con il veicolo in piano', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '159', '121002/121003', '1333', '8', 'La spia luminosa della lubrificazione si accende quando la coppa dell''olio è vuota', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '159', '121002/121003', '1334', '9', 'La spia luminosa della lubrificazione si accende quando la temperatura dell''olio supera i 100°C', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '159', '121002/121003', '1335', '10', 'Una pressione troppo elevata dell''olio di lubrificazione del motore può essere causata dall''usura degli ingranaggi della pompa dell''olio', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '159', '121002/121003', '1336', '11', 'È buona regola scaricare periodicamente l''olio dal motore, filtrarlo con un panno pulito e, successivamente, rimetterlo nella coppa', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '159', '121002/121003', '1337', '12', 'Il rabbocco di olio lubrificante in un motore Diesel non è consentito, se il livello è basso occorre sostituirlo completamente', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '159', '121002/121003', '1338', '13', 'L''olio esausto deve generalmente essere bruciato in un luogo lontano dal veicolo e dai centri abitati', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '159', '121002/121003', '1339', '14', 'Il livello dell''olio lubrificante del motore si controlla grazie a un manometro installato sul cruscotto del veicolo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '159', '121002/121003', '1340', '15', 'L''olio di lubrificazione di un motore Diesel deve essere sostituito solo quando è diventato molto scuro', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '159', '121002/121003', '1341', '16', 'Quando l''olio motore è troppo denso è bene aggiungere olio vegetale', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '159', '121002/121003', '1342', '17', 'L''olio di lubrificazione deve essere sostituito ogni due mesi', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '159', '121002/121003', '1343', '18', 'L''olio lubrificante del cambio e del differenziale devono essere sostituiti ad ogni cambio di olio del motore', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '159', '121002/121003', '1344', '19', 'È necessario controllare periodicamente il livello dell''olio tramite l''apposita asta graduata, ma non prima di aver lasciato il motore acceso per almeno 15 minuti', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '159', '121002/121003', '1345', '20', 'Il controllo periodico del livello dell''olio lubrificante può essere effettuato solo presso un''officina specializzata', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '159', '121002/121003', '1346', '21', 'Il controllo periodico del livello dell''olio lubrificante può essere effettuato solo se il veicolo ha la parte anteriore più in alto rispetto al retrotreno', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '160', '122003', '', '', 'Impianto di raffreddamento del motore 1', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '160', '122003', '1347', '1', 'Gli organi di raffreddamento dei motori a scoppio e Diesel hanno lo scopo di evitare che le parti del motore si riscaldino eccessivamente', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '160', '122003', '1348', '2', 'Gli organi di raffreddamento dei motori a scoppio e Diesel sono costituiti, tra l''altro, dal radiatore, dalla ventola e dalla pompa centrifuga', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '160', '122003', '1349', '3', 'La temperatura troppo elevata del liquido refrigerante di un motore può essere dovuta alla formazione di incrostazioni nei tubetti del radiatore', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '160', '122003', '1350', '4', 'L''elettroventilatore dell''impianto di raffreddamento è dotato di sistema di innesto elettromagnetico comandato da un interruttore termostatico', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '160', '122003', '1351', '5', 'In un impianto di raffreddamento a liquido refrigerante, il termostato regola il passaggio del liquido dalle intercapedini della testata al radiatore', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '160', '122003', '1352', '6', 'In caso di malfunzionamento del termostato dell''impianto e surriscaldamento del liquido di raffreddamento, è sufficiente procedere alla rimozione del termostato stesso', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '160', '122003', '1353', '7', 'Il surriscaldamento del motore ha come principale effetto quello di aumentare le emissioni inquinanti del motore', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '160', '122003', '1354', '8', 'Il surriscaldamento del motore ha come principale effetto quello di aumentare i consumi di carburante', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '160', '122003', '1355', '9', 'Il surriscaldamento del motore ha come principale effetto quello di rendere impossibile l''uso dell''impianto di condizionamento dell''aria', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '160', '122003', '1356', '10', 'Sul cruscotto si trova un apposito manometro che indica il valore della pressione del liquido di raffreddamento nel radiatore', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '160', '122003', '1357', '11', 'Nei veicoli pesanti, la temperatura del liquido refrigerante non deve mai superare gli 80°C', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '160', '122003', '1358', '12', 'Per limitare i consumi è bene che il motore funzioni sempre a basse temperature (massimo 60°C)', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '161', '122001', '', '', 'Impianto di raffreddamento del motore 2', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '161', '122001', '1359', '1', 'In un impianto di raffreddamento a liquido, il liquido si riscalda circolando nelle intercapedini ricavate nel monoblocco e nella testata intorno ai cilindri', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '161', '122001', '1360', '2', 'In un impianto di raffreddamento a liquido, il liquido cede il calore sottratto al motore all''atmosfera tramite il radiatore', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '161', '122001', '1361', '3', 'Il liquido di raffreddamento deve avere una bassa temperatura di congelamento, un''alta temperatura di ebollizione e proprietà anticorrosive', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '161', '122001', '1362', '4', 'Un eccessivo riscaldamento del liquido di raffreddamento del motore può essere dovuto alla valvola termostatica malfunzionante', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '161', '122001', '1363', '5', 'Il raffreddamento del motore evita che una temperatura eccessivamente elevata danneggi le varie parti del motore', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '161', '122001', '1364', '6', 'Il circuito di raffreddamento è costituito in modo tale da portare l''acqua del circuito a una temperatura che può anche essere più alta di 100°C', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '161', '122001', '1365', '7', 'Nei climi freddi è indispensabile mettere anticongelante nel circuito di raffreddamento', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '161', '122001', '1366', '8', 'Normalmente il liquido di raffreddamento del motore è acqua distillata', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '161', '122001', '1367', '9', 'In un impianto di raffreddamento a liquido, la testata del motore è dotata di alette per aumentarne la superficie e favorire lo scambio termico con il liquido', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '161', '122001', '1368', '10', 'Il liquido di raffreddamento deve avere una bassa temperatura di congelamento e una bassa temperatura di ebollizione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '161', '122001', '1369', '11', 'Negli impianti di raffreddamento dei veicoli moderni non è mai necessario verificare il livello del liquido in quanto l''impianto stesso è sigillato', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '161', '122001', '1370', '12', 'Negli impianti di raffreddamento dei veicoli moderni non è consentito utilizzare l''acqua in sostituzione del liquido refrigerante', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '161', '122001', '1371', '13', 'In caso di elevata temperatura del circuito, è buona norma aprire immediatamente il tappo del radiatore', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '161', '122001', '1372', '14', 'La ventola del radiatore entra in funzione per evitare il congelamento dell''acqua', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '161', '122001', '1373', '15', 'In caso di elevata temperatura del circuito di raffreddamento, è buona norma buttare un secchio di acqua molto fredda sul motore', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '162', '122004', '', '', 'Radiatore', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '162', '122004', '1374', '1', 'Il radiatore serve a smaltire, grazie al passaggio forzato di aria, il calore in eccesso prodotto dal funzionamento del motore', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '162', '122004', '1375', '2', 'Il radiatore è uno scambiatore di calore acqua-aria', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '162', '122004', '1376', '3', 'Il radiatore è l''elemento che permette che il liquido del circuito di raffreddamento non superi una temperatura dannosa per il motore', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '162', '122004', '1377', '4', 'Non si deve aggiungere acqua o liquido freddo nel radiatore quando il motore è molto caldo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '162', '122004', '1378', '5', 'In estate è bene sostituire il liquido refrigerante con acqua perché si evitano possibili surriscaldamenti del motore', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '162', '122004', '1379', '6', 'Il radiatore serve a riscaldare l''abitacolo del veicolo utilizzando il calore prodotto dal funzionamento del motore', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '162', '122004', '1380', '7', 'Se si prevede di circolare con temperature molto basse (-10°C) occorre sostituire il liquido refrigerante con alcool etilico', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '163', '122005', '', '', 'Impianto di raffreddamento del motore 4', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '163', '122005', '1381', '1', 'Il ventilatore dell''impianto di raffreddamento serve ad accelerare il raffreddamento del liquido che passa dal radiatore', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '163', '122005', '1382', '2', 'Il ventilatore dell''impianto di raffreddamento si ferma automaticamente quando la temperatura del liquido di raffreddamento si abbassa oltre una determinata temperatura', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '163', '122005', '1383', '3', 'Il ventilatore dell''impianto di raffreddamento si attiva automaticamente quando la temperatura del liquido di raffreddamento supera una determinata temperatura', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '163', '122005', '1384', '4', 'Il liquido di raffreddamento viene messo in circolazione da una pompa centrifuga', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '163', '122005', '1385', '5', 'Il liquido dell''impianto di raffreddamento può essere sostituito con acqua, in caso di urgente necessità', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '163', '122005', '1386', '6', 'Il liquido dell''impianto di raffreddamento ha un''alta temperatura di ebollizione (oltre 100°C) e una bassa temperatura di congelamento (meno di 0°C)', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '163', '122005', '1387', '7', 'Il corretto funzionamento dell''impianto di raffreddamento si controlla grazie a una spia luminosa e a un termometro posti sulla plancia portastrumenti del veicolo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '163', '122005', '1388', '8', 'Il termostato dell''impianto di raffreddamento serve a regolare il riscaldamento del motore mantenendo la sua temperatura entro un intervallo di temperature ottimale', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '163', '122005', '1389', '9', 'Il ventilatore dell''impianto di raffreddamento funziona solamente quando il veicolo è fermo con il motore in moto', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '163', '122005', '1390', '10', 'Il ventilatore dell''impianto di raffreddamento viene attivato dal conducente per mezzo di un interruttore che si trova in cabina', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '163', '122005', '1391', '11', 'Quando il ventilatore dell''impianto di raffreddamento viene attivato, si disattiva contemporaneamente il compressore dell''impianto di condizionamento dell''aria dell''abitacolo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '163', '122005', '1392', '12', 'Il ventilatore dell''impianto di raffreddamento non può entrare in funzione se la temperatura dell''aria esterna è inferiore a 0°C', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '163', '122005', '1393', '13', 'Il ventilatore dell''impianto di raffreddamento non può entrare in funzione se la velocità di marcia del veicolo è superiore a 80 km/h', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '163', '122005', '1394', '14', 'Il ventilatore dell''impianto di raffreddamento viene spento nelle lunghe discese', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '163', '122005', '1395', '15', 'Il termostato dell''impianto di raffreddamento può essere attivato dal conducente per mezzo di un interruttore che si trova sul cruscotto', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '164', '122002', '', '', 'Malfunzionamenti dell''impianto di raffenddamento del motore', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '164', '122002', '1396', '1', 'Se la temperatura del liquido di raffreddamento si alza troppo, si accende una spia rossa sulla plancia portastrumenti', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '164', '122002', '1397', '2', 'Se la temperatura del liquido di raffreddamento si alza troppo, ciò può dipendere dal termostato guasto', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '164', '122002', '1398', '3', 'Se la temperatura del liquido di raffreddamento si alza troppo, ciò può dipendere dall''elettroventola guasta', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '164', '122002', '1399', '4', 'Se la temperatura del liquido di raffreddamento si alza troppo, ciò può dipendere dalla cinghia del ventilatore allentata o rotta', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '164', '122002', '1400', '5', 'Se la temperatura del liquido di raffreddamento si alza troppo, ciò può dipendere dal radiatore intasato all''interno o sporco e incrostato all''esterno', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '164', '122002', '1401', '6', 'Se il motore è molto caldo, è pericoloso togliere in quel momento il tappo del radiatore o del vaso di espansione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '164', '122002', '1402', '7', 'I motori a scoppio e Diesel vengono raffreddati, durante il loro funzionamento, mediante una circolazione di aria o di liquido', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '164', '122002', '1403', '8', 'Se la temperatura del motore si alza troppo, ciò può dipendere da una sua cattiva lubrificazione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '164', '122002', '1404', '9', 'Se la temperatura del liquido di raffreddamento si alza troppo, ciò può dipendere da scarsità di liquido refrigerante nel serbatoio', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '164', '122002', '1405', '10', 'Se il motore è troppo caldo, è bene togliere subito il tappo dal radiatore per facilitare il raffreddamento del liquido', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '164', '122002', '1406', '11', 'Quando la temperatura del liquido del raffreddamento è troppo elevata, si spegne la spia luminosa che si trova sul cruscotto', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '164', '122002', '1407', '12', 'Se la temperatura del liquido di raffreddamento si alza troppo, ciò dipende esclusivamente dalla condotta di guida del conducente', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '164', '122002', '1408', '13', 'Se la temperatura del liquido di raffreddamento si alza troppo, la soluzione più efficace per ristabilire i valori normali è quella di circolare con il cofano motore socchiuso', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '164', '122002', '1409', '14', 'Se la temperatura del liquido di raffreddamento si alza troppo, l''unica soluzione efficace è quella di circolare a non più di 40 km/h', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '164', '122002', '1410', '15', 'Se la temperatura del liquido di raffreddamento si alza troppo, è bene controllare che la frizione non abbia giochi eccessivi', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '164', '122002', '1411', '16', 'Nei motori dei grandi autocarri, è più frequente il sistema di raffreddamento ad aria forzata che non quello a circolazione di liquido', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '164', '122002', '1412', '17', 'Se la temperatura del liquido di raffreddamento si alza troppo, è necessario introdurre subito del ghiaccio nel vaso di espansione dell''impianto di raffreddamento', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '164', '122002', '1413', '18', 'Se il motore si riscalda troppo, è necessario utilizzare olio lubrificante più fluido', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '165', '114005', '', '', 'Organi di accensione', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '165', '114005', '1414', '1', 'Gli organi di accensione di un motore a scoppio hanno il compito di produrre una scintilla elettrica per incendiare la miscela aria-benzina', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '165', '114005', '1415', '2', 'Gli organi di accensione di un motore Diesel hanno il compito di produrre una scintilla elettrica per incendiare il gasolio', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '165', '114005', '1416', '3', 'Nel motore Diesel ogni cilindro necessita di due candele di accensione, perché il gasolio brucia meno rapidamente della benzina', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '165', '114005', '1417', '4', 'Le candele dei motori Diesel devono essere più resistenti al calore di quelle dei motori a scoppio, a causa della maggior temperatura di funzionamento del motore', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '166', '114002', '', '', 'Alternatore 1', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '166', '114002', '1418', '1', 'In molti casi la stessa cinghia trapezoidale che comanda l''alternatore mette in movimento anche la pompa centrifuga del liquido di raffreddamento', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '166', '114002', '1419', '2', 'L''alternatore viene azionato da una o più cinghie trapezoidali', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '166', '114002', '1420', '3', 'L''alternatore è un generatore di corrente alternata', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '166', '114002', '1421', '4', 'Se l''alternatore è guasto si accende una spia rossa situata sul cruscotto', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '166', '114002', '1422', '5', 'Se l''alternatore genera poca corrente è possibile che la cinghia trapezoidale si sia allentata', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '166', '114002', '1423', '6', 'La corrente generata dall''alternatore, per essere accumulata dalla batteria, deve venire raddrizzata, cioè trasformata in corrente continua', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '166', '114002', '1424', '7', 'Il buon funzionamento dell''alternatore si controlla per mezzo di una spia luminosa posta sul cruscotto, che deve rimanere spenta', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '166', '114002', '1425', '8', 'I moderni alternatori hanno incorporato un raddrizzatore che rende continua la corrente in uscita', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '166', '114002', '1426', '9', 'L''autista deve verificare periodicamente lo stato di carica dell''alternatore', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '166', '114002', '1427', '10', 'Alcuni motori Diesel sono privi di alternatore', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '166', '114002', '1428', '11', 'Nei motori a scoppio o Diesel, l''alternatore sostituisce la batteria', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '166', '114002', '1429', '12', 'Nei motori a scoppio o Diesel, l''alternatore fornisce la corrente per il motore, mentre la batteria produce quella per le luci e per i servizi', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '166', '114002', '1430', '13', 'Se l''alternatore è guasto si accende una spia arancione situata sul cruscotto', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '166', '114002', '1431', '14', 'Quando l''alternatore si guasta, il motore si spegne di colpo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '166', '114002', '1432', '15', 'L''alternatore fornisce corrente alternativamente al motore o alla batteria', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '166', '114002', '1433', '16', 'Mediante un pulsante situato sul cruscotto, l''autista decide se l''alternatore deve fornire corrente al motore o alla batteria', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '166', '114002', '1434', '17', 'L''alternatore funziona anche a motore spento', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '167', '114006', '', '', 'Alternatore 2', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '167', '114006', '1435', '1', 'L''alternatore è un dispositivo che trasforma energia meccanica in energia elettrica sotto forma di corrente alternata', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '167', '114006', '1436', '2', 'L''accensione della spia sul cruscotto raffigurante una batteria indica che bisogna immediatamente fermarsi per evitare danni all''impianto elettrico', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '168', '114004', '', '', 'Batteria', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '168', '114004', '1437', '1', 'La batteria viene mantenuta carica dall''alternatore', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '168', '114004', '1438', '2', 'La corrente erogata dalla batteria è sempre continua', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '168', '114004', '1439', '3', 'La corrente erogata dalla batteria è a bassa tensione ed è continua', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '168', '114004', '1440', '4', 'Alcune batterie richiedono periodicamente verifica ed eventuale aggiunta di acqua distillata', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '168', '114004', '1441', '5', 'Molte batterie non richiedono particolare manutenzione (batterie sigillate) salvo mantenerle pulite, asciutte e con i morsetti ingrassati', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '168', '114004', '1442', '6', 'La batteria deve essere tenuta pulita, asciutta e sui morsetti deve essere periodicamente messo un apposito grasso', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '168', '114004', '1443', '7', 'Il peso delle batterie incide negativamente sulle prestazioni di un autoveicolo con motore elettrico', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '168', '114004', '1444', '8', 'L''impianto elettrico di un veicolo pesante funziona generalmente con tensione di 24 volts', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '168', '114004', '1445', '9', 'La batteria dell''impianto elettrico di un autoveicolo è un generatore di corrente alternata a bassa tensione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '168', '114004', '1446', '10', 'Quando l''alternatore si guasta, la batteria si scarica di colpo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '168', '114004', '1447', '11', 'In caso di emergenza, negli elementi della batteria può essere aggiunto liquido antigelo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '168', '114004', '1448', '12', 'L''acqua distillata che si aggiunge nella batteria deve avere temperatura superiore ai 25°', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '168', '114004', '1449', '13', 'Non si può aggiungere acqua distillata alla batteria se il motore è ancora caldo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '168', '114004', '1450', '14', 'Se l''alternatore non funziona, per alcuni chilometri si possono collegare direttamente tra loro i due poli della batteria', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '168', '114004', '1451', '15', 'La batteria fornisce corrente al solo motore', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '168', '114004', '1452', '16', 'La batteria eroga corrente quando il motore è in funzione, mentre si stacca automaticamente a motore fermo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '168', '114004', '1453', '17', 'La batteria dell''impianto elettrico di un autoveicolo è un accumulatore di corrente alternata a bassa tensione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '169', '114003', '', '', 'Motorino di avviamento', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '169', '114003', '1454', '1', 'Sia il motore a scoppio che quello Diesel sono dotati del motorino d''avviamento', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '169', '114003', '1455', '2', 'Il motorino d''avviamento viene alimentato dalla corrente fornita dalla batteria', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '169', '114003', '1456', '3', 'Se il motorino di avviamento non funziona, ciò può dipendere dal fatto che la batteria è scarica', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '169', '114003', '1457', '4', 'Se il motorino di avviamento funziona, ma il motore non gira, può darsi che il pignoncino o il suo cuscinetto reggi-spinta si siano rotti', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '169', '114003', '1458', '5', 'Su alcuni veicoli a motore Diesel, il motorino di avviamento è alimentato dal gasolio proveniente dal serbatoio', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '169', '114003', '1459', '6', 'In caso di emergenza, il motorino d''avviamento può essere azionato con apposita manovella', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '169', '114003', '1460', '7', 'Il motorino d''avviamento non è presente sugli autoveicoli con cambio automatico', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '169', '114003', '1461', '8', 'Il motorino d''avviamento può essere utilizzato facendolo girare al contrario, per potenziare l''effetto frenante del motore', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '170', '114001', '', '', 'Fusibili', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '170', '114001', '1462', '1', 'Non è opportuno sostituire un fusibile con uno di amperaggio diverso', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '170', '114001', '1463', '2', 'Tutti i fusibili sono raccolti in uno o più contenitori facilmente accessibil i', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '170', '114001', '1464', '3', 'Ogni fusibile è distinto dagli altri per il colore e per il numero scritto sulla sua parte in plastica', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '170', '114001', '1465', '4', 'Il fusibile è un dispositivo in grado di interrompere il flusso di corrente a un apparato se questa supera una soglia prefissata', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '170', '114001', '1466', '5', 'I fusibili sono costituiti da un contenitore al cui interno è presente un filo metallico dimensionato in modo tale da fondere se la corrente che lo attraversa supera un valore limite', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '170', '114001', '1467', '6', 'La posizione della scatola dei fusibili è indicata sul libretto di uso e manutenzione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '170', '114001', '1468', '7', 'La parte isolante dei fusibili ha in genere colori differenti a seconda del loro amperaggio', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '170', '114001', '1469', '8', 'Sui fusibili si trova scritto il valore del loro amperaggio', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '170', '114001', '1470', '9', 'Il valore dell''amperaggio di un fusibile è un numero seguito dalla lettera A maiuscola', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '170', '114001', '1471', '10', 'L''impianto elettrico di un autoveicolo è suddiviso in sezioni, ciascuna protetta dal proprio fusibile', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '170', '114001', '1472', '11', 'I fusibili sono sostituibili solo in officina', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '170', '114001', '1473', '12', 'Se non si dispone di un fusibile sostitutivo uguale, occorre sostituire quello guasto con uno di amperaggio minore', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '170', '114001', '1474', '13', 'Tutti i fusibili vanno sostituiti ogni volta che si effettua la revisione periodica del veicolo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '170', '114001', '1475', '14', 'I fusibili di colore rosso possono essere sostituiti solo da addetti delle officine specializzate', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '170', '114001', '1476', '15', 'Se lo stesso fusibile brucia spesso è bene sostituirlo con uno di amperaggio inferiore', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '170', '114001', '1477', '16', 'Se lo stesso fusibile brucia spesso è bene sostituirlo con un cavallotto di filo elettrico', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '170', '114001', '1478', '17', 'La sostituzione di un fusibile va fatta con la chiave del quadro inserita, in modo da vedere subito se si è operato correttamente', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '170', '114001', '1479', '18', 'I circuiti elettrici dell''autoveicolo sono raramente protetti da un fusibile', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '170', '114001', '1480', '19', 'Il fusibile è un dispositivo idraulico che è in grado di proteggere l''impianto di lubrificazione da eventuali sovrappressioni', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '170', '114001', '1481', '20', 'Sui fusibili si trova scritto il valore del loro voltaggio', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '170', '114001', '1482', '21', 'Il valore del voltaggio di un fusibile è un numero seguito dalla lettera V maiuscola', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '170', '114001', '1483', '22', 'Nei veicoli pesanti, tutti i fusibili hanno di norma il medesimo amperaggio', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '170', '114001', '1484', '23', 'I fusibili hanno una specifica polarità e vanno pertanto infilati nella loro sede con un verso preciso', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '170', '114001', '1485', '24', 'I veicoli dotati di motore Diesel non hanno fusibili perché non necessitano di impianto di accensione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '171', '115002', '', '', 'Organi di trasmissione 2', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '171', '115002', '1486', '1', 'In un veicolo munito di trasmissione integrale permanente è necessaria la presenza di tre differenziali', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '171', '115002', '1487', '2', 'La frizione è un dispositivo che consente all''albero motore di girare senza trasmettere il movimento agli organi di trasmissione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '171', '115002', '1488', '3', 'Gli organi della trasmissione del moto presenti negli autoveicoli sono l''insieme di organi che hanno lo scopo di trasferire la potenza generata dal motore alle ruote', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '171', '115002', '1489', '4', 'Il cambio, variando il numero di giri tra albero motore e albero di trasmissione, consente di utilizzare sempre il motore ad un regime di rotazione vicino a quello di potenza massima', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '171', '115002', '1490', '5', 'Il cambio dei veicoli elettrici ha in genere più rapporti di quello montato sui veicoli con motore a scoppio', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '171', '115002', '1491', '6', 'La frizione è un dispositivo che permette di variare il rapporto tra il numero dei giri dell''albero motore ed il numero dei giri dell''albero di trasmissione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '172', '115001', '', '', 'Organi di trasmissione 1', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '172', '115001', '1492', '1', 'Gli organi di trasmissione del moto servono a trasmettere il moto di rotazione del motore alle ruote motrici', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '172', '115001', '1493', '2', 'Fanno parte degli organi di trasmissione del moto la frizione, il cambio, il differenziale e la coppia conica', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '172', '115001', '1494', '3', 'L''albero di trasmissione non è presente se trazione e motore sono posteriori', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '172', '115001', '1495', '4', 'Sui veicoli pesanti, l''albero di trasmissione è generalmente realizzato in più tronconi collegati da giunti', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '172', '115001', '1496', '5', 'L''albero di trasmissione non è presente se la trazione è posteriore ed il motore è posizionato nella parte anteriore', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '172', '115001', '1497', '6', 'Gli organi di trasmissione del moto servono a tenere informata l''azienda sulla posizione del veicolo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '172', '115001', '1498', '7', 'Fa parte degli organi di trasmissione del moto il sistema satellitare GPS', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '172', '115001', '1499', '8', 'Fanno parte degli organi di trasmissione del moto le balestre, il volante e il limitatore di velocità', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '172', '115001', '1500', '9', ' Gli organi di trasmissione del moto non sono necessari a bordo dei veicoli elettrici', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '173', '115007', '', '', 'Tipi di trazione', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '173', '115007', '1501', '1', 'Se il veicolo è a trazione integrale, significa che tutte le ruote sono motrici', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '173', '115007', '1502', '2', 'Trazione anteriore significa che sono motrici le sole ruote di uno o più assi anteriori', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '173', '115007', '1503', '3', 'Trazione posteriore significa che sono motrici le sole ruote di uno o più assi posteriori', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '173', '115007', '1504', '4', 'In genere, i veicoli pesanti sono a trazione anteriore', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '173', '115007', '1505', '5', 'I veicoli dotati di cambio automatico sono in genere a trazione integrale', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '173', '115007', '1506', '6', 'I veicoli a trazione posteriore devono forzatamente avere motore anteriore', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '174', '115012', '', '', 'La Frizione', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '174', '115012', '1507', '1', 'Sono organi di trasmissione: la frizione, il cambio, l''albero di trasmissione ed il differenziale', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '174', '115012', '1508', '2', 'La frizione fa parte degli organi di trasmissione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '174', '115012', '1509', '3', 'La frizione fa parte degli organi di sospensione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '174', '115012', '1510', '4', 'La frizione ed il riduttore si trovano tra il cambio ed il differenziale', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '174', '115012', '1511', '5', 'I veicoli con 4 ruote motrici non necessitano di frizione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '174', '115012', '1512', '6', 'I veicoli con doppio asse sterzante sono dotati di due frizioni separate', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '175', '115015', '', '', 'La Frizione – usura e manutenzione', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '175', '115015', '1513', '1', 'Lo stato di usura della frizione dipende anche dallo stile di guida del conducente', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '175', '115015', '1514', '2', 'Il sovraccarico del veicolo usura maggiormente la frizione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '175', '115015', '1515', '3', 'Periodicamente può essere necessario registrare la corsa del pedale della frizione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '175', '115015', '1516', '4', 'Durante la marcia del veicolo la frizione è staccata dal volano', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '175', '115015', '1517', '5', 'Per mantenere efficiente la frizione è necessario sostituire l''olio dell''impianto frenante', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '175', '115015', '1518', '6', 'Per mantenere efficiente la frizione è necessario sostituire l''olio del motore', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '175', '115015', '1519', '7', 'Per mantenere efficiente la frizione è necessario controllare il livello dell''olio tramite l''apposita asta graduata', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '175', '115015', '1520', '8', 'La frizione non ha bisogno di manutenzione in quanto è autoregistrante', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '175', '115015', '1521', '9', 'Durante i mesi invernali vi è il rischio che la frizione si blocchi per formazione di ghiaccio al suo interno', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '175', '115015', '1522', '10', 'Se la frizione diventa troppo dura è bene togliere una delle sue molle di regolazione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '175', '115015', '1523', '11', 'In discesa è bene viaggiare con frizione premuta per ottenere un risparmio di carburante', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '175', '115015', '1524', '12', 'In discesa è bene viaggiare con frizione premuta per evitarne il surriscaldamento', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '175', '115015', '1525', '13', 'Per migliorare la postura di guida, il conducente deve tenere appoggiato il piede sinistro sul pedale della frizione durante la marcia', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '176', '115003', '', '', 'Frizione - automatico', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '176', '115003', '1526', '1', 'La frizione può essere di tipo automatico', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '176', '115003', '1527', '2', 'In alcuni autoveicoli il comando della frizione è automatico', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '176', '115003', '1528', '3', 'Con la frizione automatica, per ottenere la partenza del veicolo è sufficiente accelerare', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '176', '115003', '1529', '4', 'La frizione può essere comandata automaticamente tramite un controllo elettronico', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '176', '115003', '1530', '5', 'L''innesto della frizione automatica può essere di tipo elettromagnetico', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '176', '115003', '1531', '6', 'L''innesto della frizione automatica può avvenire tramite lo spostamento di masse centrifughe', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '176', '115003', '1532', '7', 'L''innesto e il disinnesto della frizione automatica può avvenire tramite lo spostamento di masse centrifughe ', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '176', '115003', '1533', '8', 'Le masse centrifughe che innestano la frizione automatica si spostano verso l''interno quando i giri del motore diminuiscono', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '176', '115003', '1534', '9', 'Le masse centrifughe che innestano la frizione automatica si spostano verso l''esterno quando i giri del motore aumentano', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '176', '115003', '1535', '10', 'Se il veicolo è dotato di frizione automatica il pedale della frizione non esiste', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '176', '115003', '1536', '11', 'Le masse centrifughe che innestano la frizione automatica si spostano verso l''interno quando i giri del motore aumentano', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '176', '115003', '1537', '12', 'Le masse centrifughe che innestano la frizione automatica si spostano verso l''esterno quando i giri del motore diminuiscono', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '176', '115003', '1538', '13', 'Le masse centrifughe che innestano la frizione automatica non si spostano quando i giri del motore diminuiscono', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '176', '115003', '1539', '14', 'Le masse centrifughe che innestano la frizione automatica non si spostano quando i giri del motore aumentano', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '176', '115003', '1540', '15', 'Un veicolo dotato di frizione automatica ha sempre una portata minore di un veicolo a frizione comandata dal conducente', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '176', '115003', '1541', '16', 'L''innesto della frizione automatica può avvenire tramite lo spostamento di leve telescopiche', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '176', '115003', '1542', '17', 'Il disinnesto della frizione automatica avviene tramite lo spostamento di giunti cardanici', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '176', '115003', '1543', '18', 'L''innesto della frizione automatica può essere solo di tipo idraulico', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '176', '115003', '1544', '19', 'Se il veicolo è dotato di frizione automatica il veicolo è privo di cambio di velocità', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '176', '115003', '1545', '20', ' I veicoli dotati di frizione automatica sono maggiormente soggetti a ribaltamento in curva', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '176', '115003', '1546', '21', 'Se il veicolo ha la frizione automatica, non è necessario provvedere alla manutenzione della frizione stessa', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '176', '115003', '1547', '22', 'Se la frizione è automatica il pedale è sostituito da un interruttore', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '176', '115003', '1548', '23', 'Se il veicolo è dotato di frizione automatica il pedale della frizione viene bloccato in posizione abbassata', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '176', '115003', '1549', '24', 'Se il veicolo è dotato di frizione automatica il pedale della frizione viene bloccato in posizione alzata', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '176', '115003', '1550', '25', 'Se il veicolo è dotato di frizione automatica il pedale della frizione non esiste e il veicolo è privo di cambio di velocità', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '176', '115003', '1551', '26', 'Nei veicoli provvisti di cambio automatico la frizione deve essere del tipo ad attrito e a disco singolo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '177', '115003', '', '', 'Slittamento del frizione  ', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '177', '115003', '1552', '1', 'Se la frizione slitta è possibile che sia entrato olio dal motore o dal cambio', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '177', '115003', '1553', '2', 'Se la frizione slitta è possibile che il materiale d''attrito del disco sia usurato', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '177', '115003', '1554', '3', 'Se la frizione slitta è possibile che il disco sia imbrattato dall''olio del servocomando', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '177', '115003', '1555', '4', 'Se la frizione slitta è possibile che sia guasto il sistema di molle dello spingi-disco', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '177', '115003', '1556', '5', 'Se la frizione slitta e non si interviene è possibile che si verifichino danni maggiori a causa del surriscaldamento di tutto il meccanismo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '177', '115003', '1557', '6', 'Per evitare lo slittamento in fase di partenza, la frizione può essere sostituita da un collegamento a ingranaggi', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '177', '115003', '1558', '7', 'Se la frizione tende a slittare bisogna circolare senza cambiare mai marcia', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '177', '115003', '1559', '8', 'Se la frizione slitta, può essere opportuno introdurvi gesso o sabbia dall''apposito sportellino', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '178', '115003', '', '', 'Frizione – disco unico e dischi multipli', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '178', '115003', '1560', '1', 'Il disco della frizione è collegato permanentemente all''albero primario del cambio', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '178', '115003', '1561', '2', 'Il ferodo presente sul disco della frizione è soggetto a usura', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '178', '115003', '1562', '3', 'Il ferodo presente sul disco della frizione può usurarsi causando lo slittamento del disco sul volano', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '178', '115003', '1563', '4', 'La frizione può essere del tipo a disco unico o a dischi multipli', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '178', '115003', '1564', '5', 'Alcuni autoveicoli sono dotati di frizione a dischi multipli', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '178', '115003', '1565', '6', 'La frizione a dischi multipli è adatta soprattutto per i veicoli di massa e/o potenza elevate', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '178', '115003', '1566', '7', 'Se la frizione è a dischi multipli può essere del tipo a bagno d''olio', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '178', '115003', '1567', '8', 'La frizione a dischi multipli può essere a secco oppure a bagno d''olio', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '178', '115003', '1568', '9', 'La frizione può essere di tipo monodisco a secco', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '178', '115003', '1569', '10', 'La frizione monodisco a secco è costituita da un grande disco di acciaio le cui superfici sono fornite di una guarnizione di attrito (ferodo)', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '178', '115003', '1570', '11', 'Se la frizione è a disco unico è normalmente del tipo a secco', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '178', '115003', '1571', '12', 'La frizione a dischi multipli viene utilizzata se il cambio di velocità ha più di 4 marce', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '178', '115003', '1572', '13', 'La frizione a dischi multipli non viene mai montata sui veicoli pesanti', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '178', '115003', '1573', '14', 'La frizione a dischi multipli può essere a secco oppure a bagno d''acqua', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '178', '115003', '1574', '15', 'La frizione a dischi multipli può essere solo a secco', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '178', '115003', '1575', '16', 'La frizione a dischi multipli può essere montata solo su veicoli dotati di cambio automatico', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '178', '115003', '1576', '17', 'La frizione può essere del tipo a dischi variabili', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '178', '115003', '1577', '18', 'Il disco della frizione è collegato permanentemente all''albero secondario del cambio', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '178', '115003', '1578', '19', 'Il disco della frizione è collegato permanentemente all''albero ausiliario del cambio', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '178', '115003', '1579', '20', 'Il disco della frizione è in acciaio spazzolato ed è privo di qualsiasi rivestimento', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '178', '115003', '1580', '21', 'Il ferodo presente sul disco della frizione non è soggetto a usura', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '178', '115003', '1581', '22', 'Il ferodo presente sul disco della frizione deve essere periodicamente ingrassato e oliato', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '178', '115003', '1582', '23', 'Il ferodo presente sul disco della frizione deve essere periodicamente pulito con benzina', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '178', '115003', '1583', '24', 'Periodicamente è necessario ingrassare il disco della frizione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '179', '115003', '', '', 'Frizione – Innestato e  disinnestata', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '179', '115003', '1584', '1', 'In alcuni autoveicoli la frizione si innesta automaticamente', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '179', '115003', '1585', '2', 'La frizione si dice innestata quando il pedale che la comanda è sollevato', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '179', '115003', '1586', '3', 'Quando il pedale della frizione è sollevato, la frizione è innestata', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '179', '115003', '1587', '4', 'La frizione deve essere innestata in maniera graduale', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '179', '115003', '1588', '5', 'Il pedale della frizione è alzato quando la frizione è innestata', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '179', '115003', '1589', '6', 'Il cambio permette di trasmettere alle ruote una forza variabile a seconda della marcia innestata', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '179', '115003', '1590', '7', 'Il disco della frizione, a frizione innestata (pedale alzato), entra in contatto col volano', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '179', '115003', '1591', '8', 'Il disco della frizione non deve mai entrare in contatto col volano', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '179', '115003', '1592', '9', 'La frizione si dice innestata quando il pedale che la comanda è abbassato', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '179', '115003', '1593', '10', 'La frizione si dice innestata quando il pedale che la comanda è premuto a fondo dal conducente', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '179', '115003', '1594', '11', 'Quando il pedale della frizione è sollevato la frizione è disinnestata', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '180', '115003', '', '', 'Frizione– spingidisco', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '180', '115003', '1595', '1', 'Il piatto spingidisco fa parte degli organi di trasmissione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '180', '115003', '1596', '2', 'Lo spingidisco fa parte dei componenti della frizione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '180', '115003', '1597', '3', 'Lo spingidisco fa parte dei componenti del differenziale', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '180', '115003', '1598', '4', 'Lo spingidisco fa parte dei componenti dei freni a tamburo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '180', '115003', '1599', '5', 'Il piatto spingidisco fa parte degli organi di sospensione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '180', '115003', '1600', '6', 'Il piatto spingidisco fa parte degli organi di frenatura', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '180', '115003', '1601', '7', 'Periodicamente è necessario allentare le molle dello spingidisco della frizione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '181', '115003', '', '', 'Il comando idraulico della frizione', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '181', '115003', '1602', '1', 'La frizione può essere ad attrito meccanico, idraulica, elettroidraulica o elettromagnetica', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '181', '115003', '1603', '2', 'Nei veicoli pesanti, il comando idraulico della frizione riduce lo sforzo del conducente', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '181', '115003', '1604', '3', 'Il comando idraulico della frizione consente di ridurre lo sforzo del conducente', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '181', '115003', '1605', '4', 'Il comando idraulico della frizione sfrutta la pressione dell''olio per ridurre lo sforzo del conducente', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '181', '115003', '1606', '5', 'Se la trasmissione è idraulica non esistono la frizione meccanica e il relativo pedale', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '181', '115003', '1607', '6', 'In molti autocarri e autobus di media portata, il pedale della frizione agisce su un servocomando a funzionamento idraulico', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '181', '115003', '1608', '7', 'Un veicolo provvisto di frizione a comando pneumo-idraulico deve essere lasciato in sosta col cambio con la prima marcia inserita', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '181', '115003', '1609', '8', 'Non esistono frizioni comandate automaticamente', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '181', '115003', '1610', '9', 'Il comando idraulico della frizione migliora la stabilità del veicolo in curva', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '181', '115003', '1611', '10', 'Il comando idraulico della frizione sfrutta la pressione dell''aria per ridurre lo sforzo del conducente', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '181', '115003', '1612', '11', 'Il comando idraulico della frizione sfrutta la pressione dell''olio motore per ridurre lo sforzo del conducente', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '181', '115003', '1613', '12', 'Il conducente non può intervenire sul comando della frizione se la stessa è di tipo pneumo-idraulico', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '181', '115003', '1614', '13', 'La frizione a comando pneumo-idraulico non è idonea all''uso sui veicoli industriali', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '182', '115003', '', '', 'Funzionamento del frizione – 1', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '182', '115003', '1615', '1', 'La frizione è un meccanismo che funziona per attrito', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '182', '115003', '1616', '2', 'La frizione è generalmente autoregolante al fine di recuperare il gioco dovuto all''usura delle guarnizione d''attrito (ferodi), in caso contrario deve venire periodicamente registrata in officina', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '182', '115003', '1617', '3', 'La frizione serve a collegare in modo graduale il motore con il cambio', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '182', '115003', '1618', '4', 'La frizione permette di effettuare una partenza graduale con un veicolo carico di merce', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '182', '115003', '1619', '5', 'La frizione è posta tra il cambio e l''albero di trasmissione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '182', '115003', '1620', '6', 'La frizione è l''organo di trasmissione principale del cambio automatico', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '182', '115003', '1621', '7', 'La frizione è raffreddata da apposita ventola', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '182', '115003', '1622', '8', 'La frizione serve a mettere in collegamento direttamente il motore con gli assi delle ruote motrici', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '182', '115003', '1623', '9', 'La frizione serve a dirigere il veicolo durante la marcia', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '183', '115003', '', '', 'Funzionamento del frizione – 2', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '183', '115003', '1624', '1', 'La frizione consente di collegare e scollegare il motore con gli altri organi di trasmissione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '183', '115003', '1625', '2', 'La frizione consente di far partire il veicolo collegando gradualmente l''albero motore con le ruote motrici attraverso gli organi di trasmissione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '183', '115003', '1626', '3', 'La frizione consente di tenere in funzionamento il motore quando il veicolo è fermo con marcia inserita', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '183', '115003', '1627', '4', 'La frizione consente di cambiare marcia quando il veicolo è in movimento', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '183', '115003', '1628', '5', 'La frizione consente di connettere e disconnettere gradualmente il motore con le ruote motrici', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '183', '115003', '1629', '6', 'La frizione consente l''innesto o il disinnesto del motore con gli altri organi di trasmissione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '183', '115003', '1630', '7', 'La frizione consente di connettere e disconnettere gradualmente il motore con la pompa di iniezione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '184', '115003', '', '', 'Il pedale della frizione', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '184', '115003', '1631', '1', 'Il pedale della frizione comanda il cuscinetto reggispinta della frizione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '184', '115003', '1632', '2', 'Il pedale della frizione può comandare il cuscinetto reggispinta con dispositivi di tipo idraulico', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '184', '115003', '1633', '3', 'Il pedale della frizione può comandare il cuscinetto reggispinta con dispositivi di tipo pneumatico', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '184', '115003', '1634', '4', 'Il pedale della frizione può comandare il cuscinetto reggispinta con dispositivi di tipo meccanico', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '184', '115003', '1635', '5', 'Il pedale della frizione deve essere rilasciato gradualmente', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '184', '115003', '1636', '6', 'Il piede sinistro del conducente, quando non agisce sul comando per effettuare un cambio di marcia, non deve appoggiare sul pedale della frizione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '184', '115003', '1637', '7', 'Nei mezzi pesanti la frizione viene comandata da un pedale, che aziona un servocomando che spesso utilizza aria compressa', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '184', '115003', '1638', '8', 'Il pedale della frizione può essere rilasciato bruscamente dal conducente in fase di partenza', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '184', '115003', '1639', '9', 'Il pedale della frizione può comandare il cuscinetto reggispinta con dispositivi di tipo a vite senza fine', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '184', '115003', '1640', '10', 'Il pedale della frizione comanda i satelliti del differenziale', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '184', '115003', '1641', '11', 'Il pedale della frizione va spinto con molta gradualità e va invece lasciato con movimento rapido e secco', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '184', '115003', '1642', '12', 'In tutti gli autoveicoli è presente il pedale della frizione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '184', '115003', '1643', '13', 'Se il veicolo non ha il pedale della frizione allora è anche sprovvisto di cambio di velocità', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '184', '115003', '1644', '14', 'Tutti i veicoli che non sono dotati di pedale della frizione sono detti a presa diretta', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '184', '115003', '1645', '15', ' È sempre bene mantenere il piede appoggiato sulla frizione durante la marcia, in modo da velocizzare le cambiate di marcia', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '185', '115005', '', '', 'Cambio di velocità 1', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '185', '115005', '1646', '1', 'Il cambio serve a variare il rapporto fra i giri del motore ed i giri dell''albero di trasmissione e, di conseguenza, delle ruote motrici', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '185', '115005', '1647', '2', 'In prima marcia, il numero di giri in uscita dal cambio è inferiore al numero di giri del motore', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '185', '115005', '1648', '3', 'Il sincronizzatore rende più agevole e silenzioso l''innesto delle marce', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '185', '115005', '1649', '4', 'Nei veicoli pesanti, il cambio è spesso munito di riduttore che raddoppia di fatto il numero delle marce', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '185', '115005', '1650', '5', 'Nei veicoli pesanti, il riduttore del cambio consente di adattare più facilmente la potenza del motore alla variazione degli sforzi', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '185', '115005', '1651', '6', 'Il cambio non ha mai due marce con lo stesso rapporto di giri', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '185', '115005', '1652', '7', 'Il cambio di un veicolo pesante ha in genere più marce di quello di una autovettura', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '185', '115005', '1653', '8', 'Nei veicoli pesanti, il cambio è di norma munito di riduttore che serve a diminuire il numero delle marce disponibili', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '185', '115005', '1654', '9', 'Il cambio serve a variare il rapporto fra il numero di giri dell''albero di trasmissione e quello dei semiassi', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '185', '115005', '1655', '10', 'Il cambio è posto tra l''albero motore e la frizione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '185', '115005', '1656', '11', 'Il cambio non ha mai più di 6 marce', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '185', '115005', '1657', '12', 'In prima marcia, il numero di giri in uscita dal cambio è superiore al numero di giri del motore', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '185', '115005', '1658', '13', 'I veicoli eccezionali non sono dotati di cambio', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '186', '115006', '', '', 'Cambio di velocità 2', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '186', '115006', '1659', '1', 'L''uso del cambio consente al conducente di variare la forza di trazione in relazione al carico del veicolo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '186', '115006', '1660', '2', 'Il cambio serve a variare il rapporto tra i giri del motore ed i giri dell''albero di trasmissione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '186', '115006', '1661', '3', 'Il cambio permette di trasmettere alle ruote una forza variabile a seconda della marcia innestata', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '186', '115006', '1662', '4', 'In genere, nei veicoli pesanti, la leva del cambio non agisce direttamente sul selettore delle marce, bensì su di un servocomando di tipo pneumatico o idraulico', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '186', '115006', '1663', '5', 'Il cambio contiene olio lubrificante nel quale sono parzialmente immersi gli ingranaggi', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '186', '115006', '1664', '6', 'Il cambio dei veicoli pesanti può talvolta avere più di dieci marce', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '186', '115006', '1665', '7', 'Il cambio automatico rende più uniforme la velocità del veicolo, anche durante i cambi di marcia', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '186', '115006', '1666', '8', 'I gruppi epicicloidali sono sistemi di ingranaggi di cui sono dotati alcuni cambi di veicoli pesanti', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '186', '115006', '1667', '9', 'In uscita dal cambio di velocità può talvolta trovarsi il rallentatore', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '186', '115006', '1668', '10', 'Il cambio dei veicoli pesanti ha normalmente due sole marce, una normale e una ridotta', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '186', '115006', '1669', '11', 'Quando si necessita di un cambio silenzioso, le ruote dentate sono sostituite da innesti a frizione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '186', '115006', '1670', '12', 'I cambi automatici possono avere al massimo 5 marce', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '186', '115006', '1671', '13', 'Per risparmiare carburante è bene cambiare marcia senza usare la frizione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '186', '115006', '1672', '14', 'Tramite il contagiri posto sul cruscotto, è possibile conoscere il numero di giri delle ruote motrici', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '186', '115006', '1673', '15', 'Quando un veicolo molto carico percorre una salita si usano sempre le marce più alte', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '186', '115006', '1674', '16', 'Il turbocompressore del motore entra in azione quando si innesta una marcia superiore alla terza', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '186', '115006', '1675', '17', 'Alcuni veicoli per cava e cantiere hanno il cambio raffreddato ad acqua', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '186', '115006', '1676', '18', 'Innestando una marcia più alta si aumenta il regime di giri del motore', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '186', '115006', '1677', '19', 'Il cambio automatico deve essere disinserito nelle discese ripide', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '187', '115006', '', '', 'Cambio di velocità 3', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '187', '115006', '1678', '1', 'Più piccola è la marcia che si inserisce, più alta è la forza di spunto del veicolo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '187', '115006', '1679', '2', 'Quanto più ridotte sono le marce, tanto più elevata è la forza di trazione trasmessa alle ruote', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '187', '115006', '1680', '3', 'Se il cambio è molto rumoroso, è possibile che ciò sia dovuto a mancanza di olio all''interno della sua scatola', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '187', '115006', '1681', '4', 'Se il cambio è molto rumoroso, può essere che qualche cuscinetto degli ingranaggi si sia usurato', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '187', '115006', '1682', '5', 'La scatola del cambio contiene olio lubrificante che deve venire sostituito periodicamente', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '187', '115006', '1683', '6', 'La scatola del cambio contiene olio lubrificante il cui livello deve essere periodicamente controllato', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '187', '115006', '1684', '7', 'Se un ingranaggio grande ne fa girare uno piccolo, l''ingranaggio piccolo ruota con un numero di giri maggiore dell''ingranaggio grande (moltiplicatore)', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '187', '115006', '1685', '8', 'Se un ingranaggio piccolo ne fa girare uno più grande, l''ingranaggio grande ruota con un numero di giri minore dell''ingranaggio piccolo (riduttore)', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '187', '115006', '1686', '9', 'Se un ingranaggio ne fa girare un altro delle stesse dimensioni, tutti e due gli ingranaggi ruotano con la stessa velocità (rapporto di parità)', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '187', '115006', '1687', '10', 'Il cambio è un meccanismo che permette alle ruote di girare più velocemente del motore', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '187', '115006', '1688', '11', 'Più alta è la marcia che si innesta, più alta è la forza di trazione trasmessa alle ruote', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '187', '115006', '1689', '12', 'Le marce ridotte sono quelle che trasmettono alle ruote motrici una minore forza di trazione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '187', '115006', '1690', '13', 'Per effettuare uno spunto in salita con il veicolo carico è necessario inserire una marcia adeguatamente alta', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '187', '115006', '1691', '14', 'Scalando una marcia si diminuisce il regime di giri del motore', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '187', '115006', '1692', '15', 'Il cambio serve a mantenere la velocità rotativa dell''albero di trasmissione costante, indipendentemente dal numero dei giri del motore', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '187', '115006', '1693', '16', 'Per sfruttare l''effetto frenante del motore occorre usare marce alte', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '188', '115008', '', '', 'Coppia conica', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '188', '115008', '1694', '1', 'La coppia conica è costituita da due ingranaggi sempre in presa, chiamati pignone e corona', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '188', '115008', '1695', '2', 'La coppia conica serve, tra l''altro, a ridurre la velocità delle ruote rispetto a quella dell''albero motore', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '188', '115008', '1696', '3', 'La coppia conica serve, tra l''altro, a deviare di 90° il moto rotatorio dell''albero di trasmissione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '188', '115008', '1697', '4', 'La coppia conica è in genere utilizzata nei veicoli con motore anteriore e trazione posteriore', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '188', '115008', '1698', '5', 'Una delle caratteristiche della coppia conica è il rapporto al ponte', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '188', '115008', '1699', '6', 'Il rapporto al ponte è un dato caratteristico di omologazione di un veicolo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '188', '115008', '1700', '7', 'La coppia conica serve, tra l''altro, ad aumentare la velocità delle ruote rispetto a quella dell''albero motore', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '188', '115008', '1701', '8', 'La coppia conica serve, tra l''altro, a deviare di 60° il moto rotatorio dell''albero di trasmissione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '188', '115008', '1702', '9', 'La coppia conica è inserita manualmente dall''autista grazie a un comando posto sul cruscotto', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '188', '115008', '1703', '10', 'Nella coppia conica, il pignone trasmette il moto alla corona grazie a un disco a frizione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '188', '115008', '1704', '11', 'Il rapporto al ponte può essere variato a piacimento dell''autista', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '189', '115009', '', '', 'Organi di trasmissione 3', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '189', '115009', '1705', '1', 'Il sincronizzatore è un dispositivo che facilita l''accoppiamento degli ingranaggi del cambio durante il passaggio da una marcia all''altra', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '189', '115009', '1706', '2', 'Durante la percorrenza di una curva, il differenziale consente di trasferire parte della velocità di rotazione dalla ruota motrice interna a quella esterna alla curva', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '189', '115009', '1707', '3', 'La funzione del cambio di velocità è quella di modificare la potenza erogabile alle ruote per vincere più facilmente le resistenze all''avanzamento', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '189', '115009', '1708', '4', 'Il differenziale è un meccanismo che consente di evitare lo strisciamento delle ruote durante la percorrenza di una curva', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '189', '115009', '1709', '5', 'Nei veicoli pesanti le variazioni di massa tra veicolo carico e veicolo scarico sono significative e quindi vengono utilizzati cambi con un numero di rapporti superiore ai cambi utilizzati nei veicoli leggeri', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '189', '115009', '1710', '6', 'Nei cambi montati sui veicoli pesanti, lo splitter è una levetta posizionata sotto il pomello del cambio che consente di scegliere tra un gruppo di marce veloci e uno di lente', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '189', '115009', '1711', '7', 'Il differenziale è un meccanismo che consente di far girare le ruote esterne e quelle interne alla stessa velocità, durante la percorrenza di una curva', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '189', '115009', '1712', '8', 'Il differenziale consente di mantenere una trazione ottimale del veicolo anche su terreni con scarsa aderenza', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '189', '115009', '1713', '9', 'Durante la percorrenza di una curva, il differenziale consente di trasferire parte della velocità di rotazione dalla ruota motrice esterna a quella interna alla curva', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '189', '115009', '1714', '10', 'Il sincronizzatore è un dispositivo che fa parte del cambio di velocità e che consente di far girare l''albero motore e quello di trasmissione alla stessa velocità', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '189', '115009', '1715', '11', 'Se un cambio di velocità è dotato di sincronizzatori delle marce, diventa inutile la presenza della frizione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '190', '115011', '', '', 'Differenziale', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '190', '115011', '1716', '1', 'Ogni asse motore è dotato di un differenziale', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '190', '115011', '1717', '2', 'Il differenziale trasmette il moto alle ruote tramite due semiassi', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '190', '115011', '1718', '3', 'Il differenziale è un ripartitore di giri tra le ruote motrici di uno stesso asse', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '190', '115011', '1719', '4', 'Il differenziale contiene olio lubrificante che deve venire periodicamente sostituito', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '190', '115011', '1720', '5', 'Il differenziale permette alle ruote motrici di svere diversa velocita una respetta all altro', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '190', '115011', '1721', '6', 'L''albero di trasmissione deve essere molto rigido per impedire le oscillazioni del differenziale', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '190', '115011', '1722', '7', 'Il differenziale non può essere sull''asse delle ruote direttrici', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '190', '115011', '1723', '8', 'Il differenziale non si trova mai sui veicoli adatti a percorsi fuori-strada perché è troppo delicato e potrebbe essere danneggiato dai sobbalzi', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '190', '115011', '1724', '9', 'Se un asse è dotato di ruote gemellate deve avere due differenziali', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '190', '115011', '1725', '10', 'Il differenziale non viene montato sui veicoli da cava o cantiere perché è troppo delicato', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '190', '115011', '1726', '11', 'Al posto del differenziale può essere montata la coppia conica', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '190', '115011', '1727', '12', 'Se il cambio è automatico, il differenziale è inserito al suo interno', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '191', '154001', '', '', 'Organi di sospensione 1', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '191', '154001', '1728', '1', 'Gli organi di sospensione del veicolo sono presenti fra le ruote ed il telaio', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '191', '154001', '1729', '2', 'L''efficienza degli organi di sospensione del veicolo si riflette sul confort dei passeggeri', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '191', '154001', '1730', '3', 'Gli organi di sospensione del veicolo non devono essere sovraccaricati', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '191', '154001', '1731', '4', 'Gli organi di sospensione del veicolo si danneggiano più facilmente se il carico non è disposto correttamente', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '191', '154001', '1732', '5', 'Gli organi di sospensione possono essere sovraccaricati, ma di non oltre il 20%', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '191', '154001', '1733', '6', 'Gli organi di sospensione possono essere disattivati dal conducente in qualunque momento', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '192', '154002', '', '', 'Organi di sospensione 2', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '192', '154002', '1734', '1', 'Gli organi di sospensione del veicolo servono a ridurre o a limitare il saltellamento delle ruote', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '192', '154002', '1735', '2', 'Gli organi di sospensione del veicolo servono a mantenere il più possibile costante il peso aderente delle singole ruote', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '192', '154002', '1736', '3', 'Gli organi di sospensione del veicolo servono a mantenerne costante l''assetto', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '192', '154002', '1737', '4', 'Gli organi di sospensione del veicolo servono a limitarne il rollio e il beccheggio', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '192', '154002', '1738', '5', 'Gli organi di sospensione del veicolo servono a ridurre gli effetti degli urti trasmessi al veicolo dai dislivelli e dalle irregolarità stradali', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '192', '154002', '1739', '6', 'Gli organi di sospensione del veicolo servono a evitare il bloccaggio delle ruote posteriori in frenata', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '192', '154002', '1740', '7', 'Gli organi di sospensione del veicolo servono a evitare il surriscaldamento degli pneumatici', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '192', '154002', '1741', '8', 'Gli organi di sospensione del veicolo servono ad aumentarne il raggio di sterzata', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '192', '154002', '1742', '9', 'Gli organi di sospensione del veicolo devono essere regolati prima di intraprendere ogni lungo viaggio', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '192', '154002', '1743', '10', 'Gli organi di sospensione del veicolo entrano in funzione a velocità superiori a 50 km/h', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '192', '154002', '1744', '11', 'Gli organi di sospensione servono a zavorrare il veicolo per aumentarne la massa rimorchiabile', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '192', '154002', '1745', '12', 'Gli organi di sospensione del veicolo non necessitano di manutenzione, perché sono progettati per essere efficienti per tutta la vita del veicolo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '192', '154002', '1746', '13', 'Gli organi di sospensione del veicolo funzionano perfettamente anche con veicolo sovraccarico', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '193', '154003', '', '', 'Organi di sospensione 3', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '193', '154003', '1747', '1', 'L''impianto di sospensioni pneumatiche permette al conducente di variare l''altezza da terra del cassone del veicolo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '193', '154003', '1748', '2', 'Le sospensioni degli autoveicoli servono ad attutire i sobbalzi causati dalle asperità del fondo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '193', '154003', '1749', '3', 'Le sospensioni pneumatiche consentono una facile correzione dell''assetto grazie ad apposite valvole livellatrici', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '193', '154003', '1750', '4', 'La barra stabilizzatrice frena il coricamento dell''autoveicolo dovuto alla forza centrifuga in curva o a un dislivello stradale trasversale', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '193', '154003', '1751', '5', 'Le sospensioni degli autoveicoli servono ad attutire il rumore prodotto dalla combustione del carburante', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '193', '154003', '1752', '6', 'Le sospensioni ad acqua devono essere integrate con liquido antigelo durante la stagione fredda', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '193', '154003', '1753', '7', 'Sui veicoli pesanti dotati di doppio assale posteriore, l''asse anteriore non è dotato di sospensioni', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '193', '154003', '1754', '8', 'Le sospensioni pneumatiche non possono essere comandate manualmente', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '193', '154003', '1755', '9', 'Le sospensioni pneumatiche non possono essere utilizzate su veicoli di massa complessiva a pieno carico superiore a 3,5 tonnellate', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '193', '154003', '1756', '10', 'Le sospensioni pneumatiche non possono essere utilizzate su veicoli atti al traino', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '193', '154003', '1757', '11', 'Se si rompe una sospensione a balestra, per circolare in sicurezza è sufficiente gonfiare gli pneumatici del relativo asse di 0,5 bar oltre il valore normale', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '193', '154003', '1758', '12', ' La sospensione a balestra è autolubrificante e non necessita di manutenzione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '194', '154004', '', '', 'Ammortizzatori 1', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '194', '154004', '1759', '1', 'L''ammortizzatore ha la funzione di smorzare le oscillazioni delle molle delle sospensioni', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '194', '154004', '1760', '2', 'In genere gli ammortizzatori sono di tipo idraulico o a gas', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '194', '154004', '1761', '3', 'Gli ammortizzatori idraulici sono in genere del tipo a doppio effetto', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '194', '154004', '1762', '4', 'Gli ammortizzatori idraulici utilizzano uno speciale liquido resistente alle alte temperature', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '194', '154004', '1763', '5', 'Gli ammortizzatori sono collegati da una parte al telaio e dall''altra all''asse delle ruote', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '194', '154004', '1764', '6', 'Gli ammortizzatori non sono utilizzati su veicoli di massa complessiva a pieno carico superiore a 7 tonnellate', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '194', '154004', '1765', '7', 'Il liquido degli ammortizzatori idraulici deve essere rabboccato dall''autista ogni tremila chilometri circa', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '195', '154005', '', '', 'Efficienza degli ammortizzatori', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '195', '154005', '1766', '1', 'L''inefficienza degli ammortizzatori rende la guida di un autoveicolo poco sicura', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '195', '154005', '1767', '2', 'L''inefficienza degli ammortizzatori allunga la distanza di frenatura', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '195', '154005', '1768', '3', 'L''inefficienza degli ammortizzatori aumenta il pericolo di sbandamento in curva', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '195', '154005', '1769', '4', 'L''inefficienza degli ammortizzatori aumenta il pericolo di aquaplaning', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '195', '154005', '1770', '5', 'L''inefficienza degli ammortizzatori aumenta l''affaticamento del conducente alla guida', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '195', '154005', '1771', '6', 'L''inefficienza degli ammortizzatori peggiora la visibilità notturna a causa dell''eccessiva oscillazione del fascio luminoso dei proiettori', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '195', '154005', '1772', '7', 'L''inefficienza degli ammortizzatori aumenta il pericolo di abbagliamento dei conducenti provenienti dalla direzione opposta a causa dell''oscillazione del fascio luminoso dei proiettori', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '195', '154005', '1773', '8', 'L''efficienza degli ammortizzatori migliora la sicurezza del veicolo durante la marcia', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '195', '154005', '1774', '9', 'L''inefficienza degli ammortizzatori può essere compensata dall''irrigidimento delle sospensioni', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '195', '154005', '1775', '10', 'Alle basse temperature, il liquido degli ammortizzatori idraulici deve essere miscelato con liquido antigelo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '195', '154005', '1776', '11', 'Gli ammortizzatori scarichi favoriscono la stabilità di marcia perché abbassano il baricentro del veicolo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '195', '154005', '1777', '12', 'Sull''assale che monta ruote gemellate non è necessario montare ammortizzatori', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '195', '154005', '1778', '13', 'Occorre controllare periodicamente la pressione degli ammortizzatori', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '195', '154005', '1779', '14', 'La sostituzione di un ammortizzatore è un''operazione semplice, che può essere svolta da qualunque autista', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '195', '154005', '1780', '15', 'Una specifica spia rossa accesa sul cruscotto, segnala che uno o più ammortizzatori sono inefficienti', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '196', '154006/163005', '', '', 'Ammortizzatori 2', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '196', '154006/163005', '1781', '1', 'Gli ammortizzatori, se scarichi, non provvedono a smorzare correttamente le oscillazioni delle balestre', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '196', '154006/163005', '1782', '2', 'Il mancato funzionamento della valvola livellatrice delle sospensioni pneumatiche non permette che venga variata la pressione interna in funzione del carico', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '196', '154006/163005', '1783', '3', 'Gli ammortizzatori scarichi comportano una maggiore rumorosità del veicolo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '196', '154006/163005', '1784', '4', 'Gli ammortizzatori scarichi possono provocare un anomalo orientamento del fascio di luce dei proiettori anabbaglianti', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '196', '154006/163005', '1785', '5', 'Gli ammortizzatori scarichi possono provocare un anomalo consumo del battistrada', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '196', '154006/163005', '1786', '6', 'Gli ammortizzatori anteriori possono essere rimossi dal veicolo senza comprometterne il livello di sicurezza', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '196', '154006/163005', '1787', '7', 'Gli ammortizzatori hanno funzionamento di tipo esclusivamente meccanico', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '196', '154006/163005', '1788', '8', 'Il mancato funzionamento della valvola livellatrice delle sospensioni pneumatiche determina un cattivo funzionamento del motore', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '196', '154006/163005', '1789', '9', 'Gli ammortizzatori scarichi provocano un notevole risparmio di carburante', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '196', '154006/163005', '1790', '10', 'Gli ammortizzatori scarichi garantiscono un miglior comfort dei passeggeri perché rendono meno rigido il veicolo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '197', '163005', '', '', 'Cause dell''instabilità di marcia del veicolo', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '197', '163005', '1791', '1', 'L''instabilità di marcia del veicolo può dipendere dagli ammortizzatori scarichi', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '197', '163005', '1792', '2', 'L''instabilità di marcia del veicolo può dipendere dalle balestre o dalle molle elicoidali danneggiate', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '197', '163005', '1793', '3', 'L''instabilità di marcia del veicolo può dipendere dal sistema di regolazione delle sospensioni pneumatiche malfunzionante', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '197', '163005', '1794', '4', 'Gli ammortizzatori scarichi possono provocare anomale oscillazioni del veicolo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '197', '163005', '1795', '5', 'L''instabilità di marcia del veicolo può dipendere da scarsità di olio nel circuito di lubrificazione del motore', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '197', '163005', '1796', '6', 'La stabilità del veicolo è indipendente dalle modalità di posizionamento del carico', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '197', '163005', '1797', '7', 'L''instabilità di marcia del veicolo può dipendere dall''utilizzo di pneumatici invernali in una giornata in cui la temperatura dell''asfalto è di circa 25°C', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '197', '163005', '1798', '8', 'L''instabilità di marcia del veicolo può dipendere dall''impianto di navigazione malfunzionante', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '198', '153001', '', '', 'Servosterzo', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '198', '153001', '1799', '1', 'Una cattiva registrazione del servosterzo può essere la causa di un effetto sovrasterzante', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '198', '153001', '1800', '2', 'Una cattiva registrazione del servosterzo può essere la causa di un effetto sottosterzante', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '198', '153001', '1801', '3', 'Il servosterzo serve a rendere possibile la guida, anche di veicoli pesanti, con limitato sforzo da parte del conducente', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '198', '153001', '1802', '4', 'Il servosterzo assorbe potenza al motore', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '198', '153001', '1803', '5', 'Il servosterzo diminuisce lo sforzo del conducente durante la sterzata', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '198', '153001', '1804', '6', 'Il servosterzo aiuta a mantenere la tenuta di strada in caso di brusco sgonfiamento di uno pneumatico', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '198', '153001', '1805', '7', 'Il servosterzo riduce il tempo necessario alla sterzata', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '198', '153001', '1806', '8', 'Il servosterzo riduce il tempo necessario alla sterzata, purché non si abbia un rimorchio agganciato', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '198', '153001', '1807', '9', 'Il servosterzo aumenta il tempo necessario alla sterzata', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '198', '153001', '1808', '10', 'Se la pompa del servosterzo è comandata dal motore, quando si sterza energicamente occorre anche accelerare', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '198', '153001', '1809', '11', 'Il servosterzo, se usato nelle lunghe discese, può surriscaldare gli pneumatici', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '198', '153001', '1810', '12', 'La pompa idraulica di un servosterzo non può essere a funzionamento elettrico', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '198', '153001', '1811', '13', 'Il servosterzo funziona male nelle lunghe discese con tornanti stretti e ripetuti', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '198', '153001', '1812', '14', 'Il servosterzo aumenta lo sforzo del conducente durante la sterzata in movimento', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '198', '153001', '1813', '15', 'Il servosterzo può causare perdita della tenuta di strada', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '199', '153002', '', '', 'Perdita d''olio del servosterzo', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '199', '153002', '1814', '1', 'La perdita d''olio del servosterzo compromette la manovrabilità dello sterzo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '199', '153002', '1815', '2', 'La perdita d''olio del servosterzo non compromette la manovrabilità perché viene compensata dall''olio dei freni tramite apposito travaso', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '200', '153003', '', '', 'Inconvenienti negli organi dello sterzo', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '200', '153003', '1816', '1', 'Gli inconvenienti negli organi dello sterzo possono dipendere dal non corretto accoppiamento degli ingranaggi della scatola guida', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '200', '153003', '1817', '2', 'Gli inconvenienti negli organi dello sterzo possono dipendere dalla perdita di lubrificante nella scatola guida', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '200', '153003', '1818', '3', 'Gli inconvenienti negli organi dello sterzo possono dipendere dagli pneumatici di tipo invernale montati durante la stagione calda', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '200', '153003', '1819', '4', 'Gli inconvenienti negli organi dello sterzo possono dipendere dal limitatore di velocità non correttamente tarato', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '201', '153004', '', '', 'Veicoli sottosterzanti ', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '201', '153004', '1820', '1', 'In curva, un veicolo sottosterzante tende ad allargare la sua traiettoria', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '201', '153004', '1821', '2', 'Un veicolo è sottosterzante se tende a curvare meno di quanto il conducente vorrebbe, sulla base della traiettoria impostata', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '201', '153004', '1822', '3', 'Si definisce sottosterzante quel veicolo che tende a stringere la curva', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '201', '153004', '1823', '4', 'Quando il veicolo in curva tende a percorrere una traiettoria più stretta di quella impostata con il volante, il veicolo stesso ha un comportamento sottosterzante', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '202', '153004', '', '', 'Veicoli sovrasterzanti ', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '202', '153004', '1824', '1', 'Si definisce sovrasterzante quel veicolo che tende a stringere eccessivamente la curva', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '202', '153004', '1825', '2', 'Si definisce sovrasterzante quel veicolo che tende a sterzare di più rispetto all''azione sul volante esercitata dal conducente', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '202', '153004', '1826', '3', 'Un veicolo sovrasterzante, in curva, tende ad allargare la sua traiettoria', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '202', '153004', '1827', '4', 'In genere, gli pneumatici anteriori sgonfi rendono il veicolo sovrasterzante', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '203', '153004', '', '', 'Veicoli sottosterzanti o sovrasterzanti ', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '203', '153004', '1828', '1', 'Sovrasterzo e sottosterzo possono dipendere da una cattiva sistemazione del carico nel cassone del veicolo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '203', '153004', '1829', '2', 'Il sovrasterzo ed il sottosterzo sono comportamenti del veicolo che, se non corretti adeguatamente, sfavoriscono la tenuta di strada in curva', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '203', '153004', '1830', '3', 'L''effetto del sovrasterzo può causare il testa-coda', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '203', '153004', '1831', '4', 'L''effetto del sottosterzo può causare il testa-coda', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '203', '153004', '1832', '5', 'Quando il veicolo in curva tende a percorrere una traiettoria più stretta di quella impostata, il conducente deve accelerare con decisione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('10', '203', '153004', '1833', '6', 'I veicoli con due assi anteriori sterzanti non sono mai né sovrasterzanti né sottosterzanti in curva', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '204', '131002', '', '', 'Disco e cerchio della ruota', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '204', '131002', '1834', '1', 'La ruota è composta da disco e cerchio', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '204', '131002', '1835', '2', 'Sul cerchio è riportata una sigla che permette di ricavarne le principali caratteristiche', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '204', '131002', '1836', '3', 'Dalla sigla riportata sul cerchio si può ricavarne la larghezza', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '204', '131002', '1837', '4', 'Dalla sigla riportata sul cerchio si può ricavare il tipo di profilo della balconata', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '204', '131002', '1838', '5', 'Dalla sigla riportata sul cerchio si può ricavarne il diametro di calettamento', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '204', '131002', '1839', '6', 'Dalla sigla riportata sul cerchio si può ricavarne l''OFF-SET', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '204', '131002', '1840', '7', 'Dalla sigla riportata sullo pneumatico si può ricavarne l''anno e la settimana di fabbricazione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '204', '131002', '1841', '8', 'Dalla sigla riportata sul cerchio si può ricavarne il giorno di fabbricazione dello pneumatico', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '204', '131002', '1842', '9', 'Dalla sigla riportata sul cerchio si può ricavarne la data in cui deve essere revisionato lo pneumatico', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '204', '131002', '1843', '10', 'Dalla sigla riportata sul cerchio si può ricavarne l''anno di scadenza dello pneumatico', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '204', '131002', '1844', '11', 'Dalla sigla riportata sul cerchio si può ricavare il tipo di materiale con cui è stato realizzato lo pneumatico,', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '204', '131002', '1845', '12', 'La ruota è composta da disco e camera d''aria', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '204', '131002', '1846', '13', 'Sul cerchio è riportata una sigla che permette di ricavare se può essere utilizzato per marcia fuoristrada', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '204', '131002', '1847', '14', 'La sigla riportata sul cerchio è in inglese e nella lingua dell''intestatario del veicolo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '205', '131012', '', '', 'Ruote gemellate', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '205', '131012', '1848', '1', 'Ruote gemellate significa che ad ogni estremità di un asse, è montata una coppia di ruote', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '205', '131012', '1849', '2', 'Due ruote gemellate devono possibilmente avere lo stesso grado di usura', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '205', '131012', '1850', '3', 'Due ruote gemellate devono essere gonfiate alla stessa pressione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '205', '131012', '1851', '4', 'Sulla ruota interna di ruote montate in gemello, occorre in genere utilizzare una valvola di gonfiaggio dotata di apposita prolunga', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '205', '131012', '1852', '5', 'Tra le due ruote gemellate viene montato un distanziale per evitare sfregamenti dei fianchi', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '205', '131012', '1853', '6', 'I segnalatori della pressione degli pneumatici sono particolarmente utili quando le ruote sono gemellate', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '205', '131012', '1854', '7', 'Le ruote gemellate hanno un''unica valvola di gonfiaggio, in quanto le camere d''aria sono comunicanti tra di loro', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '205', '131012', '1855', '8', 'L''aderenza del veicolo sull''asfalto è minore se le ruote sono gemellate', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '205', '131012', '1856', '9', 'Il distanziale posto tra le due ruote gemellate facilita lo sfregamento degli pneumatici tra loro, in particolare a veicolo carico', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '205', '131012', '1857', '10', 'Tutti i veicoli di massa complessiva a pieno carico superiore a 7 tonnellate, devono montare ruote gemellate sull''ultimo asse posteriore', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '205', '131012', '1858', '11', 'I veicoli dotati di ruote gemellate devono essere muniti di sistema antibloccaggio in frenata ABS', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '205', '131012', '1859', '12', 'Le ruote gemellate sono generalmente montate sull’asse sterzante', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '206', '131015', '', '', 'Tipi di pneumatici 1', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '206', '131015', '1860', '1', 'Gli pneumatici radiali sono più flessibili di quelli a tele incrociate', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '206', '131015', '1861', '2', 'Uno pneumatico tubeless è sprovvisto di camera d''aria', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '206', '131015', '1862', '3', 'Gli pneumatici gemellati devono essere simmetrici, per dimensione e disegno, rispetto all''asse di marcia', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '206', '131015', '1863', '4', 'Gli pneumatici gemellati che presentano zone di consumo sui fianchi interni non sono pericolosi perché il fatto è dovuto a normale usura da contatto', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '206', '131015', '1864', '5', 'Gli pneumatici radiali sono più resistenti di quelli tradizionali', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '206', '131015', '1865', '6', 'Gli pneumatici radiali sono obbligatori per l''equipaggiamento di un mezzo d''opera', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '206', '131015', '1866', '7', 'Uno pneumatico tubeless deve essere gonfiato ad una pressione maggiore rispetto a uno con camera d''aria', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '206', '131015', '1867', '8', 'Uno pneumatico tubeless ha una minore tenuta di strada rispetto a uno con camera d''aria', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '207', '131001', '', '', 'Gli penumatici 1', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '207', '131001', '1868', '1', 'Scopo fondamentale dello pneumatico è quello di assicurare l''aderenza ruota-strada', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '207', '131001', '1869', '2', 'Lo pneumatico agisce insieme alle sospensioni per il molleggio del veicolo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '207', '131001', '1870', '3', 'Entro i limiti dell''aderenza, lo pneumatico assicura la trasmissione al suolo della forza motrice', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '207', '131001', '1871', '4', 'Entro i limiti dell''aderenza, lo pneumatico assicura la trasmissione al suolo della forza frenante', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '207', '131001', '1872', '5', 'Lo pneumatico evita che il peso del veicolo gravante sulla strada danneggi il manto stradale', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '207', '131001', '1873', '6', 'Entro i limiti dell''aderenza, lo pneumatico permette di dirigere il veicolo nella direzione impostata dal conducente', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '207', '131001', '1874', '7', 'Il battistrada dello pneumatico ha la funzione di drenare l''acqua, eventualmente presente sulla carreggiata, dall''area di impronta del battistrada', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '207', '131001', '1875', '8', 'Lo pneumatico deve essere il più rigido possibile', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '207', '131001', '1876', '9', 'Durante la stagione fredda, lo pneumatico deve essere fatto scaldare alcuni minuti prima della partenza', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '207', '131001', '1877', '10', 'Una spia rossa posta sul cruscotto avverte l''autista quando lo pneumatico deve essere sostituito', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '207', '131001', '1878', '11', 'Acquistando nuovi pneumatici con mescola più morbida del set precedente, occorre irrigidire gli ammortizzatori', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '207', '131001', '1879', '12', 'L''aderenza ruota-strada non dipende dallo stato d''usura degli pneumatici', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '207', '131001', '1880', '13', 'Se, con un veicolo pesante, si devono affrontare salite di pendenza superiore al 15%, occorre montare pneumatici rinforzati', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '207', '131001', '1881', '14', 'Non si può circolare in autostrada con veicoli che montano pneumatici ricostruiti', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '208', '131003', '', '', 'Parti degli pnematici', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '208', '131003', '1882', '1', 'Gli pneumatici possono avere una camera d''aria interna', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '208', '131003', '1883', '2', 'Gli pneumatici tubeless sono ricoperti all''interno di un strato di gomma speciale', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '208', '131003', '1884', '3', 'In uno pneumatico tubeless la valvola è fissata al cerchio', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '208', '131003', '1885', '4', 'In uno pneumatico con camera d''aria la valvola è fissata alla camera d''aria stessa', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '208', '131003', '1886', '5', 'Generalmente, uno pneumatico tubeless forato si affloscia più lentamente di uno pneumatico con lo stesso tipo di foratura, dotato di camera d''aria', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '208', '131003', '1887', '6', 'Se si usano pneumatici tubeless con aggiunta di camera d''aria vi è il pericolo di formazioni di sacche d''aria interne allo pneumatico', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '208', '131003', '1888', '7', 'Lo pneumatico comprende la carcassa, i talloni e il battistrada', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '208', '131003', '1889', '8', 'I talloni sono la parte dello pneumatico a contatto col cerchio', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '208', '131003', '1890', '9', 'Negli pneumatici tubeless, i talloni sono fondamentali per garantire la tenuta d''aria', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '208', '131003', '1891', '10', 'La carcassa di uno pneumatico è principalmente composta da tele con fili metallici e gomma', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '208', '131003', '1892', '11', 'Il rumore generato da uno pneumatico dipende principalmente dal disegno e dal materiale con cui sono realizzate le scolpiture del battistrada', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '208', '131003', '1893', '12', 'Gli pneumatici per veicoli pesanti devono avere una camera d''aria interna', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '208', '131003', '1894', '13', 'Gli pneumatici tubeless sono più pericolosi di quelli con camera d''aria', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '208', '131003', '1895', '14', 'Lo pneumatico di tipo tubeless è costituito dalla valvola, dal copertone e dalla camera d''aria', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '208', '131003', '1896', '15', 'Generalmente, uno pneumatico dotato di camera d''aria forato si affloscia più lentamente di uno pneumatico con lo stesso tipo di foratura, ma di tipo tubeless', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '208', '131003', '1897', '16', 'Uno pneumatico di tipo tubeless non è dotato di valvola', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '208', '131003', '1898', '17', 'Uno pneumatico con camera d''aria è più soggetto a consumarsi al centro', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '208', '131003', '1899', '18', 'Il disegno del battistrada dello pneumatico è annotato sulla carta di circolazione del veicolo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '208', '131003', '1900', '19', 'Sul veicolo possono essere montati 4 pneumatici con disegno del battistrada differente', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '208', '131003', '1901', '20', 'I talloni sono le parti dello pneumatico che garantiscono l''aderenza con la strada', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '208', '131003', '1902', '21', 'Sui fianchi dello pneumatico sono presenti piccoli intagli che migliorano lo smaltimento dell''acqua in caso di forti temporali', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '208', '131003', '1903', '22', 'I fianchi dello pneumatico devono essere indeformabili', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '209', '131004', '', '', 'Tipi di pneumatici 2', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '209', '131004', '1904', '1', 'Gli pneumatici possono essere di tipo e tele incrociate', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '209', '131004', '1905', '2', 'Gli pneumatici possono essere di tipo radiale', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '209', '131004', '1906', '3', 'Gli pneumatici si distinguono in radiali o  a tele incrociate a seconda della disposizione delle tele della carcassa', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '209', '131004', '1907', '4', 'Gli pneumatici di tipo radiale presentano, in genere, una maggior flessibilità dei fianchi rispetto a quelli a tele incrociate', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '209', '131004', '1908', '5', 'Rispetto agli pneumatici radiali, gli pneumatici di tipo  a tele incrociate presentano, in genere, una minor area di contatto in curva', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '209', '131004', '1909', '6', 'G li pneumatici possono essere di tipo  a tele sfalsate', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '209', '131004', '1910', '7', 'Gli pneumatici possono essere di tipo longitudinale', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '209', '131004', '1911', '8', 'Gli pneumatici si distinguono in radiali o a  a tele incrociate a seconda del numero e del materiale di composizione delle tele', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '209', '131004', '1912', '9', 'Rispetto agli pneumatici radiali, gli pneumatici di tipo  a tele incrociate presentano, in genere, una maggior area di contatto in curva', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '209', '131004', '1913', '10', 'Gli pneumatici di tipo radiale presentano, in genere, una minor flessibilità dei fianchi rispetto a quelli a tele incrociate', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '210', '131005', '', '', 'Battistrada degli pneumetici', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '210', '131005', '1914', '1', 'Il battistrada è la parte dello pneumatico che serve a garantire l''aderenza con l''asfalto', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '210', '131005', '1915', '2', 'Il battistrada deve garantire un idoneo drenaggio dell''acqua eventualmente presente sulla strada', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '210', '131005', '1916', '3', 'Nel battistrada sono presenti degli indicatori di usura', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '210', '131005', '1917', '4', 'L''elasticità del battistrada varia al variare della temperatura', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '210', '131005', '1918', '5', 'Il battistrada presenta differenti disegni a seconda della marca e del tipo dello pneumatico', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '210', '131005', '1919', '6', 'Il disegno del battistrada degli pneumatici degli assi posteriori può essere diverso da quello dell''asse anteriore', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '210', '131005', '1920', '7', 'Il battistrada deve avere, per legge, uno spessore minimo degli intagli di 1,6 millimetri su tutta la sua superficie', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '210', '131005', '1921', '8', 'Il battistrada deve avere, per legge, uno spessore minimo degli intagli di 1,6 millimetri su almeno l''80% della sua superficie', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '210', '131005', '1922', '9', 'Il battistrada di uno pneumatico radiale deve avere maggior larghezza rispetto a uno pneumatico a tele incrociate', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '210', '131005', '1923', '10', 'In caso di ruote gemellate, il battistrada degli pneumatici dell''asse posteriore può essere realizzato senza scolpiture', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '210', '131005', '1924', '11', 'Il battistrada deve essere realizzato con tasselli indeformabili', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '210', '131005', '1925', '12', 'Il battistrada è realizzato con un composto di nylon e gomma', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '210', '131005', '1926', '13', 'Il battistrada deve essere il più possibile rigido', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '210', '131005', '1927', '14', 'I tasselli del battistrada non possono essere più alti di 3 millimetri per non aumentare eccessivamente la deriva dello pneumatico', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '210', '131005', '1928', '15', 'Il battistrada deve avere, per legge, uno spessore minimo degli intagli di 2,5 millimetri su tutta la sua superficie', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '211', '131006', '', '', 'Fianchi degli pneumatici', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '211', '131006', '1929', '1', 'I fianchi di uno pneumatico devono essere moderatamente flessibili', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '211', '131006', '1930', '2', 'I fianchi di uno pneumatico sono costituiti principalmente da gomma e tele', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '211', '131006', '1931', '3', 'I fianchi di uno pneumatico cooperano allo smaltimento del calore', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '211', '131006', '1932', '4', 'Se i fianchi di uno pneumatico sono eccessivamente flessibili vi è un aumento della deriva', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '211', '131006', '1933', '5', 'I fianchi di uno pneumatico non possono presentare lesioni che interessano le tele', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '211', '131006', '1934', '6', 'I fianchi di uno pneumatico devono essere molto rigidi', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '211', '131006', '1935', '7', 'Se i fianchi di uno pneumatico sono rigidi vi è un aumento della deriva', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '211', '131006', '1936', '8', 'I fianchi interni degli pneumatici gemellati possono essere rivestiti in lega di alluminio, per poter resistere al reciproco sfregamento', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '211', '131006', '1937', '9', 'I fianchi di uno pneumatico possono aver subito lesioni che interessano le tele, purché le stesse siano state riparate da officina autorizzata che ne certifichi la sicurezza', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '211', '131006', '1938', '10', 'I fianchi di uno pneumatico devono avere spessore minimo di 1,6 millimetri', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '212', '131010', '', '', 'Pneumatici utilizzabili su un veicolo 1', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '212', '131010', '1939', '1', 'I tipi di pneumatici utilizzabili sul veicolo sono indicati sulla carta di circolazione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '212', '131010', '1940', '2', 'Gli pneumatici dello stesso asse devono essere dello stesso tipo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '212', '131010', '1941', '3', 'Gli pneumatici del veicolo possono essere di tipi differenti, purché uguali sullo stesso asse', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '212', '131010', '1942', '4', 'Due ruote che montano cerchi diversi possono avere la stessa circonferenza di rotolamento', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '212', '131010', '1943', '5', 'Il complesso pneumatico-cerchio non deve sporgere lateralmente dalla sagoma originale del veicolo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '212', '131010', '1944', '6', 'Le caratteristiche dimensionali degli pneumatici che sono montati sul veicolo non possono essere diverse da quelle riportate sulla carta di circolazione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '212', '131010', '1945', '7', 'Su ogni veicolo può essere utilizzato un solo tipo di pneumatico', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '212', '131010', '1946', '8', 'La marca di pneumatici utilizzabili sul veicolo è indicata sulla carta di circolazione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '212', '131010', '1947', '9', 'Gli pneumatici montati sullo stesso asse devono essere di tipo diverso', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '212', '131010', '1948', '10', 'Gli pneumatici dell''asse motore sono contraddistinti dalla sigla TRACT', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '212', '131010', '1949', '11', 'Sui veicoli pesanti è obbligatoria la rotazione degli pneumatici al massimo ogni 20000 km', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '212', '131010', '1950', '12', 'Sulla carta di circolazione è indicata la data a partire dalla quale è possibile utilizzare gli pneumatici invernali', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '213', '131007', '', '', 'Indice della capacità di carico degli pneumatici', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '213', '131007', '1951', '1', 'L''indice di capacità di carico di uno pneumatico, indica il carico massimo sopportabile dallo pneumatico alla velocità massima per esso ammessa', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '213', '131007', '1952', '2', 'L''indice di capacità di carico di uno pneumatico, può essere un numero del tipo 154', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '213', '131007', '1953', '3', 'L''indice di capacità di carico di uno pneumatico, può riportare anche il valore massimo ammesso nel caso di montaggio in gemello', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '213', '131007', '1954', '4', 'L''indice di capacità di carico di uno pneumatico, è un numero di due o tre cifre riportato sul fianco della copertura', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '213', '131007', '1955', '5', 'L''indice di capacità di carico di uno pneumatico, può essere un numero del tipo 15400', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '213', '131007', '1956', '6', 'Nel caso di pneumatici gemellati, l''indice di capacità di carico di uno pneumatico è un numero di tre cifre seguito dalla sigla TWIN', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '213', '131007', '1957', '7', 'L''indice di capacità di carico di uno pneumatico è un numero di due o tre cifre seguito dalle lettere KG, riportato sul fianco della copertura', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '213', '131007', '1958', '8', 'L''indice di capacità di carico di uno pneumatico non viene indicato nel caso di pneumatici da utilizzare solo in gemello', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '214', '131008', '', '', 'Indice di velocità degli pneumatici', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '214', '131008', '1959', '1', 'L''indice di velocità di uno pneumatico indica la sua velocità massima di impiego', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '214', '131008', '1960', '2', 'L''indice di velocità di uno pneumatico può essere rappresentato da una lettera maiuscola, ad esempio J', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '214', '131008', '1961', '3', 'L''indice di velocità di uno pneumatico è sempre indicato sul suo fianco', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '214', '131008', '1962', '4', 'L''indice di velocità di uno pneumatico permette di ricavare la velocità massima di impiego dello pneumatico in buone condizioni di manutenzione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '214', '131008', '1963', '5', 'L''indice di velocità di uno pneumatico indica la sua velocità minima di impiego', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '214', '131008', '1964', '6', 'Nel caso di pneumatici gemellati, l''indice di velocità di uno pneumatico indica il doppio della sua velocità massima di impiego', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '214', '131008', '1965', '7', 'L''indice di capacità di carico di uno pneumatico è un numero di due o tre cifre seguito dalle lettere km/h, riportato sul suo fianco', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '214', '131008', '1966', '8', 'L''indice di velocità di uno pneumatico non viene indicato nel caso di pneumatici da utilizzare solo in gemello', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '215', '131009', '', '', 'Scritte (marcature) sul fianco degli pneumatici 1', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '215', '131009', '1967', '1', 'Sul fianco dello pneumatico viene indicato se si tratta di uno pneumatico da utilizzare senza camera d''aria', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '215', '131009', '1968', '2', 'Se sul fianco dello pneumatico compare la scritta M+S, significa che lo pneumatico è omologato a percorsi innevati o fangosi', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '215', '131009', '1969', '3', 'Sul fianco dello penumetico viene indicato se si tratto di uno punematico da utilizzaro con camera di aria', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '215', '131009', '1970', '4', 'Dalle scritte che compaiono sul fianco dello pneumatico è possibile ricavare la settimana e l''anno di produzione dello pneumatico', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '215', '131009', '1971', '5', 'Sul fianco dello pneumatico si può trovare la sigla TWI', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '215', '131009', '1972', '6', 'La sigla TWI, posta sul fianco dello pneumatico, si trova in corrispondenza degli indicatori di usura', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '215', '131009', '1973', '7', 'Il diametro di calettamento del cerchio su cui montare lo pneumatico è in genere indicato in millimetri', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '215', '131009', '1974', '8', 'La larghezza nominale di sezione di uno pneumatico è in genere indicata in pollici', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '215', '131009', '1975', '9', 'Se sul fianco dello pneumatico compare la scritta M+S, significa che lo pneumatico è da utilizzarsi su veicoli che trainano un rimorchio', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '215', '131009', '1976', '10', 'Sul fianco dello pneumatico si può trovare la sigla MCTC se è stato omologato in Italia', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '215', '131009', '1977', '11', 'La sigla TWI, posta sul fianco dello pneumatico, si trova in corrispondenza della valvola di gonfiaggio', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '215', '131009', '1978', '12', 'La sigla TWI posta sul fianco dello pneumatico indica che si tratta di uno pneumatico da usare in gemello', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '215', '131009', '1979', '13', 'Dalle scritte che compaiono sul fianco dello pneumatico è possibile ricavare il giorno di produzione dello pneumatico', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '215', '131009', '1980', '14', 'Dalle scritte che compaiono sul fianco dello pneumatico non è possibile ricavarne la marca', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '216', '131013', '', '', 'Scritte (marcature) sul fianco degli pneumatici', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '216', '131013', '1981', '1', 'Lo pneumatico è un involucro di gomma, rinforzato con elementi metallici o tessili, che viene montato sul cerchio della ruota e viene gonfiato ad una pressione superiore a quella atmosferica', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '216', '131013', '1982', '2', 'In uno pneumatico marcato 315/80 R 22,5 152/148 M, il valore 22,5 rappresenta il diametro di calettamento del cerchio, espresso in pollici', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '216', '131013', '1983', '3', 'L''usura anomala del battistrada di uno pneumatico nella zona centrale può essere dovuta ad una pressione di gonfiaggio eccessiva', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '216', '131013', '1984', '4', 'La verifica dell''efficienza di uno pneumatico prevede il controllo della sua struttura per individuare eventuali anomalie dovute ad usura, deterioramento del materiale a causa dell''invecchiamento o lesioni dovute a contatti accidentali', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '216', '131013', '1985', '5', 'L''usura eccessiva del battistrada di uno pneumatico può causare il fenomeno dell''aquaplaning', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '216', '131013', '1986', '6', 'Il fenomeno dell''aquaplaning è favorito essenzialmente dalla forte usura del battistrada, dalla elevata velocità del veicolo, dalla bassa pressione di gonfiaggio dello pneumatico, dall''alto spessore dell''acqua presente sul manto stradale', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '216', '131013', '1987', '7', 'In caso di sostituzione di uno pneumatico si devono rispettare tassativamente i valori dimensionali, di carico e di velocità, riportati sulla carta di circolazione del veicolo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '216', '131013', '1988', '8', 'Il battistrada di uno pneumatico ha lo scopo di facilitare l''allontanamento dell''acqua e, negli autoveicoli, deve avere uno spessore di almeno 1,2 millimetri su tutta la superficie dello pneumatico', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '216', '131013', '1989', '9', 'In uno pneumatico marcato 315/80 R 22,5 152/148 M, i valori 152/148 rappresentano rispettivamente la larghezza nominale della sezione e l''altezza del fianco espresse in millimetri', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '216', '131013', '1990', '10', 'In uno pneumatico marcato 315/80 R 22,5 152/148 M, i valori 152/148 rappresentano l''indice di carico rispettivamente per ruota gemellata e per ruota singola', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '216', '131013', '1991', '11', 'In uno pneumatico marcato 315/80 R 22,5 152/148 M, l''iscrizione R 22,5 significa che lo pneumatico ha un raggio di 22,5 centimetri', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '216', '131013', '1992', '12', 'La verifica della pressione di gonfiaggio degli pneumatici va sempre effettuata con gli pneumatici caldi e quindi dopo aver percorso un numero sufficiente di chilometri', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '216', '131013', '1993', '13', 'Il fenomeno dell''aquaplaning è favorito dall''eccessiva pressione di gonfiaggio degli pneumatici', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '217', '131014', '', '', 'Scritte (marcature) sul fianco degli pneumatici 2', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '217', '131014', '1994', '1', 'In uno pneumatico marcato 315/100 R 20 154/149 M, i numeri 154/149 rappresentano rispettivamente l''indice di carico in singolo o in gemello e la lettera M il codice di velocità', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '217', '131014', '1995', '2', 'Tra le caratteristiche di uno pneumatico riportate sulla carta di circolazione è presente l''indice di velocità', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '217', '131014', '1996', '3', 'La serie o rapporto di aspetto indica la percentuale tra l''altezza dello pneumatico e la sua larghezza', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '217', '131014', '1997', '4', 'Gli pneumatici possono avere un senso di rotazione che, nel caso, viene indicato sul fianco con una freccia', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '217', '131014', '1998', '5', 'L''indicazione M & S indica uno pneumatico idoneo all''uso in caso di neve, o comunque di basse temperature', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '217', '131014', '1999', '6', 'Lo spessore minimo del battistrada è di 1,6 millimetri sulla fascia centrale del battistrada e 1,4 millimetri sui 3 centimetri di bordo da entrambi i lati', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '217', '131014', '2000', '7', 'Lo spessore minimo del battistrada per uno pneumatico industriale è di 2 millimetri su tutta la superficie del battistrada', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '217', '131014', '2001', '8', 'In uno pneumatico marcato 315/100 R 20 154/149 M, le lettere R ed M indicano rispettivamente la possibilità d''uso su un rimorchio e quella su strade di montagna', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '217', '131014', '2002', '9', 'Quando gli indici di carico segnati su uno pneumatico sono due, il primo si riferisce allo pneumatico su ruota gemellata, il secondo su ruota singola', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '217', '131014', '2003', '10', 'La ruota di scorta fa parte dei dispositivi obbligatori di un autoveicolo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '217', '131014', '2004', '11', 'Un veicolo industriale di massa complessiva a pieno carico superiore a 18 tonnellate non rischia di subire il fenomeno dell''aquaplaning, perché è troppo pesante perché ciò accada', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '217', '131014', '2005', '12', 'L''indicazione M & S indica uno pneumatico fabbricato nel Regno Unito', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '218', '132006', '', '', 'Pneumatici utilizzabili su un veicolo 2', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '218', '132006', '2006', '1', 'Il montaggio di pneumatici alternativi a quelli precedentemente utilizzati è ammesso se i nuovi sono tra quelli riportati sulla carta di circolazione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '218', '132006', '2007', '2', 'Il montaggio di pneumatici alternativi a quelli precedentemente utilizzati è ammesso se i nuovi sono consigliati e garantiti da un gommista iscritto all''albo dei manutentori', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '219', '132001', '', '', 'Controllo della pressione degli pneumatici 1', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '219', '132001', '2008', '1', 'La corretta pressione di gonfiaggio degli pneumatici è fondamentale per il confort di marcia', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '219', '132001', '2009', '2', 'La corretta pressione di gonfiaggio degli pneumatici è fondamentale per la tenuta di strada del veicolo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '219', '132001', '2010', '3', 'La corretta pressione di gonfiaggio degli pneumatici è fondamentale per la stabilità del veicolo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '219', '132001', '2011', '4', 'La pressione di gonfiaggio degli pneumatici deve essere adeguata al carico del veicolo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '219', '132001', '2012', '5', 'La corretta pressione di gonfiaggio degli pneumatici è fondamentale per la durata dello pneumatico', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '219', '132001', '2013', '6', 'La corretta pressione di gonfiaggio degli pneumatici garantisce la giusta area di impronta del battistrada', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '219', '132001', '2014', '7', 'La corretta pressione di gonfiaggio degli pneumatici comporta migliore confort, ma maggior consumo di carburante', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '219', '132001', '2015', '8', 'Se si prevede di dover percorrere lunghe discese, occorre abbassare la pressione degli pneumatici di mezzo Bar', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '219', '132001', '2016', '9', 'La corretta pressione di gonfiaggio degli pneumatici è fondamentale solo nei mesi estivi', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '219', '132001', '2017', '10', 'Il valore della corretta pressione di gonfiaggio degli pneumatici è indicato sul fianco dello pneumatico', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '219', '132001', '2018', '11', 'La corretta pressione di gonfiaggio degli pneumatici è fondamentale solo a veicolo a pieno carico', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '219', '132001', '2019', '12', 'La corretta pressione di gonfiaggio degli pneumatici deve essere annotata sulla carta di circolazione', 'F');
