INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '220', '132002', '2020', '1', 'La pressione di gonfiaggio degli pneumatici deve essere misurata a pneumatico freddo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '220', '132002', '2021', '2', 'Alla fine di un viaggio, uno pneumatico troppo gonfio ha il battistrada più caldo al centro', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '220', '132002', '2022', '3', 'Alla fine di un viaggio, uno pneumatico sgonfio ha il battistrada più caldo ai bordi', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '220', '132002', '2023', '4', 'La pressione di gonfiaggio degli pneumatici deve essere misurata a pneumatico caldo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '220', '132002', '2024', '5', 'Per migliorare la stabilità del veicolo, nelle forti discese è bene diminuire la pressione degli pneumatici posteriori di mezzo bar', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '220', '132002', '2025', '6', 'Sui veicoli dotati di sospensioni pneumatiche, la pressione di gonfiaggio deve essere ridotta di 0,2 bar', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '220', '132002', '2026', '7', 'Sui veicoli dotati di sospensioni pneumatiche, non è necessario il periodico controllo della pressione di gonfiaggio', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '220', '132002', '2027', '8', 'Alla fine di un viaggio, uno pneumatico troppo gonfio ha il battistrada più caldo ai bordi', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '220', '132002', '2028', '9', 'Alla fine di un viaggio, uno pneumatico sgonfio ha il battistrada più caldo al centro', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '221', '132002', '', '', 'Effetti dell''insufficiente pressione gonfiaggio sugli pneumatici', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '221', '132002', '2029', '1', 'L''insufficiente pressione di gonfiaggio di uno pneumatico non consente il corretto appoggio del battistrada sulla sede stradale', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '221', '132002', '2030', '2', 'L''insufficiente pressione di gonfiaggio di uno pneumatico riduce l''appoggio e la pressione di contatto della parte centrale del battistrada sulla sede stradale', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '221', '132002', '2031', '3', 'L''insufficiente pressione di gonfiaggio di uno pneumatico riduce l''aderenza', 'V');
